        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Reviews"
              value={stats.totalReviews}
              prefix={<EyeOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      <Card style={{ marginBottom: 24 }}>
        <Title level={4}>
          <FireOutlined style={{ color: '#ff4d4f' }} /> Hot Deals - Limited Time Offers
        </Title>
        <Carousel autoplay dotPosition="bottom">
          {mockProducts
            .filter((p) => p.discount)
            .map((product) => (
              <div key={product.id}>
                <Row
                  gutter={24}
                  align="middle"
                  style={{
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    padding: 40,
                    borderRadius: 8,
                  }}
                >
                  <Col xs={24} md={12}>
                    <Image
                      src={product.image}
                      alt={product.name}
                      preview={false}
                      style={{ borderRadius: 8 }}
                    />
                  </Col>
                  <Col xs={24} md={12}>
                    <Space direction="vertical" size="large">
                      <Tag color="red" style={{ fontSize: 16, padding: '4px 12px' }}>
                        <ThunderboltOutlined /> {product.discount}% OFF
                      </Tag>
                      <Title level={2} style={{ color: '#fff', margin: 0 }}>
                        {product.name}
                      </Title>
                      <div>
                        <Text style={{ fontSize: 32, color: '#fff', fontWeight: 'bold' }}>
                          ${product.price.toFixed(2)}
                        </Text>
                        {product.originalPrice && (
                          <Text
                            delete
                            style={{ fontSize: 20, color: '#ddd', marginLeft: 16 }}
                          >
                            ${product.originalPrice.toFixed(2)}
                          </Text>
                        )}
                      </div>
                      <Button
                        type="primary"
                        size="large"
                        icon={<ShoppingCartOutlined />}
                        onClick={() => message.success('Added to cart')}
                      >
                        Grab This Deal
                      </Button>
                    </Space>
                  </Col>
                </Row>
