import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Tag,
  Divider,
  Space,
  Grid,
  Alert,
  Table,
  List,
  Button,
  Drawer,
  Descriptions,
} from 'antd';
import {
  DesktopOutlined,
  TabletOutlined,
  MobileOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  InfoCircleOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text, Paragraph } = Typography;
const { useBreakpoint } = Grid;

interface Breakpoint {
  name: string;
  key: string;
  minWidth: number;
  icon: React.ReactNode;
  color: string;
}

interface ResponsiveFeature {
  feature: string;
  mobile: boolean;
  tablet: boolean;
  desktop: boolean;
  status: 'complete' | 'partial' | 'pending';
}

const breakpoints: Breakpoint[] = [
  {
    name: 'Mobile (XS)',
    key: 'xs',
    minWidth: 0,
    icon: <MobileOutlined />,
    color: '#ff4d4f',
  },
  {
    name: 'Mobile (SM)',
    key: 'sm',
    minWidth: 576,
    icon: <MobileOutlined />,
    color: '#ff9900',
  },
  {
    name: 'Tablet (MD)',
    key: 'md',
    minWidth: 768,
    icon: <TabletOutlined />,
    color: '#1890ff',
  },
  {
    name: 'Desktop (LG)',
    key: 'lg',
    minWidth: 992,
    icon: <DesktopOutlined />,
    color: '#52c41a',
  },
  {
    name: 'Large Desktop (XL)',
    key: 'xl',
    minWidth: 1200,
    icon: <DesktopOutlined />,
    color: '#722ed1',
  },
  {
    name: 'Extra Large (XXL)',
    key: 'xxl',
    minWidth: 1600,
    icon: <DesktopOutlined />,
    color: '#13c2c2',
  },
];

const responsiveFeatures: ResponsiveFeature[] = [
  {
    feature: 'Navigation Menu',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Product Grid Layout',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Shopping Cart',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Product Details',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Checkout Flow',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'User Profile',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Dashboard Charts',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Data Tables',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Forms & Modals',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Image Gallery',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
];

const ResponsiveDesignPage: React.FC = () => {
  const screens = useBreakpoint();
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [drawerVisible, setDrawerVisible] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const getCurrentBreakpoint = (): Breakpoint | undefined => {
    const activeBreakpoints = Object.entries(screens)
      .filter(([_, active]) => active)
      .map(([key]) => key);

    if (activeBreakpoints.includes('xxl')) return breakpoints[5];
    if (activeBreakpoints.includes('xl')) return breakpoints[4];
    if (activeBreakpoints.includes('lg')) return breakpoints[3];
    if (activeBreakpoints.includes('md')) return breakpoints[2];
    if (activeBreakpoints.includes('sm')) return breakpoints[1];
    return breakpoints[0];
  };

  const currentBreakpoint = getCurrentBreakpoint();
  const isMobile = !screens.md;

  const columns: ColumnsType<ResponsiveFeature> = [
    {
      title: 'Feature',
      dataIndex: 'feature',
      key: 'feature',
      fixed: isMobile ? undefined : 'left',
    },
    {
      title: 'Mobile',
      key: 'mobile',
      align: 'center',
      render: (_, record) =>
        record.mobile ? (
          <CheckCircleOutlined style={{ color: '#52c41a', fontSize: 18 }} />
        ) : (
          <CloseCircleOutlined style={{ color: '#ff4d4f', fontSize: 18 }} />
        ),
    },
    {
      title: 'Tablet',
      key: 'tablet',
      align: 'center',
      render: (_, record) =>
        record.tablet ? (
          <CheckCircleOutlined style={{ color: '#52c41a', fontSize: 18 }} />
        ) : (
          <CloseCircleOutlined style={{ color: '#ff4d4f', fontSize: 18 }} />
        ),
    },
    {
      title: 'Desktop',
      key: 'desktop',
      align: 'center',
      render: (_, record) =>
        record.desktop ? (
          <CheckCircleOutlined style={{ color: '#52c41a', fontSize: 18 }} />
        ) : (
          <CloseCircleOutlined style={{ color: '#ff4d4f', fontSize: 18 }} />
        ),
    },
    {
      title: 'Status',
      key: 'status',
      render: (_, record) => {
        const colors = {
          complete: 'green',
          partial: 'orange',
          pending: 'red',
        };
        return <Tag color={colors[record.status]}>{record.status.toUpperCase()}</Tag>;
      },
    },
  ];

  return (
    <div style={{ padding: isMobile ? 16 : 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={isMobile ? 4 : 3}>
          {currentBreakpoint?.icon} Responsive Design
        </Title>
        <Paragraph type="secondary">
          All features optimized for mobile, tablet, and desktop devices
        </Paragraph>
      </div>

      {/* Current Viewport Info */}
      <Alert
        message="Current Viewport"
        description={
          <Space direction="vertical" style={{ width: '100%' }}>
            <Space>
              <Text strong>Breakpoint:</Text>
              <Tag color={currentBreakpoint?.color} icon={currentBreakpoint?.icon}>
                {currentBreakpoint?.name}
              </Tag>
            </Space>
            <Space>
              <Text strong>Screen Width:</Text>
              <Text code>{windowWidth}px</Text>
            </Space>
            <Space wrap>
              <Text strong>Active:</Text>
              {Object.entries(screens)
                .filter(([_, active]) => active)
                .map(([key]) => (
                  <Tag key={key} color="blue">
                    {key}
                  </Tag>
                ))}
            </Space>
          </Space>
        }
        type="info"
        showIcon
        icon={<InfoCircleOutlined />}
        style={{ marginBottom: 24 }}
      />

      {/* Breakpoint Details */}
      <Card title="Breakpoint Details" style={{ marginBottom: 24 }}>
        <Row gutter={[16, 16]}>
          {breakpoints.map((bp) => {
            const isActive = screens[bp.key as keyof typeof screens];
            return (
              <Col xs={24} sm={12} md={8} lg={6} xl={4} key={bp.key}>
                <Card
                  size="small"
                  style={{
                    borderColor: isActive ? bp.color : '#d9d9d9',
                    background: isActive ? `${bp.color}10` : 'white',
                  }}
                >
                  <Space direction="vertical" align="center" style={{ width: '100%' }}>
                    <div style={{ fontSize: 32, color: bp.color }}>{bp.icon}</div>
                    <Text strong>{bp.name}</Text>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                       {bp.minWidth}px
                    </Text>
                    {isActive && (
                      <Tag color={bp.color} style={{ margin: 0 }}>
                        Active
                      </Tag>
                    )}
                  </Space>
                </Card>
              </Col>
            );
          })}
        </Row>
      </Card>

      {/* Responsive Features Table */}
      <Card
        title="Feature Support Matrix"
        extra={
          isMobile && (
            <Button type="primary" size="small" onClick={() => setDrawerVisible(true)}>
              View Details
            </Button>
          )
        }
        style={{ marginBottom: 24 }}
      >
        {isMobile ? (
          <List
            dataSource={responsiveFeatures}
            renderItem={(item) => (
              <List.Item>
                <List.Item.Meta
                  title={item.feature}
                  description={
                    <Space size={4}>
                      <Tag color={item.mobile ? 'green' : 'red'} style={{ fontSize: 10 }}>
                        Mobile
                      </Tag>
                      <Tag color={item.tablet ? 'green' : 'red'} style={{ fontSize: 10 }}>
                        Tablet
                      </Tag>
                      <Tag color={item.desktop ? 'green' : 'red'} style={{ fontSize: 10 }}>
                        Desktop
                      </Tag>
                    </Space>
                  }
                />
                <Tag
                  color={
                    item.status === 'complete' ? 'green' : item.status === 'partial' ? 'orange' : 'red'
                  }
                >
                  {item.status}
                </Tag>
              </List.Item>
            )}
          />
        ) : (
          <Table
            columns={columns}
            dataSource={responsiveFeatures}
            rowKey="feature"
            pagination={false}
            scroll={{ x: 800 }}
          />
        )}
      </Card>

      {/* Responsive Guidelines */}
      <Card title="Responsive Design Guidelines">
        <Row gutter={[16, 16]}>
          <Col xs={24} md={12}>
            <Card type="inner" title="Mobile First (XS/SM)">
              <Space direction="vertical" style={{ width: '100%' }}>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Single column layouts</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Touch-friendly buttons (min 44px)</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Collapsible menus & drawers</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Simplified navigation</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Bottom navigation on mobile</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Card-based content display</Text>
                </div>
              </Space>
            </Card>
          </Col>

          <Col xs={24} md={12}>
            <Card type="inner" title="Tablet (MD)">
              <Space direction="vertical" style={{ width: '100%' }}>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>2-column grid layouts</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Expanded side navigation</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Hybrid touch & mouse support</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Responsive tables with scroll</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Optimized forms & modals</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Better data visualization</Text>
                </div>
              </Space>
            </Card>
          </Col>

          <Col xs={24} md={12}>
            <Card type="inner" title="Desktop (LG/XL)">
              <Space direction="vertical" style={{ width: '100%' }}>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Multi-column layouts (3-4 columns)</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Fixed sidebar navigation</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Hover states & tooltips</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Full-featured data tables</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Advanced charts & graphs</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Keyboard shortcuts support</Text>
                </div>
              </Space>
            </Card>
          </Col>

          <Col xs={24} md={12}>
            <Card type="inner" title="Extra Large (XXL)">
              <Space direction="vertical" style={{ width: '100%' }}>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Wide layouts (max-width: 1600px)</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Enhanced spacing & whitespace</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Multi-panel dashboards</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Split-screen interfaces</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Large data visualizations</Text>
                </div>
                <div>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  <Text>Rich media galleries</Text>
                </div>
              </Space>
            </Card>
          </Col>
        </Row>
      </Card>

      {/* Mobile Drawer */}
      {isMobile && (
        <Drawer
          title="Feature Details"
          placement="bottom"
          onClose={() => setDrawerVisible(false)}
          open={drawerVisible}
          height="70%"
        >
          <List
            dataSource={responsiveFeatures}
            renderItem={(item) => (
              <List.Item>
                <Descriptions column={1} size="small" style={{ width: '100%' }}>
                  <Descriptions.Item label="Feature">{item.feature}</Descriptions.Item>
                  <Descriptions.Item label="Mobile">
                    {item.mobile ? (
                      <Tag color="green">Supported</Tag>
                    ) : (
                      <Tag color="red">Not Supported</Tag>
                    )}
                  </Descriptions.Item>
                  <Descriptions.Item label="Tablet">
                    {item.tablet ? (
                      <Tag color="green">Supported</Tag>
                    ) : (
                      <Tag color="red">Not Supported</Tag>
                    )}
                  </Descriptions.Item>
                  <Descriptions.Item label="Desktop">
                    {item.desktop ? (
                      <Tag color="green">Supported</Tag>
                    ) : (
                      <Tag color="red">Not Supported</Tag>
                    )}
                  </Descriptions.Item>
                  <Descriptions.Item label="Status">
                    <Tag
                      color={
                        item.status === 'complete'
                          ? 'green'
                          : item.status === 'partial'
                          ? 'orange'
                          : 'red'
                      }
                    >
                      {item.status.toUpperCase()}
                    </Tag>
                  </Descriptions.Item>
                </Descriptions>
              </List.Item>
            )}
          />
        </Drawer>
      )}
    </div>
  );
};

export default ResponsiveDesignPage;

checkout/payment-integration.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Radio,
  Space,
  Button,
  Form,
  Input,
  Select,
  Divider,
  Alert,
  Switch,
  Tag,
  Statistic,
  message,
  Modal,
  List,
  Avatar,
} from 'antd';
import {
  CreditCardOutlined,
  BankOutlined,
  DollarOutlined,
  SafetyOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  WarningOutlined,
  PayCircleOutlined,
  WalletOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;

interface PaymentMethod {
  id: string;
  type: 'card' | 'paypal' | 'bank' | 'wallet' | 'cod';
  name: string;
  description: string;
  icon: React.ReactNode;
  enabled: boolean;
  processingFee: number;
  processingTime: string;
}

interface SavedCard {
  id: number;
  type: 'visa' | 'mastercard' | 'amex';
  last4: string;
  expiryMonth: number;
  expiryYear: number;
  isDefault: boolean;
  holderName: string;
}

const paymentMethods: PaymentMethod[] = [
  {
    id: 'stripe',
    type: 'card',
    name: 'Credit/Debit Card',
    description: 'Pay securely with Stripe. We accept Visa, Mastercard, and American Express.',
    icon: <CreditCardOutlined />,
    enabled: true,
    processingFee: 2.9,
    processingTime: 'Instant',
  },
  {
    id: 'paypal',
    type: 'paypal',
    name: 'PayPal',
    description: 'Pay with your PayPal account or PayPal Credit.',
    icon: <PayCircleOutlined />,
    enabled: true,
    processingFee: 3.49,
    processingTime: 'Instant',
  },
  {
    id: 'bank_transfer',
    type: 'bank',
    name: 'Bank Transfer',
    description: 'Direct bank transfer. Order will be processed after payment verification.',
    icon: <BankOutlined />,
    enabled: true,
    processingFee: 0,
    processingTime: '1-3 business days',
  },
  {
    id: 'wallet',
    type: 'wallet',
    name: 'Digital Wallet',
    description: 'Use your account balance for instant payment.',
    icon: <WalletOutlined />,
    enabled: true,
    processingFee: 0,
    processingTime: 'Instant',
  },
  {
    id: 'cod',
    type: 'cod',
    name: 'Cash on Delivery',
    description: 'Pay when your order is delivered. Additional fees may apply.',
    icon: <DollarOutlined />,
    enabled: true,
    processingFee: 5,
    processingTime: 'On delivery',
  },
];

const mockSavedCards: SavedCard[] = [
  {
    id: 1,
    type: 'visa',
    last4: '4242',
    expiryMonth: 12,
    expiryYear: 2025,
    isDefault: true,
    holderName: 'John Doe',
  },
  {
    id: 2,
    type: 'mastercard',
    last4: '5555',
    expiryMonth: 8,
    expiryYear: 2026,
    isDefault: false,
    holderName: 'John Doe',
  },
];

const PaymentIntegrationPage: React.FC = () => {
  const [selectedMethod, setSelectedMethod] = useState<string>('stripe');
  const [savedCards, setSavedCards] = useState<SavedCard[]>(mockSavedCards);
  const [selectedCard, setSelectedCard] = useState<number | null>(1);
  const [saveCard, setSaveCard] = useState(false);
  const [amount] = useState(299.99);
  const [form] = Form.useForm();

  const currentMethod = paymentMethods.find((m) => m.id === selectedMethod);
  const processingFee = currentMethod ? (amount * currentMethod.processingFee) / 100 : 0;
  const totalAmount = amount + processingFee;

  const handlePayment = () => {
    if (selectedMethod === 'stripe') {
      if (selectedCard) {
        // Use saved card
        message.success('Payment processed successfully with saved card!');
      } else {
        // New card
        form.validateFields().then((values) => {
          message.success('Payment processed successfully!');
          if (saveCard) {
            const newCard: SavedCard = {
              id: Date.now(),
              type: 'visa',
              last4: values.cardNumber.slice(-4),
              expiryMonth: parseInt(values.expiry.split('/')[0]),
              expiryYear: parseInt('20' + values.expiry.split('/')[1]),
              isDefault: savedCards.length === 0,
              holderName: values.cardHolder,
            };
            setSavedCards([...savedCards, newCard]);
          }
        });
      }
    } else if (selectedMethod === 'paypal') {
      // Simulate PayPal redirect
      message.info('Redirecting to PayPal...');
      setTimeout(() => {
        message.success('PayPal payment completed!');
      }, 2000);
    } else {
      message.success(`Payment initiated via ${currentMethod?.name}`);
    }
  };

  const handleSetDefaultCard = (id: number) => {
    setSavedCards((prev) =>
      prev.map((card) => ({ ...card, isDefault: card.id === id }))
    );
    message.success('Default payment method updated');
  };

  const handleDeleteCard = (id: number) => {
    Modal.confirm({
      title: 'Delete Card',
      content: 'Are you sure you want to remove this card?',
      okText: 'Delete',
      okType: 'danger',
      onOk: () => {
        setSavedCards((prev) => prev.filter((card) => card.id !== id));
        if (selectedCard === id) setSelectedCard(null);
        message.success('Card removed successfully');
      },
    });
  };

  const getCardIcon = (type: string) => {
    switch (type) {
      case 'visa':
        return ' Visa';
      case 'mastercard':
        return ' Mastercard';
      case 'amex':
        return ' Amex';
      default:
        return '';
    }
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <CreditCardOutlined style={{ color: '#1890ff' }} /> Payment Integration
        </Title>
        <Paragraph type="secondary">
          Secure payment processing with multiple payment gateways
        </Paragraph>
      </div>

      <Alert
        message="Secure Payments"
        description="All transactions are encrypted and secure. We never store your full card details."
        type="info"
        showIcon
        icon={<SafetyOutlined />}
        style={{ marginBottom: 24 }}
      />

      <Row gutter={16}>
        {/* Payment Method Selection */}
        <Col xs={24} lg={16}>
          <Card title="Select Payment Method" style={{ marginBottom: 16 }}>
            <Radio.Group
              value={selectedMethod}
              onChange={(e) => setSelectedMethod(e.target.value)}
              style={{ width: '100%' }}
            >
              <Space direction="vertical" style={{ width: '100%' }} size="middle">
                {paymentMethods.map((method) => (
                  <Card
                    key={method.id}
                    size="small"
                    hoverable
                    style={{
                      border: selectedMethod === method.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                      background: selectedMethod === method.id ? '#e6f7ff' : 'white',
                    }}
                  >
                    <Radio value={method.id} style={{ width: '100%' }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Space>
                          <div style={{ fontSize: 24, color: '#1890ff' }}>{method.icon}</div>
                          <div>
                            <Text strong>{method.name}</Text>
                            <div>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {method.description}
                              </Text>
                            </div>
                          </div>
                        </Space>
                        <Space direction="vertical" align="end" size={0}>
                          <Tag color={method.enabled ? 'green' : 'red'}>
                            {method.enabled ? 'Available' : 'Unavailable'}
                          </Tag>
                          <Text type="secondary" style={{ fontSize: 11 }}>
                            Fee: {method.processingFee}%
                          </Text>
                        </Space>
                      </div>
                    </Radio>
                  </Card>
                ))}
              </Space>
            </Radio.Group>
          </Card>

          {/* Payment Details */}
          {selectedMethod === 'stripe' && (
            <Card title="Payment Details">
              {savedCards.length > 0 && (
                <>
                  <Text strong style={{ display: 'block', marginBottom: 12 }}>
                    Saved Cards
                  </Text>
                  <Radio.Group
                    value={selectedCard}
                    onChange={(e) => setSelectedCard(e.target.value)}
                    style={{ width: '100%', marginBottom: 16 }}
                  >
                    <Space direction="vertical" style={{ width: '100%' }}>
                      {savedCards.map((card) => (
                        <Card
                          key={card.id}
                          size="small"
                          style={{
                            border: selectedCard === card.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                          }}
                        >
                          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Radio value={card.id}>
                              <Space>
                                <Text>{getCardIcon(card.type)}</Text>
                                <Text> {card.last4}</Text>
                                <Text type="secondary">
                                  {card.expiryMonth}/{card.expiryYear}
                                </Text>
                                {card.isDefault && <Tag color="blue">Default</Tag>}
                              </Space>
                            </Radio>
                            <Space>
                              {!card.isDefault && (
                                <Button size="small" onClick={() => handleSetDefaultCard(card.id)}>
                                  Set Default
                                </Button>
                              )}
                              <Button size="small" danger onClick={() => handleDeleteCard(card.id)}>
                                Remove
                              </Button>
                            </Space>
                          </div>
                        </Card>
                      ))}
                      <Radio value={null}>
                        <Text type="secondary">Use a different card</Text>
                      </Radio>
                    </Space>
                  </Radio.Group>
                  <Divider />
                </>
              )}

              {selectedCard === null && (
                <Form form={form} layout="vertical">
                  <Form.Item
                    name="cardNumber"
                    label="Card Number"
                    rules={[
                      { required: true, message: 'Please enter card number' },
                      { pattern: /^\d{16}$/, message: 'Invalid card number' },
                    ]}
                  >
                    <Input
                      prefix={<CreditCardOutlined />}
                      placeholder="1234 5678 9012 3456"
                      maxLength={16}
                    />
                  </Form.Item>
                  <Form.Item
                    name="cardHolder"
                    label="Card Holder Name"
                    rules={[{ required: true, message: 'Please enter card holder name' }]}
                  >
                    <Input placeholder="John Doe" />
                  </Form.Item>
                  <Row gutter={16}>
                    <Col span={12}>
                      <Form.Item
                        name="expiry"
                        label="Expiry Date"
                        rules={[
                          { required: true, message: 'Required' },
                          { pattern: /^\d{2}\/\d{2}$/, message: 'MM/YY' },
                        ]}
                      >
                        <Input placeholder="MM/YY" maxLength={5} />
                      </Form.Item>
                    </Col>
                    <Col span={12}>
                      <Form.Item
                        name="cvv"
                        label="CVV"
                        rules={[
                          { required: true, message: 'Required' },
                          { pattern: /^\d{3,4}$/, message: 'Invalid CVV' },
                        ]}
                      >
                        <Input.Password placeholder="123" maxLength={4} />
                      </Form.Item>
                    </Col>
                  </Row>
                  <Form.Item>
                    <Switch checked={saveCard} onChange={setSaveCard} />
                    <Text style={{ marginLeft: 8 }}>Save card for future payments</Text>
                  </Form.Item>
                </Form>
              )}
            </Card>
          )}

          {selectedMethod === 'paypal' && (
            <Card>
              <Space direction="vertical" align="center" style={{ width: '100%', padding: '40px 0' }}>
                <PayCircleOutlined style={{ fontSize: 64, color: '#0070ba' }} />
                <Title level={4}>PayPal Checkout</Title>
                <Paragraph type="secondary" style={{ textAlign: 'center' }}>
                  You'll be redirected to PayPal to complete your payment securely
                </Paragraph>
              </Space>
            </Card>
          )}

          {selectedMethod === 'bank_transfer' && (
            <Card>
              <Alert
                message="Bank Transfer Instructions"
                description={
                  <div style={{ marginTop: 12 }}>
                    <Text strong>Bank Name:</Text> Example Bank
                    <br />
                    <Text strong>Account Number:</Text> 1234567890
                    <br />
                    <Text strong>Routing Number:</Text> 987654321
                    <br />
                    <Text strong>Reference:</Text> Your Order ID
                    <br />
                    <br />
                    <Text type="secondary">
                      Please include your order ID in the transfer reference. Your order will be
                      processed once payment is verified.
                    </Text>
                  </div>
                }
                type="warning"
                showIcon
              />
            </Card>
          )}

          {selectedMethod === 'wallet' && (
            <Card>
              <Space direction="vertical" style={{ width: '100%' }}>
                <Statistic
                  title="Available Balance"
                  value={1250.50}
                  prefix={<WalletOutlined />}
                  precision={2}
                  valueStyle={{ color: '#52c41a' }}
                />
                {amount > 1250.50 ? (
                  <Alert
                    message="Insufficient Balance"
                    description="You don't have enough balance. Please add funds to your wallet."
                    type="error"
                    showIcon
                  />
                ) : (
                  <Alert
                    message="Sufficient Balance"
                    description="You can complete this payment with your wallet balance."
                    type="success"
                    showIcon
                  />
                )}
              </Space>
            </Card>
          )}
        </Col>

        {/* Order Summary */}
        <Col xs={24} lg={8}>
          <Card title="Order Summary" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text>Subtotal</Text>
                <Text strong>${amount.toFixed(2)}</Text>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text>Processing Fee ({currentMethod?.processingFee}%)</Text>
                <Text>${processingFee.toFixed(2)}</Text>
              </div>
              <Divider style={{ margin: '8px 0' }} />
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text strong style={{ fontSize: 18 }}>Total</Text>
                <Text strong style={{ fontSize: 18, color: '#1890ff' }}>
                  ${totalAmount.toFixed(2)}
                </Text>
              </div>
              <Button
                type="primary"
                size="large"
                block
                icon={<SafetyOutlined />}
                onClick={handlePayment}
              >
                Pay ${totalAmount.toFixed(2)}
              </Button>
              <Text type="secondary" style={{ fontSize: 11, textAlign: 'center', display: 'block' }}>
                <SafetyOutlined /> Secured by 256-bit SSL encryption
              </Text>
            </Space>
          </Card>

          <Card title="Processing Time">
            <Space direction="vertical" style={{ width: '100%' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                <ClockCircleOutlined style={{ color: '#1890ff' }} />
                <Text>{currentMethod?.processingTime}</Text>
              </div>
              <Divider style={{ margin: '12px 0' }} />
              <Text type="secondary" style={{ fontSize: 12 }}>
                Your order will be processed immediately after payment confirmation.
              </Text>
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default PaymentIntegrationPage;

