const mockClaims: Claim[] = [
  {
    id: 1,
    warrantyId: 4,
    productName: 'Samsung 65" QLED TV',
    issueType: 'Screen damage',
    description: 'Vertical line appeared on the screen',
    status: 'processing',
    submittedDate: dayjs().subtract(3, 'days').format('YYYY-MM-DD'),
