import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Select,
  DatePicker,
  Input,
  message,
  Statistic,
  Progress,
  Tooltip,
  Badge,
  Modal,
  Descriptions,
  Spin,
  Form,
} from 'antd';
import {
  TeamOutlined,
  DollarOutlined,
  ShoppingOutlined,
  TrophyOutlined,
  UserAddOutlined,
  ShareAltOutlined,
  GiftOutlined,
  CopyOutlined,
  LinkOutlined,
  CheckCircleOutlined,
  ReloadOutlined,
  PlusOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { Line, Column } from '@ant-design/charts';
import { affiliateAPI } from '@/services/api/affiliate';
import type {
  AffiliateAccount,
  ReferralLink,
  Commission as AffiliateCommission,
  ReferralActivity,
  CreateReferralLinkData,
} from '@/services/api/affiliate';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;

interface Referral {
  id: number;
  name: string;
  email: string;
  status: 'pending' | 'active' | 'completed';
  signupDate: string;
  firstPurchaseDate: string;
  totalSpent: number;
  commission: number;
  tier: 'bronze' | 'silver' | 'gold' | 'platinum';
}

interface Commission {
  id: number;
  date: string;
  referralName: string;
  orderAmount: number;
  commissionRate: number;
  commissionAmount: number;
  status: 'pending' | 'approved' | 'paid';
}

const mockReferrals: Referral[] = [
  {
    id: 1,
    name: 'John Smith',
    email: 'john@example.com',
    status: 'completed',
    signupDate: dayjs().subtract(30, 'days').format('YYYY-MM-DD'),
    firstPurchaseDate: dayjs().subtract(28, 'days').format('YYYY-MM-DD'),
    totalSpent: 450,
    commission: 45,
    tier: 'gold',
  },
  {
    id: 2,
    name: 'Jane Doe',
    email: 'jane@example.com',
    status: 'active',
    signupDate: dayjs().subtract(15, 'days').format('YYYY-MM-DD'),
    firstPurchaseDate: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    totalSpent: 200,
    commission: 20,
    tier: 'silver',
  },
  {
    id: 3,
    name: 'Bob Wilson',
    email: 'bob@example.com',
    status: 'pending',
    signupDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    firstPurchaseDate: '',
    totalSpent: 0,
    commission: 0,
    tier: 'bronze',
  },
  {
    id: 4,
    name: 'Alice Brown',
    email: 'alice@example.com',
    status: 'completed',
    signupDate: dayjs().subtract(60, 'days').format('YYYY-MM-DD'),
    firstPurchaseDate: dayjs().subtract(55, 'days').format('YYYY-MM-DD'),
    totalSpent: 800,
    commission: 80,
    tier: 'platinum',
  },
];

const mockCommissions: Commission[] = [
  {
    id: 1,
    date: dayjs().subtract(2, 'days').format('YYYY-MM-DD'),
    referralName: 'John Smith',
    orderAmount: 150,
    commissionRate: 10,
    commissionAmount: 15,
    status: 'approved',
  },
  {
    id: 2,
    date: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    referralName: 'Alice Brown',
    orderAmount: 300,
    commissionRate: 10,
    commissionAmount: 30,
    status: 'paid',
  },
  {
    id: 3,
    date: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    referralName: 'Jane Doe',
    orderAmount: 100,
    commissionRate: 10,
    commissionAmount: 10,
    status: 'pending',
  },
];

const AffiliateReferralPage: React.FC = () => {
  // State management
  const [loading, setLoading] = useState<boolean>(false);
  const [affiliateAccount, setAffiliateAccount] = useState<AffiliateAccount | null>(null);
  const [referralLinks, setReferralLinks] = useState<ReferralLink[]>([]);
  const [commissions, setCommissions] = useState<AffiliateCommission[]>([]);
  const [referrals, setReferrals] = useState<any[]>([]);
  const [dashboardStats, setDashboardStats] = useState<any>(null);
  const [earningsStats, setEarningsStats] = useState<any>(null);

  // UI State
  const [isShareModalVisible, setIsShareModalVisible] = useState<boolean>(false);
  const [isLinkModalVisible, setIsLinkModalVisible] = useState<boolean>(false);
  const [form] = Form.useForm();

  // Get primary referral code and link
  const referralCode = affiliateAccount?.affiliateCode || 'REF-ABC123';
  const primaryLink = referralLinks.find(link => link.title === 'Primary') || 
    { referralUrl: 'https://example.com/ref/ABC123' };

  // Fetch affiliate data on mount
  useEffect(() => {
    fetchAffiliateData();
  }, []);

  const fetchAffiliateData = async () => {
    setLoading(true);
    try {
      const [accountRes, linksRes, commissionsRes, referralsRes, dashboardRes, earningsRes] = await Promise.all([
        affiliateAPI.getMyAffiliateAccount(),
        affiliateAPI.getReferralLinks(),
        affiliateAPI.getCommissions({ limit: 50 }),
        affiliateAPI.getReferrals({ limit: 50 }),
        affiliateAPI.getDashboardStats(),
        affiliateAPI.getEarningsStats(),
      ]);

      setAffiliateAccount(accountRes);
      setReferralLinks(linksRes);
      setCommissions(commissionsRes.data);
      setReferrals(referralsRes.data);
      setDashboardStats(dashboardRes);
      setEarningsStats(earningsRes);
    } catch (error) {
      // If APIs aren't available, continue with mock data
      console.warn('Affiliate APIs not available, using mock data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCopyCode = () => {
    navigator.clipboard.writeText(referralCode);
    message.success('Referral code copied to clipboard');
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(primaryLink.referralUrl);
    message.success('Referral link copied to clipboard');
  };

  const handleShareLink = () => {
    setIsShareModalVisible(true);
  };

  const handleCreateLink = async (values: CreateReferralLinkData) => {
    try {
      await affiliateAPI.createReferralLink(values);
      message.success('Referral link created successfully');
      setIsLinkModalVisible(false);
      form.resetFields();
      fetchAffiliateData();
    } catch (error) {
      message.error('Failed to create referral link');
      console.error(error);
    }
  };

  const referralColumns: ColumnsType<Referral> = [
    {
      title: 'Referral',
      key: 'referral',
      render: (_, record) => (
        <div>
          <Text strong>{record.name}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 12 }}>{record.email}</Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Referral['status']) => {
        const config: Record<Referral['status'], { color: string; text: string }> = {
          pending: { color: 'orange', text: 'Pending' },
          active: { color: 'blue', text: 'Active' },
          completed: { color: 'green', text: 'Completed' },
        };
        return <Tag color={config[status].color}>{config[status].text}</Tag>;
      },
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Active', value: 'active' },
        { text: 'Completed', value: 'completed' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Tier',
      dataIndex: 'tier',
      key: 'tier',
      render: (tier: Referral['tier']) => {
        const config: Record<Referral['tier'], { color: string; icon: React.ReactNode }> = {
          bronze: { color: '#cd7f32', icon: <TrophyOutlined /> },
          silver: { color: '#c0c0c0', icon: <TrophyOutlined /> },
          gold: { color: '#ffd700', icon: <TrophyOutlined /> },
          platinum: { color: '#e5e4e2', icon: <TrophyOutlined /> },
        };
        return (
          <Tag color={config[tier].color} icon={config[tier].icon}>
            {tier.toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: 'Signup Date',
      dataIndex: 'signupDate',
      key: 'signupDate',
      render: (date) => <Text>{dayjs(date).format('MMM DD, YYYY')}</Text>,
    },
    {
      title: 'Total Spent',
      dataIndex: 'totalSpent',
      key: 'totalSpent',
      render: (amount) => <Text strong>${amount}</Text>,
      sorter: (a, b) => a.totalSpent - b.totalSpent,
    },
    {
      title: 'Commission Earned',
      dataIndex: 'commission',
      key: 'commission',
      render: (amount) => (
        <Text strong style={{ color: '#52c41a' }}>
          ${amount}
        </Text>
      ),
      sorter: (a, b) => a.commission - b.commission,
    },
  ];

  const commissionColumns: ColumnsType<any> = [
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
      render: (date, record: any) => <Text>{dayjs(date || record.createdAt).format('MMM DD, YYYY')}</Text>,
    },
    {
      title: 'Referral',
      dataIndex: 'referralName',
      key: 'referralName',
      render: (name, record: any) => <Text>{name || record.customerEmail}</Text>,
    },
    {
      title: 'Order Amount',
      dataIndex: 'orderAmount',
      key: 'orderAmount',
      render: (amount, record: any) => <Text>${amount || record.orderValue}</Text>,
    },
    {
      title: 'Commission Rate',
      dataIndex: 'commissionRate',
      key: 'commissionRate',
      render: (rate) => <Text>{rate}%</Text>,
    },
    {
      title: 'Commission',
      dataIndex: 'commissionAmount',
      key: 'commissionAmount',
      render: (amount) => (
        <Text strong style={{ color: '#52c41a' }}>
          ${amount}
        </Text>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Commission['status']) => {
        const config: Record<Commission['status'], { color: string; text: string }> = {
          pending: { color: 'orange', text: 'Pending' },
          approved: { color: 'blue', text: 'Approved' },
          paid: { color: 'green', text: 'Paid' },
        };
        return <Tag color={config[status].color}>{config[status].text}</Tag>;
      },
    },
  ];

  // Calculate statistics using real data when available
  const totalReferrals = dashboardStats?.overview?.totalReferrals || 
    affiliateAccount?.totalReferrals || 
    mockReferrals.length;

  const activeReferrals = dashboardStats?.overview?.activeReferrals || 
    referrals.filter((r) => r.status === 'active').length ||
    mockReferrals.filter((r) => r.status === 'active').length;

  const totalCommission = earningsStats?.totalEarnings || 
    affiliateAccount?.totalEarnings || 
    mockReferrals.reduce((sum, r) => sum + r.commission, 0);

  const pendingCommission = earningsStats?.pendingEarnings || 
    affiliateAccount?.pendingEarnings ||
    mockCommissions
      .filter((c) => c.status === 'pending')
      .reduce((sum, c) => sum + c.commissionAmount, 0);

  const referralTrendData = [
    { month: 'Jan', referrals: 5 },
    { month: 'Feb', referrals: 8 },
    { month: 'Mar', referrals: 12 },
    { month: 'Apr', referrals: 15 },
    { month: 'May', referrals: 20 },
    { month: 'Jun', referrals: 25 },
  ];

  const commissionData = [
    { month: 'Jan', commission: 50 },
    { month: 'Feb', commission: 80 },
    { month: 'Mar', commission: 120 },
    { month: 'Apr', commission: 150 },
    { month: 'May', commission: 200 },
    { month: 'Jun', commission: 250 },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Spin spinning={loading}>
        <div style={{ marginBottom: 24, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <Title level={3}>
              <TeamOutlined style={{ color: '#1890ff' }} /> Affiliate & Referral Program
            </Title>
            <Paragraph type="secondary">
              Earn commissions by referring friends and family to our store
            </Paragraph>
          </div>
          <Button 
            icon={<ReloadOutlined />} 
            onClick={fetchAffiliateData}
            loading={loading}
          >
            Refresh
          </Button>
        </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Referrals"
              value={totalReferrals}
              prefix={<TeamOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Active Referrals"
              value={activeReferrals}
              prefix={<UserAddOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Commission"
              value={totalCommission}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Pending Commission"
              value={pendingCommission}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16} style={{ marginBottom: 16 }}>
        <Col xs={24} lg={12}>
          <Card title="Your Referral Code">
            <Space direction="vertical" size="large" style={{ width: '100%' }}>
              <div
                style={{
                  padding: '24px',
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  borderRadius: 8,
                  textAlign: 'center',
                }}
              >
                <Text style={{ color: 'white', fontSize: 12, display: 'block', marginBottom: 8 }}>
                  Your Referral Code
                </Text>
                <Text strong style={{ color: 'white', fontSize: 32, letterSpacing: 4 }}>
                  {referralCode}
                </Text>
              </div>

              <Space style={{ width: '100%', justifyContent: 'center' }} size="middle">
                <Button icon={<CopyOutlined />} onClick={handleCopyCode}>
                  Copy Code
                </Button>
                <Button type="primary" icon={<ShareAltOutlined />} onClick={handleShareLink}>
                  Share Link
                </Button>
              </Space>

              <div>
                <Text type="secondary" style={{ display: 'block', marginBottom: 8, fontSize: 12 }}>
                  Your Referral Link:
                </Text>
                <Input
                  value={primaryLink.referralUrl}
                  readOnly
                  addonAfter={
                    <Tooltip title="Copy Link">
                      <LinkOutlined onClick={handleCopyLink} style={{ cursor: 'pointer' }} />
                    </Tooltip>
                  }
                />
              </div>

              <div style={{ background: '#f0f2f5', padding: 16, borderRadius: 4 }}>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  How it works:
                </Text>
                <ol style={{ marginBottom: 0, paddingLeft: 20 }}>
                  <li>Share your referral code or link with friends</li>
                  <li>They sign up and make their first purchase</li>
                  <li>You earn 10% commission on their orders</li>
                  <li>Your friend gets 15% off their first order</li>
                </ol>
              </div>
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={12}>
          <Card title="Commission Tiers">
            <Space direction="vertical" size="middle" style={{ width: '100%' }}>
              <div>
                <Row justify="space-between" align="middle" style={{ marginBottom: 4 }}>
                  <Col>
                    <Space>
                      <TrophyOutlined style={{ color: '#cd7f32' }} />
                      <Text strong>Bronze (0-5 referrals)</Text>
                    </Space>
                  </Col>
                  <Col>
                    <Text>10% commission</Text>
                  </Col>
                </Row>
                <Progress percent={100} strokeColor="#cd7f32" showInfo={false} />
              </div>

              <div>
                <Row justify="space-between" align="middle" style={{ marginBottom: 4 }}>
                  <Col>
                    <Space>
                      <TrophyOutlined style={{ color: '#c0c0c0' }} />
                      <Text strong>Silver (6-15 referrals)</Text>
                    </Space>
                  </Col>
                  <Col>
                    <Text>12% commission</Text>
                  </Col>
                </Row>
                <Progress percent={40} strokeColor="#c0c0c0" showInfo={false} />
                <Text type="secondary" style={{ fontSize: 11 }}>
                  2 more referrals to unlock
                </Text>
              </div>

              <div>
                <Row justify="space-between" align="middle" style={{ marginBottom: 4 }}>
                  <Col>
                    <Space>
                      <TrophyOutlined style={{ color: '#ffd700' }} />
                      <Text strong>Gold (16-30 referrals)</Text>
                    </Space>
                  </Col>
                  <Col>
                    <Text>15% commission</Text>
                  </Col>
                </Row>
                <Progress percent={0} strokeColor="#ffd700" showInfo={false} />
                <Text type="secondary" style={{ fontSize: 11 }}>
                  Locked
                </Text>
              </div>

              <div>
                <Row justify="space-between" align="middle" style={{ marginBottom: 4 }}>
                  <Col>
                    <Space>
                      <TrophyOutlined style={{ color: '#e5e4e2' }} />
                      <Text strong>Platinum (31+ referrals)</Text>
                    </Space>
                  </Col>
                  <Col>
                    <Text>20% commission</Text>
                  </Col>
                </Row>
                <Progress percent={0} strokeColor="#e5e4e2" showInfo={false} />
                <Text type="secondary" style={{ fontSize: 11 }}>
                  Locked
                </Text>
              </div>

              <div style={{ background: '#f6ffed', padding: 12, borderRadius: 4, border: '1px solid #b7eb8f' }}>
                <Space>
                  <GiftOutlined style={{ color: '#52c41a' }} />
                  <div>
                    <Text strong style={{ color: '#52c41a', display: 'block', fontSize: 12 }}>
                      Bonus: Unlock Silver tier to get a $50 bonus!
                    </Text>
                  </div>
                </Space>
              </div>
            </Space>
          </Card>
        </Col>
      </Row>

      <Row gutter={16} style={{ marginBottom: 16 }}>
        <Col xs={24} lg={12}>
          <Card title="Referral Trend">
            <Line
              data={referralTrendData}
              xField="month"
              yField="referrals"
              height={250}
              point={{ size: 5, shape: 'diamond' }}
              label={{
                style: {
                  fill: '#1890ff',
                },
              }}
            />
          </Card>
        </Col>

        <Col xs={24} lg={12}>
          <Card title="Commission Earned">
            <Column
              data={commissionData}
              xField="month"
              yField="commission"
              height={250}
              label={{
                position: 'top',
                style: {
                  fill: '#52c41a',
                },
              }}
              columnStyle={{
                fill: 'l(270) 0:#52c41a 1:#95de64',
              }}
            />
          </Card>
        </Col>
      </Row>

      <Card title="My Referrals" style={{ marginBottom: 16 }}>
        <Table 
          columns={referralColumns} 
          dataSource={referrals.length > 0 ? referrals : mockReferrals} 
          rowKey="id" 
          pagination={false}
          loading={loading}
        />
      </Card>

      <Card title="Commission History">
        <Table 
          columns={commissionColumns} 
          dataSource={commissions.length > 0 ? commissions : mockCommissions} 
          rowKey="id" 
          pagination={false}
          loading={loading}
        />
      </Card>

      <Modal
        title="Share Your Referral Link"
        open={isShareModalVisible}
        onCancel={() => setIsShareModalVisible(false)}
        footer={null}
        width={500}
      >
        <Space direction="vertical" size="large" style={{ width: '100%' }}>
          <div>
            <Text type="secondary">Share via social media or email</Text>
          </div>

          <Input.TextArea
            value={`Join me on this amazing store! Use my referral code ${referralCode} and get 15% off your first order: ${primaryLink.referralUrl}`}
            rows={4}
            readOnly
          />

          <Row gutter={16}>
            <Col span={12}>
              <Button
                block
                icon={<ShareAltOutlined />}
                onClick={() => message.success('Sharing on Facebook')}
              >
                Facebook
              </Button>
            </Col>
            <Col span={12}>
              <Button
                block
                icon={<ShareAltOutlined />}
                onClick={() => message.success('Sharing on Twitter')}
              >
                Twitter
              </Button>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Button
                block
                icon={<ShareAltOutlined />}
                onClick={() => message.success('Copying WhatsApp message')}
              >
                WhatsApp
              </Button>
            </Col>
            <Col span={12}>
              <Button
                block
                icon={<ShareAltOutlined />}
                onClick={() => message.success('Opening email client')}
              >
                Email
              </Button>
            </Col>
          </Row>

          <Button type="primary" block icon={<CopyOutlined />} onClick={handleCopyLink}>
            Copy Link
          </Button>
        </Space>
      </Modal>
      </Spin>
    </div>
  );
};

export default AffiliateReferralPage;

customer/currency-converter.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Select,
  InputNumber,
  Space,
  Divider,
  Tag,
  Table,
  Alert,
} from 'antd';
import {
  DollarOutlined,
  SwapOutlined,
  InfoCircleOutlined,
  RiseOutlined,
  FallOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;

interface Currency {
  code: string;
  name: string;
  symbol: string;
  flag: string;
  rate: number;
}

interface ExchangeRate {
  from: string;
  to: string;
  rate: number;
  change: number;
  changePercent: number;
  lastUpdated: string;
}

const currencies: Currency[] = [
  { code: 'USD', name: 'US Dollar', symbol: '$', flag: '', rate: 1.0 },
  { code: 'EUR', name: 'Euro', symbol: '', flag: '', rate: 0.92 },
  { code: 'GBP', name: 'British Pound', symbol: '', flag: '', rate: 0.79 },
  { code: 'JPY', name: 'Japanese Yen', symbol: '', flag: '', rate: 149.50 },
  { code: 'CNY', name: 'Chinese Yuan', symbol: '', flag: '', rate: 7.24 },
  { code: 'INR', name: 'Indian Rupee', symbol: '', flag: '', rate: 83.12 },
  { code: 'AUD', name: 'Australian Dollar', symbol: 'A$', flag: '', rate: 1.53 },
  { code: 'CAD', name: 'Canadian Dollar', symbol: 'C$', flag: '', rate: 1.36 },
  { code: 'CHF', name: 'Swiss Franc', symbol: 'CHF', flag: '', rate: 0.88 },
  { code: 'HKD', name: 'Hong Kong Dollar', symbol: 'HK$', flag: '', rate: 7.83 },
  { code: 'SGD', name: 'Singapore Dollar', symbol: 'S$', flag: '', rate: 1.35 },
  { code: 'SEK', name: 'Swedish Krona', symbol: 'kr', flag: '', rate: 10.87 },
  { code: 'KRW', name: 'South Korean Won', symbol: '', flag: '', rate: 1320.50 },
  { code: 'NOK', name: 'Norwegian Krone', symbol: 'kr', flag: '', rate: 10.72 },
  { code: 'NZD', name: 'New Zealand Dollar', symbol: 'NZ$', flag: '', rate: 1.67 },
  { code: 'MXN', name: 'Mexican Peso', symbol: 'MX$', flag: '', rate: 17.15 },
  { code: 'BRL', name: 'Brazilian Real', symbol: 'R$', flag: '', rate: 4.98 },
  { code: 'ZAR', name: 'South African Rand', symbol: 'R', flag: '', rate: 18.65 },
];

const popularPairs: ExchangeRate[] = [
  {
    from: 'USD',
    to: 'EUR',
    rate: 0.92,
    change: 0.005,
    changePercent: 0.55,
    lastUpdated: dayjs().format('YYYY-MM-DD HH:mm:ss'),
  },
  {
    from: 'USD',
    to: 'GBP',
    rate: 0.79,
    change: -0.003,
    changePercent: -0.38,
    lastUpdated: dayjs().format('YYYY-MM-DD HH:mm:ss'),
  },
  {
    from: 'USD',
    to: 'JPY',
    rate: 149.50,
    change: 1.20,
    changePercent: 0.81,
    lastUpdated: dayjs().format('YYYY-MM-DD HH:mm:ss'),
  },
  {
    from: 'EUR',
    to: 'GBP',
    rate: 0.86,
    change: -0.002,
    changePercent: -0.23,
    lastUpdated: dayjs().format('YYYY-MM-DD HH:mm:ss'),
  },
  {
    from: 'GBP',
    to: 'USD',
    rate: 1.27,
    change: 0.004,
    changePercent: 0.32,
    lastUpdated: dayjs().format('YYYY-MM-DD HH:mm:ss'),
  },
];

const CurrencyConverterPage: React.FC = () => {
  const [fromCurrency, setFromCurrency] = useState<string>('USD');
  const [toCurrency, setToCurrency] = useState<string>('EUR');
  const [amount, setAmount] = useState<number>(100);
  const [convertedAmount, setConvertedAmount] = useState<number>(0);

  useEffect(() => {
    calculateConversion();
  }, [fromCurrency, toCurrency, amount]);

  const calculateConversion = () => {
    const fromRate = currencies.find((c) => c.code === fromCurrency)?.rate || 1;
    const toRate = currencies.find((c) => c.code === toCurrency)?.rate || 1;
    const result = (amount / fromRate) * toRate;
    setConvertedAmount(Number(result.toFixed(2)));
  };

  const handleSwapCurrencies = () => {
    const temp = fromCurrency;
    setFromCurrency(toCurrency);
    setToCurrency(temp);
  };

  const getExchangeRate = () => {
    const fromRate = currencies.find((c) => c.code === fromCurrency)?.rate || 1;
    const toRate = currencies.find((c) => c.code === toCurrency)?.rate || 1;
    return (toRate / fromRate).toFixed(4);
  };

  const getCurrencySymbol = (code: string) => {
    return currencies.find((c) => c.code === code)?.symbol || code;
  };

  const columns: ColumnsType<ExchangeRate> = [
    {
      title: 'Currency Pair',
      key: 'pair',
      render: (_, record) => (
        <Text strong>
          {record.from}/{record.to}
        </Text>
      ),
    },
    {
      title: 'Exchange Rate',
      dataIndex: 'rate',
      key: 'rate',
      render: (rate) => <Text>{rate.toFixed(4)}</Text>,
    },
    {
      title: 'Change',
      key: 'change',
      render: (_, record) => {
        const isPositive = record.change >= 0;
        return (
          <Space>
            {isPositive ? (
              <RiseOutlined style={{ color: '#52c41a' }} />
            ) : (
              <FallOutlined style={{ color: '#ff4d4f' }} />
            )}
            <Text style={{ color: isPositive ? '#52c41a' : '#ff4d4f' }}>
              {isPositive ? '+' : ''}
              {record.change.toFixed(4)} ({isPositive ? '+' : ''}
              {record.changePercent.toFixed(2)}%)
            </Text>
          </Space>
        );
      },
    },
    {
      title: 'Last Updated',
      dataIndex: 'lastUpdated',
      key: 'lastUpdated',
      render: (time) => <Text type="secondary">{dayjs(time).format('MMM DD, HH:mm')}</Text>,
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <DollarOutlined style={{ color: '#52c41a' }} /> Currency Converter
        </Title>
        <Paragraph type="secondary">
          Real-time currency conversion with up-to-date exchange rates
        </Paragraph>
      </div>

      <Row gutter={16}>
        <Col xs={24} lg={14}>
          <Card
            title="Convert Currency"
            extra={
              <Tag color="blue" icon={<InfoCircleOutlined />}>
                Updated {dayjs().format('MMM DD, YYYY HH:mm')}
              </Tag>
            }
          >
            <Space direction="vertical" size="large" style={{ width: '100%' }}>
              <Row gutter={16} align="middle">
                <Col xs={24} sm={11}>
                  <div style={{ marginBottom: 8 }}>
                    <Text type="secondary">From</Text>
                  </div>
                  <Select
                    size="large"
                    value={fromCurrency}
                    onChange={setFromCurrency}
                    style={{ width: '100%' }}
                    showSearch
                    optionFilterProp="children"
                  >
                    {currencies.map((currency) => (
                      <Option key={currency.code} value={currency.code}>
                        <Space>
                          <span>{currency.flag}</span>
                          <Text>{currency.code}</Text>
                          <Text type="secondary">- {currency.name}</Text>
                        </Space>
                      </Option>
                    ))}
                  </Select>
                  <div style={{ marginTop: 12 }}>
                    <InputNumber
                      size="large"
                      value={amount}
                      onChange={(value) => setAmount(value || 0)}
                      style={{ width: '100%' }}
                      min={0}
                      precision={2}
                      formatter={(value) => `${getCurrencySymbol(fromCurrency)} ${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                      parser={(value) => Number(value?.replace(/[^\d.]/g, '') || 0)}
                    />
                  </div>
                </Col>

                <Col xs={24} sm={2} style={{ textAlign: 'center' }}>
                  <div style={{ height: 80, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                    <SwapOutlined
                      style={{ fontSize: 24, color: '#1890ff', cursor: 'pointer' }}
                      onClick={handleSwapCurrencies}
                    />
                  </div>
                </Col>

                <Col xs={24} sm={11}>
                  <div style={{ marginBottom: 8 }}>
                    <Text type="secondary">To</Text>
                  </div>
                  <Select
                    size="large"
                    value={toCurrency}
                    onChange={setToCurrency}
                    style={{ width: '100%' }}
                    showSearch
                    optionFilterProp="children"
                  >
                    {currencies.map((currency) => (
                      <Option key={currency.code} value={currency.code}>
                        <Space>
                          <span>{currency.flag}</span>
                          <Text>{currency.code}</Text>
                          <Text type="secondary">- {currency.name}</Text>
                        </Space>
                      </Option>
                    ))}
                  </Select>
                  <div style={{ marginTop: 12 }}>
                    <div
                      style={{
                        height: 50,
                        background: '#f0f2f5',
                        borderRadius: 8,
                        display: 'flex',
                        alignItems: 'center',
                        padding: '0 16px',
                      }}
                    >
                      <Text strong style={{ fontSize: 20, color: '#52c41a' }}>
                        {getCurrencySymbol(toCurrency)} {convertedAmount.toLocaleString()}
                      </Text>
                    </div>
                  </div>
                </Col>
              </Row>

              <Divider />

              <div
                style={{
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  padding: 24,
                  borderRadius: 12,
                  color: 'white',
                }}
              >
                <Row justify="space-between" align="middle">
                  <Col>
                    <div>
                      <Text style={{ color: 'white', opacity: 0.9, fontSize: 14 }}>Exchange Rate</Text>
                    </div>
                    <div>
                      <Text strong style={{ color: 'white', fontSize: 28 }}>
                        1 {fromCurrency} = {getExchangeRate()} {toCurrency}
                      </Text>
                    </div>
                  </Col>
                  <Col>
                    <div style={{ textAlign: 'right' }}>
                      <Text style={{ color: 'white', opacity: 0.9, fontSize: 14 }}>Inverse Rate</Text>
                      <div>
                        <Text strong style={{ color: 'white', fontSize: 20 }}>
                          1 {toCurrency} = {(1 / Number(getExchangeRate())).toFixed(4)} {fromCurrency}
                        </Text>
                      </div>
                    </div>
                  </Col>
                </Row>
              </div>

              <Alert
                message="Currency Conversion Information"
                description={
                  <ul style={{ marginBottom: 0, paddingLeft: 20 }}>
                    <li>Exchange rates are updated every 15 minutes</li>
                    <li>Actual rates may vary slightly based on payment method</li>
                    <li>Additional fees may apply for international transactions</li>
                    <li>Rates shown are for reference purposes only</li>
                  </ul>
                }
                type="info"
                showIcon
              />
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={10}>
          <Card title="Popular Currency Pairs" style={{ marginBottom: 16 }}>
            <Table
              columns={columns}
              dataSource={popularPairs}
              rowKey={(record) => `${record.from}-${record.to}`}
              pagination={false}
              size="small"
            />
          </Card>

          <Card title="Quick Conversions">
            <Space direction="vertical" size="small" style={{ width: '100%' }}>
              {[10, 50, 100, 500, 1000].map((value) => {
                const fromRate = currencies.find((c) => c.code === fromCurrency)?.rate || 1;
                const toRate = currencies.find((c) => c.code === toCurrency)?.rate || 1;
                const converted = ((value / fromRate) * toRate).toFixed(2);
                return (
                  <Row key={value} justify="space-between" align="middle" style={{ padding: '8px 0' }}>
                    <Col>
                      <Text>
                        {getCurrencySymbol(fromCurrency)} {value.toLocaleString()}
                      </Text>
                    </Col>
                    <Col>
                      <SwapOutlined style={{ color: '#1890ff' }} />
                    </Col>
                    <Col>
                      <Text strong style={{ color: '#52c41a' }}>
                        {getCurrencySymbol(toCurrency)} {Number(converted).toLocaleString()}
                      </Text>
                    </Col>
                  </Row>
                );
              })}
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default CurrencyConverterPage;

customer/order-tracking-map.__content:
import React, { useState, useEffect, useCallback } from 'react';
import { Card, Input, Button, Timeline, Badge, Row, Col, Space, Avatar, Rate, Modal, Switch, message, Descriptions, Tag, Divider } from 'antd';
import { SearchOutlined, EnvironmentOutlined, PhoneOutlined, CarOutlined, BellOutlined, EyeOutlined, ClockCircleOutlined, InboxOutlined } from '@ant-design/icons';
import { orderTrackingAPI, type OrderTracking } from '../../services/api/orderTrackingAPI';
// import './order-tracking.less';

const { Search } = Input;

// Enhanced Order Tracking Component with Real-time Updates
const EnhancedOrderTracking: React.FC = () => {
  const [searchValue, setSearchValue] = useState('');
  const [loading, setLoading] = useState(false);
  const [trackingData, setTrackingData] = useState<OrderTracking | null>(null);
  const [realTimeLocation, setRealTimeLocation] = useState<any>(null);
  const [showNotificationModal, setShowNotificationModal] = useState(false);
  const [notificationPrefs, setNotificationPrefs] = useState({
    email: true,
    sms: false,
    push: true
  });
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Auto-refresh real-time location every 30 seconds
  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (autoRefresh && trackingData?.trackingNumber) {
      interval = setInterval(async () => {
        try {
          const response = await orderTrackingAPI.getRealTimeLocation(trackingData.trackingNumber!);
          if (response.success) {
            setRealTimeLocation(response.data);
          }
        } catch (error) {
          console.error('Error updating real-time location:', error);
        }
      }, 30000); // 30 seconds
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [autoRefresh, trackingData?.trackingNumber]);

  const handleSearch = async (value: string) => {
    if (!value.trim()) {
      message.warning('Please enter an order number or tracking number');
      return;
    }

    setLoading(true);
    try {
      const response = await orderTrackingAPI.getOrderTracking(value);
      if (response.success && response.data) {
        setTrackingData(response.data);

        // Get initial real-time location if shipped
        if (response.data.status === 'shipped' || response.data.status === 'out_for_delivery') {
          const locationResponse = await orderTrackingAPI.getRealTimeLocation(response.data.trackingNumber!);
          if (locationResponse.success) {
            setRealTimeLocation(locationResponse.data);
          }
        }

        message.success('Order tracking information loaded');
      } else {
        message.error(response.message || 'Order not found');
        setTrackingData(null);
      }
    } catch (error) {
      console.error('Error searching order:', error);
      message.error('Failed to search order');
    } finally {
      setLoading(false);
    }
  };

  const handleNotificationUpdate = async () => {
    if (!trackingData) return;

    try {
      const response = await orderTrackingAPI.subscribeToNotifications(
        trackingData.orderId,
        notificationPrefs
      );

      if (response.success) {
        message.success('Notification preferences updated');
        setShowNotificationModal(false);
      } else {
        message.error('Failed to update preferences');
      }
    } catch (error) {
      console.error('Error updating notifications:', error);
      message.error('Failed to update preferences');
    }
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      placed: 'default',
      confirmed: 'processing',
      processing: 'warning',
      shipped: 'blue',
      out_for_delivery: 'orange',
      delivered: 'success',
      cancelled: 'error'
    };
    return colors[status] || 'default';
  };

  const getStatusIcon = (status: string) => {
    const icons: Record<string, React.ReactNode> = {
      placed: <ClockCircleOutlined />,
      confirmed: <ClockCircleOutlined />,
      processing: <InboxOutlined />,
      shipped: <CarOutlined />,
      out_for_delivery: <EnvironmentOutlined />,
      delivered: <InboxOutlined />,
      cancelled: <ClockCircleOutlined />
    };
    return icons[status] || <ClockCircleOutlined />;
  };

  const renderTrackingTimeline = () => {
    if (!trackingData) return null;

    const timelineItems = trackingData.updates.map((update, index) => ({
      key: update.id,
      dot: getStatusIcon(update.status),
      color: getStatusColor(update.status),
      children: (
        <div>
          <div style={{ fontWeight: 500, marginBottom: 4 }}>
            {update.description}
          </div>
          <div style={{ color: '#666', fontSize: '12px', marginBottom: 4 }}>
            <EnvironmentOutlined style={{ marginRight: 4 }} />
            {update.location}
          </div>
          <div style={{ color: '#999', fontSize: '11px' }}>
            {new Date(update.timestamp).toLocaleString()}
          </div>
          {update.trackingNumber && (
            <div style={{ color: '#1890ff', fontSize: '11px', marginTop: 2 }}>
              Tracking: {update.trackingNumber}
            </div>
          )}
        </div>
      )
    }));

    return (
      <Timeline
        mode="left"
        items={timelineItems}
        style={{ marginTop: 16 }}
      />
    );
  };

  const renderDeliveryAgent = () => {
    if (!trackingData?.deliveryAgent) return null;

    const { deliveryAgent } = trackingData;

    return (
      <Card title="Delivery Agent" size="small" style={{ marginTop: 16 }}>
        <Row gutter={16} align="middle">
          <Col>
            <Avatar size={64} src={deliveryAgent.photo} icon={<CarOutlined />} />
          </Col>
          <Col flex={1}>
            <div style={{ fontWeight: 500, fontSize: '16px' }}>
              {deliveryAgent.name}
            </div>
            <div style={{ color: '#666', marginBottom: 4 }}>
              <PhoneOutlined style={{ marginRight: 4 }} />
              {deliveryAgent.phone}
            </div>
            <div style={{ marginBottom: 4 }}>
              <Rate disabled value={deliveryAgent.rating} allowHalf />
              <span style={{ marginLeft: 8, color: '#666' }}>
                {deliveryAgent.rating}/5
              </span>
            </div>
            {deliveryAgent.vehicleInfo && (
              <div style={{ color: '#666', fontSize: '12px' }}>
                <CarOutlined style={{ marginRight: 4 }} />
                {deliveryAgent.vehicleInfo}
              </div>
            )}
          </Col>
          <Col>
            <Button type="primary" ghost icon={<PhoneOutlined />}>
              Call
            </Button>
          </Col>
        </Row>
      </Card>
    );
  };

  const renderRealTimeLocation = () => {
    if (!realTimeLocation) return null;

    return (
      <Card 
        title="Real-time Location" 
        size="small" 
        style={{ marginTop: 16 }}
        extra={
          <Switch
            checkedChildren="Auto-refresh"
            unCheckedChildren="Manual"
            checked={autoRefresh}
            onChange={setAutoRefresh}
          />
        }
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          <div>
            <EnvironmentOutlined style={{ color: '#1890ff', marginRight: 8 }} />
            <strong>Current Location:</strong> {realTimeLocation.currentLocation.address}
          </div>
          <div>
            <ClockCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
            <strong>Estimated Arrival:</strong> {new Date(realTimeLocation.estimatedArrival).toLocaleString()}
          </div>
          <div style={{ fontSize: '12px', color: '#666' }}>
            Last updated: {new Date(realTimeLocation.currentLocation.timestamp).toLocaleString()}
          </div>
          <Button
            type="dashed"
            icon={<EyeOutlined />}
            onClick={() => {
              // In a real implementation, this would open a map modal
              message.info('Map view would open here');
            }}
          >
            View on Map
          </Button>
        </Space>
      </Card>
    );
  };

  const renderPackageInfo = () => {
    if (!trackingData?.packages?.length) return null;

    return (
      <Card title="Package Information" size="small" style={{ marginTop: 16 }}>
        {trackingData.packages.map((pkg, index) => (
          <div key={pkg.id} style={{ marginBottom: index < trackingData.packages.length - 1 ? 16 : 0 }}>
            <Descriptions size="small" column={2}>
              <Descriptions.Item label="Weight">{pkg.weight}</Descriptions.Item>
              <Descriptions.Item label="Dimensions">{pkg.dimensions}</Descriptions.Item>
            </Descriptions>

            <Divider style={{ margin: '8px 0' }} />

            <div style={{ fontWeight: 500, marginBottom: 8 }}>Items in Package:</div>
            {pkg.items.map((item, itemIndex) => (
              <Row key={itemIndex} gutter={8} style={{ marginBottom: 4 }}>
                <Col>
                  <Avatar size="small" src={item.image} icon={<InboxOutlined />} />
                </Col>
                <Col flex={1}>
                  <span>{item.name}</span>
                  <Tag style={{ marginLeft: 8 }}>
                    Qty: {item.quantity}
                  </Tag>
                </Col>
              </Row>
            ))}
          </div>
        ))}
      </Card>
    );
  };

  return (
    <div className="enhanced-order-tracking" style={{ padding: '24px', maxWidth: 1200, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <h1 style={{ fontSize: '28px', fontWeight: 600, marginBottom: 8 }}>
          Order Tracking
        </h1>
        <p style={{ color: '#666', fontSize: '16px' }}>
          Track your orders in real-time with detailed updates and delivery information
        </p>
      </div>

      {/* Search Section */}
      <Card style={{ marginBottom: 24 }}>
        <Space direction="vertical" style={{ width: '100%' }}>
          <div style={{ fontWeight: 500, marginBottom: 8 }}>
            Enter Order Number or Tracking Number
          </div>
          <Search
            placeholder="e.g., ORD-123456 or TRK123456"
            enterButton={<Button type="primary" icon={<SearchOutlined />}>Track Order</Button>}
            size="large"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
            onSearch={handleSearch}
            loading={loading}
          />
          <div style={{ fontSize: '12px', color: '#666' }}>
            You can also track using your email address or phone number
          </div>
        </Space>
      </Card>

      {/* Tracking Results */}
      {trackingData && (
        <Row gutter={[16, 16]}>
          <Col xs={24} lg={14}>
            {/* Order Summary */}
            <Card
              title={
                <Space>
                  <span>Order {trackingData.orderNumber}</span>
                  <Badge
                    status={getStatusColor(trackingData.status) as any}
                    text={trackingData.status.toUpperCase()}
                  />
                </Space>
              }
              extra={
                <Button
                  icon={<BellOutlined />}
                  onClick={() => setShowNotificationModal(true)}
                >
                  Notifications
                </Button>
              }
            >
              <Row gutter={[16, 8]}>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Tracking Number</div>
                  <div style={{ fontWeight: 500 }}>{trackingData.trackingNumber}</div>
                </Col>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Carrier</div>
                  <div style={{ fontWeight: 500 }}>{trackingData.carrier}</div>
                </Col>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Estimated Delivery</div>
                  <div style={{ fontWeight: 500, color: '#52c41a' }}>
                    {new Date(trackingData.estimatedDelivery).toLocaleDateString()}
                  </div>
                </Col>
                <Col span={12}>
                  <div style={{ color: '#666' }}>Delivery Address</div>
                  <div style={{ fontWeight: 500 }}>
                    {trackingData.shippingAddress.street}, {trackingData.shippingAddress.city}
                  </div>
                </Col>
              </Row>
            </Card>

            {/* Tracking Timeline */}
            <Card title="Tracking History" style={{ marginTop: 16 }}>
              {renderTrackingTimeline()}
            </Card>

            {/* Package Information */}
            {renderPackageInfo()}
          </Col>

          <Col xs={24} lg={10}>
            {/* Real-time Location */}
            {(trackingData.status === 'shipped' || trackingData.status === 'out_for_delivery') && renderRealTimeLocation()}

            {/* Delivery Agent */}
            {trackingData.status === 'out_for_delivery' && renderDeliveryAgent()}

            {/* Quick Actions */}
            <Card title="Quick Actions" size="small" style={{ marginTop: 16 }}>
              <Space direction="vertical" style={{ width: '100%' }}>
                <Button block icon={<PhoneOutlined />}>
                  Contact Support
                </Button>
                <Button block icon={<EnvironmentOutlined />}>
                  Update Delivery Address
                </Button>
                <Button block icon={<ClockCircleOutlined />}>
                  Reschedule Delivery
                </Button>
                <Button block>
                  Download Invoice
                </Button>
              </Space>
            </Card>

            {/* Delivery Instructions */}
            <Card title="Delivery Instructions" size="small" style={{ marginTop: 16 }}>
              <div style={{ fontSize: '12px', color: '#666', lineHeight: 1.5 }}>
                 Please deliver to the front door
                 Call upon arrival
                 Leave package with security if not home
                 Signature required for delivery
              </div>
            </Card>
          </Col>
        </Row>
      )}

      {/* No Results State */}
      {!trackingData && !loading && searchValue && (
        <Card style={{ textAlign: 'center', padding: '48px 24px' }}>
          <InboxOutlined style={{ fontSize: '48px', color: '#d9d9d9', marginBottom: 16 }} />
          <h3>No tracking information found</h3>
          <p style={{ color: '#666', marginBottom: 16 }}>
            We couldn't find any tracking information for "{searchValue}".
            Please check your order number or tracking number and try again.
          </p>
          <Button type="primary" onClick={() => setSearchValue('')}>
            Try Another Search
          </Button>
        </Card>
      )}

      {/* Notification Preferences Modal */}
      <Modal
        title="Notification Preferences"
        open={showNotificationModal}
        onOk={handleNotificationUpdate}
        onCancel={() => setShowNotificationModal(false)}
        okText="Save Preferences"
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          <div>
            <Switch
              checked={notificationPrefs.email}
              onChange={(checked) => setNotificationPrefs(prev => ({ ...prev, email: checked }))}
            />
            <span style={{ marginLeft: 8 }}>Email Notifications</span>
          </div>
          <div>
            <Switch
              checked={notificationPrefs.sms}
              onChange={(checked) => setNotificationPrefs(prev => ({ ...prev, sms: checked }))}
            />
            <span style={{ marginLeft: 8 }}>SMS Notifications</span>
          </div>
          <div>
            <Switch
              checked={notificationPrefs.push}
              onChange={(checked) => setNotificationPrefs(prev => ({ ...prev, push: checked }))}
            />
            <span style={{ marginLeft: 8 }}>Push Notifications</span>
          </div>
        </Space>
      </Modal>
    </div>
  );
};

export default EnhancedOrderTracking;

customer/product-comparison.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Modal,
  Form,
  Input,
  Select,
  message,
  Divider,
  Descriptions,
  Avatar,
  Badge,
} from 'antd';
import {
  ShoppingOutlined,
  EyeOutlined,
  SwapOutlined,
  CloseCircleOutlined,
  StarFilled,
  CheckCircleOutlined,
  MinusCircleOutlined,
  PlusOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text, Paragraph } = Typography;

interface Product {
  id: number;
  name: string;
  brand: string;
  category: string;
  price: number;
  rating: number;
  reviews: number;
  inStock: boolean;
  image: string;
  specs: ProductSpecs;
}

interface ProductSpecs {
  [key: string]: string | number | boolean;
}

const mockProducts: Product[] = [
  {
    id: 1,
    name: 'Wireless Headphones Pro',
    brand: 'TechBrand',
    category: 'Electronics',
    price: 99,
    rating: 4.5,
    reviews: 234,
    inStock: true,
    image: '',
    specs: {
      'Battery Life': '30 hours',
      'Noise Cancellation': 'Active',
      'Bluetooth Version': '5.0',
      'Weight': '250g',
      'Warranty': '2 years',
      'Color Options': 'Black, White, Blue',
      'Foldable': true,
      'Voice Assistant': 'Yes',
    },
  },
  {
    id: 2,
    name: 'Wireless Headphones Lite',
    brand: 'TechBrand',
    category: 'Electronics',
    price: 59,
    rating: 4.2,
    reviews: 156,
    inStock: true,
    image: '',
    specs: {
      'Battery Life': '20 hours',
      'Noise Cancellation': 'Passive',
      'Bluetooth Version': '4.2',
      'Weight': '200g',
      'Warranty': '1 year',
      'Color Options': 'Black, White',
      'Foldable': false,
      'Voice Assistant': 'No',
    },
  },
  {
    id: 3,
    name: 'Premium Headphones Max',
    brand: 'AudioPro',
    category: 'Electronics',
    price: 199,
    rating: 4.8,
    reviews: 456,
    inStock: false,
    image: '',
    specs: {
      'Battery Life': '40 hours',
      'Noise Cancellation': 'Adaptive',
      'Bluetooth Version': '5.2',
      'Weight': '300g',
      'Warranty': '3 years',
      'Color Options': 'Black, Silver, Gold',
      'Foldable': true,
      'Voice Assistant': 'Yes',
    },
  },
];

const ProductComparisonPage: React.FC = () => {
  const [comparedProducts, setComparedProducts] = useState<Product[]>([mockProducts[0], mockProducts[1]]);
  const [isAddProductModalVisible, setIsAddProductModalVisible] = useState<boolean>(false);
  const [availableProducts, setAvailableProducts] = useState<Product[]>(mockProducts);

  const handleAddProduct = (productId: number) => {
    if (comparedProducts.length >= 4) {
      message.warning('You can compare up to 4 products at a time');
      return;
    }

    const product = availableProducts.find((p) => p.id === productId);
    if (product) {
      setComparedProducts([...comparedProducts, product]);
      message.success(`${product.name} added to comparison`);
      setIsAddProductModalVisible(false);
    }
  };

  const handleRemoveProduct = (productId: number) => {
    if (comparedProducts.length <= 2) {
      message.warning('You need at least 2 products to compare');
      return;
    }

    setComparedProducts(comparedProducts.filter((p) => p.id !== productId));
    message.success('Product removed from comparison');
  };

  const handleClearComparison = () => {
    Modal.confirm({
      title: 'Clear Comparison',
      content: 'Are you sure you want to clear all products from comparison?',
      onOk: () => {
        setComparedProducts([]);
        message.success('Comparison cleared');
      },
    });
  };

  const getAllSpecKeys = (): string[] => {
    const allKeys = new Set<string>();
    comparedProducts.forEach((product) => {
      Object.keys(product.specs).forEach((key) => allKeys.add(key));
    });
    return Array.from(allKeys);
  };

  const renderSpecValue = (value: string | number | boolean) => {
    if (typeof value === 'boolean') {
      return value ? (
        <Tag color="green" icon={<CheckCircleOutlined />}>
          Yes
        </Tag>
      ) : (
        <Tag color="red" icon={<CloseCircleOutlined />}>
          No
        </Tag>
      );
    }
    return <Text>{value}</Text>;
  };

  const findBestValue = (specKey: string, values: (string | number | boolean)[]): number[] => {
    const bestIndices: number[] = [];

    // For battery life, higher is better
    if (specKey === 'Battery Life') {
      const numericValues = values.map((v) => {
        if (typeof v === 'string') {
          const match = v.match(/(\d+)/);
          return match ? parseInt(match[1]) : 0;
        }
        return 0;
      });
      const max = Math.max(...numericValues);
      numericValues.forEach((val, idx) => {
        if (val === max) bestIndices.push(idx);
      });
    }

    // For weight, lower is better
    if (specKey === 'Weight') {
      const numericValues = values.map((v) => {
        if (typeof v === 'string') {
          const match = v.match(/(\d+)/);
          return match ? parseInt(match[1]) : Infinity;
        }
        return Infinity;
      });
      const min = Math.min(...numericValues);
      numericValues.forEach((val, idx) => {
        if (val === min) bestIndices.push(idx);
      });
    }

    // For boolean values, true is better
    if (typeof values[0] === 'boolean') {
      values.forEach((val, idx) => {
        if (val === true) bestIndices.push(idx);
      });
    }

    return bestIndices;
  };

  const productListColumns: ColumnsType<Product> = [
    {
      title: 'Product',
      key: 'product',
      render: (_, record) => (
        <Space>
          <Avatar
            size={48}
            shape="square"
            style={{ background: '#f0f0f0', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
          >
            <Text type="secondary" style={{ fontSize: 10 }}>IMG</Text>
          </Avatar>
          <div>
            <Text strong>{record.name}</Text>
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>{record.brand}</Text>
            </div>
          </div>
        </Space>
      ),
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      render: (price) => <Text strong>${price}</Text>,
    },
    {
      title: 'Rating',
      key: 'rating',
      render: (_, record) => (
        <Space>
          <StarFilled style={{ color: '#faad14' }} />
          <Text>{record.rating}</Text>
        </Space>
      ),
    },
    {
      title: 'Stock',
      dataIndex: 'inStock',
      key: 'inStock',
      render: (inStock) =>
        inStock ? (
          <Tag color="green">In Stock</Tag>
        ) : (
          <Tag color="red">Out of Stock</Tag>
        ),
    },
    {
      title: 'Action',
      key: 'action',
      render: (_, record) => (
        <Button
          type="primary"
          onClick={() => handleAddProduct(record.id)}
          disabled={comparedProducts.some((p) => p.id === record.id)}
        >
          {comparedProducts.some((p) => p.id === record.id) ? 'Already Added' : 'Add to Compare'}
        </Button>
      ),
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Row justify="space-between" align="middle">
          <Col>
            <Title level={3}>
              <SwapOutlined style={{ color: '#1890ff' }} /> Product Comparison
            </Title>
            <Paragraph type="secondary">
              Compare products side-by-side to make better buying decisions
            </Paragraph>
          </Col>
          <Col>
            <Space>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => setIsAddProductModalVisible(true)}
                disabled={comparedProducts.length >= 4}
              >
                Add Product
              </Button>
              {comparedProducts.length > 0 && (
                <Button danger onClick={handleClearComparison}>
                  Clear All
                </Button>
              )}
            </Space>
          </Col>
        </Row>
      </div>

      {comparedProducts.length === 0 ? (
        <Card>
          <div style={{ textAlign: 'center', padding: '60px 0' }}>
            <SwapOutlined style={{ fontSize: 64, color: '#d9d9d9', marginBottom: 16 }} />
            <Title level={4}>No Products to Compare</Title>
            <Paragraph type="secondary">
              Add at least 2 products to start comparing
            </Paragraph>
            <Button type="primary" icon={<PlusOutlined />} onClick={() => setIsAddProductModalVisible(true)}>
              Add Products
            </Button>
          </div>
        </Card>
      ) : (
        <Card>
          <Row gutter={16}>
            {/* Product Cards */}
            {comparedProducts.map((product, index) => (
              <Col key={product.id} xs={24} sm={12} lg={6}>
                <Card
                  style={{ marginBottom: 16 }}
                  cover={
                    <div
                      style={{
                        height: 200,
                        background: '#f0f0f0',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        position: 'relative',
                      }}
                    >
                      {comparedProducts.length > 2 && (
                        <Button
                          type="text"
                          danger
                          icon={<CloseCircleOutlined />}
                          style={{ position: 'absolute', top: 8, right: 8 }}
                          onClick={() => handleRemoveProduct(product.id)}
                        />
                      )}
                      <Text type="secondary">Product Image</Text>
                    </div>
                  }
                >
                  <Card.Meta
                    title={<Text strong>{product.name}</Text>}
                    description={
                      <Space direction="vertical" size="small" style={{ width: '100%' }}>
                        <Text type="secondary">{product.brand}</Text>
                        <Space>
                          <StarFilled style={{ color: '#faad14' }} />
                          <Text>{product.rating} ({product.reviews})</Text>
                        </Space>
                        <Text strong style={{ fontSize: 20, color: '#ff4d4f' }}>
                          ${product.price}
                        </Text>
                        {product.inStock ? (
                          <Tag color="green">In Stock</Tag>
                        ) : (
                          <Tag color="red">Out of Stock</Tag>
                        )}
                        <Button type="primary" block icon={<ShoppingOutlined />} disabled={!product.inStock}>
                          Add to Cart
                        </Button>
                      </Space>
                    }
                  />
                </Card>
              </Col>
            ))}

            {/* Add Product Placeholder */}
            {comparedProducts.length < 4 && (
              <Col xs={24} sm={12} lg={6}>
                <Card
                  style={{
                    marginBottom: 16,
                    minHeight: 400,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: 'pointer',
                    border: '2px dashed #d9d9d9',
                  }}
                  bodyStyle={{ textAlign: 'center', width: '100%' }}
                  onClick={() => setIsAddProductModalVisible(true)}
                >
                  <PlusOutlined style={{ fontSize: 48, color: '#d9d9d9', marginBottom: 16 }} />
                  <div>
                    <Text type="secondary">Add Another Product</Text>
                  </div>
                </Card>
              </Col>
            )}
          </Row>

          <Divider>Specifications Comparison</Divider>

          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ background: '#fafafa' }}>
                  <th style={{ padding: '12px', textAlign: 'left', borderBottom: '1px solid #f0f0f0' }}>
                    <Text strong>Specification</Text>
                  </th>
                  {comparedProducts.map((product) => (
                    <th
                      key={product.id}
                      style={{ padding: '12px', textAlign: 'center', borderBottom: '1px solid #f0f0f0' }}
                    >
                      <Text type="secondary" style={{ fontSize: 12 }}>{product.name}</Text>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {/* Price Row */}
                <tr>
                  <td style={{ padding: '12px', borderBottom: '1px solid #f0f0f0' }}>
                    <Text strong>Price</Text>
                  </td>
                  {comparedProducts.map((product, idx) => {
                    const prices = comparedProducts.map((p) => p.price);
                    const minPrice = Math.min(...prices);
                    const isBest = product.price === minPrice;
                    return (
                      <td
                        key={product.id}
                        style={{
                          padding: '12px',
                          textAlign: 'center',
                          borderBottom: '1px solid #f0f0f0',
                          background: isBest ? '#f6ffed' : 'transparent',
                        }}
                      >
                        <Space direction="vertical" size={0}>
                          <Text strong style={{ color: isBest ? '#52c41a' : '#ff4d4f', fontSize: 16 }}>
                            ${product.price}
                          </Text>
                          {isBest && <Badge count="Best Price" style={{ backgroundColor: '#52c41a' }} />}
                        </Space>
                      </td>
                    );
                  })}
                </tr>

                {/* Rating Row */}
                <tr>
                  <td style={{ padding: '12px', borderBottom: '1px solid #f0f0f0' }}>
                    <Text strong>Rating</Text>
                  </td>
                  {comparedProducts.map((product) => {
                    const ratings = comparedProducts.map((p) => p.rating);
                    const maxRating = Math.max(...ratings);
                    const isBest = product.rating === maxRating;
                    return (
                      <td
                        key={product.id}
                        style={{
                          padding: '12px',
                          textAlign: 'center',
                          borderBottom: '1px solid #f0f0f0',
                          background: isBest ? '#f6ffed' : 'transparent',
                        }}
                      >
                        <Space>
                          <StarFilled style={{ color: '#faad14' }} />
                          <Text strong>{product.rating}</Text>
                          <Text type="secondary" style={{ fontSize: 11 }}>({product.reviews})</Text>
                        </Space>
                      </td>
                    );
                  })}
                </tr>

                {/* Specifications Rows */}
                {getAllSpecKeys().map((specKey) => {
                  const values = comparedProducts.map((p) => p.specs[specKey] || '-');
                  const bestIndices = findBestValue(specKey, values);

                  return (
                    <tr key={specKey}>
                      <td style={{ padding: '12px', borderBottom: '1px solid #f0f0f0' }}>
                        <Text strong>{specKey}</Text>
                      </td>
                      {comparedProducts.map((product, idx) => {
                        const value = product.specs[specKey];
                        const isBest = bestIndices.includes(idx);
                        return (
                          <td
                            key={product.id}
                            style={{
                              padding: '12px',
                              textAlign: 'center',
                              borderBottom: '1px solid #f0f0f0',
                              background: isBest ? '#f6ffed' : 'transparent',
                            }}
                          >
                            {value ? renderSpecValue(value) : <Text type="secondary">-</Text>}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>

          <Divider />

          <Row justify="center">
            <Col>
              <Space>
                <Button type="primary" icon={<PlusOutlined />} onClick={() => setIsAddProductModalVisible(true)}>
                  Add Another Product
                </Button>
                <Button onClick={handleClearComparison}>Clear Comparison</Button>
              </Space>
            </Col>
          </Row>
        </Card>
      )}

      <Modal
        title="Add Product to Comparison"
        open={isAddProductModalVisible}
        onCancel={() => setIsAddProductModalVisible(false)}
        footer={null}
        width={800}
      >
        <Table
          columns={productListColumns}
          dataSource={availableProducts}
          rowKey="id"
          pagination={{ pageSize: 5 }}
        />
      </Modal>
    </div>
  );
};

export default ProductComparisonPage;

customer/push-notifications.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Switch,
  Button,
  Space,
  Alert,
  List,
  Tag,
  Divider,
  message,
  Modal,
  Form,
  Select,
  Input,
  Badge,
  Statistic,
  Timeline,
} from 'antd';
import {
  BellOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  SettingOutlined,
  NotificationOutlined,
  MobileOutlined,
  DesktopOutlined,
  TabletOutlined,
  DeleteOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

interface PushDevice {
  id: number;
  name: string;
  type: 'mobile' | 'desktop' | 'tablet';
  browser: string;
  os: string;
  enabled: boolean;
  lastActive: string;
}

interface PushNotification {
  id: number;
  title: string;
  body: string;
  timestamp: string;
  status: 'delivered' | 'clicked' | 'dismissed';
  deviceType: string;
}

const mockDevices: PushDevice[] = [
  {
    id: 1,
    name: 'iPhone 13 Pro',
    type: 'mobile',
    browser: 'Safari',
    os: 'iOS 17.0',
    enabled: true,
    lastActive: '2024-11-10T10:30:00',
  },
  {
    id: 2,
    name: 'MacBook Pro',
    type: 'desktop',
    browser: 'Chrome',
    os: 'macOS 14.0',
    enabled: true,
    lastActive: '2024-11-10T09:15:00',
  },
  {
    id: 3,
    name: 'iPad Air',
    type: 'tablet',
    browser: 'Safari',
    os: 'iPadOS 17.0',
    enabled: false,
    lastActive: '2024-11-08T14:20:00',
  },
  {
    id: 4,
    name: 'Windows PC',
    type: 'desktop',
    browser: 'Edge',
    os: 'Windows 11',
    enabled: true,
    lastActive: '2024-11-09T16:45:00',
  },
];

const mockHistory: PushNotification[] = [
  {
    id: 1,
    title: 'Order Shipped',
    body: 'Your order #12345 has been shipped!',
    timestamp: '2024-11-10T10:30:00',
    status: 'clicked',
    deviceType: 'mobile',
  },
  {
    id: 2,
    title: 'Flash Sale',
    body: '50% off on Electronics - Limited time!',
    timestamp: '2024-11-10T08:00:00',
    status: 'delivered',
    deviceType: 'desktop',
  },
  {
    id: 3,
    title: 'Price Drop Alert',
    body: 'Wireless Headphones now $20 cheaper',
    timestamp: '2024-11-09T15:45:00',
    status: 'clicked',
    deviceType: 'mobile',
  },
  {
    id: 4,
    title: 'Order Delivered',
    body: 'Your package has been delivered',
    timestamp: '2024-11-08T14:20:00',
    status: 'dismissed',
    deviceType: 'desktop',
  },
];

const PushNotificationsPage: React.FC = () => {
  const [devices, setDevices] = useState<PushDevice[]>(mockDevices);
  const [history, setHistory] = useState<PushNotification[]>(mockHistory);
  const [masterEnabled, setMasterEnabled] = useState(true);
  const [permissionStatus, setPermissionStatus] = useState<'granted' | 'denied' | 'default'>('default');
  const [isTestModalVisible, setIsTestModalVisible] = useState(false);
  const [form] = Form.useForm();

  useEffect(() => {
    // Check browser notification permission
    if ('Notification' in window) {
      setPermissionStatus(Notification.permission);
    }
  }, []);

  const requestPermission = async () => {
    if ('Notification' in window) {
      const permission = await Notification.requestPermission();
      setPermissionStatus(permission);
      if (permission === 'granted') {
        message.success('Push notifications enabled successfully!');
      } else {
        message.error('Push notifications permission denied');
      }
    } else {
      message.error('This browser does not support push notifications');
    }
  };

  const handleToggleDevice = (id: number, enabled: boolean) => {
    setDevices((prev) =>
      prev.map((device) => (device.id === id ? { ...device, enabled } : device))
    );
    message.success(`Push notifications ${enabled ? 'enabled' : 'disabled'} for this device`);
  };

  const handleRemoveDevice = (id: number) => {
    Modal.confirm({
      title: 'Remove Device',
      content: 'Are you sure you want to remove this device from push notifications?',
      okText: 'Remove',
      okType: 'danger',
      onOk: () => {
        setDevices((prev) => prev.filter((device) => device.id !== id));
        message.success('Device removed successfully');
      },
    });
  };

  const handleMasterToggle = (enabled: boolean) => {
    setMasterEnabled(enabled);
    if (!enabled) {
      setDevices((prev) => prev.map((device) => ({ ...device, enabled: false })));
      message.info('Push notifications disabled for all devices');
    } else {
      message.success('Push notifications enabled');
    }
  };

  const sendTestNotification = () => {
    form.validateFields().then((values) => {
      if (permissionStatus === 'granted' && 'Notification' in window) {
        const notification = new Notification(values.title, {
          body: values.body,
          icon: '/favicon.ico',
          badge: '/favicon.ico',
          tag: 'test-notification',
        });

        notification.onclick = () => {
          window.focus();
          notification.close();
        };

        const newHistoryItem: PushNotification = {
          id: Date.now(),
          title: values.title,
          body: values.body,
          timestamp: new Date().toISOString(),
          status: 'delivered',
          deviceType: 'desktop',
        };

        setHistory((prev) => [newHistoryItem, ...prev]);
        message.success('Test notification sent!');
        setIsTestModalVisible(false);
        form.resetFields();
      } else {
        message.error('Please enable push notifications first');
      }
    });
  };

  const getDeviceIcon = (type: string) => {
    switch (type) {
      case 'mobile':
        return <MobileOutlined style={{ fontSize: 24, color: '#1890ff' }} />;
      case 'desktop':
        return <DesktopOutlined style={{ fontSize: 24, color: '#52c41a' }} />;
      case 'tablet':
        return <TabletOutlined style={{ fontSize: 24, color: '#ff9900' }} />;
      default:
        return <NotificationOutlined style={{ fontSize: 24 }} />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'delivered':
        return 'blue';
      case 'clicked':
        return 'green';
      case 'dismissed':
        return 'default';
      default:
        return 'default';
    }
  };

  const enabledDevicesCount = devices.filter((d) => d.enabled).length;
  const clickRate = history.length > 0
    ? ((history.filter((h) => h.status === 'clicked').length / history.length) * 100).toFixed(1)
    : '0';

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <NotificationOutlined style={{ color: '#1890ff' }} /> Push Notifications
        </Title>
        <Paragraph type="secondary">
          Manage push notification settings and connected devices
        </Paragraph>
      </div>

      {/* Permission Status Alert */}
      {permissionStatus === 'denied' && (
        <Alert
          message="Push Notifications Blocked"
          description="You have blocked push notifications. Please enable them in your browser settings."
          type="error"
          showIcon
          icon={<CloseCircleOutlined />}
          style={{ marginBottom: 16 }}
        />
      )}

      {permissionStatus === 'default' && (
        <Alert
          message="Enable Push Notifications"
          description="Get instant updates about your orders, promotions, and more. Click the button below to enable."
          type="info"
          showIcon
          icon={<BellOutlined />}
          action={
            <Button type="primary" onClick={requestPermission}>
              Enable Now
            </Button>
          }
          style={{ marginBottom: 16 }}
        />
      )}

      {/* Statistics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Active Devices"
              value={enabledDevicesCount}
              suffix={`/ ${devices.length}`}
              prefix={<MobileOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Notifications Sent"
              value={history.length}
              prefix={<BellOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Click Rate"
              value={clickRate}
              suffix="%"
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#ff9900' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16}>
        {/* Devices */}
        <Col xs={24} lg={12}>
          <Card
            title={
              <Space>
                <SettingOutlined />
                <span>Connected Devices</span>
                <Badge count={enabledDevicesCount} showZero />
              </Space>
            }
            extra={
              <Space>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Master Control
                </Text>
                <Switch
                  checked={masterEnabled}
                  onChange={handleMasterToggle}
                  checkedChildren={<PlayCircleOutlined />}
                  unCheckedChildren={<PauseCircleOutlined />}
                />
              </Space>
            }
          >
            <List
              dataSource={devices}
              renderItem={(device) => (
                <List.Item
                  actions={[
                    <Switch
                      key="toggle"
                      checked={device.enabled}
                      onChange={(checked) => handleToggleDevice(device.id, checked)}
                      disabled={!masterEnabled}
                    />,
                    <Button
                      key="remove"
                      type="text"
                      danger
                      icon={<DeleteOutlined />}
                      onClick={() => handleRemoveDevice(device.id)}
                    />,
                  ]}
                >
                  <List.Item.Meta
                    avatar={getDeviceIcon(device.type)}
                    title={
                      <Space>
                        <Text strong>{device.name}</Text>
                        {device.enabled && <Tag color="green">Active</Tag>}
                      </Space>
                    }
                    description={
                      <Space direction="vertical" size={0}>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          {device.browser}  {device.os}
                        </Text>
                        <Text type="secondary" style={{ fontSize: 11 }}>
                          Last active: {new Date(device.lastActive).toLocaleString()}
                        </Text>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />

            <Divider />

            <Space style={{ width: '100%', justifyContent: 'center' }}>
              <Button
                type="primary"
                icon={<BellOutlined />}
                onClick={() => setIsTestModalVisible(true)}
                disabled={permissionStatus !== 'granted' || !masterEnabled}
              >
                Send Test Notification
              </Button>
            </Space>
          </Card>
        </Col>

        {/* History */}
        <Col xs={24} lg={12}>
          <Card
            title={
              <Space>
                <BellOutlined />
                <span>Recent Notifications</span>
              </Space>
            }
          >
            <Timeline
              items={history.slice(0, 10).map((item) => ({
                color: getStatusColor(item.status) as any,
                children: (
                  <div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>
                      <div style={{ flex: 1 }}>
                        <Text strong>{item.title}</Text>
                        <div>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            {item.body}
                          </Text>
                        </div>
                      </div>
                      <Tag color={getStatusColor(item.status)} style={{ marginLeft: 8 }}>
                        {item.status}
                      </Tag>
                    </div>
                    <div style={{ marginTop: 4 }}>
                      <Space size={4}>
                        {getDeviceIcon(item.deviceType)}
                        <Text type="secondary" style={{ fontSize: 11 }}>
                          {new Date(item.timestamp).toLocaleString()}
                        </Text>
                      </Space>
                    </div>
                  </div>
                ),
              }))}
            />
          </Card>
        </Col>
      </Row>

      {/* Test Notification Modal */}
      <Modal
        title="Send Test Push Notification"
        open={isTestModalVisible}
        onOk={sendTestNotification}
        onCancel={() => {
          setIsTestModalVisible(false);
          form.resetFields();
        }}
        okText="Send"
      >
        <Form form={form} layout="vertical">
          <Form.Item
            name="title"
            label="Title"
            rules={[{ required: true, message: 'Please enter a title' }]}
            initialValue="Test Notification"
          >
            <Input placeholder="Notification title" />
          </Form.Item>
          <Form.Item
            name="body"
            label="Message"
            rules={[{ required: true, message: 'Please enter a message' }]}
            initialValue="This is a test push notification from your e-commerce platform!"
          >
            <TextArea rows={3} placeholder="Notification message" />
          </Form.Item>
          <Alert
            message="Note"
            description="This will send a real browser notification if you have granted permission."
            type="info"
            showIcon
            style={{ marginTop: 16 }}
          />
        </Form>
      </Modal>
    </div>
  );
};

export default PushNotificationsPage;

