    pointsSpent: 300,
    redeemedAt: dayjs().subtract(1, 'day').format('YYYY-MM-DD HH:mm'),
    status: 'pending',
  },
  {
    id: 3,
    userId: 103,
    userName: 'Bob Johnson',
    rewardName: '$25 Off Coupon',
    pointsSpent: 1200,
    redeemedAt: dayjs().subtract(3, 'days').format('YYYY-MM-DD HH:mm'),
    status: 'claimed',
  },
];

const RewardsRedemptionPage: React.FC = () => {
  const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
  const [selectedReward, setSelectedReward] = useState<Reward | null>(null);
  const [form] = Form.useForm();

  const handleRedeemReward = (reward: Reward) => {
    setSelectedReward(reward);
    setIsModalVisible(true);
  };

  const handleConfirmRedemption = () => {
    message.success('Reward redeemed successfully!');
    setIsModalVisible(false);
    form.resetFields();
  };

  const rewardColumns: ColumnsType<Reward> = [
    {
      title: 'Reward',
      key: 'reward',
      render: (_, record) => (
        <Space>
          <Avatar size={48} style={{ fontSize: 24 }}>
            {record.image}
          </Avatar>
          <div>
            <Text strong>{record.name}</Text>
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                {record.description}
              </Text>
            </div>
          </div>
        </Space>
      ),
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: Reward['type']) => {
        const colors: Record<Reward['type'], string> = {
          discount: 'blue',
          product: 'purple',
          shipping: 'green',
          voucher: 'orange',
        };
        return <Tag color={colors[type]}>{type.toUpperCase()}</Tag>;
      },
      filters: [
        { text: 'Discount', value: 'discount' },
        { text: 'Product', value: 'product' },
        { text: 'Shipping', value: 'shipping' },
        { text: 'Voucher', value: 'voucher' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Points Cost',
      dataIndex: 'pointsCost',
      key: 'pointsCost',
      render: (points) => (
        <Space>
          <StarOutlined style={{ color: '#faad14' }} />
          <Text strong>{points.toLocaleString()}</Text>
        </Space>
      ),
      sorter: (a, b) => a.pointsCost - b.pointsCost,
    },
    {
      title: 'Value',
      dataIndex: 'value',
      key: 'value',
      render: (value) => <Text strong style={{ color: '#52c41a' }}>{value}</Text>,
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock',
      render: (stock) => (
        <Tag color={stock > 50 ? 'green' : stock > 20 ? 'orange' : 'red'}>{stock} available</Tag>
      ),
    },
    {
      title: 'Redeemed',
      dataIndex: 'redeemed',
      key: 'redeemed',
      render: (redeemed) => <Text type="secondary">{redeemed} times</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Button
          type="primary"
          size="small"
          icon={<GiftOutlined />}
          onClick={() => handleRedeemReward(record)}
          disabled={record.stock === 0}
        >
          Redeem
        </Button>
      ),
    },
  ];

  const redemptionColumns: ColumnsType<UserReward> = [
    {
      title: 'User',
      key: 'user',
      render: (_, record) => (
        <Space>
          <Avatar icon={<UserOutlined />} />
          <Text>{record.userName}</Text>
        </Space>
      ),
    },
    {
      title: 'Reward',
      dataIndex: 'rewardName',
      key: 'rewardName',
    },
    {
      title: 'Points Spent',
      dataIndex: 'pointsSpent',
      key: 'pointsSpent',
      render: (points) => (
        <Space>
          <StarOutlined style={{ color: '#faad14' }} />
          <Text>{points.toLocaleString()}</Text>
        </Space>
      ),
    },
    {
      title: 'Redeemed At',
      dataIndex: 'redeemedAt',
      key: 'redeemedAt',
      render: (date) => <Text type="secondary">{date}</Text>,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: UserReward['status']) => {
        const config: Record<UserReward['status'], { color: string; text: string }> = {
          pending: { color: 'orange', text: 'Pending' },
          claimed: { color: 'green', text: 'Claimed' },
          expired: { color: 'red', text: 'Expired' },
        };
        return <Tag color={config[status].color}>{config[status].text}</Tag>;
      },
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Claimed', value: 'claimed' },
        { text: 'Expired', value: 'expired' },
      ],
      onFilter: (value, record) => record.status === value,
    },
  ];

  const totalRedemptions = mockRedemptions.length;
  const totalPointsSpent = mockRedemptions.reduce((sum, r) => sum + r.pointsSpent, 0);
  const activeRewards = mockRewards.filter((r) => r.stock > 0).length;
  const avgRedemptionValue = totalPointsSpent / totalRedemptions;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <GiftOutlined style={{ color: '#1890ff' }} /> Rewards & Redemption
        </Title>
        <Paragraph type="secondary">
          Manage loyalty rewards, track redemptions, and configure tier benefits
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Active Rewards"
              value={activeRewards}
              prefix={<GiftOutlined />}
              valueStyle={{ color: '#1890ff' }}
              suffix={`/ ${mockRewards.length}`}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Redemptions"
              value={totalRedemptions}
              prefix={<ShoppingCartOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Points Redeemed"
              value={totalPointsSpent}
              prefix={<StarOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg. Redemption"
              value={avgRedemptionValue}
              prefix={<TrophyOutlined />}
              valueStyle={{ color: '#722ed1' }}
              precision={0}
              suffix="pts"
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} lg={16}>
          <Card title="Available Rewards" style={{ marginBottom: 16 }}>
            <Table
              columns={rewardColumns}
              dataSource={mockRewards}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>

          <Card title="Recent Redemptions">
            <Table
              columns={redemptionColumns}
              dataSource={mockRedemptions}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card title="Loyalty Tiers" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {loyaltyTiers.map((tier, index) => (
                <Card
                  key={tier.name}
                  size="small"
                  style={{ borderLeft: `4px solid ${tier.color}` }}
                >
                  <Space direction="vertical" style={{ width: '100%' }} size="small">
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Space>
                        <TrophyOutlined style={{ fontSize: 24, color: tier.color }} />
                        <Text strong style={{ fontSize: 16 }}>
                          {tier.name}
                        </Text>
                      </Space>
                      <Tag color={tier.color}>{tier.multiplier}x</Tag>
                    </div>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      {tier.minPoints.toLocaleString()}+ points required
                    </Text>
                    <Divider style={{ margin: '8px 0' }} />
                    <div>
                      <Text strong style={{ fontSize: 12 }}>
                        Benefits:
                      </Text>
                      <ul style={{ margin: '4px 0', paddingLeft: 20, fontSize: 12 }}>
                        {tier.benefits.map((benefit, i) => (
                          <li key={i}>{benefit}</li>
                        ))}
                      </ul>
                    </div>
                  </Space>
                </Card>
              ))}
            </Space>
          </Card>

          <Card title="Popular Rewards">
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              {mockRewards
                .sort((a, b) => b.redeemed - a.redeemed)
                .slice(0, 5)
                .map((reward) => (
                  <div key={reward.id}>
                    <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                      <Space>
                        <span style={{ fontSize: 20 }}>{reward.image}</span>
                        <div>
                          <Text strong style={{ fontSize: 12 }}>
                            {reward.name}
                          </Text>
                          <div>
                            <Text type="secondary" style={{ fontSize: 11 }}>
                              {reward.pointsCost} pts
                            </Text>
                          </div>
                        </div>
                      </Space>
                      <Tag color="blue">{reward.redeemed}</Tag>
                    </Space>
                    <Progress
                      percent={(reward.redeemed / (reward.stock + reward.redeemed)) * 100}
                      size="small"
                      showInfo={false}
                    />
                  </div>
                ))}
            </Space>
          </Card>
        </Col>
      </Row>

      <Modal
        title={`Redeem: ${selectedReward?.name}`}
        open={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        footer={[
          <Button key="cancel" onClick={() => setIsModalVisible(false)}>
            Cancel
          </Button>,
          <Button key="confirm" type="primary" onClick={handleConfirmRedemption}>
            Confirm Redemption
          </Button>,
        ]}
      >
        {selectedReward && (
          <Space direction="vertical" style={{ width: '100%' }} size="middle">
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: 48 }}>{selectedReward.image}</div>
              <Title level={4}>{selectedReward.name}</Title>
              <Paragraph type="secondary">{selectedReward.description}</Paragraph>
            </div>
            <Divider />
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Text>Points Cost:</Text>
              <Text strong style={{ color: '#faad14' }}>
                <StarOutlined /> {selectedReward.pointsCost.toLocaleString()}
              </Text>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Text>Value:</Text>
              <Text strong style={{ color: '#52c41a' }}>
                {selectedReward.value}
              </Text>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Text>Stock Available:</Text>
              <Tag color={selectedReward.stock > 50 ? 'green' : 'orange'}>
                {selectedReward.stock} remaining
              </Tag>
            </div>
          </Space>
        )}
      </Modal>
    </div>
  );
};

export default RewardsRedemptionPage;

demo/personalized-dashboard.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Switch,
  Divider,
  message,
  Select,
  Slider,
  ColorPicker,
  Input,
  Tag,
  List,
  Avatar,
  Checkbox,
} from 'antd';
import {
  DashboardOutlined,
  PlusOutlined,
  DeleteOutlined,
  EditOutlined,
  DragOutlined,
  BarChartOutlined,
  ShoppingCartOutlined,
  UserOutlined,
  DollarOutlined,
  HeartOutlined,
  SaveOutlined,
  ReloadOutlined,
} from '@ant-design/icons';
import type { Color } from 'antd/es/color-picker';

const { Title, Text, Paragraph } = Typography;

interface Widget {
  id: string;
  type: 'statistic' | 'chart' | 'list' | 'activity';
  title: string;
  icon: React.ReactNode;
  color: string;
  enabled: boolean;
  size: 'small' | 'medium' | 'large';
  position: number;
}

const availableWidgets: Widget[] = [
  {
    id: 'total_sales',
    type: 'statistic',
    title: 'Total Sales',
    icon: <DollarOutlined />,
    color: '#52c41a',
    enabled: true,
    size: 'small',
    position: 0,
  },
  {
    id: 'total_orders',
    type: 'statistic',
    title: 'Total Orders',
    icon: <ShoppingCartOutlined />,
    color: '#1890ff',
    enabled: true,
    size: 'small',
    position: 1,
  },
  {
    id: 'total_customers',
    type: 'statistic',
    title: 'Total Customers',
    icon: <UserOutlined />,
    color: '#722ed1',
    enabled: true,
    size: 'small',
    position: 2,
  },
  {
    id: 'wishlist_items',
    type: 'statistic',
    title: 'Wishlist Items',
    icon: <HeartOutlined />,
    color: '#ff4d4f',
    enabled: true,
    size: 'small',
    position: 3,
  },
  {
    id: 'sales_chart',
    type: 'chart',
    title: 'Sales Overview',
    icon: <BarChartOutlined />,
    color: '#1890ff',
    enabled: true,
    size: 'large',
    position: 4,
  },
  {
    id: 'recent_orders',
    type: 'list',
    title: 'Recent Orders',
    icon: <ShoppingCartOutlined />,
    color: '#ff9900',
    enabled: true,
    size: 'medium',
    position: 5,
  },
  {
    id: 'activity_feed',
    type: 'activity',
    title: 'Activity Feed',
    icon: <DashboardOutlined />,
    color: '#13c2c2',
    enabled: false,
    size: 'medium',
    position: 6,
  },
];

const PersonalizedDashboardPage: React.FC = () => {
  const [widgets, setWidgets] = useState<Widget[]>(availableWidgets);
  const [theme, setTheme] = useState<'light' | 'dark'>('light');
  const [compactMode, setCompactMode] = useState(false);
  const [primaryColor, setPrimaryColor] = useState<Color | string>('#1890ff');
  const [refreshInterval, setRefreshInterval] = useState(30);
  const [showAnimations, setShowAnimations] = useState(true);

  const handleToggleWidget = (id: string) => {
    setWidgets((prev) =>
      prev.map((w) => (w.id === id ? { ...w, enabled: !w.enabled } : w))
    );
    message.success('Widget visibility updated');
  };

  const handleChangeWidgetSize = (id: string, size: 'small' | 'medium' | 'large') => {
    setWidgets((prev) =>
      prev.map((w) => (w.id === id ? { ...w, size } : w))
    );
    message.success('Widget size updated');
  };

  const handleSaveSettings = () => {
    const settings = {
      widgets,
      theme,
      compactMode,
      primaryColor,
      refreshInterval,
      showAnimations,
    };
    localStorage.setItem('dashboard_settings', JSON.stringify(settings));
    message.success('Dashboard settings saved successfully!');
  };

  const handleResetSettings = () => {
    setWidgets(availableWidgets);
    setTheme('light');
    setCompactMode(false);
    setPrimaryColor('#1890ff');
    setRefreshInterval(30);
    setShowAnimations(true);
    localStorage.removeItem('dashboard_settings');
    message.info('Dashboard settings reset to default');
  };

  const enabledWidgets = widgets.filter((w) => w.enabled).sort((a, b) => a.position - b.position);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <DashboardOutlined style={{ color: '#1890ff' }} /> Personalized Dashboard
        </Title>
        <Paragraph type="secondary">
          Customize your dashboard layout, widgets, and appearance
        </Paragraph>
      </div>

      <Row gutter={16}>
        {/* Settings Panel */}
        <Col xs={24} lg={8}>
          <Card title="Dashboard Settings" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {/* Theme */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Theme
                </Text>
                <Select
                  style={{ width: '100%' }}
                  value={theme}
                  onChange={setTheme}
                  options={[
                    { label: ' Light Theme', value: 'light' },
                    { label: ' Dark Theme', value: 'dark' },
                  ]}
                />
              </div>

              {/* Primary Color */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Primary Color
                </Text>
                <ColorPicker
                  value={primaryColor}
                  onChange={setPrimaryColor}
                  showText
                  style={{ width: '100%' }}
                />
              </div>

              {/* Refresh Interval */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Auto Refresh Interval: {refreshInterval}s
                </Text>
                <Slider
                  min={10}
                  max={300}
                  step={10}
                  value={refreshInterval}
                  onChange={setRefreshInterval}
                  marks={{
                    10: '10s',
                    60: '1m',
                    120: '2m',
                    300: '5m',
                  }}
                />
              </div>

              <Divider />

              {/* Display Options */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 12 }}>
                  Display Options
                </Text>
                <Space direction="vertical" style={{ width: '100%' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text>Compact Mode</Text>
                    <Switch checked={compactMode} onChange={setCompactMode} />
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text>Show Animations</Text>
                    <Switch checked={showAnimations} onChange={setShowAnimations} />
                  </div>
                </Space>
              </div>

              <Divider />

              {/* Action Buttons */}
              <Space style={{ width: '100%' }} direction="vertical">
                <Button
                  type="primary"
                  icon={<SaveOutlined />}
                  block
                  onClick={handleSaveSettings}
                >
                  Save Settings
                </Button>
                <Button
                  danger
                  icon={<ReloadOutlined />}
                  block
                  onClick={handleResetSettings}
                >
                  Reset to Default
                </Button>
              </Space>
            </Space>
          </Card>

          {/* Widget Manager */}
          <Card title="Widget Manager">
            <List
              dataSource={widgets}
              renderItem={(widget) => (
                <List.Item
                  actions={[
                    <Switch
                      key="toggle"
                      checked={widget.enabled}
                      onChange={() => handleToggleWidget(widget.id)}
                    />,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        icon={widget.icon}
                        style={{ backgroundColor: widget.color }}
                      />
                    }
                    title={widget.title}
                    description={
                      <Space>
                        <Select
                          size="small"
                          value={widget.size}
                          onChange={(size) => handleChangeWidgetSize(widget.id, size)}
                          disabled={!widget.enabled}
                          style={{ width: 100 }}
                          options={[
                            { label: 'Small', value: 'small' },
                            { label: 'Medium', value: 'medium' },
                            { label: 'Large', value: 'large' },
                          ]}
                        />
                        <Tag color={widget.enabled ? 'green' : 'default'}>
                          {widget.type}
                        </Tag>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        {/* Dashboard Preview */}
        <Col xs={24} lg={16}>
          <Card
            title={
              <Space>
                <DashboardOutlined />
                <span>Dashboard Preview</span>
                <Tag color="blue">{enabledWidgets.length} widgets</Tag>
              </Space>
            }
          >
            <Row gutter={[16, 16]}>
              {enabledWidgets.map((widget) => {
                const colSpan =
                  widget.size === 'small'
                    ? { xs: 24, sm: 12, md: 6 }
                    : widget.size === 'medium'
                    ? { xs: 24, sm: 12 }
                    : { xs: 24 };

                return (
                  <Col key={widget.id} {...colSpan}>
                    <Card
                      size={compactMode ? 'small' : 'default'}
                      style={{
                        borderLeft: `4px solid ${widget.color}`,
                        transition: showAnimations ? 'all 0.3s' : 'none',
                      }}
                      hoverable={showAnimations}
                    >
                      <Space direction="vertical" style={{ width: '100%' }}>
                        <Space>
                          <div style={{ fontSize: 24, color: widget.color }}>
                            {widget.icon}
                          </div>
                          <div>
                            <Text strong style={{ fontSize: 16 }}>
                              {widget.title}
                            </Text>
                            <div>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {widget.type.toUpperCase()}
                              </Text>
                            </div>
                          </div>
                        </Space>

                        {/* Widget Content Preview */}
                        {widget.type === 'statistic' && (
                          <div style={{ padding: '16px 0' }}>
                            <div style={{ fontSize: 32, fontWeight: 'bold', color: widget.color }}>
                              {Math.floor(Math.random() * 10000)}
                            </div>
                            <Text type="secondary">
                              +{Math.floor(Math.random() * 20)}% from last month
                            </Text>
                          </div>
                        )}

                        {widget.type === 'chart' && (
                          <div
                            style={{
                              height: 200,
                              background: '#f0f0f0',
                              borderRadius: 4,
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                            }}
                          >
                            <BarChartOutlined style={{ fontSize: 48, color: '#bfbfbf' }} />
                          </div>
                        )}

                        {widget.type === 'list' && (
                          <List
                            size="small"
                            dataSource={[1, 2, 3]}
                            renderItem={(item) => (
                              <List.Item>
                                <Text>Order #1234{item}</Text>
                                <Tag color="blue">${(Math.random() * 100).toFixed(2)}</Tag>
                              </List.Item>
                            )}
                          />
                        )}

                        {widget.type === 'activity' && (
                          <Space direction="vertical" style={{ width: '100%' }} size="small">
                            {[1, 2, 3].map((i) => (
                              <div key={i} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                                <Avatar size="small" icon={<UserOutlined />} />
                                <Text type="secondary" style={{ fontSize: 12 }}>
                                  User action #{i}
                                </Text>
                              </div>
                            ))}
                          </Space>
                        )}
                      </Space>
                    </Card>
                  </Col>
                );
              })}

              {enabledWidgets.length === 0 && (
                <Col span={24}>
                  <div
                    style={{
                      textAlign: 'center',
                      padding: '60px 20px',
                      background: '#fafafa',
                      borderRadius: 8,
                    }}
                  >
                    <DashboardOutlined style={{ fontSize: 64, color: '#bfbfbf', marginBottom: 16 }} />
                    <Title level={4} type="secondary">
                      No Widgets Enabled
                    </Title>
                    <Paragraph type="secondary">
                      Enable widgets from the Widget Manager to customize your dashboard
                    </Paragraph>
                  </div>
                </Col>
              )}
            </Row>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default PersonalizedDashboardPage;

admin/finance/transactions.__content:
import React, { useState } from 'react';
import {
  Card,
  Table,
  Tag,
  Space,
  Input,
  Select,
  DatePicker,
  Typography,
  Row,
  Col,
  Statistic,
  Button,
} from 'antd';
import {
  SearchOutlined,
  DollarOutlined,
  ArrowUpOutlined,
  ArrowDownOutlined,
  DownloadOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { Option } = Select;
const { RangePicker } = DatePicker;

interface Transaction {
  id: number;
  transactionId: string;
  date: string;
  type: string;
  category: string;
  amount: number;
  status: string;
  from: string;
  to: string;
  orderId?: string;
  paymentMethod: string;
}

const mockTransactions: Transaction[] = [
  {
    id: 1,
    transactionId: 'TXN-2024-100001',
    date: '2024-11-04 10:30:00',
    type: 'credit',
    category: 'order_payment',
    amount: 399.99,
    status: 'completed',
    from: 'John Doe',
    to: 'Platform',
    orderId: 'ORD-2024-10001',
    paymentMethod: 'Credit Card',
  },
  {
    id: 2,
    transactionId: 'TXN-2024-100002',
    date: '2024-11-04 09:15:00',
    type: 'debit',
    category: 'vendor_payout',
    amount: 500.00,
    status: 'completed',
    from: 'Platform',
    to: 'TechStore Inc',
    paymentMethod: 'Bank Transfer',
  },
  {
    id: 3,
    transactionId: 'TXN-2024-100003',
    date: '2024-11-03 14:20:00',
    type: 'credit',
    category: 'order_payment',
    amount: 199.99,
    status: 'completed',
    from: 'Sarah Smith',
    to: 'Platform',
    orderId: 'ORD-2024-10002',
    paymentMethod: 'PayPal',
  },
  {
    id: 4,
    transactionId: 'TXN-2024-100004',
    date: '2024-11-03 11:45:00',
    type: 'debit',
    category: 'refund',
    amount: 49.99,
    status: 'pending',
    from: 'Platform',
    to: 'Mike Johnson',
    orderId: 'ORD-2024-09980',
    paymentMethod: 'Credit Card',
  },
  {
    id: 5,
    transactionId: 'TXN-2024-100005',
    date: '2024-11-02 16:30:00',
    type: 'credit',
    category: 'commission',
    amount: 29.99,
    status: 'completed',
    from: 'TechStore Inc',
    to: 'Platform',
    orderId: 'ORD-2024-10001',
    paymentMethod: 'Auto Deduct',
  },
];

const AdminTransactionsPage: React.FC = () => {
  const [transactions] = useState<Transaction[]>(mockTransactions);

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      completed: 'success',
      pending: 'warning',
      failed: 'error',
      cancelled: 'default',
    };
    return colors[status] || 'default';
  };

  const getCategoryColor = (category: string) => {
    const colors: Record<string, string> = {
      order_payment: 'blue',
      vendor_payout: 'purple',
      refund: 'orange',
      commission: 'green',
    };
    return colors[category] || 'default';
  };

  const columns: ColumnsType<Transaction> = [
    {
      title: 'Transaction ID',
      dataIndex: 'transactionId',
      key: 'transactionId',
      render: (text: string) => <Text strong copyable>{text}</Text>,
    },
    {
      title: 'Date & Time',
      dataIndex: 'date',
      key: 'date',
      render: (date: string) => (
        <div>
          <Text>{new Date(date).toLocaleDateString()}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: 12 }}>
            {new Date(date).toLocaleTimeString()}
          </Text>
        </div>
      ),
      sorter: (a, b) => new Date(a.date).getTime() - new Date(b.date).getTime(),
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: string) => (
        <Tag
          color={type === 'credit' ? 'success' : 'error'}
          icon={type === 'credit' ? <ArrowDownOutlined /> : <ArrowUpOutlined />}
        >
          {type.toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Credit', value: 'credit' },
        { text: 'Debit', value: 'debit' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: string) => (
        <Tag color={getCategoryColor(category)}>
          {category.replace('_', ' ').toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Order Payment', value: 'order_payment' },
        { text: 'Vendor Payout', value: 'vendor_payout' },
        { text: 'Refund', value: 'refund' },
        { text: 'Commission', value: 'commission' },
      ],
      onFilter: (value, record) => record.category === value,
    },
    {
      title: 'From  To',
      key: 'parties',
      render: (_, record) => (
        <div>
          <Text>{record.from}</Text>
          <Text type="secondary">  </Text>
          <Text>{record.to}</Text>
        </div>
      ),
    },
    {
      title: 'Amount',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount: number, record) => (
        <Text
          strong
          style={{
            color: record.type === 'credit' ? '#52c41a' : '#ff4d4f',
            fontSize: 16,
          }}
        >
          {record.type === 'credit' ? '+' : '-'}${amount.toFixed(2)}
        </Text>
      ),
      sorter: (a, b) => a.amount - b.amount,
    },
    {
      title: 'Payment Method',
      dataIndex: 'paymentMethod',
      key: 'paymentMethod',
    },
    {
      title: 'Order ID',
      dataIndex: 'orderId',
      key: 'orderId',
      render: (orderId?: string) => (orderId ? <Text copyable>{orderId}</Text> : <Text type="secondary">-</Text>),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Completed', value: 'completed' },
        { text: 'Pending', value: 'pending' },
        { text: 'Failed', value: 'failed' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
  ];

  const totalCredit = transactions
    .filter((t) => t.type === 'credit' && t.status === 'completed')
    .reduce((sum, t) => sum + t.amount, 0);

  const totalDebit = transactions
    .filter((t) => t.type === 'debit' && t.status === 'completed')
    .reduce((sum, t) => sum + t.amount, 0);

  const netAmount = totalCredit - totalDebit;
  const pendingAmount = transactions
    .filter((t) => t.status === 'pending')
    .reduce((sum, t) => sum + t.amount, 0);

  return (
    <div style={{ padding: 24 }}>
      <Title level={3}>Transaction Management</Title>

      {/* Statistics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Credit"
              value={totalCredit}
              precision={2}
              prefix={<ArrowDownOutlined style={{ color: '#52c41a' }} />}
              suffix="USD"
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Debit"
              value={totalDebit}
              precision={2}
              prefix={<ArrowUpOutlined style={{ color: '#ff4d4f' }} />}
              suffix="USD"
              valueStyle={{ color: '#ff4d4f' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Net Amount"
              value={netAmount}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#1890ff' }} />}
              suffix="USD"
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Pending"
              value={pendingAmount}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#faad14' }} />}
              suffix="USD"
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      <Card>
        {/* Filters */}
        <Space style={{ marginBottom: 16, width: '100%', flexWrap: 'wrap' }}>
          <Input
            placeholder="Search transactions..."
            prefix={<SearchOutlined />}
            style={{ width: 250 }}
          />
          <Select placeholder="Type" style={{ width: 150 }}>
            <Option value="all">All Types</Option>
            <Option value="credit">Credit</Option>
            <Option value="debit">Debit</Option>
          </Select>
          <Select placeholder="Category" style={{ width: 180 }}>
            <Option value="all">All Categories</Option>
            <Option value="order_payment">Order Payment</Option>
            <Option value="vendor_payout">Vendor Payout</Option>
            <Option value="refund">Refund</Option>
            <Option value="commission">Commission</Option>
          </Select>
          <Select placeholder="Status" style={{ width: 150 }}>
            <Option value="all">All Status</Option>
            <Option value="completed">Completed</Option>
            <Option value="pending">Pending</Option>
            <Option value="failed">Failed</Option>
          </Select>
          <RangePicker />
          <Button type="primary" icon={<DownloadOutlined />}>
            Export
          </Button>
        </Space>

        {/* Transactions Table */}
        <Table
          columns={columns}
          dataSource={transactions}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} transactions`,
          }}
          scroll={{ x: 1200 }}
        />
      </Card>
    </div>
  );
};

export default AdminTransactionsPage;

