        <TabPane tab="Credit Requests" key="requests">
          <Card 
            title="Credit Limit Requests"
            extra={
              <Button type="primary" onClick={() => setCreditRequestVisible(true)}>
                New Request
              </Button>
            }
          >
            <Table
              columns={creditRequestColumns}
              dataSource={creditRequests}
              rowKey="id"
              pagination={false}
            />
          </Card>
        </TabPane>

        <TabPane tab="Invoices & Billing" key="invoices">
          <Card title="Outstanding Invoices">
            <Table
              columns={invoiceColumns}
              dataSource={invoices}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>
        </TabPane>
      </Tabs>

      {/* Credit Request Modal */}
      <Modal
        title="Request Credit Increase"
        visible={creditRequestVisible}
        onCancel={() => setCreditRequestVisible(false)}
        footer={null}
        width={600}
      >
        <Form form={form} layout="vertical">
          <Form.Item label="Current Credit Limit" name="currentLimit">
            <Input value={`$${creditInfo.creditLimit.toLocaleString()}`} disabled />
          </Form.Item>

          <Form.Item label="Requested Increase Amount" name="requestAmount" rules={[{ required: true }]}>
            <Input prefix="$" placeholder="25,000" />
          </Form.Item>

          <Form.Item label="Business Justification" name="reason" rules={[{ required: true }]}>
            <Input.TextArea rows={4} placeholder="Please explain why you need this credit increase..." />
          </Form.Item>

          <Form.Item label="Supporting Documentation" name="documents">
            <Upload>
              <Button icon={<UploadOutlined />}>Upload Financial Documents</Button>
            </Upload>
          </Form.Item>

          <div style={{ textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setCreditRequestVisible(false)}>Cancel</Button>
              <Button type="primary">Submit Request</Button>
            </Space>
          </div>
        </Form>
      </Modal>

      {/* Payment Modal */}
      <Modal
        title="Make Payment"
        visible={paymentModalVisible}
        onCancel={() => setPaymentModalVisible(false)}
        footer={null}
        width={500}
      >
        <Form layout="vertical">
          <Form.Item label="Payment Amount" rules={[{ required: true }]}>
            <Input prefix="$" placeholder="Enter amount" />
          </Form.Item>

          <Form.Item label="Payment Method" rules={[{ required: true }]}>
            <Select placeholder="Select payment method">
              <Option value="bank">Bank Transfer (***1234)</Option>
              <Option value="card">Credit Card (***5678)</Option>
              <Option value="ach">ACH Transfer</Option>
            </Select>
          </Form.Item>

          <Form.Item label="Payment Date">
            <Input placeholder="Immediate" disabled />
          </Form.Item>

          <div style={{ textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setPaymentModalVisible(false)}>Cancel</Button>
              <Button type="primary">Process Payment</Button>
            </Space>
          </div>
        </Form>
      </Modal>
    </div>
  );
};

export default CreditManagement;

customer/digital-downloads.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Progress,
  message,
  Descriptions,
  Modal,
  Alert,
  Statistic,
  Input,
  Tooltip,
} from 'antd';
import {
  DownloadOutlined,
  FileOutlined,
  SafetyOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  CopyOutlined,
  KeyOutlined,
  LockOutlined,
  CloudDownloadOutlined,
  FileZipOutlined,
  FilePdfOutlined,
  FileImageOutlined,
  CodeOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;

interface DigitalProduct {
  id: number;
  name: string;
  type: 'software' | 'ebook' | 'music' | 'video' | 'course' | 'template';
  orderDate: string;
  expiryDate?: string;
  downloadLimit: number;
  downloadCount: number;
  fileSize: string;
  version: string;
  licenseKey?: string;
  downloadUrl: string;
  status: 'active' | 'expired' | 'used_up';
}

const mockDigitalProducts: DigitalProduct[] = [
  {
    id: 1,
    name: 'Professional Photo Editing Software',
    type: 'software',
    orderDate: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    expiryDate: dayjs().add(355, 'days').format('YYYY-MM-DD'),
    downloadLimit: 5,
    downloadCount: 2,
    fileSize: '2.4 GB',
    version: '2024.1.0',
    licenseKey: 'XXXX-XXXX-XXXX-XXXX',
    downloadUrl: 'https://example.com/download/software',
    status: 'active',
  },
  {
    id: 2,
    name: 'Complete Web Development Course',
    type: 'course',
    orderDate: dayjs().subtract(30, 'days').format('YYYY-MM-DD'),
    downloadLimit: 10,
    downloadCount: 5,
    fileSize: '15.8 GB',
    version: '1.0',
    licenseKey: 'COURSE-2024-ABC123',
    downloadUrl: 'https://example.com/download/course',
    status: 'active',
  },
  {
    id: 3,
    name: 'Design Templates Bundle',
    type: 'template',
    orderDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    expiryDate: dayjs().add(25, 'days').format('YYYY-MM-DD'),
    downloadLimit: 3,
    downloadCount: 3,
    fileSize: '450 MB',
    version: '2.5',
    downloadUrl: 'https://example.com/download/templates',
    status: 'used_up',
  },
  {
    id: 4,
    name: 'JavaScript Programming eBook',
    type: 'ebook',
    orderDate: dayjs().subtract(60, 'days').format('YYYY-MM-DD'),
    expiryDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    downloadLimit: 5,
    downloadCount: 1,
    fileSize: '12 MB',
    version: '3rd Edition',
    downloadUrl: 'https://example.com/download/ebook',
    status: 'expired',
  },
];

const DigitalDownloadsPage: React.FC = () => {
  const [products, setProducts] = useState<DigitalProduct[]>(mockDigitalProducts);
  const [selectedProduct, setSelectedProduct] = useState<DigitalProduct | null>(null);
  const [isDetailsModalVisible, setIsDetailsModalVisible] = useState<boolean>(false);

  const handleDownload = (product: DigitalProduct) => {
    if (product.status === 'expired') {
      message.error('This download has expired. Please contact support.');
      return;
    }

    if (product.status === 'used_up') {
      message.error('Download limit reached. Please contact support for assistance.');
      return;
    }

    const updatedProducts = products.map((p) =>
      p.id === product.id ? { ...p, downloadCount: p.downloadCount + 1 } : p
    );
    setProducts(updatedProducts);

    message.success(`Starting download: ${product.name}`);
    // Simulate download
    window.open(product.downloadUrl, '_blank');
  };

  const handleCopyLicense = (licenseKey: string) => {
    navigator.clipboard.writeText(licenseKey);
    message.success('License key copied to clipboard!');
  };

  const getFileIcon = (type: DigitalProduct['type']) => {
    const icons = {
      software: <CodeOutlined style={{ fontSize: 24, color: '#1890ff' }} />,
      ebook: <FilePdfOutlined style={{ fontSize: 24, color: '#ff4d4f' }} />,
      music: <FileOutlined style={{ fontSize: 24, color: '#722ed1' }} />,
      video: <FileImageOutlined style={{ fontSize: 24, color: '#fa8c16' }} />,
      course: <FileZipOutlined style={{ fontSize: 24, color: '#52c41a' }} />,
      template: <FileOutlined style={{ fontSize: 24, color: '#13c2c2' }} />,
    };
    return icons[type];
  };

  const columns: ColumnsType<DigitalProduct> = [
    {
      title: 'Product',
      key: 'product',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <div
            style={{
              width: 48,
              height: 48,
              background: '#f0f0f0',
              borderRadius: 8,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            {getFileIcon(record.type)}
          </div>
          <div>
            <div>
              <Text strong>{record.name}</Text>
            </div>
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                Version: {record.version}  {record.fileSize}
              </Text>
            </div>
          </div>
        </div>
      ),
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: DigitalProduct['type']) => {
        const typeConfig = {
          software: { color: 'blue', text: 'Software' },
          ebook: { color: 'red', text: 'eBook' },
          music: { color: 'purple', text: 'Music' },
          video: { color: 'orange', text: 'Video' },
          course: { color: 'green', text: 'Course' },
          template: { color: 'cyan', text: 'Template' },
        };
        const config = typeConfig[type];
        return <Tag color={config.color}>{config.text}</Tag>;
      },
      filters: [
        { text: 'Software', value: 'software' },
        { text: 'eBook', value: 'ebook' },
        { text: 'Course', value: 'course' },
        { text: 'Template', value: 'template' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Downloads',
      key: 'downloads',
      render: (_, record) => {
        const percentage = (record.downloadCount / record.downloadLimit) * 100;
        return (
          <div style={{ width: 150 }}>
            <div style={{ marginBottom: 4 }}>
              <Text style={{ fontSize: 12 }}>
                {record.downloadCount} / {record.downloadLimit} used
              </Text>
            </div>
            <Progress
              percent={percentage}
              showInfo={false}
              size="small"
              strokeColor={percentage === 100 ? '#ff4d4f' : '#1890ff'}
            />
          </div>
        );
      },
    },
    {
      title: 'Purchased',
      dataIndex: 'orderDate',
      key: 'orderDate',
      render: (date) => dayjs(date).format('MMM DD, YYYY'),
    },
    {
      title: 'Expires',
      dataIndex: 'expiryDate',
      key: 'expiryDate',
      render: (date) => {
        if (!date) return <Text type="secondary">Never</Text>;
        const isExpired = dayjs(date).isBefore(dayjs());
        const daysLeft = dayjs(date).diff(dayjs(), 'days');
        return (
          <div>
            <div>{dayjs(date).format('MMM DD, YYYY')}</div>
            {!isExpired && daysLeft <= 30 && (
              <Tag color="warning" style={{ marginTop: 4 }}>
                {daysLeft} days left
              </Tag>
            )}
          </div>
        );
      },
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: DigitalProduct['status']) => {
        const statusConfig = {
          active: { color: 'success', icon: <CheckCircleOutlined />, text: 'Active' },
          expired: { color: 'error', icon: <ClockCircleOutlined />, text: 'Expired' },
          used_up: { color: 'warning', icon: <ClockCircleOutlined />, text: 'Limit Reached' },
        };
        const config = statusConfig[status];
        return (
          <Tag color={config.color} icon={config.icon}>
            {config.text}
          </Tag>
        );
      },
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Expired', value: 'expired' },
        { text: 'Limit Reached', value: 'used_up' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="primary"
            icon={<DownloadOutlined />}
            onClick={() => handleDownload(record)}
            disabled={record.status !== 'active'}
          >
            Download
          </Button>
          <Button
            onClick={() => {
              setSelectedProduct(record);
              setIsDetailsModalVisible(true);
            }}
          >
            Details
          </Button>
        </Space>
      ),
    },
  ];

  const activeProducts = products.filter((p) => p.status === 'active').length;
  const totalDownloads = products.reduce((sum, p) => sum + p.downloadCount, 0);
  const expiringProducts = products.filter(
    (p) =>
      p.expiryDate &&
      dayjs(p.expiryDate).isAfter(dayjs()) &&
      dayjs(p.expiryDate).diff(dayjs(), 'days') <= 30
  ).length;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <CloudDownloadOutlined style={{ color: '#1890ff' }} /> Digital Downloads
          </Title>
          <Paragraph type="secondary">
            Access and manage your digital product purchases
          </Paragraph>
        </Col>
      </Row>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Active Downloads"
              value={activeProducts}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Downloads"
              value={totalDownloads}
              prefix={<DownloadOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Expiring Soon"
              value={expiringProducts}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: expiringProducts > 0 ? '#fa8c16' : '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      <Alert
        message={
          <Space>
            <SafetyOutlined />
            <Text strong>Download Security Notice</Text>
          </Space>
        }
        description="All downloads are securely encrypted and verified. Keep your license keys safe and do not share them publicly."
        type="info"
        showIcon={false}
        style={{ marginBottom: 24 }}
      />

      <Card>
        <Table
          columns={columns}
          dataSource={products}
          rowKey="id"
          pagination={{ pageSize: 10 }}
        />
      </Card>

      <Modal
        title={
          <Space>
            <FileOutlined />
            <span>Download Details</span>
          </Space>
        }
        open={isDetailsModalVisible}
        onCancel={() => setIsDetailsModalVisible(false)}
        footer={[
          <Button key="close" onClick={() => setIsDetailsModalVisible(false)}>
            Close
          </Button>,
          <Button
            key="download"
            type="primary"
            icon={<DownloadOutlined />}
            onClick={() => {
              if (selectedProduct) {
                handleDownload(selectedProduct);
                setIsDetailsModalVisible(false);
              }
            }}
            disabled={selectedProduct?.status !== 'active'}
          >
            Download Now
          </Button>,
        ]}
        width={700}
      >
        {selectedProduct && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <div
              style={{
                background: '#f0f0f0',
                padding: 24,
                borderRadius: 8,
                textAlign: 'center',
              }}
            >
              {getFileIcon(selectedProduct.type)}
              <Title level={4} style={{ marginTop: 12, marginBottom: 0 }}>
                {selectedProduct.name}
              </Title>
              <Text type="secondary">Version {selectedProduct.version}</Text>
            </div>

            <Descriptions bordered column={1}>
              <Descriptions.Item label="Product Type">
                <Tag color="blue">{selectedProduct.type.toUpperCase()}</Tag>
              </Descriptions.Item>
              <Descriptions.Item label="File Size">{selectedProduct.fileSize}</Descriptions.Item>
              <Descriptions.Item label="Purchase Date">
                {dayjs(selectedProduct.orderDate).format('MMMM DD, YYYY')}
              </Descriptions.Item>
              {selectedProduct.expiryDate && (
                <Descriptions.Item label="Expiry Date">
                  {dayjs(selectedProduct.expiryDate).format('MMMM DD, YYYY')}
                  {dayjs(selectedProduct.expiryDate).diff(dayjs(), 'days') <= 30 && (
                    <Tag color="warning" style={{ marginLeft: 8 }}>
                      {dayjs(selectedProduct.expiryDate).diff(dayjs(), 'days')} days left
                    </Tag>
                  )}
                </Descriptions.Item>
              )}
              <Descriptions.Item label="Download Usage">
                <Progress
                  percent={
                    (selectedProduct.downloadCount / selectedProduct.downloadLimit) * 100
                  }
                  format={() =>
                    `${selectedProduct.downloadCount} / ${selectedProduct.downloadLimit}`
                  }
                />
              </Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag
                  color={
                    selectedProduct.status === 'active'
                      ? 'success'
                      : selectedProduct.status === 'expired'
                      ? 'error'
                      : 'warning'
                  }
                >
                  {selectedProduct.status.replace('_', ' ').toUpperCase()}
                </Tag>
              </Descriptions.Item>
            </Descriptions>

            {selectedProduct.licenseKey && (
              <Alert
                message={
                  <Space>
                    <KeyOutlined />
                    <Text strong>License Key</Text>
                  </Space>
                }
                description={
                  <div style={{ marginTop: 12 }}>
                    <Input.Group compact>
                      <Input
                        value={selectedProduct.licenseKey}
                        readOnly
                        style={{ width: 'calc(100% - 100px)' }}
                        prefix={<LockOutlined />}
                      />
                      <Tooltip title="Copy to clipboard">
                        <Button
                          icon={<CopyOutlined />}
                          onClick={() => handleCopyLicense(selectedProduct.licenseKey!)}
                        >
                          Copy
                        </Button>
                      </Tooltip>
                    </Input.Group>
                  </div>
                }
                type="warning"
                showIcon={false}
              />
            )}

            {selectedProduct.status === 'expired' && (
              <Alert
                message="Download Expired"
                description="This download link has expired. Please contact our support team to request a new download link."
                type="error"
                showIcon
              />
            )}

            {selectedProduct.status === 'used_up' && (
              <Alert
                message="Download Limit Reached"
                description="You have reached the maximum number of downloads for this product. Contact support if you need additional downloads."
                type="warning"
                showIcon
              />
            )}
          </Space>
        )}
      </Modal>
    </div>
  );
};

export default DigitalDownloadsPage;

support-tickets-integrated.__content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Table, Tag, Space, Modal, Form, Input, Select, Upload, message, Badge, Row, Col, Timeline, Avatar, Rate, Divider, Spin, Empty, Tabs, Statistic, Progress, Typography, List, Tooltip, FloatButton } from 'antd';
import { PlusOutlined, MessageOutlined, SearchOutlined, FilterOutlined, FileTextOutlined, ClockCircleOutlined, CheckCircleOutlined, ExclamationCircleOutlined, SendOutlined, PaperClipOutlined, CustomerServiceOutlined, StarOutlined, ReloadOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import { supportTicketsAPI, type SupportTicket, type TicketMessage, type CreateTicketRequest, type TicketStats } from '../services/api/supportTicketsAPI';
import './support-tickets.less';

const { TextArea } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Title, Text } = Typography;

// Support Tickets System with Live Chat Integration
const SupportTicketsSystem: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [tickets, setTickets] = useState<SupportTicket[]>([]);
  const [stats, setStats] = useState<TicketStats | null>(null);
  const [selectedTicket, setSelectedTicket] = useState<SupportTicket | null>(null);
  const [messages, setMessages] = useState<TicketMessage[]>([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showChatModal, setShowChatModal] = useState(false);
  const [showTicketDetails, setShowTicketDetails] = useState(false);
  const [messagesLoading, setMessagesLoading] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);
  const [chatAvailable, setChatAvailable] = useState(false);
  const [newMessage, setNewMessage] = useState('');
  const [filters, setFilters] = useState({
    status: undefined,
    priority: undefined,
    category: undefined,
    search: ''
  });

  const [createForm] = Form.useForm();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<any>(null);

  // Load initial data
  useEffect(() => {
    loadTickets();
    loadStats();
    checkChatAvailability();
  }, []);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadTickets = async () => {
    setLoading(true);
    try {
      const response = await supportTicketsAPI.getTickets(filters);
      if (response.success) {
        setTickets(response.data.tickets);
      } else {
        message.error('Failed to load tickets');
      }
    } catch (error) {
      console.error('Error loading tickets:', error);
      message.error('Failed to load tickets');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const response = await supportTicketsAPI.getTicketStats();
      if (response.success) {
        setStats(response.data);
      }
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const checkChatAvailability = async () => {
    try {
      const response = await supportTicketsAPI.getChatAvailability();
      if (response.success) {
        setChatAvailable(response.data.available);
      }
    } catch (error) {
      console.error('Error checking chat availability:', error);
    }
  };

  const loadTicketMessages = async (ticketId: string) => {
    setMessagesLoading(true);
    try {
      const response = await supportTicketsAPI.getTicketMessages(ticketId);
      if (response.success) {
        setMessages(response.data);
      } else {
        message.error('Failed to load messages');
      }
    } catch (error) {
      console.error('Error loading messages:', error);
      message.error('Failed to load messages');
    } finally {
      setMessagesLoading(false);
    }
  };

  const handleCreateTicket = async (values: any) => {
    try {
      const ticketData: CreateTicketRequest = {
        subject: values.subject,
        description: values.description,
        category: values.category,
        priority: values.priority,
        orderId: values.orderId
      };

      const response = await supportTicketsAPI.createTicket(ticketData);
      if (response.success) {
        message.success('Support ticket created successfully');
        setShowCreateModal(false);
        createForm.resetFields();
        loadTickets();
        loadStats();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating ticket:', error);
      message.error('Failed to create ticket');
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !selectedTicket) return;

    setSendingMessage(true);
    try {
      const response = await supportTicketsAPI.sendMessage(selectedTicket.id, newMessage);
      if (response.success) {
        setMessages([...messages, response.data]);
        setNewMessage('');
        loadTickets(); // Refresh tickets to update last activity
      } else {
        message.error('Failed to send message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      message.error('Failed to send message');
    } finally {
      setSendingMessage(false);
    }
  };

  const handleTicketSelect = async (ticket: SupportTicket) => {
    setSelectedTicket(ticket);
    setShowTicketDetails(true);
    await loadTicketMessages(ticket.id);
  };

  const handleSubmitRating = async (ticketId: string, rating: number, feedback?: string) => {
    try {
      const response = await supportTicketsAPI.submitRating(ticketId, rating, feedback);
      if (response.success) {
        message.success('Thank you for your feedback!');
        loadTickets();
      } else {
        message.error('Failed to submit rating');
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      message.error('Failed to submit rating');
    }
  };

  const getStatusColor = (status: SupportTicket['status']) => {
    const colors = {
      open: 'blue',
      in_progress: 'orange',
      waiting_for_customer: 'purple',
      resolved: 'green',
      closed: 'default'
    };
    return colors[status];
  };

  const getPriorityColor = (priority: SupportTicket['priority']) => {
    const colors = {
      low: 'default',
      medium: 'blue',
      high: 'orange',
      urgent: 'red'
    };
    return colors[priority];
  };

  const ticketColumns = [
    {
      title: 'Ticket #',
      dataIndex: 'ticketNumber',
      key: 'ticketNumber',
      render: (text: string, record: SupportTicket) => (
        <Button type="link" onClick={() => handleTicketSelect(record)}>
          {text}
        </Button>
      )
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      ellipsis: true
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: string) => (
        <Tag>{category.replace('_', ' ').toUpperCase()}</Tag>
      )
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: SupportTicket['status']) => (
        <Tag color={getStatusColor(status)}>
          {status.replace('_', ' ').toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Priority',
      dataIndex: 'priority',
      key: 'priority',
      render: (priority: SupportTicket['priority']) => (
        <Tag color={getPriorityColor(priority)}>
          {priority.toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Agent',
      dataIndex: 'assignedAgent',
      key: 'assignedAgent',
      render: (agent: SupportTicket['assignedAgent']) => (
        agent ? (
          <Space>
            <Avatar size="small" src={agent.avatar} icon={<CustomerServiceOutlined />} />
            <span>{agent.name}</span>
            <Badge status={agent.isOnline ? 'success' : 'default'} />
          </Space>
        ) : (
          <Text type="secondary">Unassigned</Text>
        )
      )
    },
    {
      title: 'Last Updated',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
      render: (date: string) => (
        <Tooltip title={new Date(date).toLocaleString()}>
          {new Date(date).toLocaleDateString()}
        </Tooltip>
      )
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_: any, record: SupportTicket) => (
        <Space>
          <Button 
            size="small" 
            icon={<MessageOutlined />}
            onClick={() => handleTicketSelect(record)}
          >
            View
          </Button>
          {record.status === 'resolved' && !record.satisfactionRating && (
            <Button 
              size="small" 
              icon={<StarOutlined />}
              onClick={() => {
                Modal.confirm({
                  title: 'Rate this support experience',
                  content: (
                    <div>
                      <p>How satisfied are you with the resolution?</p>
                      <Rate onChange={(value) => handleSubmitRating(record.id, value)} />
                    </div>
                  ),
                  okText: 'Submit Rating'
                });
              }}
            >
              Rate
            </Button>
          )}
        </Space>
      )
    }
  ];

  const renderTicketStats = () => (
    <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Total Tickets"
            value={stats?.total || 0}
            prefix={<FileTextOutlined />}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Open/In Progress"
            value={(stats?.open || 0) + (stats?.inProgress || 0)}
            prefix={<ClockCircleOutlined />}
            valueStyle={{ color: '#faad14' }}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Avg Response Time"
            value={stats?.avgResponseTime || 0}
            suffix="hrs"
            prefix={<ClockCircleOutlined />}
            precision={1}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Satisfaction Score"
            value={stats?.satisfactionScore || 0}
            prefix={<StarOutlined />}
            precision={1}
            suffix="/5"
            valueStyle={{ color: '#52c41a' }}
          />
        </Card>
      </Col>
    </Row>
  );

  const renderMessageBubble = (msg: TicketMessage) => {
    const isCustomer = msg.sender.type === 'customer';
    return (
      <div
        key={msg.id}
        style={{
          display: 'flex',
          justifyContent: isCustomer ? 'flex-end' : 'flex-start',
          marginBottom: 16
        }}
      >
        <div style={{ maxWidth: '70%' }}>
          {!isCustomer && (
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <Avatar size="small" src={msg.sender.avatar} icon={<CustomerServiceOutlined />} />
              <Text strong style={{ marginLeft: 8 }}>{msg.sender.name}</Text>
              {msg.sender.type === 'agent' && (
                <Tag style={{ marginLeft: 4 }}>Agent</Tag>
              )}
            </div>
          )}
          <div
            style={{
              backgroundColor: isCustomer ? '#1890ff' : '#f0f0f0',
              color: isCustomer ? 'white' : 'black',
              padding: '8px 12px',
              borderRadius: '8px',
              wordBreak: 'break-word'
            }}
          >
            {msg.message}
          </div>
          <div style={{ fontSize: '11px', color: '#999', marginTop: 4, textAlign: isCustomer ? 'right' : 'left' }}>
            {new Date(msg.sentAt).toLocaleString()}
          </div>
        </div>
      </div>
    );
  };

  const renderTicketDetails = () => (
    <Modal
      title={
        <Space>
          <FileTextOutlined />
          {selectedTicket?.ticketNumber} - {selectedTicket?.subject}
        </Space>
      }
      open={showTicketDetails}
      onCancel={() => {
        setShowTicketDetails(false);
        setSelectedTicket(null);
        setMessages([]);
      }}
      width={800}
      footer={null}
    >
      {selectedTicket && (
        <div>
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={8}>
              <Text strong>Status: </Text>
              <Tag color={getStatusColor(selectedTicket.status)}>
                {selectedTicket.status.replace('_', ' ').toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Priority: </Text>
              <Tag color={getPriorityColor(selectedTicket.priority)}>
                {selectedTicket.priority.toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Category: </Text>
              <Tag>{selectedTicket.category.toUpperCase()}</Tag>
            </Col>
          </Row>

          {selectedTicket.assignedAgent && (
            <Card size="small" style={{ marginBottom: 16 }}>
              <Space>
                <Avatar src={selectedTicket.assignedAgent.avatar} icon={<CustomerServiceOutlined />} />
                <div>
                  <Text strong>{selectedTicket.assignedAgent.name}</Text>
                  <br />
                  <Text type="secondary">{selectedTicket.assignedAgent.role}</Text>
                  <Badge 
                    status={selectedTicket.assignedAgent.isOnline ? 'success' : 'default'} 
                    text={selectedTicket.assignedAgent.isOnline ? 'Online' : 'Offline'}
                    style={{ marginLeft: 8 }}
                  />
                </div>
              </Space>
            </Card>
          )}

          <Divider>Conversation</Divider>

          <div style={{ maxHeight: 400, overflowY: 'auto', padding: '0 8px', marginBottom: 16 }}>
            {messagesLoading ? (
              <div style={{ textAlign: 'center', padding: 20 }}>
                <Spin />
              </div>
            ) : messages.length > 0 ? (
              <>
                {messages.map(renderMessageBubble)}
                <div ref={messagesEndRef} />
              </>
            ) : (
              <Empty description="No messages yet" />
            )}
          </div>

          {selectedTicket.status !== 'closed' && selectedTicket.status !== 'resolved' && (
            <div style={{ display: 'flex', gap: 8 }}>
              <Input.TextArea
                ref={messageInputRef}
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                autoSize={{ minRows: 2, maxRows: 4 }}
                onPressEnter={(e) => {
                  if (e.shiftKey) return;
                  e.preventDefault();
                  handleSendMessage();
                }}
              />
              <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
                <Button
                  type="primary"
                  icon={<SendOutlined />}
                  onClick={handleSendMessage}
                  loading={sendingMessage}
                  disabled={!newMessage.trim()}
                >
                  Send
                </Button>
                <Button
                  size="small"
                  icon={<PaperClipOutlined />}
                  title="Attach File"
                >
                  Attach
                </Button>
              </div>
            </div>
          )}

          {selectedTicket.satisfactionRating && (
            <Card size="small" style={{ marginTop: 16, backgroundColor: '#f6ffed' }}>
              <Text strong>Customer Rating: </Text>
              <Rate disabled value={selectedTicket.satisfactionRating.rating} />
              {selectedTicket.satisfactionRating.feedback && (
                <div style={{ marginTop: 8 }}>
                  <Text>"{selectedTicket.satisfactionRating.feedback}"</Text>
                </div>
              )}
            </Card>
          )}
        </div>
      )}
    </Modal>
  );

  const renderCreateTicketModal = () => (
    <Modal
      title="Create New Support Ticket"
      open={showCreateModal}
      onCancel={() => {
        setShowCreateModal(false);
        createForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={createForm}
        layout="vertical"
        onFinish={handleCreateTicket}
      >
        <Form.Item
          label="Subject"
          name="subject"
          rules={[{ required: true, message: 'Please enter a subject' }]}
        >
          <Input placeholder="Brief description of your issue" />
        </Form.Item>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Category"
              name="category"
              rules={[{ required: true, message: 'Please select a category' }]}
            >
              <Select placeholder="Select category">
                <Option value="general">General Inquiry</Option>
                <Option value="order">Order Issue</Option>
                <Option value="payment">Payment Problem</Option>
                <Option value="technical">Technical Support</Option>
                <Option value="product">Product Question</Option>
                <Option value="account">Account Issue</Option>
                <Option value="shipping">Shipping Problem</Option>
                <Option value="returns">Returns & Refunds</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Priority"
              name="priority"
              rules={[{ required: true, message: 'Please select priority' }]}
            >
              <Select placeholder="Select priority">
                <Option value="low">Low</Option>
                <Option value="medium">Medium</Option>
                <Option value="high">High</Option>
                <Option value="urgent">Urgent</Option>
              </Select>
            </Form.Item>
          </Col>
        </Row>

        <Form.Item
          label="Order ID (optional)"
          name="orderId"
        >
          <Input placeholder="Enter order number if related to an order" />
        </Form.Item>

        <Form.Item
          label="Description"
          name="description"
          rules={[{ required: true, message: 'Please describe your issue' }]}
        >
          <TextArea
            rows={4}
            placeholder="Please provide detailed information about your issue..."
          />
        </Form.Item>

        <Form.Item
          label="Attachments"
          name="attachments"
        >
          <Upload
            multiple
            beforeUpload={() => false}
            fileList={[]}
          >
            <Button icon={<PaperClipOutlined />}>
              Attach Files (Screenshots, Documents)
            </Button>
          </Upload>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit">
              Create Ticket
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderQuickActions = () => (
    <Card size="small" style={{ marginBottom: 16 }}>
      <Space wrap>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={() => setShowCreateModal(true)}
        >
          New Ticket
        </Button>
        <Button
          icon={<MessageOutlined />}
          onClick={() => setShowChatModal(true)}
          disabled={!chatAvailable}
        >
          Live Chat {chatAvailable && <Badge status="success" />}
        </Button>
        <Button
          icon={<ReloadOutlined />}
          onClick={loadTickets}
          loading={loading}
        >
          Refresh
        </Button>
        <Button
          icon={<QuestionCircleOutlined />}
          onClick={() => window.open('/help-center', '_blank')}
        >
          Help Center
        </Button>
      </Space>
    </Card>
  );

  return (
    <div className="support-tickets" style={{ padding: '24px', maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <CustomerServiceOutlined /> Support Center
        </Title>
        <Text type="secondary">
          Get help with your orders, technical issues, and general inquiries
        </Text>
      </div>

      {/* Stats */}
      {renderTicketStats()}

      {/* Quick Actions */}
      {renderQuickActions()}

      {/* Tickets Table */}
      <Card title="Your Support Tickets">
        <div style={{ marginBottom: 16 }}>
          <Row gutter={16}>
            <Col flex={1}>
              <Input
                placeholder="Search tickets..."
                prefix={<SearchOutlined />}
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                onPressEnter={loadTickets}
              />
            </Col>
            <Col>
              <Space>
                <Select
                  placeholder="Status"
                  allowClear
                  style={{ width: 120 }}
                  value={filters.status}
                  onChange={(value) => setFilters({ ...filters, status: value })}
                >
                  <Option value="open">Open</Option>
                  <Option value="in_progress">In Progress</Option>
                  <Option value="waiting_for_customer">Waiting</Option>
                  <Option value="resolved">Resolved</Option>
                  <Option value="closed">Closed</Option>
                </Select>
                <Select
                  placeholder="Priority"
                  allowClear
                  style={{ width: 100 }}
                  value={filters.priority}
                  onChange={(value) => setFilters({ ...filters, priority: value })}
                >
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="urgent">Urgent</Option>
                </Select>
                <Button icon={<FilterOutlined />} onClick={loadTickets}>
                  Filter
                </Button>
              </Space>
            </Col>
          </Row>
        </div>

        <Table
          columns={ticketColumns}
          dataSource={tickets}
          rowKey="id"
          loading={loading}
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `Total ${total} tickets`
          }}
        />
      </Card>

      {/* Modals */}
      {renderCreateTicketModal()}
      {renderTicketDetails()}

      {/* Live Chat Float Button */}
      {chatAvailable && (
        <FloatButton
          icon={<MessageOutlined />}
          type="primary"
          onClick={() => setShowChatModal(true)}
          tooltip="Start Live Chat"
        />
      )}

      {/* Chat Modal */}
      <Modal
        title="Live Chat Support"
        open={showChatModal}
        onCancel={() => setShowChatModal(false)}
        footer={null}
        width={500}
      >
        <div style={{ textAlign: 'center', padding: '20px 0' }}>
          <CustomerServiceOutlined style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }} />
          <Title level={4}>Connect with Support</Title>
          <Text>
            Our support agents are ready to help you with any questions or issues.
          </Text>
          <div style={{ margin: '20px 0' }}>
            <Button type="primary" size="large">
              Start Chat Session
            </Button>
          </div>
          <Text type="secondary">
            Average response time: 2 minutes
          </Text>
        </div>
      </Modal>
    </div>
  );
};

export default SupportTicketsSystem;

admin/automated-workflows.__content:
import React, { useState } from 'react';
import { Card, Table, Button, Space, Tag, Row, Col, Modal, Form, Input, Select, Switch, InputNumber, Typography, Alert, Tabs, Timeline, Progress } from 'antd';
import { PlayCircleOutlined, PauseCircleOutlined, EditOutlined, DeleteOutlined, PlusOutlined, ClockCircleOutlined, CheckCircleOutlined, ExclamationCircleOutlined, RobotOutlined, SettingOutlined } from '@ant-design/icons';

const { Option } = Select;
const { Title, Text } = Typography;
const { TabPane } = Tabs;
const { TextArea } = Input;

const AutomatedWorkflowsPage: React.FC = () => {
  const [workflows, setWorkflows] = useState([
    {
      id: 1,
      name: 'Auto Reorder Alert',
      description: 'Automatically send alerts when inventory falls below threshold',
      trigger: 'Inventory Level',
      status: 'active',
      lastRun: '2024-01-15 14:30',
      nextRun: '2024-01-16 00:00',
      category: 'inventory',
    },
    {
      id: 2,
      name: 'Price Monitoring',
      description: 'Monitor competitor prices and suggest adjustments',
      trigger: 'Daily Schedule',
      status: 'active',
      lastRun: '2024-01-15 06:00',
      nextRun: '2024-01-16 06:00',
      category: 'pricing',
    },
    {
      id: 3,
      name: 'Vendor Performance Review',
      description: 'Monthly vendor performance evaluation and reporting',
      trigger: 'Monthly Schedule',
      status: 'paused',
      lastRun: '2024-01-01 00:00',
      nextRun: '2024-02-01 00:00',
      category: 'vendor',
    },
  ]);

  const [isModalVisible, setIsModalVisible] = useState(false);
  const [editingWorkflow, setEditingWorkflow] = useState<any>(null);
  const [form] = Form.useForm();

  const workflowHistory = [
    { time: '2024-01-15 14:30', action: 'Auto Reorder Alert executed', status: 'success', details: '15 products flagged for reorder' },
    { time: '2024-01-15 12:00', action: 'Bulk Price Update completed', status: 'success', details: '142 products updated' },
    { time: '2024-01-15 06:00', action: 'Price Monitoring scan', status: 'success', details: '5 pricing recommendations generated' },
    { time: '2024-01-14 23:45', action: 'Inventory Sync failed', status: 'error', details: 'Connection timeout to vendor API' },
  ];

  const activeRuns = [
    { id: 1, workflow: 'Inventory Sync', progress: 75, status: 'running', eta: '5 minutes' },
    { id: 2, workflow: 'Customer Email Campaign', progress: 45, status: 'running', eta: '12 minutes' },
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'green';
      case 'paused': return 'orange';
      case 'error': return 'red';
      default: return 'blue';
    }
  };

  const getHistoryStatusColor = (status: string) => {
    switch (status) {
      case 'success': return 'green';
      case 'error': return 'red';
      case 'warning': return 'orange';
      default: return 'blue';
    }
  };

  const toggleWorkflowStatus = (id: number) => {
    setWorkflows(prev => prev.map(w => 
      w.id === id 
        ? { ...w, status: w.status === 'active' ? 'paused' : 'active' }
        : w
    ));
  };

  const handleCreateWorkflow = () => {
    setEditingWorkflow(null);
    setIsModalVisible(true);
    form.resetFields();
  };

  const handleEditWorkflow = (workflow: any) => {
    setEditingWorkflow(workflow);
    setIsModalVisible(true);
    form.setFieldsValue(workflow);
  };

  const handleSaveWorkflow = async () => {
    try {
      const values = await form.validateFields();
      if (editingWorkflow) {
        setWorkflows(prev => prev.map(w => 
          w.id === editingWorkflow.id ? { ...w, ...values } : w
        ));
      } else {
        const newWorkflow = {
          id: Date.now(),
          ...values,
          status: 'active',
          lastRun: '-',
          nextRun: 'Pending',
        };
        setWorkflows(prev => [...prev, newWorkflow]);
      }
      setIsModalVisible(false);
      form.resetFields();
    } catch (error) {
      console.error('Validation failed:', error);
    }
  };

  const workflowColumns = [
    {
      title: 'Workflow',
      key: 'workflow',
      render: (record: any) => (
        <div>
          <Text strong>{record.name}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: '12px' }}>{record.description}</Text>
        </div>
      ),
      width: 300,
    },
    {
      title: 'Trigger',
      dataIndex: 'trigger',
      key: 'trigger',
      width: 120,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)} icon={status === 'active' ? <CheckCircleOutlined /> : <PauseCircleOutlined />}>
          {status.toUpperCase()}
        </Tag>
      ),
      width: 100,
    },
    {
      title: 'Last Run',
      dataIndex: 'lastRun',
      key: 'lastRun',
      width: 140,
    },
    {
      title: 'Next Run',
      dataIndex: 'nextRun',
      key: 'nextRun',
      width: 140,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (record: any) => (
        <Space>
          <Button 
            size="small" 
            icon={record.status === 'active' ? <PauseCircleOutlined /> : <PlayCircleOutlined />}
            onClick={() => toggleWorkflowStatus(record.id)}
          >
            {record.status === 'active' ? 'Pause' : 'Resume'}
          </Button>
          <Button size="small" icon={<EditOutlined />} onClick={() => handleEditWorkflow(record)} />
          <Button size="small" icon={<DeleteOutlined />} danger />
        </Space>
      ),
      width: 180,
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <RobotOutlined style={{ marginRight: 8 }} />
          Automated Workflows
        </Title>
        <Text type="secondary">Phase 4 - Intelligent automation system</Text>
      </div>

      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Text type="secondary">Active Workflows</Text>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#52c41a' }}>
              {workflows.filter(w => w.status === 'active').length}
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Text type="secondary">Total Executions Today</Text>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#1890ff' }}>
              47
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Text type="secondary">Success Rate</Text>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#52c41a' }}>
              98.2%
            </div>
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Text type="secondary">Time Saved</Text>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: '#722ed1' }}>
              12.5h
            </div>
          </Card>
        </Col>
      </Row>

      <Tabs defaultActiveKey="workflows">
        <TabPane tab="Workflow Management" key="workflows">
          <Card
            title="Automated Workflows"
            extra={
              <Button type="primary" icon={<PlusOutlined />} onClick={handleCreateWorkflow}>
                Create Workflow
              </Button>
            }
          >
            <Table
              columns={workflowColumns}
              dataSource={workflows}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>
        </TabPane>

        <TabPane tab="Active Runs" key="running">
          <Card title="Currently Running Workflows">
            {activeRuns.map(run => (
              <Card key={run.id} size="small" style={{ marginBottom: 16 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div>
                    <Text strong>{run.workflow}</Text>
                    <br />
                    <Text type="secondary">ETA: {run.eta}</Text>
                  </div>
                  <div style={{ width: 200 }}>
                    <Progress percent={run.progress} status="active" />
                  </div>
                  <Button size="small" icon={<PauseCircleOutlined />}>
                    Pause
                  </Button>
                </div>
              </Card>
            ))}
          </Card>
        </TabPane>

        <TabPane tab="Execution History" key="history">
          <Card title="Recent Workflow Executions">
            <Timeline>
              {workflowHistory.map((item, index) => (
                <Timeline.Item
                  key={index}
                  color={getHistoryStatusColor(item.status)}
                  dot={item.status === 'error' ? <ExclamationCircleOutlined /> : <CheckCircleOutlined />}
                >
                  <div>
                    <Text strong>{item.action}</Text>
                    <br />
                    <Text type="secondary">{item.time}</Text>
                    <br />
                    <Text>{item.details}</Text>
                  </div>
                </Timeline.Item>
              ))}
            </Timeline>
          </Card>
        </TabPane>

        <TabPane tab="Workflow Templates" key="templates">
          <Row gutter={16}>
            <Col span={8}>
              <Card 
                title="Inventory Management" 
                actions={[<Button type="link">Use Template</Button>]}
              >
                <Alert message="Auto-reorder alerts, stock level monitoring, and supplier notifications" type="info" />
              </Card>
            </Col>
            <Col span={8}>
              <Card 
                title="Price Optimization" 
                actions={[<Button type="link">Use Template</Button>]}
              >
                <Alert message="Competitor price monitoring, dynamic pricing, and profit margin optimization" type="info" />
              </Card>
            </Col>
            <Col span={8}>
              <Card 
                title="Customer Engagement" 
                actions={[<Button type="link">Use Template</Button>]}
              >
                <Alert message="Automated email campaigns, loyalty rewards, and personalized recommendations" type="info" />
              </Card>
            </Col>
          </Row>
        </TabPane>
      </Tabs>

      <Modal
        title={editingWorkflow ? 'Edit Workflow' : 'Create New Workflow'}
        visible={isModalVisible}
        onOk={handleSaveWorkflow}
        onCancel={() => setIsModalVisible(false)}
        width={600}
      >
        <Form form={form} layout="vertical">
          <Form.Item label="Workflow Name" name="name" rules={[{ required: true }]}>
            <Input placeholder="Enter workflow name" />
          </Form.Item>

          <Form.Item label="Description" name="description" rules={[{ required: true }]}>
            <TextArea placeholder="Describe what this workflow does" rows={3} />
          </Form.Item>

          <Form.Item label="Category" name="category" rules={[{ required: true }]}>
            <Select placeholder="Select category">
              <Option value="inventory">Inventory Management</Option>
              <Option value="pricing">Price Optimization</Option>
              <Option value="vendor">Vendor Management</Option>
              <Option value="customer">Customer Engagement</Option>
              <Option value="reporting">Reporting & Analytics</Option>
            </Select>
          </Form.Item>

          <Form.Item label="Trigger Type" name="trigger" rules={[{ required: true }]}>
            <Select placeholder="Select trigger">
              <Option value="Manual">Manual Execution</Option>
              <Option value="Daily Schedule">Daily Schedule</Option>
              <Option value="Weekly Schedule">Weekly Schedule</Option>
              <Option value="Monthly Schedule">Monthly Schedule</Option>
              <Option value="Inventory Level">Inventory Level Trigger</Option>
              <Option value="Price Change">Price Change Trigger</Option>
              <Option value="Order Event">Order Event Trigger</Option>
            </Select>
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Priority" name="priority">
                <Select placeholder="Select priority" defaultValue="medium">
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="critical">Critical</Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Timeout (minutes)" name="timeout">
                <InputNumber style={{ width: '100%' }} defaultValue={30} min={1} max={1440} />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item label="Auto-start" name="autoStart" valuePropName="checked">
            <Switch defaultChecked />
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default AutomatedWorkflowsPage;

customer/advanced-filters.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Slider,
  Checkbox,
  Radio,
  Space,
  Button,
  Tag,
  Divider,
  Collapse,
  Rate,
  Input,
  Badge,
  Empty,
  Select,
  Image,
} from 'antd';
import {
  FilterOutlined,
  SearchOutlined,
  ClearOutlined,
  StarOutlined,
  DollarOutlined,
  TagsOutlined,
  ShoppingOutlined,
  CheckCircleOutlined,
} from '@ant-design/icons';

const { Title, Text } = Typography;
const { Panel } = Collapse;

interface FilterState {
  priceRange: [number, number];
  categories: string[];
  brands: string[];
  ratings: number | null;
  features: string[];
  colors: string[];
  sizes: string[];
  availability: string[];
  shipping: string[];
  discount: boolean;
  searchQuery: string;
  sortBy: string;
}

interface Product {
  id: number;
  name: string;
  price: number;
  originalPrice?: number;
  category: string;
  brand: string;
  rating: number;
  reviews: number;
  colors: string[];
  sizes: string[];
  features: string[];
  inStock: boolean;
  freeShipping: boolean;
  image: string;
}

const mockProducts: Product[] = [
  {
    id: 1,
    name: 'Premium Wireless Headphones',
    price: 299.99,
    originalPrice: 399.99,
    category: 'Electronics',
    brand: 'AudioTech',
    rating: 4.8,
    reviews: 1234,
    colors: ['Black', 'Silver', 'White'],
    sizes: ['One Size'],
    features: ['Noise Cancelling', 'Bluetooth 5.0', 'Long Battery'],
    inStock: true,
    freeShipping: true,
    image: 'https://via.placeholder.com/200x200?text=Headphones',
  },
  {
    id: 2,
    name: 'Smart Fitness Watch',
    price: 199.99,
    category: 'Wearables',
    brand: 'FitGear',
    rating: 4.5,
    reviews: 856,
    colors: ['Black', 'Rose Gold'],
    sizes: ['S', 'M', 'L'],
    features: ['Heart Rate Monitor', 'GPS', 'Waterproof'],
    inStock: true,
    freeShipping: false,
    image: 'https://via.placeholder.com/200x200?text=Watch',
  },
  {
    id: 3,
    name: 'Professional Camera Lens',
    price: 799.99,
    category: 'Photography',
    brand: 'LensPro',
    rating: 4.9,
    reviews: 423,
    colors: ['Black'],
    sizes: ['50mm', '85mm'],
    features: ['Image Stabilization', 'Weather Sealed', 'Auto Focus'],
    inStock: false,
    freeShipping: true,
    image: 'https://via.placeholder.com/200x200?text=Lens',
  },
  {
    id: 4,
    name: 'Gaming Mechanical Keyboard',
    price: 149.99,
    originalPrice: 199.99,
    category: 'Electronics',
    brand: 'GameGear',
    rating: 4.7,
    reviews: 2341,
    colors: ['Black', 'White', 'RGB'],
    sizes: ['Full Size', 'TKL'],
    features: ['RGB Lighting', 'Mechanical Switches', 'Programmable'],
    inStock: true,
    freeShipping: true,
    image: 'https://via.placeholder.com/200x200?text=Keyboard',
  },
];

const AdvancedFiltersPage: React.FC = () => {
  const [filters, setFilters] = useState<FilterState>({
    priceRange: [0, 1000],
    categories: [],
    brands: [],
    ratings: null,
    features: [],
    colors: [],
    sizes: [],
    availability: [],
    shipping: [],
    discount: false,
    searchQuery: '',
    sortBy: 'relevance',
  });

  const [activeFiltersCount, setActiveFiltersCount] = useState<number>(0);

  const updateFilter = (key: keyof FilterState, value: any) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);
    calculateActiveFilters(newFilters);
  };

  const calculateActiveFilters = (currentFilters: FilterState) => {
    let count = 0;
    if (currentFilters.categories.length > 0) count++;
    if (currentFilters.brands.length > 0) count++;
    if (currentFilters.ratings !== null) count++;
    if (currentFilters.features.length > 0) count++;
    if (currentFilters.colors.length > 0) count++;
    if (currentFilters.sizes.length > 0) count++;
    if (currentFilters.availability.length > 0) count++;
    if (currentFilters.shipping.length > 0) count++;
    if (currentFilters.discount) count++;
    if (currentFilters.priceRange[0] !== 0 || currentFilters.priceRange[1] !== 1000) count++;
    setActiveFiltersCount(count);
  };

  const clearAllFilters = () => {
    const resetFilters: FilterState = {
      priceRange: [0, 1000],
      categories: [],
      brands: [],
      ratings: null,
      features: [],
      colors: [],
      sizes: [],
      availability: [],
      shipping: [],
      discount: false,
      searchQuery: '',
      sortBy: 'relevance',
    };
    setFilters(resetFilters);
    setActiveFiltersCount(0);
  };

  const filteredProducts = mockProducts.filter((product) => {
    // Price filter
    if (product.price < filters.priceRange[0] || product.price > filters.priceRange[1]) {
      return false;
    }

    // Category filter
    if (filters.categories.length > 0 && !filters.categories.includes(product.category)) {
      return false;
    }

    // Brand filter
    if (filters.brands.length > 0 && !filters.brands.includes(product.brand)) {
      return false;
    }

    // Rating filter
    if (filters.ratings !== null && product.rating < filters.ratings) {
      return false;
    }

    // Features filter
    if (
      filters.features.length > 0 &&
      !filters.features.some((f) => product.features.includes(f))
    ) {
      return false;
    }

    // Colors filter
    if (filters.colors.length > 0 && !filters.colors.some((c) => product.colors.includes(c))) {
      return false;
    }

    // Sizes filter
    if (filters.sizes.length > 0 && !filters.sizes.some((s) => product.sizes.includes(s))) {
      return false;
    }

    // Availability filter
    if (filters.availability.includes('in_stock') && !product.inStock) {
      return false;
    }
    if (filters.availability.includes('out_of_stock') && product.inStock) {
      return false;
    }

    // Shipping filter
    if (filters.shipping.includes('free_shipping') && !product.freeShipping) {
      return false;
    }

    // Discount filter
    if (filters.discount && !product.originalPrice) {
      return false;
    }

    // Search query
    if (
      filters.searchQuery &&
      !product.name.toLowerCase().includes(filters.searchQuery.toLowerCase())
    ) {
      return false;
    }

    return true;
  });

  const categories = Array.from(new Set(mockProducts.map((p) => p.category)));
  const brands = Array.from(new Set(mockProducts.map((p) => p.brand)));
  const allFeatures = Array.from(new Set(mockProducts.flatMap((p) => p.features)));
  const allColors = Array.from(new Set(mockProducts.flatMap((p) => p.colors)));
  const allSizes = Array.from(new Set(mockProducts.flatMap((p) => p.sizes)));

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <FilterOutlined style={{ color: '#1890ff' }} /> Advanced Product Filters
          </Title>
          <Space>
            <Text type="secondary">{filteredProducts.length} products found</Text>
            {activeFiltersCount > 0 && (
              <Badge count={activeFiltersCount} style={{ backgroundColor: '#1890ff' }}>
                <Tag color="blue">Active Filters</Tag>
              </Badge>
            )}
          </Space>
        </Col>
        <Col>
          <Space>
            <Button icon={<ClearOutlined />} onClick={clearAllFilters}>
              Clear All Filters
            </Button>
          </Space>
        </Col>
      </Row>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={6}>
          <Card title="Filters" style={{ position: 'sticky', top: 24 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              <div>
                <Input
                  placeholder="Search products..."
                  prefix={<SearchOutlined />}
                  size="large"
                  value={filters.searchQuery}
                  onChange={(e) => updateFilter('searchQuery', e.target.value)}
                  allowClear
                />
              </div>

              <Divider style={{ margin: '12px 0' }} />

              <Collapse
                defaultActiveKey={['price', 'category', 'brand', 'rating']}
                ghost
                expandIconPosition="end"
              >
                <Panel
                  header={
                    <Space>
                      <DollarOutlined />
                      <Text strong>Price Range</Text>
                    </Space>
                  }
                  key="price"
                >
                  <Space direction="vertical" style={{ width: '100%' }}>
                    <Slider
                      range
                      min={0}
                      max={1000}
                      step={10}
                      value={filters.priceRange}
                      onChange={(value) => updateFilter('priceRange', value)}
                      tooltip={{ formatter: (value) => `$${value}` }}
                    />
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                      <Text>${filters.priceRange[0]}</Text>
                      <Text>${filters.priceRange[1]}</Text>
                    </div>
                  </Space>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <TagsOutlined />
                      <Text strong>Category</Text>
                      {filters.categories.length > 0 && (
                        <Badge count={filters.categories.length} />
                      )}
                    </Space>
                  }
                  key="category"
                >
                  <Checkbox.Group
                    value={filters.categories}
                    onChange={(values) => updateFilter('categories', values)}
                  >
                    <Space direction="vertical">
                      {categories.map((cat) => (
                        <Checkbox key={cat} value={cat}>
                          {cat}
                        </Checkbox>
                      ))}
                    </Space>
                  </Checkbox.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <ShoppingOutlined />
                      <Text strong>Brand</Text>
                      {filters.brands.length > 0 && <Badge count={filters.brands.length} />}
                    </Space>
                  }
                  key="brand"
                >
                  <Checkbox.Group
                    value={filters.brands}
                    onChange={(values) => updateFilter('brands', values)}
                  >
                    <Space direction="vertical">
                      {brands.map((brand) => (
                        <Checkbox key={brand} value={brand}>
                          {brand}
                        </Checkbox>
                      ))}
                    </Space>
                  </Checkbox.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <StarOutlined />
                      <Text strong>Rating</Text>
                    </Space>
                  }
                  key="rating"
                >
                  <Radio.Group
                    value={filters.ratings}
                    onChange={(e) => updateFilter('ratings', e.target.value)}
                  >
                    <Space direction="vertical">
                      {[4, 3, 2, 1].map((rating) => (
                        <Radio key={rating} value={rating}>
                          <Rate disabled value={rating} style={{ fontSize: 14 }} /> & Up
                        </Radio>
                      ))}
                      <Radio value={null}>Any Rating</Radio>
                    </Space>
                  </Radio.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <CheckCircleOutlined />
                      <Text strong>Features</Text>
                      {filters.features.length > 0 && <Badge count={filters.features.length} />}
                    </Space>
                  }
                  key="features"
                >
                  <Checkbox.Group
                    value={filters.features}
                    onChange={(values) => updateFilter('features', values)}
                  >
                    <Space direction="vertical">
                      {allFeatures.map((feature) => (
                        <Checkbox key={feature} value={feature}>
                          {feature}
                        </Checkbox>
                      ))}
                    </Space>
                  </Checkbox.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <span></span>
                      <Text strong>Color</Text>
                      {filters.colors.length > 0 && <Badge count={filters.colors.length} />}
                    </Space>
                  }
                  key="color"
                >
                  <Checkbox.Group
                    value={filters.colors}
                    onChange={(values) => updateFilter('colors', values)}
                  >
                    <Space direction="vertical">
                      {allColors.map((color) => (
                        <Checkbox key={color} value={color}>
                          <Space>
                            <div
                              style={{
                                width: 16,
                                height: 16,
                                borderRadius: '50%',
                                background: color.toLowerCase(),
                                border: '1px solid #d9d9d9',
                              }}
                            />
                            {color}
                          </Space>
                        </Checkbox>
                      ))}
                    </Space>
                  </Checkbox.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <span></span>
                      <Text strong>Size</Text>
                      {filters.sizes.length > 0 && <Badge count={filters.sizes.length} />}
                    </Space>
                  }
                  key="size"
                >
                  <Checkbox.Group
                    value={filters.sizes}
                    onChange={(values) => updateFilter('sizes', values)}
                  >
                    <Space direction="vertical">
                      {allSizes.map((size) => (
                        <Checkbox key={size} value={size}>
                          {size}
                        </Checkbox>
                      ))}
                    </Space>
                  </Checkbox.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <span></span>
                      <Text strong>Availability</Text>
                    </Space>
                  }
                  key="availability"
                >
                  <Checkbox.Group
                    value={filters.availability}
                    onChange={(values) => updateFilter('availability', values)}
                  >
                    <Space direction="vertical">
                      <Checkbox value="in_stock">In Stock</Checkbox>
                      <Checkbox value="out_of_stock">Out of Stock</Checkbox>
                    </Space>
                  </Checkbox.Group>
                </Panel>

                <Panel
                  header={
                    <Space>
                      <span></span>
                      <Text strong>Shipping</Text>
                    </Space>
                  }
                  key="shipping"
                >
                  <Checkbox.Group
                    value={filters.shipping}
                    onChange={(values) => updateFilter('shipping', values)}
                  >
                    <Space direction="vertical">
                      <Checkbox value="free_shipping">Free Shipping</Checkbox>
                    </Space>
                  </Checkbox.Group>
                </Panel>
              </Collapse>

              <Checkbox
                checked={filters.discount}
                onChange={(e) => updateFilter('discount', e.target.checked)}
              >
                <Text strong>Discounted Items Only</Text>
              </Checkbox>
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={18}>
          <Card
            title={`Results (${filteredProducts.length})`}
            extra={
              <Select
                value={filters.sortBy}
                onChange={(value) => updateFilter('sortBy', value)}
                style={{ width: 200 }}
              >
                <Select.Option value="relevance">Most Relevant</Select.Option>
                <Select.Option value="price_low">Price: Low to High</Select.Option>
                <Select.Option value="price_high">Price: High to Low</Select.Option>
                <Select.Option value="rating">Highest Rated</Select.Option>
                <Select.Option value="reviews">Most Reviewed</Select.Option>
              </Select>
            }
          >
            {filteredProducts.length === 0 ? (
              <Empty description="No products match your filters" />
            ) : (
              <Row gutter={[16, 16]}>
                {filteredProducts.map((product) => (
                  <Col xs={24} sm={12} lg={8} key={product.id}>
                    <Card hoverable>
                      <div style={{ position: 'relative' }}>
                        <Image
                          src={product.image}
                          alt={product.name}
                          preview={false}
                          style={{ marginBottom: 12 }}
                        />
                        {product.originalPrice && (
                          <Tag color="red" style={{ position: 'absolute', top: 8, left: 8 }}>
                            {Math.round(
                              ((product.originalPrice - product.price) / product.originalPrice) *
                                100
                            )}
                            % OFF
                          </Tag>
                        )}
                        {!product.inStock && (
                          <Tag color="default" style={{ position: 'absolute', top: 8, right: 8 }}>
                            Out of Stock
                          </Tag>
                        )}
                      </div>

                      <Title level={5} ellipsis={{ rows: 2 }} style={{ minHeight: 48 }}>
                        {product.name}
                      </Title>

                      <div style={{ marginBottom: 8 }}>
                        <Rate disabled value={product.rating} style={{ fontSize: 12 }} />
                        <Text type="secondary" style={{ marginLeft: 8, fontSize: 12 }}>
                          ({product.reviews})
                        </Text>
                      </div>

                      <div style={{ marginBottom: 12 }}>
                        <Text strong style={{ fontSize: 20, color: '#1890ff' }}>
                          ${product.price.toFixed(2)}
                        </Text>
                        {product.originalPrice && (
                          <Text delete type="secondary" style={{ marginLeft: 8 }}>
                            ${product.originalPrice.toFixed(2)}
                          </Text>
                        )}
                      </div>

                      <Space wrap>
                        {product.freeShipping && (
                          <Tag color="green" icon={<CheckCircleOutlined />}>
                            Free Shipping
                          </Tag>
                        )}
                        {product.features.slice(0, 1).map((feature) => (
                          <Tag key={feature}>{feature}</Tag>
                        ))}
                      </Space>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default AdvancedFiltersPage;

customer/favorite-vendors.__content:
