    window.location.href = `/customer/vendor-profile?vendor=${vendorSlug}`;
  };

  const getFilteredVendors = () => {
    if (activeTab === 'all') return favorites;
    return favorites.filter(v => 
      v.categories.some(cat => cat.toLowerCase().includes(activeTab.toLowerCase()))
    );
  };

  const filteredVendors = getFilteredVendors();

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <StarFilled style={{ color: '#ff9900' }} /> My Favorite Vendors
        </Title>
        <Paragraph type="secondary">
          Keep track of your favorite vendors and get notified of their latest products and offers
        </Paragraph>
      </div>

      {/* Statistics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Favorite Vendors"
              value={favorites.length}
              prefix={<StarOutlined />}
              valueStyle={{ color: '#ff9900' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Products"
              value={favorites.reduce((sum, v) => sum + v.totalProducts, 0)}
              prefix={<ShopOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Average Rating"
              value={
                favorites.length > 0
                  ? (favorites.reduce((sum, v) => sum + v.rating, 0) / favorites.length).toFixed(1)
                  : 0
              }
              prefix={<TrophyOutlined />}
              suffix="/ 5.0"
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Filter Tabs */}
      <Card>
        <Tabs
          activeKey={activeTab}
          onChange={setActiveTab}
          items={[
            { key: 'all', label: `All (${favorites.length})` },
            { key: 'electronics', label: 'Electronics' },
            { key: 'fashion', label: 'Fashion' },
            { key: 'home', label: 'Home & Garden' },
            { key: 'sports', label: 'Sports' },
          ]}
        />

        {filteredVendors.length === 0 ? (
          <Empty
            description="No favorite vendors found"
            image={Empty.PRESENTED_IMAGE_SIMPLE}
          >
            <Button type="primary" href="/customer/vendors">
              Browse Vendors
            </Button>
          </Empty>
        ) : (
          <Row gutter={[16, 16]}>
            {filteredVendors.map(vendor => (
              <Col xs={24} lg={12} key={vendor.id}>
                <Card
                  hoverable
                  style={{ height: '100%' }}
                  actions={[
                    <Tooltip title="Visit Store">
                      <Button
                        type="text"
                        icon={<EyeOutlined />}
                        onClick={() => handleVisitStore(vendor.slug)}
                      >
                        View Store
                      </Button>
                    </Tooltip>,
                    <Tooltip title="Unfollow">
                      <Button
                        type="text"
                        danger
                        icon={<DeleteOutlined />}
                        onClick={() => handleUnfollow(vendor.id, vendor.name)}
                      >
                        Unfollow
                      </Button>
                    </Tooltip>,
                  ]}
                >
                  <div style={{ display: 'flex', gap: 16 }}>
                    <Avatar
                      size={80}
                      src={vendor.logo}
                      alt={vendor.name}
                    />
                    <div style={{ flex: 1 }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 8 }}>
                        <Title level={5} style={{ margin: 0 }}>
                          {vendor.name}
                        </Title>
                        {vendor.verified && (
                          <Tooltip title="Verified Vendor">
                            <Tag color="blue">Verified</Tag>
                          </Tooltip>
                        )}
                      </div>

                      <div style={{ marginBottom: 8 }}>
                        <Rate disabled value={vendor.rating} allowHalf />
                        <Text type="secondary" style={{ marginLeft: 8 }}>
                          {vendor.rating} ({vendor.totalReviews} reviews)
                        </Text>
                      </div>

                      <Paragraph
                        type="secondary"
                        ellipsis={{ rows: 2 }}
                        style={{ marginBottom: 12 }}
                      >
                        {vendor.description}
                      </Paragraph>

                      {/* Badges */}
                      <Space wrap style={{ marginBottom: 12 }}>
                        {vendor.badges.map(badge => (
                          <Tag color="gold" key={badge}>
                            <TrophyOutlined /> {badge}
                          </Tag>
                        ))}
                      </Space>

                      {/* Categories */}
                      <div style={{ marginBottom: 12 }}>
                        <Space wrap size={[0, 4]}>
                          {vendor.categories.map(cat => (
                            <Tag key={cat}>{cat}</Tag>
                          ))}
                        </Space>
                      </div>

                      {/* Contact Info */}
                      <div style={{ fontSize: 12, color: '#8c8c8c' }}>
                        <div style={{ marginBottom: 4 }}>
                          <EnvironmentOutlined /> {vendor.location}
                        </div>
                        <div style={{ marginBottom: 4 }}>
                          <ShopOutlined /> {vendor.totalProducts} products
                        </div>
                        <div style={{ marginBottom: 4 }}>
                          <StarOutlined /> Followed since {new Date(vendor.followedDate).toLocaleDateString()}
                        </div>
                      </div>

                      {/* Quick Contact */}
                      <Space style={{ marginTop: 12 }} wrap>
                        <Tooltip title={vendor.phone}>
                          <Button
                            size="small"
                            icon={<PhoneOutlined />}
                            href={`tel:${vendor.phone}`}
                          />
                        </Tooltip>
                        <Tooltip title={vendor.email}>
                          <Button
                            size="small"
                            icon={<MailOutlined />}
                            href={`mailto:${vendor.email}`}
                          />
                        </Tooltip>
                        <Tooltip title={vendor.website}>
                          <Button
                            size="small"
                            icon={<GlobalOutlined />}
                            href={`https://${vendor.website}`}
                            target="_blank"
                          />
                        </Tooltip>
                      </Space>
                    </div>
                  </div>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </Card>
    </div>
  );
};

export default FavoriteVendorsPage;

customer/referral-program.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Input,
  Space,
  Tag,
  message,
  List,
  Avatar,
  Statistic,
  Progress,
  Divider,
  Alert,
  Steps,
  Table,
  Tooltip,
  QRCode,
  Modal,
  Badge,
} from 'antd';
import {
  GiftOutlined,
  CopyOutlined,
  ShareAltOutlined,
  DollarOutlined,
  UserAddOutlined,
  TrophyOutlined,
  CheckCircleOutlined,
  QrcodeOutlined,
  MailOutlined,
  FacebookOutlined,
  TwitterOutlined,
  WhatsAppOutlined,
  LinkedinOutlined,
  StarOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;

interface Referral {
  id: number;
  name: string;
  email: string;
  status: 'pending' | 'signed_up' | 'purchased' | 'rewarded';
  signupDate?: string;
  purchaseDate?: string;
  rewardAmount: number;
  orderValue?: number;
}

interface Reward {
  id: number;
  type: 'store_credit' | 'discount' | 'cashback';
  amount: number;
  description: string;
  earnedDate: string;
  expiryDate?: string;
  status: 'pending' | 'available' | 'used' | 'expired';
}

interface ReferralTier {
  name: string;
  minReferrals: number;
  bonusPercentage: number;
  perks: string[];
  color: string;
  icon: React.ReactNode;
}

const mockReferrals: Referral[] = [
  {
    id: 1,
    name: 'John Doe',
    email: 'john@email.com',
    status: 'rewarded',
    signupDate: dayjs().subtract(30, 'days').format('YYYY-MM-DD'),
    purchaseDate: dayjs().subtract(25, 'days').format('YYYY-MM-DD'),
    rewardAmount: 25,
    orderValue: 150,
  },
  {
    id: 2,
    name: 'Jane Smith',
    email: 'jane@email.com',
    status: 'purchased',
    signupDate: dayjs().subtract(15, 'days').format('YYYY-MM-DD'),
    purchaseDate: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    rewardAmount: 25,
    orderValue: 200,
  },
  {
    id: 3,
    name: 'Mike Johnson',
    email: 'mike@email.com',
    status: 'signed_up',
    signupDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    rewardAmount: 0,
  },
  {
    id: 4,
    name: 'Sarah Williams',
    email: 'sarah@email.com',
    status: 'pending',
    rewardAmount: 0,
  },
];

const mockRewards: Reward[] = [
  {
    id: 1,
    type: 'store_credit',
    amount: 25,
    description: 'Referral reward for John Doe purchase',
    earnedDate: dayjs().subtract(25, 'days').format('YYYY-MM-DD'),
    status: 'used',
  },
  {
    id: 2,
    type: 'store_credit',
    amount: 25,
    description: 'Referral reward for Jane Smith purchase',
    earnedDate: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    expiryDate: dayjs().add(80, 'days').format('YYYY-MM-DD'),
    status: 'available',
  },
];

const referralTiers: ReferralTier[] = [
  {
    name: 'Bronze',
    minReferrals: 0,
    bonusPercentage: 0,
    perks: ['$10 per referral', 'Basic rewards'],
    color: '#CD7F32',
    icon: <StarOutlined />,
  },
  {
    name: 'Silver',
    minReferrals: 5,
    bonusPercentage: 10,
    perks: ['$10 per referral', '10% bonus rewards', 'Priority support'],
    color: '#C0C0C0',
    icon: <StarOutlined />,
  },
  {
    name: 'Gold',
    minReferrals: 15,
    bonusPercentage: 20,
    perks: ['$15 per referral', '20% bonus rewards', 'Exclusive deals', 'Early access'],
    color: '#FFD700',
    icon: <TrophyOutlined />,
  },
  {
    name: 'Platinum',
    minReferrals: 30,
    bonusPercentage: 30,
    perks: [
      '$20 per referral',
      '30% bonus rewards',
      'VIP support',
      'Exclusive events',
      'Special gifts',
    ],
    color: '#E5E4E2',
    icon: <TrophyOutlined />,
  },
];

const ReferralProgramPage: React.FC = () => {
  const [referrals] = useState<Referral[]>(mockReferrals);
  const [rewards] = useState<Reward[]>(mockRewards);
  const [isQRModalVisible, setIsQRModalVisible] = useState<boolean>(false);

  const referralCode = 'JOHN2024';
  const referralLink = `https://shop.com/ref/${referralCode}`;

  const successfulReferrals = referrals.filter((r) => r.status === 'rewarded').length;
  const pendingReferrals = referrals.filter((r) => r.status === 'pending' || r.status === 'signed_up').length;
  const totalEarnings = rewards.reduce((sum, r) => sum + r.amount, 0);
  const availableBalance = rewards
    .filter((r) => r.status === 'available')
    .reduce((sum, r) => sum + r.amount, 0);

  const getCurrentTier = () => {
    for (let i = referralTiers.length - 1; i >= 0; i--) {
      if (successfulReferrals >= referralTiers[i].minReferrals) {
        return referralTiers[i];
      }
    }
    return referralTiers[0];
  };

  const getNextTier = () => {
    const currentTier = getCurrentTier();
    const currentIndex = referralTiers.findIndex((t) => t.name === currentTier.name);
    return currentIndex < referralTiers.length - 1 ? referralTiers[currentIndex + 1] : null;
  };

  const currentTier = getCurrentTier();
  const nextTier = getNextTier();
  const referralsToNextTier = nextTier ? nextTier.minReferrals - successfulReferrals : 0;

  const handleCopyCode = () => {
    navigator.clipboard.writeText(referralCode);
    message.success('Referral code copied!');
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(referralLink);
    message.success('Referral link copied!');
  };

  const handleShare = (platform: string) => {
    const text = `Join me on Shop.com and get $10 off your first order! Use my referral code: ${referralCode}`;
    const urls = {
      facebook: `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(referralLink)}`,
      twitter: `https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(referralLink)}`,
      whatsapp: `https://wa.me/?text=${encodeURIComponent(text + ' ' + referralLink)}`,
      linkedin: `https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(referralLink)}`,
      email: `mailto:?subject=Join me on Shop.com&body=${encodeURIComponent(text + '\n\n' + referralLink)}`,
    };
    window.open(urls[platform as keyof typeof urls], '_blank');
  };

  const referralColumns: ColumnsType<Referral> = [
    {
      title: 'Friend',
      key: 'friend',
      render: (_, record) => (
        <Space>
          <Avatar style={{ backgroundColor: '#1890ff' }}>{record.name[0]}</Avatar>
          <div>
            <div>
              <Text strong>{record.name}</Text>
            </div>
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.email}
            </Text>
          </div>
        </Space>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Referral['status']) => {
        const statusConfig = {
          pending: { color: 'default', text: 'Invited', icon: <MailOutlined /> },
          signed_up: { color: 'processing', text: 'Signed Up', icon: <UserAddOutlined /> },
          purchased: { color: 'warning', text: 'Purchased', icon: <CheckCircleOutlined /> },
          rewarded: { color: 'success', text: 'Rewarded', icon: <GiftOutlined /> },
        };
        const config = statusConfig[status];
        return (
          <Tag color={config.color} icon={config.icon}>
            {config.text}
          </Tag>
        );
      },
    },
    {
      title: 'Signup Date',
      dataIndex: 'signupDate',
      key: 'signupDate',
      render: (date) => (date ? dayjs(date).format('MMM DD, YYYY') : '-'),
    },
    {
      title: 'Order Value',
      dataIndex: 'orderValue',
      key: 'orderValue',
      render: (value) => (value ? `$${value.toFixed(2)}` : '-'),
    },
    {
      title: 'Your Reward',
      dataIndex: 'rewardAmount',
      key: 'reward',
      render: (amount) => (
        <Text strong style={{ color: amount > 0 ? '#52c41a' : '#999' }}>
          ${amount.toFixed(2)}
        </Text>
      ),
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <GiftOutlined style={{ color: '#1890ff' }} /> Referral Program
          </Title>
          <Paragraph type="secondary">Share the love and earn rewards!</Paragraph>
        </Col>
      </Row>

      <Alert
        message=" Give $10, Get $10!"
        description="Share your referral link with friends. When they make their first purchase, you both get $10 in store credit!"
        type="success"
        showIcon={false}
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Referrals"
              value={successfulReferrals}
              prefix={<UserAddOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Pending"
              value={pendingReferrals}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#fa8c16' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Earned"
              value={totalEarnings}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Available Balance"
              value={availableBalance}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={16}>
          <Card
            title={
              <Space>
                <ShareAltOutlined />
                <span>Share Your Referral Link</span>
              </Space>
            }
            style={{ marginBottom: 24 }}
          >
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              <div>
                <Text strong style={{ marginBottom: 8, display: 'block' }}>
                  Your Referral Code
                </Text>
                <Input
                  size="large"
                  value={referralCode}
                  readOnly
                  addonAfter={
                    <Tooltip title="Copy code">
                      <CopyOutlined onClick={handleCopyCode} style={{ cursor: 'pointer' }} />
                    </Tooltip>
                  }
                  style={{ fontWeight: 'bold', fontSize: 18 }}
                />
              </div>

              <div>
                <Text strong style={{ marginBottom: 8, display: 'block' }}>
                  Your Referral Link
                </Text>
                <Input
                  size="large"
                  value={referralLink}
                  readOnly
                  addonAfter={
                    <Tooltip title="Copy link">
                      <CopyOutlined onClick={handleCopyLink} style={{ cursor: 'pointer' }} />
                    </Tooltip>
                  }
                />
              </div>

              <Divider>Share Via</Divider>

              <Row gutter={16} justify="center">
                <Col>
                  <Button
                    size="large"
                    icon={<FacebookOutlined />}
                    onClick={() => handleShare('facebook')}
                    style={{ background: '#1877f2', color: '#fff', border: 'none' }}
                  >
                    Facebook
                  </Button>
                </Col>
                <Col>
                  <Button
                    size="large"
                    icon={<TwitterOutlined />}
                    onClick={() => handleShare('twitter')}
                    style={{ background: '#1da1f2', color: '#fff', border: 'none' }}
                  >
                    Twitter
                  </Button>
                </Col>
                <Col>
                  <Button
                    size="large"
                    icon={<WhatsAppOutlined />}
                    onClick={() => handleShare('whatsapp')}
                    style={{ background: '#25d366', color: '#fff', border: 'none' }}
                  >
                    WhatsApp
                  </Button>
                </Col>
                <Col>
                  <Button
                    size="large"
                    icon={<LinkedinOutlined />}
                    onClick={() => handleShare('linkedin')}
                    style={{ background: '#0077b5', color: '#fff', border: 'none' }}
                  >
                    LinkedIn
                  </Button>
                </Col>
                <Col>
                  <Button
                    size="large"
                    icon={<MailOutlined />}
                    onClick={() => handleShare('email')}
                  >
                    Email
                  </Button>
                </Col>
                <Col>
                  <Button
                    size="large"
                    icon={<QrcodeOutlined />}
                    onClick={() => setIsQRModalVisible(true)}
                  >
                    QR Code
                  </Button>
                </Col>
              </Row>
            </Space>
          </Card>

          <Card title="How It Works">
            <Steps
              direction="vertical"
              current={-1}
              items={[
                {
                  title: 'Share Your Link',
                  description:
                    'Send your unique referral link to friends and family via email, social media, or messaging apps.',
                  icon: <ShareAltOutlined />,
                },
                {
                  title: 'Friend Signs Up',
                  description:
                    'Your friend creates an account using your referral link and gets $10 off their first order.',
                  icon: <UserAddOutlined />,
                },
                {
                  title: 'Friend Makes Purchase',
                  description:
                    'When your friend completes their first purchase, you earn $10 in store credit!',
                  icon: <DollarOutlined />,
                },
                {
                  title: 'Get Your Reward',
                  description:
                    'Use your earned credits on any future purchase. Credits are automatically applied at checkout.',
                  icon: <GiftOutlined />,
                },
              ]}
            />
          </Card>

          <Card title="Your Referrals" style={{ marginTop: 24 }}>
            <Table
              columns={referralColumns}
              dataSource={referrals}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card
            title={
              <Space>
                <TrophyOutlined />
                <span>Your Tier Status</span>
              </Space>
            }
            style={{ marginBottom: 24 }}
          >
            <div style={{ textAlign: 'center', marginBottom: 24 }}>
              <div
                style={{
                  fontSize: 48,
                  color: currentTier.color,
                  marginBottom: 8,
                }}
              >
                {currentTier.icon}
              </div>
              <Title level={3} style={{ margin: 0, color: currentTier.color }}>
                {currentTier.name}
              </Title>
              <Text type="secondary">Current Tier</Text>
            </div>

            <List
              size="small"
              header={<Text strong>Your Perks</Text>}
              dataSource={currentTier.perks}
              renderItem={(perk) => (
                <List.Item>
                  <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                  {perk}
                </List.Item>
              )}
            />

            {nextTier && (
              <>
                <Divider />
                <div>
                  <div style={{ marginBottom: 16 }}>
                    <Text strong>Progress to {nextTier.name}</Text>
                  </div>
                  <Progress
                    percent={(successfulReferrals / nextTier.minReferrals) * 100}
                    strokeColor={nextTier.color}
                    format={() => `${successfulReferrals}/${nextTier.minReferrals}`}
                  />
                  <Text type="secondary" style={{ fontSize: 12, marginTop: 8, display: 'block' }}>
                    {referralsToNextTier} more referrals to unlock {nextTier.name} tier!
                  </Text>
                </div>
              </>
            )}
          </Card>

          <Card title="All Tiers">
            <List
              dataSource={referralTiers}
              renderItem={(tier) => {
                const isCurrentTier = tier.name === currentTier.name;
                const isUnlocked = successfulReferrals >= tier.minReferrals;

                return (
                  <List.Item
                    style={{
                      background: isCurrentTier ? '#e6f7ff' : 'transparent',
                      padding: 12,
                      borderRadius: 8,
                      marginBottom: 8,
                      border: isCurrentTier ? '2px solid #1890ff' : '1px solid #f0f0f0',
                    }}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          size="large"
                          style={{
                            backgroundColor: tier.color,
                            color: '#000',
                          }}
                        >
                          {tier.icon}
                        </Avatar>
                      }
                      title={
                        <Space>
                          <Text strong style={{ color: tier.color }}>
                            {tier.name}
                          </Text>
                          {isCurrentTier && <Tag color="blue">Current</Tag>}
                          {isUnlocked && !isCurrentTier && (
                            <CheckCircleOutlined style={{ color: '#52c41a' }} />
                          )}
                        </Space>
                      }
                      description={
                        <Space direction="vertical" size="small">
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            {tier.minReferrals}+ successful referrals
                          </Text>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            +{tier.bonusPercentage}% bonus rewards
                          </Text>
                        </Space>
                      }
                    />
                  </List.Item>
                );
              }}
            />
          </Card>
        </Col>
      </Row>

      <Modal
        title="QR Code"
        open={isQRModalVisible}
        onCancel={() => setIsQRModalVisible(false)}
        footer={[
          <Button key="close" onClick={() => setIsQRModalVisible(false)}>
            Close
          </Button>,
        ]}
      >
        <div style={{ textAlign: 'center', padding: 24 }}>
          <QRCode value={referralLink} size={256} />
          <Paragraph style={{ marginTop: 16 }}>
            Share this QR code with friends to let them scan and join!
          </Paragraph>
        </div>
      </Modal>
    </div>
  );
};

export default ReferralProgramPage;

customer/seller-directory.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  List,
  Avatar,
  Tag,
  Input,
  Select,
  Empty,
  Badge,
  Tooltip,
  Modal,
  Form,
  Rate,
  message,
  Divider,
  Tabs,
} from 'antd';
import {
  UserOutlined,
  ShoppingOutlined,
  MessageOutlined,
  PhoneOutlined,
  MailOutlined,
  StarOutlined,
  TeamOutlined,
  ShopOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';

dayjs.extend(relativeTime);

const { Title, Text, Paragraph } = Typography;
const { Search } = Input;
const { Option } = Select;
const { TextArea } = Input;

interface Seller {
  id: number;
  name: string;
  avatar: string;
  rating: number;
  totalReviews: number;
  responseTime: string; // e.g., "< 2 hours"
  responseRate: number; // percentage
  joinedDate: string;
  productsCount: number;
  totalSales: number;
  isVerified: boolean;
  isPremium: boolean;
  location: string;
  categories: string[];
  badges: string[];
  lastActive: string;
}

interface Message {
  id: number;
  sellerId: number;
  sellerName: string;
  lastMessage: string;
  timestamp: string;
  isRead: boolean;
  unreadCount: number;
}

const mockSellers: Seller[] = [
  {
    id: 1,
    name: 'TechStore Official',
    avatar: 'https://via.placeholder.com/60?text=TS',
    rating: 4.8,
    totalReviews: 12453,
    responseTime: '< 1 hour',
    responseRate: 98,
    joinedDate: dayjs().subtract(5, 'years').format('YYYY-MM-DD'),
    productsCount: 856,
    totalSales: 45230,
    isVerified: true,
    isPremium: true,
    location: 'New York, USA',
    categories: ['Electronics', 'Computers', 'Accessories'],
    badges: ['Top Seller', 'Verified', 'Fast Shipping'],
    lastActive: dayjs().subtract(10, 'minutes').toISOString(),
  },
  {
    id: 2,
    name: 'Fashion Hub',
    avatar: 'https://via.placeholder.com/60?text=FH',
    rating: 4.6,
    totalReviews: 8934,
    responseTime: '< 2 hours',
    responseRate: 95,
    joinedDate: dayjs().subtract(3, 'years').format('YYYY-MM-DD'),
    productsCount: 1234,
    totalSales: 32145,
    isVerified: true,
    isPremium: false,
    location: 'Los Angeles, USA',
    categories: ['Fashion', 'Clothing', 'Accessories'],
    badges: ['Verified', 'Quality Products'],
    lastActive: dayjs().subtract(1, 'hour').toISOString(),
  },
  {
    id: 3,
    name: 'Home & Garden Experts',
    avatar: 'https://via.placeholder.com/60?text=HG',
    rating: 4.9,
    totalReviews: 5678,
    responseTime: '< 3 hours',
    responseRate: 92,
    joinedDate: dayjs().subtract(2, 'years').format('YYYY-MM-DD'),
    productsCount: 445,
    totalSales: 18934,
    isVerified: true,
    isPremium: true,
    location: 'Seattle, USA',
    categories: ['Home', 'Garden', 'Furniture'],
    badges: ['Top Seller', 'Verified', 'Eco-Friendly'],
    lastActive: dayjs().subtract(30, 'minutes').toISOString(),
  },
  {
    id: 4,
    name: 'Sports Gear Pro',
    avatar: 'https://via.placeholder.com/60?text=SP',
    rating: 4.7,
    totalReviews: 3456,
    responseTime: '< 4 hours',
    responseRate: 89,
    joinedDate: dayjs().subtract(1, 'year').format('YYYY-MM-DD'),
    productsCount: 234,
    totalSales: 9876,
    isVerified: false,
    isPremium: false,
    location: 'Chicago, USA',
    categories: ['Sports', 'Fitness', 'Outdoor'],
    badges: ['Fast Shipping'],
    lastActive: dayjs().subtract(2, 'hours').toISOString(),
  },
  {
    id: 5,
    name: 'Book Haven',
    avatar: 'https://via.placeholder.com/60?text=BH',
    rating: 4.5,
    totalReviews: 2345,
    responseTime: '< 5 hours',
    responseRate: 87,
    joinedDate: dayjs().subtract(6, 'months').format('YYYY-MM-DD'),
    productsCount: 567,
    totalSales: 6543,
    isVerified: true,
    isPremium: false,
    location: 'Boston, USA',
    categories: ['Books', 'Education', 'Stationery'],
    badges: ['Verified', 'Quality Products'],
    lastActive: dayjs().subtract(5, 'hours').toISOString(),
  },
];

const mockMessages: Message[] = [
  {
    id: 1,
    sellerId: 1,
    sellerName: 'TechStore Official',
    lastMessage: 'Yes, we have that item in stock. Would you like to place an order?',
    timestamp: dayjs().subtract(30, 'minutes').toISOString(),
    isRead: false,
    unreadCount: 2,
  },
  {
    id: 2,
    sellerId: 3,
    sellerName: 'Home & Garden Experts',
    lastMessage: 'Thank you for your purchase! Your order will ship tomorrow.',
    timestamp: dayjs().subtract(2, 'hours').toISOString(),
    isRead: true,
    unreadCount: 0,
  },
];

const SellerDirectoryPage: React.FC = () => {
  const [sellers] = useState<Seller[]>(mockSellers);
  const [messages] = useState<Message[]>(mockMessages);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [sortBy, setSortBy] = useState<string>('rating');
  const [isContactModalVisible, setIsContactModalVisible] = useState<boolean>(false);
  const [selectedSeller, setSelectedSeller] = useState<Seller | null>(null);
  const [form] = Form.useForm();

  const handleContactSeller = (seller: Seller) => {
    setSelectedSeller(seller);
    form.resetFields();
    setIsContactModalVisible(true);
  };

  const handleSendMessage = (values: any) => {
    message.success(`Message sent to ${selectedSeller?.name}`);
    setIsContactModalVisible(false);
    form.resetFields();
    setSelectedSeller(null);
  };

  const filteredSellers = sellers
    .filter((s) => {
      const matchesSearch =
        s.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        s.categories.some((c) => c.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesCategory = filterCategory === 'all' || s.categories.includes(filterCategory);
      return matchesSearch && matchesCategory;
    })
    .sort((a, b) => {
      if (sortBy === 'rating') return b.rating - a.rating;
      if (sortBy === 'sales') return b.totalSales - a.totalSales;
      if (sortBy === 'products') return b.productsCount - a.productsCount;
      return 0;
    });

  const allCategories = Array.from(new Set(sellers.flatMap((s) => s.categories)));
  const unreadMessages = messages.filter((m) => !m.isRead).length;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Title level={3}>
        <ShopOutlined style={{ color: '#1890ff' }} /> Seller Directory
      </Title>
      <Paragraph type="secondary">
        Browse and connect with trusted sellers on our platform
      </Paragraph>

      <Tabs defaultActiveKey="directory">
        <Tabs.TabPane
          tab={
            <span>
              <TeamOutlined />
              All Sellers ({sellers.length})
            </span>
          }
          key="directory"
        >
          <Card style={{ marginBottom: 24 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              <Row gutter={16}>
                <Col xs={24} md={12}>
                  <Search
                    placeholder="Search sellers or categories..."
                    allowClear
                    size="large"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </Col>
                <Col xs={24} md={6}>
                  <Select
                    value={filterCategory}
                    onChange={setFilterCategory}
                    style={{ width: '100%' }}
                    size="large"
                  >
                    <Option value="all">All Categories</Option>
                    {allCategories.map((cat) => (
                      <Option key={cat} value={cat}>
                        {cat}
                      </Option>
                    ))}
                  </Select>
                </Col>
                <Col xs={24} md={6}>
                  <Select
                    value={sortBy}
                    onChange={setSortBy}
                    style={{ width: '100%' }}
                    size="large"
                  >
                    <Option value="rating">Highest Rating</Option>
                    <Option value="sales">Most Sales</Option>
                    <Option value="products">Most Products</Option>
                  </Select>
                </Col>
              </Row>
            </Space>
          </Card>

          {filteredSellers.length === 0 ? (
            <Empty description="No sellers found" image={Empty.PRESENTED_IMAGE_SIMPLE} />
          ) : (
            <Row gutter={[16, 16]}>
              {filteredSellers.map((seller) => (
                <Col key={seller.id} xs={24} sm={12} lg={8}>
                  <Card
                    hoverable
                    style={{
                      height: '100%',
                      border: seller.isPremium ? '2px solid #fa8c16' : undefined,
                    }}
                  >
                    <div style={{ textAlign: 'center' }}>
                      <Badge
                        count={seller.isPremium ? 'PREMIUM' : 0}
                        style={{ backgroundColor: '#fa8c16' }}
                      >
                        <Avatar src={seller.avatar} size={80} />
                      </Badge>
                      <div style={{ marginTop: 12 }}>
                        <Space align="center">
                          <Text strong style={{ fontSize: 16 }}>
                            {seller.name}
                          </Text>
                          {seller.isVerified && (
                            <Tooltip title="Verified Seller">
                              <CheckCircleOutlined style={{ color: '#52c41a' }} />
                            </Tooltip>
                          )}
                        </Space>
                      </div>
                      <div style={{ marginTop: 4 }}>
                        <Rate disabled defaultValue={seller.rating} allowHalf />
                        <Text type="secondary" style={{ marginLeft: 8, fontSize: 12 }}>
                          {seller.rating} ({seller.totalReviews.toLocaleString()})
                        </Text>
                      </div>
                      <Divider style={{ margin: '12px 0' }} />
                      <Space direction="vertical" size="small" style={{ width: '100%' }}>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                           {seller.location}
                        </Text>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                           Response: {seller.responseTime} ({seller.responseRate}%)
                        </Text>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                           {seller.productsCount} Products | {seller.totalSales.toLocaleString()}{' '}
                          Sales
                        </Text>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                           Active {dayjs(seller.lastActive).fromNow()}
                        </Text>
                      </Space>
                      <Divider style={{ margin: '12px 0' }} />
                      <Space wrap size="small" style={{ justifyContent: 'center' }}>
                        {seller.badges.map((badge, index) => (
                          <Tag
                            key={index}
                            color={
                              badge === 'Top Seller'
                                ? 'gold'
                                : badge === 'Verified'
                                ? 'green'
                                : 'blue'
                            }
                          >
                            {badge}
                          </Tag>
                        ))}
                      </Space>
                      <Divider style={{ margin: '12px 0' }} />
                      <Space direction="vertical" style={{ width: '100%' }} size="small">
                        <Button
                          type="primary"
                          block
                          icon={<ShoppingOutlined />}
                          onClick={() => message.info(`Viewing ${seller.name}'s store`)}
                        >
                          Visit Store
                        </Button>
                        <Button
                          block
                          icon={<MessageOutlined />}
                          onClick={() => handleContactSeller(seller)}
                        >
                          Contact Seller
                        </Button>
                      </Space>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          )}
        </Tabs.TabPane>

        <Tabs.TabPane
          tab={
            <span>
              <Badge count={unreadMessages}>
                <MessageOutlined />
              </Badge>
              <span style={{ marginLeft: 8 }}>Messages ({messages.length})</span>
            </span>
          }
          key="messages"
        >
          <Card>
            <List
              dataSource={messages}
              renderItem={(msg) => (
                <List.Item
                  style={{
                    background: msg.isRead ? '#fff' : '#e6f7ff',
                    padding: 16,
                    marginBottom: 8,
                    borderRadius: 8,
                  }}
                  actions={[
                    <Button type="link">View</Button>,
                    <Button type="link">Reply</Button>,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Badge dot={!msg.isRead}>
                        <Avatar icon={<UserOutlined />} size={48} />
                      </Badge>
                    }
                    title={
                      <Space>
                        <Text strong>{msg.sellerName}</Text>
                        {msg.unreadCount > 0 && (
                          <Badge count={msg.unreadCount} style={{ backgroundColor: '#1890ff' }} />
                        )}
                      </Space>
                    }
                    description={
                      <div>
                        <Paragraph
                          style={{ margin: 0, marginBottom: 8 }}
                          ellipsis={{ rows: 2 }}
                        >
                          {msg.lastMessage}
                        </Paragraph>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          <ClockCircleOutlined /> {dayjs(msg.timestamp).fromNow()}
                        </Text>
                      </div>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Tabs.TabPane>
      </Tabs>

      <Modal
        title={`Contact ${selectedSeller?.name}`}
        open={isContactModalVisible}
        onCancel={() => {
          setIsContactModalVisible(false);
          setSelectedSeller(null);
        }}
        footer={null}
        width={600}
      >
        {selectedSeller && (
          <>
            <div style={{ marginBottom: 24, textAlign: 'center' }}>
              <Avatar src={selectedSeller.avatar} size={60} />
              <div style={{ marginTop: 8 }}>
                <Text strong>{selectedSeller.name}</Text>
              </div>
              <Space style={{ marginTop: 4 }}>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  <ClockCircleOutlined /> Response time: {selectedSeller.responseTime}
                </Text>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  <CheckCircleOutlined /> {selectedSeller.responseRate}% response rate
                </Text>
              </Space>
            </div>

            <Form form={form} layout="vertical" onFinish={handleSendMessage}>
              <Form.Item
                label="Subject"
                name="subject"
                rules={[{ required: true, message: 'Please enter subject' }]}
              >
                <Select size="large" placeholder="Select subject">
                  <Option value="product">Product Inquiry</Option>
                  <Option value="order">Order Question</Option>
                  <Option value="shipping">Shipping Information</Option>
                  <Option value="return">Return/Refund</Option>
                  <Option value="other">Other</Option>
                </Select>
              </Form.Item>

              <Form.Item
                label="Message"
                name="message"
                rules={[
                  { required: true, message: 'Please enter your message' },
                  { min: 10, message: 'Message must be at least 10 characters' },
                ]}
              >
                <TextArea
                  rows={5}
                  placeholder="Type your message here..."
                  maxLength={500}
                  showCount
                />
              </Form.Item>

              <Form.Item>
                <Space>
                  <Button type="primary" htmlType="submit" size="large" icon={<MessageOutlined />}>
                    Send Message
                  </Button>
                  <Button
                    size="large"
                    onClick={() => {
                      setIsContactModalVisible(false);
                      setSelectedSeller(null);
                    }}
                  >
                    Cancel
                  </Button>
                </Space>
              </Form.Item>
            </Form>
          </>
        )}
      </Modal>
    </div>
  );
};

export default SellerDirectoryPage;

customer/wishlist-sharing.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  List,
  Avatar,
  Tag,
  Empty,
  Modal,
  Tooltip,
  message,
  Divider,
  Badge,
  Statistic,
  Input,
} from 'antd';
import {
  HeartOutlined,
  HeartFilled,
  DeleteOutlined,
  ShoppingCartOutlined,
  ShareAltOutlined,
  StarFilled,
  FacebookOutlined,
  TwitterOutlined,
  LinkedinOutlined,
  MailOutlined,
  LinkOutlined,
} from '@ant-design/icons';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;

interface WishlistItem {
  id: number;
  productId: number;
  productName: string;
  brand: string;
  price: number;
  originalPrice: number;
  rating: number;
  reviews: number;
  inStock: boolean;
  image: string;
  addedDate: string;
  notes: string;
}

const mockWishlistItems: WishlistItem[] = [
  {
    id: 1,
    productId: 101,
    productName: 'Wireless Headphones Pro',
    brand: 'TechBrand',
    price: 99,
    originalPrice: 149,
    rating: 4.5,
    reviews: 234,
    inStock: true,
    image: '',
    addedDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    notes: 'Wait for sale',
  },
  {
    id: 2,
    productId: 102,
    productName: 'Smart Watch Series 5',
    brand: 'SmartTech',
    price: 299,
    originalPrice: 399,
    rating: 4.8,
    reviews: 567,
    inStock: true,
    image: '',
    addedDate: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    notes: '',
  },
  {
    id: 3,
    productId: 103,
    productName: 'Ergonomic Office Chair',
    brand: 'ComfortPlus',
    price: 449,
    originalPrice: 549,
    rating: 4.3,
    reviews: 123,
    inStock: true,
    image: '',
    addedDate: dayjs().subtract(2, 'days').format('YYYY-MM-DD'),
    notes: 'Birthday gift for mom',
  },
  {
    id: 4,
    productId: 104,
    productName: 'Leather Jacket',
    brand: 'StyleCo',
    price: 299,
    originalPrice: 399,
    rating: 4.6,
    reviews: 89,
    inStock: false,
    image: '',
    addedDate: dayjs().subtract(15, 'days').format('YYYY-MM-DD'),
    notes: '',
  },
];

const WishlistPage: React.FC = () => {
  const [wishlistItems, setWishlistItems] = useState<WishlistItem[]>(mockWishlistItems);
  const [isShareModalVisible, setIsShareModalVisible] = useState<boolean>(false);
  const [shareUrl] = useState<string>('https://example.com/wishlist/shared/abc123');

  const handleRemoveItem = (id: number) => {
    Modal.confirm({
      title: 'Remove from Wishlist',
      content: 'Are you sure you want to remove this item from your wishlist?',
      onOk: () => {
        setWishlistItems(wishlistItems.filter((item) => item.id !== id));
        message.success('Item removed from wishlist');
      },
    });
  };

  const handleAddToCart = (item: WishlistItem) => {
    if (!item.inStock) {
      message.warning('This item is currently out of stock');
      return;
    }
    message.success(`${item.productName} added to cart`);
  };

  const handleAddAllToCart = () => {
    const inStockItems = wishlistItems.filter((item) => item.inStock);
    if (inStockItems.length === 0) {
      message.warning('No items in stock');
      return;
    }
    message.success(`${inStockItems.length} items added to cart`);
  };

  const handleClearWishlist = () => {
    Modal.confirm({
      title: 'Clear Wishlist',
      content: 'Are you sure you want to clear all items from your wishlist?',
      onOk: () => {
        setWishlistItems([]);
        message.success('Wishlist cleared');
      },
    });
  };

  const handleShareWishlist = () => {
    setIsShareModalVisible(true);
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(shareUrl);
    message.success('Link copied to clipboard');
  };

  const handleShareVia = (platform: string) => {
    message.success(`Sharing via ${platform}`);
    setIsShareModalVisible(false);
  };

  const totalValue = wishlistItems.reduce((sum, item) => sum + item.price, 0);
  const totalSavings = wishlistItems.reduce((sum, item) => sum + (item.originalPrice - item.price), 0);
  const inStockCount = wishlistItems.filter((item) => item.inStock).length;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Row justify="space-between" align="middle">
          <Col>
            <Title level={3}>
              <HeartFilled style={{ color: '#ff4d4f' }} /> My Wishlist
            </Title>
            <Paragraph type="secondary">
              Save items for later and track price changes
            </Paragraph>
          </Col>
          <Col>
            <Space>
              <Button icon={<ShareAltOutlined />} onClick={handleShareWishlist}>
                Share Wishlist
              </Button>
              {wishlistItems.length > 0 && (
                <Button danger onClick={handleClearWishlist}>
                  Clear All
                </Button>
              )}
            </Space>
          </Col>
        </Row>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Items"
              value={wishlistItems.length}
              prefix={<HeartOutlined />}
              valueStyle={{ color: '#ff4d4f' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Value"
              value={totalValue}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Savings"
              value={totalSavings}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      <Card>
        {wishlistItems.length === 0 ? (
          <Empty
            description="Your wishlist is empty"
            image={Empty.PRESENTED_IMAGE_SIMPLE}
          >
            <Button type="primary" href="/products">
              Start Shopping
            </Button>
          </Empty>
        ) : (
          <>
            <div style={{ marginBottom: 16 }}>
              <Space>
                <Button
                  type="primary"
                  icon={<ShoppingCartOutlined />}
                  onClick={handleAddAllToCart}
                  disabled={inStockCount === 0}
                >
                  Add All to Cart ({inStockCount})
                </Button>
                <Text type="secondary">
                  {wishlistItems.length} item{wishlistItems.length !== 1 ? 's' : ''} in your wishlist
                </Text>
              </Space>
            </div>

            <List
              itemLayout="horizontal"
              dataSource={wishlistItems}
              renderItem={(item) => (
                <List.Item
                  actions={[
                    <Tooltip title={item.inStock ? 'Add to Cart' : 'Out of Stock'}>
                      <Button
                        type="primary"
                        icon={<ShoppingCartOutlined />}
                        onClick={() => handleAddToCart(item)}
                        disabled={!item.inStock}
                      >
                        {item.inStock ? 'Add to Cart' : 'Out of Stock'}
                      </Button>
                    </Tooltip>,
                    <Tooltip title="Remove from Wishlist">
                      <Button
                        danger
                        icon={<DeleteOutlined />}
                        onClick={() => handleRemoveItem(item.id)}
                      >
                        Remove
                      </Button>
                    </Tooltip>,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Badge
                        count={
                          !item.inStock ? (
                            <Tag color="red" style={{ marginTop: 0 }}>
                              Out of Stock
                            </Tag>
                          ) : item.originalPrice > item.price ? (
                            <Tag color="red" style={{ marginTop: 0 }}>
                              {Math.round(((item.originalPrice - item.price) / item.originalPrice) * 100)}% OFF
                            </Tag>
                          ) : null
                        }
                        offset={[-10, 10]}
                      >
                        <Avatar
                          size={100}
                          shape="square"
                          style={{
                            background: '#f0f0f0',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                          }}
                        >
                          <Text type="secondary" style={{ fontSize: 11 }}>Image</Text>
                        </Avatar>
                      </Badge>
                    }
                    title={
                      <Space direction="vertical" size={0}>
                        <Text strong style={{ fontSize: 16 }}>{item.productName}</Text>
                        <Text type="secondary" style={{ fontSize: 12 }}>{item.brand}</Text>
                      </Space>
                    }
                    description={
                      <Space direction="vertical" size="small" style={{ marginTop: 8 }}>
                        <Space>
                          <StarFilled style={{ color: '#faad14' }} />
                          <Text>
                            {item.rating} ({item.reviews} reviews)
                          </Text>
                        </Space>

                        <Space size="large">
                          <Space direction="vertical" size={0}>
                            <Text type="secondary" style={{ fontSize: 12 }}>Price</Text>
                            <Space>
                              <Text strong style={{ fontSize: 20, color: '#ff4d4f' }}>
                                ${item.price}
                              </Text>
                              {item.originalPrice > item.price && (
                                <Text delete type="secondary">
                                  ${item.originalPrice}
                                </Text>
                              )}
                            </Space>
                          </Space>

                          <Space direction="vertical" size={0}>
                            <Text type="secondary" style={{ fontSize: 12 }}>You Save</Text>
                            <Text strong style={{ color: '#52c41a' }}>
                              ${item.originalPrice - item.price}
                            </Text>
                          </Space>
                        </Space>

                        <Space direction="vertical" size={0}>
                          <Text type="secondary" style={{ fontSize: 11 }}>
                            Added on {dayjs(item.addedDate).format('MMM DD, YYYY')}
                          </Text>
                          {item.notes && (
                            <Text italic style={{ fontSize: 12 }}>
                              Note: {item.notes}
                            </Text>
                          )}
                        </Space>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />

            <Divider />

            <Row justify="space-between" align="middle">
              <Col>
                <Space direction="vertical" size={0}>
                  <Text strong style={{ fontSize: 16 }}>Total Value:</Text>
                  <Text type="secondary" style={{ fontSize: 12 }}>
                    {wishlistItems.length} item{wishlistItems.length !== 1 ? 's' : ''}
                  </Text>
                </Space>
              </Col>
              <Col>
                <Space direction="vertical" size={0} style={{ textAlign: 'right' }}>
                  <Text strong style={{ fontSize: 24, color: '#ff4d4f' }}>
                    ${totalValue.toFixed(2)}
                  </Text>
                  <Text type="secondary" style={{ fontSize: 12 }}>
                    Total Savings: ${totalSavings.toFixed(2)}
                  </Text>
                </Space>
              </Col>
            </Row>
          </>
        )}
      </Card>

      <Modal
        title="Share Your Wishlist"
        open={isShareModalVisible}
        onCancel={() => setIsShareModalVisible(false)}
        footer={null}
        width={500}
      >
        <Space direction="vertical" size="large" style={{ width: '100%' }}>
          <div>
            <Text type="secondary">Share your wishlist with friends and family</Text>
          </div>

          <div>
            <Text strong style={{ display: 'block', marginBottom: 8 }}>
              Share Link
            </Text>
            <Space.Compact style={{ width: '100%' }}>
              <Input value={shareUrl} readOnly />
              <Button type="primary" icon={<LinkOutlined />} onClick={handleCopyLink}>
                Copy
              </Button>
            </Space.Compact>
          </div>

          <Divider>Or share via</Divider>

          <Row gutter={16}>
            <Col span={6}>
              <Button
                type="primary"
                icon={<FacebookOutlined />}
                block
                style={{ background: '#1877f2' }}
                onClick={() => handleShareVia('Facebook')}
              >
                Facebook
              </Button>
            </Col>
            <Col span={6}>
              <Button
                type="primary"
                icon={<TwitterOutlined />}
                block
                style={{ background: '#1da1f2' }}
                onClick={() => handleShareVia('Twitter')}
              >
                Twitter
              </Button>
            </Col>
            <Col span={6}>
              <Button
                type="primary"
                icon={<LinkedinOutlined />}
                block
                style={{ background: '#0077b5' }}
                onClick={() => handleShareVia('LinkedIn')}
              >
                LinkedIn
              </Button>
            </Col>
            <Col span={6}>
              <Button
                icon={<MailOutlined />}
                block
                onClick={() => handleShareVia('Email')}
              >
                Email
              </Button>
            </Col>
          </Row>
        </Space>
      </Modal>
    </div>
  );
};

export default WishlistPage;

admin/bulk-import-export.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  InputNumber,
  Upload,
  Modal,
  message,
  Divider,
  Statistic,
  Switch,
  Alert,
} from 'antd';
import {
  InboxOutlined,
  UploadOutlined,
  FileExcelOutlined,
  CloudUploadOutlined,
  CheckCircleOutlined,
  WarningOutlined,
  DeleteOutlined,
  EditOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import type { UploadProps } from 'antd';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Dragger } = Upload;
const { Option } = Select;

interface ImportedProduct {
  id: number;
  sku: string;
  name: string;
  category: string;
  price: number;
  stock: number;
  status: 'success' | 'warning' | 'error';
  issues: string[];
}

interface ImportHistory {
  id: number;
  filename: string;
  totalRows: number;
  successCount: number;
  errorCount: number;
  timestamp: string;
  status: 'completed' | 'processing' | 'failed';
}

const mockImportedProducts: ImportedProduct[] = [
  {
    id: 1,
    sku: 'ELEC-001',
    name: 'Wireless Headphones',
    category: 'Electronics',
    price: 79.99,
    stock: 150,
    status: 'success',
    issues: [],
  },
  {
    id: 2,
    sku: 'FASH-002',
    name: 'Leather Jacket',
    category: 'Fashion',
    price: 199.99,
    stock: 0,
    status: 'warning',
    issues: ['Stock is 0'],
  },
  {
    id: 3,
    sku: 'HOME-003',
    name: 'Office Chair',
    category: 'Home & Garden',
    price: -50.00,
    stock: 45,
    status: 'error',
    issues: ['Invalid price (negative value)', 'Missing product description'],
  },
  {
    id: 4,
    sku: 'ELEC-004',
    name: 'Smart Watch',
    category: 'Electronics',
    price: 299.99,
    stock: 200,
    status: 'success',
    issues: [],
  },
];

const mockImportHistory: ImportHistory[] = [
  {
    id: 1,
    filename: 'products_batch_001.xlsx',
    totalRows: 500,
    successCount: 485,
    errorCount: 15,
    timestamp: dayjs().subtract(2, 'hours').format('YYYY-MM-DD HH:mm:ss'),
    status: 'completed',
  },
  {
    id: 2,
    filename: 'products_update_202401.csv',
    totalRows: 1200,
    successCount: 1150,
    errorCount: 50,
    timestamp: dayjs().subtract(1, 'day').format('YYYY-MM-DD HH:mm:ss'),
    status: 'completed',
  },
  {
    id: 3,
    filename: 'new_inventory.xlsx',
    totalRows: 350,
    successCount: 350,
    errorCount: 0,
    timestamp: dayjs().subtract(3, 'days').format('YYYY-MM-DD HH:mm:ss'),
    status: 'completed',
  },
];

const BulkImportExportPage: React.FC = () => {
  const [importing, setImporting] = useState<boolean>(false);
  const [previewData, setPreviewData] = useState<ImportedProduct[]>([]);
  const [isPreviewVisible, setIsPreviewVisible] = useState<boolean>(false);
  const [form] = Form.useForm();

  const uploadProps: UploadProps = {
    name: 'file',
    multiple: false,
    accept: '.xlsx,.xls,.csv',
    beforeUpload: (file) => {
      const isValidType = file.type === 'application/vnd.ms-excel' || 
                         file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet' ||
                         file.type === 'text/csv';
      if (!isValidType) {
        message.error('You can only upload Excel or CSV files!');
      }
      const isLt10M = file.size / 1024 / 1024 < 10;
      if (!isLt10M) {
        message.error('File must be smaller than 10MB!');
      }
      return isValidType && isLt10M;
    },
    onChange(info) {
      const { status } = info.file;
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`);
        setPreviewData(mockImportedProducts);
        setIsPreviewVisible(true);
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`);
      }
    },
  };

  const handleBulkImport = () => {
    setImporting(true);
    setTimeout(() => {
      message.success('Products imported successfully!');
      setImporting(false);
      setIsPreviewVisible(false);
      setPreviewData([]);
    }, 2000);
  };

  const handleExport = (format: string) => {
    message.success(`Exporting products as ${format.toUpperCase()}...`);
  };

  const handleDownloadTemplate = () => {
    message.success('Template downloaded successfully!');
  };

  const productColumns: ColumnsType<ImportedProduct> = [
    {
      title: 'SKU',
      dataIndex: 'sku',
      key: 'sku',
      render: (sku) => <Text code>{sku}</Text>,
    },
    {
      title: 'Product Name',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category) => <Tag color="blue">{category}</Tag>,
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      render: (price) => (
        <Text strong style={{ color: price < 0 ? '#ff4d4f' : '#52c41a' }}>
          ${price.toFixed(2)}
        </Text>
      ),
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock',
      render: (stock) => (
        <Tag color={stock > 0 ? 'green' : 'red'}>{stock}</Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: ImportedProduct['status']) => {
        const config: Record<ImportedProduct['status'], { color: string; icon: React.ReactNode; text: string }> = {
          success: { color: 'green', icon: <CheckCircleOutlined />, text: 'Valid' },
          warning: { color: 'orange', icon: <WarningOutlined />, text: 'Warning' },
          error: { color: 'red', icon: <WarningOutlined />, text: 'Error' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
    },
    {
      title: 'Issues',
      dataIndex: 'issues',
      key: 'issues',
      render: (issues: string[]) => (
        <div>
          {issues.length > 0 ? (
            <ul style={{ margin: 0, paddingLeft: 16, fontSize: 12, color: '#ff4d4f' }}>
              {issues.map((issue, i) => (
                <li key={i}>{issue}</li>
              ))}
            </ul>
          ) : (
            <Text type="secondary">No issues</Text>
          )}
        </div>
      ),
    },
  ];

  const historyColumns: ColumnsType<ImportHistory> = [
    {
      title: 'Filename',
      dataIndex: 'filename',
      key: 'filename',
      render: (name) => (
        <Space>
          <FileExcelOutlined style={{ color: '#52c41a' }} />
          <Text>{name}</Text>
        </Space>
      ),
    },
    {
      title: 'Total Rows',
      dataIndex: 'totalRows',
      key: 'totalRows',
      render: (count) => <Text>{count.toLocaleString()}</Text>,
    },
    {
      title: 'Success',
      dataIndex: 'successCount',
      key: 'successCount',
      render: (count) => <Tag color="green">{count.toLocaleString()}</Tag>,
    },
    {
      title: 'Errors',
      dataIndex: 'errorCount',
      key: 'errorCount',
      render: (count) => <Tag color={count > 0 ? 'red' : 'default'}>{count}</Tag>,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: ImportHistory['status']) => {
        const colors: Record<ImportHistory['status'], string> = {
          completed: 'green',
          processing: 'blue',
          failed: 'red',
        };
        return <Tag color={colors[status]}>{status.toUpperCase()}</Tag>;
      },
    },
    {
      title: 'Timestamp',
      dataIndex: 'timestamp',
      key: 'timestamp',
      render: (time) => <Text type="secondary">{time}</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: () => (
        <Space>
          <Button size="small" icon={<EditOutlined />}>View Details</Button>
        </Space>
      ),
    },
  ];

  const successRate = ((mockImportedProducts.filter(p => p.status === 'success').length / mockImportedProducts.length) * 100).toFixed(1);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <CloudUploadOutlined style={{ color: '#1890ff' }} /> Bulk Import/Export
        </Title>
        <Paragraph type="secondary">
          Import products in bulk via Excel/CSV files or export your catalog
        </Paragraph>
      </div>

      <Alert
        message="Bulk Operations"
        description="Upload Excel or CSV files to import products. Maximum file size: 10MB. Up to 10,000 products per file."
        type="info"
        showIcon
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Imports"
              value={mockImportHistory.length}
              prefix={<CloudUploadOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Success Rate"
              value={successRate}
              suffix="%"
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Last Import"
              value="2h ago"
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Products Imported"
              value={1635}
              prefix={<InboxOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} lg={12}>
          <Card title="Import Products" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              <div>
                <Button
                  type="dashed"
                  icon={<UploadOutlined />}
                  onClick={handleDownloadTemplate}
                  block
                >
                  Download Excel Template
                </Button>
                <Text type="secondary" style={{ fontSize: 12, display: 'block', marginTop: 8 }}>
                  Download our template to ensure your data is formatted correctly
                </Text>
              </div>

              <Divider />

              <Dragger {...uploadProps}>
                <p className="ant-upload-drag-icon">
                  <InboxOutlined style={{ color: '#1890ff' }} />
                </p>
                <p className="ant-upload-text">Click or drag file to this area to upload</p>
                <p className="ant-upload-hint">
                  Support for Excel (.xlsx, .xls) or CSV files. Maximum file size: 10MB
                </p>
              </Dragger>

              <Divider />

              <Form form={form} layout="vertical">
                <Form.Item label="Import Options">
                  <Space direction="vertical" style={{ width: '100%' }}>
                    <Form.Item name="updateExisting" valuePropName="checked" noStyle>
                      <Switch /> <Text style={{ marginLeft: 8 }}>Update existing products (match by SKU)</Text>
                    </Form.Item>
                    <Form.Item name="skipErrors" valuePropName="checked" noStyle>
                      <Switch defaultChecked /> <Text style={{ marginLeft: 8 }}>Skip rows with errors</Text>
                    </Form.Item>
                    <Form.Item name="validateStock" valuePropName="checked" noStyle>
                      <Switch defaultChecked /> <Text style={{ marginLeft: 8 }}>Validate stock levels</Text>
                    </Form.Item>
                  </Space>
                </Form.Item>
              </Form>
            </Space>
          </Card>

          <Card title="Export Products">
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              <Form layout="vertical">
                <Form.Item label="Export Format">
                  <Select defaultValue="xlsx" style={{ width: '100%' }}>
                    <Option value="xlsx">Excel (.xlsx)</Option>
                    <Option value="csv">CSV (.csv)</Option>
                    <Option value="json">JSON (.json)</Option>
                  </Select>
                </Form.Item>
                <Form.Item label="Include Fields">
                  <Select
                    mode="multiple"
                    defaultValue={['sku', 'name', 'price', 'stock']}
                    style={{ width: '100%' }}
                  >
                    <Option value="sku">SKU</Option>
                    <Option value="name">Name</Option>
                    <Option value="description">Description</Option>
                    <Option value="price">Price</Option>
                    <Option value="stock">Stock</Option>
                    <Option value="category">Category</Option>
                    <Option value="images">Images</Option>
                    <Option value="attributes">Attributes</Option>
                  </Select>
                </Form.Item>
                <Form.Item label="Filter by Category">
                  <Select placeholder="All categories" allowClear style={{ width: '100%' }}>
                    <Option value="electronics">Electronics</Option>
                    <Option value="fashion">Fashion</Option>
                    <Option value="home">Home & Garden</Option>
                  </Select>
                </Form.Item>
              </Form>
              <Space style={{ width: '100%' }}>
                <Button type="primary" icon={<UploadOutlined />} onClick={() => handleExport('xlsx')}>
                  Export to Excel
                </Button>
                <Button icon={<FileExcelOutlined />} onClick={() => handleExport('csv')}>
                  Export to CSV
                </Button>
              </Space>
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={12}>
          <Card title="Import History">
            <Table
              columns={historyColumns}
              dataSource={mockImportHistory}
              rowKey="id"
              pagination={{ pageSize: 10 }}
              size="small"
            />
          </Card>
        </Col>
      </Row>

      <Modal
        title="Preview Imported Data"
        open={isPreviewVisible}
        onCancel={() => setIsPreviewVisible(false)}
        width={1000}
        footer={[
          <Button key="cancel" onClick={() => setIsPreviewVisible(false)}>
            Cancel
          </Button>,
          <Button key="import" type="primary" loading={importing} onClick={handleBulkImport}>
            Import {previewData.filter(p => p.status !== 'error').length} Products
          </Button>,
        ]}
      >
        <Alert
          message="Import Summary"
          description={
            <div>
              <Text>Total: {previewData.length} products</Text> <Divider type="vertical" />
              <Text type="success">{previewData.filter(p => p.status === 'success').length} valid</Text> <Divider type="vertical" />
              <Text type="warning">{previewData.filter(p => p.status === 'warning').length} warnings</Text> <Divider type="vertical" />
              <Text type="danger">{previewData.filter(p => p.status === 'error').length} errors</Text>
            </div>
          }
          type="info"
          showIcon
          style={{ marginBottom: 16 }}
        />
        <Table
          columns={productColumns}
          dataSource={previewData}
          rowKey="id"
          pagination={{ pageSize: 5 }}
          size="small"
          scroll={{ x: 900 }}
        />
      </Modal>
    </div>
  );
};

export default BulkImportExportPage;

