    message.success(`Variation duplicated: ${variation.sku}`);
  };

  const handleCreateAttribute = (values: any) => {
    console.log('Creating attribute:', values);
    message.success('Attribute created successfully');
    setIsAttributeModalVisible(false);
    attributeForm.resetFields();
  };

  const handleBulkGenerate = (values: any) => {
    console.log('Bulk generating variations:', values);
    message.success('Product variations generated successfully');
    setIsBulkGenerateVisible(false);
  };

  const handleToggleStatus = (variation: ProductVariation) => {
    message.success(`Variation ${variation.isActive ? 'deactivated' : 'activated'}`);
  };

  const variationColumns: ColumnsType<ProductVariation> = [
    {
      title: 'Product',
      key: 'product',
      width: 200,
      render: (_, record) => (
        <div>
          <Text strong>{record.productName}</Text>
          <div>
            <Text type="secondary" style={{ fontSize: 12 }}>SKU: {record.sku}</Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Attributes',
      key: 'attributes',
      width: 250,
      render: (_, record) => (
        <Space direction="vertical" size={0}>
          {record.attributeType.split(', ').map((attr, idx) => (
            <Tag key={idx} color="blue" style={{ fontSize: 11 }}>
              {attr}
            </Tag>
          ))}
        </Space>
      ),
    },
    {
      title: 'Price',
      dataIndex: 'price',
      key: 'price',
      render: (price) => <Text strong>${price}</Text>,
      sorter: (a, b) => a.price - b.price,
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock',
      render: (stock) => (
        <Tag color={stock > 20 ? 'green' : stock > 0 ? 'orange' : 'red'}>
          {stock} units
        </Tag>
      ),
      sorter: (a, b) => a.stock - b.stock,
    },
    {
      title: 'Status',
      dataIndex: 'isActive',
      key: 'isActive',
      render: (isActive, record) => (
        <Switch
          checked={isActive}
          checkedChildren={<CheckCircleOutlined />}
          unCheckedChildren={<CloseCircleOutlined />}
          onChange={() => handleToggleStatus(record)}
        />
      ),
      filters: [
        { text: 'Active', value: true },
        { text: 'Inactive', value: false },
      ],
      onFilter: (value, record) => record.isActive === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 200,
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Space size="small">
            <Button
              size="small"
              icon={<EditOutlined />}
              onClick={() => handleEditVariation(record)}
            >
              Edit
            </Button>
            <Button
              size="small"
              icon={<CopyOutlined />}
              onClick={() => handleDuplicateVariation(record)}
            >
              Duplicate
            </Button>
          </Space>
          <Button
            size="small"
            danger
            icon={<DeleteOutlined />}
            onClick={() => handleDeleteVariation(record.id)}
          >
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  const attributeColumns: ColumnsType<AttributeOption> = [
    {
      title: 'Attribute Name',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type) => (
        <Tag color={type === 'color' ? 'magenta' : type === 'size' ? 'blue' : 'green'}>
          {type.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Values',
      dataIndex: 'values',
      key: 'values',
      render: (values: string[]) => (
        <Space wrap>
          {values.slice(0, 5).map((value, idx) => (
            <Tag key={idx} style={{ fontSize: 11 }}>
              {value}
            </Tag>
          ))}
          {values.length > 5 && <Text type="secondary">+{values.length - 5} more</Text>}
        </Space>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button size="small" icon={<EditOutlined />}>
            Edit
          </Button>
          <Button size="small" danger icon={<DeleteOutlined />}>
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  const totalVariations = mockVariations.length;
  const activeVariations = mockVariations.filter((v) => v.isActive).length;
  const outOfStock = mockVariations.filter((v) => v.stock === 0).length;
  const totalAttributes = mockAttributes.length;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <AppstoreOutlined style={{ color: '#1890ff' }} /> Product Variations
        </Title>
        <Paragraph type="secondary">
          Manage product variations with multiple attributes like size, color, and material
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Total Variations</Text>
              <Text strong style={{ fontSize: 24, color: '#1890ff' }}>
                {totalVariations}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Active Variations</Text>
              <Text strong style={{ fontSize: 24, color: '#52c41a' }}>
                {activeVariations}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Out of Stock</Text>
              <Text strong style={{ fontSize: 24, color: '#ff4d4f' }}>
                {outOfStock}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Total Attributes</Text>
              <Text strong style={{ fontSize: 24, color: '#722ed1' }}>
                {totalAttributes}
              </Text>
            </Space>
          </Card>
        </Col>
      </Row>

      <Card>
        <Tabs defaultActiveKey="1">
          <TabPane tab="Product Variations" key="1">
            <Space style={{ marginBottom: 16 }}>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => {
                  setSelectedVariation(null);
                  form.resetFields();
                  setIsVariationModalVisible(true);
                }}
              >
                Add Variation
              </Button>
              <Button
                icon={<AppstoreOutlined />}
                onClick={() => setIsBulkGenerateVisible(true)}
              >
                Bulk Generate
              </Button>
            </Space>

            <Table
              columns={variationColumns}
              dataSource={mockVariations}
              rowKey="id"
              scroll={{ x: 1200 }}
              pagination={{ pageSize: 10 }}
            />
          </TabPane>

          <TabPane tab="Attribute Options" key="2">
            <Space style={{ marginBottom: 16 }}>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => setIsAttributeModalVisible(true)}
              >
                Add Attribute
              </Button>
            </Space>

            <Alert
              message="Attribute Management"
              description="Define reusable attribute options like sizes, colors, and materials. These can be used across multiple products."
              type="info"
              showIcon
              style={{ marginBottom: 16 }}
            />

            <Table
              columns={attributeColumns}
              dataSource={mockAttributes}
              rowKey="id"
              pagination={false}
            />
          </TabPane>
        </Tabs>
      </Card>

      {/* Add/Edit Variation Modal */}
      <Modal
        title={selectedVariation ? 'Edit Variation' : 'Add Product Variation'}
        open={isVariationModalVisible}
        onCancel={() => setIsVariationModalVisible(false)}
        footer={null}
        width={700}
      >
        <Form form={form} layout="vertical" onFinish={handleCreateVariation}>
          <Form.Item
            label="Product"
            name="productId"
            rules={[{ required: true, message: 'Please select product' }]}
          >
            <Select placeholder="Select product">
              <Select.Option value={101}>T-Shirt</Select.Option>
              <Select.Option value={102}>Sneakers</Select.Option>
              <Select.Option value={103}>Jeans</Select.Option>
            </Select>
          </Form.Item>

          <Divider>Attributes</Divider>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Size" name="size">
                <Select placeholder="Select size">
                  {mockAttributes
                    .find((a) => a.name === 'Size')
                    ?.values.map((v) => (
                      <Select.Option key={v} value={v}>
                        {v}
                      </Select.Option>
                    ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Color" name="color">
                <Select placeholder="Select color">
                  {mockAttributes
                    .find((a) => a.name === 'Color')
                    ?.values.map((v) => (
                      <Select.Option key={v} value={v}>
                        {v}
                      </Select.Option>
                    ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Material" name="material">
                <Select placeholder="Select material">
                  {mockAttributes
                    .find((a) => a.name === 'Material')
                    ?.values.map((v) => (
                      <Select.Option key={v} value={v}>
                        {v}
                      </Select.Option>
                    ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Style" name="style">
                <Select placeholder="Select style">
                  {mockAttributes
                    .find((a) => a.name === 'Style')
                    ?.values.map((v) => (
                      <Select.Option key={v} value={v}>
                        {v}
                      </Select.Option>
                    ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Divider>Pricing & Inventory</Divider>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="SKU"
                name="sku"
                rules={[{ required: true, message: 'Please enter SKU' }]}
              >
                <Input placeholder="e.g., TSHIRT-M-RED" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Price"
                name="price"
                rules={[{ required: true, message: 'Please enter price' }]}
              >
                <InputNumber
                  prefix="$"
                  min={0}
                  step={0.01}
                  style={{ width: '100%' }}
                  placeholder="0.00"
                />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Stock Quantity"
                name="stock"
                rules={[{ required: true, message: 'Please enter stock' }]}
              >
                <InputNumber min={0} style={{ width: '100%' }} placeholder="0" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Status" name="isActive" valuePropName="checked">
                <Switch
                  checkedChildren="Active"
                  unCheckedChildren="Inactive"
                  defaultChecked
                />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item label="Image URL" name="image">
            <Input placeholder="https://example.com/image.jpg" />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                {selectedVariation ? 'Update Variation' : 'Create Variation'}
              </Button>
              <Button onClick={() => setIsVariationModalVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Add Attribute Modal */}
      <Modal
        title="Add Attribute Option"
        open={isAttributeModalVisible}
        onCancel={() => setIsAttributeModalVisible(false)}
        footer={null}
        width={600}
      >
        <Form form={attributeForm} layout="vertical" onFinish={handleCreateAttribute}>
          <Form.Item
            label="Attribute Name"
            name="name"
            rules={[{ required: true, message: 'Please enter attribute name' }]}
          >
            <Input placeholder="e.g., Size, Color, Material" />
          </Form.Item>

          <Form.Item
            label="Attribute Type"
            name="type"
            rules={[{ required: true, message: 'Please select type' }]}
          >
            <Select placeholder="Select type">
              <Select.Option value="size">Size</Select.Option>
              <Select.Option value="color">Color</Select.Option>
              <Select.Option value="material">Material</Select.Option>
              <Select.Option value="custom">Custom</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Attribute Values"
            name="values"
            rules={[{ required: true, message: 'Please enter values' }]}
            extra="Enter comma-separated values (e.g., Small, Medium, Large)"
          >
            <Input.TextArea
              rows={4}
              placeholder="Small, Medium, Large, XL"
            />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                Create Attribute
              </Button>
              <Button onClick={() => setIsAttributeModalVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Bulk Generate Modal */}
      <Modal
        title="Bulk Generate Variations"
        open={isBulkGenerateVisible}
        onCancel={() => setIsBulkGenerateVisible(false)}
        footer={null}
        width={700}
      >
        <Alert
          message="Bulk Generation"
          description="Select attributes to automatically generate all possible combinations as product variations."
          type="info"
          showIcon
          style={{ marginBottom: 16 }}
        />

        <Form layout="vertical" onFinish={handleBulkGenerate}>
          <Form.Item
            label="Product"
            name="productId"
            rules={[{ required: true, message: 'Please select product' }]}
          >
            <Select placeholder="Select product">
              <Select.Option value={101}>T-Shirt</Select.Option>
              <Select.Option value={102}>Sneakers</Select.Option>
              <Select.Option value={103}>Jeans</Select.Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Select Attributes to Combine"
            name="attributes"
            rules={[{ required: true, message: 'Please select at least one attribute' }]}
          >
            <Select mode="multiple" placeholder="Select attributes">
              <Select.Option value="size">Size</Select.Option>
              <Select.Option value="color">Color</Select.Option>
              <Select.Option value="material">Material</Select.Option>
              <Select.Option value="style">Style</Select.Option>
            </Select>
          </Form.Item>

          <Collapse ghost>
            <Panel header="Advanced Options" key="1">
              <Form.Item label="Base Price" name="basePrice">
                <InputNumber
                  prefix="$"
                  min={0}
                  step={0.01}
                  style={{ width: '100%' }}
                  placeholder="0.00"
                />
              </Form.Item>

              <Form.Item label="Default Stock Quantity" name="defaultStock">
                <InputNumber min={0} style={{ width: '100%' }} placeholder="0" />
              </Form.Item>

              <Form.Item label="SKU Prefix" name="skuPrefix">
                <Input placeholder="e.g., PROD-" />
              </Form.Item>
            </Panel>
          </Collapse>

          <Alert
            message="Preview"
            description={`This will generate variations for all combinations of selected attributes.`}
            type="warning"
            showIcon
            style={{ marginTop: 16, marginBottom: 16 }}
          />

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                Generate Variations
              </Button>
              <Button onClick={() => setIsBulkGenerateVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default ProductVariationsPage;

admin/reports/categories.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Statistic,
  Select,
  DatePicker,
  Button,
  Space,
  Typography,
  Table,
  Tag,
  Progress,
} from 'antd';
import {
  AppstoreOutlined,
  ShoppingOutlined,
  DollarOutlined,
  DownloadOutlined,
  RiseOutlined,
  FallOutlined,
} from '@ant-design/icons';
import { Column, Pie } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;

interface CategoryReport {
  id: number;
  name: string;
  parentCategory: string;
  products: number;
  sales: number;
  revenue: number;
  avgPrice: number;
  marketShare: number;
  trend: 'up' | 'down';
  trendValue: number;
}

const mockCategories: CategoryReport[] = [
  {
    id: 1,
    name: 'Electronics',
    parentCategory: 'Root',
    products: 456,
    sales: 2340,
    revenue: 234500,
    avgPrice: 100.21,
    marketShare: 42,
    trend: 'up',
    trendValue: 15.3,
  },
  {
    id: 2,
    name: 'Headphones',
    parentCategory: 'Electronics',
    products: 89,
    sales: 678,
    revenue: 45600,
    avgPrice: 67.26,
    marketShare: 19,
    trend: 'up',
    trendValue: 8.7,
  },
  {
    id: 3,
    name: 'Accessories',
    parentCategory: 'Root',
    products: 234,
    sales: 1890,
    revenue: 67800,
    avgPrice: 35.87,
    marketShare: 12,
    trend: 'down',
    trendValue: 3.2,
  },
  {
    id: 4,
    name: 'Fashion',
    parentCategory: 'Root',
    products: 567,
    sales: 1456,
    revenue: 123400,
    avgPrice: 84.75,
    marketShare: 22,
    trend: 'up',
    trendValue: 12.1,
  },
  {
    id: 5,
    name: 'Home & Garden',
    parentCategory: 'Root',
    products: 178,
    sales: 567,
    revenue: 28900,
    avgPrice: 50.97,
    marketShare: 5,
    trend: 'down',
    trendValue: 2.5,
  },
];

const AdminCategoryReportsPage: React.FC = () => {
  const [period, setPeriod] = useState<string>('month');

  const totalCategories = mockCategories.length;
  const totalProducts = mockCategories.reduce((sum, c) => sum + c.products, 0);
  const totalSales = mockCategories.reduce((sum, c) => sum + c.sales, 0);
  const totalRevenue = mockCategories.reduce((sum, c) => sum + c.revenue, 0);

  // Market share data
  const marketShareData = mockCategories
    .filter(c => c.parentCategory === 'Root')
    .map(c => ({
      type: c.name,
      value: c.marketShare,
    }));

  const marketShareChartConfig = {
    data: marketShareData,
    angleField: 'value',
    colorField: 'type',
    radius: 0.8,
    label: {
      type: 'outer',
      content: '{name} {percentage}',
    },
    interactions: [{ type: 'element-active' }],
  };

  // Revenue by category chart
  const revenueChartData = mockCategories
    .sort((a, b) => b.revenue - a.revenue)
    .slice(0, 5)
    .map(c => ({
      category: c.name,
      revenue: c.revenue,
    }));

  const revenueChartConfig = {
    data: revenueChartData,
    xField: 'category',
    yField: 'revenue',
    color: '#1890ff',
    columnWidthRatio: 0.6,
    label: {
      position: 'top' as const,
      formatter: (datum: { revenue: number }) => `$${(datum.revenue / 1000).toFixed(0)}K`,
    },
    yAxis: {
      label: {
        formatter: (v: string) => `$${(Number(v) / 1000).toFixed(0)}K`,
      },
    },
  };

  const columns: ColumnsType<CategoryReport> = [
    {
      title: 'Category Name',
      dataIndex: 'name',
      key: 'name',
      fixed: 'left',
      width: 180,
      render: (text: string, record) => (
        <Space>
          {record.parentCategory !== 'Root' && (
            <Text type="secondary" style={{ fontSize: 12 }}>

            </Text>
          )}
          <Text strong>{text}</Text>
        </Space>
      ),
    },
    {
      title: 'Parent',
      dataIndex: 'parentCategory',
      key: 'parentCategory',
      width: 150,
      render: (text: string) => (
        text === 'Root' ? (
          <Tag color="blue">Root</Tag>
        ) : (
          <Tag>{text}</Tag>
        )
      ),
    },
    {
      title: 'Products',
      dataIndex: 'products',
      key: 'products',
      width: 120,
      sorter: (a, b) => a.products - b.products,
      render: (value: number) => (
        <Space>
          <AppstoreOutlined />
          {value.toLocaleString()}
        </Space>
      ),
    },
    {
      title: 'Sales',
      dataIndex: 'sales',
      key: 'sales',
      width: 120,
      sorter: (a, b) => a.sales - b.sales,
      render: (value: number) => (
        <Space>
          <ShoppingOutlined />
          {value.toLocaleString()}
        </Space>
      ),
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      width: 140,
      sorter: (a, b) => a.revenue - b.revenue,
      render: (value: number) => (
        <Text strong style={{ color: '#1890ff' }}>
          ${value.toLocaleString()}
        </Text>
      ),
    },
    {
      title: 'Avg Price',
      dataIndex: 'avgPrice',
      key: 'avgPrice',
      width: 120,
      sorter: (a, b) => a.avgPrice - b.avgPrice,
      render: (value: number) => `$${value.toFixed(2)}`,
    },
    {
      title: 'Market Share',
      dataIndex: 'marketShare',
      key: 'marketShare',
      width: 150,
      sorter: (a, b) => a.marketShare - b.marketShare,
      render: (value: number) => (
        <div>
          <Progress
            percent={value}
            size="small"
            status={value >= 30 ? 'success' : value >= 15 ? 'normal' : 'exception'}
          />
        </div>
      ),
    },
    {
      title: 'Trend',
      key: 'trend',
      width: 120,
      render: (_, record) => (
        <Space>
          {record.trend === 'up' ? (
            <Tag color="success" icon={<RiseOutlined />}>
              +{record.trendValue}%
            </Tag>
          ) : (
            <Tag color="error" icon={<FallOutlined />}>
              -{record.trendValue}%
            </Tag>
          )}
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <Title level={3}>Category Performance Reports</Title>
        <Space>
          <Select value={period} onChange={setPeriod} style={{ width: 150 }}>
            <Option value="week">Last 7 Days</Option>
            <Option value="month">This Month</Option>
            <Option value="quarter">This Quarter</Option>
            <Option value="year">This Year</Option>
            <Option value="custom">Custom Range</Option>
          </Select>
          {period === 'custom' && <RangePicker />}
          <Button type="primary" icon={<DownloadOutlined />}>
            Export Report
          </Button>
        </Space>
      </div>

      {/* Statistics Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Categories"
              value={totalCategories}
              prefix={<AppstoreOutlined style={{ color: '#1890ff' }} />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Products"
              value={totalProducts}
              prefix={<ShoppingOutlined style={{ color: '#52c41a' }} />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Sales"
              value={totalSales}
              valueStyle={{ color: '#722ed1' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              Units sold
            </Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Revenue"
              value={totalRevenue}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#faad14' }} />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={16}>
          <Card title="Revenue by Category" bordered={false}>
            <Column {...revenueChartConfig} />
          </Card>
        </Col>
        <Col xs={24} lg={8}>
          <Card title="Market Share Distribution" bordered={false}>
            <Pie {...marketShareChartConfig} />
          </Card>
        </Col>
      </Row>

      {/* Category Table */}
      <Card title="Category Performance Details" bordered={false}>
        <Table
          columns={columns}
          dataSource={mockCategories}
          rowKey="id"
          scroll={{ x: 1200 }}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} categories`,
          }}
        />
      </Card>
    </div>
  );
};

export default AdminCategoryReportsPage;

admin/reviews-moderation.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  Rate,
  Avatar,
  Modal,
  message,
  Divider,
  Statistic,
  Progress,
  Image,
  Switch,
  Spin,
} from 'antd';
import {
  StarOutlined,
  LikeOutlined,
  DislikeOutlined,
  UserOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  CommentOutlined,
  EyeOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { reviewsAPI } from '@/services/api/reviews';
import type { Review } from '@/services/api/reviews';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

const ReviewsModerationPage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [reviews, setReviews] = useState<Review[]>([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [statusFilter, setStatusFilter] = useState<string | undefined>();
  const [ratingFilter, setRatingFilter] = useState<number | undefined>();
  const [selectedReview, setSelectedReview] = useState<Review | null>(null);
  const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
  const [replyText, setReplyText] = useState<string>('');

  useEffect(() => {
    fetchReviews();
  }, [page, pageSize, statusFilter, ratingFilter]);

  const fetchReviews = async () => {
    try {
      setLoading(true);
      const response = await reviewsAPI.getAll({
        page,
        limit: pageSize,
        status: statusFilter,
        rating: ratingFilter,
      });
      setReviews(response.data);
      setTotal(response.total);
    } catch (error) {
      message.error('Failed to load reviews');
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReview = async (id: string) => {
    try {
      await reviewsAPI.approve(id);
      message.success('Review approved successfully');
      fetchReviews(); // Refresh list
    } catch (error) {
      message.error('Failed to approve review');
    }
  };

  const handleRejectReview = async (id: string) => {
    try {
      await reviewsAPI.reject(id);
      message.success('Review rejected');
      fetchReviews(); // Refresh list
    } catch (error) {
      message.error('Failed to reject review');
    }
  };

  const handleDeleteReview = async (id: string) => {
    Modal.confirm({
      title: 'Delete Review',
      content: 'Are you sure you want to delete this review?',
      onOk: async () => {
        try {
          await reviewsAPI.delete(id);
          message.success('Review deleted successfully');
          fetchReviews();
        } catch (error) {
          message.error('Failed to delete review');
        }
      },
    });
  };

  const handleViewReview = (review: Review) => {
    setSelectedReview(review);
    setIsModalVisible(true);
  };

  const handleReplySubmit = () => {
    message.success('Reply posted successfully');
    setIsModalVisible(false);
    setReplyText('');
  };

  const reviewColumns: ColumnsType<Review> = [
    {
      title: 'Customer',
      key: 'customer',
      width: 200,
      render: (_, record) => (
        <Space>
          <Avatar src={record.customerAvatar} icon={<UserOutlined />} />
          <div>
            <Text strong>{record.customerName}</Text>
            {record.verified && (
              <div>
                <Tag color="green" style={{ fontSize: 10 }}>
                  <CheckCircleOutlined /> Verified Purchase
                </Tag>
              </div>
            )}
          </div>
        </Space>
      ),
    },
    {
      title: 'Product ID',
      dataIndex: 'productId',
      key: 'productId',
      render: (id) => <Text>#{id}</Text>,
    },
    {
      title: 'Rating',
      dataIndex: 'rating',
      key: 'rating',
      render: (rating) => (
        <div>
          <Rate disabled defaultValue={rating} style={{ fontSize: 14 }} />
          <Text type="secondary" style={{ marginLeft: 8, fontSize: 12 }}>
            {rating}.0
          </Text>
        </div>
      ),
      sorter: (a, b) => a.rating - b.rating,
    },
    {
      title: 'Review',
      key: 'review',
      width: 300,
      render: (_, record) => (
        <div>
          <Text strong style={{ display: 'block', marginBottom: 4 }}>{record.title}</Text>
          <Text ellipsis style={{ fontSize: 12 }}>
            {record.comment.substring(0, 100)}...
          </Text>
        </div>
      ),
    },
    {
      title: 'Helpful',
      key: 'helpful',
      render: (_, record) => (
        <Space direction="vertical" size={0}>
          <Space>
            <LikeOutlined style={{ color: '#52c41a' }} />
            <Text>{record.helpful}</Text>
          </Space>
          <Space>
            <DislikeOutlined style={{ color: '#ff4d4f' }} />
            <Text>{record.notHelpful}</Text>
          </Space>
        </Space>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Review['status']) => {
        const config: Record<Review['status'], { color: string; icon: React.ReactNode; text: string }> = {
          pending: { color: 'orange', icon: <EyeOutlined />, text: 'Pending' },
          approved: { color: 'green', icon: <CheckCircleOutlined />, text: 'Approved' },
          rejected: { color: 'red', icon: <CloseCircleOutlined />, text: 'Rejected' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Approved', value: 'approved' },
        { text: 'Rejected', value: 'rejected' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date) => <Text type="secondary" style={{ fontSize: 12 }}>{date}</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button size="small" type="link" onClick={() => handleViewReview(record)}>
            View Details
          </Button>
          {record.status === 'pending' && (
            <Space size="small">
              <Button
                size="small"
                type="primary"
                icon={<CheckCircleOutlined />}
                onClick={() => handleApproveReview(record.id)}
              >
                Approve
              </Button>
              <Button
                size="small"
                danger
                icon={<CloseCircleOutlined />}
                onClick={() => handleRejectReview(record.id)}
              >
                Reject
              </Button>
            </Space>
          )}
        </Space>
      ),
    },
  ];

  // Calculate stats from loaded reviews
  const totalReviews = total;
  const pendingReviews = reviews.filter((r) => r.status === 'pending').length;
  const approvedReviews = reviews.filter((r) => r.status === 'approved').length;
  const avgRating = reviews.length > 0
    ? (reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length).toFixed(1)
    : '0.0';

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <StarOutlined style={{ color: '#1890ff' }} /> Reviews Moderation
        </Title>
        <Paragraph type="secondary">
          Moderate customer reviews, approve or reject submissions, and respond to feedback
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Reviews"
              value={totalReviews}
              prefix={<CommentOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Pending Approval"
              value={pendingReviews}
              prefix={<EyeOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Approved"
              value={approvedReviews}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Average Rating"
              value={avgRating}
              suffix="/ 5"
              prefix={<StarOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={16}>
        <Col xs={24} lg={16}>
          <Card title="Customer Reviews">
            <Table
              columns={reviewColumns}
              dataSource={reviews}
              rowKey="id"
              pagination={{
                current: page,
                pageSize,
                total,
                onChange: (newPage, newPageSize) => {
                  setPage(newPage);
                  setPageSize(newPageSize || 10);
                },
                showSizeChanger: true,
                showTotal: (total) => `Total ${total} reviews`,
              }}
              scroll={{ x: 1200 }}
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card title="Rating Distribution" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              {[5, 4, 3, 2, 1].map((star) => {
                const count = reviews.filter((r) => r.rating === star).length;
                const percentage = totalReviews > 0 ? (count / totalReviews) * 100 : 0;
                return (
                  <div key={star}>
                    <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                      <Space>
                        <Text>{star}</Text>
                        <StarOutlined style={{ color: '#faad14' }} />
                      </Space>
                      <Text type="secondary">{count}</Text>
                    </Space>
                    <Progress percent={percentage} showInfo={false} strokeColor="#faad14" />
                  </div>
                );
              })}
            </Space>
          </Card>

          <Card title="Moderation Settings">
            <Form layout="vertical">
              <Form.Item label="Auto-approve verified purchases" valuePropName="checked">
                <Switch defaultChecked />
              </Form.Item>
              <Form.Item label="Require minimum rating to approve">
                <Select defaultValue="1" style={{ width: '100%' }}>
                  <Select.Option value="1">1 star or higher</Select.Option>
                  <Select.Option value="2">2 stars or higher</Select.Option>
                  <Select.Option value="3">3 stars or higher</Select.Option>
                </Select>
              </Form.Item>
              <Form.Item label="Enable profanity filter" valuePropName="checked">
                <Switch defaultChecked />
              </Form.Item>
              <Form.Item label="Allow customer images" valuePropName="checked">
                <Switch defaultChecked />
              </Form.Item>
              <Form.Item>
                <Button type="primary" block>
                  Save Settings
                </Button>
              </Form.Item>
            </Form>
          </Card>
        </Col>
      </Row>

      <Modal
        title="Review Details"
        open={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        width={700}
        footer={null}
      >
        {selectedReview && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <div>
              <Space style={{ marginBottom: 16 }}>
                <Avatar size={48} icon={<UserOutlined />} />
                <div>
                  <Text strong style={{ display: 'block' }}>{selectedReview.customerName}</Text>
                  <Text type="secondary" style={{ fontSize: 12 }}>{selectedReview.createdAt}</Text>
                  {selectedReview.verified && (
                    <Tag color="green" style={{ fontSize: 10, marginTop: 4 }}>
                      <CheckCircleOutlined /> Verified Purchase
                    </Tag>
                  )}
                </div>
              </Space>
            </div>

            <div>
              <Text type="secondary">Product ID:</Text>
              <div>
                <Text strong>#{selectedReview.productId}</Text>
              </div>
            </div>

            <div>
              <Text type="secondary">Rating:</Text>
              <div>
                <Rate disabled defaultValue={selectedReview.rating} />
                <Text style={{ marginLeft: 8 }}>{selectedReview.rating}.0 / 5.0</Text>
              </div>
            </div>

            <div>
              <Text type="secondary">Review Title:</Text>
              <div>
                <Text strong style={{ fontSize: 16 }}>{selectedReview.title}</Text>
              </div>
            </div>

            <div>
              <Text type="secondary">Review:</Text>
              <div style={{ marginTop: 8, padding: 12, background: '#f5f5f5', borderRadius: 4 }}>
                <Paragraph>{selectedReview.comment}</Paragraph>
              </div>
            </div>

            <div>
              <Text type="secondary">Helpful Votes:</Text>
              <div style={{ marginTop: 8 }}>
                <Space size="large">
                  <Space>
                    <LikeOutlined style={{ color: '#52c41a', fontSize: 18 }} />
                    <Text>{selectedReview.helpful} found this helpful</Text>
                  </Space>
                  <Space>
                    <DislikeOutlined style={{ color: '#ff4d4f', fontSize: 18 }} />
                    <Text>{selectedReview.notHelpful} not helpful</Text>
                  </Space>
                </Space>
              </div>
            </div>

            <Divider />

            <div>
              <Text strong>Respond to Review:</Text>
              <TextArea
                rows={4}
                placeholder="Type your response to the customer..."
                value={replyText}
                onChange={(e) => setReplyText(e.target.value)}
                style={{ marginTop: 8 }}
              />
            </div>

            <Space>
              {selectedReview.status === 'pending' && (
                <>
                  <Button
                    type="primary"
                    icon={<CheckCircleOutlined />}
                    onClick={() => handleApproveReview(selectedReview.id)}
                  >
                    Approve Review
                  </Button>
                  <Button
                    danger
                    icon={<CloseCircleOutlined />}
                    onClick={() => handleRejectReview(selectedReview.id)}
                  >
                    Reject Review
                  </Button>
                </>
              )}
              <Button onClick={handleReplySubmit} disabled={!replyText}>
                Post Reply
              </Button>
            </Space>
          </Space>
        )}
      </Modal>
    </div>
    </Spin>
  );
};

export default ReviewsModerationPage;

customer/advanced-search.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Input,
  Space,
  Button,
  Select,
  Checkbox,
  Slider,
  Tag,
  Divider,
  Collapse,
  Badge,
  List,
  Empty,
  Spin,
} from 'antd';
import {
  SearchOutlined,
  FilterOutlined,
  AppstoreOutlined,
  UnorderedListOutlined,
  HeartOutlined,
  ShoppingCartOutlined,
  StarFilled,
  CloseOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;
const { Panel } = Collapse;

interface Product {
  id: number;
  name: string;
  category: string;
  brand: string;
  price: number;
  originalPrice: number;
  rating: number;
  reviews: number;
  inStock: boolean;
  image: string;
  tags: string[];
}

interface FilterState {
  categories: string[];
  brands: string[];
  priceRange: [number, number];
  rating: number;
  inStock: boolean;
  tags: string[];
}

const mockProducts: Product[] = [
  {
    id: 1,
    name: 'Wireless Headphones Pro',
    category: 'Electronics',
    brand: 'TechBrand',
    price: 99,
    originalPrice: 149,
    rating: 4.5,
    reviews: 234,
    inStock: true,
    image: '',
    tags: ['Bestseller', 'Sale', 'New'],
  },
  {
    id: 2,
    name: 'Smart Watch Series 5',
    category: 'Electronics',
    brand: 'SmartTech',
    price: 299,
    originalPrice: 399,
    rating: 4.8,
    reviews: 567,
    inStock: true,
    image: '',
    tags: ['Premium', 'Bestseller'],
  },
  {
    id: 3,
    name: 'Ergonomic Office Chair',
    category: 'Furniture',
    brand: 'ComfortPlus',
    price: 449,
    originalPrice: 549,
    rating: 4.3,
    reviews: 123,
    inStock: true,
    image: '',
    tags: ['Popular'],
  },
  {
    id: 4,
    name: 'Leather Jacket',
    category: 'Fashion',
    brand: 'StyleCo',
    price: 299,
    originalPrice: 399,
    rating: 4.6,
    reviews: 89,
    inStock: false,
    image: '',
    tags: ['New', 'Limited'],
  },
  {
    id: 5,
    name: 'Gaming Mouse RGB',
    category: 'Electronics',
    brand: 'GameTech',
    price: 59,
    originalPrice: 79,
    rating: 4.7,
    reviews: 345,
    inStock: true,
    image: '',
    tags: ['Sale', 'Gaming'],
  },
  {
    id: 6,
    name: 'Yoga Mat Premium',
    category: 'Sports',
    brand: 'FitLife',
    price: 39,
    originalPrice: 49,
    rating: 4.4,
    reviews: 156,
    inStock: true,
    image: '',
    tags: ['Eco-Friendly', 'Popular'],
  },
];

const categories = ['Electronics', 'Fashion', 'Furniture', 'Sports', 'Home & Garden'];
const brands = ['TechBrand', 'SmartTech', 'ComfortPlus', 'StyleCo', 'GameTech', 'FitLife'];
const tags = ['Bestseller', 'Sale', 'New', 'Premium', 'Popular', 'Gaming', 'Eco-Friendly', 'Limited'];

const AdvancedSearchPage: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [sortBy, setSortBy] = useState<string>('relevance');
  const [loading, setLoading] = useState<boolean>(false);
  const [filters, setFilters] = useState<FilterState>({
    categories: [],
    brands: [],
    priceRange: [0, 1000],
    rating: 0,
    inStock: false,
    tags: [],
  });
  const [activeFiltersCount, setActiveFiltersCount] = useState<number>(0);

  const handleSearch = () => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 500);
  };

  const handleCategoryChange = (checkedValues: any) => {
    setFilters({ ...filters, categories: checkedValues });
    updateActiveFiltersCount({ ...filters, categories: checkedValues });
  };

  const handleBrandChange = (checkedValues: any) => {
    setFilters({ ...filters, brands: checkedValues });
    updateActiveFiltersCount({ ...filters, brands: checkedValues });
  };

  const handlePriceChange = (value: [number, number]) => {
    setFilters({ ...filters, priceRange: value });
    updateActiveFiltersCount({ ...filters, priceRange: value });
  };

  const handleRatingChange = (value: number) => {
    setFilters({ ...filters, rating: value });
    updateActiveFiltersCount({ ...filters, rating: value });
  };

  const handleTagChange = (checkedValues: any) => {
    setFilters({ ...filters, tags: checkedValues });
    updateActiveFiltersCount({ ...filters, tags: checkedValues });
  };

  const handleClearFilters = () => {
    setFilters({
      categories: [],
      brands: [],
      priceRange: [0, 1000],
      rating: 0,
      inStock: false,
      tags: [],
    });
    setActiveFiltersCount(0);
  };

  const updateActiveFiltersCount = (currentFilters: FilterState) => {
    let count = 0;
    if (currentFilters.categories.length > 0) count++;
    if (currentFilters.brands.length > 0) count++;
    if (currentFilters.priceRange[0] > 0 || currentFilters.priceRange[1] < 1000) count++;
    if (currentFilters.rating > 0) count++;
    if (currentFilters.inStock) count++;
    if (currentFilters.tags.length > 0) count++;
    setActiveFiltersCount(count);
  };

  const filterProducts = (products: Product[]): Product[] => {
    return products.filter((product) => {
      // Search query
      if (searchQuery && !product.name.toLowerCase().includes(searchQuery.toLowerCase())) {
        return false;
      }

      // Category filter
      if (filters.categories.length > 0 && !filters.categories.includes(product.category)) {
        return false;
      }

      // Brand filter
      if (filters.brands.length > 0 && !filters.brands.includes(product.brand)) {
        return false;
      }

      // Price range filter
      if (product.price < filters.priceRange[0] || product.price > filters.priceRange[1]) {
        return false;
      }

      // Rating filter
      if (filters.rating > 0 && product.rating < filters.rating) {
        return false;
      }

      // In stock filter
      if (filters.inStock && !product.inStock) {
        return false;
      }

      // Tags filter
      if (filters.tags.length > 0) {
        const hasTag = filters.tags.some((tag) => product.tags.includes(tag));
        if (!hasTag) return false;
      }

      return true;
    });
  };

  const sortProducts = (products: Product[]): Product[] => {
    const sorted = [...products];
    switch (sortBy) {
      case 'price-low':
        return sorted.sort((a, b) => a.price - b.price);
      case 'price-high':
        return sorted.sort((a, b) => b.price - a.price);
      case 'rating':
        return sorted.sort((a, b) => b.rating - a.rating);
      case 'reviews':
        return sorted.sort((a, b) => b.reviews - a.reviews);
      case 'name':
        return sorted.sort((a, b) => a.name.localeCompare(b.name));
      default:
        return sorted;
    }
  };

  const filteredProducts = sortProducts(filterProducts(mockProducts));

  const ProductCard: React.FC<{ product: Product }> = ({ product }) => (
    <Card
      hoverable
      style={{ height: '100%' }}
      cover={
        <div
          style={{
            height: 200,
            background: '#f0f0f0',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            position: 'relative',
          }}
        >
          {product.tags.length > 0 && (
            <div style={{ position: 'absolute', top: 8, left: 8 }}>
              {product.tags.slice(0, 2).map((tag, idx) => (
                <Tag
                  key={idx}
                  color={tag === 'Sale' ? 'red' : tag === 'New' ? 'green' : 'blue'}
                  style={{ marginBottom: 4 }}
                >
                  {tag}
                </Tag>
              ))}
            </div>
          )}
          <Text type="secondary">Product Image</Text>
        </div>
      }
      actions={[
        <HeartOutlined key="wishlist" />,
        <ShoppingCartOutlined key="cart" />,
      ]}
    >
      <Card.Meta
        title={<Text ellipsis>{product.name}</Text>}
        description={
          <Space direction="vertical" size="small" style={{ width: '100%' }}>
            <Text type="secondary" style={{ fontSize: 12 }}>
              {product.brand}  {product.category}
            </Text>
            <Space>
              <StarFilled style={{ color: '#faad14', fontSize: 12 }} />
              <Text style={{ fontSize: 12 }}>
                {product.rating} ({product.reviews})
              </Text>
            </Space>
            <Space>
              <Text strong style={{ fontSize: 18, color: '#ff4d4f' }}>
                ${product.price}
              </Text>
              {product.originalPrice > product.price && (
                <Text delete type="secondary" style={{ fontSize: 12 }}>
                  ${product.originalPrice}
                </Text>
              )}
            </Space>
            {!product.inStock && (
              <Tag color="red" style={{ fontSize: 11 }}>
                Out of Stock
              </Tag>
            )}
          </Space>
        }
      />
    </Card>
  );

  const ProductListItem: React.FC<{ product: Product }> = ({ product }) => (
    <List.Item
      actions={[
        <Button key="wishlist" icon={<HeartOutlined />}>
          Wishlist
        </Button>,
        <Button key="cart" type="primary" icon={<ShoppingCartOutlined />} disabled={!product.inStock}>
          Add to Cart
        </Button>,
      ]}
    >
      <List.Item.Meta
        avatar={
          <div
            style={{
              width: 100,
              height: 100,
              background: '#f0f0f0',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <Text type="secondary" style={{ fontSize: 11 }}>Image</Text>
          </div>
        }
        title={
          <Space>
            <Text strong>{product.name}</Text>
            {product.tags.map((tag, idx) => (
              <Tag
                key={idx}
                color={tag === 'Sale' ? 'red' : tag === 'New' ? 'green' : 'blue'}
                style={{ fontSize: 10 }}
              >
                {tag}
              </Tag>
            ))}
          </Space>
        }
        description={
          <Space direction="vertical" size="small">
            <Text type="secondary">
              {product.brand}  {product.category}
            </Text>
            <Space>
              <StarFilled style={{ color: '#faad14' }} />
              <Text>
                {product.rating} ({product.reviews} reviews)
              </Text>
            </Space>
            <Space>
              <Text strong style={{ fontSize: 20, color: '#ff4d4f' }}>
                ${product.price}
              </Text>
              {product.originalPrice > product.price && (
                <Text delete type="secondary">
                  ${product.originalPrice}
                </Text>
              )}
              {product.originalPrice > product.price && (
                <Tag color="red">
                  {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                </Tag>
              )}
            </Space>
            {!product.inStock && (
              <Tag color="red">Out of Stock</Tag>
            )}
          </Space>
        }
      />
    </List.Item>
  );

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <SearchOutlined style={{ color: '#1890ff' }} /> Advanced Search
        </Title>
        <Paragraph type="secondary">
          Search products with advanced filters and faceted navigation
        </Paragraph>
      </div>

      <Row gutter={24}>
        {/* Filters Sidebar */}
        <Col xs={24} lg={6}>
          <Card
            title={
              <Space>
                <FilterOutlined />
                <Text>Filters</Text>
                {activeFiltersCount > 0 && <Badge count={activeFiltersCount} />}
              </Space>
            }
            extra={
              activeFiltersCount > 0 && (
                <Button
                  type="link"
                  size="small"
                  icon={<CloseOutlined />}
                  onClick={handleClearFilters}
                >
                  Clear All
                </Button>
              )
            }
          >
            <Collapse
              defaultActiveKey={['category', 'price', 'rating']}
              ghost
              expandIconPosition="end"
            >
              <Panel header="Category" key="category">
                <Checkbox.Group
                  style={{ display: 'flex', flexDirection: 'column' }}
                  value={filters.categories}
                  onChange={handleCategoryChange}
                >
                  {categories.map((category) => (
                    <Checkbox key={category} value={category} style={{ marginBottom: 8 }}>
                      {category}
                    </Checkbox>
                  ))}
                </Checkbox.Group>
              </Panel>

              <Panel header="Brand" key="brand">
                <Checkbox.Group
                  style={{ display: 'flex', flexDirection: 'column' }}
                  value={filters.brands}
                  onChange={handleBrandChange}
                >
                  {brands.map((brand) => (
                    <Checkbox key={brand} value={brand} style={{ marginBottom: 8 }}>
                      {brand}
                    </Checkbox>
                  ))}
                </Checkbox.Group>
              </Panel>

              <Panel header="Price Range" key="price">
                <Slider
                  range
                  min={0}
                  max={1000}
                  step={10}
                  value={filters.priceRange}
                  onChange={(value) => handlePriceChange(value as [number, number])}
                  tooltip={{ formatter: (value) => `$${value}` }}
                />
                <Space style={{ width: '100%', justifyContent: 'space-between', marginTop: 8 }}>
                  <Text>${filters.priceRange[0]}</Text>
                  <Text>${filters.priceRange[1]}</Text>
                </Space>
              </Panel>

              <Panel header="Rating" key="rating">
                <Space direction="vertical" style={{ width: '100%' }}>
                  {[4, 3, 2, 1].map((rating) => (
                    <Button
                      key={rating}
                      type={filters.rating === rating ? 'primary' : 'default'}
                      size="small"
                      block
                      onClick={() => handleRatingChange(rating)}
                      style={{ textAlign: 'left' }}
                    >
                      <Space>
                        {[...Array(rating)].map((_, i) => (
                          <StarFilled key={i} style={{ color: '#faad14', fontSize: 12 }} />
                        ))}
                        <Text>& Up</Text>
                      </Space>
                    </Button>
                  ))}
                  {filters.rating > 0 && (
                    <Button size="small" block onClick={() => handleRatingChange(0)}>
                      Clear Rating
                    </Button>
                  )}
                </Space>
              </Panel>

              <Panel header="Tags" key="tags">
                <Checkbox.Group
                  style={{ display: 'flex', flexDirection: 'column' }}
                  value={filters.tags}
                  onChange={handleTagChange}
                >
                  {tags.map((tag) => (
                    <Checkbox key={tag} value={tag} style={{ marginBottom: 8 }}>
                      {tag}
                    </Checkbox>
                  ))}
                </Checkbox.Group>
              </Panel>

              <Panel header="Availability" key="availability">
                <Checkbox
                  checked={filters.inStock}
                  onChange={(e) => {
                    const newFilters = { ...filters, inStock: e.target.checked };
                    setFilters(newFilters);
                    updateActiveFiltersCount(newFilters);
                  }}
                >
                  In Stock Only
                </Checkbox>
              </Panel>
            </Collapse>
          </Card>
        </Col>

        {/* Products Section */}
        <Col xs={24} lg={18}>
          <Card>
            <Space direction="vertical" size="large" style={{ width: '100%' }}>
              {/* Search Bar */}
              <Input
                size="large"
                placeholder="Search products..."
                prefix={<SearchOutlined />}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onPressEnter={handleSearch}
                allowClear
              />

              <Divider style={{ margin: 0 }} />

              {/* Toolbar */}
              <Row justify="space-between" align="middle">
                <Col>
                  <Text type="secondary">
                    {filteredProducts.length} product{filteredProducts.length !== 1 ? 's' : ''} found
                  </Text>
                </Col>
                <Col>
                  <Space>
                    <Text type="secondary">Sort by:</Text>
                    <Select value={sortBy} onChange={setSortBy} style={{ width: 150 }}>
                      <Select.Option value="relevance">Relevance</Select.Option>
                      <Select.Option value="price-low">Price: Low to High</Select.Option>
                      <Select.Option value="price-high">Price: High to Low</Select.Option>
                      <Select.Option value="rating">Rating</Select.Option>
                      <Select.Option value="reviews">Most Reviews</Select.Option>
                      <Select.Option value="name">Name</Select.Option>
                    </Select>
                    <Button.Group>
                      <Button
                        icon={<AppstoreOutlined />}
                        type={viewMode === 'grid' ? 'primary' : 'default'}
                        onClick={() => setViewMode('grid')}
                      />
                      <Button
                        icon={<UnorderedListOutlined />}
                        type={viewMode === 'list' ? 'primary' : 'default'}
                        onClick={() => setViewMode('list')}
                      />
                    </Button.Group>
                  </Space>
                </Col>
              </Row>

              {/* Results */}
              <Spin spinning={loading}>
                {filteredProducts.length === 0 ? (
                  <Empty description="No products found" />
                ) : viewMode === 'grid' ? (
                  <Row gutter={[16, 16]}>
                    {filteredProducts.map((product) => (
                      <Col key={product.id} xs={24} sm={12} md={8}>
                        <ProductCard product={product} />
                      </Col>
                    ))}
                  </Row>
                ) : (
                  <List
                    dataSource={filteredProducts}
                    renderItem={(product) => <ProductListItem product={product} />}
                  />
                )}
              </Spin>
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default AdvancedSearchPage;

customer/payment-methods.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Modal,
  Form,
  Input,
  Select,
  message,
  Statistic,
  Alert,
  Spin,
  Tabs,
  Tooltip,
  Popconfirm,
  Progress,
  Switch,
  Divider,
  Empty,
} from 'antd';
import {
  DollarOutlined,
  PlusOutlined,
  BankOutlined,
  CreditCardOutlined,
  PayCircleOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  WarningOutlined,
  SafetyOutlined,
  DeleteOutlined,
  EditOutlined,
  WalletOutlined,
  LockOutlined,
  ShoppingOutlined,
  HistoryOutlined,
  VerifiedOutlined,
  ExclamationCircleOutlined,
  ReloadOutlined,
  ArrowUpOutlined,
  ArrowDownOutlined,
  EyeOutlined,
  StarOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import accountAPI from '../../services/api/account';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TabPane } = Tabs;

// Interfaces
interface PaymentMethodData {
  id: string;
  type: 'card' | 'bank' | 'wallet' | 'paypal';
  name: string;
  last4: string;
  expiryDate?: string;
  brand?: string;
  isDefault: boolean;
  isVerified: boolean;
  createdAt: string;
  status: 'active' | 'expired' | 'suspended';
}

interface WalletTransactionData {
  id: string;
  type: 'credit' | 'debit';
  amount: number;
  currency: string;
  description: string;
  status: 'completed' | 'pending' | 'failed';
  createdAt: string;
  paymentMethodId?: string;
  orderId?: string;
}

interface WalletBalance {
  available: number;
  pending: number;
  currency: string;
}

interface PaymentStats {
  totalSpent: number;
  transactionsCount: number;
  averageOrder: number;
  currency: string;
}

const PaymentMethodsPage: React.FC = () => {
  // State management
  const [paymentMethods, setPaymentMethods] = useState<PaymentMethodData[]>([]);
  const [transactions, setTransactions] = useState<WalletTransactionData[]>([]);
  const [walletBalance, setWalletBalance] = useState<WalletBalance | null>(null);
  const [paymentStats, setPaymentStats] = useState<PaymentStats | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [saving, setSaving] = useState<boolean>(false);

  // Modal states
  const [isAddModalVisible, setIsAddModalVisible] = useState<boolean>(false);
  const [isEditModalVisible, setIsEditModalVisible] = useState<boolean>(false);
  const [editingPayment, setEditingPayment] = useState<PaymentMethodData | null>(null);

  // Form instances
  const [form] = Form.useForm();
  const [editForm] = Form.useForm();

  // Load data
  useEffect(() => {
    loadPaymentData();
  }, []);

  const loadPaymentData = async () => {
    try {
      setLoading(true);
      const paymentMethodsData = await accountAPI.paymentMethodsAPI.getPaymentMethods();

      setPaymentMethods(paymentMethodsData.data);

      // Mock data for demonstration - replace with real API calls
      setTransactions([
        {
          id: '1',
          type: 'debit',
          amount: 99.99,
          currency: 'USD',
          description: 'Purchase - Electronics Store',
          status: 'completed',
          createdAt: '2024-01-15T10:30:00Z',
          paymentMethodId: 'pm_1',
          orderId: 'ORD-2024-001'
        },
        {
          id: '2',
          type: 'credit',
          amount: 25.00,
          currency: 'USD',
          description: 'Refund - Return Processing',
          status: 'completed',
          createdAt: '2024-01-14T15:20:00Z',
          orderId: 'ORD-2024-001'
        },
        {
          id: '3',
          type: 'debit',
          amount: 149.99,
          currency: 'USD',
          description: 'Purchase - Fashion Items',
          status: 'pending',
          createdAt: '2024-01-13T09:15:00Z',
          paymentMethodId: 'pm_2'
        },
        {
          id: '4',
          type: 'debit',
          amount: 75.50,
          currency: 'USD',
          description: 'Purchase - Home & Garden',
          status: 'completed',
          createdAt: '2024-01-12T14:45:00Z',
          paymentMethodId: 'pm_1'
        },
        {
          id: '5',
          type: 'credit',
          amount: 15.99,
          currency: 'USD',
          description: 'Cashback Reward',
          status: 'completed',
          createdAt: '2024-01-10T11:20:00Z'
        }
      ]);

      setWalletBalance({
        available: 125.50,
        pending: 25.00,
        currency: 'USD'
      });

      setPaymentStats({
        totalSpent: 2450.75,
        transactionsCount: 23,
        averageOrder: 106.56,
        currency: 'USD'
      });

    } catch (error) {
      console.error('Failed to load payment data:', error);
      message.error('Failed to load payment data');
    } finally {
      setLoading(false);
    }
  };

  // Payment method handlers
  const handleAddPaymentMethod = async (values: any) => {
    try {
      setSaving(true);
      const response = await accountAPI.paymentMethodsAPI.addPaymentMethod({
        type: values.type,
        token: values.cardNumber, // In real implementation, this would be a tokenized card
        billingAddressId: undefined,
        setAsDefault: values.isDefault || false,
      });

      setPaymentMethods(prev => [...prev, response.data]);
      message.success('Payment method added successfully');
      setIsAddModalVisible(false);
      form.resetFields();
    } catch (error) {
      console.error('Failed to add payment method:', error);
      message.error('Failed to add payment method');
    } finally {
      setSaving(false);
    }
  };

  const handleEditPaymentMethod = async (values: any) => {
    try {
      setSaving(true);
      if (!editingPayment) return;

      const response = await accountAPI.paymentMethodsAPI.updatePaymentMethod(editingPayment.id, {
        title: values.name,
        billingAddressId: undefined,
      });

      setPaymentMethods(prev => prev.map(pm => 
        pm.id === editingPayment.id ? response.data : pm
      ));
      message.success('Payment method updated successfully');
      setIsEditModalVisible(false);
      setEditingPayment(null);
      editForm.resetFields();
    } catch (error) {
      console.error('Failed to update payment method:', error);
      message.error('Failed to update payment method');
    } finally {
      setSaving(false);
    }
  };

  const handleSetDefault = async (id: string) => {
    try {
      await accountAPI.paymentMethodsAPI.setDefaultPaymentMethod(id);
      setPaymentMethods(prev => prev.map(pm => ({
        ...pm,
        isDefault: pm.id === id
      })));
      message.success('Default payment method updated');
    } catch (error) {
      console.error('Failed to set default payment method:', error);
      message.error('Failed to set default payment method');
    }
  };

  const handleDeletePaymentMethod = async (id: string) => {
    try {
      await accountAPI.paymentMethodsAPI.deletePaymentMethod(id);
      setPaymentMethods(prev => prev.filter(pm => pm.id !== id));
      message.success('Payment method deleted successfully');
    } catch (error) {
      console.error('Failed to delete payment method:', error);
      message.error('Failed to delete payment method');
    }
  };

  // Utility functions
  const getPaymentIcon = (type: PaymentMethodData['type']) => {
    const icons = {
      card: <CreditCardOutlined style={{ color: '#1890ff' }} />,
      bank: <BankOutlined style={{ color: '#52c41a' }} />,
      paypal: <PayCircleOutlined style={{ color: '#0070ba' }} />,
      wallet: <WalletOutlined style={{ color: '#722ed1' }} />,
    };
    return icons[type];
  };

  const getStatusColor = (status: PaymentMethodData['status']) => {
    const colors = {
      active: 'green',
      expired: 'orange',
      suspended: 'red',
    };
    return colors[status];
  };

  const getTransactionIcon = (type: WalletTransactionData['type']) => {
    return type === 'credit' ? 
      <ArrowUpOutlined style={{ color: '#52c41a' }} /> : 
      <ArrowDownOutlined style={{ color: '#f5222d' }} />;
  };

  // Table columns
  const paymentColumns: ColumnsType<PaymentMethodData> = [
    {
      title: 'Type',
      key: 'type',
      width: 100,
      render: (record) => (
        <Space>
          {getPaymentIcon(record.type)}
          <span style={{ textTransform: 'capitalize' }}>{record.type}</span>
        </Space>
      ),
    },
    {
      title: 'Payment Details',
      key: 'details',
      render: (record) => (
        <div>
          <div style={{ fontWeight: 500 }}>{record.name}</div>
          <Text type="secondary">
            {record.brand && `${record.brand}  `}****{record.last4}
            {record.expiryDate && `  Expires ${record.expiryDate}`}
          </Text>
        </div>
      ),
    },
    {
      title: 'Status',
      key: 'status',
      width: 150,
      render: (record) => (
        <Space direction="vertical" size="small">
          <Tag color={getStatusColor(record.status)}>
            {record.status.toUpperCase()}
          </Tag>
          <Space>
            {record.isDefault && <Tag color="blue">DEFAULT</Tag>}
            {record.isVerified ? (
              <Tag color="green" icon={<VerifiedOutlined />}>VERIFIED</Tag>
            ) : (
              <Tag color="orange" icon={<WarningOutlined />}>UNVERIFIED</Tag>
            )}
          </Space>
        </Space>
      ),
    },
    {
      title: 'Added',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 120,
      render: (date) => (
        <div>
          <div>{dayjs(date).format('MMM DD')}</div>
          <Text type="secondary">{dayjs(date).format('YYYY')}</Text>
        </div>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 150,
      render: (record) => (
        <Space>
          <Tooltip title="Edit">
            <Button
              type="link"
              size="small"
              icon={<EditOutlined />}
              onClick={() => {
                setEditingPayment(record);
                editForm.setFieldsValue(record);
                setIsEditModalVisible(true);
              }}
            />
          </Tooltip>
          {!record.isDefault && (
            <Tooltip title="Set as default">
              <Button
                type="link"
                size="small"
                onClick={() => handleSetDefault(record.id)}
              >
                Set Default
              </Button>
            </Tooltip>
          )}
          <Popconfirm
            title="Delete payment method"
            description="Are you sure you want to delete this payment method?"
            onConfirm={() => handleDeletePaymentMethod(record.id)}
            okText="Delete"
            cancelText="Cancel"
            okButtonProps={{ danger: true }}
          >
            <Tooltip title="Delete">
              <Button
                type="link"
                size="small"
                danger
                icon={<DeleteOutlined />}
              />
            </Tooltip>
          </Popconfirm>
        </Space>
      ),
    },
  ];

  const transactionColumns: ColumnsType<WalletTransactionData> = [
    {
      title: 'Type',
      key: 'type',
      width: 80,
      render: (record) => (
        <Space>
          {getTransactionIcon(record.type)}
          <span style={{ 
            textTransform: 'capitalize',
            color: record.type === 'credit' ? '#52c41a' : '#f5222d'
          }}>
            {record.type}
          </span>
        </Space>
      ),
    },
    {
      title: 'Description',
      key: 'description',
      render: (record) => (
        <div>
          <div style={{ fontWeight: 500 }}>{record.description}</div>
          {record.orderId && (
            <Text type="secondary">Order: {record.orderId}</Text>
          )}
        </div>
      ),
    },
    {
      title: 'Amount',
      key: 'amount',
      width: 120,
      render: (record) => (
        <div style={{
          color: record.type === 'credit' ? '#52c41a' : '#f5222d',
          fontWeight: 500
        }}>
          {record.type === 'credit' ? '+' : '-'}
          {record.currency} {record.amount.toFixed(2)}
        </div>
      ),
    },
    {
      title: 'Status',
      key: 'status',
      width: 100,
      render: (record) => {
        const config = {
          completed: { color: 'green', icon: <CheckCircleOutlined /> },
          pending: { color: 'orange', icon: <ClockCircleOutlined /> },
          failed: { color: 'red', icon: <ExclamationCircleOutlined /> },
        };
        const { color, icon } = config[record.status as keyof typeof config];
        return (
          <Tag color={color} icon={icon}>
            {record.status.toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 120,
      render: (date) => (
        <div>
          <div>{dayjs(date).format('MMM DD')}</div>
          <Text type="secondary">{dayjs(date).format('HH:mm')}</Text>
        </div>
      ),
    },
  ];

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '400px' }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: '24px' }}>
        <Title level={2}>
          <WalletOutlined /> Payment Methods & Wallet
        </Title>
        <Paragraph>
          Manage your payment methods, view transaction history, and monitor your wallet balance.
        </Paragraph>
      </div>

      {/* Stats Cards */}
      <Row gutter={[16, 16]} style={{ marginBottom: '24px' }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Wallet Balance"
              value={walletBalance?.available || 0}
              prefix={<DollarOutlined style={{ color: '#52c41a' }} />}
              precision={2}
              valueStyle={{ color: '#52c41a' }}
              suffix={walletBalance?.currency}
            />
            {walletBalance && walletBalance.pending > 0 && (
              <Text type="secondary">
                {walletBalance.currency} {walletBalance.pending.toFixed(2)} pending
              </Text>
            )}
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Spent"
              value={paymentStats?.totalSpent || 0}
              prefix={<ShoppingOutlined style={{ color: '#1890ff' }} />}
              precision={2}
              suffix={paymentStats?.currency}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Transactions"
              value={paymentStats?.transactionsCount || 0}
              prefix={<HistoryOutlined style={{ color: '#722ed1' }} />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Average Order"
              value={paymentStats?.averageOrder || 0}
              prefix={<StarOutlined style={{ color: '#faad14' }} />}
              precision={2}
              suffix={paymentStats?.currency}
            />
          </Card>
        </Col>
      </Row>

      {/* Main Content */}
      <Card>
        <Tabs defaultActiveKey="1" size="large">
          {/* Payment Methods Tab */}
          <TabPane tab={<span><CreditCardOutlined />Payment Methods</span>} key="1">
            <div style={{ marginBottom: '16px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Title level={4} style={{ margin: 0 }}>
                Saved Payment Methods ({paymentMethods.length})
              </Title>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => setIsAddModalVisible(true)}
              >
                Add Payment Method
              </Button>
            </div>

            {paymentMethods.length === 0 ? (
              <Empty
                image={Empty.PRESENTED_IMAGE_SIMPLE}
                description="No payment methods found"
              >
                <Button type="primary" onClick={() => setIsAddModalVisible(true)}>
                  Add Your First Payment Method
                </Button>
              </Empty>
            ) : (
              <Table
                dataSource={paymentMethods}
                columns={paymentColumns}
                rowKey="id"
                pagination={{
                  pageSize: 10,
                  showSizeChanger: false,
                  showQuickJumper: true,
                  showTotal: (total, range) =>
                    `${range[0]}-${range[1]} of ${total} payment methods`,
                }}
                locale={{ emptyText: 'No payment methods found' }}
              />
            )}
          </TabPane>

          {/* Transaction History Tab */}
          <TabPane tab={<span><HistoryOutlined />Transaction History</span>} key="2">
            <div style={{ marginBottom: '16px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Title level={4} style={{ margin: 0 }}>
                Recent Transactions ({transactions.length})
              </Title>
              <Button icon={<ReloadOutlined />} onClick={loadPaymentData}>
                Refresh
              </Button>
            </div>

            <Table
              dataSource={transactions}
              columns={transactionColumns}
              rowKey="id"
              pagination={{
                pageSize: 10,
                showSizeChanger: true,
                showQuickJumper: true,
                showTotal: (total, range) =>
                  `${range[0]}-${range[1]} of ${total} transactions`,
              }}
              locale={{ emptyText: 'No transactions found' }}
            />
          </TabPane>

          {/* Security Tab */}
          <TabPane tab={<span><SafetyOutlined />Security</span>} key="3">
            <Title level={4}>Payment Security Settings</Title>
            <Space direction="vertical" size="large" style={{ width: '100%' }}>
              <Card size="small">
                <Row gutter={16} align="middle">
                  <Col flex="auto">
                    <Title level={5} style={{ margin: 0 }}>Two-Factor Authentication</Title>
                    <Text type="secondary">
                      Add an extra layer of security to your payments
                    </Text>
                  </Col>
                  <Col>
                    <Switch defaultChecked />
                  </Col>
                </Row>
              </Card>

              <Card size="small">
                <Row gutter={16} align="middle">
                  <Col flex="auto">
                    <Title level={5} style={{ margin: 0 }}>Payment Notifications</Title>
                    <Text type="secondary">
                      Get notified for every transaction
                    </Text>
                  </Col>
                  <Col>
                    <Switch defaultChecked />
                  </Col>
                </Row>
              </Card>

              <Card size="small">
                <Row gutter={16} align="middle">
                  <Col flex="auto">
                    <Title level={5} style={{ margin: 0 }}>Auto-save Payment Methods</Title>
                    <Text type="secondary">
                      Automatically save new payment methods for future use
                    </Text>
                  </Col>
                  <Col>
                    <Switch defaultChecked />
                  </Col>
                </Row>
              </Card>

              <Alert
                message="Security Tip"
                description="Always verify payment method details and enable two-factor authentication for enhanced security."
                type="info"
                showIcon
                icon={<LockOutlined />}
              />
            </Space>
          </TabPane>
        </Tabs>
      </Card>

      {/* Add Payment Method Modal */}
      <Modal
        title="Add Payment Method"
        open={isAddModalVisible}
        onCancel={() => {
          setIsAddModalVisible(false);
          form.resetFields();
        }}
        footer={null}
        width={600}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleAddPaymentMethod}
        >
          <Form.Item
            name="type"
            label="Payment Type"
            rules={[{ required: true, message: 'Please select a payment type' }]}
          >
            <Select placeholder="Select payment type" size="large">
              <Option value="card">Credit/Debit Card</Option>
              <Option value="bank">Bank Account</Option>
              <Option value="paypal">PayPal</Option>
              <Option value="wallet">Digital Wallet</Option>
            </Select>
          </Form.Item>

          <Form.Item
            name="name"
            label="Name on Card/Account"
            rules={[{ required: true, message: 'Please enter the name' }]}
          >
            <Input size="large" placeholder="John Doe" />
          </Form.Item>

          <Row gutter={16}>
            <Col span={16}>
              <Form.Item
                name="cardNumber"
                label="Card Number"
                rules={[{ required: true, message: 'Please enter card number' }]}
              >
                <Input size="large" placeholder="1234 5678 9012 3456" />
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item
                name="cvv"
                label="CVV"
                rules={[{ required: true, message: 'Please enter CVV' }]}
              >
                <Input size="large" placeholder="123" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={8}>
              <Form.Item
                name="expiryMonth"
                label="Month"
                rules={[{ required: true, message: 'Please select month' }]}
              >
                <Select size="large" placeholder="MM">
                  {Array.from({ length: 12 }, (_, i) => (
                    <Option key={i + 1} value={String(i + 1).padStart(2, '0')}>
                      {String(i + 1).padStart(2, '0')}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item
                name="expiryYear"
                label="Year"
                rules={[{ required: true, message: 'Please select year' }]}
              >
                <Select size="large" placeholder="YYYY">
                  {Array.from({ length: 10 }, (_, i) => {
                    const year = new Date().getFullYear() + i;
                    return (
                      <Option key={year} value={String(year)}>
                        {year}
                      </Option>
                    );
                  })}
                </Select>
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item name="brand" label="Brand">
                <Select size="large" placeholder="Select brand">
                  <Option value="visa">Visa</Option>
                  <Option value="mastercard">Mastercard</Option>
                  <Option value="amex">American Express</Option>
                  <Option value="discover">Discover</Option>
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Form.Item name="isDefault" valuePropName="checked">
            <Switch /> Set as default payment method
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" loading={saving}>
                Add Payment Method
              </Button>
              <Button onClick={() => setIsAddModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Edit Payment Method Modal */}
      <Modal
        title="Edit Payment Method"
        open={isEditModalVisible}
        onCancel={() => {
          setIsEditModalVisible(false);
          setEditingPayment(null);
          editForm.resetFields();
        }}
        footer={null}
        width={500}
      >
        <Form
          form={editForm}
          layout="vertical"
          onFinish={handleEditPaymentMethod}
        >
          <Form.Item
            name="name"
            label="Name on Card/Account"
            rules={[{ required: true, message: 'Please enter the name' }]}
          >
            <Input size="large" placeholder="John Doe" />
          </Form.Item>

          <Form.Item name="isDefault" valuePropName="checked">
            <Switch /> Set as default payment method
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" loading={saving}>
                Update Payment Method
              </Button>
              <Button onClick={() => setIsEditModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default PaymentMethodsPage;

customer/sample-requests.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Space,
  Table,
  Tag,
  Modal,
  Upload,
  Select,
  InputNumber,
  DatePicker,
  message,
  Divider,
  Steps,
  Alert,
} from 'antd';
import {
  InboxOutlined,
  PlusOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  CloseCircleOutlined,
  FileOutlined,
  SendOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import type { UploadFile } from 'antd/es/upload/interface';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;
const { Dragger } = Upload;

interface SampleRequest {
  id: number;
  productName: string;
  vendorName: string;
  quantity: number;
  requestDate: string;
  requiredDate: string;
  status: 'pending' | 'approved' | 'rejected' | 'shipped' | 'received';
  notes: string;
  specifications?: string;
  attachments?: string[];
}

const mockSampleRequests: SampleRequest[] = [
  {
    id: 1,
    productName: 'Wireless Headphones Pro',
    vendorName: 'TechHub Electronics',
    quantity: 2,
    requestDate: '2024-11-01',
    requiredDate: '2024-11-15',
    status: 'approved',
    notes: 'Need samples for quality testing',
    specifications: 'Bluetooth 5.0, Active Noise Cancellation',
  },
  {
    id: 2,
    productName: 'Organic Cotton T-Shirt',
    vendorName: 'Fashion Boutique',
    quantity: 5,
    requestDate: '2024-10-28',
    requiredDate: '2024-11-10',
    status: 'shipped',
    notes: 'Different sizes needed for fit testing',
    specifications: 'Sizes: S, M, L, XL, XXL',
  },
  {
    id: 3,
    productName: 'Smart Watch Series 5',
    vendorName: 'TechHub Electronics',
    quantity: 1,
    requestDate: '2024-10-25',
    requiredDate: '2024-11-05',
    status: 'pending',
    notes: 'Evaluation for potential bulk order',
  },
];

const SampleRequestsPage: React.FC = () => {
  const [requests, setRequests] = useState<SampleRequest[]>(mockSampleRequests);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [fileList, setFileList] = useState<UploadFile[]>([]);
  const [form] = Form.useForm();

  const handleCreateRequest = () => {
    form.validateFields().then((values) => {
      const newRequest: SampleRequest = {
        id: Date.now(),
        productName: values.productName,
        vendorName: values.vendorName,
        quantity: values.quantity,
        requestDate: dayjs().format('YYYY-MM-DD'),
        requiredDate: values.requiredDate.format('YYYY-MM-DD'),
        status: 'pending',
        notes: values.notes,
        specifications: values.specifications,
        attachments: fileList.map((file) => file.name),
      };

      setRequests([newRequest, ...requests]);
      message.success('Sample request created successfully!');
      setIsModalVisible(false);
      setCurrentStep(0);
      form.resetFields();
      setFileList([]);
    });
  };

  const handleNextStep = () => {
    if (currentStep === 0) {
      form
        .validateFields(['productName', 'vendorName', 'quantity'])
        .then(() => setCurrentStep(1));
    } else if (currentStep === 1) {
      form.validateFields(['requiredDate', 'notes']).then(() => setCurrentStep(2));
    }
  };

  const handleUpdateStatus = (id: number, status: SampleRequest['status']) => {
    setRequests((prev) =>
      prev.map((req) => (req.id === id ? { ...req, status } : req))
    );
    message.success(`Request status updated to ${status}`);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'orange';
      case 'approved':
        return 'blue';
      case 'rejected':
        return 'red';
      case 'shipped':
        return 'cyan';
      case 'received':
        return 'green';
      default:
        return 'default';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending':
        return <ClockCircleOutlined />;
      case 'approved':
        return <CheckCircleOutlined />;
      case 'rejected':
        return <CloseCircleOutlined />;
      case 'shipped':
        return <SendOutlined />;
      case 'received':
        return <CheckCircleOutlined />;
      default:
        return null;
    }
  };

  const columns: ColumnsType<SampleRequest> = [
    {
      title: 'Request ID',
      dataIndex: 'id',
      key: 'id',
      render: (id) => <Text code>SR-{id}</Text>,
    },
    {
      title: 'Product',
      dataIndex: 'productName',
      key: 'productName',
    },
    {
      title: 'Vendor',
      dataIndex: 'vendorName',
      key: 'vendorName',
    },
    {
      title: 'Quantity',
      dataIndex: 'quantity',
      key: 'quantity',
      align: 'center',
      render: (qty) => <Tag>{qty}</Tag>,
    },
    {
      title: 'Request Date',
      dataIndex: 'requestDate',
      key: 'requestDate',
      render: (date) => dayjs(date).format('MMM D, YYYY'),
    },
    {
      title: 'Required By',
      dataIndex: 'requiredDate',
      key: 'requiredDate',
      render: (date) => dayjs(date).format('MMM D, YYYY'),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status) => (
        <Tag color={getStatusColor(status)} icon={getStatusIcon(status)}>
          {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          {record.status === 'pending' && (
            <>
              <Button
                size="small"
                type="primary"
                onClick={() => handleUpdateStatus(record.id, 'approved')}
              >
                Approve
              </Button>
              <Button
                size="small"
                danger
                onClick={() => handleUpdateStatus(record.id, 'rejected')}
              >
                Reject
              </Button>
            </>
          )}
          {record.status === 'approved' && (
            <Button
              size="small"
              type="primary"
              onClick={() => handleUpdateStatus(record.id, 'shipped')}
            >
              Mark Shipped
            </Button>
          )}
          {record.status === 'shipped' && (
            <Button
              size="small"
              type="primary"
              onClick={() => handleUpdateStatus(record.id, 'received')}
            >
              Mark Received
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const uploadProps = {
    fileList,
    onChange: ({ fileList }: { fileList: UploadFile[] }) => setFileList(fileList),
    beforeUpload: () => false,
    multiple: true,
  };

  const stats = {
    total: requests.length,
    pending: requests.filter((r) => r.status === 'pending').length,
    approved: requests.filter((r) => r.status === 'approved').length,
    received: requests.filter((r) => r.status === 'received').length,
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <Title level={3}>
              <FileOutlined style={{ color: '#1890ff' }} /> Sample Requests
            </Title>
            <Paragraph type="secondary">
              Request product samples from vendors for evaluation
            </Paragraph>
          </div>
          <Button
            type="primary"
            size="large"
            icon={<PlusOutlined />}
            onClick={() => setIsModalVisible(true)}
          >
            New Sample Request
          </Button>
        </div>
      </div>

      {/* Statistics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={6}>
          <Card>
            <Statistic
              title="Total Requests"
              value={stats.total}
              prefix={<FileOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={6}>
          <Card>
            <Statistic
              title="Pending"
              value={stats.pending}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#ff9900' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={6}>
          <Card>
            <Statistic
              title="Approved"
              value={stats.approved}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={6}>
          <Card>
            <Statistic
              title="Received"
              value={stats.received}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Requests Table */}
      <Card>
        <Table
          columns={columns}
          dataSource={requests}
          rowKey="id"
          pagination={{ pageSize: 10 }}
          expandable={{
            expandedRowRender: (record) => (
              <div style={{ padding: 16 }}>
                <Space direction="vertical" style={{ width: '100%' }}>
                  <div>
                    <Text strong>Notes:</Text>
                    <Paragraph style={{ marginTop: 8 }}>{record.notes}</Paragraph>
                  </div>
                  {record.specifications && (
                    <div>
                      <Text strong>Specifications:</Text>
                      <Paragraph style={{ marginTop: 8 }}>{record.specifications}</Paragraph>
                    </div>
                  )}
                  {record.attachments && record.attachments.length > 0 && (
                    <div>
                      <Text strong>Attachments:</Text>
                      <div style={{ marginTop: 8 }}>
                        {record.attachments.map((file, idx) => (
                          <Tag key={idx} icon={<FileOutlined />}>
                            {file}
                          </Tag>
                        ))}
                      </div>
                    </div>
                  )}
                </Space>
              </div>
            ),
          }}
        />
      </Card>

      {/* Create Request Modal */}
      <Modal
        title="Create Sample Request"
        open={isModalVisible}
        onCancel={() => {
          setIsModalVisible(false);
          setCurrentStep(0);
          form.resetFields();
          setFileList([]);
        }}
        footer={null}
        width={700}
      >
        <Steps
          current={currentStep}
          items={[
            { title: 'Product Details' },
            { title: 'Request Info' },
            { title: 'Attachments' },
          ]}
          style={{ marginBottom: 24 }}
        />

        <Form form={form} layout="vertical">
          {/* Step 1: Product Details */}
          {currentStep === 0 && (
            <div>
              <Form.Item
                name="productName"
                label="Product Name"
                rules={[{ required: true, message: 'Please enter product name' }]}
              >
                <Input placeholder="e.g., Wireless Headphones Pro" />
              </Form.Item>
              <Form.Item
                name="vendorName"
                label="Vendor"
                rules={[{ required: true, message: 'Please select vendor' }]}
              >
                <Select
                  placeholder="Select vendor"
                  options={[
                    { label: 'TechHub Electronics', value: 'TechHub Electronics' },
                    { label: 'Fashion Boutique', value: 'Fashion Boutique' },
                    { label: 'Home Essentials', value: 'Home Essentials' },
                  ]}
                />
              </Form.Item>
              <Form.Item
                name="quantity"
                label="Quantity"
                rules={[{ required: true, message: 'Please enter quantity' }]}
                initialValue={1}
              >
                <InputNumber min={1} max={10} style={{ width: '100%' }} />
              </Form.Item>
              <Form.Item name="specifications" label="Specifications">
                <TextArea rows={3} placeholder="Any specific requirements or specifications" />
              </Form.Item>
            </div>
          )}

          {/* Step 2: Request Info */}
          {currentStep === 1 && (
            <div>
              <Form.Item
                name="requiredDate"
                label="Required By Date"
                rules={[{ required: true, message: 'Please select required date' }]}
              >
                <DatePicker style={{ width: '100%' }} disabledDate={(d) => d.isBefore(dayjs())} />
              </Form.Item>
              <Form.Item
                name="notes"
                label="Request Notes"
                rules={[{ required: true, message: 'Please enter notes' }]}
              >
                <TextArea
                  rows={4}
                  placeholder="Explain the purpose of this sample request..."
                />
              </Form.Item>
              <Alert
                message="Sample Request Guidelines"
                description="Please provide detailed information about why you need these samples. This helps vendors prioritize and process your request faster."
                type="info"
                showIcon
              />
            </div>
          )}

          {/* Step 3: Attachments */}
          {currentStep === 2 && (
            <div>
              <Form.Item label="Attachments (Optional)">
                <Dragger {...uploadProps}>
                  <p className="ant-upload-drag-icon">
                    <InboxOutlined />
                  </p>
                  <p className="ant-upload-text">Click or drag files to upload</p>
                  <p className="ant-upload-hint">
                    Upload technical specifications, drawings, or reference images
                  </p>
                </Dragger>
              </Form.Item>
              <Alert
                message="Ready to Submit"
                description="Review your request details and click Submit to send it to the vendor."
                type="success"
                showIcon
              />
            </div>
          )}

          <Divider />

          {/* Navigation Buttons */}
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <Button onClick={() => setCurrentStep(Math.max(0, currentStep - 1))} disabled={currentStep === 0}>
              Previous
            </Button>
            <Space>
              <Button onClick={() => setIsModalVisible(false)}>Cancel</Button>
              {currentStep < 2 ? (
                <Button type="primary" onClick={handleNextStep}>
                  Next
                </Button>
              ) : (
                <Button type="primary" onClick={handleCreateRequest}>
                  Submit Request
                </Button>
              )}
            </Space>
          </div>
        </Form>
      </Modal>
    </div>
  );
};

// Add Statistic import
import { Statistic } from 'antd';

export default SampleRequestsPage;

customer/saved-addresses.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Modal,
  Form,
  Input,
  Select,
  Switch,
  message,
  Tag,
  Radio,
  Divider,
} from 'antd';
import {
  HomeOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  CheckCircleOutlined,
  EnvironmentOutlined,
  PhoneOutlined,
  UserOutlined,
} from '@ant-design/icons';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;

interface Address {
  id: number;
  label: string;
  name: string;
  phone: string;
  addressLine1: string;
  addressLine2?: string;
  city: string;
  state: string;
  zip: string;
  country: string;
  isDefault: boolean;
  isBilling: boolean;
  isShipping: boolean;
  createdAt: string;
}

const mockAddresses: Address[] = [
  {
    id: 1,
    label: 'Home',
    name: 'John Doe',
    phone: '(555) 123-4567',
    addressLine1: '123 Main Street',
    addressLine2: 'Apt 4B',
    city: 'New York',
    state: 'NY',
    zip: '10001',
    country: 'United States',
    isDefault: true,
    isBilling: true,
    isShipping: true,
    createdAt: dayjs().subtract(180, 'days').format('YYYY-MM-DD'),
  },
  {
    id: 2,
    label: 'Work',
    name: 'John Doe',
    phone: '(555) 987-6543',
    addressLine1: '456 Business Avenue',
    addressLine2: 'Suite 200',
    city: 'New York',
    state: 'NY',
    zip: '10022',
    country: 'United States',
    isDefault: false,
    isBilling: false,
    isShipping: true,
    createdAt: dayjs().subtract(90, 'days').format('YYYY-MM-DD'),
  },
  {
    id: 3,
    label: "Parent's House",
    name: 'Jane Doe',
    phone: '(555) 456-7890',
    addressLine1: '789 Elm Street',
    city: 'Brooklyn',
    state: 'NY',
    zip: '11201',
    country: 'United States',
    isDefault: false,
    isBilling: false,
    isShipping: true,
    createdAt: dayjs().subtract(30, 'days').format('YYYY-MM-DD'),
  },
];

const SavedAddressesPage: React.FC = () => {
  const [addresses, setAddresses] = useState<Address[]>(mockAddresses);
  const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
  const [editingAddress, setEditingAddress] = useState<Address | null>(null);
  const [form] = Form.useForm();

  const handleAddNew = () => {
    setEditingAddress(null);
    form.resetFields();
    setIsModalVisible(true);
  };

  const handleEdit = (address: Address) => {
    setEditingAddress(address);
    form.setFieldsValue(address);
    setIsModalVisible(true);
  };

  const handleDelete = (id: number) => {
    const address = addresses.find((a) => a.id === id);
    if (address?.isDefault) {
      message.warning('Cannot delete default address. Please set another address as default first.');
      return;
    }

    Modal.confirm({
      title: 'Delete Address',
      content: 'Are you sure you want to delete this address?',
      okText: 'Delete',
      okType: 'danger',
      onOk: () => {
        setAddresses(addresses.filter((a) => a.id !== id));
        message.success('Address deleted successfully');
      },
    });
  };

  const handleSetDefault = (id: number) => {
    setAddresses(
      addresses.map((a) => ({
        ...a,
        isDefault: a.id === id,
      }))
    );
    message.success('Default address updated');
  };

  const handleSubmit = (values: any) => {
    if (editingAddress) {
      // Update existing address
      setAddresses(
        addresses.map((a) =>
          a.id === editingAddress.id
            ? {
                ...a,
                ...values,
                isDefault: values.isDefault ? true : a.isDefault,
              }
            : {
                ...a,
                isDefault: values.isDefault ? false : a.isDefault,
              }
        )
      );
      message.success('Address updated successfully');
    } else {
      // Add new address
      const newAddress: Address = {
        id: Math.max(...addresses.map((a) => a.id)) + 1,
        ...values,
        createdAt: dayjs().format('YYYY-MM-DD'),
      };

      if (values.isDefault) {
        setAddresses([
          newAddress,
          ...addresses.map((a) => ({ ...a, isDefault: false })),
        ]);
      } else {
        setAddresses([newAddress, ...addresses]);
      }
      message.success('Address added successfully');
    }

    setIsModalVisible(false);
    form.resetFields();
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <EnvironmentOutlined style={{ color: '#1890ff' }} /> Saved Addresses
          </Title>
          <Paragraph type="secondary">
            Manage your shipping and billing addresses
          </Paragraph>
        </Col>
        <Col>
          <Button type="primary" icon={<PlusOutlined />} onClick={handleAddNew}>
            Add New Address
          </Button>
        </Col>
      </Row>

      <Row gutter={[16, 16]}>
        {addresses.map((address) => (
          <Col xs={24} md={12} lg={8} key={address.id}>
            <Card
              hoverable
              style={{
                border: address.isDefault ? '2px solid #1890ff' : '1px solid #d9d9d9',
                position: 'relative',
              }}
            >
              {address.isDefault && (
                <div
                  style={{
                    position: 'absolute',
                    top: 0,
                    right: 0,
                    background: '#1890ff',
                    color: 'white',
                    padding: '4px 12px',
                    borderBottomLeftRadius: 8,
                    fontSize: 12,
                  }}
                >
                  <CheckCircleOutlined style={{ marginRight: 4 }} />
                  Default
                </div>
              )}

              <div style={{ marginBottom: 16 }}>
                <Space>
                  <Tag color="blue" icon={<HomeOutlined />}>
                    {address.label}
                  </Tag>
                  {address.isShipping && <Tag color="green">Shipping</Tag>}
                  {address.isBilling && <Tag color="purple">Billing</Tag>}
                </Space>
              </div>

              <div style={{ marginBottom: 16 }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 8 }}>
                  <UserOutlined style={{ color: '#1890ff' }} />
                  <Text strong>{address.name}</Text>
                </div>

                <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 8 }}>
                  <EnvironmentOutlined style={{ color: '#52c41a' }} />
                  <div>
                    <div>{address.addressLine1}</div>
                    {address.addressLine2 && <div>{address.addressLine2}</div>}
                    <div>
                      {address.city}, {address.state} {address.zip}
                    </div>
                    <div>{address.country}</div>
                  </div>
                </div>

                <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                  <PhoneOutlined style={{ color: '#722ed1' }} />
                  <Text>{address.phone}</Text>
                </div>
              </div>

              <Divider style={{ margin: '12px 0' }} />

              <Space>
                {!address.isDefault && (
                  <Button
                    type="link"
                    size="small"
                    icon={<CheckCircleOutlined />}
                    onClick={() => handleSetDefault(address.id)}
                  >
                    Set as Default
                  </Button>
                )}
                <Button
                  type="link"
                  size="small"
                  icon={<EditOutlined />}
                  onClick={() => handleEdit(address)}
                >
                  Edit
                </Button>
                {!address.isDefault && (
                  <Button
                    type="link"
                    danger
                    size="small"
                    icon={<DeleteOutlined />}
                    onClick={() => handleDelete(address.id)}
                  >
                    Delete
                  </Button>
                )}
              </Space>
            </Card>
          </Col>
        ))}

        {addresses.length === 0 && (
          <Col span={24}>
            <Card>
              <div style={{ textAlign: 'center', padding: '40px 0' }}>
                <EnvironmentOutlined style={{ fontSize: 64, color: '#d9d9d9', marginBottom: 16 }} />
                <Title level={4}>No Saved Addresses</Title>
                <Paragraph type="secondary">
                  Add your first address to make checkout faster
                </Paragraph>
                <Button type="primary" icon={<PlusOutlined />} onClick={handleAddNew}>
                  Add Address
                </Button>
              </div>
            </Card>
          </Col>
        )}
      </Row>

      <Modal
        title={editingAddress ? 'Edit Address' : 'Add New Address'}
        open={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        footer={null}
        width={700}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item
            label="Address Label"
            name="label"
            rules={[{ required: true, message: 'Please enter address label' }]}
          >
            <Radio.Group buttonStyle="solid">
              <Radio.Button value="Home">
                <HomeOutlined /> Home
              </Radio.Button>
              <Radio.Button value="Work">Work</Radio.Button>
              <Radio.Button value="Other">Other</Radio.Button>
            </Radio.Group>
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Full Name"
                name="name"
                rules={[{ required: true, message: 'Please enter full name' }]}
              >
                <Input size="large" placeholder="John Doe" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Phone Number"
                name="phone"
                rules={[{ required: true, message: 'Please enter phone number' }]}
              >
                <Input size="large" placeholder="(555) 123-4567" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            label="Address Line 1"
            name="addressLine1"
            rules={[{ required: true, message: 'Please enter address' }]}
          >
            <Input size="large" placeholder="Street address, P.O. box" />
          </Form.Item>

          <Form.Item label="Address Line 2 (Optional)" name="addressLine2">
            <Input size="large" placeholder="Apartment, suite, unit, building, floor, etc." />
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="City"
                name="city"
                rules={[{ required: true, message: 'Please enter city' }]}
              >
                <Input size="large" placeholder="New York" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="State / Province"
                name="state"
                rules={[{ required: true, message: 'Please select state' }]}
              >
                <Select size="large" placeholder="Select state" showSearch>
                  <Option value="AL">Alabama</Option>
                  <Option value="AK">Alaska</Option>
                  <Option value="AZ">Arizona</Option>
                  <Option value="CA">California</Option>
                  <Option value="FL">Florida</Option>
                  <Option value="NY">New York</Option>
                  <Option value="TX">Texas</Option>
                  {/* Add more states as needed */}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="ZIP / Postal Code"
                name="zip"
                rules={[{ required: true, message: 'Please enter ZIP code' }]}
              >
                <Input size="large" placeholder="10001" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Country"
                name="country"
                rules={[{ required: true, message: 'Please select country' }]}
                initialValue="United States"
              >
                <Select size="large">
                  <Option value="United States">United States</Option>
                  <Option value="Canada">Canada</Option>
                  <Option value="United Kingdom">United Kingdom</Option>
                  <Option value="Australia">Australia</Option>
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Divider />

          <Form.Item label="Address Type" style={{ marginBottom: 8 }}>
            <Space direction="vertical">
              <Form.Item name="isShipping" valuePropName="checked" noStyle initialValue={true}>
                <Switch />
                <Text style={{ marginLeft: 8 }}>Use as shipping address</Text>
              </Form.Item>
              <Form.Item name="isBilling" valuePropName="checked" noStyle initialValue={false}>
                <Switch />
                <Text style={{ marginLeft: 8 }}>Use as billing address</Text>
              </Form.Item>
            </Space>
          </Form.Item>

          <Form.Item name="isDefault" valuePropName="checked" initialValue={false}>
            <Switch />
            <Text style={{ marginLeft: 8 }}>Set as default address</Text>
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" size="large" icon={<CheckCircleOutlined />}>
                {editingAddress ? 'Update Address' : 'Save Address'}
              </Button>
              <Button size="large" onClick={() => setIsModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default SavedAddressesPage;

customer/video-tutorials.__content:
import React, { useState } from 'react';
