import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Collapse,
  Space,
  Divider,
  Anchor,
  Timeline,
  Tag,
  Button,
  Input,
} from 'antd';
import {
  QuestionCircleOutlined,
  ShoppingCartOutlined,
  TruckOutlined,
  SafetyOutlined,
  DollarOutlined,
  GiftOutlined,
  StarOutlined,
  SearchOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;
const { Panel } = Collapse;
const { Search } = Input;

interface BuyingTip {
  category: string;
  tips: {
    title: string;
    content: string;
