        open={isEditModalVisible}
        onCancel={() => {
          setIsEditModalVisible(false);
          setSelectedSearch(null);
          form.resetFields();
        }}
        footer={null}
        width={700}
      >
        <Form form={form} layout="vertical" onFinish={handleUpdateSearch}>
