                          description={
                            <Space direction="vertical" style={{ width: '100%' }} size="small">
                              <Rate disabled value={product.rating} style={{ fontSize: 12 }} />
                              <Text strong style={{ color: '#ff9900' }}>${product.price.toFixed(2)}</Text>
