                  dataSource={mockReviews}
                  renderItem={review => (
                    <List.Item>
                      <List.Item.Meta
                        avatar={<Avatar icon={<UserOutlined />} />}
                        title={
                          <Space>
                            <Text strong>{review.customerName}</Text>
                            {review.verified && (
                              <Tag color="success" icon={<CheckCircleFilled />}>
                                Verified Purchase
