import {
  Card,
  Row,
  Col,
  Statistic,
  Select,
  DatePicker,
  Button,
  Space,
  Typography,
  Table,
  Tag,
  Progress,
} from 'antd';
import {
  ShoppingOutlined,
  EyeOutlined,
  StarOutlined,
  DownloadOutlined,
  RiseOutlined,
  FallOutlined,
  DollarOutlined,
} from '@ant-design/icons';
import { Column, Pie } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;

interface ProductPerformance {
  id: number;
  name: string;
  category: string;
  sales: number;
  revenue: number;
  views: number;
  conversionRate: number;
  rating: number;
  stock: number;
  trend: 'up' | 'down';
  trendValue: number;
}

const mockProducts: ProductPerformance[] = [
  {
    id: 1,
    name: 'Wireless Headphones',
