          form={form}
          layout="vertical"
          onFinish={handleSubmit}
          initialValues={initialValues}
        >
          <Row gutter={16}>
            <Col xs={24} md={12}>
              <Form.Item
                name="facebook"
                label={
                  <Space>
                    <FacebookOutlined style={{ color: '#1877F2' }} />
                    <Text>Facebook</Text>
                  </Space>
                }
                rules={[
                  {
                    type: 'url',
                    message: 'Please enter a valid URL',
                  },
                ]}
              >
                <Input placeholder="https://facebook.com/your-page" />
              </Form.Item>
            </Col>

            <Col xs={24} md={12}>
              <Form.Item
                name="twitter"
                label={
                  <Space>
                    <TwitterOutlined style={{ color: '#1DA1F2' }} />
                    <Text>Twitter / X</Text>
                  </Space>
                }
                rules={[
                  {
                    type: 'url',
                    message: 'Please enter a valid URL',
                  },
                ]}
              >
                <Input placeholder="https://twitter.com/your-handle" />
              </Form.Item>
            </Col>

            <Col xs={24} md={12}>
              <Form.Item
                name="instagram"
                label={
                  <Space>
                    <InstagramOutlined style={{ color: '#E4405F' }} />
                    <Text>Instagram</Text>
                  </Space>
                }
                rules={[
                  {
                    type: 'url',
                    message: 'Please enter a valid URL',
                  },
