              {templates.length === 0 && (
                <Col span={24}>
                  <Empty description="No bundle templates found">
                    <Button type="primary" icon={<PlusOutlined />}>
                      Create Template
                    </Button>
                  </Empty>
                </Col>
              )}
            </Row>
          </TabPane>

          <TabPane
            tab={
              <span>
                <BarChartOutlined />
                Analytics
              </span>
            }
            key="analytics"
          >
            <Row gutter={[16, 16]}>
              <Col xs={24} lg={16}>
                <Card title="Bundle Revenue Performance">
                  <Column
                    data={revenueChartData}
                    xField="name"
                    yField="revenue"
                    label={{
                      position: 'top',
                      style: {
                        fill: '#000000',
                        opacity: 0.6,
                      },
                    }}
                    meta={{
                      revenue: {
                        formatter: (v: number) => `$${(v / 1000).toFixed(1)}k`,
                      },
                    }}
                    height={300}
                  />
                </Card>
              </Col>
              <Col xs={24} lg={8}>
                <Card title="Bundle Type Distribution">
                  <Pie
                    data={typeDistribution}
                    angleField="value"
                    colorField="type"
                    radius={0.8}
                    label={{
                      type: 'outer',
                      content: '{name} {percentage}',
                    }}
                    interactions={[{ type: 'element-active' }]}
                    height={300}
                  />
                </Card>
              </Col>
            </Row>
          </TabPane>
        </Tabs>

        {/* Create Bundle Modal */}
        <Modal
          title={
            <div>
              <GiftOutlined style={{ marginRight: 8 }} />
              Create Product Bundle
