    quantity: 1,
    image: 'https://via.placeholder.com/80x80?text=Watch',
  },
];

const GiftWrappingPage: React.FC = () => {
  const [selectedWrap, setSelectedWrap] = useState<number | null>(null);
  const [selectedItems, setSelectedItems] = useState<number[]>([]);
  const [giftMessage, setGiftMessage] = useState<string>('');
  const [recipientName, setRecipientName] = useState<string>('');
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [form] = Form.useForm();

  const handleAddToCart = () => {
    if (!selectedWrap) {
      message.warning('Please select a gift wrap');
      return;
    }

    if (selectedItems.length === 0) {
      message.warning('Please select at least one item to wrap');
      return;
    }

    const wrap = mockGiftWraps.find((w) => w.id === selectedWrap);
    const totalPrice = wrap!.price * selectedItems.length;

    message.success({
      content: `Added gift wrapping for ${selectedItems.length} item(s) - $${totalPrice.toFixed(
        2
      )}`,
      duration: 3,
    });
  };

  const filteredWraps =
    filterCategory === 'all'
      ? mockGiftWraps
      : mockGiftWraps.filter((wrap) => wrap.category === filterCategory);

  const selectedWrapData = mockGiftWraps.find((w) => w.id === selectedWrap);
  const totalPrice = selectedWrapData ? selectedWrapData.price * selectedItems.length : 0;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <GiftOutlined style={{ color: '#1890ff' }} /> Gift Wrapping
          </Title>
          <Paragraph type="secondary">Make your gifts extra special with our wrapping service</Paragraph>
        </Col>
      </Row>

      <Alert
        message="Add a Personal Touch"
        description="Choose from our beautiful gift wrapping options and add a personalized message to make your gift memorable."
        type="info"
        showIcon
        icon={<GiftOutlined />}
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={16}>
          <Card title="Select Items to Wrap" style={{ marginBottom: 24 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {mockCartItems.map((item) => (
                <Card key={item.id} size="small">
                  <Row align="middle" gutter={16}>
                    <Col>
                      <Checkbox
                        checked={selectedItems.includes(item.id)}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setSelectedItems([...selectedItems, item.id]);
                          } else {
                            setSelectedItems(selectedItems.filter((id) => id !== item.id));
                          }
                        }}
                      />
                    </Col>
                    <Col>
                      <Image
                        src={item.image}
                        alt={item.name}
                        width={80}
                        height={80}
                        style={{ borderRadius: 8 }}
                      />
                    </Col>
                    <Col flex="auto">
                      <div>
                        <Text strong style={{ fontSize: 16 }}>
                          {item.name}
                        </Text>
                        <div>
                          <Text type="secondary">Quantity: {item.quantity}</Text>
                        </div>
                        <div>
                          <Text strong style={{ fontSize: 18, color: '#1890ff' }}>
                            ${item.price.toFixed(2)}
                          </Text>
                        </div>
                      </div>
                    </Col>
                    <Col>
                      {selectedItems.includes(item.id) && (
                        <Tag color="success" icon={<CheckCircleOutlined />}>
                          Selected
                        </Tag>
                      )}
                    </Col>
                  </Row>
                </Card>
              ))}
            </Space>

            {selectedItems.length > 0 && (
              <Alert
                message={`${selectedItems.length} item(s) selected for gift wrapping`}
                type="success"
                showIcon
                style={{ marginTop: 16 }}
              />
            )}
          </Card>

          <Card
            title="Choose Gift Wrap"
            extra={
              <Radio.Group value={filterCategory} onChange={(e) => setFilterCategory(e.target.value)}>
                <Radio.Button value="all">All</Radio.Button>
                <Radio.Button value="paper">Paper</Radio.Button>
                <Radio.Button value="box">Box</Radio.Button>
                <Radio.Button value="bag">Bag</Radio.Button>
                <Radio.Button value="premium">Premium</Radio.Button>
              </Radio.Group>
            }
          >
            <Row gutter={[16, 16]}>
              {filteredWraps.map((wrap) => (
                <Col xs={24} sm={12} lg={8} key={wrap.id}>
                  <Card
                    hoverable
                    onClick={() => setSelectedWrap(wrap.id)}
                    style={{
                      border:
                        selectedWrap === wrap.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                      cursor: 'pointer',
                    }}
                  >
                    <div style={{ position: 'relative' }}>
                      <Image
                        src={wrap.image}
                        alt={wrap.name}
                        style={{ borderRadius: 8, marginBottom: 12 }}
                        preview={false}
                      />
                      {wrap.popular && (
                        <Tag
                          color="gold"
                          icon={<StarOutlined />}
                          style={{ position: 'absolute', top: 8, right: 8 }}
                        >
                          Popular
                        </Tag>
                      )}
                      {selectedWrap === wrap.id && (
                        <div
                          style={{
                            position: 'absolute',
                            top: 8,
                            left: 8,
                            background: '#52c41a',
                            borderRadius: '50%',
                            width: 32,
                            height: 32,
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                          }}
                        >
                          <CheckCircleOutlined style={{ color: '#fff', fontSize: 18 }} />
                        </div>
                      )}
                    </div>

                    <Title level={5} style={{ marginBottom: 8 }}>
                      {wrap.name}
                    </Title>
                    <Paragraph type="secondary" ellipsis={{ rows: 2 }} style={{ marginBottom: 12 }}>
                      {wrap.description}
                    </Paragraph>

                    <div style={{ marginBottom: 12 }}>
                      {wrap.occasion.slice(0, 2).map((occ) => (
                        <Tag key={occ} color="blue" style={{ marginBottom: 4 }}>
                          {occ}
                        </Tag>
                      ))}
                    </div>

                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Text strong style={{ fontSize: 20, color: '#1890ff' }}>
                        ${wrap.price.toFixed(2)}
                      </Text>
                      <Text type="secondary" style={{ fontSize: 12 }}>
                        per item
                      </Text>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>

          <Card title="Gift Message (Optional)" style={{ marginTop: 24 }}>
            <Form form={form} layout="vertical">
              <Form.Item label="Recipient Name" name="recipientName">
                <Input
                  size="large"
                  placeholder="To: John Doe"
                  value={recipientName}
                  onChange={(e) => setRecipientName(e.target.value)}
                  prefix={<HeartOutlined />}
                />
              </Form.Item>

              <Form.Item
                label="Personal Message"
                name="message"
                help="Maximum 200 characters"
              >
                <TextArea
                  rows={4}
                  placeholder="Wishing you all the best on your special day!"
                  maxLength={200}
                  showCount
                  value={giftMessage}
                  onChange={(e) => setGiftMessage(e.target.value)}
                />
              </Form.Item>

              <Alert
                message="Message Preview"
                description={
                  <div style={{ marginTop: 12 }}>
                    {recipientName && (
                      <div>
                        <Text strong>To: {recipientName}</Text>
                      </div>
                    )}
                    {giftMessage && (
                      <div style={{ marginTop: 8 }}>
                        <Text italic>"{giftMessage}"</Text>
                      </div>
                    )}
                    {!recipientName && !giftMessage && (
                      <Text type="secondary">Your message will appear here</Text>
                    )}
                  </div>
                }
                type="info"
                showIcon={false}
              />
            </Form>
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card
            title={
              <Space>
                <ShoppingCartOutlined />
                <span>Order Summary</span>
              </Space>
            }
            style={{ position: 'sticky', top: 24 }}
          >
            {selectedWrapData && (
              <div>
                <div
                  style={{
                    background: '#f0f0f0',
                    padding: 16,
                    borderRadius: 8,
                    marginBottom: 16,
                  }}
                >
                  <Image
                    src={selectedWrapData.image}
                    alt={selectedWrapData.name}
                    style={{ borderRadius: 8, marginBottom: 12 }}
                    preview={false}
                  />
                  <Title level={5}>{selectedWrapData.name}</Title>
                  <Text type="secondary">{selectedWrapData.description}</Text>
                </div>
              </div>
            )}

            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text>Gift Wrap Price:</Text>
                <Text strong>
                  {selectedWrapData ? `$${selectedWrapData.price.toFixed(2)}` : '$0.00'}
                </Text>
              </div>

              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text>Number of Items:</Text>
                <Text strong>{selectedItems.length}</Text>
              </div>

              {giftMessage && (
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Text>Gift Message:</Text>
                  <Tag color="green">Included Free</Tag>
                </div>
              )}

              <Divider style={{ margin: '12px 0' }} />

              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Title level={5} style={{ margin: 0 }}>
                  Total:
                </Title>
                <Title level={4} style={{ margin: 0, color: '#1890ff' }}>
                  ${totalPrice.toFixed(2)}
                </Title>
              </div>

              <Button
                type="primary"
                size="large"
                icon={<PlusOutlined />}
                onClick={handleAddToCart}
                block
                disabled={!selectedWrap || selectedItems.length === 0}
              >
                Add to Cart
              </Button>

              <Alert
                message="Free gift message card with every order"
                type="success"
                showIcon
                icon={<GiftOutlined />}
              />
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default GiftWrappingPage;

customer/notifications.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  List,
  Badge,
  Button,
  Space,
  Typography,
  Avatar,
  Tag,
  Tabs,
  Dropdown,
  Popconfirm,
  Empty,
  Divider,
  Switch,
  Form,
  Select,
  Checkbox,
  Row,
  Col,
  Statistic,
  Spin,
  message,
} from 'antd';
import {
  BellOutlined,
  ShoppingOutlined,
  StarOutlined,
  GiftOutlined,
  TruckOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  MoreOutlined,
  SettingOutlined,
  DeleteOutlined,
  EyeOutlined,
  MailOutlined,
  MobileOutlined,
  NotificationOutlined,
  DollarOutlined,
  FileTextOutlined,
} from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { notificationsAPI, type Notification, type NotificationPreferences } from '@/services/api/notifications';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';

dayjs.extend(relativeTime);

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;
const { Option } = Select;

// Helper function to get icon and color for notification type
const getNotificationStyle = (type: Notification['type']) => {
  const styles = {
    order: { icon: <ShoppingOutlined />, color: '#1890ff' },
    payment: { icon: <DollarOutlined />, color: '#52c41a' },
    product: { icon: <GiftOutlined />, color: '#722ed1' },
    rfq: { icon: <FileTextOutlined />, color: '#13c2c2' },
    review: { icon: <StarOutlined />, color: '#faad14' },
    system: { icon: <NotificationOutlined />, color: '#ff9900' },
    promotion: { icon: <GiftOutlined />, color: '#ff4d4f' },
  };
  return styles[type] || styles.system;
};

const mockNotifications: Notification[] = [];

const CustomerNotificationsPage: React.FC = () => {
  const [notifications, setNotifications] = useState<Notification[]>(mockNotifications);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('all');
  const [preferences, setPreferences] = useState<NotificationPreferences | null>(null);
  const [unreadCount, setUnreadCount] = useState(0);

  // Fetch notifications
  const fetchNotifications = async (filters?: { type?: string; isRead?: boolean }) => {
    try {
      setLoading(true);
      const data = await notificationsAPI.getAll(filters);
      setNotifications(data);

      // Update unread count
      const count = await notificationsAPI.getUnreadCount();
      setUnreadCount(count);
    } catch (error) {
      message.error('Failed to load notifications');
      console.error('Fetch notifications error:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch preferences
  const fetchPreferences = async () => {
    try {
      const prefs = await notificationsAPI.getPreferences();
      setPreferences(prefs);
    } catch (error) {
      console.error('Fetch preferences error:', error);
    }
  };

  // Load data on mount
  useEffect(() => {
    fetchNotifications();
    fetchPreferences();
  }, []);

  const markAsRead = async (id: string) => {
    try {
      await notificationsAPI.markAsRead(id);
      setNotifications(notifications.map(n => 
        n.id === id ? { ...n, isRead: true } : n
      ));
      setUnreadCount(prev => Math.max(0, prev - 1));
      message.success('Marked as read');
    } catch (error) {
      message.error('Failed to mark as read');
      console.error('Mark as read error:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      await notificationsAPI.markAllAsRead();
      setNotifications(notifications.map(n => ({ ...n, isRead: true })));
      setUnreadCount(0);
      message.success('All notifications marked as read');
    } catch (error) {
      message.error('Failed to mark all as read');
      console.error('Mark all as read error:', error);
    }
  };

  const deleteNotification = async (id: string) => {
    try {
      await notificationsAPI.delete(id);
      setNotifications(notifications.filter(n => n.id !== id));
      message.success('Notification deleted');
    } catch (error) {
      message.error('Failed to delete notification');
      console.error('Delete notification error:', error);
    }
  };

  const clearAllNotifications = async () => {
    try {
      await notificationsAPI.clearAll();
      setNotifications([]);
      setUnreadCount(0);
      message.success('All notifications cleared');
    } catch (error) {
      message.error('Failed to clear notifications');
      console.error('Clear all error:', error);
    }
  };

  const savePreferences = async (newPrefs: Partial<NotificationPreferences>) => {
    try {
      const updated = await notificationsAPI.updatePreferences(newPrefs);
      setPreferences(updated);
      message.success('Preferences saved successfully');
    } catch (error) {
      message.error('Failed to save preferences');
      console.error('Save preferences error:', error);
    }
  };

  const getMenuItems = (notification: Notification): MenuProps['items'] => [
    {
      key: 'read',
      label: notification.isRead ? 'Mark as unread' : 'Mark as read',
      icon: notification.isRead ? <CloseCircleOutlined /> : <CheckCircleOutlined />,
      onClick: () => markAsRead(notification.id),
    },
    {
      key: 'view',
      label: 'View details',
      icon: <EyeOutlined />,
      disabled: !notification.link,
    },
    {
      type: 'divider',
    },
    {
      key: 'delete',
      label: 'Delete',
      icon: <DeleteOutlined />,
      danger: true,
      onClick: () => deleteNotification(notification.id),
    },
  ];

  const filteredNotifications = notifications.filter(n => {
    if (activeTab === 'all') return true;
    if (activeTab === 'unread') return !n.isRead;
    return n.type === activeTab;
  });

  const notificationsByType = {
    order: notifications.filter(n => n.type === 'order').length,
    promotion: notifications.filter(n => n.type === 'promotion').length,
    payment: notifications.filter(n => n.type === 'payment').length,
    review: notifications.filter(n => n.type === 'review').length,
    product: notifications.filter(n => n.type === 'product').length,
    rfq: notifications.filter(n => n.type === 'rfq').length,
    system: notifications.filter(n => n.type === 'system').length,
  };

  return (
    <div style={{ padding: 24 }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <BellOutlined /> Notifications
          {unreadCount > 0 && (
            <Badge count={unreadCount} style={{ marginLeft: 12 }} />
          )}
        </Title>
      </div>

      <Row gutter={16}>
        {/* Main Notifications */}
        <Col xs={24} lg={16}>
          <Card
            title={
              <Space>
                <Text>Notification Center</Text>
                {unreadCount > 0 && (
                  <Tag color="red">{unreadCount} unread</Tag>
                )}
              </Space>
            }
            extra={
              <Space>
                <Button
                  type="link"
                  onClick={markAllAsRead}
                  disabled={unreadCount === 0}
                >
                  Mark all as read
                </Button>
                <Popconfirm
                  title="Clear all notifications?"
                  description="This action cannot be undone."
                  onConfirm={clearAllNotifications}
                  okText="Yes"
                  cancelText="No"
                >
                  <Button danger type="text" icon={<DeleteOutlined />}>
                    Clear all
                  </Button>
                </Popconfirm>
              </Space>
            }
          >
            <Tabs activeKey={activeTab} onChange={setActiveTab}>
              <TabPane
                tab={
                  <Badge count={notifications.length} showZero>
                    <span>All</span>
                  </Badge>
                }
                key="all"
              />
              <TabPane
                tab={
                  <Badge count={unreadCount}>
                    <span>Unread</span>
                  </Badge>
                }
                key="unread"
              />
              <TabPane
                tab={
                  <Badge count={notificationsByType.order} showZero>
                    <span>Orders</span>
                  </Badge>
                }
                key="order"
              />
              <TabPane
                tab={
                  <Badge count={notificationsByType.promotion} showZero>
                    <span>Promotions</span>
                  </Badge>
                }
                key="promotion"
              />
              <TabPane
                tab={
                  <Badge count={notificationsByType.payment} showZero>
                    <span>Payments</span>
                  </Badge>
                }
                key="payment"
              />
            </Tabs>

            {filteredNotifications.length === 0 ? (
              <Empty description="No notifications" />
            ) : (
              <Spin spinning={loading}>
                <List
                  itemLayout="horizontal"
                  dataSource={filteredNotifications}
                  renderItem={(notification) => {
                    const style = getNotificationStyle(notification.type);
                    return (
                      <List.Item
                        style={{
                          background: notification.isRead ? 'transparent' : '#e6f7ff',
                          padding: 16,
                          borderRadius: 8,
                          marginBottom: 8,
                        }}
                        actions={[
                          <Dropdown
                            menu={{ items: getMenuItems(notification) }}
                            trigger={['click']}
                          >
                            <Button type="text" icon={<MoreOutlined />} />
                          </Dropdown>,
                        ]}
                      >
                        <List.Item.Meta
                          avatar={
                            <Badge dot={!notification.isRead}>
                              <Avatar
                                icon={style.icon}
                                style={{ backgroundColor: style.color }}
                              />
                            </Badge>
                          }
                          title={
                            <Space>
                              <Text strong={!notification.isRead}>{notification.title}</Text>
                              {!notification.isRead && <Tag color="blue">New</Tag>}
                            </Space>
                          }
                          description={
                            <Space direction="vertical" size="small">
                              <Text>{notification.message}</Text>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {dayjs(notification.createdAt).fromNow()}
                              </Text>
                            </Space>
                          }
                        />
                      </List.Item>
                    );
                  }}
                />
              </Spin>
            )}
          </Card>
        </Col>

        {/* Notification Settings */}
        <Col xs={24} lg={8}>
          <Card title={<><SettingOutlined /> Notification Settings</>}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {/* Statistics */}
              <div>
                <Title level={5}>Overview</Title>
                <Row gutter={16}>
                  <Col span={12}>
                    <Statistic
                      title="Total"
                      value={notifications.length}
                      prefix={<BellOutlined />}
                      valueStyle={{ fontSize: 20 }}
                    />
                  </Col>
                  <Col span={12}>
                    <Statistic
                      title="Unread"
                      value={unreadCount}
                      valueStyle={{ fontSize: 20, color: '#ff4d4f' }}
                    />
                  </Col>
                </Row>
              </div>

              <Divider />

              {/* Channels */}
              <div>
                <Title level={5}>Notification Channels</Title>
                <Space direction="vertical" style={{ width: '100%' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Space>
                      <MailOutlined style={{ color: '#1890ff' }} />
                      <Text>Email Notifications</Text>
                    </Space>
                    <Switch
                      checked={preferences?.email?.orders ?? true}
                      onChange={(checked) => {
                        if (preferences) {
                          savePreferences({
                            email: { ...preferences.email, orders: checked }
                          });
                        }
                      }}
                    />
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Space>
                      <NotificationOutlined style={{ color: '#52c41a' }} />
                      <Text>Push Notifications</Text>
                    </Space>
                    <Switch
                      checked={preferences?.push?.orders ?? true}
                      onChange={(checked) => {
                        if (preferences) {
                          savePreferences({
                            push: { ...preferences.push, orders: checked }
                          });
                        }
                      }}
                    />
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Space>
                      <MobileOutlined style={{ color: '#faad14' }} />
                      <Text>SMS Notifications</Text>
                    </Space>
                    <Switch
                      checked={preferences?.sms?.orders ?? false}
                      onChange={(checked) => {
                        if (preferences) {
                          savePreferences({
                            sms: { ...preferences.sms, orders: checked }
                          });
                        }
                      }}
                    />
                  </div>
                </Space>
              </div>

              <Divider />

              {/* Preferences */}
              <div>
                <Title level={5}>Notification Preferences</Title>
                <Form layout="vertical">
                  <Form.Item label="Notify me about">
                    <Checkbox.Group style={{ width: '100%' }} defaultValue={['orders', 'promotions', 'delivery']}>
                      <Space direction="vertical" style={{ width: '100%' }}>
                        <Checkbox value="orders">Order updates</Checkbox>
                        <Checkbox value="promotions">Promotions & offers</Checkbox>
                        <Checkbox value="delivery">Delivery status</Checkbox>
                        <Checkbox value="reviews">Review reminders</Checkbox>
                        <Checkbox value="wishlist">Wishlist price drops</Checkbox>
                        <Checkbox value="account">Account activity</Checkbox>
                      </Space>
                    </Checkbox.Group>
                  </Form.Item>

                  <Form.Item label="Notification Frequency">
                    <Select defaultValue="instant" style={{ width: '100%' }}>
                      <Option value="instant">Instant</Option>
                      <Option value="hourly">Hourly digest</Option>
                      <Option value="daily">Daily digest</Option>
                      <Option value="weekly">Weekly digest</Option>
                    </Select>
                  </Form.Item>

                  <Form.Item label="Quiet Hours">
                    <Select defaultValue="none" style={{ width: '100%' }}>
                      <Option value="none">None</Option>
                      <Option value="night">10 PM - 8 AM</Option>
                      <Option value="work">9 AM - 5 PM</Option>
                      <Option value="custom">Custom</Option>
                    </Select>
                  </Form.Item>

                  <Button type="primary" block>
                    Save Preferences
                  </Button>
                </Form>
              </div>
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default CustomerNotificationsPage;

customer/quick-reorder.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  List,
  Space,
  Tag,
  Image,
  message,
  Divider,
  Statistic,
  Badge,
  Tooltip,
  Modal,
  Checkbox,
  Empty,
  Alert,
} from 'antd';
import {
  ShoppingCartOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  HeartOutlined,
  ThunderboltOutlined,
  CalendarOutlined,
  DollarOutlined,
  RocketOutlined,
} from '@ant-design/icons';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;

interface OrderItem {
  id: number;
  productId: number;
  productName: string;
  productImage: string;
  quantity: number;
  price: number;
  category: string;
}

interface PastOrder {
  id: number;
  orderNumber: string;
  orderDate: string;
  totalAmount: number;
  items: OrderItem[];
  status: 'delivered' | 'cancelled';
}

interface FrequentItem {
  productId: number;
  productName: string;
  productImage: string;
  category: string;
  timesOrdered: number;
  lastOrdered: string;
  avgPrice: number;
  inStock: boolean;
}

const mockPastOrders: PastOrder[] = [
  {
    id: 1,
    orderNumber: '#12345',
    orderDate: dayjs().subtract(30, 'days').format('YYYY-MM-DD'),
    totalAmount: 299.99,
    status: 'delivered',
    items: [
      {
        id: 1,
        productId: 101,
        productName: 'Wireless Headphones',
        productImage: 'https://via.placeholder.com/80x80?text=Headphones',
        quantity: 1,
        price: 299.99,
        category: 'Electronics',
      },
    ],
  },
  {
    id: 2,
    orderNumber: '#12340',
    orderDate: dayjs().subtract(60, 'days').format('YYYY-MM-DD'),
    totalAmount: 149.99,
    status: 'delivered',
    items: [
      {
        id: 2,
        productId: 102,
        productName: 'Coffee Maker',
        productImage: 'https://via.placeholder.com/80x80?text=Coffee',
        quantity: 1,
        price: 89.99,
        category: 'Home',
      },
      {
        id: 3,
        productId: 103,
        productName: 'Coffee Beans (1kg)',
        productImage: 'https://via.placeholder.com/80x80?text=Beans',
        quantity: 2,
        price: 29.99,
        category: 'Groceries',
      },
    ],
  },
  {
    id: 3,
    orderNumber: '#12335',
    orderDate: dayjs().subtract(90, 'days').format('YYYY-MM-DD'),
    totalAmount: 199.99,
    status: 'delivered',
    items: [
      {
        id: 4,
        productId: 104,
        productName: 'Yoga Mat',
        productImage: 'https://via.placeholder.com/80x80?text=Yoga',
        quantity: 1,
        price: 49.99,
        category: 'Sports',
      },
      {
        id: 5,
        productId: 103,
        productName: 'Coffee Beans (1kg)',
        productImage: 'https://via.placeholder.com/80x80?text=Beans',
        quantity: 5,
        price: 29.99,
        category: 'Groceries',
      },
    ],
  },
];

const mockFrequentItems: FrequentItem[] = [
  {
    productId: 103,
    productName: 'Coffee Beans (1kg)',
    productImage: 'https://via.placeholder.com/120x120?text=Beans',
    category: 'Groceries',
    timesOrdered: 7,
    lastOrdered: dayjs().subtract(20, 'days').format('YYYY-MM-DD'),
    avgPrice: 29.99,
    inStock: true,
  },
  {
    productId: 105,
    productName: 'Printer Paper (500 sheets)',
    productImage: 'https://via.placeholder.com/120x120?text=Paper',
    category: 'Office',
    timesOrdered: 5,
    lastOrdered: dayjs().subtract(15, 'days').format('YYYY-MM-DD'),
    avgPrice: 12.99,
    inStock: true,
  },
  {
    productId: 106,
    productName: 'Hand Soap Refill',
    productImage: 'https://via.placeholder.com/120x120?text=Soap',
    category: 'Personal Care',
    timesOrdered: 4,
    lastOrdered: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    avgPrice: 8.99,
    inStock: false,
  },
];

const QuickReorderPage: React.FC = () => {
  const [orders] = useState<PastOrder[]>(mockPastOrders);
  const [frequentItems] = useState<FrequentItem[]>(mockFrequentItems);
  const [selectedItems, setSelectedItems] = useState<number[]>([]);
  const [isReorderModalVisible, setIsReorderModalVisible] = useState<boolean>(false);
  const [reorderingOrder, setReorderingOrder] = useState<PastOrder | null>(null);

  const handleReorderEntireOrder = (order: PastOrder) => {
    setReorderingOrder(order);
    setSelectedItems(order.items.map(item => item.id));
    setIsReorderModalVisible(true);
  };

  const handleConfirmReorder = () => {
    const itemCount = selectedItems.length;
    message.success({
      content: ` ${itemCount} ${itemCount === 1 ? 'item' : 'items'} added to cart!`,
      duration: 3,
    });
    setIsReorderModalVisible(false);
    setReorderingOrder(null);
    setSelectedItems([]);
  };

  const handleQuickAddItem = (item: FrequentItem) => {
    if (!item.inStock) {
      message.warning('This item is currently out of stock');
      return;
    }
    message.success(`${item.productName} added to cart!`);
  };

  const handleToggleItem = (itemId: number) => {
    setSelectedItems(prev =>
      prev.includes(itemId)
        ? prev.filter(id => id !== itemId)
        : [...prev, itemId]
    );
  };

  const totalPastOrders = orders.length;
  const totalSpent = orders
    .filter(o => o.status === 'delivered')
    .reduce((sum, o) => sum + o.totalAmount, 0);
  const avgOrderValue = totalPastOrders > 0 ? totalSpent / totalPastOrders : 0;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <ThunderboltOutlined style={{ color: '#1890ff' }} /> Quick Reorder
          </Title>
          <Paragraph type="secondary">
            Reorder your favorite products with one click
          </Paragraph>
        </Col>
      </Row>

      <Alert
        message=" Quick Reorder saves you time!"
        description="Easily reorder items from your past purchases or frequently bought products."
        type="info"
        showIcon={false}
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Past Orders"
              value={totalPastOrders}
              prefix={<ShoppingCartOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Spent"
              value={totalSpent}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Avg Order Value"
              value={avgOrderValue}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={16}>
          <Card
            title={
