                <ClockCircleOutlined />
                <span>Recent Orders</span>
              </Space>
            }
          >
            <List
              dataSource={orders}
              locale={{ emptyText: 'No past orders found' }}
              renderItem={(order) => (
                <List.Item
                  actions={[
                    <Button
                      type="primary"
                      icon={<RocketOutlined />}
                      onClick={() => handleReorderEntireOrder(order)}
                    >
                      Reorder All
                    </Button>,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <div
                        style={{
                          width: 60,
                          height: 60,
                          borderRadius: 8,
                          background: '#1890ff',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                        }}
                      >
                        <ShoppingCartOutlined style={{ fontSize: 28, color: '#fff' }} />
                      </div>
                    }
                    title={
                      <Space>
                        <Text strong>Order {order.orderNumber}</Text>
                        <Tag color={order.status === 'delivered' ? 'success' : 'default'}>
                          {order.status.toUpperCase()}
                        </Tag>
                      </Space>
                    }
                    description={
                      <Space direction="vertical" style={{ width: '100%' }} size="small">
                        <div>
                          <Text type="secondary">
                            <CalendarOutlined /> {dayjs(order.orderDate).format('MMM DD, YYYY')}
                          </Text>
                          <Text type="secondary" style={{ marginLeft: 16 }}>
                            <DollarOutlined /> ${order.totalAmount.toFixed(2)}
                          </Text>
                        </div>

                        <div>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            {order.items.length} {order.items.length === 1 ? 'item' : 'items'}
                          </Text>
                        </div>

                        <Divider style={{ margin: '8px 0' }} />

                        <div>
                          <Space wrap size="small">
                            {order.items.map((item) => (
                              <Tooltip key={item.id} title={item.productName}>
                                <Image
                                  src={item.productImage}
                                  width={40}
                                  height={40}
                                  preview={false}
                                  style={{ borderRadius: 4, border: '1px solid #d9d9d9' }}
                                />
                              </Tooltip>
                            ))}
                          </Space>
                        </div>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card
            title={
              <Space>
                <HeartOutlined />
                <span>Frequently Ordered</span>
              </Space>
            }
          >
            <List
              dataSource={frequentItems}
              locale={{ emptyText: 'No frequent items yet' }}
              renderItem={(item) => (
                <List.Item
                  style={{
                    opacity: item.inStock ? 1 : 0.6,
                  }}
                >
                  <List.Item.Meta
                    avatar={
                      <Badge count={item.timesOrdered} color="#1890ff">
                        <Image
                          src={item.productImage}
                          width={60}
                          height={60}
                          preview={false}
                          style={{ borderRadius: 8 }}
                        />
                      </Badge>
                    }
                    title={
                      <div>
                        <Text strong ellipsis>
                          {item.productName}
                        </Text>
                        {!item.inStock && (
                          <Tag color="error" style={{ marginLeft: 8 }}>
                            Out of Stock
                          </Tag>
                        )}
                      </div>
                    }
                    description={
                      <Space direction="vertical" size="small" style={{ width: '100%' }}>
                        <div>
                          <Text strong style={{ fontSize: 16, color: '#1890ff' }}>
                            ${item.avgPrice.toFixed(2)}
                          </Text>
                        </div>

                        <div>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            Ordered {item.timesOrdered} times
                          </Text>
                        </div>

                        <div>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            Last: {dayjs(item.lastOrdered).format('MMM DD, YYYY')}
                          </Text>
                        </div>

                        <Button
                          type="primary"
                          size="small"
                          icon={<ShoppingCartOutlined />}
                          onClick={() => handleQuickAddItem(item)}
                          disabled={!item.inStock}
                          block
                        >
                          {item.inStock ? 'Quick Add' : 'Unavailable'}
                        </Button>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>

      <Modal
        title={
          <Space>
            <RocketOutlined />
            <span>Reorder Items</span>
          </Space>
        }
        open={isReorderModalVisible}
        onCancel={() => {
          setIsReorderModalVisible(false);
          setReorderingOrder(null);
          setSelectedItems([]);
        }}
        footer={[
          <Button
            key="cancel"
            onClick={() => {
              setIsReorderModalVisible(false);
              setReorderingOrder(null);
              setSelectedItems([]);
            }}
          >
            Cancel
          </Button>,
          <Button
            key="reorder"
            type="primary"
            icon={<ShoppingCartOutlined />}
            onClick={handleConfirmReorder}
            disabled={selectedItems.length === 0}
          >
            Add {selectedItems.length} {selectedItems.length === 1 ? 'Item' : 'Items'} to Cart
          </Button>,
        ]}
        width={600}
      >
        {reorderingOrder && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Alert
              message="Select items to reorder"
              description="Choose which items from this order you'd like to add to your cart."
              type="info"
              showIcon
            />

            <List
              dataSource={reorderingOrder.items}
              renderItem={(item) => {
                const isSelected = selectedItems.includes(item.id);
                return (
                  <List.Item
                    style={{
                      background: isSelected ? '#e6f7ff' : 'transparent',
                      padding: 12,
                      borderRadius: 8,
                      marginBottom: 8,
                      cursor: 'pointer',
                    }}
                    onClick={() => handleToggleItem(item.id)}
                  >
                    <List.Item.Meta
                      avatar={
                        <Checkbox
                          checked={isSelected}
                          onChange={() => handleToggleItem(item.id)}
                        />
                      }
                      title={
                        <Space>
                          <Image
                            src={item.productImage}
                            width={60}
                            height={60}
                            preview={false}
                            style={{ borderRadius: 8 }}
                          />
                          <div>
                            <div>
                              <Text strong>{item.productName}</Text>
                            </div>
                            <div>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                Quantity: {item.quantity}
                              </Text>
                            </div>
                            <div>
                              <Text strong style={{ fontSize: 16, color: '#1890ff' }}>
                                ${item.price.toFixed(2)}
                              </Text>
                            </div>
                          </div>
                        </Space>
                      }
                    />
                    {isSelected && (
                      <CheckCircleOutlined style={{ fontSize: 20, color: '#52c41a' }} />
                    )}
                  </List.Item>
                );
              }}
            />

            <Divider />

            <div>
              <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                <Text strong>Selected Items:</Text>
                <Text strong style={{ fontSize: 16 }}>
                  {selectedItems.length} / {reorderingOrder.items.length}
                </Text>
              </Space>

              <div style={{ marginTop: 8 }}>
                <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                  <Text strong>Estimated Total:</Text>
                  <Text strong style={{ fontSize: 20, color: '#1890ff' }}>
                    $
                    {reorderingOrder.items
                      .filter(item => selectedItems.includes(item.id))
                      .reduce((sum, item) => sum + item.price * item.quantity, 0)
                      .toFixed(2)}
                  </Text>
                </Space>
              </div>
            </div>
          </Space>
        )}
      </Modal>
    </div>
  );
};

export default QuickReorderPage;

