        <Form form={form} layout="vertical">
          <Form.Item label="Promotion Name" name="name" rules={[{ required: true }]}>
            <Input placeholder="Enter promotion name" />
          </Form.Item>

          <Form.Item label="Promotion Type" name="type" rules={[{ required: true }]}>
            <Select placeholder="Select promotion type">
              <Option value="percentage">Percentage Discount</Option>
              <Option value="fixed">Fixed Amount Discount</Option>
              <Option value="shipping">Free Shipping</Option>
              <Option value="quantity">Buy X Get Y</Option>
            </Select>
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Discount Value" name="value" rules={[{ required: true }]}>
                <Input placeholder="Enter discount value" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Usage Limit" name="limit" rules={[{ required: true }]}>
                <Input placeholder="Enter usage limit" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item label="Valid Period" name="period" rules={[{ required: true }]}>
            <RangePicker style={{ width: '100%' }} />
          </Form.Item>

          <Form.Item label="Minimum Order Amount" name="minOrder">
            <Input placeholder="Enter minimum order amount (optional)" />
          </Form.Item>

          <Form.Item label="Applicable Products" name="products">
            <Select mode="multiple" placeholder="Select products (leave empty for all)">
              <Option value="all">All Products</Option>
              <Option value="electronics">Electronics</Option>
              <Option value="accessories">Accessories</Option>
            </Select>
          </Form.Item>

          <Form.Item name="autoApply" valuePropName="checked">
            <Switch /> <Text>Auto-apply at checkout</Text>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default VendorMarketingTools;

admin/bulk-operations.__content:
import React, { useState } from 'react';
import { Card, Table, Button, Space, Tag, Row, Col, Input, Select, Modal, Form, InputNumber, message, Upload, Tabs, Typography, Progress, Alert, Checkbox } from 'antd';
import { UploadOutlined, DownloadOutlined, EditOutlined, DeleteOutlined, PlayCircleOutlined, PauseCircleOutlined, ClockCircleOutlined } from '@ant-design/icons';

const { Search } = Input;
const { Option } = Select;
const { Title, Text } = Typography;
const { TabPane } = Tabs;
const { TextArea } = Input;

const BulkOperationsPage: React.FC = () => {
  const [selectedItems, setSelectedItems] = useState<any[]>([]);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [operationType, setOperationType] = useState('');
  const [processing, setProcessing] = useState(false);
  const [progress, setProgress] = useState(0);
  const [form] = Form.useForm();

  const mockProducts = [
    { id: 1, name: 'Wireless Headphones Pro', sku: 'WH-PRO-001', currentPrice: 79.99, stock: 45, vendor: 'Audio Tech' },
    { id: 2, name: 'Smart Watch Series X', sku: 'SW-X-002', currentPrice: 249.99, stock: 8, vendor: 'Tech Innovations' },
    { id: 3, name: 'Laptop Stand Premium', sku: 'LS-PREM-003', currentPrice: 89.99, stock: 32, vendor: 'Office Solutions' },
    { id: 4, name: 'USB-C Hub Multi', sku: 'USBH-M-004', currentPrice: 45.99, stock: 67, vendor: 'Connect Pro' },
  ];

  const operationHistory = [
    { id: 1, operation: 'Price Update', items: 25, status: 'completed', date: '2024-01-15', user: 'Admin' },
    { id: 2, operation: 'Stock Adjustment', items: 12, status: 'running', date: '2024-01-15', user: 'Manager' },
    { id: 3, operation: 'Bulk Delete', items: 8, status: 'failed', date: '2024-01-14', user: 'Admin' },
  ];

  const handleBulkOperation = (type: string) => {
    setOperationType(type);
    setIsModalVisible(true);
    form.resetFields();
  };

  const executeBulkOperation = async () => {
    try {
      setProcessing(true);
      setProgress(0);

      // Simulate progress
      const interval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) {
            clearInterval(interval);
            setProcessing(false);
            setIsModalVisible(false);
            setSelectedItems([]);
            message.success(`${operationType} completed successfully for ${selectedItems.length} items`);
            return 100;
          }
          return prev + 10;
        });
      }, 300);
    } catch (error) {
      setProcessing(false);
      message.error('Operation failed');
    }
  };

  const getOperationColor = (status: string) => {
    switch (status) {
      case 'completed': return 'green';
      case 'running': return 'blue';
      case 'failed': return 'red';
      default: return 'default';
    }
  };

  const productColumns = [
    {
      title: '',
      key: 'select',
      render: (record: any) => (
        <Checkbox 
          checked={selectedItems.some(item => item.id === record.id)}
          onChange={(e) => {
            if (e.target.checked) {
              setSelectedItems([...selectedItems, record]);
            } else {
              setSelectedItems(selectedItems.filter(item => item.id !== record.id));
            }
          }}
        />
      ),
      width: 50,
    },
    {
      title: 'Product',
      key: 'product',
      render: (record: any) => (
        <div>
          <Text strong>{record.name}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: '12px' }}>SKU: {record.sku}</Text>
        </div>
      ),
    },
    {
      title: 'Current Price',
      dataIndex: 'currentPrice',
      key: 'currentPrice',
      render: (price: number) => <Text strong>${price}</Text>,
    },
    {
      title: 'Stock',
      dataIndex: 'stock',
      key: 'stock',
    },
    {
      title: 'Vendor',
      dataIndex: 'vendor',
      key: 'vendor',
    },
  ];

  const historyColumns = [
    {
      title: 'Operation',
      dataIndex: 'operation',
      key: 'operation',
    },
    {
      title: 'Items',
      dataIndex: 'items',
      key: 'items',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getOperationColor(status)} icon={status === 'running' ? <ClockCircleOutlined /> : null}>
          {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
    },
    {
      title: 'User',
      dataIndex: 'user',
      key: 'user',
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (record: any) => (
        <Space>
          {record.status === 'running' && (
            <Button size="small" icon={<PauseCircleOutlined />} onClick={() => message.info('Operation paused')}>
              Pause
            </Button>
          )}
          <Button size="small" type="text" onClick={() => message.info('Details coming soon')}>
            Details
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <PlayCircleOutlined style={{ marginRight: 8 }} />
          Bulk Operations
        </Title>
        <Text type="secondary">Phase 4 - Advanced bulk management system</Text>
      </div>

      <Card style={{ marginBottom: 24 }}>
        <Row gutter={16}>
          <Col span={18}>
            <Space wrap>
              <Button 
                type="primary" 
                icon={<EditOutlined />}
                disabled={selectedItems.length === 0}
                onClick={() => handleBulkOperation('Price Update')}
              >
                Update Prices ({selectedItems.length})
              </Button>
              <Button 
                icon={<EditOutlined />}
                disabled={selectedItems.length === 0}
                onClick={() => handleBulkOperation('Stock Adjustment')}
              >
                Adjust Stock ({selectedItems.length})
              </Button>
              <Button 
                icon={<DeleteOutlined />}
                danger
                disabled={selectedItems.length === 0}
                onClick={() => handleBulkOperation('Bulk Delete')}
              >
                Delete Selected ({selectedItems.length})
              </Button>
              <Upload>
                <Button icon={<UploadOutlined />}>Import Updates</Button>
              </Upload>
              <Button icon={<DownloadOutlined />} onClick={() => message.success('Template downloaded')}>
                Export Template
              </Button>
            </Space>
          </Col>
          <Col span={6}>
            <Search placeholder="Search products..." allowClear />
          </Col>
        </Row>
      </Card>

      <Tabs defaultActiveKey="products">
        <TabPane tab="Product Selection" key="products">
          <Card>
            <Alert
              message={`${selectedItems.length} products selected for bulk operations`}
              type={selectedItems.length > 0 ? 'success' : 'info'}
              style={{ marginBottom: 16 }}
              action={
                selectedItems.length > 0 && (
                  <Button size="small" type="text" onClick={() => setSelectedItems([])}>
                    Clear Selection
                  </Button>
                )
              }
            />

            <Table
              columns={productColumns}
              dataSource={mockProducts}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>
        </TabPane>

        <TabPane tab="Operation History" key="history">
          <Card>
            <Table
              columns={historyColumns}
              dataSource={operationHistory}
              rowKey="id"
              pagination={{ pageSize: 10 }}
            />
          </Card>
        </TabPane>

        <TabPane tab="Scheduled Operations" key="scheduled">
          <Card>
            <Alert
              message="Scheduled Operations"
              description="Set up automated bulk operations to run at specific times."
              type="info"
              showIcon
            />
          </Card>
        </TabPane>
      </Tabs>

      <Modal
        title={`${operationType} - ${selectedItems.length} items selected`}
        visible={isModalVisible}
        onOk={executeBulkOperation}
        onCancel={() => setIsModalVisible(false)}
        confirmLoading={processing}
        width={600}
      >
        {processing && (
          <div style={{ marginBottom: 16 }}>
            <Text>Processing operation...</Text>
            <Progress percent={progress} status="active" />
          </div>
        )}

        <Form form={form} layout="vertical">
          {operationType === 'Price Update' && (
            <>
              <Form.Item label="Update Type" name="updateType" rules={[{ required: true }]}>
                <Select placeholder="Select update type">
                  <Option value="percentage">Percentage Change</Option>
                  <Option value="fixed">Fixed Amount Change</Option>
                  <Option value="set">Set New Price</Option>
                </Select>
              </Form.Item>
              <Form.Item label="Value" name="value" rules={[{ required: true }]}>
                <InputNumber style={{ width: '100%' }} placeholder="Enter value" />
              </Form.Item>
            </>
          )}

          {operationType === 'Stock Adjustment' && (
            <>
              <Form.Item label="Adjustment Type" name="adjustmentType" rules={[{ required: true }]}>
                <Select placeholder="Select adjustment type">
                  <Option value="add">Add Stock</Option>
                  <Option value="subtract">Subtract Stock</Option>
                  <Option value="set">Set Stock Level</Option>
                </Select>
              </Form.Item>
              <Form.Item label="Quantity" name="quantity" rules={[{ required: true }]}>
                <InputNumber style={{ width: '100%' }} placeholder="Enter quantity" />
              </Form.Item>
            </>
          )}

          {operationType === 'Bulk Delete' && (
            <Alert
              message="Warning"
              description="This action cannot be undone. All selected products will be permanently deleted."
              type="warning"
              showIcon
            />
          )}

          <Form.Item label="Notes" name="notes">
            <TextArea placeholder="Add operation notes..." rows={3} />
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default BulkOperationsPage;

admin/email-marketing.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  Upload,
  message,
  Divider,
  Switch,
  InputNumber,
  Alert,
  Modal,
  Spin,
  Statistic,
} from 'antd';
import {
  MailOutlined,
  SendOutlined,
  UserOutlined,
  TeamOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  UploadOutlined,
  FileTextOutlined,
  EditOutlined,
  DeleteOutlined,
  PlusOutlined,
  EyeOutlined,
  CopyOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { emailAPI } from '@/services/api/email';
import type { EmailTemplate, EmailCampaign, EmailStats } from '@/services/api/email';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;

const EmailMarketingPage: React.FC = () => {
  const [form] = Form.useForm();
  const [templateForm] = Form.useForm();

  // State
  const [loading, setLoading] = useState(false);
  const [templates, setTemplates] = useState<EmailTemplate[]>([]);
  const [campaigns, setCampaigns] = useState<EmailCampaign[]>([]);
  const [stats, setStats] = useState<EmailStats | null>(null);
  const [selectedTemplate, setSelectedTemplate] = useState<EmailTemplate | null>(null);
  const [isTemplateModalVisible, setIsTemplateModalVisible] = useState(false);
  const [isPreviewModalVisible, setIsPreviewModalVisible] = useState(false);
  const [previewHtml, setPreviewHtml] = useState('');

  // Fetch data on mount
  useEffect(() => {
    fetchTemplates();
    fetchCampaigns();
    fetchStats();
  }, []);

  const fetchTemplates = async () => {
    try {
      setLoading(true);
      const data = await emailAPI.getTemplates();
      setTemplates(data);
    } catch (error) {
      message.error('Failed to fetch email templates');
    } finally {
      setLoading(false);
    }
  };

  const fetchCampaigns = async () => {
    try {
      const data = await emailAPI.getCampaigns();
      setCampaigns(data);
    } catch (error) {
      message.error('Failed to fetch campaigns');
    }
  };

  const fetchStats = async () => {
    try {
      const data = await emailAPI.getStats();
      setStats(data);
    } catch (error) {
      message.error('Failed to fetch statistics');
    }
  };

  // Template handlers
  const handleCreateTemplate = () => {
    setSelectedTemplate(null);
    templateForm.resetFields();
    setIsTemplateModalVisible(true);
  };

  const handleEditTemplate = (template: EmailTemplate) => {
    setSelectedTemplate(template);
    templateForm.setFieldsValue(template);
    setIsTemplateModalVisible(true);
  };

  const handleTemplateSubmit = async (values: any) => {
    try {
      setLoading(true);
      if (selectedTemplate) {
        await emailAPI.updateTemplate(selectedTemplate.id, values);
        message.success('Template updated successfully');
      } else {
        await emailAPI.createTemplate(values);
        message.success('Template created successfully');
      }
      setIsTemplateModalVisible(false);
      fetchTemplates();
      fetchStats();
    } catch (error) {
      message.error('Failed to save template');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteTemplate = async (id: string) => {
    Modal.confirm({
      title: 'Delete Template',
      content: 'Are you sure you want to delete this template?',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await emailAPI.deleteTemplate(id);
          message.success('Template deleted successfully');
          fetchTemplates();
          fetchStats();
        } catch (error) {
          message.error('Failed to delete template');
        }
      },
    });
  };

  const handleDuplicateTemplate = async (id: string, name: string) => {
    try {
      await emailAPI.duplicateTemplate(id, `${name} (Copy)`);
      message.success('Template duplicated successfully');
      fetchTemplates();
    } catch (error) {
      message.error('Failed to duplicate template');
    }
  };

  const handlePreviewTemplate = async (id: string) => {
    try {
      setLoading(true);
      const preview = await emailAPI.previewTemplate(id);
      setPreviewHtml(preview.html);
      setIsPreviewModalVisible(true);
    } catch (error) {
      message.error('Failed to generate preview');
    } finally {
      setLoading(false);
    }
  };

  const handleSendTestEmail = async (templateId: string) => {
    const email = prompt('Enter email address for test:');
    if (!email) return;

    try {
      await emailAPI.sendTestEmail({ templateId, recipientEmail: email });
      message.success(`Test email sent to ${email}`);
    } catch (error) {
      message.error('Failed to send test email');
    }
  };

  // Campaign handlers
  const handleSendCampaign = async (values: any) => {
    try {
      setLoading(true);
      await emailAPI.createCampaign(values);
      message.success('Email campaign scheduled successfully!');
      form.resetFields();
      fetchCampaigns();
    } catch (error) {
      message.error('Failed to schedule campaign');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCampaign = async (id: string) => {
    Modal.confirm({
      title: 'Delete Campaign',
      content: 'Are you sure you want to delete this campaign?',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await emailAPI.deleteCampaign(id);
          message.success('Campaign deleted successfully');
          fetchCampaigns();
        } catch (error) {
          message.error('Failed to delete campaign');
        }
      },
    });
  };

  const templateColumns: ColumnsType<EmailTemplate> = [
    {
      title: 'Template Name',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      render: (subject) => <Text code>{subject}</Text>,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category) => {
        const colorMap: Record<string, string> = {
          onboarding: 'blue',
          transactional: 'green',
          marketing: 'orange',
          newsletter: 'purple',
          notification: 'cyan',
        };
        return <Tag color={colorMap[category] || 'default'}>{category?.toUpperCase()}</Tag>;
      },
      filters: [
        { text: 'Onboarding', value: 'onboarding' },
        { text: 'Transactional', value: 'transactional' },
        { text: 'Marketing', value: 'marketing' },
        { text: 'Newsletter', value: 'newsletter' },
        { text: 'Notification', value: 'notification' },
      ],
      onFilter: (value, record) => record.category === value,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: EmailTemplate['status']) => (
        <Tag color={status === 'active' ? 'green' : 'orange'}>
          {status === 'active' ? <CheckCircleOutlined /> : <ClockCircleOutlined />} {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handlePreviewTemplate(record.id)}
          >
            Preview
          </Button>
          <Button
            size="small"
            icon={<EditOutlined />}
            onClick={() => handleEditTemplate(record)}
          >
            Edit
          </Button>
          <Button
            size="small"
            icon={<CopyOutlined />}
            onClick={() => handleDuplicateTemplate(record.id, record.name)}
          >
            Duplicate
          </Button>
          <Button
            size="small"
            icon={<SendOutlined />}
            onClick={() => handleSendTestEmail(record.id)}
          >
            Test
          </Button>
          <Button
            size="small"
            danger
            icon={<DeleteOutlined />}
            onClick={() => handleDeleteTemplate(record.id)}
          >
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  const campaignColumns: ColumnsType<EmailCampaign> = [
    {
      title: 'Campaign',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
    },
    {
      title: 'Recipients',
      dataIndex: 'recipients',
      key: 'recipients',
      render: (count) => (
        <Space>
          <TeamOutlined />
          <Text>{count.toLocaleString()}</Text>
        </Space>
      ),
    },
    {
      title: 'Open Rate',
      key: 'openRate',
      render: (_, record) => {
        const rate = record.sent > 0 ? ((record.opened / record.sent) * 100).toFixed(1) : '0.0';
        return <Tag color="blue">{rate}%</Tag>;
      },
      sorter: (a, b) => (a.opened / (a.sent || 1)) - (b.opened / (b.sent || 1)),
    },
    {
      title: 'Click Rate',
      key: 'clickRate',
      render: (_, record) => {
        const rate = record.sent > 0 ? ((record.clicked / record.sent) * 100).toFixed(1) : '0.0';
        return <Tag color="green">{rate}%</Tag>;
      },
      sorter: (a, b) => (a.clicked / (a.sent || 1)) - (b.clicked / (b.sent || 1)),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: EmailCampaign['status']) => {
        const colors: Record<EmailCampaign['status'], string> = {
          draft: 'orange',
          scheduled: 'blue',
          sending: 'purple',
          sent: 'green',
          paused: 'yellow',
          cancelled: 'red',
        };
        return <Tag color={colors[status]}>{status.toUpperCase()}</Tag>;
      },
      filters: [
        { text: 'Draft', value: 'draft' },
        { text: 'Scheduled', value: 'scheduled' },
        { text: 'Sending', value: 'sending' },
        { text: 'Sent', value: 'sent' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Scheduled',
      dataIndex: 'scheduledAt',
      key: 'scheduledAt',
      render: (date) => <Text type="secondary">{date}</Text>,
    },
  ];

  return (
    <>
    <Spin spinning={loading} tip="Loading email data...">
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <Title level={3}>
            <MailOutlined style={{ color: '#1890ff' }} /> Email Marketing
          </Title>
          <Paragraph type="secondary">
            Create and send email campaigns, manage templates, and track performance
          </Paragraph>
        </div>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={handleCreateTemplate}
          size="large"
        >
          Create Template
        </Button>
      </div>

      {/* Statistics Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Templates"
              value={stats?.totalTemplates || 0}
              prefix={<FileTextOutlined />}
              valueStyle={{ color: '#3f8600' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Active Templates"
              value={stats?.activeTemplates || 0}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
