admin/email-templates.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Space,
  Modal,
  Form,
  Input,
  Select,
  message,
  Tag,
  Switch,
  Row,
  Col,
  Divider,
  Typography,
  Spin,
  Tooltip,
  Badge,
} from 'antd';
import {
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  EyeOutlined,
  CopyOutlined,
  SendOutlined,
  MailOutlined,
} from '@ant-design/icons';
import { PageContainer } from '@ant-design/pro-layout';
import { emailTemplatesAPI } from '@/services/api';

const { TextArea } = Input;
const { Option } = Select;
const { Title, Text } = Typography;

interface EmailTemplate {
  id: string;
  name: string;
  subject: string;
  type: 'order_confirmation' | 'welcome' | 'password_reset' | 'promotional' | 'notification';
  content: string;
  variables: string[];
  isActive: boolean;
  lastUsed?: string;
  usageCount: number;
  createdAt: string;
  updatedAt: string;
}

const EmailTemplatesPage: React.FC = () => {
  const [templates, setTemplates] = useState<EmailTemplate[]>([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [previewVisible, setPreviewVisible] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<EmailTemplate | null>(null);
  const [previewTemplate, setPreviewTemplate] = useState<EmailTemplate | null>(null);
  const [form] = Form.useForm();

  const templateTypes = [
    { value: 'order_confirmation', label: 'Order Confirmation', color: 'blue' },
    { value: 'welcome', label: 'Welcome Email', color: 'green' },
    { value: 'password_reset', label: 'Password Reset', color: 'orange' },
    { value: 'promotional', label: 'Promotional', color: 'purple' },
    { value: 'notification', label: 'Notification', color: 'cyan' },
  ];

  const availableVariables = [
    '{{customer_name}}',
    '{{order_number}}',
    '{{order_total}}',
    '{{product_name}}',
    '{{company_name}}',
    '{{support_email}}',
    '{{website_url}}',
    '{{reset_link}}',
    '{{verification_code}}',
  ];

  useEffect(() => {
    fetchTemplates();
  }, []);

  const fetchTemplates = async () => {
    setLoading(true);
    try {
      const response = await emailTemplatesAPI.getAll();
      setTemplates(response.data);
    } catch (error) {
      message.error('Failed to fetch email templates');
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = () => {
    setEditingTemplate(null);
    form.resetFields();
    setModalVisible(true);
  };

  const handleEdit = (template: EmailTemplate) => {
    setEditingTemplate(template);
    form.setFieldsValue({
      name: template.name,
      subject: template.subject,
      type: template.type,
      content: template.content,
      isActive: template.isActive,
    });
    setModalVisible(true);
  };

  const handleDelete = async (id: string) => {
    Modal.confirm({
      title: 'Delete Email Template',
      content: 'Are you sure you want to delete this email template?',
      okText: 'Delete',
      okType: 'danger',
      onOk: async () => {
        try {
          await emailTemplatesAPI.delete(id);
          message.success('Email template deleted successfully');
          fetchTemplates();
        } catch (error) {
          message.error('Failed to delete email template');
        }
      },
    });
  };

  const handleSubmit = async (values: any) => {
    try {
      if (editingTemplate) {
        await emailTemplatesAPI.update(editingTemplate.id, values);
        message.success('Email template updated successfully');
      } else {
        await emailTemplatesAPI.create(values);
        message.success('Email template created successfully');
      }
      setModalVisible(false);
      fetchTemplates();
    } catch (error) {
      message.error('Failed to save email template');
    }
  };

  const handleToggleStatus = async (id: string, isActive: boolean) => {
    try {
      await emailTemplatesAPI.toggleStatus(id, isActive);
      message.success(`Template ${isActive ? 'activated' : 'deactivated'} successfully`);
      fetchTemplates();
    } catch (error) {
      message.error('Failed to update template status');
    }
  };

  const handlePreview = (template: EmailTemplate) => {
    setPreviewTemplate(template);
    setPreviewVisible(true);
  };

  const handleDuplicate = async (template: EmailTemplate) => {
    try {
      const duplicateData = {
        ...template,
        name: `${template.name} (Copy)`,
        isActive: false,
      };
      delete duplicateData.id;
      delete duplicateData.createdAt;
      delete duplicateData.updatedAt;
      delete duplicateData.lastUsed;
      delete duplicateData.usageCount;

      await emailTemplatesAPI.create(duplicateData);
      message.success('Email template duplicated successfully');
      fetchTemplates();
    } catch (error) {
      message.error('Failed to duplicate email template');
    }
  };

  const columns = [
    {
      title: 'Template Name',
      dataIndex: 'name',
      key: 'name',
      render: (text: string, record: EmailTemplate) => (
        <Space direction="vertical" size={0}>
          <Text strong>{text}</Text>
          <Text type="secondary" style={{ fontSize: '12px' }}>
            {record.subject}
          </Text>
        </Space>
      ),
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: string) => {
        const typeConfig = templateTypes.find(t => t.value === type);
        return (
          <Tag color={typeConfig?.color}>
            {typeConfig?.label}
          </Tag>
        );
      },
    },
    {
      title: 'Status',
      dataIndex: 'isActive',
      key: 'isActive',
      render: (isActive: boolean, record: EmailTemplate) => (
        <Switch
          checked={isActive}
          onChange={(checked) => handleToggleStatus(record.id, checked)}
          checkedChildren="Active"
          unCheckedChildren="Inactive"
        />
      ),
    },
    {
      title: 'Usage',
      dataIndex: 'usageCount',
      key: 'usageCount',
      render: (count: number, record: EmailTemplate) => (
        <Space direction="vertical" size={0}>
          <Badge count={count} style={{ backgroundColor: '#52c41a' }} />
          {record.lastUsed && (
            <Text type="secondary" style={{ fontSize: '12px' }}>
              Last used: {new Date(record.lastUsed).toLocaleDateString()}
            </Text>
          )}
        </Space>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record: EmailTemplate) => (
        <Space>
          <Tooltip title="Preview">
            <Button
              type="text"
              icon={<EyeOutlined />}
              onClick={() => handlePreview(record)}
            />
          </Tooltip>
          <Tooltip title="Edit">
            <Button
              type="text"
              icon={<EditOutlined />}
              onClick={() => handleEdit(record)}
            />
          </Tooltip>
          <Tooltip title="Duplicate">
            <Button
              type="text"
              icon={<CopyOutlined />}
              onClick={() => handleDuplicate(record)}
            />
          </Tooltip>
          <Tooltip title="Delete">
            <Button
              type="text"
              danger
              icon={<DeleteOutlined />}
              onClick={() => handleDelete(record.id)}
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  return (
    <PageContainer
      header={{
        title: 'Email Templates',
        breadcrumb: {
          items: [
            { title: 'Admin' },
            { title: 'Email Templates' },
          ],
        },
      }}
    >
      <Card>
        <Row justify="space-between" align="middle" style={{ marginBottom: 16 }}>
          <Col>
            <Space>
              <MailOutlined style={{ fontSize: 20, color: '#1890ff' }} />
              <Title level={4} style={{ margin: 0 }}>
                Email Templates Management
              </Title>
            </Space>
          </Col>
          <Col>
            <Button
              type="primary"
              icon={<PlusOutlined />}
              onClick={handleCreate}
            >
              Create Template
            </Button>
          </Col>
        </Row>

        <Table
          columns={columns}
          dataSource={templates}
          rowKey="id"
          loading={loading}
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `Total ${total} templates`,
          }}
        />
      </Card>

      {/* Create/Edit Modal */}
      <Modal
        title={editingTemplate ? 'Edit Email Template' : 'Create Email Template'}
        open={modalVisible}
        onCancel={() => setModalVisible(false)}
        footer={null}
        width={800}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleSubmit}
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="name"
                label="Template Name"
                rules={[{ required: true, message: 'Please input template name' }]}
              >
                <Input placeholder="Enter template name" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="type"
                label="Template Type"
                rules={[{ required: true, message: 'Please select template type' }]}
              >
                <Select placeholder="Select template type">
                  {templateTypes.map(type => (
                    <Option key={type.value} value={type.value}>
                      {type.label}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            name="subject"
            label="Email Subject"
            rules={[{ required: true, message: 'Please input email subject' }]}
          >
            <Input placeholder="Enter email subject" />
          </Form.Item>

          <Form.Item
            name="content"
            label="Email Content"
            rules={[{ required: true, message: 'Please input email content' }]}
          >
            <TextArea
              rows={10}
              placeholder="Enter email content with variables like {{customer_name}}"
            />
          </Form.Item>

          <Form.Item name="isActive" valuePropName="checked">
            <Switch checkedChildren="Active" unCheckedChildren="Inactive" />
            <span style={{ marginLeft: 8 }}>Template Status</span>
          </Form.Item>

          <Divider>Available Variables</Divider>
          <Space wrap>
            {availableVariables.map(variable => (
              <Tag
                key={variable}
                style={{ cursor: 'pointer' }}
                onClick={() => {
                  const content = form.getFieldValue('content') || '';
                  form.setFieldsValue({
                    content: content + ' ' + variable
                  });
                }}
              >
                {variable}
              </Tag>
            ))}
          </Space>

          <div style={{ marginTop: 24, textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setModalVisible(false)}>
                Cancel
              </Button>
              <Button type="primary" htmlType="submit">
                {editingTemplate ? 'Update' : 'Create'} Template
              </Button>
            </Space>
          </div>
        </Form>
      </Modal>

      {/* Preview Modal */}
      <Modal
        title="Email Template Preview"
        open={previewVisible}
        onCancel={() => setPreviewVisible(false)}
        footer={[
          <Button key="close" onClick={() => setPreviewVisible(false)}>
            Close
          </Button>,
          <Button
            key="send"
            type="primary"
            icon={<SendOutlined />}
            onClick={() => {
              message.info('Test email functionality would be implemented here');
            }}
          >
            Send Test Email
          </Button>,
        ]}
        width={700}
      >
        {previewTemplate && (
          <div>
            <div style={{ marginBottom: 16 }}>
              <Text strong>Subject: </Text>
              <Text>{previewTemplate.subject}</Text>
            </div>
            <Divider />
            <div
              style={{
                padding: 16,
                border: '1px solid #d9d9d9',
                borderRadius: 4,
                backgroundColor: '#fafafa',
                whiteSpace: 'pre-wrap',
              }}
            >
              {previewTemplate.content}
            </div>
          </div>
        )}
      </Modal>
    </PageContainer>
  );
};

export default EmailTemplatesPage;

admin/finance/payouts.__content:
import React, { useState } from 'react';
import {
  Card,
  Table,
  Tag,
  Button,
  Space,
  Input,
  Select,
  Modal,
  Typography,
  Row,
  Col,
  Statistic,
  message,
  Descriptions,
  Drawer,
} from 'antd';
import {
  SearchOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  EyeOutlined,
  DollarOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { Option } = Select;
const { TextArea } = Input;

interface Payout {
  id: number;
  payoutId: string;
  vendorName: string;
  vendorId: number;
  amount: number;
  requestDate: string;
  status: string;
  paymentMethod: string;
  accountNumber: string;
  processedDate?: string;
  notes?: string;
  transactionId?: string;
}

const mockPayouts: Payout[] = [
  {
    id: 1,
    payoutId: 'PAY-2024-10001',
    vendorName: 'TechStore Inc',
    vendorId: 101,
    amount: 2500.00,
    requestDate: '2024-11-01',
    status: 'pending',
    paymentMethod: 'Bank Transfer',
    accountNumber: '****1234',
  },
  {
    id: 2,
    payoutId: 'PAY-2024-10002',
    vendorName: 'Fashion Hub',
    vendorId: 102,
    amount: 1800.00,
    requestDate: '2024-10-30',
    status: 'approved',
    paymentMethod: 'PayPal',
    accountNumber: 'vendor@fashionhub.com',
  },
  {
    id: 3,
    payoutId: 'PAY-2024-10003',
    vendorName: 'Electronics World',
    vendorId: 103,
    amount: 3200.00,
    requestDate: '2024-10-28',
    status: 'completed',
    paymentMethod: 'Bank Transfer',
    accountNumber: '****5678',
    processedDate: '2024-10-30',
    transactionId: 'TXN-2024-100002',
  },
  {
    id: 4,
    payoutId: 'PAY-2024-10004',
    vendorName: 'Home Goods Co',
    vendorId: 104,
    amount: 950.00,
    requestDate: '2024-10-25',
    status: 'rejected',
    paymentMethod: 'Bank Transfer',
    accountNumber: '****9012',
    notes: 'Insufficient balance verification',
  },
];

const AdminPayoutsPage: React.FC = () => {
  const [payouts, setPayouts] = useState<Payout[]>(mockPayouts);
  const [selectedPayout, setSelectedPayout] = useState<Payout | null>(null);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [rejectModalVisible, setRejectModalVisible] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      pending: 'warning',
      approved: 'processing',
      completed: 'success',
      rejected: 'error',
    };
    return colors[status] || 'default';
  };

  const handleViewPayout = (payout: Payout) => {
    setSelectedPayout(payout);
    setDrawerVisible(true);
  };

  const handleApprovePayout = (payout: Payout) => {
    Modal.confirm({
      title: 'Approve Payout Request',
      content: `Approve payout of $${payout.amount.toFixed(2)} to ${payout.vendorName}?`,
      okText: 'Approve',
      onOk: () => {
        setPayouts(
          payouts.map((p) =>
            p.id === payout.id ? { ...p, status: 'approved' } : p
          )
        );
        message.success('Payout approved successfully');
      },
    });
  };

  const handleRejectPayout = (payout: Payout) => {
    setSelectedPayout(payout);
    setRejectModalVisible(true);
  };

  const handleConfirmReject = () => {
    if (!rejectionReason.trim()) {
      message.error('Please provide a reason for rejection');
      return;
    }
    if (selectedPayout) {
      setPayouts(
        payouts.map((p) =>
          p.id === selectedPayout.id
            ? { ...p, status: 'rejected', notes: rejectionReason }
            : p
        )
      );
      message.success('Payout rejected');
      setRejectModalVisible(false);
      setRejectionReason('');
    }
  };

  const handleCompletePayout = (payout: Payout) => {
    Modal.confirm({
      title: 'Mark as Completed',
      content: `Mark payout of $${payout.amount.toFixed(2)} to ${payout.vendorName} as completed?`,
      okText: 'Complete',
      onOk: () => {
        setPayouts(
          payouts.map((p) =>
            p.id === payout.id
              ? {
                  ...p,
                  status: 'completed',
                  processedDate: new Date().toISOString().split('T')[0],
                  transactionId: `TXN-2024-${Math.floor(Math.random() * 100000)}`,
                }
              : p
          )
        );
        message.success('Payout marked as completed');
      },
    });
  };

  const columns: ColumnsType<Payout> = [
    {
      title: 'Payout ID',
      dataIndex: 'payoutId',
      key: 'payoutId',
      render: (text: string) => <Text strong copyable>{text}</Text>,
    },
    {
      title: 'Vendor',
      key: 'vendor',
      render: (_, record) => (
        <div>
          <Text strong>{record.vendorName}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: 12 }}>
            ID: {record.vendorId}
          </Text>
        </div>
      ),
    },
    {
      title: 'Amount',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount: number) => (
        <Text strong style={{ color: '#1890ff', fontSize: 16 }}>
          ${amount.toFixed(2)}
        </Text>
      ),
      sorter: (a, b) => a.amount - b.amount,
    },
    {
      title: 'Request Date',
      dataIndex: 'requestDate',
      key: 'requestDate',
      render: (date: string) => new Date(date).toLocaleDateString(),
      sorter: (a, b) => new Date(a.requestDate).getTime() - new Date(b.requestDate).getTime(),
    },
    {
      title: 'Payment Method',
      dataIndex: 'paymentMethod',
      key: 'paymentMethod',
    },
    {
      title: 'Account',
      dataIndex: 'accountNumber',
      key: 'accountNumber',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Approved', value: 'approved' },
        { text: 'Completed', value: 'completed' },
        { text: 'Rejected', value: 'rejected' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button
            type="link"
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handleViewPayout(record)}
          >
            View
          </Button>
          {record.status === 'pending' && (
            <>
              <Button
                type="link"
                size="small"
                icon={<CheckCircleOutlined />}
                style={{ color: '#52c41a' }}
                onClick={() => handleApprovePayout(record)}
              >
                Approve
              </Button>
              <Button
                type="link"
                size="small"
                danger
                icon={<CloseCircleOutlined />}
                onClick={() => handleRejectPayout(record)}
              >
                Reject
              </Button>
            </>
          )}
          {record.status === 'approved' && (
            <Button
              type="link"
              size="small"
              icon={<CheckCircleOutlined />}
              style={{ color: '#52c41a' }}
              onClick={() => handleCompletePayout(record)}
            >
              Mark Completed
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const totalPending = payouts
    .filter((p) => p.status === 'pending')
    .reduce((sum, p) => sum + p.amount, 0);

  const totalApproved = payouts
    .filter((p) => p.status === 'approved')
    .reduce((sum, p) => sum + p.amount, 0);

  const totalCompleted = payouts
    .filter((p) => p.status === 'completed')
    .reduce((sum, p) => sum + p.amount, 0);

  const pendingCount = payouts.filter((p) => p.status === 'pending').length;

  return (
    <div style={{ padding: 24 }}>
      <Title level={3}>Vendor Payout Management</Title>

      {/* Statistics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Pending Payouts"
              value={totalPending}
              precision={2}
              prefix={<ClockCircleOutlined style={{ color: '#faad14' }} />}
              suffix="USD"
              valueStyle={{ color: '#faad14' }}
            />
            <Text type="secondary">{pendingCount} requests</Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Approved"
              value={totalApproved}
              precision={2}
              prefix={<CheckCircleOutlined style={{ color: '#1890ff' }} />}
              suffix="USD"
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Completed"
              value={totalCompleted}
              precision={2}
              prefix={<CheckCircleOutlined style={{ color: '#52c41a' }} />}
              suffix="USD"
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Paid Out"
              value={totalCompleted}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#722ed1' }} />}
              suffix="USD"
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Card>
        {/* Filters */}
        <Space style={{ marginBottom: 16, width: '100%', flexWrap: 'wrap' }}>
          <Input
            placeholder="Search payouts..."
            prefix={<SearchOutlined />}
            style={{ width: 250 }}
          />
          <Select placeholder="Status" style={{ width: 150 }}>
            <Option value="all">All Status</Option>
            <Option value="pending">Pending</Option>
            <Option value="approved">Approved</Option>
            <Option value="completed">Completed</Option>
            <Option value="rejected">Rejected</Option>
          </Select>
          <Select placeholder="Payment Method" style={{ width: 180 }}>
            <Option value="all">All Methods</Option>
            <Option value="bank">Bank Transfer</Option>
            <Option value="paypal">PayPal</Option>
            <Option value="wire">Wire Transfer</Option>
          </Select>
        </Space>

        {/* Payouts Table */}
        <Table
          columns={columns}
          dataSource={payouts}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} payout requests`,
          }}
        />
      </Card>

      {/* Payout Detail Drawer */}
      <Drawer
        title="Payout Details"
        placement="right"
        width={600}
        open={drawerVisible}
        onClose={() => setDrawerVisible(false)}
      >
        {selectedPayout && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Descriptions title="Payout Information" bordered column={1} size="small">
              <Descriptions.Item label="Payout ID">{selectedPayout.payoutId}</Descriptions.Item>
              <Descriptions.Item label="Vendor">
                {selectedPayout.vendorName} (ID: {selectedPayout.vendorId})
              </Descriptions.Item>
              <Descriptions.Item label="Amount">
                <Text strong style={{ fontSize: 18, color: '#1890ff' }}>
                  ${selectedPayout.amount.toFixed(2)}
                </Text>
              </Descriptions.Item>
              <Descriptions.Item label="Request Date">
                {new Date(selectedPayout.requestDate).toLocaleDateString()}
              </Descriptions.Item>
              <Descriptions.Item label="Payment Method">
                {selectedPayout.paymentMethod}
              </Descriptions.Item>
              <Descriptions.Item label="Account Number">
                {selectedPayout.accountNumber}
              </Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={getStatusColor(selectedPayout.status)}>
                  {selectedPayout.status.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              {selectedPayout.processedDate && (
                <Descriptions.Item label="Processed Date">
                  {new Date(selectedPayout.processedDate).toLocaleDateString()}
                </Descriptions.Item>
              )}
              {selectedPayout.transactionId && (
                <Descriptions.Item label="Transaction ID">
                  <Text copyable>{selectedPayout.transactionId}</Text>
                </Descriptions.Item>
              )}
              {selectedPayout.notes && (
                <Descriptions.Item label="Notes">{selectedPayout.notes}</Descriptions.Item>
              )}
            </Descriptions>

            <Space>
              {selectedPayout.status === 'pending' && (
                <>
                  <Button
                    type="primary"
                    icon={<CheckCircleOutlined />}
                    onClick={() => handleApprovePayout(selectedPayout)}
                  >
                    Approve
                  </Button>
                  <Button
                    danger
                    icon={<CloseCircleOutlined />}
                    onClick={() => handleRejectPayout(selectedPayout)}
                  >
                    Reject
                  </Button>
                </>
              )}
              {selectedPayout.status === 'approved' && (
                <Button
                  type="primary"
                  icon={<CheckCircleOutlined />}
                  onClick={() => handleCompletePayout(selectedPayout)}
                >
                  Mark as Completed
                </Button>
              )}
            </Space>
          </Space>
        )}
      </Drawer>

      {/* Reject Modal */}
      <Modal
        title="Reject Payout Request"
        open={rejectModalVisible}
        onCancel={() => setRejectModalVisible(false)}
        onOk={handleConfirmReject}
        okText="Reject"
        okButtonProps={{ danger: true }}
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          <Text>Provide a reason for rejecting this payout request:</Text>
          <TextArea
            rows={4}
            value={rejectionReason}
            onChange={(e) => setRejectionReason(e.target.value)}
            placeholder="Enter rejection reason..."
          />
        </Space>
      </Modal>
    </div>
  );
};

export default AdminPayoutsPage;

