    {
      title: 'Reward',
      dataIndex: 'reward',
      key: 'reward',
      render: (reward) => (
        <Tag icon={<GiftOutlined />} color="green">
          {reward}
        </Tag>
      ),
    },
    {
      title: 'Rarity',
      dataIndex: 'rarity',
      key: 'rarity',
      render: (rarity: Achievement['rarity']) => {
        const config = rarityConfig[rarity];
