        </TabPane>

        <TabPane
          tab={
            <span>
              <RocketOutlined />
              Leaderboard
            </span>
          }
          key="leaderboard"
        >
          <List
            dataSource={mockLeaderboard}
            renderItem={(item) => (
              <List.Item
                style={{
                  background: item.username === 'YOU' ? '#fff7e6' : '#fff',
                  padding: 16,
                  marginBottom: 8,
                  borderRadius: 8,
                  border: item.username === 'YOU' ? '2px solid #fa8c16' : '1px solid #f0f0f0',
                }}
              >
                <List.Item.Meta
                  avatar={
                    <div style={{ position: 'relative' }}>
                      <Avatar src={item.avatar} size={48} />
                      {item.rank <= 3 && (
                        <div
                          style={{
                            position: 'absolute',
                            top: -8,
                            right: -8,
                            width: 24,
                            height: 24,
                            borderRadius: '50%',
                            background:
                              item.rank === 1
                                ? '#ffd700'
                                : item.rank === 2
                                ? '#c0c0c0'
                                : '#cd7f32',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            fontWeight: 'bold',
                            fontSize: 12,
                          }}
                        >
                          {item.rank}
                        </div>
                      )}
                    </div>
                  }
                  title={
                    <Space>
                      {item.rank > 3 && (
                        <Text strong style={{ fontSize: 18, color: '#8c8c8c' }}>
                          #{item.rank}
                        </Text>
                      )}
                      <Text strong style={{ fontSize: 16 }}>
                        {item.username}
                      </Text>
                      {item.username === 'YOU' && (
                        <Tag color="orange">You</Tag>
                      )}
                    </Space>
                  }
                  description={
                    <Space size="large">
                      <Text>
                        <StarOutlined style={{ color: '#fa8c16' }} /> {item.points.toLocaleString()} points
                      </Text>
                      <Text>
                        <TrophyOutlined style={{ color: '#1890ff' }} /> {item.achievements} achievements
                      </Text>
                    </Space>
                  }
                />
              </List.Item>
            )}
          />
        </TabPane>
      </Tabs>
    </div>
  );
};

export default GamificationPage;

customer/price-alerts.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Modal,
  Form,
  InputNumber,
  Select,
  message,
  Switch,
  Badge,
  Image,
  Alert,
  Progress,
} from 'antd';
import {
  BellOutlined,
  DollarOutlined,
  DeleteOutlined,
  PlusOutlined,
  CheckCircleOutlined,
  PercentageOutlined,
  FallOutlined,
  RiseOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;

interface PriceAlert {
  id: number;
  productId: number;
  productName: string;
  productImage: string;
  currentPrice: number;
  originalPrice: number;
  targetPrice: number;
  alertType: 'exact' | 'below' | 'percentage';
  percentageDiscount?: number;
  status: 'active' | 'triggered' | 'expired';
  createdAt: string;
  triggeredAt?: string;
  isNotificationEnabled: boolean;
  category: string;
}

interface PriceHistory {
  date: string;
  price: number;
}

const mockAlerts: PriceAlert[] = [
  {
    id: 1,
    productId: 1,
    productName: 'Sony WH-1000XM5 Headphones',
    productImage: 'https://via.placeholder.com/60?text=Headphones',
    currentPrice: 349,
    originalPrice: 399,
    targetPrice: 299,
    alertType: 'below',
    status: 'active',
    createdAt: dayjs().subtract(15, 'days').format('YYYY-MM-DD'),
    isNotificationEnabled: true,
    category: 'Electronics',
  },
  {
    id: 2,
    productId: 2,
    productName: 'MacBook Pro 14" M4',
    productImage: 'https://via.placeholder.com/60?text=MacBook',
    currentPrice: 1899,
    originalPrice: 1999,
    targetPrice: 1799,
    alertType: 'exact',
    status: 'triggered',
    createdAt: dayjs().subtract(30, 'days').format('YYYY-MM-DD'),
    triggeredAt: dayjs().subtract(2, 'days').format('YYYY-MM-DD'),
    isNotificationEnabled: true,
    category: 'Computers',
  },
  {
    id: 3,
    productId: 3,
    productName: 'Samsung 65" QLED TV',
    productImage: 'https://via.placeholder.com/60?text=TV',
    currentPrice: 1299,
    originalPrice: 1499,
    targetPrice: 999,
    alertType: 'percentage',
    percentageDiscount: 30,
    status: 'active',
    createdAt: dayjs().subtract(7, 'days').format('YYYY-MM-DD'),
    isNotificationEnabled: true,
    category: 'Electronics',
  },
  {
    id: 4,
    productId: 4,
    productName: 'iPhone 16 Pro Max 256GB',
    productImage: 'https://via.placeholder.com/60?text=iPhone',
    currentPrice: 1199,
    originalPrice: 1299,
    targetPrice: 1099,
    alertType: 'below',
    status: 'active',
    createdAt: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    isNotificationEnabled: false,
    category: 'Smartphones',
  },
];

const PriceAlertsPage: React.FC = () => {
  const [alerts, setAlerts] = useState<PriceAlert[]>(mockAlerts);
  const [isModalVisible, setIsModalVisible] = useState<boolean>(false);
  const [form] = Form.useForm();

  const handleCreateAlert = () => {
    form.resetFields();
    setIsModalVisible(true);
  };

  const handleSubmit = (values: any) => {
    const newAlert: PriceAlert = {
      id: alerts.length + 1,
      productId: Math.floor(Math.random() * 1000),
      productName: values.productName,
      productImage: 'https://via.placeholder.com/60?text=Product',
      currentPrice: values.currentPrice,
      originalPrice: values.currentPrice,
      targetPrice: values.targetPrice,
      alertType: values.alertType,
      percentageDiscount: values.percentageDiscount,
      status: 'active',
      createdAt: dayjs().format('YYYY-MM-DD'),
      isNotificationEnabled: values.isNotificationEnabled,
      category: values.category,
    };

    setAlerts([newAlert, ...alerts]);
    message.success('Price alert created successfully! You will be notified when the price drops.');
    setIsModalVisible(false);
    form.resetFields();
  };

  const handleDelete = (id: number) => {
    Modal.confirm({
      title: 'Delete Price Alert',
      content: 'Are you sure you want to delete this price alert?',
      okText: 'Delete',
      okType: 'danger',
      onOk: () => {
        setAlerts(alerts.filter((a) => a.id !== id));
        message.success('Price alert deleted successfully');
      },
    });
  };

  const handleToggleNotification = (id: number) => {
    setAlerts(
      alerts.map((a) =>
        a.id === id ? { ...a, isNotificationEnabled: !a.isNotificationEnabled } : a
      )
    );
  };

  const columns: ColumnsType<PriceAlert> = [
    {
      title: 'Product',
      key: 'product',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <Image src={record.productImage} width={60} height={60} preview={false} />
          <div>
            <Text strong>{record.productName}</Text>
            <div>
              <Tag color="blue" style={{ margin: 0 }}>
                {record.category}
              </Tag>
            </div>
          </div>
        </div>
      ),
    },
    {
      title: 'Current Price',
      key: 'currentPrice',
      render: (_, record) => {
        const discount = ((record.originalPrice - record.currentPrice) / record.originalPrice) * 100;
        return (
          <div>
            <div>
              <Text strong style={{ fontSize: 16, color: '#1890ff' }}>
                ${record.currentPrice}
              </Text>
            </div>
            {discount > 0 && (
              <div>
                <Text delete type="secondary" style={{ fontSize: 12 }}>
                  ${record.originalPrice}
                </Text>
                <Tag color="green" style={{ marginLeft: 4 }}>
                  -{discount.toFixed(0)}%
                </Tag>
              </div>
            )}
          </div>
        );
      },
    },
    {
      title: 'Target Price',
      key: 'targetPrice',
      render: (_, record) => {
        const neededDrop = ((record.currentPrice - record.targetPrice) / record.currentPrice) * 100;
        return (
          <div>
            <div>
              <Text strong style={{ fontSize: 16, color: '#52c41a' }}>
                ${record.targetPrice}
              </Text>
            </div>
            {record.alertType === 'percentage' && record.percentageDiscount && (
              <Tag color="orange" icon={<PercentageOutlined />}>
                {record.percentageDiscount}% off
              </Tag>
            )}
            {record.status === 'active' && neededDrop > 0 && (
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Needs {neededDrop.toFixed(0)}% drop
                </Text>
              </div>
            )}
          </div>
        );
      },
    },
    {
      title: 'Alert Type',
      dataIndex: 'alertType',
      key: 'alertType',
      render: (type: PriceAlert['alertType']) => {
        const config: Record<PriceAlert['alertType'], { color: string; text: string }> = {
          exact: { color: 'blue', text: 'Exact Price' },
          below: { color: 'green', text: 'Below Price' },
          percentage: { color: 'orange', text: 'Percentage Off' },
        };
        return <Tag color={config[type].color}>{config[type].text}</Tag>;
      },
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: PriceAlert['status']) => {
        const config: Record<PriceAlert['status'], { color: string; text: string; icon: React.ReactNode }> = {
          active: { color: 'blue', text: 'Active', icon: <BellOutlined /> },
          triggered: { color: 'green', text: 'Triggered', icon: <CheckCircleOutlined /> },
          expired: { color: 'default', text: 'Expired', icon: null },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Triggered', value: 'triggered' },
        { text: 'Expired', value: 'expired' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Created',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date) => <Text>{dayjs(date).format('MMM DD, YYYY')}</Text>,
    },
    {
      title: 'Notifications',
      key: 'notifications',
      render: (_, record) => (
        <Switch
          checked={record.isNotificationEnabled}
          onChange={() => handleToggleNotification(record.id)}
          checkedChildren="On"
          unCheckedChildren="Off"
        />
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          {record.status === 'triggered' && (
            <Button type="primary" size="small">
              Buy Now
            </Button>
          )}
          <Button
            type="link"
            danger
            size="small"
            icon={<DeleteOutlined />}
            onClick={() => handleDelete(record.id)}
          >
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  const activeAlerts = alerts.filter((a) => a.status === 'active').length;
  const triggeredAlerts = alerts.filter((a) => a.status === 'triggered').length;
  const totalSavings = alerts
    .filter((a) => a.status === 'triggered')
    .reduce((sum, a) => sum + (a.originalPrice - a.currentPrice), 0);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <BellOutlined style={{ color: '#fa8c16' }} /> Price Alerts
          </Title>
          <Paragraph type="secondary">
            Get notified when your favorite products drop in price
          </Paragraph>
        </Col>
        <Col>
          <Button type="primary" icon={<PlusOutlined />} onClick={handleCreateAlert}>
            Create Price Alert
          </Button>
        </Col>
      </Row>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div
                style={{
                  width: 48,
                  height: 48,
                  background: '#e6f7ff',
                  borderRadius: 8,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <BellOutlined style={{ fontSize: 24, color: '#1890ff' }} />
              </div>
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Active Alerts
                </Text>
                <div>
                  <Text strong style={{ fontSize: 24 }}>
                    {activeAlerts}
                  </Text>
                </div>
              </div>
            </div>
          </Card>
        </Col>

        <Col xs={24} sm={8}>
          <Card>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div
                style={{
                  width: 48,
                  height: 48,
                  background: '#f6ffed',
                  borderRadius: 8,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <CheckCircleOutlined style={{ fontSize: 24, color: '#52c41a' }} />
              </div>
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Triggered Alerts
                </Text>
                <div>
                  <Text strong style={{ fontSize: 24 }}>
                    {triggeredAlerts}
                  </Text>
                </div>
              </div>
            </div>
          </Card>
        </Col>

        <Col xs={24} sm={8}>
          <Card>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div
                style={{
                  width: 48,
                  height: 48,
                  background: '#fff7e6',
                  borderRadius: 8,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <DollarOutlined style={{ fontSize: 24, color: '#fa8c16' }} />
              </div>
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Potential Savings
                </Text>
                <div>
                  <Text strong style={{ fontSize: 24 }}>
                    ${totalSavings}
                  </Text>
                </div>
              </div>
            </div>
          </Card>
        </Col>
      </Row>

      {triggeredAlerts > 0 && (
        <Alert
          message="Price Alert Triggered!"
          description={`${triggeredAlerts} product${triggeredAlerts > 1 ? 's have' : ' has'} reached your target price. Check them out before the price changes!`}
          type="success"
          showIcon
          icon={<CheckCircleOutlined />}
          style={{ marginBottom: 16 }}
          action={
            <Button type="primary" size="small">
              View Deals
            </Button>
          }
        />
      )}

      <Card title="My Price Alerts">
        <Table columns={columns} dataSource={alerts} rowKey="id" scroll={{ x: 1200 }} />
      </Card>

      <Modal
        title="Create Price Alert"
        open={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        footer={null}
        width={600}
      >
        <Alert
          message="How Price Alerts Work"
          description={
            <ul style={{ marginBottom: 0, paddingLeft: 20 }}>
              <li>Set your target price for any product</li>
              <li>We'll monitor the price and notify you when it drops</li>
              <li>Get email and push notifications instantly</li>
              <li>Alerts expire after 90 days if not triggered</li>
            </ul>
          }
          type="info"
          showIcon
          style={{ marginBottom: 24 }}
        />

        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item
            label="Product Name"
            name="productName"
            rules={[{ required: true, message: 'Please enter product name' }]}
          >
            <Select
              placeholder="Search for a product"
              showSearch
              size="large"
              filterOption={false}
            >
              <Option value="Sony WH-1000XM5 Headphones">Sony WH-1000XM5 Headphones</Option>
              <Option value="MacBook Pro 14 M4">MacBook Pro 14" M4</Option>
              <Option value="iPhone 16 Pro Max">iPhone 16 Pro Max</Option>
              <Option value="Samsung 65 QLED TV">Samsung 65" QLED TV</Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Category"
            name="category"
            rules={[{ required: true, message: 'Please select category' }]}
          >
            <Select placeholder="Select category" size="large">
              <Option value="Electronics">Electronics</Option>
              <Option value="Computers">Computers</Option>
              <Option value="Smartphones">Smartphones</Option>
              <Option value="Home & Garden">Home & Garden</Option>
            </Select>
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Current Price"
                name="currentPrice"
                rules={[{ required: true, message: 'Please enter current price' }]}
              >
                <InputNumber
                  min={0}
                  style={{ width: '100%' }}
                  prefix="$"
                  size="large"
                  placeholder="299.99"
                />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Target Price"
                name="targetPrice"
                rules={[{ required: true, message: 'Please enter target price' }]}
              >
                <InputNumber
                  min={0}
                  style={{ width: '100%' }}
                  prefix="$"
                  size="large"
                  placeholder="249.99"
                />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            label="Alert Type"
            name="alertType"
            rules={[{ required: true, message: 'Please select alert type' }]}
            initialValue="below"
          >
            <Select size="large">
              <Option value="exact">Notify at exact price</Option>
              <Option value="below">Notify when price goes below</Option>
              <Option value="percentage">Notify at percentage discount</Option>
            </Select>
          </Form.Item>

          <Form.Item
            noStyle
            shouldUpdate={(prevValues, currentValues) => prevValues.alertType !== currentValues.alertType}
          >
            {({ getFieldValue }) =>
              getFieldValue('alertType') === 'percentage' ? (
                <Form.Item
                  label="Target Discount Percentage"
                  name="percentageDiscount"
                  rules={[{ required: true, message: 'Please enter percentage' }]}
                >
                  <InputNumber
                    min={1}
                    max={99}
                    style={{ width: '100%' }}
                    suffix="%"
                    size="large"
                    placeholder="20"
                  />
                </Form.Item>
              ) : null
            }
          </Form.Item>

          <Form.Item
            name="isNotificationEnabled"
            valuePropName="checked"
            initialValue={true}
          >
            <Space>
              <Switch defaultChecked />
              <Text>Enable email and push notifications</Text>
            </Space>
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" size="large" icon={<BellOutlined />}>
                Create Alert
              </Button>
              <Button size="large" onClick={() => setIsModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default PriceAlertsPage;

customer/social-login.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Form,
  Input,
  Select,
  Checkbox,
  Radio,
  message,
  Divider,
  Alert,
  Steps,
  Modal,
} from 'antd';
import {
  GoogleOutlined,
  FacebookOutlined,
  TwitterOutlined,
  AppleOutlined,
  LinkedinOutlined,
  GithubOutlined,
  LockOutlined,
  UserOutlined,
  MailOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;
const { Step } = Steps;

interface SocialProvider {
  id: string;
  name: string;
  icon: React.ReactNode;
  connected: boolean;
  connectedEmail?: string;
  connectedAt?: string;
}

const SocialLoginPage: React.FC = () => {
  const [currentStep, setCurrentStep] = useState<number>(0);
  const [selectedProvider, setSelectedProvider] = useState<string>('');
  const [form] = Form.useForm();
  const [socialProviders, setSocialProviders] = useState<SocialProvider[]>([
    {
      id: 'google',
      name: 'Google',
      icon: <GoogleOutlined style={{ fontSize: 24 }} />,
      connected: true,
      connectedEmail: 'user@gmail.com',
      connectedAt: '2024-01-15',
    },
    {
      id: 'facebook',
      name: 'Facebook',
      icon: <FacebookOutlined style={{ fontSize: 24 }} />,
      connected: false,
    },
    {
      id: 'twitter',
      name: 'Twitter',
      icon: <TwitterOutlined style={{ fontSize: 24 }} />,
      connected: false,
    },
    {
      id: 'apple',
      name: 'Apple',
      icon: <AppleOutlined style={{ fontSize: 24 }} />,
      connected: false,
    },
    {
      id: 'linkedin',
      name: 'LinkedIn',
      icon: <LinkedinOutlined style={{ fontSize: 24 }} />,
      connected: true,
      connectedEmail: 'user@linkedin.com',
      connectedAt: '2024-02-20',
    },
    {
      id: 'github',
      name: 'GitHub',
      icon: <GithubOutlined style={{ fontSize: 24 }} />,
      connected: false,
    },
  ]);

  const handleConnectProvider = (providerId: string) => {
    setSelectedProvider(providerId);
    setCurrentStep(1);
  };

  const handleDisconnectProvider = (providerId: string) => {
    Modal.confirm({
      title: 'Disconnect Account',
      content: `Are you sure you want to disconnect your ${providerId} account?`,
      onOk: () => {
        setSocialProviders(
          socialProviders.map((p) =>
            p.id === providerId
              ? { ...p, connected: false, connectedEmail: undefined, connectedAt: undefined }
              : p
          )
        );
        message.success(`${providerId} account disconnected`);
      },
    });
  };

  const handleLoginWithProvider = (provider: string) => {
    message.loading(`Connecting to ${provider}...`, 2);
    setTimeout(() => {
      message.success(`Successfully logged in with ${provider}`);
      // Simulate connection
      setSocialProviders(
        socialProviders.map((p) =>
          p.id === provider
            ? {
                ...p,
                connected: true,
                connectedEmail: `user@${provider}.com`,
                connectedAt: new Date().toISOString().split('T')[0],
              }
            : p
        )
      );
      setCurrentStep(0);
    }, 2000);
  };

  const handleCompleteSetup = (values: any) => {
    console.log('Setup values:', values);
    handleLoginWithProvider(selectedProvider);
  };

  const getProviderColor = (providerId: string): string => {
    const colors: Record<string, string> = {
      google: '#DB4437',
      facebook: '#1877F2',
      twitter: '#1DA1F2',
      apple: '#000000',
      linkedin: '#0A66C2',
      github: '#181717',
    };
    return colors[providerId] || '#1890ff';
  };

  const connectedCount = socialProviders.filter((p) => p.connected).length;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <LockOutlined style={{ color: '#1890ff' }} /> Social Login
        </Title>
        <Paragraph type="secondary">
          Connect your social media accounts for quick and secure login
        </Paragraph>
      </div>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Connected Accounts</Text>
              <Text strong style={{ fontSize: 24, color: '#52c41a' }}>
                {connectedCount}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Available Providers</Text>
              <Text strong style={{ fontSize: 24, color: '#1890ff' }}>
                {socialProviders.length}
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Login Method</Text>
              <Text strong style={{ fontSize: 16 }}>
                Social + Email
              </Text>
            </Space>
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Space direction="vertical" size={0}>
              <Text type="secondary">Security Level</Text>
              <Text strong style={{ fontSize: 16, color: '#52c41a' }}>
                High
              </Text>
            </Space>
          </Card>
        </Col>
      </Row>

      {currentStep === 0 ? (
        <Row gutter={16}>
          <Col xs={24} lg={16}>
            <Card title="Social Login Providers">
              <Alert
                message="Quick & Secure Login"
                description="Connect your social accounts to log in quickly without remembering passwords. Your credentials are never shared with us."
                type="info"
                showIcon
                style={{ marginBottom: 24 }}
              />

              <Row gutter={[16, 16]}>
                {socialProviders.map((provider) => (
                  <Col xs={24} sm={12} key={provider.id}>
                    <Card
                      hoverable={!provider.connected}
                      style={{
                        border: provider.connected ? `2px solid ${getProviderColor(provider.id)}` : '1px solid #d9d9d9',
                      }}
                    >
                      <Space direction="vertical" size="large" style={{ width: '100%' }}>
                        <Row justify="space-between" align="middle">
                          <Col>
                            <Space>
                              <div
                                style={{
                                  width: 48,
                                  height: 48,
                                  borderRadius: 8,
                                  background: getProviderColor(provider.id),
                                  color: 'white',
                                  display: 'flex',
                                  alignItems: 'center',
                                  justifyContent: 'center',
                                }}
                              >
                                {provider.icon}
                              </div>
                              <div>
                                <Text strong style={{ fontSize: 16 }}>{provider.name}</Text>
                                {provider.connected && (
                                  <div>
                                    <Text type="success" style={{ fontSize: 12 }}>
                                       Connected
                                    </Text>
                                  </div>
                                )}
                              </div>
                            </Space>
                          </Col>
                        </Row>

                        {provider.connected ? (
                          <div>
                            <Space direction="vertical" size="small" style={{ width: '100%' }}>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                Email: {provider.connectedEmail}
                              </Text>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                Connected on: {provider.connectedAt}
                              </Text>
                            </Space>
                            <Divider style={{ margin: '12px 0' }} />
                            <Button
                              danger
                              block
                              onClick={() => handleDisconnectProvider(provider.id)}
                            >
                              Disconnect
                            </Button>
                          </div>
                        ) : (
                          <Button
                            type="primary"
                            block
                            style={{
                              background: getProviderColor(provider.id),
                              borderColor: getProviderColor(provider.id),
                            }}
                            onClick={() => handleConnectProvider(provider.id)}
                          >
                            Connect {provider.name}
                          </Button>
                        )}
                      </Space>
                    </Card>
                  </Col>
                ))}
              </Row>
            </Card>
          </Col>

          <Col xs={24} lg={8}>
            <Card title="Benefits" style={{ marginBottom: 16 }}>
              <Space direction="vertical" size="middle" style={{ width: '100%' }}>
                <div>
                  <Text strong> Quick Login</Text>
                  <div>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      Log in with just one click using your social accounts
                    </Text>
                  </div>
                </div>

                <div>
                  <Text strong> Enhanced Security</Text>
                  <div>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      Leverage the security features of major platforms
                    </Text>
                  </div>
                </div>

                <div>
                  <Text strong> No Password Reset</Text>
                  <div>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      Never worry about forgetting your password again
                    </Text>
                  </div>
                </div>

                <div>
                  <Text strong> Auto-fill Profile</Text>
                  <div>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      Your profile information is automatically filled from your social account
                    </Text>
                  </div>
                </div>

                <div>
                  <Text strong> Privacy Protected</Text>
                  <div>
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      We never post to your social media or access private data
                    </Text>
                  </div>
                </div>
              </Space>
            </Card>

            <Card title="Security Settings">
              <Space direction="vertical" size="middle" style={{ width: '100%' }}>
                <div>
                  <Checkbox defaultChecked>Require email verification</Checkbox>
                </div>
                <div>
                  <Checkbox defaultChecked>Enable two-factor authentication</Checkbox>
                </div>
                <div>
                  <Checkbox>Remember me on this device</Checkbox>
                </div>
                <div>
                  <Checkbox defaultChecked>Notify me of new login attempts</Checkbox>
                </div>
              </Space>
            </Card>
          </Col>
        </Row>
      ) : (
        <Row justify="center">
          <Col xs={24} md={16} lg={12}>
            <Card>
              <Steps current={currentStep} style={{ marginBottom: 32 }}>
                <Step title="Choose Provider" />
                <Step title="Authorize" />
                <Step title="Complete Setup" />
              </Steps>

              {currentStep === 1 && (
                <div style={{ textAlign: 'center', padding: '40px 0' }}>
                  <div
                    style={{
                      width: 80,
                      height: 80,
                      borderRadius: 16,
                      background: getProviderColor(selectedProvider),
                      color: 'white',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      margin: '0 auto 24px',
                    }}
                  >
                    {socialProviders.find((p) => p.id === selectedProvider)?.icon}
                  </div>

                  <Title level={4}>
                    Connect to {socialProviders.find((p) => p.id === selectedProvider)?.name}
                  </Title>

                  <Paragraph type="secondary">
                    You will be redirected to {socialProviders.find((p) => p.id === selectedProvider)?.name} to authorize
                    access. We will only access your basic profile information.
                  </Paragraph>

                  <Alert
                    message="What we'll access"
                    description={
                      <ul style={{ textAlign: 'left', marginTop: 8, paddingLeft: 20 }}>
                        <li>Your name and profile picture</li>
                        <li>Your email address</li>
                      </ul>
                    }
                    type="info"
                    showIcon
                    style={{ marginBottom: 24, textAlign: 'left' }}
                  />

                  <Form layout="vertical" onFinish={handleCompleteSetup}>
                    <Form.Item
                      label="Account Permissions"
                      name="permissions"
                      rules={[{ required: true, message: 'Please agree to the permissions' }]}
                    >
                      <Checkbox.Group style={{ display: 'flex', flexDirection: 'column' }}>
                        <Checkbox value="profile" disabled defaultChecked>
                          Access basic profile information
                        </Checkbox>
                        <Checkbox value="email" disabled defaultChecked>
                          Access email address
                        </Checkbox>
                      </Checkbox.Group>
                    </Form.Item>

                    <Form.Item
                      name="agree"
                      valuePropName="checked"
                      rules={[
                        {
                          validator: (_, value) =>
                            value ? Promise.resolve() : Promise.reject(new Error('Please accept the terms')),
                        },
                      ]}
                    >
                      <Checkbox>
                        I agree to the terms and conditions and privacy policy
                      </Checkbox>
                    </Form.Item>

                    <Space style={{ width: '100%', justifyContent: 'center' }}>
                      <Button onClick={() => setCurrentStep(0)}>Cancel</Button>
                      <Button
                        type="primary"
                        htmlType="submit"
                        size="large"
                        style={{
                          background: getProviderColor(selectedProvider),
                          borderColor: getProviderColor(selectedProvider),
                        }}
                      >
                        Authorize & Connect
                      </Button>
                    </Space>
                  </Form>
                </div>
              )}
            </Card>
          </Col>
        </Row>
      )}
    </div>
  );
};

export default SocialLoginPage;

customer/store-credit.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Modal,
  Form,
  Select,
  Input,
  InputNumber,
  DatePicker,
  message,
  Statistic,
  Progress,
  Alert,
  Divider,
} from 'antd';
import {
  CreditCardOutlined,
  PlusOutlined,
  DeleteOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  WarningOutlined,
  DollarOutlined,
  CalendarOutlined,
  ReloadOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TextArea } = Input;

interface StoreCredit {
  id: number;
  amount: number;
  source: 'refund' | 'promotion' | 'loyalty' | 'compensation' | 'gift' | 'return';
  description: string;
  expiryDate: string;
  isExpired: boolean;
  used: boolean;
  usedAmount: number;
  remainingAmount: number;
  orderId?: string;
  createdAt: string;
  usedAt?: string;
}

interface CreditTransaction {
  id: number;
  type: 'earned' | 'spent';
  amount: number;
  description: string;
  timestamp: string;
  orderId?: string;
  balance: number;
}

const mockStoreCredits: StoreCredit[] = [
  {
    id: 1,
    amount: 50,
    source: 'refund',
    description: 'Refund for order #12345',
    expiryDate: dayjs().add(90, 'days').format('YYYY-MM-DD'),
    isExpired: false,
    used: false,
    usedAmount: 0,
    remainingAmount: 50,
    orderId: '#12345',
    createdAt: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
  },
  {
    id: 2,
    amount: 25,
    source: 'promotion',
    description: 'Welcome bonus - New customer promotion',
    expiryDate: dayjs().add(30, 'days').format('YYYY-MM-DD'),
    isExpired: false,
    used: true,
    usedAmount: 15,
    remainingAmount: 10,
    createdAt: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    usedAt: dayjs().subtract(3, 'days').format('YYYY-MM-DD'),
  },
  {
    id: 3,
    amount: 100,
    source: 'loyalty',
    description: 'Loyalty reward - Platinum member bonus',
    expiryDate: dayjs().add(180, 'days').format('YYYY-MM-DD'),
    isExpired: false,
    used: false,
    usedAmount: 0,
    remainingAmount: 100,
    createdAt: dayjs().subtract(15, 'days').format('YYYY-MM-DD'),
  },
  {
    id: 4,
    amount: 75,
    source: 'compensation',
    description: 'Apology credit for delayed delivery',
    expiryDate: dayjs().add(60, 'days').format('YYYY-MM-DD'),
    isExpired: false,
    used: true,
    usedAmount: 75,
    remainingAmount: 0,
    orderId: '#12348',
    createdAt: dayjs().subtract(20, 'days').format('YYYY-MM-DD'),
    usedAt: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
  },
  {
    id: 5,
    amount: 20,
    source: 'gift',
    description: 'Birthday gift from Groow',
    expiryDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    isExpired: true,
    used: false,
    usedAmount: 0,
    remainingAmount: 0,
    createdAt: dayjs().subtract(40, 'days').format('YYYY-MM-DD'),
  },
];

const mockTransactions: CreditTransaction[] = [
  {
    id: 1,
    type: 'earned',
    amount: 50,
    description: 'Refund for order #12345',
    timestamp: dayjs().subtract(5, 'days').toISOString(),
    orderId: '#12345',
    balance: 235,
  },
  {
    id: 2,
    type: 'spent',
    amount: 15,
    description: 'Applied to order #12346',
    timestamp: dayjs().subtract(3, 'days').toISOString(),
    orderId: '#12346',
    balance: 220,
  },
  {
    id: 3,
    type: 'earned',
    amount: 100,
    description: 'Loyalty reward - Platinum bonus',
    timestamp: dayjs().subtract(15, 'days').toISOString(),
    balance: 235,
  },
  {
    id: 4,
    type: 'spent',
    amount: 75,
    description: 'Applied to order #12348',
    timestamp: dayjs().subtract(10, 'days').toISOString(),
    orderId: '#12348',
    balance: 160,
  },
];

const StoreCreditPage: React.FC = () => {
  const [credits, setCredits] = useState<StoreCredit[]>(mockStoreCredits);
  const [transactions] = useState<CreditTransaction[]>(mockTransactions);
  const [isRequestModalVisible, setIsRequestModalVisible] = useState<boolean>(false);
  const [form] = Form.useForm();

  const handleRequestCredit = (values: any) => {
    message.success(
      'Store credit request submitted. Our team will review and respond within 2-3 business days.'
    );
    setIsRequestModalVisible(false);
    form.resetFields();
  };

  const totalCredits = credits.reduce((sum, c) => sum + c.amount, 0);
  const availableCredits = credits
    .filter((c) => !c.isExpired)
    .reduce((sum, c) => sum + c.remainingAmount, 0);
  const usedCredits = credits.reduce((sum, c) => sum + c.usedAmount, 0);
  const expiredCredits = credits
    .filter((c) => c.isExpired)
    .reduce((sum, c) => sum + c.remainingAmount, 0);

  const creditColumns: ColumnsType<StoreCredit> = [
    {
      title: 'Credit Details',
      key: 'details',
      render: (_, record) => {
        const sourceConfig = {
          refund: { color: 'blue', text: 'Refund' },
          promotion: { color: 'green', text: 'Promotion' },
          loyalty: { color: 'purple', text: 'Loyalty' },
          compensation: { color: 'orange', text: 'Compensation' },
          gift: { color: 'magenta', text: 'Gift' },
          return: { color: 'cyan', text: 'Return' },
        };
        const config = sourceConfig[record.source];
        return (
          <div>
            <div>
              <Text strong>{record.description}</Text>
            </div>
            <Space style={{ marginTop: 4 }}>
              <Tag color={config.color}>{config.text}</Tag>
              {record.orderId && <Text type="secondary" style={{ fontSize: 12 }}>Order: {record.orderId}</Text>}
            </Space>
          </div>
        );
      },
    },
    {
      title: 'Amount',
      key: 'amount',
      render: (_, record) => (
        <div>
          <div>
            <Text strong style={{ fontSize: 16, color: '#1890ff' }}>
              ${record.amount}
            </Text>
          </div>
          {record.usedAmount > 0 && (
            <div style={{ marginTop: 4 }}>
              <Text type="secondary" style={{ fontSize: 12 }}>
                Used: ${record.usedAmount}
              </Text>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Remaining',
      dataIndex: 'remainingAmount',
      key: 'remaining',
      render: (amount, record) => (
        <div>
          <Text
            strong
            style={{
              fontSize: 16,
              color: record.isExpired ? '#8c8c8c' : '#52c41a',
            }}
          >
            ${amount}
          </Text>
          {!record.isExpired && amount > 0 && (
            <div style={{ marginTop: 4 }}>
              <Progress
                percent={(amount / record.amount) * 100}
                size="small"
                showInfo={false}
                strokeColor="#52c41a"
              />
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Expiry Date',
      key: 'expiry',
      render: (_, record) => {
        const daysLeft = dayjs(record.expiryDate).diff(dayjs(), 'days');
        return (
          <div>
            <div>
              <Text>{dayjs(record.expiryDate).format('MMM DD, YYYY')}</Text>
            </div>
            {!record.isExpired && (
              <Text
                type={daysLeft < 7 ? 'danger' : 'secondary'}
                style={{ fontSize: 12 }}
              >
                {daysLeft > 0 ? `${daysLeft} days left` : 'Expires today'}
              </Text>
            )}
          </div>
        );
      },
    },
    {
      title: 'Status',
      key: 'status',
      render: (_, record) => {
        if (record.isExpired) {
          return (
            <Tag color="default" icon={<ClockCircleOutlined />}>
              Expired
            </Tag>
          );
        }
        if (record.remainingAmount === 0) {
          return (
            <Tag color="success" icon={<CheckCircleOutlined />}>
              Fully Used
            </Tag>
          );
        }
        if (record.usedAmount > 0) {
          return (
            <Tag color="processing" icon={<ReloadOutlined />}>
              Partially Used
            </Tag>
          );
        }
        return (
          <Tag color="green" icon={<CheckCircleOutlined />}>
            Available
          </Tag>
        );
      },
      filters: [
        { text: 'Available', value: 'available' },
        { text: 'Partially Used', value: 'partial' },
        { text: 'Fully Used', value: 'used' },
        { text: 'Expired', value: 'expired' },
      ],
      onFilter: (value, record) => {
        if (value === 'expired') return record.isExpired;
        if (value === 'used') return record.remainingAmount === 0 && !record.isExpired;
        if (value === 'partial') return record.usedAmount > 0 && record.remainingAmount > 0;
        return record.remainingAmount > 0 && !record.isExpired;
      },
    },
    {
      title: 'Created',
      dataIndex: 'createdAt',
      key: 'created',
      render: (date) => dayjs(date).format('MMM DD, YYYY'),
    },
  ];

  const transactionColumns: ColumnsType<CreditTransaction> = [
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: 'earned' | 'spent') => (
        <Tag color={type === 'earned' ? 'green' : 'red'}>
          {type === 'earned' ? 'Earned' : 'Spent'}
        </Tag>
      ),
      filters: [
        { text: 'Earned', value: 'earned' },
        { text: 'Spent', value: 'spent' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Description',
      dataIndex: 'description',
      key: 'description',
      render: (desc, record) => (
        <div>
          <Text>{desc}</Text>
          {record.orderId && (
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                {record.orderId}
              </Text>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Amount',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount, record) => (
        <Text
          strong
          style={{
            fontSize: 16,
            color: record.type === 'earned' ? '#52c41a' : '#ff4d4f',
          }}
        >
          {record.type === 'earned' ? '+' : '-'}${amount}
        </Text>
      ),
    },
    {
      title: 'Balance',
      dataIndex: 'balance',
      key: 'balance',
      render: (balance) => (
        <Text strong style={{ fontSize: 16, color: '#1890ff' }}>
          ${balance}
        </Text>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'timestamp',
      key: 'timestamp',
      render: (date) => dayjs(date).format('MMM DD, YYYY HH:mm'),
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <CreditCardOutlined style={{ color: '#52c41a' }} /> Store Credit
          </Title>
          <Paragraph type="secondary">
            Manage your store credits and view transaction history
          </Paragraph>
        </Col>
        <Col>
          <Button
            type="primary"
            icon={<PlusOutlined />}
            onClick={() => setIsRequestModalVisible(true)}
          >
            Request Credit
          </Button>
        </Col>
      </Row>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Available Credit"
              value={availableCredits}
              precision={2}
              valueStyle={{ color: '#52c41a' }}
              prefix={<DollarOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Earned"
              value={totalCredits}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Used"
              value={usedCredits}
              prefix="$"
              precision={2}
              valueStyle={{ color: '#fa8c16' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Expired"
              value={expiredCredits}
              precision={2}
              valueStyle={{ color: '#8c8c8c' }}
              prefix={<WarningOutlined />}
            />
          </Card>
        </Col>
      </Row>

      {availableCredits > 0 && (
        <Alert
          message={`You have $${availableCredits.toFixed(2)} in available store credit!`}
          description="Your store credit will be automatically applied at checkout. Credits with the earliest expiry dates will be used first."
          type="success"
          showIcon
          icon={<CheckCircleOutlined />}
          style={{ marginBottom: 16 }}
        />
      )}

      {expiredCredits > 0 && (
        <Alert
          message={`$${expiredCredits.toFixed(2)} in store credit has expired`}
          description="Store credits typically expire 90 days after being issued. Make sure to use your credits before they expire!"
          type="warning"
          showIcon
          icon={<WarningOutlined />}
          closable
          style={{ marginBottom: 16 }}
        />
      )}

      <Card title="Store Credits" style={{ marginBottom: 24 }}>
        <Table
          columns={creditColumns}
          dataSource={credits}
          rowKey="id"
          pagination={{ pageSize: 10 }}
        />
      </Card>

      <Card title="Transaction History">
        <Table
          columns={transactionColumns}
          dataSource={transactions}
          rowKey="id"
          pagination={{ pageSize: 10 }}
        />
      </Card>

      <Modal
        title="Request Store Credit"
        open={isRequestModalVisible}
        onCancel={() => setIsRequestModalVisible(false)}
        footer={null}
        width={600}
      >
        <Alert
          message="When to Request Store Credit"
          description={
            <ul style={{ marginBottom: 0, paddingLeft: 20 }}>
              <li>Product arrived damaged or defective</li>
              <li>Wrong item was delivered</li>
              <li>Order was delayed significantly</li>
              <li>Product didn't match description</li>
              <li>Poor customer service experience</li>
            </ul>
          }
          type="info"
          showIcon
          style={{ marginBottom: 24 }}
        />

        <Form form={form} layout="vertical" onFinish={handleRequestCredit}>
          <Form.Item
            label="Reason"
            name="reason"
            rules={[{ required: true, message: 'Please select a reason' }]}
          >
            <Select placeholder="Select reason for credit request" size="large">
              <Option value="damaged">Damaged Product</Option>
              <Option value="wrong">Wrong Item Delivered</Option>
              <Option value="delay">Significant Delay</Option>
              <Option value="mismatch">Product Mismatch</Option>
              <Option value="service">Customer Service Issue</Option>
              <Option value="other">Other</Option>
            </Select>
          </Form.Item>

          <Form.Item
            label="Related Order"
            name="orderId"
            rules={[{ required: true, message: 'Please enter order ID' }]}
          >
            <Input placeholder="#12345" size="large" />
          </Form.Item>

          <Form.Item
            label="Requested Amount"
            name="amount"
            rules={[{ required: true, message: 'Please enter amount' }]}
          >
            <InputNumber
              min={1}
              max={1000}
              style={{ width: '100%' }}
              prefix="$"
              size="large"
              placeholder="50.00"
            />
          </Form.Item>

          <Form.Item
            label="Description"
            name="description"
            rules={[
              { required: true, message: 'Please provide details' },
              { min: 20, message: 'Please provide at least 20 characters' },
            ]}
          >
            <TextArea
              rows={4}
              placeholder="Please describe the issue in detail..."
              maxLength={500}
              showCount
            />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" size="large" icon={<PlusOutlined />}>
                Submit Request
              </Button>
              <Button size="large" onClick={() => setIsRequestModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default StoreCreditPage;

customer/voice-search.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Tag,
  List,
  Avatar,
  Badge,
  Progress,
  Alert,
  Divider,
  Switch,
  Select,
  Tooltip,
  Modal,
  Empty,
  Statistic,
} from 'antd';
import {
  AudioOutlined,
  AudioMutedOutlined,
  SearchOutlined,
  DeleteOutlined,
  HistoryOutlined,
  SoundOutlined,
  ThunderboltOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  SettingOutlined,
  QuestionCircleOutlined,
  StarOutlined,
  ShoppingCartOutlined,
  DollarOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;

interface VoiceSearchResult {
  id: number;
  productName: string;
  productImage: string;
  category: string;
  price: number;
  rating: number;
  reviews: number;
  inStock: boolean;
  relevanceScore: number;
}

interface SearchHistory {
  id: number;
  query: string;
  timestamp: string;
  resultsCount: number;
  language: string;
}

interface VoiceSettings {
  language: string;
  autoSearch: boolean;
  soundEffects: boolean;
  saveHistory: boolean;
}

const mockSearchResults: VoiceSearchResult[] = [
  {
    id: 1,
    productName: 'Wireless Bluetooth Headphones',
    productImage: 'https://via.placeholder.com/120x120?text=Headphones',
    category: 'Electronics',
    price: 79.99,
    rating: 4.5,
    reviews: 342,
    inStock: true,
    relevanceScore: 95,
  },
  {
    id: 2,
    productName: 'Premium Noise Cancelling Headphones',
    productImage: 'https://via.placeholder.com/120x120?text=Premium',
    category: 'Electronics',
    price: 299.99,
    rating: 4.8,
    reviews: 1245,
    inStock: true,
    relevanceScore: 92,
  },
  {
    id: 3,
    productName: 'Gaming Headset with Microphone',
    productImage: 'https://via.placeholder.com/120x120?text=Gaming',
    category: 'Gaming',
    price: 59.99,
    rating: 4.3,
    reviews: 876,
    inStock: true,
    relevanceScore: 88,
  },
  {
    id: 4,
    productName: 'Sports Wireless Earbuds',
    productImage: 'https://via.placeholder.com/120x120?text=Sports',
    category: 'Sports',
    price: 39.99,
    rating: 4.2,
    reviews: 523,
    inStock: false,
    relevanceScore: 85,
  },
];

const mockSearchHistory: SearchHistory[] = [
  {
    id: 1,
    query: 'wireless headphones',
    timestamp: new Date(Date.now() - 3600000).toISOString(),
    resultsCount: 45,
    language: 'en-US',
  },
  {
    id: 2,
    query: 'laptop bag',
    timestamp: new Date(Date.now() - 7200000).toISOString(),
    resultsCount: 32,
    language: 'en-US',
  },
  {
    id: 3,
    query: 'gaming mouse',
    timestamp: new Date(Date.now() - 86400000).toISOString(),
    resultsCount: 28,
    language: 'en-US',
  },
];

const VoiceSearchPage: React.FC = () => {
  const [isListening, setIsListening] = useState<boolean>(false);
  const [transcript, setTranscript] = useState<string>('');
  const [searchResults, setSearchResults] = useState<VoiceSearchResult[]>([]);
  const [searchHistory, setSearchHistory] = useState<SearchHistory[]>(mockSearchHistory);
  const [isSearching, setIsSearching] = useState<boolean>(false);
  const [audioLevel, setAudioLevel] = useState<number>(0);
  const [settings, setSettings] = useState<VoiceSettings>({
    language: 'en-US',
    autoSearch: true,
    soundEffects: true,
    saveHistory: true,
  });
  const [isSettingsModalVisible, setIsSettingsModalVisible] = useState<boolean>(false);

  // Simulate audio level fluctuation when listening
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isListening) {
      interval = setInterval(() => {
        setAudioLevel(Math.random() * 100);
      }, 100);
    } else {
      setAudioLevel(0);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isListening]);

  const handleStartListening = () => {
    setIsListening(true);
    setTranscript('');
    setSearchResults([]);

    // Simulate speech recognition
    setTimeout(() => {
      const queries = [
        'wireless headphones',
        'laptop bag',
        'gaming mouse',
        'bluetooth speaker',
        'phone case',
      ];
      const randomQuery = queries[Math.floor(Math.random() * queries.length)];
      setTranscript(randomQuery);

      if (settings.autoSearch) {
        handleSearch(randomQuery);
      }
    }, 2000);
  };

  const handleStopListening = () => {
    setIsListening(false);
    setAudioLevel(0);
  };

  const handleSearch = (query: string) => {
    setIsSearching(true);

    // Simulate API call
    setTimeout(() => {
      setSearchResults(mockSearchResults);
      setIsSearching(false);

      // Add to history
      if (settings.saveHistory) {
        const newHistoryItem: SearchHistory = {
          id: Date.now(),
          query,
          timestamp: new Date().toISOString(),
          resultsCount: mockSearchResults.length,
          language: settings.language,
        };
        setSearchHistory((prev) => [newHistoryItem, ...prev.slice(0, 9)]);
      }
    }, 1000);
  };

  const handleClearHistory = () => {
    Modal.confirm({
      title: 'Clear Search History',
      content: 'Are you sure you want to clear all voice search history?',
      onOk: () => {
        setSearchHistory([]);
      },
    });
  };

  const handleDeleteHistoryItem = (id: number) => {
    setSearchHistory((prev) => prev.filter((item) => item.id !== id));
  };

  const handleRepeatSearch = (query: string) => {
    setTranscript(query);
    handleSearch(query);
  };

  const getTimeAgo = (timestamp: string) => {
    const seconds = Math.floor((Date.now() - new Date(timestamp).getTime()) / 1000);
    if (seconds < 60) return `${seconds}s ago`;
    if (seconds < 3600) return `${Math.floor(seconds / 60)}m ago`;
    if (seconds < 86400) return `${Math.floor(seconds / 3600)}h ago`;
    return `${Math.floor(seconds / 86400)}d ago`;
  };

  const totalSearches = searchHistory.length;
  const avgResults =
    searchHistory.length > 0
      ? Math.round(
          searchHistory.reduce((sum, h) => sum + h.resultsCount, 0) / searchHistory.length
        )
      : 0;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <SoundOutlined style={{ color: '#1890ff' }} /> Voice Search
          </Title>
          <Paragraph type="secondary">
            Search products using your voice - just speak naturally!
          </Paragraph>
        </Col>
        <Col>
          <Button
            icon={<SettingOutlined />}
            onClick={() => setIsSettingsModalVisible(true)}
          >
            Settings
          </Button>
        </Col>
      </Row>

      <Alert
        message=" Voice Search is Active"
        description="Click the microphone button and speak your search query. Our AI will understand natural language."
        type="info"
        showIcon={false}
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Total Voice Searches"
              value={totalSearches}
              prefix={<SearchOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Avg Results per Search"
              value={avgResults}
              prefix={<ThunderboltOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title="Recognition Accuracy"
              value={96}
              suffix="%"
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={12}>
          <Card
            title={
              <Space>
                <AudioOutlined />
                <span>Voice Input</span>
              </Space>
            }
          >
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              <div style={{ textAlign: 'center', padding: '40px 20px' }}>
                <Button
                  type={isListening ? 'default' : 'primary'}
                  danger={isListening}
                  size="large"
                  shape="circle"
                  icon={isListening ? <AudioMutedOutlined /> : <AudioOutlined />}
                  onClick={isListening ? handleStopListening : handleStartListening}
                  style={{
                    width: 120,
                    height: 120,
                    fontSize: 48,
                    boxShadow: isListening
                      ? '0 0 30px rgba(255, 77, 79, 0.5)'
                      : '0 0 30px rgba(24, 144, 255, 0.3)',
                    animation: isListening ? 'pulse 1.5s infinite' : 'none',
                  }}
                />

                <div style={{ marginTop: 24 }}>
                  {isListening ? (
                    <Space direction="vertical" size="small" style={{ width: '100%' }}>
                      <Badge status="processing" text="Listening..." />
                      <Progress
                        percent={audioLevel}
                        showInfo={false}
                        strokeColor={{
                          '0%': '#ff4d4f',
                          '50%': '#ff7a45',
                          '100%': '#ffa940',
                        }}
                        style={{ marginTop: 12 }}
                      />
                    </Space>
                  ) : (
                    <Text type="secondary">
                      Click the microphone to start voice search
                    </Text>
                  )}
                </div>
              </div>

              <Divider />

              {transcript && (
                <div>
                  <Text strong>Recognized Speech:</Text>
                  <div
                    style={{
                      background: '#e6f7ff',
                      padding: 16,
                      borderRadius: 8,
                      marginTop: 8,
                      border: '1px solid #91d5ff',
                    }}
                  >
                    <Space>
                      <SearchOutlined style={{ fontSize: 20, color: '#1890ff' }} />
                      <Text style={{ fontSize: 18 }}>{transcript}</Text>
                    </Space>
                  </div>

                  {!settings.autoSearch && (
                    <Button
                      type="primary"
                      icon={<SearchOutlined />}
                      onClick={() => handleSearch(transcript)}
                      loading={isSearching}
                      block
                      style={{ marginTop: 12 }}
                    >
                      Search
                    </Button>
                  )}
                </div>
              )}

              {isSearching && (
                <div style={{ textAlign: 'center', padding: 20 }}>
                  <Space direction="vertical">
                    <ThunderboltOutlined
                      spin
                      style={{ fontSize: 32, color: '#1890ff' }}
                    />
                    <Text>Searching products...</Text>
                  </Space>
                </div>
              )}

              {searchResults.length > 0 && !isSearching && (
                <div>
                  <div style={{ marginBottom: 12 }}>
                    <Space>
                      <CheckCircleOutlined style={{ color: '#52c41a' }} />
                      <Text strong>
                        Found {searchResults.length} matching products
                      </Text>
                    </Space>
                  </div>

                  <List
                    dataSource={searchResults}
                    renderItem={(product) => (
                      <List.Item
                        actions={[
                          <Tooltip title="Add to Cart">
                            <Button
                              type="primary"
                              icon={<ShoppingCartOutlined />}
                              disabled={!product.inStock}
                            >
                              Add
                            </Button>
                          </Tooltip>,
                        ]}
                        style={{
                          opacity: product.inStock ? 1 : 0.6,
                        }}
                      >
                        <List.Item.Meta
                          avatar={
                            <Badge
                              count={`${product.relevanceScore}%`}
                              style={{ backgroundColor: '#52c41a' }}
                            >
                              <Avatar
                                src={product.productImage}
                                shape="square"
                                size={64}
                              />
                            </Badge>
                          }
                          title={
                            <Space>
                              <Text strong>{product.productName}</Text>
                              {!product.inStock && (
                                <Tag color="error">Out of Stock</Tag>
                              )}
                            </Space>
                          }
                          description={
                            <Space direction="vertical" size="small">
                              <div>
                                <Tag color="blue">{product.category}</Tag>
                              </div>
                              <div>
                                <Space>
                                  <Text strong style={{ fontSize: 18, color: '#1890ff' }}>
                                    <DollarOutlined />
                                    {product.price.toFixed(2)}
                                  </Text>
                                  <Divider type="vertical" />
                                  <Space size="small">
                                    <StarOutlined style={{ color: '#faad14' }} />
                                    <Text>{product.rating}</Text>
                                    <Text type="secondary">
                                      ({product.reviews} reviews)
                                    </Text>
                                  </Space>
                                </Space>
                              </div>
                            </Space>
                          }
                        />
                      </List.Item>
                    )}
                  />
                </div>
              )}
            </Space>
          </Card>
        </Col>

        <Col xs={24} lg={12}>
          <Card
            title={
              <Space>
                <HistoryOutlined />
                <span>Search History</span>
              </Space>
            }
            extra={
              searchHistory.length > 0 && (
                <Button
                  type="link"
                  danger
                  icon={<DeleteOutlined />}
                  onClick={handleClearHistory}
                >
                  Clear All
                </Button>
              )
            }
          >
            {searchHistory.length === 0 ? (
              <Empty description="No search history yet" />
            ) : (
              <List
                dataSource={searchHistory}
                renderItem={(item) => (
                  <List.Item
                    actions={[
                      <Tooltip title="Search Again">
                        <Button
                          type="link"
                          icon={<SearchOutlined />}
                          onClick={() => handleRepeatSearch(item.query)}
                        >
                          Repeat
                        </Button>
                      </Tooltip>,
                      <Tooltip title="Delete">
                        <Button
                          type="link"
                          danger
                          icon={<DeleteOutlined />}
                          onClick={() => handleDeleteHistoryItem(item.id)}
                        />
                      </Tooltip>,
                    ]}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          icon={<AudioOutlined />}
                          style={{ background: '#1890ff' }}
                        />
                      }
                      title={
                        <Space>
                          <Text strong>"{item.query}"</Text>
                          <Tag color="green">{item.resultsCount} results</Tag>
                        </Space>
                      }
                      description={
                        <Space>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            {getTimeAgo(item.timestamp)}
                          </Text>
                          <Divider type="vertical" />
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            {item.language}
                          </Text>
                        </Space>
                      }
                    />
                  </List.Item>
                )}
              />
            )}
          </Card>

          <Card
            title={
              <Space>
                <QuestionCircleOutlined />
                <span>Voice Search Tips</span>
              </Space>
            }
            style={{ marginTop: 16 }}
          >
            <List size="small">
              <List.Item>
                <Space>
                  <CheckCircleOutlined style={{ color: '#52c41a' }} />
                  <Text>Speak clearly and naturally</Text>
                </Space>
              </List.Item>
              <List.Item>
                <Space>
                  <CheckCircleOutlined style={{ color: '#52c41a' }} />
                  <Text>Use specific product names or categories</Text>
                </Space>
              </List.Item>
              <List.Item>
                <Space>
                  <CheckCircleOutlined style={{ color: '#52c41a' }} />
                  <Text>Include colors, sizes, or brands for better results</Text>
                </Space>
              </List.Item>
              <List.Item>
                <Space>
                  <CheckCircleOutlined style={{ color: '#52c41a' }} />
                  <Text>Try "show me" or "find" commands</Text>
                </Space>
              </List.Item>
            </List>
          </Card>
        </Col>
      </Row>

      <Modal
        title={
          <Space>
            <SettingOutlined />
            <span>Voice Search Settings</span>
          </Space>
        }
        open={isSettingsModalVisible}
        onCancel={() => setIsSettingsModalVisible(false)}
        footer={[
          <Button key="close" type="primary" onClick={() => setIsSettingsModalVisible(false)}>
            Close
          </Button>,
        ]}
      >
        <Space direction="vertical" style={{ width: '100%' }} size="large">
          <div>
            <Space style={{ width: '100%', justifyContent: 'space-between' }}>
              <div>
                <Text strong>Language</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Select voice recognition language
                </Text>
              </div>
              <Select
                value={settings.language}
                onChange={(value) => setSettings({ ...settings, language: value })}
                style={{ width: 150 }}
              >
                <Option value="en-US">English (US)</Option>
                <Option value="en-GB">English (UK)</Option>
                <Option value="es-ES">Spanish</Option>
                <Option value="fr-FR">French</Option>
                <Option value="de-DE">German</Option>
                <Option value="zh-CN">Chinese</Option>
              </Select>
            </Space>
          </div>

          <Divider style={{ margin: 0 }} />

          <div>
            <Space style={{ width: '100%', justifyContent: 'space-between' }}>
              <div>
                <Text strong>Auto Search</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Automatically search after voice input
                </Text>
              </div>
              <Switch
                checked={settings.autoSearch}
                onChange={(checked) => setSettings({ ...settings, autoSearch: checked })}
              />
            </Space>
          </div>

          <Divider style={{ margin: 0 }} />

          <div>
            <Space style={{ width: '100%', justifyContent: 'space-between' }}>
              <div>
                <Text strong>Sound Effects</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Play sounds during voice recognition
                </Text>
              </div>
              <Switch
                checked={settings.soundEffects}
                onChange={(checked) => setSettings({ ...settings, soundEffects: checked })}
              />
            </Space>
          </div>

          <Divider style={{ margin: 0 }} />

          <div>
            <Space style={{ width: '100%', justifyContent: 'space-between' }}>
              <div>
                <Text strong>Save History</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Store voice search history
                </Text>
              </div>
              <Switch
                checked={settings.saveHistory}
                onChange={(checked) => setSettings({ ...settings, saveHistory: checked })}
              />
            </Space>
          </div>
        </Space>
      </Modal>

      <style>
        {`
          @keyframes pulse {
            0%, 100% {
              transform: scale(1);
              box-shadow: 0 0 30px rgba(255, 77, 79, 0.5);
            }
            50% {
              transform: scale(1.05);
              box-shadow: 0 0 50px rgba(255, 77, 79, 0.8);
            }
          }
        `}
      </style>
    </div>
  );
};

export default VoiceSearchPage;

product-qa-integrated.__content:
import React, { useState, useEffect } from 'react';
import { Card, Button, List, Avatar, Space, Tag, Input, Modal, Form, Select, Upload, message, Row, Col, Tabs, Typography, Badge, Rate, Divider, Tooltip, Empty, Spin, Statistic, Progress, Timeline } from 'antd';
import { QuestionCircleOutlined, LikeOutlined, DislikeOutlined, MessageOutlined, CheckCircleOutlined, UserOutlined, PlusOutlined, SearchOutlined, FilterOutlined, StarFilled, TrophyOutlined, ClockCircleOutlined, PaperClipOutlined, SendOutlined, ThunderboltOutlined } from '@ant-design/icons';
import { productQAAPI, type ProductQuestion, type ProductAnswer, type CreateQuestionRequest, type QAStats } from '../services/api/productQAAPI';
import './product-qa.less';

const { TextArea } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Title, Text, Paragraph } = Typography;
const { Search } = Input;

// Product Q&A System with Community Features
const ProductQASystem: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [questions, setQuestions] = useState<ProductQuestion[]>([]);
  const [myQuestions, setMyQuestions] = useState<ProductQuestion[]>([]);
  const [stats, setStats] = useState<QAStats | null>(null);
  const [selectedQuestion, setSelectedQuestion] = useState<ProductQuestion | null>(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showAnswerModal, setShowAnswerModal] = useState(false);
  const [showQuestionDetails, setShowQuestionDetails] = useState(false);
  const [activeTab, setActiveTab] = useState('browse');
  const [filters, setFilters] = useState({
    category: undefined,
    isAnswered: undefined,
    sortBy: 'newest' as const,
    search: ''
  });

  const [createForm] = Form.useForm();
  const [answerForm] = Form.useForm();

  // Sample product ID for demonstration
  const currentProductId = 'sample-product-1';

  // Load initial data
  useEffect(() => {
    loadQuestions();
    loadMyQuestions();
    loadStats();
  }, [filters]);

  const loadQuestions = async () => {
    setLoading(true);
    try {
      const response = await productQAAPI.getProductQuestions(currentProductId, filters);
      if (response.success) {
        setQuestions(response.data.questions);
      } else {
        message.error('Failed to load questions');
      }
    } catch (error) {
      console.error('Error loading questions:', error);
      message.error('Failed to load questions');
    } finally {
      setLoading(false);
    }
  };

  const loadMyQuestions = async () => {
    try {
      const response = await productQAAPI.getMyQuestions(filters);
      if (response.success) {
        setMyQuestions(response.data.questions);
      }
    } catch (error) {
      console.error('Error loading my questions:', error);
    }
  };

  const loadStats = async () => {
    try {
      const response = await productQAAPI.getQAStats(currentProductId);
      if (response.success) {
        setStats(response.data);
      }
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const handleCreateQuestion = async (values: any) => {
    try {
      const questionData: CreateQuestionRequest = {
        productId: currentProductId,
        question: values.question,
        isPublic: values.isPublic !== false,
        category: values.category,
        tags: values.tags?.split(',').map((tag: string) => tag.trim()) || []
      };

      const response = await productQAAPI.createQuestion(questionData);
      if (response.success) {
        message.success('Question posted successfully');
        setShowCreateModal(false);
        createForm.resetFields();
        loadQuestions();
        loadMyQuestions();
        loadStats();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating question:', error);
      message.error('Failed to post question');
    }
  };

  const handleCreateAnswer = async (values: any) => {
    if (!selectedQuestion) return;

    try {
      const answerData = {
        questionId: selectedQuestion.id,
        answer: values.answer,
        attachments: values.attachments?.fileList?.map((file: any) => file.originFileObj) || []
      };

      const response = await productQAAPI.createAnswer(answerData);
      if (response.success) {
        message.success('Answer posted successfully');
        setShowAnswerModal(false);
        answerForm.resetFields();

        // Update the question with new answer
        const updatedQuestion = {
          ...selectedQuestion,
          answers: [...selectedQuestion.answers, response.data],
          isAnswered: true
        };
        setSelectedQuestion(updatedQuestion);

        // Refresh questions list
        loadQuestions();
        loadMyQuestions();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating answer:', error);
      message.error('Failed to post answer');
    }
  };

  const handleVoteQuestion = async (questionId: string, voteType: 'up' | 'down') => {
    try {
      const response = await productQAAPI.voteQuestion(questionId, voteType);
      if (response.success) {
        // Update local state to reflect vote
        setQuestions(questions.map(q => 
          q.id === questionId 
            ? {
                ...q,
                upvotes: voteType === 'up' ? q.upvotes + 1 : q.upvotes,
                downvotes: voteType === 'down' ? q.downvotes + 1 : q.downvotes,
                hasUserVoted: true,
                userVoteType: voteType
              }
            : q
        ));
        message.success('Vote recorded');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error voting:', error);
      message.error('Failed to record vote');
    }
  };

  const handleVoteAnswer = async (answerId: string, voteType: 'helpful' | 'not_helpful') => {
    try {
      const response = await productQAAPI.voteAnswer(answerId, voteType);
      if (response.success) {
        // Update the selected question's answers
        if (selectedQuestion) {
          const updatedAnswers = selectedQuestion.answers.map(a => 
            a.id === answerId 
              ? {
                  ...a,
                  helpfulCount: voteType === 'helpful' ? a.helpfulCount + 1 : a.helpfulCount,
                  notHelpfulCount: voteType === 'not_helpful' ? a.notHelpfulCount + 1 : a.notHelpfulCount,
                  hasUserVoted: true,
                  userVoteType: voteType
                }
              : a
          );
          setSelectedQuestion({ ...selectedQuestion, answers: updatedAnswers });
        }
        message.success('Feedback recorded');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error voting on answer:', error);
      message.error('Failed to record feedback');
    }
  };

  const handleAcceptAnswer = async (answerId: string) => {
    try {
      const response = await productQAAPI.acceptAnswer(answerId);
      if (response.success) {
        // Update the selected question's answers
        if (selectedQuestion) {
          const updatedAnswers = selectedQuestion.answers.map(a => ({
            ...a,
            isAccepted: a.id === answerId
          }));
          setSelectedQuestion({ ...selectedQuestion, answers: updatedAnswers });
        }
        message.success('Answer marked as best answer');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error accepting answer:', error);
      message.error('Failed to mark as best answer');
    }
  };

  const renderQuestionCard = (question: ProductQuestion) => (
    <Card
      key={question.id}
      size="small"
      style={{ marginBottom: 16 }}
      actions={[
        <Space key="vote">
          <Button
            size="small"
            type={question.hasUserVoted && question.userVoteType === 'up' ? 'primary' : 'text'}
            icon={<LikeOutlined />}
            onClick={() => handleVoteQuestion(question.id, 'up')}
          >
            {question.upvotes}
          </Button>
          <Button
            size="small"
            type={question.hasUserVoted && question.userVoteType === 'down' ? 'primary' : 'text'}
            icon={<DislikeOutlined />}
            onClick={() => handleVoteQuestion(question.id, 'down')}
          >
            {question.downvotes}
          </Button>
        </Space>,
        <Button
          key="answer"
          size="small"
          icon={<MessageOutlined />}
          onClick={() => {
            setSelectedQuestion(question);
            setShowAnswerModal(true);
          }}
        >
          Answer
        </Button>,
        <Button
          key="view"
          size="small"
          type="link"
          onClick={() => {
            setSelectedQuestion(question);
            setShowQuestionDetails(true);
          }}
        >
          View Details
        </Button>
      ]}
    >
      <Row gutter={16}>
        <Col flex={1}>
          <div style={{ marginBottom: 8 }}>
            <Text strong style={{ fontSize: '16px' }}>
              {question.question}
            </Text>
          </div>

          <Space wrap style={{ marginBottom: 8 }}>
            {question.category && (
              <Tag color="blue">{question.category}</Tag>
            )}
            {question.tags.map((tag) => (
              <Tag key={tag}>{tag}</Tag>
            ))}
            {question.isAnswered && (
              <Tag color="green" icon={<CheckCircleOutlined />}>
                Answered ({question.answers.length})
              </Tag>
            )}
          </Space>

          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Space>
              <Avatar 
                size="small" 
                src={question.customerAvatar} 
                icon={<UserOutlined />} 
              />
              <Text type="secondary">{question.customerName}</Text>
              <Text type="secondary">
                {new Date(question.createdAt).toLocaleDateString()}
              </Text>
            </Space>

            {question.isAnswered && question.answers.some(a => a.isBrandResponse) && (
              <Tag color="gold" icon={<TrophyOutlined />}>
                Brand Answered
              </Tag>
            )}
          </div>
        </Col>

        {question.productImage && (
          <Col>
            <Avatar
              size={64}
              shape="square"
              src={question.productImage}
              style={{ border: '1px solid #d9d9d9' }}
            />
          </Col>
        )}
      </Row>
    </Card>
  );

  const renderAnswer = (answer: ProductAnswer, questionOwnerId?: string) => (
    <Card
      key={answer.id}
      size="small"
      style={{
        marginBottom: 12,
        backgroundColor: answer.isAccepted ? '#f6ffed' : undefined,
        border: answer.isAccepted ? '2px solid #52c41a' : undefined
      }}
    >
      <div style={{ marginBottom: 8 }}>
        <Space>
          <Avatar
            size="small"
            src={answer.answeredBy.avatar}
            icon={<UserOutlined />}
          />
          <Text strong>{answer.answeredBy.name}</Text>

          {answer.answeredBy.type === 'vendor' && (
            <Tag color="gold">
              {answer.answeredBy.badge || 'Vendor'}
            </Tag>
          )}

          {answer.answeredBy.type === 'expert' && (
            <Tag color="purple">
              {answer.answeredBy.badge || 'Expert'}
            </Tag>
          )}

          {answer.answeredBy.verificationLevel === 'verified_purchase' && (
            <Tag color="green">Verified Purchase</Tag>
          )}

          {answer.isAccepted && (
            <Tag color="success" icon={<CheckCircleOutlined />}>
              Best Answer
            </Tag>
          )}

          <Text type="secondary">
            {new Date(answer.createdAt).toLocaleDateString()}
          </Text>
        </Space>
      </div>

      <Paragraph style={{ marginBottom: 12 }}>
        {answer.answer}
      </Paragraph>

      {answer.attachments && answer.attachments.length > 0 && (
        <div style={{ marginBottom: 12 }}>
          <Text type="secondary">Attachments:</Text>
          <Row gutter={8} style={{ marginTop: 4 }}>
            {answer.attachments.map((attachment) => (
              <Col key={attachment.id}>
                {attachment.type === 'image' ? (
                  <img
                    src={attachment.thumbnail || attachment.url}
                    alt={attachment.filename}
                    style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 4 }}
                  />
                ) : (
                  <Button size="small" icon={<PaperClipOutlined />}>
                    {attachment.filename}
                  </Button>
                )}
              </Col>
            ))}
          </Row>
        </div>
      )}

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Space>
          <Button
            size="small"
            type={answer.hasUserVoted && answer.userVoteType === 'helpful' ? 'primary' : 'text'}
            onClick={() => handleVoteAnswer(answer.id, 'helpful')}
          >
             Helpful ({answer.helpfulCount})
          </Button>
          <Button
            size="small"
            type={answer.hasUserVoted && answer.userVoteType === 'not_helpful' ? 'primary' : 'text'}
            onClick={() => handleVoteAnswer(answer.id, 'not_helpful')}
          >
             Not Helpful ({answer.notHelpfulCount})
          </Button>
        </Space>

        {/* Only question owner can accept answers */}
        {questionOwnerId === '1' && !answer.isAccepted && (
          <Button
            size="small"
            type="primary"
            ghost
            icon={<CheckCircleOutlined />}
            onClick={() => handleAcceptAnswer(answer.id)}
          >
            Mark as Best Answer
          </Button>
        )}
      </div>
    </Card>
  );

  const renderStats = () => (
    <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Total Questions"
            value={stats?.totalQuestions || 0}
            prefix={<QuestionCircleOutlined />}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Answered"
            value={stats?.answeredQuestions || 0}
            prefix={<CheckCircleOutlined />}
            valueStyle={{ color: '#52c41a' }}
          />
          {stats && (
            <Progress
              percent={Math.round((stats.answeredQuestions / stats.totalQuestions) * 100)}
              size="small"
              showInfo={false}
            />
          )}
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Avg Response Time"
            value={stats?.averageResponseTime || 0}
            suffix="hrs"
            prefix={<ClockCircleOutlined />}
            precision={1}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Unanswered"
            value={stats?.unansweredQuestions || 0}
            prefix={<QuestionCircleOutlined />}
            valueStyle={{ color: '#faad14' }}
          />
        </Card>
      </Col>
    </Row>
  );

  const renderCreateQuestionModal = () => (
    <Modal
      title="Ask a Question"
      open={showCreateModal}
      onCancel={() => {
        setShowCreateModal(false);
        createForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={createForm}
        layout="vertical"
        onFinish={handleCreateQuestion}
      >
        <Form.Item
          label="Your Question"
          name="question"
          rules={[
            { required: true, message: 'Please enter your question' },
            { min: 10, message: 'Question must be at least 10 characters' }
          ]}
        >
          <TextArea
            rows={4}
            placeholder="What would you like to know about this product?"
          />
        </Form.Item>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item label="Category" name="category">
              <Select placeholder="Select category (optional)">
                <Option value="general">General Question</Option>
                <Option value="specifications">Specifications</Option>
                <Option value="compatibility">Compatibility</Option>
                <Option value="shipping">Shipping</Option>
                <Option value="warranty">Warranty</Option>
                <Option value="usage">Usage Instructions</Option>
                <Option value="comparison">Product Comparison</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item label="Tags" name="tags">
              <Input placeholder="e.g., macbook, wireless, bluetooth" />
            </Form.Item>
          </Col>
        </Row>

        <Form.Item name="isPublic" valuePropName="checked" initialValue={true}>
          <Space>
            <input type="checkbox" defaultChecked />
            <Text>Make this question public (recommended for faster answers)</Text>
          </Space>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit" icon={<SendOutlined />}>
              Post Question
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderAnswerModal = () => (
    <Modal
      title={`Answer: ${selectedQuestion?.question}`}
      open={showAnswerModal}
      onCancel={() => {
        setShowAnswerModal(false);
        answerForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={answerForm}
        layout="vertical"
        onFinish={handleCreateAnswer}
      >
        <Form.Item
          label="Your Answer"
          name="answer"
          rules={[
            { required: true, message: 'Please provide an answer' },
            { min: 10, message: 'Answer must be at least 10 characters' }
          ]}
        >
          <TextArea
            rows={6}
            placeholder="Share your knowledge and help other customers..."
          />
        </Form.Item>

        <Form.Item label="Attachments" name="attachments">
          <Upload
            multiple
            beforeUpload={() => false}
            fileList={[]}
          >
            <Button icon={<PaperClipOutlined />}>
              Attach Images or Documents
            </Button>
          </Upload>
          <Text type="secondary" style={{ fontSize: '12px', display: 'block', marginTop: 4 }}>
            Add screenshots, manuals, or other helpful files
          </Text>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowAnswerModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit" icon={<SendOutlined />}>
              Post Answer
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderQuestionDetails = () => (
    <Modal
      title="Question Details"
      open={showQuestionDetails}
      onCancel={() => {
        setShowQuestionDetails(false);
        setSelectedQuestion(null);
      }}
      width={800}
      footer={null}
    >
      {selectedQuestion && (
        <div>
          <Card style={{ marginBottom: 16 }}>
            <div style={{ marginBottom: 16 }}>
              <Title level={4}>{selectedQuestion.question}</Title>
              <Space wrap>
                {selectedQuestion.category && (
                  <Tag color="blue">{selectedQuestion.category}</Tag>
                )}
                {selectedQuestion.tags.map((tag) => (
                  <Tag key={tag}>{tag}</Tag>
                ))}
              </Space>
            </div>

            <Space style={{ marginBottom: 16 }}>
              <Avatar 
                src={selectedQuestion.customerAvatar} 
                icon={<UserOutlined />} 
              />
              <div>
                <Text strong>{selectedQuestion.customerName}</Text>
                <br />
                <Text type="secondary">
                  Asked {new Date(selectedQuestion.createdAt).toLocaleDateString()}
                </Text>
              </div>
            </Space>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Space>
                <Button
                  type={selectedQuestion.hasUserVoted && selectedQuestion.userVoteType === 'up' ? 'primary' : 'default'}
                  icon={<LikeOutlined />}
                  onClick={() => handleVoteQuestion(selectedQuestion.id, 'up')}
                >
                  {selectedQuestion.upvotes}
                </Button>
                <Button
                  type={selectedQuestion.hasUserVoted && selectedQuestion.userVoteType === 'down' ? 'primary' : 'default'}
                  icon={<DislikeOutlined />}
                  onClick={() => handleVoteQuestion(selectedQuestion.id, 'down')}
                >
                  {selectedQuestion.downvotes}
                </Button>
              </Space>

              <Button
                type="primary"
                icon={<MessageOutlined />}
                onClick={() => setShowAnswerModal(true)}
              >
                Add Answer
              </Button>
            </div>
          </Card>

          <Card title={`${selectedQuestion.answers.length} Answer${selectedQuestion.answers.length !== 1 ? 's' : ''}`}>
            {selectedQuestion.answers.length > 0 ? (
              selectedQuestion.answers
                .sort((a, b) => {
                  // Sort by: accepted first, then by helpful votes
                  if (a.isAccepted && !b.isAccepted) return -1;
                  if (!a.isAccepted && b.isAccepted) return 1;
                  return b.helpfulCount - a.helpfulCount;
                })
                .map((answer) => renderAnswer(answer, selectedQuestion.customerId))
            ) : (
              <Empty description="No answers yet" style={{ padding: '40px 0' }}>
                <Button type="primary" onClick={() => setShowAnswerModal(true)}>
                  Be the first to answer
                </Button>
              </Empty>
            )}
          </Card>
        </div>
      )}
    </Modal>
  );

  return (
    <div className="product-qa" style={{ padding: '24px', maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <QuestionCircleOutlined /> Product Q&A
        </Title>
        <Text type="secondary">
          Ask questions and get answers from our community of customers and experts
        </Text>
      </div>

      {/* Stats */}
      {renderStats()}

      {/* Tabs */}
      <Tabs activeKey={activeTab} onChange={setActiveTab} size="large">
        <TabPane
          tab={
            <Space>
              <QuestionCircleOutlined />
              Browse Questions
              {questions.length > 0 && <Badge count={questions.length} />}
            </Space>
          }
          key="browse"
        >
          {/* Filters and Actions */}
          <Card size="small" style={{ marginBottom: 16 }}>
            <Row gutter={16} align="middle">
              <Col flex={1}>
                <Search
                  placeholder="Search questions..."
                  value={filters.search}
                  onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                  onSearch={loadQuestions}
                  allowClear
                />
              </Col>
              <Col>
                <Space>
                  <Select
                    placeholder="Category"
                    allowClear
                    style={{ width: 130 }}
                    value={filters.category}
                    onChange={(value) => setFilters({ ...filters, category: value })}
                  >
                    <Option value="general">General</Option>
                    <Option value="specifications">Specs</Option>
                    <Option value="compatibility">Compatibility</Option>
                    <Option value="warranty">Warranty</Option>
                    <Option value="shipping">Shipping</Option>
                  </Select>

                  <Select
                    placeholder="Status"
                    allowClear
                    style={{ width: 120 }}
                    value={filters.isAnswered}
                    onChange={(value) => setFilters({ ...filters, isAnswered: value })}
                  >
                    <Option value={true}>Answered</Option>
                    <Option value={false}>Unanswered</Option>
                  </Select>

                  <Select
                    placeholder="Sort by"
                    style={{ width: 120 }}
                    value={filters.sortBy}
                    onChange={(value) => setFilters({ ...filters, sortBy: value })}
                  >
                    <Option value="newest">Newest</Option>
                    <Option value="oldest">Oldest</Option>
                    <Option value="most_voted">Most Voted</Option>
                    <Option value="most_answered">Most Answered</Option>
                  </Select>

                  <Button
                    type="primary"
                    icon={<PlusOutlined />}
                    onClick={() => setShowCreateModal(true)}
                  >
                    Ask Question
                  </Button>
                </Space>
              </Col>
            </Row>
          </Card>

          {/* Questions List */}
          {loading ? (
            <div style={{ textAlign: 'center', padding: 40 }}>
              <Spin size="large" />
            </div>
          ) : questions.length > 0 ? (
            questions.map(renderQuestionCard)
          ) : (
            <Card style={{ textAlign: 'center', padding: '60px 20px' }}>
              <QuestionCircleOutlined style={{ fontSize: 48, color: '#d9d9d9', marginBottom: 16 }} />
              <Title level={3}>No questions yet</Title>
              <Paragraph style={{ color: '#666', marginBottom: 24 }}>
                Be the first to ask a question about this product!
              </Paragraph>
              <Button type="primary" size="large" onClick={() => setShowCreateModal(true)}>
                Ask the First Question
              </Button>
            </Card>
          )}
        </TabPane>

        <TabPane
          tab={
            <Space>
              <UserOutlined />
              My Questions
              {myQuestions.length > 0 && <Badge count={myQuestions.length} />}
            </Space>
          }
          key="my-questions"
        >
          <div style={{ marginBottom: 16, textAlign: 'right' }}>
            <Button type="primary" icon={<PlusOutlined />} onClick={() => setShowCreateModal(true)}>
              Ask New Question
            </Button>
          </div>

          {myQuestions.length > 0 ? (
            myQuestions.map(renderQuestionCard)
          ) : (
            <Empty
              description="You haven't asked any questions yet"
              style={{ padding: '60px 20px' }}
            >
              <Button type="primary" onClick={() => setShowCreateModal(true)}>
                Ask Your First Question
              </Button>
            </Empty>
          )}
        </TabPane>
      </Tabs>

      {/* Modals */}
      {renderCreateQuestionModal()}
      {renderAnswerModal()}
      {renderQuestionDetails()}
    </div>
  );
};

export default ProductQASystem;

