import React, { useState } from 'react';
import {
  Card,
  Form,
  Input,
  Button,
  Space,
  Typography,
  Divider,
  Row,
  Col,
  Checkbox,
  Steps,
  Alert,
  message,
} from 'antd';
import {
  ShoppingCartOutlined,
  UserOutlined,
  HomeOutlined,
  CreditCardOutlined,
  CheckCircleOutlined,
} from '@ant-design/icons';
import ShippingMethodSelector from '@/components/checkout/ShippingMethodSelector';

const { Title, Text, Paragraph } = Typography;

interface GuestCheckoutFormData {
  email: string;
  firstName: string;
  lastName: string;
  phone: string;
  address: string;
  city: string;
  state: string;
  zipCode: string;
  country: string;
  shippingMethod?: any;
  createAccount: boolean;
}

const GuestCheckoutPage: React.FC = () => {
  const [form] = Form.useForm();
  const [currentStep, setCurrentStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<Partial<GuestCheckoutFormData>>({});
  const [showShippingSelector, setShowShippingSelector] = useState(false);

  const cartTotal = 89.99; // Mock cart total

  const steps = [
    {
      title: 'Contact Info',
      icon: <UserOutlined />,
    },
    {
      title: 'Shipping',
      icon: <HomeOutlined />,
    },
    {
      title: 'Payment',
      icon: <CreditCardOutlined />,
    },
    {
      title: 'Complete',
      icon: <CheckCircleOutlined />,
    },
  ];

  const handleNext = async () => {
    try {
      const values = await form.validateFields();
      setFormData({ ...formData, ...values });

      if (currentStep === 1) {
        setShowShippingSelector(true);
      } else {
        setCurrentStep(currentStep + 1);
      }
    } catch (error) {
      console.error('Validation failed:', error);
    }
  };

  const handleShippingMethodSelect = (method: any) => {
    setFormData({ ...formData, shippingMethod: method });
    setShowShippingSelector(false);
    setCurrentStep(currentStep + 1);
  };

  const handleSubmitOrder = async () => {
    try {
      setLoading(true);
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      message.success('Order placed successfully!');
      setCurrentStep(3);
    } catch (error) {
      message.error('Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  const handlePrevious = () => {
    setCurrentStep(currentStep - 1);
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: 1000, margin: '0 auto' }}>
        <Card>
          <Space direction="vertical" size="large" style={{ width: '100%' }}>
            <div>
              <Title level={3}>
                <ShoppingCartOutlined /> Guest Checkout
              </Title>
              <Paragraph type="secondary">
                Complete your purchase without creating an account
              </Paragraph>
            </div>

            <Alert
              message="Shopping as Guest"
              description="You can create an account after checkout to track your order and enjoy member benefits."
              type="info"
              showIcon
            />

            <Steps current={currentStep} items={steps} />

            <Divider />

            <Form form={form} layout="vertical">
              {/* Step 0: Contact Information */}
              {currentStep === 0 && (
                <div>
                  <Title level={4}>Contact Information</Title>
                  <Row gutter={16}>
                    <Col xs={24}>
                      <Form.Item
                        name="email"
                        label="Email Address"
                        rules={[
                          { required: true, message: 'Please enter your email' },
                          { type: 'email', message: 'Please enter a valid email' },
                        ]}
                      >
                        <Input size="large" placeholder="your.email@example.com" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="firstName"
                        label="First Name"
                        rules={[{ required: true, message: 'Please enter your first name' }]}
                      >
                        <Input size="large" placeholder="John" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="lastName"
                        label="Last Name"
                        rules={[{ required: true, message: 'Please enter your last name' }]}
                      >
                        <Input size="large" placeholder="Doe" />
                      </Form.Item>
                    </Col>
                    <Col xs={24}>
                      <Form.Item
                        name="phone"
                        label="Phone Number"
                        rules={[{ required: true, message: 'Please enter your phone number' }]}
                      >
                        <Input size="large" placeholder="+1 (555) 123-4567" />
                      </Form.Item>
                    </Col>
                  </Row>

                  <Form.Item name="createAccount" valuePropName="checked" initialValue={false}>
                    <Checkbox>Create an account for faster checkout next time</Checkbox>
                  </Form.Item>
                </div>
              )}

              {/* Step 1: Shipping Address */}
              {currentStep === 1 && (
                <div>
                  <Title level={4}>Shipping Address</Title>
                  <Row gutter={16}>
                    <Col xs={24}>
                      <Form.Item
                        name="address"
                        label="Street Address"
                        rules={[{ required: true, message: 'Please enter your address' }]}
                      >
                        <Input size="large" placeholder="123 Main Street, Apt 4B" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="city"
                        label="City"
                        rules={[{ required: true, message: 'Please enter your city' }]}
                      >
                        <Input size="large" placeholder="San Francisco" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="state"
                        label="State/Province"
                        rules={[{ required: true, message: 'Please enter your state' }]}
                      >
                        <Input size="large" placeholder="California" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="zipCode"
                        label="ZIP/Postal Code"
                        rules={[{ required: true, message: 'Please enter your ZIP code' }]}
                      >
                        <Input size="large" placeholder="94105" />
                      </Form.Item>
                    </Col>
                    <Col xs={24} md={12}>
                      <Form.Item
                        name="country"
                        label="Country"
                        initialValue="United States"
                        rules={[{ required: true, message: 'Please enter your country' }]}
                      >
                        <Input size="large" />
                      </Form.Item>
                    </Col>
                  </Row>
                </div>
              )}

              {/* Step 2: Payment */}
              {currentStep === 2 && (
                <div>
                  <Title level={4}>Payment Information</Title>

                  <Card style={{ marginBottom: 16, background: '#fafafa' }}>
                    <Space direction="vertical" size="small" style={{ width: '100%' }}>
                      <Text strong>Order Summary</Text>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Text type="secondary">Subtotal:</Text>
                        <Text>${cartTotal.toFixed(2)}</Text>
                      </div>
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Text type="secondary">Shipping ({formData.shippingMethod?.name}):</Text>
                        <Text style={{ color: formData.shippingMethod?.price === 0 ? '#52c41a' : 'inherit' }}>
                          {formData.shippingMethod?.price === 0 ? 'FREE' : `$${formData.shippingMethod?.price}`}
                        </Text>
                      </div>
                      <Divider style={{ margin: '8px 0' }} />
                      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <Text strong>Total:</Text>
                        <Text strong style={{ fontSize: 18, color: '#ff9900' }}>
                          ${(cartTotal + (formData.shippingMethod?.price || 0)).toFixed(2)}
                        </Text>
                      </div>
                    </Space>
                  </Card>

                  <Row gutter={16}>
                    <Col xs={24}>
                      <Form.Item
                        name="cardNumber"
                        label="Card Number"
                        rules={[{ required: true, message: 'Please enter card number' }]}
                      >
                        <Input size="large" placeholder="1234 5678 9012 3456" maxLength={19} />
                      </Form.Item>
                    </Col>
                    <Col xs={24}>
                      <Form.Item
                        name="cardName"
                        label="Cardholder Name"
                        rules={[{ required: true, message: 'Please enter cardholder name' }]}
                      >
                        <Input size="large" placeholder="John Doe" />
                      </Form.Item>
                    </Col>
                    <Col xs={12}>
                      <Form.Item
                        name="expiryDate"
                        label="Expiry Date"
                        rules={[{ required: true, message: 'Please enter expiry date' }]}
                      >
                        <Input size="large" placeholder="MM/YY" maxLength={5} />
                      </Form.Item>
                    </Col>
                    <Col xs={12}>
                      <Form.Item
                        name="cvv"
                        label="CVV"
                        rules={[{ required: true, message: 'Please enter CVV' }]}
                      >
                        <Input size="large" placeholder="123" maxLength={4} />
                      </Form.Item>
                    </Col>
                  </Row>

                  <Alert
                    message="Secure Payment"
                    description="Your payment information is encrypted and secure. We never store your card details."
                    type="success"
                    showIcon
                    style={{ marginTop: 16 }}
                  />
                </div>
              )}

              {/* Step 3: Success */}
              {currentStep === 3 && (
                <div style={{ textAlign: 'center', padding: '40px 0' }}>
                  <CheckCircleOutlined style={{ fontSize: 72, color: '#52c41a' }} />
                  <Title level={3} style={{ marginTop: 24 }}>
                    Order Placed Successfully!
                  </Title>
                  <Paragraph type="secondary" style={{ fontSize: 16 }}>
                    Thank you for your purchase. A confirmation email has been sent to {formData.email}
                  </Paragraph>

                  <Card style={{ marginTop: 32, textAlign: 'left' }}>
                    <Space direction="vertical" size="middle" style={{ width: '100%' }}>
                      <div>
                        <Text type="secondary">Order Number:</Text>
                        <br />
                        <Text strong style={{ fontSize: 18 }}>#ORD-{Date.now().toString().slice(-6)}</Text>
                      </div>
                      <Divider style={{ margin: '8px 0' }} />
                      <div>
                        <Text type="secondary">Estimated Delivery:</Text>
                        <br />
                        <Text strong>{formData.shippingMethod?.estimatedDays}</Text>
                      </div>
                    </Space>
                  </Card>

                  <Space size="middle" style={{ marginTop: 32 }}>
                    <Button type="primary" size="large">
                      Track Your Order
                    </Button>
                    <Button size="large">Continue Shopping</Button>
                  </Space>
                </div>
              )}
            </Form>

            {/* Navigation Buttons */}
            {currentStep < 3 && (
              <div style={{ marginTop: 24, textAlign: 'right' }}>
                <Space>
                  {currentStep > 0 && (
                    <Button onClick={handlePrevious} size="large">
                      Previous
                    </Button>
                  )}
                  {currentStep < 2 && (
                    <Button type="primary" onClick={handleNext} size="large">
                      Next
                    </Button>
                  )}
                  {currentStep === 2 && (
                    <Button
                      type="primary"
                      onClick={handleSubmitOrder}
                      loading={loading}
                      size="large"
                    >
                      Place Order
                    </Button>
                  )}
                </Space>
              </div>
            )}
          </Space>
        </Card>
      </div>

      {/* Shipping Method Selector Modal */}
      <ShippingMethodSelector
        visible={showShippingSelector}
        onCancel={() => setShowShippingSelector(false)}
        onSelect={handleShippingMethodSelect}
        cartTotal={cartTotal}
      />
    </div>
  );
};

export default GuestCheckoutPage;

admin/multi-currency.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Space,
  Button,
  Statistic,
  Select,
  DatePicker,
  Tabs,
  Alert,
  List,
  Avatar,
  message,
  Spin,
  Modal,
  Form,
  Input,
  InputNumber,
  Switch,
  Tooltip,
  Popconfirm,
  Badge,
  Divider,
  Progress,
  Empty,
  Dropdown,
  Upload,
  Radio,
  Checkbox,
} from 'antd';
import {
  DollarOutlined,
  GlobalOutlined,
  SwapOutlined,
  SettingOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  ReloadOutlined,
  DownloadOutlined,
  UploadOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  EyeOutlined,
  BarChartOutlined,
  LineChartOutlined,
  PieChartOutlined,
  FlagOutlined,
  BankOutlined,
  SwapRightOutlined,
  TrophyOutlined,
  RiseOutlined,
  FallOutlined,
  SyncOutlined,
  WarningOutlined,
  InfoCircleOutlined,
  FileExcelOutlined,
  FilePdfOutlined,
  FileTextOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie, Area } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';
import { currencyAPI, Currency, ExchangeRate, CurrencyStats, CurrencySettings } from '../../services/api/currency';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { TabPane } = Tabs;

const MultiCurrencyPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [currencies, setCurrencies] = useState<Currency[]>([]);
  const [exchangeRates, setExchangeRates] = useState<ExchangeRate[]>([]);
  const [stats, setStats] = useState<CurrencyStats | null>(null);
  const [settings, setSettings] = useState<CurrencySettings | null>(null);
  const [activeTab, setActiveTab] = useState('currencies');
  const [selectedCurrencies, setSelectedCurrencies] = useState<string[]>([]);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [settingsModalOpen, setSettingsModalOpen] = useState(false);
  const [rateHistoryModalOpen, setRateHistoryModalOpen] = useState(false);
  const [selectedCurrency, setSelectedCurrency] = useState<Currency | null>(null);
  const [selectedRatePair, setSelectedRatePair] = useState<{ from: string; to: string } | null>(null);
  const [dateRange, setDateRange] = useState<[Dayjs, Dayjs]>([
    dayjs().subtract(30, 'days'),
    dayjs(),
  ]);
  const [form] = Form.useForm();
  const [settingsForm] = Form.useForm();
  const [refreshing, setRefreshing] = useState(false);

  // Load data
  useEffect(() => {
    loadCurrencies();
    loadExchangeRates();
    loadStats();
    loadSettings();
  }, [dateRange]);

  const loadCurrencies = async () => {
    setLoading(true);
    try {
      const response = await currencyAPI.getAll({ limit: 100 });
      setCurrencies(response.currencies);
    } catch (error) {
      console.error('Error loading currencies:', error);
      message.error('Failed to load currencies');
    } finally {
      setLoading(false);
    }
  };

  const loadExchangeRates = async () => {
    try {
      const rates = await currencyAPI.getExchangeRates();
      setExchangeRates(rates);
    } catch (error) {
      console.error('Error loading exchange rates:', error);
      message.error('Failed to load exchange rates');
    }
  };

  const loadStats = async () => {
    try {
      const statsData = await currencyAPI.getStats({
        start: dateRange[0].format('YYYY-MM-DD'),
        end: dateRange[1].format('YYYY-MM-DD'),
      });
      setStats(statsData);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const loadSettings = async () => {
    try {
      const settingsData = await currencyAPI.getSettings();
      setSettings(settingsData);
      settingsForm.setFieldsValue(settingsData);
    } catch (error) {
      console.error('Error loading settings:', error);
      message.error('Failed to load currency settings');
    }
  };

  const handleCreateCurrency = async (values: any) => {
    try {
      setLoading(true);
      await currencyAPI.create({
        code: values.code.toUpperCase(),
        name: values.name,
        symbol: values.symbol,
        symbolPosition: values.symbolPosition,
        decimalPlaces: values.decimalPlaces,
        thousandsSeparator: values.thousandsSeparator,
        decimalSeparator: values.decimalSeparator,
        isActive: values.isActive || false,
        isDefault: false,
        exchangeRate: values.exchangeRate || 1,
        countryCode: values.countryCode,
        displayFormat: values.displayFormat,
      });
      message.success('Currency created successfully');
      setCreateModalOpen(false);
      form.resetFields();
      loadCurrencies();
    } catch (error) {
      console.error('Error creating currency:', error);
      message.error('Failed to create currency');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateCurrency = async (values: any) => {
    if (!selectedCurrency) return;

    try {
      setLoading(true);
      await currencyAPI.update(selectedCurrency.id, values);
      message.success('Currency updated successfully');
      setEditModalOpen(false);
      setSelectedCurrency(null);
      form.resetFields();
      loadCurrencies();
    } catch (error) {
      console.error('Error updating currency:', error);
      message.error('Failed to update currency');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteCurrency = async (currencyId: string) => {
    try {
      await currencyAPI.delete(currencyId);
      message.success('Currency deleted successfully');
      loadCurrencies();
    } catch (error) {
      console.error('Error deleting currency:', error);
      message.error('Failed to delete currency');
    }
  };

  const handleSetDefault = async (currencyId: string) => {
    try {
      await currencyAPI.setDefault(currencyId);
      message.success('Default currency updated successfully');
      loadCurrencies();
    } catch (error) {
      console.error('Error setting default currency:', error);
      message.error('Failed to set default currency');
    }
  };

  const handleToggleActive = async (currency: Currency) => {
    try {
      if (currency.isActive) {
        await currencyAPI.deactivate(currency.id);
        message.success('Currency deactivated');
      } else {
        await currencyAPI.activate(currency.id);
        message.success('Currency activated');
      }
      loadCurrencies();
    } catch (error) {
      console.error('Error toggling currency status:', error);
      message.error('Failed to update currency status');
    }
  };

  const handleRefreshRates = async () => {
    setRefreshing(true);
    try {
      const result = await currencyAPI.refreshExchangeRates();
      message.success(`${result.updated} exchange rates updated successfully`);
      if (result.failed.length > 0) {
        message.warning(`Failed to update rates for: ${result.failed.join(', ')}`);
      }
      loadExchangeRates();
    } catch (error) {
      console.error('Error refreshing exchange rates:', error);
      message.error('Failed to refresh exchange rates');
    } finally {
      setRefreshing(false);
    }
  };

  const handleUpdateSettings = async (values: any) => {
    try {
      setLoading(true);
      await currencyAPI.updateSettings(values);
      message.success('Currency settings updated successfully');
      setSettingsModalOpen(false);
      loadSettings();
    } catch (error) {
      console.error('Error updating settings:', error);
      message.error('Failed to update settings');
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async (format: 'csv' | 'xlsx' | 'json', type: 'currencies' | 'rates') => {
    try {
      setLoading(true);
      let blob: Blob;
      if (type === 'currencies') {
        blob = await currencyAPI.exportCurrencies(format);
      } else {
        blob = await currencyAPI.exportExchangeRates(format);
      }

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${type}-${dayjs().format('YYYY-MM-DD')}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      message.success(`${type} exported as ${format.toUpperCase()}`);
    } catch (error) {
      console.error('Export error:', error);
      message.error('Failed to export data');
    } finally {
      setLoading(false);
    }
  };

  const handleConvert = async (from: string, to: string, amount: number) => {
    try {
      const result = await currencyAPI.convert(from, to, amount);
      return result;
    } catch (error) {
      console.error('Error converting currency:', error);
      message.error('Failed to convert currency');
      return null;
    }
  };

  const currencyColumns: ColumnsType<Currency> = [
    {
      title: 'Currency',
      key: 'currency',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar
            size="small"
            style={{ marginRight: 8, backgroundColor: '#1890ff' }}
          >
            {record.flag || record.code[0]}
          </Avatar>
          <div>
            <Text strong>{record.code}</Text>
            <div style={{ fontSize: '12px', color: '#666' }}>
              {record.name}
            </div>
          </div>
        </div>
      ),
      sorter: (a, b) => a.code.localeCompare(b.code),
    },
    {
      title: 'Symbol',
      dataIndex: 'symbol',
      key: 'symbol',
      render: (symbol, record) => (
        <Text code>
          {record.symbolPosition === 'before' ? symbol : ''}
          1,234.56
          {record.symbolPosition === 'after' ? ` ${symbol}` : ''}
        </Text>
      ),
    },
    {
      title: 'Exchange Rate',
      dataIndex: 'exchangeRate',
      key: 'exchangeRate',
      render: (rate) => (
        <Text strong>{rate.toFixed(4)}</Text>
      ),
      sorter: (a, b) => a.exchangeRate - b.exchangeRate,
    },
    {
      title: 'Status',
      key: 'status',
      render: (_, record) => (
        <Space>
          <Tag color={record.isActive ? 'green' : 'red'}>
            {record.isActive ? 'Active' : 'Inactive'}
          </Tag>
          {record.isDefault && (
            <Tag color="gold" icon={<TrophyOutlined />}>
              Default
            </Tag>
          )}
        </Space>
      ),
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Inactive', value: 'inactive' },
        { text: 'Default', value: 'default' },
      ],
      onFilter: (value, record) => {
        if (value === 'active') return record.isActive;
        if (value === 'inactive') return !record.isActive;
        if (value === 'default') return record.isDefault;
        return true;
      },
    },
    {
      title: 'Last Updated',
      dataIndex: 'lastUpdated',
      key: 'lastUpdated',
      render: (date) => dayjs(date).format('MMM DD, YYYY HH:mm'),
      sorter: (a, b) => dayjs(a.lastUpdated).unix() - dayjs(b.lastUpdated).unix(),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="Edit">
            <Button
              type="text"
              icon={<EditOutlined />}
              onClick={() => {
                setSelectedCurrency(record);
                form.setFieldsValue(record);
                setEditModalOpen(true);
              }}
            />
          </Tooltip>
          <Tooltip title={record.isActive ? 'Deactivate' : 'Activate'}>
            <Button
              type="text"
              icon={record.isActive ? <CloseCircleOutlined /> : <CheckCircleOutlined />}
              onClick={() => handleToggleActive(record)}
              style={{ color: record.isActive ? '#ff4d4f' : '#52c41a' }}
            />
          </Tooltip>
          {!record.isDefault && (
            <Tooltip title="Set as Default">
              <Button
                type="text"
                icon={<TrophyOutlined />}
                onClick={() => handleSetDefault(record.id)}
                style={{ color: '#faad14' }}
              />
            </Tooltip>
          )}
          <Tooltip title="View Rate History">
            <Button
              type="text"
              icon={<LineChartOutlined />}
              onClick={() => {
                setSelectedRatePair({ from: record.code, to: settings?.defaultCurrency || 'USD' });
                setRateHistoryModalOpen(true);
              }}
            />
          </Tooltip>
          <Tooltip title="Delete">
            <Popconfirm
              title="Are you sure you want to delete this currency?"
              onConfirm={() => handleDeleteCurrency(record.id)}
              okText="Delete"
              cancelText="Cancel"
              okButtonProps={{ danger: true }}
            >
              <Button
                type="text"
                icon={<DeleteOutlined />}
                danger
                disabled={record.isDefault}
              />
            </Popconfirm>
          </Tooltip>
        </Space>
      ),
    },
  ];

  const ratesColumns: ColumnsType<ExchangeRate> = [
    {
      title: 'Currency Pair',
      key: 'pair',
      render: (_, record) => (
        <Text strong>
          {record.fromCurrency}/{record.toCurrency}
        </Text>
      ),
    },
    {
      title: 'Rate',
      dataIndex: 'rate',
      key: 'rate',
      render: (rate) => <Text strong>{rate.toFixed(6)}</Text>,
      sorter: (a, b) => a.rate - b.rate,
    },
    {
      title: 'Source',
      dataIndex: 'source',
      key: 'source',
      render: (source) => (
        <Tag color={source === 'api' ? 'blue' : source === 'manual' ? 'orange' : 'green'}>
          {source.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Provider',
      dataIndex: 'provider',
      key: 'provider',
      render: (provider) => provider || '-',
    },
    {
      title: 'Last Updated',
      dataIndex: 'timestamp',
      key: 'timestamp',
      render: (timestamp) => dayjs(timestamp).format('MMM DD, HH:mm'),
      sorter: (a, b) => dayjs(a.timestamp).unix() - dayjs(b.timestamp).unix(),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="View History">
            <Button
              type="text"
              icon={<LineChartOutlined />}
              onClick={() => {
                setSelectedRatePair({ from: record.fromCurrency, to: record.toCurrency });
                setRateHistoryModalOpen(true);
              }}
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  const exportMenuItems = [
    {
      key: 'currencies-csv',
      label: (
        <Space>
          <FileTextOutlined />
          Export Currencies (CSV)
        </Space>
      ),
      onClick: () => handleExport('csv', 'currencies'),
    },
    {
      key: 'currencies-xlsx',
      label: (
        <Space>
          <FileExcelOutlined />
          Export Currencies (Excel)
        </Space>
      ),
      onClick: () => handleExport('xlsx', 'currencies'),
    },
    {
      key: 'rates-csv',
      label: (
        <Space>
          <FileTextOutlined />
          Export Rates (CSV)
        </Space>
      ),
      onClick: () => handleExport('csv', 'rates'),
    },
    {
      key: 'rates-xlsx',
      label: (
        <Space>
          <FileExcelOutlined />
          Export Rates (Excel)
        </Space>
      ),
      onClick: () => handleExport('xlsx', 'rates'),
    },
  ];

  const revenueByCurrentData = stats?.revenueByCurrency ? 
    Object.entries(stats.revenueByCurrency).map(([code, revenue]) => ({
      currency: code,
      revenue: revenue,
    })) : [];

  const conversionVolumeData = stats?.conversionVolume || [];

  return (
    <Spin spinning={loading} tip="Loading currency data...">
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
        {/* Header */}
        <div style={{ marginBottom: 24 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <Title level={3}>
                <GlobalOutlined style={{ color: '#1890ff' }} /> Multi-Currency Management
              </Title>
              <Paragraph type="secondary">
                Manage currencies, exchange rates, and international pricing with real-time data
              </Paragraph>
            </div>
            <Space>
              <RangePicker
                value={dateRange}
                onChange={(dates) => dates && setDateRange(dates as [Dayjs, Dayjs])}
              />
              <Dropdown menu={{ items: exportMenuItems }} placement="bottomRight">
                <Button icon={<DownloadOutlined />}>
                  Export
                </Button>
              </Dropdown>
              <Button
                icon={<SettingOutlined />}
                onClick={() => setSettingsModalOpen(true)}
              >
                Settings
              </Button>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => setCreateModalOpen(true)}
              >
                Add Currency
              </Button>
            </Space>
          </div>
        </div>

        {/* Alert */}
        <Alert
          message="Multi-Currency Support"
          description="Manage global currencies with real-time exchange rates, automatic conversions, and localized pricing. Monitor currency performance and revenue analytics."
          type="info"
          showIcon
          closable
          style={{ marginBottom: 24 }}
        />

        {/* Statistics */}
        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Active Currencies"
                value={stats?.activeCurrencies || 0}
                prefix={<GlobalOutlined />}
                valueStyle={{ color: '#52c41a' }}
                suffix={
                  <Text type="secondary" style={{ fontSize: 12 }}>
                    /{stats?.totalCurrencies || 0} total
                  </Text>
                }
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Total Conversions"
                value={stats?.totalConversions || 0}
                prefix={<SwapOutlined />}
                valueStyle={{ color: '#1890ff' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Revenue (Multi-Currency)"
                value={Object.values(stats?.revenueByCurrency || {}).reduce((sum, val) => sum + val, 0)}
                prefix={<DollarOutlined />}
                valueStyle={{ color: '#722ed1' }}
                precision={2}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Exchange Rate Updates"
                value={exchangeRates.filter(r => dayjs(r.timestamp).isAfter(dayjs().subtract(1, 'day'))).length}
                prefix={<SyncOutlined />}
                valueStyle={{ color: '#faad14' }}
                suffix={
                  <Text type="secondary" style={{ fontSize: 12 }}>
                    /24h
                  </Text>
                }
              />
            </Card>
          </Col>
        </Row>

        {/* Main Content */}
