  trend: 'up' | 'down' | 'stable';
}

interface CategoryPerformance {
  category: string;
  sales: number;
  revenue: number;
  percentage: number;
}

interface CustomerSegment {
  segment: string;
  count: number;
  avgOrderValue: number;
  totalRevenue: number;
  color: string;
}

const productPerformance: ProductPerformance[] = [
  {
    id: '1',
    name: 'Wireless Headphones Pro',
    category: 'Electronics',
    views: 8920,
