        { text: 'Home & Garden', value: 'Home & Garden' },
      ],
      onFilter: (value, record) => record.category === value,
    },
    {
      title: 'Views',
      dataIndex: 'views',
      key: 'views',
      render: (views) => (
        <Space>
          <EyeOutlined />
          <Text>{views.toLocaleString()}</Text>
        </Space>
      ),
      sorter: (a, b) => a.views - b.views,
    },
    {
      title: 'Add to Cart',
      dataIndex: 'addToCart',
      key: 'addToCart',
      render: (count) => (
        <Space>
          <ShoppingCartOutlined />
          <Text>{count.toLocaleString()}</Text>
        </Space>
      ),
      sorter: (a, b) => a.addToCart - b.addToCart,
    },
    {
      title: 'Purchases',
      dataIndex: 'purchases',
      key: 'purchases',
      render: (count) => <Text strong>{count.toLocaleString()}</Text>,
      sorter: (a, b) => a.purchases - b.purchases,
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      render: (revenue) => <Text strong style={{ color: '#52c41a' }}>${revenue.toLocaleString()}</Text>,
      sorter: (a, b) => a.revenue - b.revenue,
    },
    {
      title: 'Conversion',
      dataIndex: 'conversionRate',
      key: 'conversionRate',
      render: (rate) => <Tag color="green">{rate}%</Tag>,
      sorter: (a, b) => a.conversionRate - b.conversionRate,
    },
    {
      title: 'Trend',
      dataIndex: 'trend',
      key: 'trend',
      render: (trend: ProductPerformance['trend']) => {
        const config: Record<ProductPerformance['trend'], { icon: React.ReactNode; color: string }> = {
          up: { icon: <RiseOutlined />, color: '#52c41a' },
          down: { icon: <FallOutlined />, color: '#ff4d4f' },
          stable: { icon: null, color: '#8c8c8c' },
        };
        return (
          <Tag color={config[trend].color} icon={config[trend].icon}>
            {trend.toUpperCase()}
          </Tag>
        );
      },
    },
  ];

  const salesTrendConfig = {
    data: salesTrendData,
    xField: 'date',
    yField: 'sales',
    smooth: true,
    label: {
      position: 'top' as const,
      style: {
        fill: '#000000',
        opacity: 0.6,
        fontSize: 10,
      },
    },
    meta: {
      sales: {
        formatter: (v: number) => `$${(v / 1000).toFixed(1)}k`,
      },
    },
  };

  const categoryConfig = {
    data: categoryPerformance,
    xField: 'category',
    yField: 'revenue',
    label: {
      position: 'top' as const,
      style: {
        fill: '#000000',
        opacity: 0.6,
      },
    },
    meta: {
      revenue: {
        formatter: (v: number) => `$${(v / 1000).toFixed(0)}k`,
      },
    },
  };

  const customerSegmentConfig = {
    data: customerSegments.map((s: any) => ({ type: s.segment, value: s.count })),
    angleField: 'value',
    colorField: 'type',
    radius: 0.8,
    label: {
      type: 'outer',
      content: '{name} {percentage}',
    },
    interactions: [{ type: 'element-active' }],
  };

  const totalRevenue = overview?.totalRevenue || productPerformance.reduce((sum, p) => sum + p.revenue, 0);
  const totalOrders = overview?.totalOrders || productPerformance.reduce((sum, p) => sum + p.purchases, 0);
  const avgOrderValue = overview?.averageOrderValue || (totalRevenue / totalOrders);
  const totalViews = productPerformance.reduce((sum, p) => sum + p.views, 0);
  const conversionRate = overview?.conversionRate || ((totalOrders / totalViews) * 100);

  const exportMenuItems = [
    {
      key: 'csv',
      label: (
        <Space>
          <FileTextOutlined />
          Export as CSV
        </Space>
      ),
      onClick: () => handleExport('csv'),
    },
    {
      key: 'xlsx',
      label: (
        <Space>
          <FileExcelOutlined />
          Export as Excel
        </Space>
      ),
      onClick: () => handleExport('xlsx'),
    },
    {
      key: 'json',
      label: (
        <Space>
          <FileTextOutlined />
          Export as JSON
        </Space>
      ),
      onClick: () => handleExport('json'),
    },
  ];

  return (
    <Spin spinning={loading} tip="Loading analytics data...">
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
        <div style={{ marginBottom: 24 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <Title level={3}>
                <BarChartOutlined style={{ color: '#1890ff' }} /> Sales Reports & Analytics
              </Title>
              <Paragraph type="secondary">
                Comprehensive sales analytics, product performance, and revenue insights
              </Paragraph>
            </div>
            <Space>
              <Select value={selectedCategory} onChange={setSelectedCategory} style={{ width: 150 }}>
                <Select.Option value="all">All Categories</Select.Option>
                <Select.Option value="electronics">Electronics</Select.Option>
                <Select.Option value="fashion">Fashion</Select.Option>
                <Select.Option value="home">Home & Garden</Select.Option>
              </Select>
              <RangePicker
                value={dateRange}
                onChange={(dates) => dates && setDateRange(dates as [Dayjs, Dayjs])}
              />
              <Dropdown menu={{ items: exportMenuItems }} placement="bottomRight">
                <Button type="primary" icon={<DownloadOutlined />}>
                  Export Report
                </Button>
              </Dropdown>
            </Space>
          </div>
        </div>

        <Alert
          message="Real-Time Sales Data"
          description="Sales data is updated every 15 minutes. Export reports in CSV, PDF, or Excel format using the API integration."
          type="info"
          showIcon
          closable
          style={{ marginBottom: 24 }}
        />

        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Total Revenue"
                value={totalRevenue}
                prefix={<DollarOutlined />}
                valueStyle={{ color: '#52c41a' }}
                precision={2}
                suffix={
                  <Tag color="green" icon={<RiseOutlined />} style={{ marginLeft: 8 }}>
                    +{overview?.revenueChange || 15.2}%
                  </Tag>
                }
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Total Orders"
                value={totalOrders}
                prefix={<ShoppingOutlined />}
                valueStyle={{ color: '#1890ff' }}
                suffix={
                  <Tag color="blue" icon={<RiseOutlined />} style={{ marginLeft: 8 }}>
                    +{overview?.ordersChange || 8.4}%
                  </Tag>
                }
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Avg Order Value"
                value={avgOrderValue}
                prefix={<DollarOutlined />}
                valueStyle={{ color: '#faad14' }}
                precision={2}
                suffix={
                  <Tag color="orange" icon={<RiseOutlined />} style={{ marginLeft: 8 }}>
                    +{overview?.aovChange || 12.1}%
                  </Tag>
                }
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Conversion Rate"
                value={conversionRate}
                prefix={<TrophyOutlined />}
                valueStyle={{ color: '#722ed1' }}
                precision={2}
                suffix="%"
              />
            </Card>
          </Col>
        </Row>

      <Tabs
        defaultActiveKey="overview"
        items={[
          {
            key: 'overview',
            label: 'Overview',
            children: (
              <Row gutter={[16, 16]}>
                <Col xs={24} lg={16}>
                  <Card title="Sales Trend (Last 30 Days)">
                    <Line {...salesTrendConfig} height={300} />
                  </Card>
                </Col>
                <Col xs={24} lg={8}>
                  <Card title="Customer Segments">
                    <Pie {...customerSegmentConfig} height={300} />
                  </Card>
                </Col>
                <Col xs={24}>
                  <Card title="Revenue by Category">
                    <Column {...categoryConfig} height={300} />
                  </Card>
                </Col>
              </Row>
            ),
          },
          {
            key: 'products',
            label: 'Product Performance',
            children: (
              <Card title="Top Performing Products">
                <Table
                  columns={productColumns}
                  dataSource={productPerformance}
                  rowKey="id"
                  pagination={{ pageSize: 10 }}
                />
              </Card>
            ),
          },
          {
            key: 'categories',
            label: 'Category Analysis',
            children: (
              <Row gutter={16}>
                <Col xs={24} lg={16}>
                  <Card title="Category Performance">
                    <Table
                      dataSource={categoryPerformance}
                      rowKey="category"
                      pagination={false}
                      columns={[
                        {
                          title: 'Category',
                          dataIndex: 'category',
                          key: 'category',
                          render: (cat) => <Text strong>{cat}</Text>,
                        },
                        {
                          title: 'Sales',
                          dataIndex: 'sales',
                          key: 'sales',
                          render: (sales) => <Text>{sales.toLocaleString()}</Text>,
                          sorter: (a, b) => a.sales - b.sales,
                        },
                        {
                          title: 'Revenue',
                          dataIndex: 'revenue',
                          key: 'revenue',
                          render: (revenue) => (
                            <Text strong style={{ color: '#52c41a' }}>
                              ${revenue.toLocaleString()}
                            </Text>
                          ),
                          sorter: (a, b) => a.revenue - b.revenue,
                        },
                        {
                          title: 'Market Share',
                          key: 'share',
                          render: (_, record) => (
                            <div>
                              <Progress percent={record.percentage} size="small" />
                            </div>
                          ),
                        },
                      ]}
                    />
                  </Card>
                </Col>
                <Col xs={24} lg={8}>
                  <Card title="Category Insights">
                    <List
                      dataSource={categoryPerformance.slice(0, 3)}
                      renderItem={(cat, index) => (
                        <List.Item>
                          <List.Item.Meta
                            avatar={
                              <Avatar
                                style={{
                                  backgroundColor: ['#1890ff', '#52c41a', '#faad14'][index],
                                }}
                              >
                                {index + 1}
                              </Avatar>
                            }
                            title={cat.category}
                            description={`$${cat.revenue.toLocaleString()} revenue`}
                          />
                          <Tag color="blue">{cat.percentage}%</Tag>
                        </List.Item>
                      )}
                    />
                  </Card>
                </Col>
              </Row>
            ),
          },
          {
            key: 'customers',
            label: 'Customer Insights',
            children: (
              <Row gutter={16}>
                <Col xs={24} lg={12}>
                  <Card title="Customer Segments">
                    <List
                      dataSource={customerSegments}
                      renderItem={(segment: any) => (
                        <List.Item>
                          <List.Item.Meta
                            avatar={
                              <Avatar
                                style={{ backgroundColor: segment.color }}
                                icon={<UserOutlined />}
                              />
                            }
                            title={segment.segment}
                            description={`${segment.count} customers  Avg Order: $${segment.avgOrderValue}`}
                          />
                          <Text strong style={{ color: '#52c41a' }}>
                            ${segment.totalRevenue.toLocaleString()}
                          </Text>
                        </List.Item>
                      )}
                    />
                  </Card>
                </Col>
                <Col xs={24} lg={12}>
                  <Card title="Customer Metrics">
                    <Space direction="vertical" style={{ width: '100%' }} size="large">
                      <div>
                        <Text type="secondary">Lifetime Value (LTV)</Text>
                        <div style={{ fontSize: 28, fontWeight: 'bold', color: '#1890ff' }}>
                          ${customerAnalytics?.customerLifetimeValue || 2450}
                        </div>
                      </div>
                      <div>
                        <Text type="secondary">Retention Rate</Text>
                        <div style={{ fontSize: 24, fontWeight: 'bold', color: '#52c41a' }}>
                          {customerAnalytics?.retentionRate || 68.5}%
                        </div>
                        <Progress percent={customerAnalytics?.retentionRate || 68.5} strokeColor="#52c41a" />
                      </div>
                      <div>
                        <Text type="secondary">Churn Rate</Text>
                        <div style={{ fontSize: 24, fontWeight: 'bold', color: '#ff4d4f' }}>
                          {customerAnalytics?.churnRate || 12.3}%
                        </div>
                        <Progress percent={customerAnalytics?.churnRate || 12.3} strokeColor="#ff4d4f" />
                      </div>
                    </Space>
                  </Card>
                </Col>
              </Row>
            ),
          },
        ]}
      />
      </div>
    </Spin>
  );
};

export default SalesReportsPage;

customer/gift-cards.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  InputNumber,
  Form,
  Input,
  Select,
  Table,
  Tag,
  Space,
  Divider,
  Alert,
  Image,
  message,
  Modal,
  Radio,
  Spin,
  Tooltip,
  DatePicker,
  Badge,
} from 'antd';
import {
  GiftOutlined,
  CreditCardOutlined,
  MailOutlined,
  PhoneOutlined,
  DollarOutlined,
  CheckCircleOutlined,
  CopyOutlined,
  ReloadOutlined,
  ShareAltOutlined,
  HistoryOutlined,
  ExclamationCircleOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { giftCardsAPI, type GiftCard, type GiftCardTemplate, type GiftCardPurchase, type GiftCardBalance } from '@/services/api/giftCards';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TextArea } = Input;

const presetAmounts = [25, 50, 100, 150, 200];

const GiftCardsPage: React.FC = () => {
  const [giftCards, setGiftCards] = useState<GiftCard[]>([]);
  const [templates, setTemplates] = useState<GiftCardTemplate[]>([]);
  const [loading, setLoading] = useState(true);
  const [purchasing, setPurchasing] = useState(false);
  const [redeeming, setRedeeming] = useState(false);
  const [balanceChecking, setBalanceChecking] = useState(false);
  const [isPurchaseModalVisible, setIsPurchaseModalVisible] = useState(false);
  const [isRedeemModalVisible, setIsRedeemModalVisible] = useState(false);
  const [isBalanceModalVisible, setIsBalanceModalVisible] = useState(false);
  const [balanceResult, setBalanceResult] = useState<GiftCardBalance | null>(null);
  const [customAmount, setCustomAmount] = useState<number>(50);
  const [selectedAmount, setSelectedAmount] = useState<number>(50);
  const [selectedTemplate, setSelectedTemplate] = useState<string>('');
  const [deliveryMethod, setDeliveryMethod] = useState<'email' | 'sms' | 'print' | 'instant'>('email');
  const [form] = Form.useForm();
  const [redeemForm] = Form.useForm();
  const [balanceForm] = Form.useForm();

  // Load data on component mount
  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [cardsResult, templatesResult] = await Promise.all([
        giftCardsAPI.getCustomerGiftCards().catch(() => null),
        giftCardsAPI.getTemplates().catch(() => null),
      ]);

      if (cardsResult) {
        setGiftCards(cardsResult.data);
      } else {
        console.warn('Gift cards API not available, using mock data');
        // Fallback to mock data with proper typing
        const mockGiftCards: GiftCard[] = [
          {
            id: '1',
            code: 'GIFT-XXXX-XXXX-1234',
            balance: 45.50,
            originalAmount: 50.00,
            status: 'active',
            purchaseDate: '2024-10-15',
            expiryDate: '2025-10-15',
            purchasedBy: 'current-user',
            purchasedByEmail: 'user@example.com',
            deliveryMethod: 'email',
            isRedeemable: true,
            redemptions: [],
            createdAt: '2024-10-15T10:00:00Z',
            updatedAt: '2024-10-15T10:00:00Z',
          },
          {
            id: '2',
            code: 'GIFT-XXXX-XXXX-5678',
            balance: 0,
            originalAmount: 25.00,
            status: 'redeemed',
            purchaseDate: '2024-09-01',
            expiryDate: '2025-09-01',
            purchasedBy: 'current-user',
            purchasedByEmail: 'user@example.com',
            recipientEmail: 'friend@example.com',
            deliveryMethod: 'email',
            isRedeemable: false,
            redemptions: [
              {
                id: 'r1',
                giftCardId: '2',
                amount: 25.00,
                redeemedBy: 'friend@example.com',
                redeemedAt: '2024-09-15T14:30:00Z',
                description: 'Order #12345',
                remainingBalance: 0,
              },
            ],
            createdAt: '2024-09-01T10:00:00Z',
            updatedAt: '2024-09-15T14:30:00Z',
          },
        ];
        setGiftCards(mockGiftCards);
      }

      if (templatesResult) {
        setTemplates(templatesResult);
      } else {
        console.warn('Gift card templates API not available, using mock data');
        // Mock templates
        const mockTemplates: GiftCardTemplate[] = [
          {
            id: 'default',
            name: 'Classic',
            description: 'Traditional gift card design',
            imageUrl: '/api/placeholder/200/120',
            previewUrl: '/api/placeholder/400/240',
            isActive: true,
            category: 'general',
            customizable: true,
            minAmount: 10,
            maxAmount: 500,
            createdAt: '2024-01-01T00:00:00Z',
          },
          {
            id: 'birthday',
            name: 'Birthday Special',
            description: 'Perfect for birthday celebrations',
            imageUrl: '/api/placeholder/200/120',
            previewUrl: '/api/placeholder/400/240',
            isActive: true,
            category: 'birthday',
            customizable: true,
            allowedAmounts: [25, 50, 100],
            createdAt: '2024-01-01T00:00:00Z',
          },
        ];
        setTemplates(mockTemplates);
      }
    } catch (error) {
      console.error('Error loading gift cards data:', error);
      message.error('Failed to load gift cards data');
    } finally {
      setLoading(false);
    }
  };

  const handlePurchase = async (values: any) => {
    try {
      setPurchasing(true);

      const purchaseData: GiftCardPurchase = {
        quantity: 1,
        amount: selectedAmount,
        templateId: selectedTemplate || undefined,
        recipientEmail: values.recipientEmail,
        recipientName: values.recipientName,
        senderName: values.senderName || 'Anonymous',
        personalMessage: values.message,
        deliveryMethod,
        scheduledDelivery: values.sendDate === 'later' ? values.scheduledDate : undefined,
        recipientPhone: values.recipientPhone,
      };

      const result = await giftCardsAPI.purchaseGiftCards(purchaseData).catch(() => null);

      if (result) {
        message.success(`Gift card purchased successfully! ${deliveryMethod === 'email' ? 'Email sent to recipient' : 'Ready for delivery'}`);
        await loadData(); // Refresh the list
      } else {
        // Fallback to mock behavior
        const newGiftCard: GiftCard = {
          id: Date.now().toString(),
          code: `GIFT-${Math.random().toString(36).substr(2, 4).toUpperCase()}-${Math.random().toString(36).substr(2, 4).toUpperCase()}-${Math.random().toString(36).substr(2, 4).toUpperCase()}`,
          balance: selectedAmount,
          originalAmount: selectedAmount,
          status: 'active',
          purchaseDate: new Date().toISOString().split('T')[0],
          expiryDate: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
          purchasedBy: 'current-user',
          purchasedByEmail: 'user@example.com',
          recipientEmail: values.recipientEmail,
          deliveryMethod,
          isRedeemable: true,
          redemptions: [],
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
        };

        setGiftCards([newGiftCard, ...giftCards]);
        message.success(`Gift card purchased successfully! ${deliveryMethod === 'email' ? 'Email sent to recipient' : 'Ready to print'}`);
      }

      setIsPurchaseModalVisible(false);
      form.resetFields();
      setSelectedAmount(50);
    } catch (error) {
      console.error('Error purchasing gift card:', error);
      message.error('Failed to purchase gift card');
    } finally {
      setPurchasing(false);
    }
  };

  const handleRedeem = async (values: any) => {
    try {
      setRedeeming(true);

      const result = await giftCardsAPI.redeemGiftCard({
        code: values.code.toUpperCase(),
        amount: values.amount || 0,
        description: 'Manual redemption',
      }).catch(() => null);

      if (result) {
        message.success(`Gift card redeemed successfully! $${result.redemption.amount} added to your account balance.`);
        await loadData(); // Refresh the list
      } else {
        // Fallback to mock behavior
        message.success(`Gift card redeemed successfully! $${values.amount || 25} added to your account balance.`);
      }

      setIsRedeemModalVisible(false);
      redeemForm.resetFields();
    } catch (error) {
      console.error('Error redeeming gift card:', error);
      message.error('Invalid gift card code or insufficient balance');
    } finally {
      setRedeeming(false);
    }
  };

  const handleCheckBalance = async (values: any) => {
    try {
      setBalanceChecking(true);

      const result = await giftCardsAPI.checkBalance(values.code.toUpperCase()).catch(() => null);

      if (result) {
        setBalanceResult(result);
      } else {
        // Mock balance check
        const mockBalance: GiftCardBalance = {
          code: values.code.toUpperCase(),
          balance: 45.50,
          originalAmount: 50.00,
          status: 'active',
          expiryDate: '2025-10-15',
          isExpired: false,
          canRedeem: true,
          lastUsed: '2024-10-01',
        };
        setBalanceResult(mockBalance);
      }
    } catch (error) {
      console.error('Error checking balance:', error);
      message.error('Invalid gift card code');
      setBalanceResult(null);
    } finally {
      setBalanceChecking(false);
    }
  };

  const copyToClipboard = (code: string) => {
    navigator.clipboard.writeText(code);
    message.success('Gift card code copied to clipboard');
  };

  const shareGiftCard = async (giftCardId: string) => {
    try {
      const result = await giftCardsAPI.shareGiftCard(giftCardId, 'email', {
        recipientEmail: 'friend@example.com',
        message: 'Check out this gift card!',
      }).catch(() => null);

      if (result) {
        message.success('Gift card shared successfully!');
      } else {
        message.success('Share link copied to clipboard!');
      }
    } catch (error) {
      console.error('Error sharing gift card:', error);
      message.error('Failed to share gift card');
    }
  };

  const getStatusTag = (status: string) => {
    const config = {
      active: { color: 'green', text: 'Active' },
      redeemed: { color: 'default', text: 'Fully Used' },
      expired: { color: 'red', text: 'Expired' },
      pending: { color: 'orange', text: 'Pending' },
      cancelled: { color: 'red', text: 'Cancelled' },
    };
    const { color, text } = config[status as keyof typeof config] || { color: 'default', text: status };
    return <Tag color={color}>{text}</Tag>;
  };

  const columns: ColumnsType<GiftCard> = [
    {
      title: 'Gift Card Code',
      dataIndex: 'code',
      key: 'code',
      render: (code) => (
        <Space>
          <CreditCardOutlined style={{ fontSize: 18, color: '#ff9900' }} />
          <Text code>{code}</Text>
          <Button
            type="link"
            size="small"
            icon={<CopyOutlined />}
            onClick={() => copyToClipboard(code)}
          />
        </Space>
      ),
    },
    {
      title: 'Balance',
      dataIndex: 'balance',
      key: 'balance',
      render: (balance, record) => (
        <div>
          <Text strong style={{ fontSize: 16, color: balance > 0 ? '#52c41a' : '#8c8c8c' }}>
            ${balance.toFixed(2)}
          </Text>
          <br />
          <Text type="secondary" style={{ fontSize: 12 }}>
            of ${record.originalAmount.toFixed(2)}
          </Text>
        </div>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status, record) => (
        <Space direction="vertical" size="small">
          {getStatusTag(status)}
          {status === 'active' && new Date(record.expiryDate) < new Date() && (
            <Badge status="warning" text="Expires Soon" />
          )}
        </Space>
      ),
    },
    {
      title: 'Purchase Date',
      dataIndex: 'purchaseDate',
      key: 'purchaseDate',
      render: (date) => new Date(date).toLocaleDateString(),
    },
    {
      title: 'Expires',
      dataIndex: 'expiryDate',
      key: 'expiryDate',
      render: (date) => {
        const expiryDate = new Date(date);
        const isExpiringSoon = expiryDate.getTime() - Date.now() < 30 * 24 * 60 * 60 * 1000; // 30 days
        return (
          <Text type={isExpiringSoon ? 'warning' : 'secondary'}>
            {expiryDate.toLocaleDateString()}
          </Text>
        );
      },
    },
    {
      title: 'Recipient',
      dataIndex: 'recipientEmail',
      key: 'recipientEmail',
      render: (email) => email ? <Text type="secondary">{email}</Text> : <Text type="secondary">Self</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Tooltip title="Share Gift Card">
            <Button
              type="link"
              icon={<ShareAltOutlined />}
              onClick={() => shareGiftCard(record.id)}
              disabled={record.status !== 'active'}
            />
          </Tooltip>
          {record.redemptions && record.redemptions.length > 0 && (
            <Tooltip title="View Redemption History">
              <Button
                type="link"
                icon={<HistoryOutlined />}
                onClick={() => {
                  Modal.info({
                    title: 'Redemption History',
                    content: (
                      <div>
                        {record.redemptions.map((redemption, index) => (
                          <div key={index} style={{ marginBottom: 8 }}>
                            <Text strong>${redemption.amount.toFixed(2)}</Text> - {redemption.description}
                            <br />
                            <Text type="secondary">
                              {new Date(redemption.redeemedAt).toLocaleString()}
                            </Text>
                          </div>
                        ))}
                      </div>
                    ),
                  });
                }}
              />
            </Tooltip>
          )}
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <GiftOutlined style={{ color: '#ff9900' }} /> Gift Cards
        </Title>
        <Paragraph type="secondary">
          Purchase gift cards for friends and family, or redeem gift cards you've received
        </Paragraph>
      </div>

      {/* Action Buttons */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card
            hoverable
            onClick={() => setIsPurchaseModalVisible(true)}
            style={{ textAlign: 'center', cursor: 'pointer' }}
          >
            <GiftOutlined style={{ fontSize: 48, color: '#ff9900', marginBottom: 16 }} />
            <Title level={4}>Purchase Gift Card</Title>
            <Paragraph type="secondary">
              Give the perfect gift - let them choose what they want
            </Paragraph>
            <Button type="primary" size="large">
              Buy Gift Card
            </Button>
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card
            hoverable
            onClick={() => setIsRedeemModalVisible(true)}
            style={{ textAlign: 'center', cursor: 'pointer' }}
          >
            <CheckCircleOutlined style={{ fontSize: 48, color: '#52c41a', marginBottom: 16 }} />
            <Title level={4}>Redeem Gift Card</Title>
            <Paragraph type="secondary">
              Have a gift card code? Apply it to your account balance
            </Paragraph>
            <Button type="primary" size="large" style={{ background: '#52c41a', borderColor: '#52c41a' }}>
              Redeem Now
            </Button>
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card
            hoverable
            onClick={() => setIsBalanceModalVisible(true)}
            style={{ textAlign: 'center', cursor: 'pointer' }}
          >
            <DollarOutlined style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }} />
            <Title level={4}>Check Balance</Title>
            <Paragraph type="secondary">
              Check the remaining balance on any gift card
            </Paragraph>
            <Button type="primary" size="large" style={{ background: '#1890ff', borderColor: '#1890ff' }}>
              Check Balance
            </Button>
          </Card>
        </Col>
      </Row>

      {/* My Gift Cards */}
      <Card 
        title={
          <Space>
            <CreditCardOutlined />
            My Gift Cards
            <Button
              type="link"
              icon={<ReloadOutlined />}
              onClick={loadData}
              loading={loading}
            >
              Refresh
            </Button>
          </Space>
        }
      >
        {loading ? (
          <div style={{ textAlign: 'center', padding: '50px 0' }}>
            <Spin size="large" />
            <div style={{ marginTop: 16 }}>
              <Text type="secondary">Loading gift cards...</Text>
            </div>
          </div>
        ) : (
          <Table
            columns={columns}
            dataSource={giftCards}
            rowKey="id"
            pagination={{ pageSize: 10 }}
            locale={{
              emptyText: (
                <div style={{ padding: '50px 0' }}>
                  <GiftOutlined style={{ fontSize: 64, color: '#d9d9d9', marginBottom: 16 }} />
                  <div>
                    <Text type="secondary">No gift cards found</Text>
                  </div>
                  <div style={{ marginTop: 16 }}>
                    <Button type="primary" onClick={() => setIsPurchaseModalVisible(true)}>
                      Purchase Your First Gift Card
                    </Button>
                  </div>
                </div>
              ),
            }}
          />
        )}
      </Card>

      {/* Purchase Modal */}
      <Modal
        title={<><GiftOutlined /> Purchase Gift Card</>}
        open={isPurchaseModalVisible}
        onCancel={() => {
          setIsPurchaseModalVisible(false);
          form.resetFields();
          setSelectedAmount(50);
          setSelectedTemplate('');
        }}
        footer={null}
        width={800}
      >
        <Form form={form} layout="vertical" onFinish={handlePurchase}>
          <Alert
            message="Perfect for any occasion!"
            description="Gift cards are valid for 1 year from the date of purchase and can be used on any product in our store."
            type="info"
            showIcon
            closable
            style={{ marginBottom: 24 }}
          />

          {/* Template Selection */}
          {templates.length > 0 && (
            <>
              <div style={{ marginBottom: 24 }}>
                <Title level={5}>Choose Design</Title>
                <Row gutter={[12, 12]}>
                  {templates.map((template) => (
                    <Col xs={12} sm={8} md={6} key={template.id}>
                      <Card
                        hoverable
                        onClick={() => setSelectedTemplate(template.id)}
                        style={{
                          cursor: 'pointer',
                          border: selectedTemplate === template.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                          background: selectedTemplate === template.id ? '#e6f7ff' : 'white',
                        }}
                        cover={
                          <Image
                            src={template.imageUrl}
                            alt={template.name}
                            height={80}
                            preview={false}
                            fallback="/api/placeholder/150/80"
                          />
                        }
                      >
                        <Card.Meta
                          title={<Text style={{ fontSize: 12 }}>{template.name}</Text>}
                          description={<Text style={{ fontSize: 10 }} type="secondary">{template.category}</Text>}
                        />
                      </Card>
                    </Col>
                  ))}
                </Row>
              </div>
              <Divider />
            </>
          )}

          <div style={{ marginBottom: 24 }}>
            <Title level={5}>Select Amount</Title>
            <Row gutter={[12, 12]}>
              {presetAmounts.map((amount: number) => (
                <Col xs={12} sm={8} md={4} key={amount}>
                  <Card
                    hoverable
                    onClick={() => setSelectedAmount(amount)}
                    style={{
                      textAlign: 'center',
                      cursor: 'pointer',
                      border: selectedAmount === amount ? '2px solid #1890ff' : '1px solid #d9d9d9',
                      background: selectedAmount === amount ? '#e6f7ff' : 'white',
                    }}
                  >
                    <DollarOutlined style={{ fontSize: 24, marginBottom: 8 }} />
                    <div>
                      <Text strong style={{ fontSize: 20 }}>${amount}</Text>
                    </div>
                  </Card>
                </Col>
              ))}
              <Col xs={12} sm={8} md={4}>
                <Card
                  hoverable
                  onClick={() => {
                    setSelectedAmount(customAmount);
                  }}
                  style={{
                    textAlign: 'center',
                    cursor: 'pointer',
                    border: !presetAmounts.includes(selectedAmount) ? '2px solid #1890ff' : '1px solid #d9d9d9',
                    background: !presetAmounts.includes(selectedAmount) ? '#e6f7ff' : 'white',
                  }}
                >
                  <InputNumber
                    min={10}
                    max={500}
                    value={customAmount}
                    onChange={(value) => {
                      if (value) {
                        setCustomAmount(value);
                        setSelectedAmount(value);
                      }
                    }}
                    prefix="$"
                    style={{ width: '100%' }}
                    onClick={(e) => e.stopPropagation()}
                  />
                  <Text type="secondary" style={{ fontSize: 11 }}>Custom</Text>
                </Card>
              </Col>
            </Row>
          </div>

          <Divider />

          <Form.Item label="Delivery Method">
            <Radio.Group value={deliveryMethod} onChange={(e) => setDeliveryMethod(e.target.value)}>
              <Space direction="vertical">
                <Radio value="email">
                  <MailOutlined /> Email to Recipient
                </Radio>
                <Radio value="sms">
                  <PhoneOutlined /> SMS to Recipient
                </Radio>
                <Radio value="instant">
                  <CheckCircleOutlined /> Instant Delivery (Digital)
                </Radio>
                <Radio value="print">
                  <CreditCardOutlined /> Print at Home
                </Radio>
              </Space>
            </Radio.Group>
          </Form.Item>

          <Form.Item
            name="senderName"
            label="Your Name"
            rules={[{ required: true, message: 'Please enter your name' }]}
          >
            <Input placeholder="Your name" />
          </Form.Item>

          {(deliveryMethod === 'email' || deliveryMethod === 'instant') && (
            <>
              <Form.Item
                name="recipientName"
                label="Recipient Name"
                rules={[{ required: true, message: 'Please enter recipient name' }]}
              >
                <Input placeholder="John Doe" />
              </Form.Item>

              <Form.Item
                name="recipientEmail"
                label="Recipient Email"
                rules={[
                  { required: true, message: 'Please enter recipient email' },
                  { type: 'email', message: 'Please enter a valid email' },
                ]}
              >
                <Input placeholder="john@example.com" prefix={<MailOutlined />} />
              </Form.Item>
            </>
          )}

          {deliveryMethod === 'sms' && (
            <>
              <Form.Item
                name="recipientName"
                label="Recipient Name"
                rules={[{ required: true, message: 'Please enter recipient name' }]}
              >
                <Input placeholder="John Doe" />
              </Form.Item>

              <Form.Item
                name="recipientPhone"
                label="Recipient Phone"
                rules={[{ required: true, message: 'Please enter recipient phone number' }]}
              >
                <Input placeholder="+1 (555) 123-4567" prefix={<PhoneOutlined />} />
              </Form.Item>
            </>
          )}

          {deliveryMethod !== 'print' && (
            <Form.Item name="message" label="Personal Message (Optional)">
              <TextArea
                rows={3}
                placeholder="Write a personal message to the recipient..."
                maxLength={200}
                showCount
              />
            </Form.Item>
          )}

          {deliveryMethod === 'email' && (
            <Form.Item name="sendDate" label="Send Date">
              <Radio.Group defaultValue="now">
                <Space direction="vertical">
                  <Radio value="now">Send Immediately</Radio>
                  <Radio value="later">Schedule for Later</Radio>
                </Space>
              </Radio.Group>
            </Form.Item>
          )}

          <Divider />

          <div style={{ background: '#fafafa', padding: 16, borderRadius: 8, marginBottom: 16 }}>
            <Row justify="space-between" style={{ marginBottom: 8 }}>
              <Text>Gift Card Amount:</Text>
              <Text strong style={{ fontSize: 18 }}>${selectedAmount.toFixed(2)}</Text>
            </Row>
            <Row justify="space-between" style={{ marginBottom: 8 }}>
              <Text>Processing Fee:</Text>
              <Text>$0.00</Text>
            </Row>
            <Divider style={{ margin: '12px 0' }} />
            <Row justify="space-between">
              <Text strong style={{ fontSize: 16 }}>Total:</Text>
              <Text strong style={{ fontSize: 20, color: '#ff9900' }}>
                ${selectedAmount.toFixed(2)}
              </Text>
            </Row>
          </div>

          <Form.Item style={{ marginBottom: 0 }}>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button
                onClick={() => {
                  setIsPurchaseModalVisible(false);
                  form.resetFields();
                  setSelectedAmount(50);
                  setSelectedTemplate('');
                }}
              >
                Cancel
              </Button>
              <Button 
                type="primary" 
                htmlType="submit" 
                size="large"
                loading={purchasing}
              >
                Purchase Gift Card - ${selectedAmount.toFixed(2)}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Redeem Modal */}
      <Modal
        title={<><CheckCircleOutlined /> Redeem Gift Card</>}
        open={isRedeemModalVisible}
        onCancel={() => {
          setIsRedeemModalVisible(false);
          redeemForm.resetFields();
        }}
        footer={null}
        width={500}
      >
        <Form form={redeemForm} layout="vertical" onFinish={handleRedeem}>
          <Alert
            message="Enter your gift card code"
            description="The gift card balance will be added to your account and can be used on your next purchase."
            type="success"
            showIcon
            style={{ marginBottom: 24 }}
          />

          <Form.Item
            name="code"
            label="Gift Card Code"
            rules={[{ required: true, message: 'Please enter gift card code' }]}
          >
            <Input
              placeholder="GIFT-XXXX-XXXX-XXXX"
              size="large"
              prefix={<CreditCardOutlined />}
              style={{ textTransform: 'uppercase' }}
            />
          </Form.Item>

          <Form.Item
            name="amount"
            label="Amount to Redeem (Optional)"
            help="Leave empty to redeem full balance"
          >
            <InputNumber
              min={0}
              precision={2}
              prefix="$"
              placeholder="25.00"
              style={{ width: '100%' }}
            />
          </Form.Item>

          <Divider />

          <Form.Item style={{ marginBottom: 0 }}>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button
                onClick={() => {
                  setIsRedeemModalVisible(false);
                  redeemForm.resetFields();
                }}
              >
                Cancel
              </Button>
              <Button 
                type="primary" 
                htmlType="submit" 
                size="large"
                loading={redeeming}
              >
                Redeem Gift Card
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Balance Check Modal */}
      <Modal
        title={<><DollarOutlined /> Check Gift Card Balance</>}
        open={isBalanceModalVisible}
        onCancel={() => {
          setIsBalanceModalVisible(false);
          balanceForm.resetFields();
          setBalanceResult(null);
        }}
        footer={null}
        width={500}
      >
        <Form form={balanceForm} layout="vertical" onFinish={handleCheckBalance}>
          <Alert
            message="Check your gift card balance"
            description="Enter the gift card code to view the current balance and status."
            type="info"
            showIcon
            style={{ marginBottom: 24 }}
          />

          <Form.Item
            name="code"
            label="Gift Card Code"
            rules={[{ required: true, message: 'Please enter gift card code' }]}
          >
            <Input
              placeholder="GIFT-XXXX-XXXX-XXXX"
              size="large"
              prefix={<CreditCardOutlined />}
              style={{ textTransform: 'uppercase' }}
            />
          </Form.Item>

          {balanceResult && (
            <div style={{ background: '#f6ffed', border: '1px solid #b7eb8f', borderRadius: 8, padding: 16, marginBottom: 16 }}>
              <Row justify="space-between" style={{ marginBottom: 8 }}>
                <Text>Current Balance:</Text>
                <Text strong style={{ fontSize: 20, color: '#52c41a' }}>
                  ${balanceResult.balance.toFixed(2)}
                </Text>
              </Row>
              <Row justify="space-between" style={{ marginBottom: 8 }}>
                <Text>Original Amount:</Text>
                <Text>${balanceResult.originalAmount.toFixed(2)}</Text>
              </Row>
              <Row justify="space-between" style={{ marginBottom: 8 }}>
                <Text>Status:</Text>
                {getStatusTag(balanceResult.status)}
              </Row>
              <Row justify="space-between" style={{ marginBottom: 8 }}>
                <Text>Expires:</Text>
                <Text type={balanceResult.isExpired ? 'danger' : 'secondary'}>
                  {new Date(balanceResult.expiryDate).toLocaleDateString()}
                </Text>
              </Row>
              {balanceResult.lastUsed && (
                <Row justify="space-between">
                  <Text>Last Used:</Text>
                  <Text type="secondary">
                    {new Date(balanceResult.lastUsed).toLocaleDateString()}
                  </Text>
                </Row>
              )}

              {!balanceResult.canRedeem && (
                <Alert
                  message="Cannot be redeemed"
                  description={balanceResult.isExpired ? "This gift card has expired" : "This gift card is not redeemable"}
                  type="warning"
                  showIcon
                  style={{ marginTop: 16 }}
                />
              )}
            </div>
          )}

          <Form.Item style={{ marginBottom: 0 }}>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button
                onClick={() => {
                  setIsBalanceModalVisible(false);
                  balanceForm.resetFields();
                  setBalanceResult(null);
                }}
              >
                Close
              </Button>
              <Button 
                type="primary" 
                htmlType="submit" 
                size="large"
                loading={balanceChecking}
              >
                Check Balance
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default GiftCardsPage;

customer/pre-orders.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Space,
  Button,
  Tag,
  Modal,
  Form,
  InputNumber,
  message,
  Progress,
  Badge,
  Image,
  Tooltip,
  Alert,
  Divider,
  Steps,
} from 'antd';
import {
  ShoppingCartOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  InfoCircleOutlined,
  CalendarOutlined,
  TruckOutlined,
  DollarOutlined,
  HeartOutlined,
  HeartFilled,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Step } = Steps;

interface PreOrderProduct {
  id: number;
  name: string;
  description: string;
  image: string;
  price: number;
  estimatedShipDate: string;
  releaseDate: string;
  preOrderEndDate: string;
  totalStock: number;
  preOrderedStock: number;
  depositAmount: number;
  depositPercentage: number;
  status: 'available' | 'soldout' | 'coming-soon';
  category: string;
  features: string[];
  isFavorite: boolean;
}

interface MyPreOrder {
  id: number;
  productId: number;
  productName: string;
  productImage: string;
  quantity: number;
  totalPrice: number;
  depositPaid: number;
  remainingAmount: number;
  orderDate: string;
  estimatedShipDate: string;
  status: 'pending' | 'confirmed' | 'processing' | 'shipped' | 'delivered';
  paymentStatus: 'deposit-paid' | 'full-paid' | 'pending';
}

const mockPreOrderProducts: PreOrderProduct[] = [
  {
    id: 1,
    name: 'iPhone 16 Pro Max',
    description: 'The latest flagship smartphone with revolutionary features',
    image: 'https://via.placeholder.com/300x200?text=iPhone+16+Pro',
    price: 1299,
    estimatedShipDate: dayjs().add(45, 'days').format('YYYY-MM-DD'),
    releaseDate: dayjs().add(30, 'days').format('YYYY-MM-DD'),
    preOrderEndDate: dayjs().add(25, 'days').format('YYYY-MM-DD'),
    totalStock: 500,
    preOrderedStock: 342,
    depositAmount: 260,
    depositPercentage: 20,
    status: 'available',
    category: 'Smartphones',
    features: ['A18 Pro chip', '48MP camera', 'Titanium design', '6.7" display'],
    isFavorite: true,
  },
  {
    id: 2,
    name: 'PlayStation 6',
    description: 'Next-gen gaming console with 8K support',
    image: 'https://via.placeholder.com/300x200?text=PlayStation+6',
    price: 599,
    estimatedShipDate: dayjs().add(60, 'days').format('YYYY-MM-DD'),
    releaseDate: dayjs().add(50, 'days').format('YYYY-MM-DD'),
    preOrderEndDate: dayjs().add(40, 'days').format('YYYY-MM-DD'),
    totalStock: 300,
    preOrderedStock: 280,
    depositAmount: 150,
    depositPercentage: 25,
    status: 'available',
    category: 'Gaming',
    features: ['8K gaming', 'Ray tracing', '2TB SSD', 'VR compatible'],
    isFavorite: false,
  },
  {
    id: 3,
    name: 'Samsung Galaxy S25 Ultra',
    description: 'Premium Android flagship with S Pen',
    image: 'https://via.placeholder.com/300x200?text=Galaxy+S25',
    price: 1199,
    estimatedShipDate: dayjs().add(35, 'days').format('YYYY-MM-DD'),
    releaseDate: dayjs().add(20, 'days').format('YYYY-MM-DD'),
    preOrderEndDate: dayjs().add(15, 'days').format('YYYY-MM-DD'),
    totalStock: 400,
    preOrderedStock: 400,
    depositAmount: 240,
    depositPercentage: 20,
    status: 'soldout',
    category: 'Smartphones',
    features: ['200MP camera', 'S Pen included', '5000mAh battery', 'Snapdragon 8 Gen 4'],
    isFavorite: false,
  },
  {
    id: 4,
    name: 'MacBook Pro M4',
    description: 'Professional laptop with M4 chip',
    image: 'https://via.placeholder.com/300x200?text=MacBook+Pro+M4',
    price: 2499,
    estimatedShipDate: dayjs().add(90, 'days').format('YYYY-MM-DD'),
    releaseDate: dayjs().add(75, 'days').format('YYYY-MM-DD'),
    preOrderEndDate: dayjs().add(70, 'days').format('YYYY-MM-DD'),
    totalStock: 200,
    preOrderedStock: 45,
    depositAmount: 500,
    depositPercentage: 20,
    status: 'coming-soon',
    category: 'Laptops',
    features: ['M4 Pro chip', '16" Liquid Retina XDR', '32GB RAM', '1TB SSD'],
    isFavorite: true,
  },
];

const mockMyPreOrders: MyPreOrder[] = [
  {
    id: 1,
    productId: 1,
    productName: 'iPhone 16 Pro Max',
    productImage: 'https://via.placeholder.com/60?text=iPhone',
    quantity: 1,
    totalPrice: 1299,
    depositPaid: 260,
    remainingAmount: 1039,
    orderDate: dayjs().subtract(5, 'days').format('YYYY-MM-DD'),
    estimatedShipDate: dayjs().add(40, 'days').format('YYYY-MM-DD'),
    status: 'confirmed',
    paymentStatus: 'deposit-paid',
  },
  {
    id: 2,
    productId: 2,
    productName: 'PlayStation 6',
    productImage: 'https://via.placeholder.com/60?text=PS6',
    quantity: 1,
    totalPrice: 599,
    depositPaid: 150,
    remainingAmount: 449,
    orderDate: dayjs().subtract(10, 'days').format('YYYY-MM-DD'),
    estimatedShipDate: dayjs().add(50, 'days').format('YYYY-MM-DD'),
    status: 'confirmed',
    paymentStatus: 'deposit-paid',
  },
];

const PreOrdersPage: React.FC = () => {
  const [products, setProducts] = useState<PreOrderProduct[]>(mockPreOrderProducts);
  const [myOrders] = useState<MyPreOrder[]>(mockMyPreOrders);
  const [isPreOrderModalVisible, setIsPreOrderModalVisible] = useState<boolean>(false);
  const [selectedProduct, setSelectedProduct] = useState<PreOrderProduct | null>(null);
  const [form] = Form.useForm();

  const handlePreOrder = (product: PreOrderProduct) => {
    if (product.status === 'soldout') {
      message.warning('This product is sold out for pre-order');
      return;
    }
    if (product.status === 'coming-soon') {
      message.info('Pre-orders for this product will open soon');
      return;
    }
    setSelectedProduct(product);
    form.setFieldsValue({ quantity: 1 });
    setIsPreOrderModalVisible(true);
  };

  const handleSubmitPreOrder = (values: any) => {
    if (!selectedProduct) return;

    const totalPrice = selectedProduct.price * values.quantity;
    const depositAmount = selectedProduct.depositAmount * values.quantity;

    message.success(
      `Pre-order placed! Deposit of $${depositAmount} will be charged. Remaining $${totalPrice - depositAmount} due before shipping.`
    );
    setIsPreOrderModalVisible(false);
    form.resetFields();
  };

  const handleToggleFavorite = (productId: number) => {
    setProducts(
      products.map((p) =>
        p.id === productId ? { ...p, isFavorite: !p.isFavorite } : p
      )
    );
    const product = products.find((p) => p.id === productId);
    if (product) {
      message.success(product.isFavorite ? 'Removed from favorites' : 'Added to favorites');
    }
  };

  const myOrderColumns: ColumnsType<MyPreOrder> = [
    {
      title: 'Product',
      key: 'product',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <Image src={record.productImage} width={60} height={60} preview={false} />
          <div>
            <Text strong>{record.productName}</Text>
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                Qty: {record.quantity}
              </Text>
            </div>
          </div>
        </div>
      ),
    },
    {
      title: 'Order Date',
      dataIndex: 'orderDate',
      key: 'orderDate',
      render: (date) => <Text>{dayjs(date).format('MMM DD, YYYY')}</Text>,
    },
    {
      title: 'Est. Ship Date',
      dataIndex: 'estimatedShipDate',
      key: 'estimatedShipDate',
      render: (date) => (
        <div>
          <CalendarOutlined style={{ color: '#1890ff', marginRight: 4 }} />
          <Text>{dayjs(date).format('MMM DD, YYYY')}</Text>
        </div>
      ),
    },
    {
      title: 'Payment',
      key: 'payment',
      render: (_, record) => (
        <div>
          <div>
            <Text strong>${record.totalPrice}</Text>
          </div>
          <div>
            <Text type="secondary" style={{ fontSize: 12 }}>
              Deposit: ${record.depositPaid}
            </Text>
          </div>
          <div>
            <Text type="warning" style={{ fontSize: 12 }}>
              Remaining: ${record.remainingAmount}
            </Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Payment Status',
      dataIndex: 'paymentStatus',
      key: 'paymentStatus',
      render: (status: MyPreOrder['paymentStatus']) => {
        const config: Record<MyPreOrder['paymentStatus'], { color: string; text: string }> = {
          'deposit-paid': { color: 'orange', text: 'Deposit Paid' },
          'full-paid': { color: 'green', text: 'Fully Paid' },
          pending: { color: 'red', text: 'Payment Pending' },
        };
        return <Tag color={config[status].color}>{config[status].text}</Tag>;
      },
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: MyPreOrder['status']) => {
        const config: Record<MyPreOrder['status'], { color: string; text: string }> = {
          pending: { color: 'orange', text: 'Pending' },
          confirmed: { color: 'blue', text: 'Confirmed' },
          processing: { color: 'cyan', text: 'Processing' },
          shipped: { color: 'purple', text: 'Shipped' },
          delivered: { color: 'green', text: 'Delivered' },
        };
        return <Tag color={config[status].color}>{config[status].text}</Tag>;
      },
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          {record.paymentStatus === 'deposit-paid' && (
            <Button type="primary" size="small">
              Pay Remaining
            </Button>
          )}
          <Button size="small">Track Order</Button>
        </Space>
      ),
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <ClockCircleOutlined style={{ color: '#fa8c16' }} /> Pre-Orders
        </Title>
        <Paragraph type="secondary">
          Reserve upcoming products and be among the first to receive them
        </Paragraph>
      </div>

      <Alert
        message="Pre-Order Benefits"
        description={
          <ul style={{ marginBottom: 0, paddingLeft: 20 }}>
            <li>Guaranteed availability - Reserve before stock runs out</li>
            <li>Low deposit required - Pay only 20-25% upfront</li>
            <li>Priority shipping - Be first in line when products launch</li>
            <li>Easy cancellation - Full refund up to 7 days before release</li>
          </ul>
        }
        type="info"
        icon={<InfoCircleOutlined />}
        style={{ marginBottom: 24 }}
      />

      <Title level={4} style={{ marginBottom: 16 }}>
        Available for Pre-Order
      </Title>

      <Row gutter={[16, 16]} style={{ marginBottom: 32 }}>
        {products.map((product) => {
          const stockPercentage = (product.preOrderedStock / product.totalStock) * 100;
          const daysUntilRelease = dayjs(product.releaseDate).diff(dayjs(), 'days');
          const daysUntilPreOrderEnd = dayjs(product.preOrderEndDate).diff(dayjs(), 'days');

          return (
            <Col xs={24} sm={12} lg={8} key={product.id}>
              <Badge.Ribbon
                text={
                  product.status === 'soldout'
                    ? 'Sold Out'
                    : product.status === 'coming-soon'
                    ? 'Coming Soon'
                    : `${daysUntilPreOrderEnd} days left`
                }
                color={
                  product.status === 'soldout'
                    ? 'red'
                    : product.status === 'coming-soon'
                    ? 'blue'
                    : 'orange'
                }
              >
                <Card
                  hoverable
                  cover={
                    <div style={{ position: 'relative' }}>
                      <Image src={product.image} preview={false} height={200} width="100%" style={{ objectFit: 'cover' }} />
                      <Button
                        type="text"
                        icon={product.isFavorite ? <HeartFilled style={{ color: '#ff4d4f' }} /> : <HeartOutlined />}
                        onClick={() => handleToggleFavorite(product.id)}
                        style={{
                          position: 'absolute',
                          top: 8,
                          right: 8,
                          background: 'rgba(255, 255, 255, 0.9)',
                        }}
                      />
                    </div>
                  }
                >
                  <div style={{ marginBottom: 8 }}>
                    <Tag color="blue">{product.category}</Tag>
                  </div>

                  <Title level={5} style={{ marginBottom: 8 }}>
                    {product.name}
                  </Title>

                  <Paragraph type="secondary" ellipsis={{ rows: 2 }} style={{ marginBottom: 12 }}>
                    {product.description}
                  </Paragraph>

                  <div style={{ marginBottom: 12 }}>
                    {product.features.slice(0, 2).map((feature, index) => (
                      <Tag key={index} style={{ marginBottom: 4 }}>
                        {feature}
                      </Tag>
                    ))}
                  </div>

                  <Divider style={{ margin: '12px 0' }} />

                  <Row justify="space-between" align="middle" style={{ marginBottom: 8 }}>
                    <Col>
                      <Text type="secondary" style={{ fontSize: 12 }}>
                        Price
                      </Text>
                      <div>
                        <Text strong style={{ fontSize: 20, color: '#1890ff' }}>
                          ${product.price}
                        </Text>
                      </div>
                    </Col>
                    <Col>
                      <Text type="secondary" style={{ fontSize: 12 }}>
                        Deposit ({product.depositPercentage}%)
                      </Text>
                      <div>
                        <Text strong style={{ fontSize: 16 }}>
                          ${product.depositAmount}
                        </Text>
                      </div>
                    </Col>
                  </Row>

                  <div style={{ marginBottom: 12 }}>
                    <Row justify="space-between" align="middle" style={{ marginBottom: 4 }}>
                      <Col>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          Pre-orders: {product.preOrderedStock}/{product.totalStock}
                        </Text>
                      </Col>
                      <Col>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          {stockPercentage.toFixed(0)}%
                        </Text>
                      </Col>
                    </Row>
                    <Progress
                      percent={stockPercentage}
                      showInfo={false}
                      strokeColor={stockPercentage >= 90 ? '#ff4d4f' : '#52c41a'}
                    />
                  </div>

                  <Space direction="vertical" size="small" style={{ width: '100%', marginBottom: 12 }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
                      <CalendarOutlined style={{ color: '#1890ff' }} />
                      <Text type="secondary" style={{ fontSize: 12 }}>
                        Release: {dayjs(product.releaseDate).format('MMM DD, YYYY')} ({daysUntilRelease} days)
                      </Text>
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', gap: 4 }}>
                      <TruckOutlined style={{ color: '#52c41a' }} />
                      <Text type="secondary" style={{ fontSize: 12 }}>
                        Ships: {dayjs(product.estimatedShipDate).format('MMM DD, YYYY')}
                      </Text>
                    </div>
                  </Space>

                  <Button
                    type="primary"
                    block
                    icon={<ShoppingCartOutlined />}
                    onClick={() => handlePreOrder(product)}
                    disabled={product.status === 'soldout'}
                  >
                    {product.status === 'soldout'
                      ? 'Sold Out'
                      : product.status === 'coming-soon'
                      ? 'Coming Soon'
                      : 'Pre-Order Now'}
                  </Button>
                </Card>
              </Badge.Ribbon>
            </Col>
          );
        })}
      </Row>

      <Title level={4} style={{ marginBottom: 16 }}>
        My Pre-Orders
      </Title>

      <Card>
        <Table columns={myOrderColumns} dataSource={myOrders} rowKey="id" scroll={{ x: 1000 }} />
      </Card>

      <Modal
        title="Place Pre-Order"
        open={isPreOrderModalVisible}
        onCancel={() => setIsPreOrderModalVisible(false)}
        footer={null}
        width={600}
      >
        {selectedProduct && (
          <div>
            <div style={{ display: 'flex', gap: 16, marginBottom: 24 }}>
              <Image src={selectedProduct.image} width={120} height={120} preview={false} />
              <div style={{ flex: 1 }}>
                <Title level={5} style={{ marginBottom: 4 }}>
                  {selectedProduct.name}
                </Title>
                <Paragraph type="secondary" ellipsis={{ rows: 2 }}>
                  {selectedProduct.description}
                </Paragraph>
                <div>
                  <Text strong style={{ fontSize: 18, color: '#1890ff' }}>
                    ${selectedProduct.price}
                  </Text>
                </div>
              </div>
            </div>

            <Steps current={0} size="small" style={{ marginBottom: 24 }}>
              <Step title="Place Order" icon={<ShoppingCartOutlined />} />
              <Step title="Pay Deposit" icon={<DollarOutlined />} />
              <Step title="Await Release" icon={<ClockCircleOutlined />} />
              <Step title="Pay Balance" icon={<DollarOutlined />} />
              <Step title="Receive Product" icon={<CheckCircleOutlined />} />
            </Steps>

            <Alert
              message="Payment Schedule"
              description={
                <div>
                  <div style={{ marginBottom: 8 }}>
                    <Text strong>Today:</Text> Pay ${selectedProduct.depositAmount} deposit ({selectedProduct.depositPercentage}%)
                  </div>
                  <div>
                    <Text strong>Before Shipping:</Text> Pay remaining $
                    {selectedProduct.price - selectedProduct.depositAmount} balance
                  </div>
                </div>
              }
              type="info"
              style={{ marginBottom: 24 }}
            />

            <Form form={form} layout="vertical" onFinish={handleSubmitPreOrder}>
              <Form.Item
                label="Quantity"
                name="quantity"
                rules={[{ required: true, message: 'Please enter quantity' }]}
              >
                <InputNumber
                  min={1}
                  max={5}
                  style={{ width: '100%' }}
                  onChange={(value) => {
                    if (value) {
                      const total = selectedProduct.price * value;
                      const deposit = selectedProduct.depositAmount * value;
                      form.setFieldsValue({
                        totalPrice: total,
                        depositAmount: deposit,
                        remainingAmount: total - deposit,
                      });
                    }
                  }}
                />
              </Form.Item>

              <Row gutter={16}>
                <Col span={8}>
                  <Form.Item label="Total Price" name="totalPrice">
                    <InputNumber
                      value={selectedProduct.price}
                      disabled
                      style={{ width: '100%' }}
                      prefix="$"
                    />
                  </Form.Item>
                </Col>
                <Col span={8}>
                  <Form.Item label="Deposit (Due Now)" name="depositAmount">
                    <InputNumber
                      value={selectedProduct.depositAmount}
                      disabled
                      style={{ width: '100%' }}
                      prefix="$"
                    />
                  </Form.Item>
                </Col>
                <Col span={8}>
                  <Form.Item label="Remaining Amount" name="remainingAmount">
                    <InputNumber
                      value={selectedProduct.price - selectedProduct.depositAmount}
                      disabled
                      style={{ width: '100%' }}
                      prefix="$"
                    />
                  </Form.Item>
                </Col>
              </Row>

              <div style={{ background: '#f0f2f5', padding: 16, borderRadius: 4, marginBottom: 16 }}>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Important Dates:
                </Text>
                <div style={{ marginBottom: 4 }}>
                  <CalendarOutlined style={{ marginRight: 8 }} />
                  <Text type="secondary">
                    Release Date: {dayjs(selectedProduct.releaseDate).format('MMMM DD, YYYY')}
                  </Text>
                </div>
                <div style={{ marginBottom: 4 }}>
                  <TruckOutlined style={{ marginRight: 8 }} />
                  <Text type="secondary">
                    Estimated Ship: {dayjs(selectedProduct.estimatedShipDate).format('MMMM DD, YYYY')}
                  </Text>
                </div>
                <div>
                  <ClockCircleOutlined style={{ marginRight: 8 }} />
                  <Text type="secondary">
                    Pre-order Ends: {dayjs(selectedProduct.preOrderEndDate).format('MMMM DD, YYYY')}
                  </Text>
                </div>
              </div>

              <Alert
                message="Cancellation Policy"
                description="Full refund available up to 7 days before the release date. After that, a 10% cancellation fee applies."
                type="warning"
                showIcon
                style={{ marginBottom: 16 }}
              />

              <Form.Item>
                <Space>
                  <Button type="primary" htmlType="submit" icon={<ShoppingCartOutlined />}>
                    Confirm Pre-Order
                  </Button>
                  <Button onClick={() => setIsPreOrderModalVisible(false)}>Cancel</Button>
                </Space>
              </Form.Item>
            </Form>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default PreOrdersPage;

customer/product-qa.__content:
import React, { useState, useEffect } from 'react';
import { Card, Button, List, Avatar, Space, Tag, Input, Modal, Form, Select, Upload, message, Row, Col, Tabs, Typography, Badge, Rate, Divider, Tooltip, Empty, Spin, Statistic, Progress, Timeline } from 'antd';
import { QuestionCircleOutlined, LikeOutlined, DislikeOutlined, MessageOutlined, CheckCircleOutlined, UserOutlined, PlusOutlined, SearchOutlined, FilterOutlined, StarFilled, TrophyOutlined, ClockCircleOutlined, PaperClipOutlined, SendOutlined, ThunderboltOutlined } from '@ant-design/icons';
import { productQAAPI, type ProductQuestion, type ProductAnswer, type CreateQuestionRequest, type QAStats } from '../../services/api/productQAAPI';
import './product-qa.less';

const { TextArea } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Title, Text, Paragraph } = Typography;
const { Search } = Input;

// Product Q&A System with Community Features
const ProductQASystem: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [questions, setQuestions] = useState<ProductQuestion[]>([]);
  const [myQuestions, setMyQuestions] = useState<ProductQuestion[]>([]);
  const [stats, setStats] = useState<QAStats | null>(null);
  const [selectedQuestion, setSelectedQuestion] = useState<ProductQuestion | null>(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showAnswerModal, setShowAnswerModal] = useState(false);
  const [showQuestionDetails, setShowQuestionDetails] = useState(false);
  const [activeTab, setActiveTab] = useState('browse');
  const [filters, setFilters] = useState({
    category: undefined,
    isAnswered: undefined,
    sortBy: 'newest' as const,
    search: ''
  });

  const [createForm] = Form.useForm();
  const [answerForm] = Form.useForm();

  // Sample product ID for demonstration
  const currentProductId = 'sample-product-1';

  // Load initial data
  useEffect(() => {
    loadQuestions();
    loadMyQuestions();
    loadStats();
  }, [filters]);

  const loadQuestions = async () => {
    setLoading(true);
    try {
      const response = await productQAAPI.getProductQuestions(currentProductId, filters);
      if (response.success) {
        setQuestions(response.data.questions);
      } else {
        message.error('Failed to load questions');
      }
    } catch (error) {
      console.error('Error loading questions:', error);
      message.error('Failed to load questions');
    } finally {
      setLoading(false);
    }
  };

  const loadMyQuestions = async () => {
    try {
      const response = await productQAAPI.getMyQuestions(filters);
      if (response.success) {
        setMyQuestions(response.data.questions);
      }
    } catch (error) {
      console.error('Error loading my questions:', error);
    }
  };

  const loadStats = async () => {
    try {
      const response = await productQAAPI.getQAStats(currentProductId);
      if (response.success) {
        setStats(response.data);
      }
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const handleCreateQuestion = async (values: any) => {
    try {
      const questionData: CreateQuestionRequest = {
        productId: currentProductId,
        question: values.question,
        isPublic: values.isPublic !== false,
        category: values.category,
        tags: values.tags?.split(',').map((tag: string) => tag.trim()) || []
      };

      const response = await productQAAPI.createQuestion(questionData);
      if (response.success) {
        message.success('Question posted successfully');
        setShowCreateModal(false);
        createForm.resetFields();
        loadQuestions();
        loadMyQuestions();
        loadStats();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating question:', error);
      message.error('Failed to post question');
    }
  };

  const handleCreateAnswer = async (values: any) => {
    if (!selectedQuestion) return;

    try {
      const answerData = {
        questionId: selectedQuestion.id,
        answer: values.answer,
        attachments: values.attachments?.fileList?.map((file: any) => file.originFileObj) || []
      };

      const response = await productQAAPI.createAnswer(answerData);
      if (response.success) {
        message.success('Answer posted successfully');
        setShowAnswerModal(false);
        answerForm.resetFields();

        // Update the question with new answer
        const updatedQuestion = {
          ...selectedQuestion,
          answers: [...selectedQuestion.answers, response.data],
          isAnswered: true
        };
        setSelectedQuestion(updatedQuestion);

        // Refresh questions list
        loadQuestions();
        loadMyQuestions();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating answer:', error);
      message.error('Failed to post answer');
    }
  };

  const handleVoteQuestion = async (questionId: string, voteType: 'up' | 'down') => {
    try {
      const response = await productQAAPI.voteQuestion(questionId, voteType);
      if (response.success) {
        // Update local state to reflect vote
        setQuestions(questions.map(q => 
          q.id === questionId 
            ? {
                ...q,
                upvotes: voteType === 'up' ? q.upvotes + 1 : q.upvotes,
                downvotes: voteType === 'down' ? q.downvotes + 1 : q.downvotes,
                hasUserVoted: true,
                userVoteType: voteType
              }
            : q
        ));
        message.success('Vote recorded');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error voting:', error);
      message.error('Failed to record vote');
    }
  };

  const handleVoteAnswer = async (answerId: string, voteType: 'helpful' | 'not_helpful') => {
    try {
      const response = await productQAAPI.voteAnswer(answerId, voteType);
      if (response.success) {
        // Update the selected question's answers
        if (selectedQuestion) {
          const updatedAnswers = selectedQuestion.answers.map(a => 
            a.id === answerId 
              ? {
                  ...a,
                  helpfulCount: voteType === 'helpful' ? a.helpfulCount + 1 : a.helpfulCount,
                  notHelpfulCount: voteType === 'not_helpful' ? a.notHelpfulCount + 1 : a.notHelpfulCount,
                  hasUserVoted: true,
                  userVoteType: voteType
                }
              : a
          );
          setSelectedQuestion({ ...selectedQuestion, answers: updatedAnswers });
        }
        message.success('Feedback recorded');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error voting on answer:', error);
      message.error('Failed to record feedback');
    }
  };

  const handleAcceptAnswer = async (answerId: string) => {
    try {
      const response = await productQAAPI.acceptAnswer(answerId);
      if (response.success) {
        // Update the selected question's answers
        if (selectedQuestion) {
          const updatedAnswers = selectedQuestion.answers.map(a => ({
            ...a,
            isAccepted: a.id === answerId
          }));
          setSelectedQuestion({ ...selectedQuestion, answers: updatedAnswers });
        }
        message.success('Answer marked as best answer');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error accepting answer:', error);
      message.error('Failed to mark as best answer');
    }
  };

  const renderQuestionCard = (question: ProductQuestion) => (
    <Card
      key={question.id}
      size="small"
      style={{ marginBottom: 16 }}
      actions={[
        <Space key="vote">
          <Button
            size="small"
            type={question.hasUserVoted && question.userVoteType === 'up' ? 'primary' : 'text'}
            icon={<LikeOutlined />}
            onClick={() => handleVoteQuestion(question.id, 'up')}
          >
            {question.upvotes}
          </Button>
          <Button
            size="small"
            type={question.hasUserVoted && question.userVoteType === 'down' ? 'primary' : 'text'}
            icon={<DislikeOutlined />}
            onClick={() => handleVoteQuestion(question.id, 'down')}
          >
            {question.downvotes}
          </Button>
        </Space>,
        <Button
          key="answer"
          size="small"
          icon={<MessageOutlined />}
          onClick={() => {
            setSelectedQuestion(question);
            setShowAnswerModal(true);
          }}
        >
          Answer
        </Button>,
        <Button
          key="view"
          size="small"
          type="link"
          onClick={() => {
            setSelectedQuestion(question);
            setShowQuestionDetails(true);
          }}
        >
          View Details
        </Button>
      ]}
    >
      <Row gutter={16}>
        <Col flex={1}>
          <div style={{ marginBottom: 8 }}>
            <Text strong style={{ fontSize: '16px' }}>
              {question.question}
            </Text>
          </div>

          <Space wrap style={{ marginBottom: 8 }}>
            {question.category && (
              <Tag color="blue">{question.category}</Tag>
            )}
            {question.tags.map((tag) => (
              <Tag key={tag}>{tag}</Tag>
            ))}
            {question.isAnswered && (
              <Tag color="green" icon={<CheckCircleOutlined />}>
                Answered ({question.answers.length})
              </Tag>
            )}
          </Space>

          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Space>
              <Avatar 
                size="small" 
                src={question.customerAvatar} 
                icon={<UserOutlined />} 
              />
              <Text type="secondary">{question.customerName}</Text>
              <Text type="secondary">
                {new Date(question.createdAt).toLocaleDateString()}
              </Text>
            </Space>

            {question.isAnswered && question.answers.some(a => a.isBrandResponse) && (
              <Tag color="gold" icon={<TrophyOutlined />}>
                Brand Answered
              </Tag>
            )}
          </div>
        </Col>

        {question.productImage && (
          <Col>
            <Avatar
              size={64}
              shape="square"
              src={question.productImage}
              style={{ border: '1px solid #d9d9d9' }}
            />
          </Col>
        )}
      </Row>
    </Card>
  );

  const renderAnswer = (answer: ProductAnswer, questionOwnerId?: string) => (
    <Card
      key={answer.id}
      size="small"
      style={{
        marginBottom: 12,
        backgroundColor: answer.isAccepted ? '#f6ffed' : undefined,
        border: answer.isAccepted ? '2px solid #52c41a' : undefined
      }}
    >
      <div style={{ marginBottom: 8 }}>
        <Space>
          <Avatar
            size="small"
            src={answer.answeredBy.avatar}
            icon={<UserOutlined />}
          />
          <Text strong>{answer.answeredBy.name}</Text>

          {answer.answeredBy.type === 'vendor' && (
            <Tag color="gold">
              {answer.answeredBy.badge || 'Vendor'}
            </Tag>
          )}

          {answer.answeredBy.type === 'expert' && (
            <Tag color="purple">
              {answer.answeredBy.badge || 'Expert'}
            </Tag>
          )}

          {answer.answeredBy.verificationLevel === 'verified_purchase' && (
            <Tag color="green">Verified Purchase</Tag>
          )}

          {answer.isAccepted && (
            <Tag color="success" icon={<CheckCircleOutlined />}>
              Best Answer
            </Tag>
          )}

          <Text type="secondary">
            {new Date(answer.createdAt).toLocaleDateString()}
          </Text>
        </Space>
      </div>

      <Paragraph style={{ marginBottom: 12 }}>
        {answer.answer}
      </Paragraph>

      {answer.attachments && answer.attachments.length > 0 && (
        <div style={{ marginBottom: 12 }}>
          <Text type="secondary">Attachments:</Text>
          <Row gutter={8} style={{ marginTop: 4 }}>
            {answer.attachments.map((attachment) => (
              <Col key={attachment.id}>
                {attachment.type === 'image' ? (
                  <img
                    src={attachment.thumbnail || attachment.url}
                    alt={attachment.filename}
                    style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 4 }}
                  />
                ) : (
                  <Button size="small" icon={<PaperClipOutlined />}>
                    {attachment.filename}
                  </Button>
                )}
              </Col>
            ))}
          </Row>
        </div>
      )}

      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Space>
          <Button
            size="small"
            type={answer.hasUserVoted && answer.userVoteType === 'helpful' ? 'primary' : 'text'}
            onClick={() => handleVoteAnswer(answer.id, 'helpful')}
          >
             Helpful ({answer.helpfulCount})
          </Button>
          <Button
            size="small"
            type={answer.hasUserVoted && answer.userVoteType === 'not_helpful' ? 'primary' : 'text'}
            onClick={() => handleVoteAnswer(answer.id, 'not_helpful')}
          >
             Not Helpful ({answer.notHelpfulCount})
          </Button>
        </Space>

        {/* Only question owner can accept answers */}
        {questionOwnerId === '1' && !answer.isAccepted && (
          <Button
            size="small"
            type="primary"
            ghost
            icon={<CheckCircleOutlined />}
            onClick={() => handleAcceptAnswer(answer.id)}
          >
            Mark as Best Answer
          </Button>
        )}
      </div>
    </Card>
  );

  const renderStats = () => (
    <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Total Questions"
            value={stats?.totalQuestions || 0}
            prefix={<QuestionCircleOutlined />}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Answered"
            value={stats?.answeredQuestions || 0}
            prefix={<CheckCircleOutlined />}
            valueStyle={{ color: '#52c41a' }}
          />
          {stats && (
            <Progress
              percent={Math.round((stats.answeredQuestions / stats.totalQuestions) * 100)}
              size="small"
              showInfo={false}
            />
          )}
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Avg Response Time"
            value={stats?.averageResponseTime || 0}
            suffix="hrs"
            prefix={<ClockCircleOutlined />}
            precision={1}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Unanswered"
            value={stats?.unansweredQuestions || 0}
            prefix={<QuestionCircleOutlined />}
            valueStyle={{ color: '#faad14' }}
          />
        </Card>
      </Col>
    </Row>
  );

  const renderCreateQuestionModal = () => (
    <Modal
      title="Ask a Question"
      open={showCreateModal}
      onCancel={() => {
        setShowCreateModal(false);
        createForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={createForm}
        layout="vertical"
        onFinish={handleCreateQuestion}
      >
        <Form.Item
          label="Your Question"
          name="question"
          rules={[
            { required: true, message: 'Please enter your question' },
            { min: 10, message: 'Question must be at least 10 characters' }
          ]}
        >
          <TextArea
            rows={4}
            placeholder="What would you like to know about this product?"
          />
        </Form.Item>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item label="Category" name="category">
              <Select placeholder="Select category (optional)">
                <Option value="general">General Question</Option>
                <Option value="specifications">Specifications</Option>
                <Option value="compatibility">Compatibility</Option>
                <Option value="shipping">Shipping</Option>
                <Option value="warranty">Warranty</Option>
                <Option value="usage">Usage Instructions</Option>
                <Option value="comparison">Product Comparison</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item label="Tags" name="tags">
              <Input placeholder="e.g., macbook, wireless, bluetooth" />
            </Form.Item>
          </Col>
        </Row>

        <Form.Item name="isPublic" valuePropName="checked" initialValue={true}>
          <Space>
            <input type="checkbox" defaultChecked />
            <Text>Make this question public (recommended for faster answers)</Text>
          </Space>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit" icon={<SendOutlined />}>
              Post Question
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderAnswerModal = () => (
    <Modal
      title={`Answer: ${selectedQuestion?.question}`}
      open={showAnswerModal}
      onCancel={() => {
        setShowAnswerModal(false);
        answerForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={answerForm}
        layout="vertical"
        onFinish={handleCreateAnswer}
      >
        <Form.Item
          label="Your Answer"
          name="answer"
          rules={[
            { required: true, message: 'Please provide an answer' },
            { min: 10, message: 'Answer must be at least 10 characters' }
          ]}
        >
          <TextArea
            rows={6}
            placeholder="Share your knowledge and help other customers..."
          />
        </Form.Item>

        <Form.Item label="Attachments" name="attachments">
          <Upload
            multiple
            beforeUpload={() => false}
            fileList={[]}
          >
            <Button icon={<PaperClipOutlined />}>
              Attach Images or Documents
            </Button>
          </Upload>
          <Text type="secondary" style={{ fontSize: '12px', display: 'block', marginTop: 4 }}>
            Add screenshots, manuals, or other helpful files
          </Text>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowAnswerModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit" icon={<SendOutlined />}>
              Post Answer
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderQuestionDetails = () => (
    <Modal
      title="Question Details"
      open={showQuestionDetails}
      onCancel={() => {
        setShowQuestionDetails(false);
        setSelectedQuestion(null);
      }}
      width={800}
      footer={null}
    >
      {selectedQuestion && (
        <div>
          <Card style={{ marginBottom: 16 }}>
            <div style={{ marginBottom: 16 }}>
              <Title level={4}>{selectedQuestion.question}</Title>
              <Space wrap>
                {selectedQuestion.category && (
                  <Tag color="blue">{selectedQuestion.category}</Tag>
                )}
                {selectedQuestion.tags.map((tag) => (
                  <Tag key={tag}>{tag}</Tag>
                ))}
              </Space>
            </div>

            <Space style={{ marginBottom: 16 }}>
              <Avatar 
                src={selectedQuestion.customerAvatar} 
                icon={<UserOutlined />} 
              />
              <div>
                <Text strong>{selectedQuestion.customerName}</Text>
                <br />
                <Text type="secondary">
                  Asked {new Date(selectedQuestion.createdAt).toLocaleDateString()}
                </Text>
              </div>
            </Space>

            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Space>
                <Button
                  type={selectedQuestion.hasUserVoted && selectedQuestion.userVoteType === 'up' ? 'primary' : 'default'}
                  icon={<LikeOutlined />}
                  onClick={() => handleVoteQuestion(selectedQuestion.id, 'up')}
                >
                  {selectedQuestion.upvotes}
                </Button>
                <Button
                  type={selectedQuestion.hasUserVoted && selectedQuestion.userVoteType === 'down' ? 'primary' : 'default'}
                  icon={<DislikeOutlined />}
                  onClick={() => handleVoteQuestion(selectedQuestion.id, 'down')}
                >
                  {selectedQuestion.downvotes}
                </Button>
              </Space>

              <Button
                type="primary"
                icon={<MessageOutlined />}
                onClick={() => setShowAnswerModal(true)}
              >
                Add Answer
              </Button>
            </div>
          </Card>

          <Card title={`${selectedQuestion.answers.length} Answer${selectedQuestion.answers.length !== 1 ? 's' : ''}`}>
            {selectedQuestion.answers.length > 0 ? (
              selectedQuestion.answers
                .sort((a, b) => {
                  // Sort by: accepted first, then by helpful votes
                  if (a.isAccepted && !b.isAccepted) return -1;
                  if (!a.isAccepted && b.isAccepted) return 1;
                  return b.helpfulCount - a.helpfulCount;
                })
                .map((answer) => renderAnswer(answer, selectedQuestion.customerId))
            ) : (
              <Empty description="No answers yet" style={{ padding: '40px 0' }}>
                <Button type="primary" onClick={() => setShowAnswerModal(true)}>
                  Be the first to answer
                </Button>
              </Empty>
            )}
          </Card>
        </div>
      )}
    </Modal>
  );

  return (
    <div className="product-qa" style={{ padding: '24px', maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <QuestionCircleOutlined /> Product Q&A
        </Title>
        <Text type="secondary">
          Ask questions and get answers from our community of customers and experts
        </Text>
      </div>

      {/* Stats */}
      {renderStats()}

      {/* Tabs */}
      <Tabs activeKey={activeTab} onChange={setActiveTab} size="large">
        <TabPane
          tab={
            <Space>
              <QuestionCircleOutlined />
              Browse Questions
              {questions.length > 0 && <Badge count={questions.length} />}
            </Space>
          }
          key="browse"
        >
          {/* Filters and Actions */}
          <Card size="small" style={{ marginBottom: 16 }}>
            <Row gutter={16} align="middle">
              <Col flex={1}>
                <Search
                  placeholder="Search questions..."
                  value={filters.search}
                  onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                  onSearch={loadQuestions}
                  allowClear
                />
              </Col>
              <Col>
                <Space>
                  <Select
                    placeholder="Category"
                    allowClear
                    style={{ width: 130 }}
                    value={filters.category}
                    onChange={(value) => setFilters({ ...filters, category: value })}
                  >
                    <Option value="general">General</Option>
                    <Option value="specifications">Specs</Option>
                    <Option value="compatibility">Compatibility</Option>
                    <Option value="warranty">Warranty</Option>
                    <Option value="shipping">Shipping</Option>
                  </Select>

                  <Select
                    placeholder="Status"
                    allowClear
                    style={{ width: 120 }}
                    value={filters.isAnswered}
                    onChange={(value) => setFilters({ ...filters, isAnswered: value })}
                  >
                    <Option value={true}>Answered</Option>
                    <Option value={false}>Unanswered</Option>
                  </Select>

                  <Select
                    placeholder="Sort by"
                    style={{ width: 120 }}
                    value={filters.sortBy}
                    onChange={(value) => setFilters({ ...filters, sortBy: value })}
                  >
                    <Option value="newest">Newest</Option>
                    <Option value="oldest">Oldest</Option>
                    <Option value="most_voted">Most Voted</Option>
                    <Option value="most_answered">Most Answered</Option>
                  </Select>

                  <Button
                    type="primary"
                    icon={<PlusOutlined />}
                    onClick={() => setShowCreateModal(true)}
                  >
                    Ask Question
                  </Button>
                </Space>
              </Col>
            </Row>
          </Card>

          {/* Questions List */}
          {loading ? (
            <div style={{ textAlign: 'center', padding: 40 }}>
              <Spin size="large" />
            </div>
          ) : questions.length > 0 ? (
            questions.map(renderQuestionCard)
          ) : (
            <Card style={{ textAlign: 'center', padding: '60px 20px' }}>
              <QuestionCircleOutlined style={{ fontSize: 48, color: '#d9d9d9', marginBottom: 16 }} />
              <Title level={3}>No questions yet</Title>
              <Paragraph style={{ color: '#666', marginBottom: 24 }}>
                Be the first to ask a question about this product!
              </Paragraph>
              <Button type="primary" size="large" onClick={() => setShowCreateModal(true)}>
                Ask the First Question
              </Button>
            </Card>
          )}
        </TabPane>

        <TabPane
          tab={
            <Space>
              <UserOutlined />
              My Questions
              {myQuestions.length > 0 && <Badge count={myQuestions.length} />}
            </Space>
          }
          key="my-questions"
        >
          <div style={{ marginBottom: 16, textAlign: 'right' }}>
            <Button type="primary" icon={<PlusOutlined />} onClick={() => setShowCreateModal(true)}>
              Ask New Question
            </Button>
          </div>

          {myQuestions.length > 0 ? (
            myQuestions.map(renderQuestionCard)
          ) : (
            <Empty
              description="You haven't asked any questions yet"
              style={{ padding: '60px 20px' }}
            >
              <Button type="primary" onClick={() => setShowCreateModal(true)}>
                Ask Your First Question
              </Button>
            </Empty>
          )}
        </TabPane>
      </Tabs>

      {/* Modals */}
      {renderCreateQuestionModal()}
      {renderAnswerModal()}
      {renderQuestionDetails()}
    </div>
  );
};

export default ProductQASystem;

customer/size-guide.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tabs,
  Space,
  Button,
  Form,
  Select,
  InputNumber,
  Radio,
  message,
  Image,
  Alert,
  Collapse,
  Divider,
} from 'antd';
import {
  ColumnHeightOutlined,
  UserOutlined,
  InfoCircleOutlined,
  CheckCircleOutlined,
  LineOutlined,
  ShoppingOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;
const { Panel } = Collapse;
const { Option } = Select;

interface SizeChart {
  size: string;
  chest: string;
  waist: string;
  hips: string;
  inseam?: string;
  shoulders?: string;
