const womensSizeChart: SizeChart[] = [
  { size: 'XS', chest: '31-32"', waist: '24-25"', hips: '33-34"', shoulders: '14"', sleeve: '30"' },
  { size: 'S', chest: '33-34"', waist: '26-27"', hips: '35-36"', shoulders: '15"', sleeve: '31"' },
