import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Select,
  Space,
  Button,
  Table,
  Tag,
  Statistic,
  Alert,
  Divider,
  Input,
  Form,
  message,
  Modal,
  Spin,
} from 'antd';
import {
  RocketOutlined,
  CarOutlined,
  EnvironmentOutlined,
  DollarOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  TruckOutlined,
  BoxPlotOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { Option } = Select;

interface ShippingRate {
  id: string;
  name: string;
  carrier: string;
  cost: number;
  deliveryTime: string;
  enabled: boolean;
}

interface ShippingCarrier {
  id: string;
  name: string;
  logo: string;
  trackingEnabled: boolean;
  pickupAvailable: boolean;
  services: ShippingService[];
}

interface ShippingService {
  id: string;
  carrierName: string;
  name: string;
  description: string;
  estimatedDays: string;
  baseRate: number;
  freeThreshold?: number;
  icon: React.ReactNode;
}

const ShippingAPI: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [selectedCarrier, setSelectedCarrier] = useState<string>('fedex');
  const [rates, setRates] = useState<ShippingRate[]>([]);

  const carriers: ShippingCarrier[] = [
    {
      id: 'fedex',
      name: 'FedEx',
      logo: ' FedEx',
      trackingEnabled: true,
      pickupAvailable: true,
      services: [
        {
          id: 'fedex_overnight',
          carrierName: 'FedEx',
          name: 'FedEx Overnight',
          description: 'Next business day delivery',
          estimatedDays: '1 day',
          baseRate: 29.99,
          icon: <RocketOutlined />,
        },
        {
          id: 'fedex_2day',
          carrierName: 'FedEx',
          name: 'FedEx 2-Day',
          description: 'Delivery in 2 business days',
          estimatedDays: '2 days',
          baseRate: 19.99,
          icon: <TruckOutlined />,
        },
        {
          id: 'fedex_ground',
          carrierName: 'FedEx',
          name: 'FedEx Ground',
          description: 'Economical ground shipping',
          estimatedDays: '3-5 days',
          baseRate: 8.99,
          freeThreshold: 75,
          icon: <CarOutlined />,
        },
      ],
    },
    {
      id: 'ups',
      name: 'UPS',
      logo: ' UPS',
      trackingEnabled: true,
      pickupAvailable: true,
      services: [
        {
          id: 'ups_next_day',
          carrierName: 'UPS',
          name: 'UPS Next Day Air',
          description: 'Next business day by 10:30 AM',
          estimatedDays: '1 day',
          baseRate: 32.99,
          icon: <RocketOutlined />,
        },
        {
          id: 'ups_2day',
          carrierName: 'UPS',
          name: 'UPS 2-Day Air',
          description: 'Delivery in 2 business days',
          estimatedDays: '2 days',
          baseRate: 21.99,
          icon: <TruckOutlined />,
        },
        {
          id: 'ups_ground',
          carrierName: 'UPS',
          name: 'UPS Ground',
          description: 'Cost-effective ground shipping',
          estimatedDays: '3-5 days',
          baseRate: 9.99,
          freeThreshold: 50,
          icon: <CarOutlined />,
        },
      ],
    },
    {
      id: 'usps',
      name: 'USPS',
      logo: ' USPS',
      trackingEnabled: true,
      pickupAvailable: false,
      services: [
        {
          id: 'usps_priority_express',
          carrierName: 'USPS',
          name: 'Priority Express',
          description: 'Overnight to most locations',
          estimatedDays: '1-2 days',
          baseRate: 26.99,
          icon: <RocketOutlined />,
        },
        {
          id: 'usps_priority',
          carrierName: 'USPS',
          name: 'Priority Mail',
          description: 'Fast, reliable service',
          estimatedDays: '1-3 days',
          baseRate: 8.70,
          icon: <TruckOutlined />,
        },
        {
          id: 'usps_ground',
          carrierName: 'USPS',
          name: 'USPS Ground',
          description: 'Economical shipping',
          estimatedDays: '2-8 days',
          baseRate: 4.95,
          freeThreshold: 35,
          icon: <CarOutlined />,
        },
      ],
    },
  ];

  const ratesColumns: ColumnsType<ShippingRate> = [
    {
      title: 'Service Name',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: 'Carrier',
      dataIndex: 'carrier',
      key: 'carrier',
    },
    {
      title: 'Cost',
      dataIndex: 'cost',
      key: 'cost',
      render: (cost: number) => `$${cost.toFixed(2)}`,
    },
    {
      title: 'Delivery Time',
      dataIndex: 'deliveryTime',
      key: 'deliveryTime',
    },
    {
      title: 'Status',
      dataIndex: 'enabled',
      key: 'enabled',
      render: (enabled: boolean) => (
        <Tag color={enabled ? 'green' : 'red'}>
          {enabled ? 'Active' : 'Inactive'}
        </Tag>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (record: ShippingRate) => (
        <Space>
          <Button size="small">Edit</Button>
          <Button size="small" danger>Remove</Button>
        </Space>
      ),
    },
  ];

  useEffect(() => {
    // Convert carrier services to rate format
    const selectedCarrierData = carriers.find(c => c.id === selectedCarrier);
