import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Typography, 
  Row, 
  Col, 
  Input, 
  Button, 
  Space, 
  Collapse, 
  Tag, 
  Spin, 
  message,
  Empty,
  Skeleton,
  Badge,
  Rate,
  Tooltip,
  AutoComplete,
  List,
  Avatar,
  Divider,
  Statistic,
  Timeline
} from 'antd';
import {
  SearchOutlined,
  QuestionCircleOutlined,
  ShoppingOutlined,
  UserOutlined,
  SafetyOutlined,
  RocketOutlined,
  LikeOutlined,
  DislikeOutlined,
  EyeOutlined,
  ClockCircleOutlined,
  TagsOutlined,
  FileTextOutlined,
  CustomerServiceOutlined,
  PhoneOutlined,
  MailOutlined,
  BookOutlined,
  StarOutlined,
  CommentOutlined,
  ArrowRightOutlined,
  CheckCircleOutlined,
  ExclamationCircleOutlined,
  BulbOutlined,
  ThunderboltOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { helpAPI, faqAPI, helpCategoriesAPI, supportTicketsAPI, helpAnalyticsAPI } from '../services/api/help';
import type { HelpArticle, FAQ, HelpCategory, HelpAnalytics } from '../services/api/help';

const { Title, Text, Paragraph } = Typography;
const { Panel } = Collapse;
const { Option } = AutoComplete;

const HelpCenterPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchSuggestions, setSearchSuggestions] = useState<string[]>([]);

  // Data states
  const [categories, setCategories] = useState<HelpCategory[]>([]);
  const [popularArticles, setPopularArticles] = useState<HelpArticle[]>([]);
  const [faqs, setFaqs] = useState<FAQ[]>([]);
  const [analytics, setAnalytics] = useState<HelpAnalytics | null>(null);
  const [searchResults, setSearchResults] = useState<{
    articles: HelpArticle[];
    faqs: FAQ[];
    total: number;
  } | null>(null);

  // Load initial data
  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);

      const [
        categoriesRes,
        popularRes,
        faqsRes,
        analyticsRes
      ] = await Promise.all([
        helpCategoriesAPI.getCategories({ withStats: true, activeOnly: true }),
        helpAPI.getArticles({ limit: 6, sort: 'popular' }),
        faqAPI.getFAQs({ limit: 6, featured: true, sort: 'popular' }),
        helpAnalyticsAPI.getAnalytics()
      ]);

      setCategories(categoriesRes.data || []);
