import React, { useState } from 'react';
import {
  Card,
  Table,
  Tag,
  Button,
  Space,
  Input,
  Select,
  Rate,
  Modal,
  Typography,
  Avatar,
  Image,
  Switch,
  message,
} from 'antd';
import {
  SearchOutlined,
  EyeOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  StarOutlined,
  UserOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;

interface Review {
  id: number;
  productId: number;
  productName: string;
  productImage: string;
  customerName: string;
  customerId: number;
  rating: number;
  title: string;
  comment: string;
