    customerId: 101,
    rating: 5,
    title: 'Excellent quality!',
    comment: 'These headphones are amazing! Great sound quality and very comfortable.',
    images: ['https://via.placeholder.com/200'],
    status: 'approved',
    helpful: 24,
    verified: true,
    createdAt: '2024-10-25',
  },
  {
    id: 2,
    productId: 2,
    productName: 'Smart Watch Pro',
    productImage: 'https://via.placeholder.com/60',
    customerName: 'Sarah Smith',
    customerId: 102,
    rating: 4,
    title: 'Good product',
    comment: 'Nice watch but battery life could be better.',
    status: 'pending',
    helpful: 8,
    verified: true,
    createdAt: '2024-11-01',
  },
  {
    id: 3,
    productId: 1,
    productName: 'Premium Wireless Headphones',
    productImage: 'https://via.placeholder.com/60',
    customerName: 'Mike Johnson',
    customerId: 103,
    rating: 2,
    title: 'Disappointed',
    comment: 'Not worth the price. Sound quality is mediocre.',
    status: 'rejected',
    helpful: 3,
    verified: false,
    createdAt: '2024-10-30',
  },
];

const AdminReviewsPage: React.FC = () => {
  const [reviews, setReviews] = useState<Review[]>(mockReviews);
  const [selectedReview, setSelectedReview] = useState<Review | null>(null);
  const [drawerVisible, setDrawerVisible] = useState(false);

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      approved: 'success',
      pending: 'warning',
      rejected: 'error',
    };
    return colors[status] || 'default';
  };

  const handleViewReview = (review: Review) => {
    setSelectedReview(review);
    setDrawerVisible(true);
  };

  const handleApproveReview = (id: number) => {
    setReviews(reviews.map((r) => (r.id === id ? { ...r, status: 'approved' } : r)));
    message.success('Review approved');
  };

  const handleRejectReview = (id: number) => {
    Modal.confirm({
      title: 'Reject Review',
      content: 'Are you sure you want to reject this review?',
      okText: 'Reject',
      okType: 'danger',
      onOk: () => {
        setReviews(reviews.map((r) => (r.id === id ? { ...r, status: 'rejected' } : r)));
        message.success('Review rejected');
      },
    });
  };

  const handleToggleFeatured = (id: number) => {
    message.success('Review featured status toggled');
  };

  const columns: ColumnsType<Review> = [
    {
      title: 'Product',
      key: 'product',
      render: (_, record) => (
        <Space>
          <Image src={record.productImage} width={50} height={50} style={{ borderRadius: 4 }} />
          <Text>{record.productName}</Text>
        </Space>
      ),
    },
    {
      title: 'Customer',
      key: 'customer',
      render: (_, record) => (
        <Space>
          <Avatar icon={<UserOutlined />} />
          <div>
            <Text>{record.customerName}</Text>
            {record.verified && (
              <div>
                <Tag color="blue" style={{ fontSize: 10 }}>
                  VERIFIED PURCHASE
                </Tag>
              </div>
            )}
          </div>
        </Space>
      ),
    },
    {
      title: 'Rating',
      dataIndex: 'rating',
      key: 'rating',
      render: (rating: number) => (
        <Space>
          <Rate disabled value={rating} style={{ fontSize: 14 }} />
          <Text strong>{rating}.0</Text>
        </Space>
      ),
      sorter: (a, b) => a.rating - b.rating,
    },
    {
      title: 'Review',
      key: 'review',
      render: (_, record) => (
        <div style={{ maxWidth: 300 }}>
          <Text strong style={{ display: 'block', marginBottom: 4 }}>
            {record.title}
          </Text>
          <Paragraph
            ellipsis={{ rows: 2, expandable: false }}
            style={{ marginBottom: 0, color: '#666' }}
          >
            {record.comment}
          </Paragraph>
        </div>
      ),
    },
    {
      title: 'Helpful',
      dataIndex: 'helpful',
      key: 'helpful',
      render: (helpful: number) => <Text>{helpful} votes</Text>,
      sorter: (a, b) => a.helpful - b.helpful,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Approved', value: 'approved' },
        { text: 'Pending', value: 'pending' },
        { text: 'Rejected', value: 'rejected' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => new Date(date).toLocaleDateString(),
      sorter: (a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime(),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space direction="vertical" size="small">
          <Button
            type="link"
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handleViewReview(record)}
          >
            View
          </Button>
          {record.status === 'pending' && (
            <>
              <Button
                type="link"
                size="small"
                icon={<CheckCircleOutlined />}
                onClick={() => handleApproveReview(record.id)}
                style={{ color: '#52c41a' }}
              >
                Approve
              </Button>
              <Button
                type="link"
                size="small"
                danger
                icon={<CloseCircleOutlined />}
                onClick={() => handleRejectReview(record.id)}
              >
                Reject
              </Button>
            </>
          )}
        </Space>
      ),
    },
  ];

  const approvedCount = reviews.filter((r) => r.status === 'approved').length;
  const pendingCount = reviews.filter((r) => r.status === 'pending').length;
  const avgRating = reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length;

  return (
    <div style={{ padding: 24 }}>
      <Card title={<Title level={3}>Product Reviews Management</Title>}>
        {/* Statistics */}
        <div style={{ display: 'flex', gap: 16, marginBottom: 16 }}>
          <Card size="small" style={{ flex: 1 }}>
            <Text type="secondary">Total Reviews</Text>
            <Title level={3} style={{ margin: 0 }}>
              {reviews.length}
            </Title>
          </Card>
          <Card size="small" style={{ flex: 1 }}>
            <Text type="secondary">Pending Approval</Text>
            <Title level={3} style={{ margin: 0, color: '#faad14' }}>
              {pendingCount}
            </Title>
          </Card>
          <Card size="small" style={{ flex: 1 }}>
            <Text type="secondary">Approved</Text>
            <Title level={3} style={{ margin: 0, color: '#52c41a' }}>
              {approvedCount}
            </Title>
          </Card>
          <Card size="small" style={{ flex: 1 }}>
            <Text type="secondary">Average Rating</Text>
            <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
              <Title level={3} style={{ margin: 0 }}>
                {avgRating.toFixed(1)}
              </Title>
              <StarOutlined style={{ fontSize: 24, color: '#faad14' }} />
            </div>
          </Card>
        </div>

        {/* Filters */}
        <Space style={{ marginBottom: 16, width: '100%' }} wrap>
          <Input
            placeholder="Search reviews..."
            prefix={<SearchOutlined />}
            style={{ width: 300 }}
          />
          <Select placeholder="Status" style={{ width: 150 }}>
            <Option value="all">All Reviews</Option>
            <Option value="approved">Approved</Option>
            <Option value="pending">Pending</Option>
            <Option value="rejected">Rejected</Option>
          </Select>
          <Select placeholder="Rating" style={{ width: 150 }}>
            <Option value="all">All Ratings</Option>
            <Option value="5">5 Stars</Option>
            <Option value="4">4 Stars</Option>
            <Option value="3">3 Stars</Option>
            <Option value="2">2 Stars</Option>
            <Option value="1">1 Star</Option>
          </Select>
        </Space>

        {/* Reviews Table */}
        <Table
          columns={columns}
          dataSource={reviews}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} reviews`,
          }}
        />
      </Card>

      {/* Review Detail Modal */}
      <Modal
        title="Review Details"
        open={drawerVisible}
        onCancel={() => setDrawerVisible(false)}
        width={700}
        footer={
          selectedReview?.status === 'pending'
            ? [
                <Button
                  key="reject"
                  danger
                  icon={<CloseCircleOutlined />}
                  onClick={() => {
                    handleRejectReview(selectedReview.id);
                    setDrawerVisible(false);
                  }}
                >
                  Reject
                </Button>,
                <Button
                  key="approve"
                  type="primary"
                  icon={<CheckCircleOutlined />}
                  onClick={() => {
                    handleApproveReview(selectedReview.id);
                    setDrawerVisible(false);
                  }}
                >
                  Approve
                </Button>,
              ]
            : null
        }
      >
        {selectedReview && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Card size="small" title="Product Information">
              <Space>
                <Image src={selectedReview.productImage} width={80} height={80} />
                <div>
                  <Text strong style={{ display: 'block' }}>
                    {selectedReview.productName}
                  </Text>
                  <Text type="secondary">Product ID: {selectedReview.productId}</Text>
                </div>
              </Space>
            </Card>

            <Card size="small" title="Customer Information">
              <Space>
                <Avatar size={50} icon={<UserOutlined />} />
                <div>
                  <Text strong style={{ display: 'block' }}>
                    {selectedReview.customerName}
                  </Text>
                  <Text type="secondary">Customer ID: {selectedReview.customerId}</Text>
                  {selectedReview.verified && (
                    <div>
                      <Tag color="blue">VERIFIED PURCHASE</Tag>
                    </div>
                  )}
                </div>
              </Space>
            </Card>

            <Card size="small" title="Review Content">
              <Space direction="vertical" style={{ width: '100%' }}>
                <div>
                  <Text type="secondary">Rating:</Text>
                  <div>
                    <Rate disabled value={selectedReview.rating} />
                    <Text strong style={{ marginLeft: 8 }}>
                      {selectedReview.rating}.0 / 5.0
                    </Text>
                  </div>
                </div>
                <div>
                  <Text type="secondary">Title:</Text>
                  <Title level={5} style={{ marginTop: 4 }}>
                    {selectedReview.title}
                  </Title>
                </div>
                <div>
                  <Text type="secondary">Comment:</Text>
                  <Paragraph style={{ marginTop: 4 }}>{selectedReview.comment}</Paragraph>
                </div>
                {selectedReview.images && selectedReview.images.length > 0 && (
                  <div>
                    <Text type="secondary">Images:</Text>
                    <div style={{ marginTop: 8 }}>
                      <Image.PreviewGroup>
                        {selectedReview.images.map((img, idx) => (
                          <Image key={idx} src={img} width={100} height={100} style={{ marginRight: 8 }} />
                        ))}
                      </Image.PreviewGroup>
                    </div>
                  </div>
                )}
                <div>
                  <Text type="secondary">Helpful Votes: </Text>
                  <Text strong>{selectedReview.helpful}</Text>
                </div>
                <div>
                  <Text type="secondary">Status: </Text>
                  <Tag color={getStatusColor(selectedReview.status)}>
                    {selectedReview.status.toUpperCase()}
                  </Tag>
                </div>
                <div>
                  <Text type="secondary">Date: </Text>
                  <Text>{new Date(selectedReview.createdAt).toLocaleDateString()}</Text>
                </div>
              </Space>
            </Card>
          </Space>
        )}
      </Modal>
    </div>
  );
};

export default AdminReviewsPage;

admin/flash-sales.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Space,
  Button,
  Statistic,
  Progress,
  Select,
  DatePicker,
  Tabs,
  Alert,
  List,
  Avatar,
  message,
  Spin,
  Modal,
  Form,
  Input,
  InputNumber,
  Upload,
  Switch,
  Tooltip,
  Popconfirm,
  Badge,
  Divider,
  Timeline,
  Empty,
  Dropdown,
} from 'antd';
import {
  ShoppingOutlined,
  FireOutlined,
  ClockCircleOutlined,
  DollarOutlined,
  RiseOutlined,
  FallOutlined,
  ThunderboltOutlined,
  CalendarOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  StopOutlined,
  CopyOutlined,
  DownloadOutlined,
  BellOutlined,
  EyeOutlined,
  ShoppingCartOutlined,
  GiftOutlined,
  TrophyOutlined,
  TeamOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  MinusCircleOutlined,
  ReloadOutlined,
  FileExcelOutlined,
  FilePdfOutlined,
  FileTextOutlined,
  UploadOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie, Area } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';
import { flashSalesAPI, FlashSale, FlashSaleProduct, DailyDeal, FlashSaleStats } from '../../services/api/flashSales';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { TabPane } = Tabs;

interface CountdownState {
  [key: string]: {
    days: number;
    hours: number;
    minutes: number;
    seconds: number;
    isActive: boolean;
  };
}

const FlashSalesPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [flashSales, setFlashSales] = useState<FlashSale[]>([]);
  const [dailyDeals, setDailyDeals] = useState<DailyDeal[]>([]);
  const [stats, setStats] = useState<FlashSaleStats | null>(null);
  const [selectedStatus, setSelectedStatus] = useState<string>('all');
  const [dateRange, setDateRange] = useState<[Dayjs, Dayjs]>([
    dayjs().subtract(7, 'days'),
    dayjs().add(7, 'days'),
  ]);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [selectedSale, setSelectedSale] = useState<FlashSale | null>(null);
  const [countdowns, setCountdowns] = useState<CountdownState>({});
  const [activeTab, setActiveTab] = useState('flashSales');
  const [form] = Form.useForm();

  // Load data
  useEffect(() => {
    loadFlashSales();
    loadDailyDeals();
    loadStats();
  }, [selectedStatus, dateRange]);

  // Update countdowns every second
  useEffect(() => {
    const interval = setInterval(() => {
      updateCountdowns();
    }, 1000);
    return () => clearInterval(interval);
  }, [flashSales]);

  const loadFlashSales = async () => {
    setLoading(true);
    try {
      const filters = {
        status: selectedStatus !== 'all' ? selectedStatus as FlashSale['status'] : undefined,
        startDate: dateRange[0].format('YYYY-MM-DD'),
        endDate: dateRange[1].format('YYYY-MM-DD'),
        limit: 50,
      };
      const response = await flashSalesAPI.getAll(filters);
      setFlashSales(response.flashSales);
    } catch (error) {
      console.error('Error loading flash sales:', error);
      message.error('Failed to load flash sales');
    } finally {
      setLoading(false);
    }
  };

  const loadDailyDeals = async () => {
    try {
      const deals = await flashSalesAPI.getDailyDeals();
      setDailyDeals(deals);
    } catch (error) {
      console.error('Error loading daily deals:', error);
      message.error('Failed to load daily deals');
    }
  };

  const loadStats = async () => {
    try {
      const statsData = await flashSalesAPI.getStats({
        start: dateRange[0].format('YYYY-MM-DD'),
        end: dateRange[1].format('YYYY-MM-DD'),
      });
      setStats(statsData);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const updateCountdowns = () => {
    const newCountdowns: CountdownState = {};
    flashSales.forEach(sale => {
      if (sale.status === 'active' || sale.status === 'scheduled') {
        const now = dayjs();
        const endTime = dayjs(sale.endTime);
        const startTime = dayjs(sale.startTime);

        let targetTime: Dayjs;
        let isActive = true;

        if (sale.status === 'scheduled' && now.isBefore(startTime)) {
          targetTime = startTime;
          isActive = false;
        } else if (sale.status === 'active' && now.isBefore(endTime)) {
          targetTime = endTime;
          isActive = true;
        } else {
          return;
        }

        const duration = targetTime.diff(now);
        if (duration > 0) {
          const days = Math.floor(duration / (1000 * 60 * 60 * 24));
          const hours = Math.floor((duration % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
          const minutes = Math.floor((duration % (1000 * 60 * 60)) / (1000 * 60));
          const seconds = Math.floor((duration % (1000 * 60)) / 1000);

          newCountdowns[sale.id] = { days, hours, minutes, seconds, isActive };
        }
      }
    });
    setCountdowns(newCountdowns);
  };

  const handleCreateSale = async (values: any) => {
    try {
      setLoading(true);
      await flashSalesAPI.create({
        title: values.title,
        description: values.description,
        startTime: values.dateRange[0].toISOString(),
        endTime: values.dateRange[1].toISOString(),
        discountType: values.discountType,
        discountValue: values.discountValue,
        maxDiscount: values.maxDiscount,
        minOrderValue: values.minOrderValue,
        productIds: values.productIds || [],
        priority: values.priority || 1,
        sendNotifications: values.sendNotifications,
      });
      message.success('Flash sale created successfully');
      setCreateModalOpen(false);
      form.resetFields();
      loadFlashSales();
    } catch (error) {
      console.error('Error creating flash sale:', error);
      message.error('Failed to create flash sale');
    } finally {
      setLoading(false);
    }
  };

  const handleStartSale = async (saleId: string) => {
    try {
      await flashSalesAPI.start(saleId);
      message.success('Flash sale started successfully');
      loadFlashSales();
    } catch (error) {
      console.error('Error starting flash sale:', error);
      message.error('Failed to start flash sale');
    }
  };

  const handleEndSale = async (saleId: string) => {
    try {
      await flashSalesAPI.end(saleId);
      message.success('Flash sale ended successfully');
      loadFlashSales();
    } catch (error) {
      console.error('Error ending flash sale:', error);
      message.error('Failed to end flash sale');
    }
  };

  const handleDeleteSale = async (saleId: string) => {
    try {
      await flashSalesAPI.delete(saleId);
      message.success('Flash sale deleted successfully');
      loadFlashSales();
    } catch (error) {
      console.error('Error deleting flash sale:', error);
      message.error('Failed to delete flash sale');
    }
  };

  const handleDuplicateSale = async (saleId: string) => {
    try {
      await flashSalesAPI.duplicate(saleId, {
        title: `Copy of ${flashSales.find(s => s.id === saleId)?.title}`,
        startTime: dayjs().add(1, 'hour').toISOString(),
        endTime: dayjs().add(25, 'hours').toISOString(),
      });
      message.success('Flash sale duplicated successfully');
      loadFlashSales();
    } catch (error) {
      console.error('Error duplicating flash sale:', error);
      message.error('Failed to duplicate flash sale');
    }
  };

  const handleExport = async (format: 'csv' | 'xlsx' | 'json') => {
    try {
      setLoading(true);
      const blob = await flashSalesAPI.export(
        {
          status: selectedStatus !== 'all' ? selectedStatus as FlashSale['status'] : undefined,
          startDate: dateRange[0].format('YYYY-MM-DD'),
          endDate: dateRange[1].format('YYYY-MM-DD'),
        },
        format
      );

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `flash-sales-${dayjs().format('YYYY-MM-DD')}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      message.success(`Flash sales exported as ${format.toUpperCase()}`);
    } catch (error) {
      console.error('Export error:', error);
      message.error('Failed to export flash sales');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: FlashSale['status']) => {
    const colors = {
      scheduled: 'blue',
      active: 'green',
      ended: 'default',
      cancelled: 'red',
    };
    return colors[status];
  };

  const getStatusIcon = (status: FlashSale['status']) => {
    const icons = {
      scheduled: <ClockCircleOutlined />,
      active: <PlayCircleOutlined />,
      ended: <CheckCircleOutlined />,
      cancelled: <MinusCircleOutlined />,
    };
    return icons[status];
  };

  const renderCountdown = (saleId: string, status: FlashSale['status']) => {
    const countdown = countdowns[saleId];
    if (!countdown) return null;

    const { days, hours, minutes, seconds, isActive } = countdown;
    const color = isActive ? '#52c41a' : '#1890ff';
    const prefix = isActive ? 'Ends in:' : 'Starts in:';

    return (
      <div style={{ fontSize: '12px', color }}>
        <Text strong style={{ color }}>
          {prefix} {days}d {hours}h {minutes}m {seconds}s
        </Text>
      </div>
    );
  };

  const flashSalesColumns: ColumnsType<FlashSale> = [
    {
      title: 'Sale Info',
      key: 'info',
      render: (_, record) => (
        <div>
          <Text strong style={{ display: 'block' }}>{record.title}</Text>
          <Text type="secondary" style={{ fontSize: '12px' }}>
            {record.description}
          </Text>
          {(record.status === 'active' || record.status === 'scheduled') && (
            <div style={{ marginTop: 4 }}>
              {renderCountdown(record.id, record.status)}
            </div>
          )}
        </div>
      ),
      width: 250,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: FlashSale['status']) => (
        <Tag color={getStatusColor(status)} icon={getStatusIcon(status)}>
          {status.toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Scheduled', value: 'scheduled' },
        { text: 'Active', value: 'active' },
        { text: 'Ended', value: 'ended' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Schedule',
      key: 'schedule',
      render: (_, record) => (
        <div>
          <div style={{ fontSize: '12px' }}>
            <Text type="secondary">Start:</Text> {dayjs(record.startTime).format('MMM DD, HH:mm')}
          </div>
          <div style={{ fontSize: '12px' }}>
            <Text type="secondary">End:</Text> {dayjs(record.endTime).format('MMM DD, HH:mm')}
          </div>
        </div>
      ),
    },
    {
      title: 'Discount',
      key: 'discount',
      render: (_, record) => (
        <div>
          <Text strong>
            {record.discountType === 'percentage' ? `${record.discountValue}%` : `$${record.discountValue}`}
          </Text>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {record.discountType === 'percentage' ? 'Percentage' : 
             record.discountType === 'fixed' ? 'Fixed Amount' : 'BOGO'}
          </div>
        </div>
      ),
    },
    {
      title: 'Products',
      dataIndex: 'products',
      key: 'products',
      render: (products: FlashSaleProduct[]) => (
        <div>
          <Text strong>{products.length}</Text>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {products.reduce((sum, p) => sum + p.soldQuantity, 0)} sold
          </div>
        </div>
      ),
    },
    {
      title: 'Performance',
      key: 'performance',
      render: (_, record) => (
        <div>
          <div style={{ fontSize: '12px' }}>
            <Text strong style={{ color: '#52c41a' }}>
              ${record.analytics.totalRevenue.toLocaleString()}
            </Text>
          </div>
          <div style={{ fontSize: '12px' }}>
            {record.analytics.totalOrders} orders
          </div>
          <div style={{ fontSize: '12px' }}>
            {record.analytics.conversionRate.toFixed(1)}% conv.
          </div>
        </div>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          {record.status === 'scheduled' && (
            <Tooltip title="Start Sale">
              <Button
                type="text"
                icon={<PlayCircleOutlined />}
                onClick={() => handleStartSale(record.id)}
                style={{ color: '#52c41a' }}
              />
            </Tooltip>
          )}
          {record.status === 'active' && (
            <Tooltip title="End Sale">
              <Button
                type="text"
                icon={<StopOutlined />}
                onClick={() => handleEndSale(record.id)}
                style={{ color: '#ff4d4f' }}
              />
            </Tooltip>
          )}
          <Tooltip title="Edit">
            <Button
              type="text"
              icon={<EditOutlined />}
              onClick={() => {
                setSelectedSale(record);
                setEditModalOpen(true);
              }}
            />
          </Tooltip>
          <Tooltip title="Duplicate">
            <Button
              type="text"
              icon={<CopyOutlined />}
              onClick={() => handleDuplicateSale(record.id)}
            />
          </Tooltip>
          <Tooltip title="Delete">
            <Popconfirm
              title="Are you sure you want to delete this flash sale?"
              onConfirm={() => handleDeleteSale(record.id)}
              okText="Delete"
              cancelText="Cancel"
              okButtonProps={{ danger: true }}
            >
              <Button
                type="text"
                icon={<DeleteOutlined />}
                danger
              />
            </Popconfirm>
          </Tooltip>
        </Space>
      ),
    },
  ];

  const exportMenuItems = [
    {
      key: 'csv',
      label: (
        <Space>
          <FileTextOutlined />
          Export as CSV
        </Space>
      ),
      onClick: () => handleExport('csv'),
    },
    {
      key: 'xlsx',
      label: (
        <Space>
          <FileExcelOutlined />
          Export as Excel
        </Space>
      ),
      onClick: () => handleExport('xlsx'),
    },
    {
      key: 'json',
      label: (
        <Space>
          <FileTextOutlined />
          Export as JSON
        </Space>
      ),
      onClick: () => handleExport('json'),
    },
  ];

  const revenueChartData = flashSales.map(sale => ({
    title: sale.title,
    revenue: sale.analytics.totalRevenue,
    orders: sale.analytics.totalOrders,
    status: sale.status,
  }));

  const statusDistribution = [
    { type: 'Active', value: stats?.activeSales || 0 },
    { type: 'Scheduled', value: stats?.scheduledSales || 0 },
    { type: 'Ended', value: stats?.endedSales || 0 },
  ];

  return (
    <Spin spinning={loading} tip="Loading flash sales data...">
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
        {/* Header */}
        <div style={{ marginBottom: 24 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <Title level={3}>
                <ThunderboltOutlined style={{ color: '#ff4d4f' }} /> Flash Sales & Daily Deals
              </Title>
              <Paragraph type="secondary">
                Manage time-limited promotions, flash sales, and daily deals with real-time analytics
              </Paragraph>
            </div>
            <Space>
              <Select
                value={selectedStatus}
                onChange={setSelectedStatus}
                style={{ width: 120 }}
              >
                <Select.Option value="all">All Status</Select.Option>
                <Select.Option value="scheduled">Scheduled</Select.Option>
                <Select.Option value="active">Active</Select.Option>
                <Select.Option value="ended">Ended</Select.Option>
                <Select.Option value="cancelled">Cancelled</Select.Option>
              </Select>
              <RangePicker
                value={dateRange}
                onChange={(dates) => dates && setDateRange(dates as [Dayjs, Dayjs])}
              />
              <Dropdown menu={{ items: exportMenuItems }} placement="bottomRight">
                <Button icon={<DownloadOutlined />}>
                  Export
                </Button>
              </Dropdown>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => setCreateModalOpen(true)}
              >
                Create Flash Sale
              </Button>
            </Space>
          </div>
        </div>

        {/* Alert */}
        <Alert
          message="Flash Sales Management"
          description="Create and manage time-limited promotions with countdown timers, inventory tracking, and real-time analytics. Monitor performance and optimize your flash sales strategy."
          type="info"
          showIcon
          closable
          style={{ marginBottom: 24 }}
        />

        {/* Statistics */}
        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
