type SortOption = 'featured' | 'price-low' | 'price-high' | 'rating' | 'newest' | 'best-seller' | 'popular';
type ViewMode = 'grid' | 'list';

const ProductListingPage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>(mockProducts);
  const [sortBy, setSortBy] = useState<SortOption>('featured');
  const [viewMode, setViewMode] = useState<ViewMode>('grid');
  const [currentPage, setCurrentPage] = useState(1);
  const [vendorSearch, setVendorSearch] = useState('');
  const [priceRange, setPriceRange] = useState<[number, number]>([0, 200]);
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [minRating, setMinRating] = useState(0);
  const [showInStockOnly, setShowInStockOnly] = useState(false);

  const pageSize = 8;

  // Sorting logic
  const sortProducts = (prods: Product[], sortOption: SortOption): Product[] => {
    const sorted = [...prods];
    switch (sortOption) {
      case 'price-low':
        return sorted.sort((a, b) => a.price - b.price);
      case 'price-high':
        return sorted.sort((a, b) => b.price - a.price);
      case 'rating':
        return sorted.sort((a, b) => b.rating - a.rating);
      case 'newest':
        return sorted.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
      case 'best-seller':
        return sorted.sort((a, b) => b.sales - a.sales);
      case 'popular':
        return sorted.sort((a, b) => b.reviews - a.reviews);
      case 'featured':
      default:
        return sorted;
    }
  };

  // Filter logic
  const getFilteredProducts = (): Product[] => {
    let filtered = [...products];

    // Vendor search
    if (vendorSearch) {
      filtered = filtered.filter(p =>
        p.vendor.toLowerCase().includes(vendorSearch.toLowerCase())
      );
    }

    // Price range
    filtered = filtered.filter(p => p.price >= priceRange[0] && p.price <= priceRange[1]);

    // Categories
    if (selectedCategories.length > 0) {
      filtered = filtered.filter(p => selectedCategories.includes(p.category));
    }

    // Rating
    if (minRating > 0) {
      filtered = filtered.filter(p => p.rating >= minRating);
    }

    // Stock
    if (showInStockOnly) {
      filtered = filtered.filter(p => p.inStock);
    }

    return sortProducts(filtered, sortBy);
  };

  const filteredProducts = getFilteredProducts();
  const paginatedProducts = filteredProducts.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  const handleCategoryChange = (category: string, checked: boolean) => {
    if (checked) {
      setSelectedCategories([...selectedCategories, category]);
    } else {
      setSelectedCategories(selectedCategories.filter(c => c !== category));
    }
  };

  const getBadgeColor = (badge?: string) => {
    switch (badge) {
      case 'Best Seller':
        return 'gold';
      case 'Trending':
        return 'orange';
      case 'New Arrival':
        return 'blue';
      case 'Deal':
        return 'red';
      case 'Popular':
        return 'green';
      default:
        return 'default';
    }
  };

  const getBadgeIcon = (badge?: string) => {
    switch (badge) {
      case 'Best Seller':
        return <TrophyOutlined />;
      case 'Trending':
        return <FireOutlined />;
      case 'New Arrival':
        return <StarFilled />;
      default:
        return null;
    }
  };

  const renderProductCard = (product: Product) => {
    if (viewMode === 'list') {
      return (
        <Card
          key={product.id}
          hoverable
          style={{ marginBottom: 16 }}
        >
          <Row gutter={16} align="middle">
            <Col xs={24} sm={6}>
              <div style={{ position: 'relative' }}>
                <Image
                  src={product.image}
                  alt={product.name}
                  preview={false}
                  style={{ width: '100%', height: 150, objectFit: 'cover' }}
                />
                {product.badge && (
                  <div style={{ position: 'absolute', top: 8, left: 8 }}>
                    <Tag color={getBadgeColor(product.badge)} icon={getBadgeIcon(product.badge)}>
                      {product.badge}
                    </Tag>
                  </div>
                )}
                {!product.inStock && (
                  <div
                    style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'rgba(0, 0, 0, 0.5)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontWeight: 600,
                    }}
                  >
                    Out of Stock
                  </div>
                )}
              </div>
            </Col>
            <Col xs={24} sm={12}>
              <Title level={5} style={{ marginBottom: 8 }}>
                {product.name}
              </Title>
              <div style={{ marginBottom: 8 }}>
                <Rate disabled value={product.rating} style={{ fontSize: 14 }} />
                <Text type="secondary" style={{ marginLeft: 8 }}>
                  {product.rating} ({product.reviews} reviews)
                </Text>
              </div>
              <Text type="secondary" style={{ display: 'block', marginBottom: 8 }}>
                Sold by: <Text strong>{product.vendor}</Text>
              </Text>
              <Text type="secondary" style={{ fontSize: 12 }}>
                {product.category}  {product.sales.toLocaleString()} sold
              </Text>
            </Col>
            <Col xs={24} sm={6} style={{ textAlign: 'right' }}>
              <div style={{ marginBottom: 16 }}>
                <div>
                  <Text strong style={{ fontSize: 24, color: '#ff9900' }}>
                    ${product.price}
                  </Text>
                </div>
                {product.originalPrice && (
                  <div>
                    <Text delete type="secondary">
                      ${product.originalPrice}
                    </Text>
                    <Tag color="red" style={{ marginLeft: 8 }}>
                      {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                    </Tag>
                  </div>
                )}
              </div>
              <Space direction="vertical" style={{ width: '100%' }}>
                <Button
                  type="primary"
                  icon={<ShoppingCartOutlined />}
                  block
                  disabled={!product.inStock}
                >
                  Add to Cart
                </Button>
                <Button icon={<HeartOutlined />} block>
                  Wishlist
                </Button>
              </Space>
            </Col>
          </Row>
        </Card>
      );
    }

    // Grid view
    return (
      <Col xs={24} sm={12} md={8} lg={6} key={product.id}>
        <Card
          hoverable
          cover={
            <div style={{ position: 'relative' }}>
              <Image
                src={product.image}
                alt={product.name}
                preview={false}
                style={{ height: 200, objectFit: 'cover' }}
              />
              {product.badge && (
                <div style={{ position: 'absolute', top: 8, left: 8 }}>
                  <Tag color={getBadgeColor(product.badge)} icon={getBadgeIcon(product.badge)}>
                    {product.badge}
                  </Tag>
                </div>
              )}
              {!product.inStock && (
                <div
                  style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'rgba(0, 0, 0, 0.5)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: 18,
                    fontWeight: 600,
                  }}
                >
                  Out of Stock
                </div>
              )}
            </div>
          }
        >
          <div style={{ minHeight: 220 }}>
            <Tooltip title={product.name}>
              <Title level={5} ellipsis={{ rows: 2 }} style={{ height: 48, marginBottom: 8 }}>
                {product.name}
              </Title>
            </Tooltip>

            <div style={{ marginBottom: 8 }}>
              <Rate disabled value={product.rating} style={{ fontSize: 14 }} />
              <Text type="secondary" style={{ marginLeft: 8, fontSize: 12 }}>
                ({product.reviews})
              </Text>
            </div>

            <Text type="secondary" style={{ display: 'block', marginBottom: 8, fontSize: 12 }}>
              by <Text strong>{product.vendor}</Text>
            </Text>

            <div style={{ marginBottom: 12 }}>
              <Text strong style={{ fontSize: 20, color: '#ff9900' }}>
                ${product.price}
              </Text>
              {product.originalPrice && (
                <Text delete type="secondary" style={{ marginLeft: 8, fontSize: 14 }}>
                  ${product.originalPrice}
                </Text>
              )}
            </div>

            <Space size="small" style={{ width: '100%' }}>
              <Button
                type="primary"
                size="small"
                icon={<ShoppingCartOutlined />}
                disabled={!product.inStock}
                style={{ flex: 1 }}
              >
                Add
              </Button>
              <Button size="small" icon={<HeartOutlined />} />
            </Space>
          </div>
        </Card>
      </Col>
    );
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row gutter={24}>
        {/* Filters Sidebar */}
        <Col xs={24} lg={6}>
          <Card title={<><FilterOutlined /> Filters</>} style={{ marginBottom: 16 }}>
            {/* Vendor Search */}
            <div style={{ marginBottom: 16 }}>
              <Text strong>Search by Vendor</Text>
              <Input
                placeholder="Enter vendor name..."
                prefix={<SearchOutlined />}
                value={vendorSearch}
                onChange={e => setVendorSearch(e.target.value)}
                allowClear
                style={{ marginTop: 8 }}
              />
            </div>

            <Divider />

            {/* Price Range */}
            <div style={{ marginBottom: 16 }}>
              <Text strong>Price Range</Text>
              <Slider
                range
                min={0}
                max={200}
                value={priceRange}
                onChange={(value) => setPriceRange(value as [number, number])}
                tooltip={{ formatter: (value) => `$${value}` }}
                style={{ marginTop: 16 }}
              />
              <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 8 }}>
                <Text type="secondary">${priceRange[0]}</Text>
                <Text type="secondary">${priceRange[1]}</Text>
              </div>
            </div>

            <Divider />

            {/* Categories */}
            <div style={{ marginBottom: 16 }}>
              <Text strong>Categories</Text>
              <div style={{ marginTop: 8 }}>
                <Checkbox
                  checked={selectedCategories.includes('Electronics')}
                  onChange={e => handleCategoryChange('Electronics', e.target.checked)}
                >
                  Electronics
                </Checkbox>
                <br />
                <Checkbox
                  checked={selectedCategories.includes('Accessories')}
                  onChange={e => handleCategoryChange('Accessories', e.target.checked)}
                  style={{ marginTop: 8 }}
                >
                  Accessories
                </Checkbox>
              </div>
            </div>

            <Divider />

            {/* Rating */}
            <div style={{ marginBottom: 16 }}>
              <Text strong>Minimum Rating</Text>
              <div style={{ marginTop: 8 }}>
                <Rate value={minRating} onChange={setMinRating} />
              </div>
            </div>

            <Divider />

            {/* Stock */}
            <Checkbox
              checked={showInStockOnly}
              onChange={e => setShowInStockOnly(e.target.checked)}
            >
              In Stock Only
            </Checkbox>
          </Card>
        </Col>

        {/* Products Grid */}
        <Col xs={24} lg={18}>
          <Card>
            {/* Toolbar */}
            <div style={{ marginBottom: 16, display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: 16 }}>
              <div>
                <Text strong>{filteredProducts.length} Products</Text>
              </div>

              <Space>
                <Select
                  value={sortBy}
                  onChange={setSortBy}
                  style={{ width: 200 }}
                  suffixIcon={<SortAscendingOutlined />}
                >
                  <Option value="featured">Featured</Option>
                  <Option value="newest">Newest Arrivals</Option>
                  <Option value="best-seller">Best Sellers</Option>
                  <Option value="popular">Most Popular</Option>
                  <Option value="rating">Highest Rated</Option>
                  <Option value="price-low">Price: Low to High</Option>
                  <Option value="price-high">Price: High to Low</Option>
                </Select>

                <Button.Group>
                  <Button
                    type={viewMode === 'grid' ? 'primary' : 'default'}
                    icon={<AppstoreOutlined />}
                    onClick={() => setViewMode('grid')}
                  />
                  <Button
                    type={viewMode === 'list' ? 'primary' : 'default'}
                    icon={<UnorderedListOutlined />}
                    onClick={() => setViewMode('list')}
                  />
                </Button.Group>
              </Space>
            </div>

            {/* Products */}
            {paginatedProducts.length === 0 ? (
              <Empty description="No products found" />
            ) : viewMode === 'list' ? (
              <div>
                {paginatedProducts.map(product => renderProductCard(product))}
              </div>
            ) : (
              <Row gutter={[16, 16]}>
                {paginatedProducts.map(product => renderProductCard(product))}
              </Row>
            )}

            {/* Pagination */}
            {filteredProducts.length > pageSize && (
              <div style={{ marginTop: 24, textAlign: 'center' }}>
                <Pagination
                  current={currentPage}
                  total={filteredProducts.length}
                  pageSize={pageSize}
                  onChange={setCurrentPage}
                  showSizeChanger={false}
                  showTotal={(total) => `Total ${total} products`}
                />
              </div>
            )}
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default ProductListingPage;

customer/wishlist.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Empty,
  Rate,
  Tag,
  Space,
  message,
  Popconfirm,
  Select,
  Spin,
} from 'antd';
import {
  DeleteOutlined,
  ShoppingCartOutlined,
  ShareAltOutlined,
  HeartFilled,
} from '@ant-design/icons';
import { history } from 'umi';
import { motion } from 'framer-motion';
import { wishlistAPI, type WishlistItem } from '@/services/api/cart';
import { useCartStore } from '@/store/cartStore';

const { Title, Text } = Typography;
const { Option } = Select;

// Extended wishlist item type with product details
interface ExtendedWishlistItem extends WishlistItem {
  name?: string;
  image?: string;
  originalPrice?: number;
  discount?: number;
  rating?: number;
  reviewCount?: number;
  vendor?: string;
}

const WishlistPage: React.FC = () => {
  const [wishlistItems, setWishlistItems] = useState<ExtendedWishlistItem[]>([]);
  const [loading, setLoading] = useState(false);
  const [sortBy, setSortBy] = useState('newest');
  const { addItem: addToCart } = useCartStore();

  // Fetch wishlist from API
  const fetchWishlist = async () => {
    try {
      setLoading(true);
      const wishlistData = await wishlistAPI.get();

      // Map API response to UI format
      const mappedItems: ExtendedWishlistItem[] = wishlistData.items.map((item) => ({
        ...item,
        name: item.productName,
        image: item.productImage || 'https://via.placeholder.com/300x300/1890ff/fff?text=Product',
        originalPrice: item.price * 1.2, // Placeholder - would come from product API
        discount: 20, // Placeholder - would come from product API
        rating: 4.5, // Placeholder - would come from product API
        reviewCount: 100, // Placeholder - would come from product API
        vendor: 'Vendor Name', // Placeholder - would come from product API
      }));

      setWishlistItems(mappedItems);
    } catch (error) {
      message.error('Failed to load wishlist');
      console.error('Error fetching wishlist:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWishlist();
  }, []);

  const handleRemoveFromWishlist = async (id: string) => {
    try {
      await wishlistAPI.removeItem(id);
      setWishlistItems(wishlistItems.filter((item) => item.id !== id));
      message.success('Item removed from wishlist');
    } catch (error) {
      message.error('Failed to remove item');
      console.error('Error removing item:', error);
    }
  };

  const handleAddToCart = async (item: ExtendedWishlistItem) => {
    if (!item.inStock) {
      message.warning('This item is currently out of stock');
      return;
    }

    try {
      await addToCart({ productId: item.productId, quantity: 1 });
      message.success(`${item.productName} added to cart`);
    } catch (error) {
      message.error('Failed to add item to cart');
      console.error('Error adding to cart:', error);
    }
  };

  const handleMoveToCart = async (item: ExtendedWishlistItem) => {
    if (!item.inStock) {
      message.warning('This item is currently out of stock');
      return;
    }

    try {
      await wishlistAPI.moveToCart(item.id);
      setWishlistItems(wishlistItems.filter((i) => i.id !== item.id));
      message.success(`${item.productName} moved to cart`);
    } catch (error) {
      message.error('Failed to move item to cart');
      console.error('Error moving to cart:', error);
    }
  };

  const handleAddAllToCart = async () => {
    const inStockItems = wishlistItems.filter((item) => item.inStock);
    if (inStockItems.length === 0) {
      message.warning('No items in stock');
      return;
    }

    try {
      setLoading(true);
      // Add all in-stock items to cart
      await Promise.all(
        inStockItems.map((item) => addToCart({ productId: item.productId, quantity: 1 }))
      );
      message.success(`${inStockItems.length} items added to cart`);
    } catch (error) {
      message.error('Failed to add some items to cart');
      console.error('Error adding all to cart:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleShare = () => {
    const shareUrl = `${window.location.origin}/wishlist/shared/${Date.now()}`;
    if (navigator.share) {
      navigator.share({
        title: 'My Wishlist',
        text: 'Check out my wishlist!',
        url: shareUrl,
      });
    } else {
      navigator.clipboard.writeText(shareUrl);
      message.success('Wishlist link copied to clipboard!');
    }
  };

  const sortedItems = [...wishlistItems].sort((a, b) => {
    switch (sortBy) {
      case 'price-low':
        return a.price - b.price;
      case 'price-high':
        return b.price - a.price;
      case 'discount':
        return (b.discount || 0) - (a.discount || 0);
      case 'newest':
      default:
        return new Date(b.addedAt).getTime() - new Date(a.addedAt).getTime();
    }
  });

  // Show loading spinner
  if (loading && wishlistItems.length === 0) {
    return (
      <div style={{ padding: 24, textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: 24, maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={2}>
            <HeartFilled style={{ color: '#ff4d4f', marginRight: 8 }} />
            My Wishlist ({wishlistItems.length} items)
          </Title>
        </Col>
        <Col>
          <Space>
            <Select
              value={sortBy}
              onChange={setSortBy}
              style={{ width: 180 }}
              placeholder="Sort by"
            >
              <Option value="newest">Newest First</Option>
              <Option value="price-low">Price: Low to High</Option>
              <Option value="price-high">Price: High to Low</Option>
              <Option value="discount">Highest Discount</Option>
            </Select>
            <Button icon={<ShareAltOutlined />} onClick={handleShare}>
              Share Wishlist
            </Button>
            <Button
              type="primary"
              icon={<ShoppingCartOutlined />}
              onClick={handleAddAllToCart}
              disabled={wishlistItems.length === 0}
            >
              Add All to Cart
            </Button>
          </Space>
        </Col>
      </Row>

      {/* Wishlist Items */}
      {wishlistItems.length === 0 ? (
        <Card>
          <Empty
            image={Empty.PRESENTED_IMAGE_SIMPLE}
            description={
              <span>
                Your wishlist is empty
                <br />
                <Text type="secondary">Browse products and add items to your wishlist</Text>
              </span>
            }
          >
            <Button type="primary" onClick={() => history.push('/products')}>
              Browse Products
            </Button>
          </Empty>
        </Card>
      ) : (
        <Row gutter={[16, 16]}>
          {sortedItems.map((item, index) => (
            <Col xs={24} sm={12} lg={8} xl={6} key={item.id}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3, delay: index * 0.05 }}
              >
                <Card
                  hoverable
                  cover={
                    <div style={{ position: 'relative' }}>
                      <img
                        alt={item.name}
                        src={item.image}
                        style={{ width: '100%', height: 280, objectFit: 'cover' }}
                        onClick={() => history.push(`/products/${item.productId}`)}
                      />
                      {item.discount && item.discount > 0 && (
                        <Tag
                          color="red"
                          style={{
                            position: 'absolute',
                            top: 12,
                            left: 12,
                            fontSize: 14,
                            fontWeight: 'bold',
                          }}
                        >
                          -{item.discount}%
                        </Tag>
                      )}
                      {!item.inStock && (
                        <div
                          style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0,0,0,0.5)',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                          }}
                        >
                          <Tag color="error" style={{ fontSize: 16, padding: '8px 16px' }}>
                            Out of Stock
                          </Tag>
                        </div>
                      )}
                      <Popconfirm
                        title="Remove from wishlist?"
                        onConfirm={() => handleRemoveFromWishlist(item.id)}
                        okText="Yes"
                        cancelText="No"
                      >
                        <Button
                          type="text"
                          icon={<DeleteOutlined />}
                          danger
                          style={{
                            position: 'absolute',
                            top: 12,
                            right: 12,
                            background: 'rgba(255,255,255,0.9)',
                          }}
                        />
                      </Popconfirm>
                    </div>
                  }
                  actions={[
                    <Button
                      key="addToCart"
                      type="primary"
                      block
                      icon={<ShoppingCartOutlined />}
                      onClick={() => handleAddToCart(item)}
                      disabled={!item.inStock}
                    >
                      Add to Cart
                    </Button>,
                  ]}
                >
                  <Card.Meta
                    title={
                      <div
                        onClick={() => history.push(`/products/${item.productId}`)}
                        style={{ cursor: 'pointer' }}
                      >
                        <Text strong ellipsis={{ tooltip: item.name }}>
                          {item.name}
                        </Text>
                      </div>
                    }
                    description={
                      <Space direction="vertical" size={4} style={{ width: '100%' }}>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          by {item.vendor}
                        </Text>
                        <Space>
                          <Rate disabled defaultValue={item.rating} style={{ fontSize: 12 }} />
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            ({item.reviewCount})
                          </Text>
                        </Space>
                        <Space align="baseline">
                          <Text strong style={{ fontSize: 18, color: '#B12704' }}>
                            ${item.price.toFixed(2)}
                          </Text>
                          {item.originalPrice && item.originalPrice > item.price && (
                            <Text delete type="secondary" style={{ fontSize: 14 }}>
                              ${item.originalPrice.toFixed(2)}
                            </Text>
                          )}
                        </Space>
                        <Text type="secondary" style={{ fontSize: 11 }}>
                          Added on {new Date(item.addedAt).toLocaleDateString()}
                        </Text>
                      </Space>
                    }
                  />
                </Card>
              </motion.div>
            </Col>
          ))}
        </Row>
      )}

      {/* Wishlist Stats */}
      {wishlistItems.length > 0 && (
        <Card style={{ marginTop: 24 }}>
          <Row gutter={32}>
            <Col span={6}>
              <Text type="secondary">Total Items</Text>
              <Title level={3} style={{ margin: 0 }}>
                {wishlistItems.length}
              </Title>
            </Col>
            <Col span={6}>
              <Text type="secondary">Total Value</Text>
              <Title level={3} style={{ margin: 0, color: '#B12704' }}>
                ${wishlistItems.reduce((sum, item) => sum + item.price, 0).toFixed(2)}
              </Title>
            </Col>
            <Col span={6}>
              <Text type="secondary">Potential Savings</Text>
              <Title level={3} style={{ margin: 0, color: '#52c41a' }}>
                $
                {wishlistItems
                  .reduce((sum, item) => sum + ((item.originalPrice || item.price) - item.price), 0)
                  .toFixed(2)}
              </Title>
            </Col>
            <Col span={6}>
              <Text type="secondary">In Stock</Text>
              <Title level={3} style={{ margin: 0 }}>
                {wishlistItems.filter((item) => item.inStock).length}
              </Title>
            </Col>
          </Row>
        </Card>
      )}
    </div>
  );
};

export default WishlistPage;

help/buying-guide.__content:
import React, { useState } from 'react';
import {
