      tips: category.tips.filter(
        tip =>
          tip.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
          tip.content.toLowerCase().includes(searchQuery.toLowerCase())
      ),
    }))
    .filter(category => category.tips.length > 0);

  return (
