const LoyaltyProgramPage: React.FC = () => {
  // State management
  const [loading, setLoading] = useState<boolean>(false);
  const [customerLoyalty, setCustomerLoyalty] = useState<CustomerLoyalty | null>(null);
  const [loyaltyTiers, setLoyaltyTiers] = useState<LoyaltyTierAPI[]>([]);
  const [loyaltyRewards, setLoyaltyRewards] = useState<LoyaltyReward[]>([]);
  const [pointsTransactions, setPointsTransactions] = useState<PointsTransaction[]>([]);
  const [loyaltyAnalytics, setLoyaltyAnalytics] = useState<LoyaltyAnalytics | null>(null);
  const [loyaltyEngagement, setLoyaltyEngagement] = useState<LoyaltyEngagement | null>(null);

  // Fallback to mock data for display
  const currentPoints = customerLoyalty?.currentPoints || 1250;
  const pointsHistory = pointsTransactions.length > 0 ? pointsTransactions : mockPointsHistory;
  const rewards = loyaltyRewards.length > 0 ? loyaltyRewards : mockRewards;

  // Fetch loyalty data on mount
  useEffect(() => {
    fetchLoyaltyData();
  }, []);

  const fetchLoyaltyData = async () => {
    setLoading(true);
    try {
      // For customer view, we need to get their own loyalty data
      const [tiersRes, rewardsRes, transactionsRes, analyticsRes] = await Promise.all([
        loyaltyAPI.getTiers(),
        loyaltyAPI.getRewards({ status: 'active' }),
        loyaltyAPI.getPointsTransactions({ limit: 50 }),
        loyaltyAPI.getAnalytics(),
      ]);

      // Try to get current customer loyalty data
      try {
        const customerRes = await loyaltyAPI.getCustomerLoyaltyById('current'); // assumes current user endpoint
        setCustomerLoyalty(customerRes);
      } catch (error) {
        console.warn('Customer loyalty data not available');
      }

      setLoyaltyTiers(tiersRes);
      setLoyaltyRewards(rewardsRes.rewards);
      setPointsTransactions(transactionsRes.transactions);
      setLoyaltyAnalytics(analyticsRes);
    } catch (error) {
      // If APIs aren't available, continue with mock data
      console.warn('Loyalty APIs not available, using mock data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRedeemReward = async (rewardId: string) => {
    try {
      await loyaltyAPI.redeemReward('current', rewardId);
      message.success('Reward redeemed successfully!');
      fetchLoyaltyData(); // Refresh data
    } catch (error) {
      message.error('Failed to redeem reward. Please try again.');
      console.error('Redeem error:', error);
    }
  };

  const getCurrentTier = () => {
    for (let i = tiers.length - 1; i >= 0; i--) {
      if (currentPoints >= tiers[i].minPoints) {
        return { tier: tiers[i], index: i };
      }
    }
    return { tier: tiers[0], index: 0 };
  };

  const getNextTier = () => {
    const { index } = getCurrentTier();
    return index < tiers.length - 1 ? tiers[index + 1] : null;
  };

  const currentTierInfo = getCurrentTier();
  const nextTier = getNextTier();
  const progressToNextTier = nextTier
    ? ((currentPoints - currentTierInfo.tier.minPoints) / (nextTier.minPoints - currentTierInfo.tier.minPoints)) * 100
    : 100;

  const historyColumns: ColumnsType<any> = [
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
      render: (date, record: any) => new Date(date || record.createdAt || record.timestamp).toLocaleDateString(),
    },
    {
      title: 'Activity',
      dataIndex: 'description',
      key: 'description',
      render: (desc, record: any) => (
        <div>
          <Text>{desc || record.reason || record.activity}</Text>
          {(record.orderId || record.referenceId) && (
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                Order: {record.orderId || record.referenceId}
              </Text>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Points',
      dataIndex: 'points',
      key: 'points',
      render: (points, record) => (
        <Tag color={record.type === 'earned' ? 'green' : 'orange'} style={{ fontSize: 14 }}>
          {points > 0 ? '+' : ''}{points}
        </Tag>
      ),
    },
  ];

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Spin spinning={loading}>
        <div style={{ marginBottom: 24, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <Title level={3}>
              <TrophyOutlined style={{ color: '#FFD700' }} /> Loyalty Rewards Program
            </Title>
            <Paragraph type="secondary">
              Earn points with every purchase and unlock exclusive rewards
            </Paragraph>
          </div>
          <Button 
            icon={<ReloadOutlined />} 
            onClick={fetchLoyaltyData}
            loading={loading}
          >
            Refresh
          </Button>
        </div>

      {/* Current Status */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={16}>
          <Card>
            <Row gutter={24}>
              <Col xs={24} sm={8}>
                <div style={{ textAlign: 'center' }}>
                  <div style={{ fontSize: 48, color: currentTierInfo.tier.color, marginBottom: 8 }}>
                    {currentTierInfo.tier.icon}
                  </div>
                  <Title level={4} style={{ margin: 0, color: currentTierInfo.tier.color }}>
                    {currentTierInfo.tier.name}
                  </Title>
                  <Text type="secondary">Current Tier</Text>
                </div>
              </Col>
              <Col xs={24} sm={16}>
                <Statistic
                  title="Total Points"
                  value={currentPoints}
                  prefix={<StarFilled style={{ color: '#FFD700' }} />}
                  valueStyle={{ color: '#1890ff', fontSize: 36 }}
                  style={{ marginBottom: 16 }}
                />
                {nextTier && (
                  <div>
                    <div style={{ marginBottom: 8 }}>
                      <Text>
                        {nextTier.minPoints - currentPoints} points to {nextTier.name} tier
                      </Text>
                    </div>
                    <Progress
                      percent={Math.round(progressToNextTier)}
                      strokeColor={{
                        '0%': currentTierInfo.tier.color,
                        '100%': nextTier.color,
                      }}
                      format={(percent) => `${percent}%`}
                    />
                  </div>
                )}
              </Col>
            </Row>
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card style={{ height: '100%' }}>
            <Statistic
              title="Points This Month"
              value={300}
              prefix={<ThunderboltOutlined />}
              valueStyle={{ color: '#52c41a' }}
              style={{ marginBottom: 16 }}
            />
            <Statistic
              title="Lifetime Points Earned"
              value={2450}
              prefix={<GiftOutlined />}
              valueStyle={{ color: '#ff9900' }}
            />
          </Card>
        </Col>
      </Row>

      {/* How to Earn Points */}
      <Card title="How to Earn Points" style={{ marginBottom: 24 }}>
        <Row gutter={[16, 16]}>
          <Col xs={24} sm={12} md={6}>
            <Card size="small" style={{ textAlign: 'center' }}>
              <ShoppingOutlined style={{ fontSize: 32, color: '#1890ff', marginBottom: 8 }} />
              <Title level={5}>Make Purchases</Title>
              <Text type="secondary">Earn 10 points per $1 spent</Text>
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card size="small" style={{ textAlign: 'center' }}>
              <StarFilled style={{ fontSize: 32, color: '#FFD700', marginBottom: 8 }} />
              <Title level={5}>Write Reviews</Title>
              <Text type="secondary">Earn 50 points per review</Text>
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card size="small" style={{ textAlign: 'center' }}>
              <TeamOutlined style={{ fontSize: 32, color: '#52c41a', marginBottom: 8 }} />
              <Title level={5}>Refer Friends</Title>
              <Text type="secondary">Earn 100 points per referral</Text>
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card size="small" style={{ textAlign: 'center' }}>
              <GiftOutlined style={{ fontSize: 32, color: '#ff9900', marginBottom: 8 }} />
              <Title level={5}>Birthday Bonus</Title>
              <Text type="secondary">Earn 200 points annually</Text>
            </Card>
          </Col>
        </Row>
      </Card>

      {/* Membership Tiers */}
      <Card title="Membership Tiers" style={{ marginBottom: 24 }}>
        <Timeline>
          {tiers.map((tier, index) => (
            <Timeline.Item
              key={tier.name}
              color={currentTierInfo.tier.name === tier.name ? tier.color : 'gray'}
              dot={
                currentTierInfo.tier.name === tier.name ? (
                  <CheckCircleOutlined style={{ fontSize: 20, color: tier.color }} />
                ) : (
                  <div style={{ fontSize: 16, color: tier.color }}>{tier.icon}</div>
                )
              }
            >
              <Card
                size="small"
                style={{
                  border: currentTierInfo.tier.name === tier.name ? `2px solid ${tier.color}` : '1px solid #d9d9d9',
                  background: currentTierInfo.tier.name === tier.name ? '#fafafa' : 'white',
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
                  <Title level={5} style={{ margin: 0, color: tier.color }}>
                    {tier.name} Tier
                  </Title>
                  <Tag color={tier.color}>{tier.minPoints}+ points</Tag>
                </div>
                <div>
                  {tier.benefits.map((benefit, idx) => (
                    <div key={idx} style={{ marginBottom: 4 }}>
                      <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                      <Text>{benefit}</Text>
                    </div>
                  ))}
                </div>
              </Card>
            </Timeline.Item>
          ))}
        </Timeline>
      </Card>

      {/* Tabs for History and Rewards */}
      <Card>
        <Tabs
          items={[
            {
              key: 'rewards',
              label: (
                <span>
                  <GiftOutlined /> Available Rewards
                </span>
              ),
              children: (
                <Row gutter={[16, 16]}>
                  {rewards.map((reward) => (
                    <Col xs={24} sm={12} md={8} lg={6} key={reward.id}>
                      <Badge.Ribbon
                        text={
                          (reward as any).available !== undefined 
                            ? ((reward as any).available ? 'Available' : 'Coming Soon')
                            : ((reward as LoyaltyReward).status === 'active' ? 'Available' : 'Coming Soon')
                        }
                        color={
                          (reward as any).available !== undefined
                            ? ((reward as any).available ? 'green' : 'gray')
                            : ((reward as LoyaltyReward).status === 'active' ? 'green' : 'gray')
                        }
                      >
                        <Card
                          hoverable
                          cover={
                            <img
                              src={reward.image}
                              alt={reward.name}
                              style={{ height: 150, objectFit: 'cover' }}
                            />
                          }
                        >
                          <Title level={5} ellipsis={{ rows: 1 }}>
                            {reward.name}
                          </Title>
                          <Paragraph type="secondary" ellipsis={{ rows: 2 }} style={{ minHeight: 44 }}>
                            {reward.description}
                          </Paragraph>
                          <div style={{ marginTop: 12 }}>
                            <Tag color="gold" style={{ fontSize: 14 }}>
                              <StarFilled /> {reward.pointsCost} points
                            </Tag>
                          </div>
                          <Button
                            type="primary"
                            block
                            style={{ marginTop: 12 }}
                            onClick={() => handleRedeemReward(String(reward.id))}
                            disabled={
                              !((reward as any).available !== undefined 
                                ? (reward as any).available
                                : (reward as LoyaltyReward).status === 'active') || 
                              currentPoints < reward.pointsCost
                            }
                          >
                            {currentPoints >= reward.pointsCost ? 'Redeem' : 'Not Enough Points'}
                          </Button>
                        </Card>
                      </Badge.Ribbon>
                    </Col>
                  ))}
                </Row>
              ),
            },
            {
              key: 'history',
              label: (
                <span>
                  <ShoppingOutlined /> Points History
                </span>
              ),
              children: <Table columns={historyColumns} dataSource={pointsHistory} rowKey="id" pagination={{ pageSize: 10 }} />,
            },
          ]}
        />
      </Card>
      </Spin>
    </div>
  );
};

export default LoyaltyProgramPage;

customer/pricing.__content:
import React, { useState } from 'react';
import {
  Card,
  Typography,
  Table,
  Tag,
  InputNumber,
  Button,
  Space,
  message,
  Row,
  Col,
  Statistic,
  Alert,
  Tooltip,
  Divider,
} from 'antd';
import {
  PercentageOutlined,
  ThunderboltOutlined,
  TeamOutlined,
  TrophyOutlined,
  InfoCircleOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text, Paragraph } = Typography;

interface PricingTier {
  minQty: number;
  maxQty: number | null;
  discount: number;
  pricePerUnit: number;
  tierName: string;
}

interface Product {
  id: number;
  name: string;
  image: string;
  basePrice: number;
  category: string;
  vendor: string;
}

const mockProduct: Product = {
  id: 1,
  name: 'Wireless Bluetooth Headphones Pro',
  image: 'https://via.placeholder.com/400x300?text=Product',
  basePrice: 89.99,
  category: 'Electronics',
  vendor: 'AudioTech Solutions',
};

const pricingTiers: PricingTier[] = [
  {
    minQty: 1,
    maxQty: 9,
    discount: 0,
    pricePerUnit: 89.99,
    tierName: 'Retail',
  },
  {
    minQty: 10,
    maxQty: 49,
    discount: 10,
    pricePerUnit: 80.99,
    tierName: 'Small Business',
  },
  {
    minQty: 50,
    maxQty: 99,
    discount: 15,
    pricePerUnit: 76.49,
    tierName: 'Bulk',
  },
  {
    minQty: 100,
    maxQty: 499,
    discount: 20,
    pricePerUnit: 71.99,
    tierName: 'Wholesale',
  },
  {
    minQty: 500,
    maxQty: null,
    discount: 25,
    pricePerUnit: 67.49,
    tierName: 'Enterprise',
  },
];

const DynamicPricingPage: React.FC = () => {
  const [quantity, setQuantity] = useState(1);

  const getCurrentTier = (): PricingTier => {
    return (
      pricingTiers.find(
        tier => quantity >= tier.minQty && (tier.maxQty === null || quantity <= tier.maxQty)
      ) || pricingTiers[0]
    );
  };

  const calculatePricing = () => {
    const tier = getCurrentTier();
    const subtotal = tier.pricePerUnit * quantity;
    const savings = (mockProduct.basePrice - tier.pricePerUnit) * quantity;

    return {
      tier,
      pricePerUnit: tier.pricePerUnit,
      subtotal,
      savings,
      savingsPercent: tier.discount,
    };
  };

  const pricing = calculatePricing();

  const columns: ColumnsType<PricingTier> = [
    {
      title: 'Tier',
      dataIndex: 'tierName',
      key: 'tierName',
      render: (text: string, record: PricingTier) => (
        <div>
          <Text strong>{text}</Text>
          {getCurrentTier().tierName === text && (
            <Tag color="blue" style={{ marginLeft: 8 }}>
              Current
            </Tag>
          )}
        </div>
      ),
    },
    {
      title: 'Quantity Range',
      key: 'range',
      render: (_, record: PricingTier) => (
        <Text>
          {record.minQty} - {record.maxQty || ''}
        </Text>
      ),
    },
    {
      title: 'Discount',
      dataIndex: 'discount',
      key: 'discount',
      render: (discount: number) => (
        <Tag color={discount > 0 ? 'green' : 'default'}>
          {discount > 0 ? `-${discount}%` : 'Standard'}
        </Tag>
      ),
    },
    {
      title: 'Price Per Unit',
      dataIndex: 'pricePerUnit',
      key: 'pricePerUnit',
      render: (price: number, record: PricingTier) => (
        <div>
          <Text strong style={{ fontSize: 16, color: '#ff9900' }}>
            ${price.toFixed(2)}
          </Text>
          {record.discount > 0 && (
            <div>
              <Text delete type="secondary" style={{ fontSize: 12 }}>
                ${mockProduct.basePrice.toFixed(2)}
              </Text>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Total Savings',
      key: 'savings',
      render: (_, record: PricingTier) => {
        if (record.discount === 0) return <Text type="secondary">-</Text>;
        const savings = (mockProduct.basePrice - record.pricePerUnit) * record.minQty;
        return (
          <Text strong style={{ color: '#52c41a' }}>
            ${savings.toFixed(2)}+
          </Text>
        );
      },
    },
  ];

  const handleAddToCart = () => {
    message.success(
      `Added ${quantity} items to cart at $${pricing.pricePerUnit.toFixed(2)} each (${
        pricing.tier.tierName
      } tier)`
    );
  };

  const handleQuantityChange = (value: number | null) => {
    if (value && value > 0) {
      setQuantity(value);
    }
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <PercentageOutlined style={{ color: '#ff9900' }} /> Dynamic Pricing Calculator
        </Title>
        <Paragraph type="secondary">
          Get better prices when you buy in bulk. The more you buy, the more you save!
        </Paragraph>
      </div>

      <Alert
        message="Volume Discounts Available"
        description="Our dynamic pricing automatically applies the best discount based on your quantity. Discounts range from 10% to 25% off!"
        type="success"
        icon={<ThunderboltOutlined />}
        showIcon
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={24}>
        {/* Product Info */}
        <Col xs={24} lg={8}>
          <Card
            cover={
              <img
                src={mockProduct.image}
                alt={mockProduct.name}
                style={{ height: 300, objectFit: 'cover' }}
              />
            }
          >
            <Title level={4}>{mockProduct.name}</Title>
            <Text type="secondary" style={{ display: 'block', marginBottom: 8 }}>
              Category: {mockProduct.category}
            </Text>
            <Text type="secondary" style={{ display: 'block', marginBottom: 16 }}>
              Sold by: <Text strong>{mockProduct.vendor}</Text>
            </Text>
            <Divider />
            <div style={{ textAlign: 'center' }}>
              <Text type="secondary">Base Price</Text>
              <div>
                <Text strong style={{ fontSize: 24, color: '#8c8c8c' }}>
                  ${mockProduct.basePrice.toFixed(2)}
                </Text>
              </div>
            </div>
          </Card>
        </Col>

        {/* Pricing Calculator */}
        <Col xs={24} lg={16}>
          <Card title="Calculate Your Price">
            <Row gutter={24}>
              <Col xs={24} md={12}>
                <div style={{ marginBottom: 24 }}>
                  <Text strong style={{ display: 'block', marginBottom: 8 }}>
                    Select Quantity:
                  </Text>
                  <InputNumber
                    min={1}
                    max={10000}
                    value={quantity}
                    onChange={handleQuantityChange}
                    size="large"
                    style={{ width: '100%' }}
                    formatter={(value) => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                  />
                  <Text type="secondary" style={{ fontSize: 12, display: 'block', marginTop: 4 }}>
                    Minimum order: 1 unit
                  </Text>
                </div>
              </Col>

              <Col xs={24} md={12}>
                <div
                  style={{
                    background: '#fafafa',
                    padding: 16,
                    borderRadius: 8,
                    border: '2px solid #1890ff',
                  }}
                >
                  <div style={{ marginBottom: 8 }}>
                    <Tag color="blue" style={{ fontSize: 14 }}>
                      {pricing.tier.tierName} Tier
                    </Tag>
                  </div>
                  <Text type="secondary" style={{ display: 'block', fontSize: 12 }}>
                    Your Price Per Unit
                  </Text>
                  <Title level={2} style={{ margin: '8px 0', color: '#ff9900' }}>
                    ${pricing.pricePerUnit.toFixed(2)}
                  </Title>
                  {pricing.savingsPercent > 0 && (
                    <Tag color="green" icon={<PercentageOutlined />}>
                      Save {pricing.savingsPercent}%
                    </Tag>
                  )}
                </div>
              </Col>
            </Row>

            <Divider />

            {/* Statistics */}
            <Row gutter={16} style={{ marginBottom: 24 }}>
              <Col xs={12} sm={6}>
                <Statistic
                  title="Quantity"
                  value={quantity}
                  prefix={<TeamOutlined />}
                  valueStyle={{ color: '#1890ff' }}
                />
              </Col>
              <Col xs={12} sm={6}>
                <Statistic
                  title="Price/Unit"
                  value={pricing.pricePerUnit}
                  prefix="$"
                  precision={2}
                  valueStyle={{ color: '#ff9900' }}
                />
              </Col>
              <Col xs={12} sm={6}>
                <Statistic
                  title="Subtotal"
                  value={pricing.subtotal}
                  prefix="$"
                  precision={2}
                  valueStyle={{ color: '#1890ff' }}
                />
              </Col>
              <Col xs={12} sm={6}>
                <Statistic
                  title="You Save"
                  value={pricing.savings}
                  prefix="$"
                  precision={2}
                  valueStyle={{ color: '#52c41a' }}
                />
              </Col>
            </Row>

            <Button
              type="primary"
              size="large"
              icon={<ThunderboltOutlined />}
              block
              onClick={handleAddToCart}
            >
              Add {quantity.toLocaleString()} {quantity === 1 ? 'Item' : 'Items'} to Cart - $
              {pricing.subtotal.toFixed(2)}
            </Button>

            {pricing.tier.maxQty && (
              <Alert
                message={
                  <div>
                    <TrophyOutlined /> Unlock the next tier!
                  </div>
                }
                description={`Buy ${
                  pricing.tier.maxQty + 1
                } or more to get ${pricingTiers[pricingTiers.indexOf(pricing.tier) + 1]?.discount}% off ($${
                  pricingTiers[pricingTiers.indexOf(pricing.tier) + 1]?.pricePerUnit.toFixed(2)
                }/unit)`}
                type="info"
                showIcon={false}
                style={{ marginTop: 16 }}
              />
            )}
          </Card>

          {/* Pricing Tiers Table */}
          <Card
            title={
              <Space>
                <span>All Pricing Tiers</span>
                <Tooltip title="Discounts automatically applied based on quantity">
                  <InfoCircleOutlined style={{ color: '#8c8c8c' }} />
                </Tooltip>
              </Space>
            }
            style={{ marginTop: 24 }}
          >
            <Table
              columns={columns}
              dataSource={pricingTiers}
              pagination={false}
              rowKey="tierName"
              size="middle"
            />

            <Divider />

            <div style={{ fontSize: 12, color: '#8c8c8c' }}>
              <div style={{ marginBottom: 4 }}>
                 Free shipping on orders over $500
              </div>
              <div style={{ marginBottom: 4 }}>
                 Volume discounts apply automatically at checkout
              </div>
              <div style={{ marginBottom: 4 }}>
                 Enterprise pricing available for orders of 500+ units
              </div>
              <div> Contact us for custom quotes on larger orders</div>
            </div>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default DynamicPricingPage;

customer/profile.__content:
import React, { useState, useEffect } from 'react';
import { Card, Tabs, Form, Input, Button, Switch, Select, Upload, Avatar, Row, Col, Space, Divider, Modal, List, Tag, Badge, Progress, message, Radio, InputNumber, Popconfirm } from 'antd';
import { UserOutlined, MailOutlined, PhoneOutlined, LockOutlined, BellOutlined, SafetyCertificateOutlined, CreditCardOutlined, GiftOutlined, DeleteOutlined, EditOutlined, PlusOutlined, CameraOutlined, HomeOutlined, EnvironmentOutlined, GlobalOutlined, SecurityScanOutlined, EyeInvisibleOutlined, StarOutlined } from '@ant-design/icons';
import { accountSettingsAPI, type UserProfile, type Address, type NotificationPreferences, type PrivacySettings, type SecuritySettings, type PaymentMethod, type LoyaltyInfo } from '../../services/api/accountSettingsAPI';
// import './account-settings.less';

const { TabPane } = Tabs;
const { Option } = Select;
const { TextArea } = Input;
const { Password } = Input;

// Account Settings Component with Complete User Management
const AccountSettings: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [addresses, setAddresses] = useState<Address[]>([]);
  const [notificationPrefs, setNotificationPrefs] = useState<NotificationPreferences | null>(null);
  const [privacySettings, setPrivacySettings] = useState<PrivacySettings | null>(null);
  const [securitySettings, setSecuritySettings] = useState<SecuritySettings | null>(null);
  const [paymentMethods, setPaymentMethods] = useState<PaymentMethod[]>([]);
  const [loyaltyInfo, setLoyaltyInfo] = useState<LoyaltyInfo | null>(null);

  const [activeTab, setActiveTab] = useState('profile');
  const [showAddressModal, setShowAddressModal] = useState(false);
  const [editingAddress, setEditingAddress] = useState<Address | null>(null);
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const [profileForm] = Form.useForm();
  const [addressForm] = Form.useForm();
  const [passwordForm] = Form.useForm();

  // Load all account data
  useEffect(() => {
    loadAccountData();
  }, []);

  const loadAccountData = async () => {
    setLoading(true);
    try {
      const [
        profileResponse,
        addressesResponse,
        notificationResponse,
        privacyResponse,
        securityResponse,
        paymentResponse,
        loyaltyResponse
      ] = await Promise.all([
        accountSettingsAPI.getProfile(),
        accountSettingsAPI.getAddresses(),
        accountSettingsAPI.getNotificationPreferences(),
        accountSettingsAPI.getPrivacySettings(),
        accountSettingsAPI.getSecuritySettings(),
        accountSettingsAPI.getPaymentMethods(),
        accountSettingsAPI.getLoyaltyInfo()
      ]);

      if (profileResponse.success) {
        setProfile(profileResponse.data);
        profileForm.setFieldsValue(profileResponse.data);
      }

      if (addressesResponse.success) {
        setAddresses(addressesResponse.data);
      }

      if (notificationResponse.success) {
        setNotificationPrefs(notificationResponse.data);
      }

      if (privacyResponse.success) {
        setPrivacySettings(privacyResponse.data);
      }

      if (securityResponse.success) {
        setSecuritySettings(securityResponse.data);
      }

      if (paymentResponse.success) {
        setPaymentMethods(paymentResponse.data);
      }

      if (loyaltyResponse.success) {
        setLoyaltyInfo(loyaltyResponse.data);
      }
    } catch (error) {
      console.error('Error loading account data:', error);
      message.error('Failed to load account data');
    } finally {
      setLoading(false);
    }
  };

  const handleProfileUpdate = async (values: any) => {
    try {
      const response = await accountSettingsAPI.updateProfile(values);
      if (response.success) {
        message.success('Profile updated successfully');
        setProfile({ ...profile!, ...values });
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      message.error('Failed to update profile');
    }
  };

  const handleAddressSubmit = async (values: any) => {
    try {
      let response;
      if (editingAddress) {
        response = await accountSettingsAPI.updateAddress(editingAddress.id, values);
      } else {
        response = await accountSettingsAPI.addAddress(values);
      }

      if (response.success) {
        message.success(`Address ${editingAddress ? 'updated' : 'added'} successfully`);
        setShowAddressModal(false);
        setEditingAddress(null);
        addressForm.resetFields();

        // Reload addresses
        const addressesResponse = await accountSettingsAPI.getAddresses();
        if (addressesResponse.success) {
          setAddresses(addressesResponse.data);
        }
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error saving address:', error);
      message.error('Failed to save address');
    }
  };

  const handleDeleteAddress = async (id: string) => {
    try {
      const response = await accountSettingsAPI.deleteAddress(id);
      if (response.success) {
        message.success('Address deleted successfully');
        setAddresses(addresses.filter(addr => addr.id !== id));
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error deleting address:', error);
      message.error('Failed to delete address');
    }
  };

  const handlePasswordChange = async (values: any) => {
    try {
      const response = await accountSettingsAPI.changePassword(
        values.currentPassword,
        values.newPassword
      );

      if (response.success) {
        message.success('Password changed successfully');
        setShowPasswordModal(false);
        passwordForm.resetFields();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error changing password:', error);
      message.error('Failed to change password');
    }
  };

  const handleNotificationUpdate = async (category: string, key: string, value: boolean) => {
    if (!notificationPrefs) return;

    const updatedPrefs = {
      ...notificationPrefs,
      [category]: {
        ...notificationPrefs[category as keyof NotificationPreferences],
        [key]: value
      }
    };

    try {
      const response = await accountSettingsAPI.updateNotificationPreferences(updatedPrefs);
      if (response.success) {
        setNotificationPrefs(updatedPrefs);
        message.success('Notification preferences updated');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error updating notifications:', error);
      message.error('Failed to update notifications');
    }
  };

  const handlePrivacyUpdate = async (key: string, value: any) => {
    if (!privacySettings) return;

    const updatedSettings = {
      ...privacySettings,
      [key]: value
    };

    try {
      const response = await accountSettingsAPI.updatePrivacySettings(updatedSettings);
      if (response.success) {
        setPrivacySettings(updatedSettings);
        message.success('Privacy settings updated');
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error updating privacy:', error);
      message.error('Failed to update privacy settings');
    }
  };

  const renderProfileTab = () => (
    <Card>
      <Row gutter={[24, 24]}>
        <Col xs={24} md={8}>
          <div style={{ textAlign: 'center' }}>
            <Avatar size={120} src={profile?.avatar} icon={<UserOutlined />} />
            <div style={{ marginTop: 16 }}>
              <Upload showUploadList={false}>
                <Button icon={<CameraOutlined />}>Change Photo</Button>
              </Upload>
            </div>
          </div>
        </Col>
        <Col xs={24} md={16}>
          <Form
            form={profileForm}
            layout="vertical"
            onFinish={handleProfileUpdate}
          >
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  label="First Name"
                  name="firstName"
                  rules={[{ required: true, message: 'Please enter your first name' }]}
                >
                  <Input prefix={<UserOutlined />} />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  label="Last Name"
                  name="lastName"
                  rules={[{ required: true, message: 'Please enter your last name' }]}
                >
                  <Input prefix={<UserOutlined />} />
                </Form.Item>
              </Col>
            </Row>

            <Form.Item
              label="Email"
              name="email"
              rules={[
                { required: true, message: 'Please enter your email' },
                { type: 'email', message: 'Please enter a valid email' }
              ]}
            >
              <Input 
                prefix={<MailOutlined />}
                suffix={profile?.isEmailVerified ? <Badge status="success" text="Verified" /> : <Badge status="warning" text="Unverified" />}
              />
            </Form.Item>

            <Form.Item label="Phone" name="phone">
              <Input 
                prefix={<PhoneOutlined />}
                suffix={profile?.isPhoneVerified ? <Badge status="success" text="Verified" /> : <Badge status="warning" text="Unverified" />}
              />
            </Form.Item>

            <Row gutter={16}>
              <Col span={12}>
                <Form.Item label="Date of Birth" name="dateOfBirth">
                  <Input type="date" />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item label="Gender" name="gender">
                  <Select placeholder="Select gender">
                    <Option value="male">Male</Option>
                    <Option value="female">Female</Option>
                    <Option value="other">Other</Option>
                  </Select>
                </Form.Item>
              </Col>
            </Row>

            <Form.Item>
              <Button type="primary" htmlType="submit">
                Update Profile
              </Button>
            </Form.Item>
          </Form>
        </Col>
      </Row>
    </Card>
  );

  const renderAddressesTab = () => (
    <div>
      <div style={{ marginBottom: 16, textAlign: 'right' }}>
        <Button 
          type="primary" 
          icon={<PlusOutlined />}
          onClick={() => {
            setEditingAddress(null);
            addressForm.resetFields();
            setShowAddressModal(true);
          }}
        >
          Add New Address
        </Button>
      </div>

