        <Space>
          <Avatar icon={<UserOutlined />} src={record.avatar} />
          <div>
            <Text strong>{name}</Text>
            <br />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.email}
            </Text>
          </div>
        </Space>
      ),
    },
    {
      title: 'Phone',
      dataIndex: 'phone',
      key: 'phone',
    },
    {
      title: 'Location',
      dataIndex: 'location',
      key: 'location',
    },
    {
      title: 'Total Orders',
      dataIndex: 'totalOrders',
      key: 'totalOrders',
      render: (orders: number) => <Text strong>{orders}</Text>,
      sorter: (a, b) => a.totalOrders - b.totalOrders,
    },
    {
      title: 'Total Spent',
      dataIndex: 'totalSpent',
      key: 'totalSpent',
      render: (spent: number) => (
        <Text strong style={{ color: '#52c41a' }}>
          ${spent.toFixed(2)}
        </Text>
      ),
      sorter: (a, b) => a.totalSpent - b.totalSpent,
    },
    {
      title: 'Join Date',
      dataIndex: 'joinDate',
      key: 'joinDate',
      render: (date: string) => new Date(date).toLocaleDateString(),
      sorter: (a, b) => new Date(a.joinDate).getTime() - new Date(b.joinDate).getTime(),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'VIP', value: 'vip' },
        { text: 'Inactive', value: 'inactive' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Button
          type="link"
          icon={<EyeOutlined />}
          onClick={() => handleViewCustomer(record)}
        >
          View Profile
        </Button>
      ),
    },
  ];

  const totalCustomers = customers.length;
  const vipCustomers = customers.filter((c) => c.status === 'vip').length;
  const totalRevenue = customers.reduce((sum, c) => sum + c.totalSpent, 0);
  const avgOrderValue = totalRevenue / customers.reduce((sum, c) => sum + c.totalOrders, 0);

  return (
    <div style={{ padding: 24 }}>
      <Title level={3}>Customer Management</Title>

      {/* Statistics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Customers"
              value={totalCustomers}
              prefix={<UserOutlined style={{ color: '#1890ff' }} />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="VIP Customers"
              value={vipCustomers}
              prefix={<UserOutlined style={{ color: '#faad14' }} />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Revenue"
              value={totalRevenue}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#52c41a' }} />}
              suffix="USD"
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Avg Order Value"
              value={avgOrderValue}
              precision={2}
              prefix={<ShoppingOutlined style={{ color: '#722ed1' }} />}
              suffix="USD"
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Card>
        {/* Filters */}
        <Row gutter={16} style={{ marginBottom: 16 }}>
          <Col xs={24} sm={12} md={8}>
            <Input
              placeholder="Search customers..."
              prefix={<SearchOutlined />}
              size="large"
            />
          </Col>
        </Row>

        {/* Customers Table */}
        <Table
          columns={columns}
          dataSource={customers}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} customers`,
          }}
        />
      </Card>

      {/* Customer Detail Drawer */}
      <Drawer
        title="Customer Profile"
        placement="right"
        width={600}
        open={drawerVisible}
        onClose={() => setDrawerVisible(false)}
      >
        {selectedCustomer && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <div style={{ textAlign: 'center' }}>
              <Avatar size={80} icon={<UserOutlined />} src={selectedCustomer.avatar} />
              <Title level={4} style={{ marginTop: 16, marginBottom: 0 }}>
                {selectedCustomer.name}
              </Title>
              <Tag color={getStatusColor(selectedCustomer.status)} style={{ marginTop: 8 }}>
                {selectedCustomer.status.toUpperCase()}
              </Tag>
            </div>

            <Descriptions title="Contact Information" bordered column={1} size="small">
              <Descriptions.Item label="Email">{selectedCustomer.email}</Descriptions.Item>
              <Descriptions.Item label="Phone">{selectedCustomer.phone}</Descriptions.Item>
              <Descriptions.Item label="Location">{selectedCustomer.location}</Descriptions.Item>
              <Descriptions.Item label="Member Since">
                {new Date(selectedCustomer.joinDate).toLocaleDateString()}
              </Descriptions.Item>
            </Descriptions>

            <Descriptions title="Purchase Statistics" bordered column={1} size="small">
              <Descriptions.Item label="Total Orders">
                <Text strong style={{ fontSize: 16 }}>
                  {selectedCustomer.totalOrders}
                </Text>
              </Descriptions.Item>
              <Descriptions.Item label="Total Spent">
                <Text strong style={{ fontSize: 16, color: '#52c41a' }}>
                  ${selectedCustomer.totalSpent.toFixed(2)}
                </Text>
              </Descriptions.Item>
              <Descriptions.Item label="Average Order Value">
                <Text strong style={{ fontSize: 16, color: '#722ed1' }}>
                  ${(selectedCustomer.totalSpent / selectedCustomer.totalOrders).toFixed(2)}
                </Text>
              </Descriptions.Item>
            </Descriptions>

            <Card title="Recent Orders" size="small">
              <Text type="secondary">Last 5 orders from this customer would appear here</Text>
            </Card>

            <Card title="Favorite Products" size="small">
              <Text type="secondary">Most purchased products would appear here</Text>
            </Card>
          </Space>
        )}
      </Drawer>
    </div>
  );
};

export default VendorCustomersPage;

vendor/dashboard.__content:
import React, { useEffect, useState } from 'react';
import { Row, Col, Card, Statistic, Table, Tag, Button, List, Avatar, Badge, Progress, Space, Typography, Spin, message } from 'antd';
import {
  DollarOutlined,
  ShoppingOutlined,
  ShoppingCartOutlined,
  RiseOutlined,
  EyeOutlined,
  BellOutlined,
  StarOutlined,
  UserOutlined,
  AlertOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie } from '@ant-design/charts';
import { vendorAPI } from '@/services/api/vendors';
import { ordersAPI } from '@/services/api/orders';
import { productAPI } from '@/services/api/products';

const { Text, Title } = Typography;

const VendorDashboard = () => {
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState<any>(null);
  const [recentOrders, setRecentOrders] = useState<any[]>([]);
  const [products, setProducts] = useState<any[]>([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Fetch vendor stats
      const vendorStats = await vendorAPI.getStats();
      setStats(vendorStats);

      // Fetch recent orders
      const ordersResponse = await ordersAPI.getAll({ limit: 5 });
      setRecentOrders(ordersResponse.data || []);

      // Fetch products
      const productsResponse = await productAPI.getAll({ limit: 100 });
      setProducts(productsResponse.data || []);

    } catch (error: any) {
      message.error(error.message || 'Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{ padding: '80px 0', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  const salesData = stats?.monthlySales || [];
  const activeProducts = products.filter(p => p.isActive).length;
  const outOfStock = products.filter(p => p.stockQuantity === 0).length;
  const totalRevenue = stats?.totalRevenue || 0;
  const totalOrders = stats?.totalOrders || 0;
  const pendingOrders = recentOrders.filter(o => o.status === 'pending').length;
  const averageRating = stats?.averageRating || 4.5;
  const totalReviews = stats?.totalReviews || 0;

  const topProducts = stats?.topProducts || [];

  const productStatusData = [
    { status: 'Active', count: activeProducts },
    { status: 'Out of Stock', count: outOfStock },
    { status: 'Draft', count: products.length - activeProducts - outOfStock },
  ];

  const orderColumns = [
    { title: 'Order #', dataIndex: 'orderNumber', key: 'orderNumber' },
    { title: 'Customer ID', dataIndex: 'customerId', key: 'customerId', ellipsis: true },
    { 
      title: 'Total', 
      dataIndex: 'total', 
      key: 'total', 
      render: (total: number) => `$${total?.toFixed(2) || '0.00'}` 
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => {
        const colors: Record<string, string> = {
          pending: 'blue',
          confirmed: 'cyan',
          processing: 'orange',
          shipped: 'purple',
          delivered: 'green',
          cancelled: 'red',
        };
        return <Tag color={colors[status] || 'default'}>{status?.toUpperCase()}</Tag>;
      },
    },
    { 
      title: 'Date', 
      dataIndex: 'createdAt', 
      key: 'createdAt',
      render: (date: string) => new Date(date).toLocaleDateString()
    },
    {
      title: 'Action',
      key: 'action',
      render: (_: any, record: any) => (
        <Button type="link" icon={<EyeOutlined />} onClick={() => window.location.href = `/vendor/orders/${record.id}`}>
          View
        </Button>
      ),
    },
  ];

  const lineChartConfig = {
    data: salesData,
    xField: 'month',
    yField: 'sales',
    point: { size: 5, shape: 'diamond' },
    smooth: true,
    color: '#1890ff',
  };

  const productStatusConfig = {
    data: productStatusData,
    angleField: 'count',
    colorField: 'status',
    radius: 0.8,
    label: {
      type: 'outer',
      content: '{name} {percentage}',
    },
    interactions: [{ type: 'element-active' }],
  };

  return (
    <div style={{ padding: 24 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <Title level={3}>Vendor Dashboard</Title>
        <Badge count={2} offset={[-5, 5]}>
          <Button icon={<BellOutlined />}>Notifications</Button>
        </Badge>
      </div>

      {/* Performance Metrics */}
      <Row gutter={[16, 16]}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Revenue"
              value={totalRevenue}
              prefix={<DollarOutlined />}
              precision={2}
              valueStyle={{ color: '#3f8600' }}
            />
            <Progress percent={75} size="small" showInfo={false} strokeColor="#3f8600" />
            <Text type="secondary" style={{ fontSize: 12 }}>
              <RiseOutlined style={{ color: '#3f8600' }} /> Growing revenue
            </Text>
          </Card>
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Products"
              value={products.length}
              prefix={<ShoppingOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
            <Progress percent={92} size="small" showInfo={false} />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {activeProducts} active, {outOfStock} out of stock
            </Text>
          </Card>
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Orders"
              value={totalOrders}
              prefix={<ShoppingCartOutlined />}
              valueStyle={{ color: '#cf1322' }}
            />
            <Progress percent={68} size="small" showInfo={false} strokeColor="#cf1322" />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {pendingOrders} pending processing
            </Text>
          </Card>
        </Col>

        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Customer Rating"
              value={averageRating}
              prefix={<StarOutlined />}
              suffix="/ 5"
              valueStyle={{ color: '#faad14' }}
            />
            <Progress percent={94} size="small" showInfo={false} strokeColor="#faad14" />
            <Text type="secondary" style={{ fontSize: 12 }}>
              Based on {totalReviews} reviews
            </Text>
          </Card>
        </Col>
      </Row>

      {/* Charts Section */}
      <Row gutter={[16, 16]} style={{ marginTop: 24 }}>
        <Col xs={24} lg={16}>
          <Card title="Sales Overview (Last 6 Months)" bordered={false}>
            <Line {...lineChartConfig} />
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          <Card title="Product Status" bordered={false}>
            <Pie {...productStatusConfig} />
          </Card>
        </Col>
      </Row>

      {/* Recent Orders */}
      <Row gutter={[16, 16]} style={{ marginTop: 24 }}>
        <Col span={24}>
          <Card 
            title="Recent Orders" 
            bordered={false}
            extra={<Button type="primary" onClick={() => window.location.href = '/vendor/orders'}>View All Orders</Button>}
          >
            <Table
              columns={orderColumns}
              dataSource={recentOrders}
              rowKey="id"
              pagination={false}
            />
          </Card>
        </Col>
      </Row>

      {/* Quick Actions */}
      <Row gutter={[16, 16]} style={{ marginTop: 24 }}>
        <Col xs={24} sm={8}>
          <Card hoverable style={{ textAlign: 'center' }} onClick={() => window.location.href = '/vendor/products/new'}>
            <ShoppingOutlined style={{ fontSize: 48, color: '#1890ff' }} />
            <Title level={4} style={{ marginTop: 16 }}>Add New Product</Title>
            <Button type="primary">Get Started</Button>
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card hoverable style={{ textAlign: 'center' }} onClick={() => window.location.href = '/vendor/orders'}>
            <CheckCircleOutlined style={{ fontSize: 48, color: '#52c41a' }} />
            <Title level={4} style={{ marginTop: 16 }}>Process Orders</Title>
            <Button type="primary">View Pending</Button>
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card hoverable style={{ textAlign: 'center' }} onClick={() => window.location.href = '/vendor/rfq'}>
            <ClockCircleOutlined style={{ fontSize: 48, color: '#faad14' }} />
            <Title level={4} style={{ marginTop: 16 }}>Manage RFQs</Title>
            <Button type="primary">View Requests</Button>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default VendorDashboard;

admin/bulk-data.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Space,
  Button,
  Statistic,
  Progress,
  Select,
  DatePicker,
  Tabs,
  Alert,
  List,
  Avatar,
  message,
  Spin,
  Modal,
  Form,
  Input,
  InputNumber,
  Upload,
  Switch,
  Tooltip,
  Popconfirm,
  Badge,
  Divider,
  Timeline,
  Empty,
  Dropdown,
  Steps,
  Radio,
  Checkbox,
  Tree,
} from 'antd';
import {
  UploadOutlined,
  DownloadOutlined,
  ImportOutlined,
  ExportOutlined,
  FileExcelOutlined,
  FilePdfOutlined,
  FileTextOutlined,
  DatabaseOutlined,
  CloudUploadOutlined,
  CloudDownloadOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  StopOutlined,
  DeleteOutlined,
  EyeOutlined,
  SettingOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ClockCircleOutlined,
  ExclamationCircleOutlined,
  InfoCircleOutlined,
  ReloadOutlined,
  CopyOutlined,
  EditOutlined,
  FolderOpenOutlined,
  TableOutlined,
  BarsOutlined,
  LineChartOutlined,
  PieChartOutlined,
  WarningOutlined,
  SafetyCertificateOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';
import type { UploadProps } from 'antd/es/upload';
import dayjs, { Dayjs } from 'dayjs';
import { 
  bulkDataAPI, 
  ImportJob, 
  ExportJob, 
  BulkOperation, 
  ImportTemplate, 
  ExportTemplate,
  ImportStats,
  ExportStats,
  ImportPreview,
  FieldMapping
} from '../../services/api/bulkData';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { Step } = Steps;

const BulkDataPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [importJobs, setImportJobs] = useState<ImportJob[]>([]);
  const [exportJobs, setExportJobs] = useState<ExportJob[]>([]);
  const [bulkOperations, setBulkOperations] = useState<BulkOperation[]>([]);
  const [importTemplates, setImportTemplates] = useState<ImportTemplate[]>([]);
  const [exportTemplates, setExportTemplates] = useState<ExportTemplate[]>([]);
  const [importStats, setImportStats] = useState<ImportStats | null>(null);
  const [exportStats, setExportStats] = useState<ExportStats | null>(null);

  const [activeTab, setActiveTab] = useState('imports');
  const [selectedStatus, setSelectedStatus] = useState<string>('all');
  const [dateRange, setDateRange] = useState<[Dayjs, Dayjs]>([
    dayjs().subtract(7, 'days'),
    dayjs(),
  ]);

  // Modals
  const [importModalOpen, setImportModalOpen] = useState(false);
  const [exportModalOpen, setExportModalOpen] = useState(false);
  const [bulkOpModalOpen, setBulkOpModalOpen] = useState(false);
  const [previewModalOpen, setPreviewModalOpen] = useState(false);
  const [templateModalOpen, setTemplateModalOpen] = useState(false);

  // Forms and data
  const [importForm] = Form.useForm();
  const [exportForm] = Form.useForm();
  const [bulkOpForm] = Form.useForm();
  const [templateForm] = Form.useForm();
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [importPreview, setImportPreview] = useState<ImportPreview | null>(null);
  const [fieldMapping, setFieldMapping] = useState<FieldMapping[]>([]);
  const [currentStep, setCurrentStep] = useState(0);

  // Load data
  useEffect(() => {
    loadData();
  }, [activeTab, selectedStatus, dateRange]);

  const loadData = async () => {
    setLoading(true);
    try {
      const filters = {
        status: selectedStatus !== 'all' ? (selectedStatus as 'pending' | 'processing' | 'completed' | 'failed' | 'cancelled') : undefined,
        startDate: dateRange[0].format('YYYY-MM-DD'),
        endDate: dateRange[1].format('YYYY-MM-DD'),
      };

      if (activeTab === 'imports') {
        const [jobsResponse, templatesResponse, statsResponse] = await Promise.all([
          bulkDataAPI.getImportJobs(filters, { page: 1, limit: 50 }),
          bulkDataAPI.getImportTemplates(),
          bulkDataAPI.getImportStats({ 
            start: dateRange[0].format('YYYY-MM-DD'), 
            end: dateRange[1].format('YYYY-MM-DD') 
          }),
        ]);
        setImportJobs(jobsResponse.jobs);
        setImportTemplates(templatesResponse);
        setImportStats(statsResponse);
      } else if (activeTab === 'exports') {
        const [jobsResponse, templatesResponse, statsResponse] = await Promise.all([
          bulkDataAPI.getExportJobs(filters, { page: 1, limit: 50 }),
          bulkDataAPI.getExportTemplates(),
          bulkDataAPI.getExportStats({ 
            start: dateRange[0].format('YYYY-MM-DD'), 
            end: dateRange[1].format('YYYY-MM-DD') 
          }),
        ]);
        setExportJobs(jobsResponse.jobs);
        setExportTemplates(templatesResponse);
        setExportStats(statsResponse);
      } else if (activeTab === 'bulk-ops') {
        const opsResponse = await bulkDataAPI.getBulkOperations(filters, { page: 1, limit: 50 });
        setBulkOperations(opsResponse.operations);
      }
    } catch (error) {
      console.error('Error loading data:', error);
      message.error('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = async (file: File) => {
    setSelectedFile(file);
    try {
      const preview = await bulkDataAPI.previewImport(file, {
        hasHeader: true,
        delimiter: ',',
        encoding: 'utf-8',
      });
      setImportPreview(preview);
      setFieldMapping(preview.detectedMapping);
      setPreviewModalOpen(true);
    } catch (error) {
      console.error('Error previewing file:', error);
      message.error('Failed to preview file');
    }
    return false; // Prevent upload
  };

  const handleImportCreate = async (values: any) => {
    if (!selectedFile) {
      message.error('Please select a file to import');
      return;
    }

    try {
      setLoading(true);
      const job = await bulkDataAPI.createImportJob({
        title: values.title,
        type: values.type,
        file: selectedFile,
        settings: {
          hasHeader: values.hasHeader,
          delimiter: values.delimiter || ',',
          encoding: values.encoding || 'utf-8',
          skipEmptyRows: values.skipEmptyRows,
          updateExisting: values.updateExisting,
          createMissing: values.createMissing,
          skipDuplicates: values.skipDuplicates,
          batchSize: values.batchSize || 100,
          mapping: fieldMapping,
          validation: {
            required: [],
            unique: [],
            format: {},
            range: {},
            enum: {},
            custom: {},
          },
          defaultValues: {},
        },
      });

      message.success('Import job created successfully');
      setImportModalOpen(false);
      setPreviewModalOpen(false);
      importForm.resetFields();
      setSelectedFile(null);
      setImportPreview(null);
      setCurrentStep(0);
      loadData();

      // Auto-start the job
      await bulkDataAPI.startImport(job.id);
      message.info('Import job started');
    } catch (error) {
      console.error('Error creating import job:', error);
      message.error('Failed to create import job');
    } finally {
      setLoading(false);
    }
  };

  const handleExportCreate = async (values: any) => {
    try {
      setLoading(true);
      const job = await bulkDataAPI.createExportJob({
        title: values.title,
        type: values.type,
        format: values.format,
        columns: values.columns,
        filters: values.filters || {},
        settings: {
          includeHeaders: values.includeHeaders,
          delimiter: values.delimiter || ',',
          encoding: values.encoding || 'utf-8',
          dateFormat: values.dateFormat || 'YYYY-MM-DD',
          numberFormat: values.numberFormat || '#,##0.00',
          compression: values.compression || 'none',
          maxRecords: values.maxRecords,
        },
      });

      message.success('Export job created successfully');
      setExportModalOpen(false);
      exportForm.resetFields();
      loadData();

      // Auto-start the job
      await bulkDataAPI.startExport(job.id);
      message.info('Export job started');
    } catch (error) {
      console.error('Error creating export job:', error);
      message.error('Failed to create export job');
    } finally {
      setLoading(false);
    }
  };

  const handleJobAction = async (jobId: string, action: string, type: 'import' | 'export') => {
    try {
      setLoading(true);
      if (type === 'import') {
        switch (action) {
          case 'start':
            await bulkDataAPI.startImport(jobId);
            break;
          case 'pause':
            await bulkDataAPI.pauseImport(jobId);
            break;
          case 'resume':
            await bulkDataAPI.resumeImport(jobId);
            break;
          case 'cancel':
            await bulkDataAPI.cancelImport(jobId);
            break;
          case 'delete':
            await bulkDataAPI.deleteImportJob(jobId);
            break;
        }
      } else {
        switch (action) {
          case 'start':
            await bulkDataAPI.startExport(jobId);
            break;
          case 'cancel':
            await bulkDataAPI.cancelExport(jobId);
            break;
          case 'download':
            const blob = await bulkDataAPI.downloadExport(jobId);
            const url = window.URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `export-${jobId}-${dayjs().format('YYYY-MM-DD')}.xlsx`;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            window.URL.revokeObjectURL(url);
            message.success('Export downloaded successfully');
            return;
          case 'delete':
            await bulkDataAPI.deleteExportJob(jobId);
            break;
        }
      }
      message.success(`Job ${action}ed successfully`);
      loadData();
    } catch (error) {
      console.error(`Error ${action}ing job:`, error);
      message.error(`Failed to ${action} job`);
    } finally {
      setLoading(false);
    }
