import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Typography, 
  Steps, 
  Timeline, 
  Alert, 
  Space, 
  Tag, 
  Row, 
  Col,
  Button,
  Modal,
  Form,
  Input,
  Select,
  Upload,
  message,
  Table,
  Badge,
  Spin,
  Empty,
  Tooltip,
  Statistic,
  Progress,
  Descriptions,
  Tabs
} from 'antd';
import {
  SyncOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  WarningOutlined,
  RetweetOutlined,
  PlusOutlined,
  UploadOutlined,
  EyeOutlined,
  FileTextOutlined,
  ShoppingCartOutlined,
  DollarOutlined,
  TruckOutlined,
  QuestionCircleOutlined,
  PhoneOutlined,
  MailOutlined,
  CommentOutlined,
  CalendarOutlined,
  TagsOutlined,
  CameraOutlined,
  ExclamationCircleOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { returnsAPI, contactAPI } from '../services/api/help';
import type { ReturnRequest } from '../services/api/help';

const { Title, Paragraph, Text } = Typography;
const { Step } = Steps;
const { TabPane } = Tabs;
const { TextArea } = Input;
const { Option } = Select;

const ReturnPolicyPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [returnsLoading, setReturnsLoading] = useState(false);
  const [createModalVisible, setCreateModalVisible] = useState(false);
  const [trackModalVisible, setTrackModalVisible] = useState(false);
  const [selectedReturn, setSelectedReturn] = useState<ReturnRequest | null>(null);
  const [form] = Form.useForm();

  // Data states
  const [returns, setReturns] = useState<ReturnRequest[]>([]);
  const [returnPolicy, setReturnPolicy] = useState<any>(null);
  const [returnStats, setReturnStats] = useState({
    totalReturns: 0,
    pendingReturns: 0,
    approvedReturns: 0,
    completedReturns: 0
  });

  // File upload state
  const [fileList, setFileList] = useState<any[]>([]);

  // Load initial data
  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);

      const [policyRes, returnsRes] = await Promise.all([
        returnsAPI.getReturnPolicy(),
        returnsAPI.getReturns({ page: 1, limit: 10 })
      ]);

      setReturnPolicy(policyRes.data || null);
      setReturns(returnsRes.data?.data || []);

      // Calculate stats
      const totalReturns = returnsRes.data?.total || 0;
      const pendingReturns = returnsRes.data?.data?.filter((r: ReturnRequest) => r.status === 'pending').length || 0;
      const approvedReturns = returnsRes.data?.data?.filter((r: ReturnRequest) => r.status === 'approved').length || 0;
      const completedReturns = returnsRes.data?.data?.filter((r: ReturnRequest) => r.status === 'refunded').length || 0;

      setReturnStats({
        totalReturns,
        pendingReturns,
        approvedReturns,
        completedReturns
      });
    } catch (error) {
      console.error('Error loading return data:', error);

      // Set fallback data
      setReturnPolicy({
        timeLimit: 30,
        conditions: [
          'Product received is defective or damaged',
          'Wrong item was shipped',
          'Product does not match description',
          'Changed your mind (within 30 days)',
          'Product arrived late beyond promised date'
        ],
        process: [
          'Request Return',
          'Get Approval',
          'Pack & Ship',
          'Inspection',
          'Refund Processed'
        ],
        excludedItems: [
          'Personalized or custom-made items',
          'Perishable goods (food, flowers, etc.)',
          'Intimate or sanitary goods',
          'Digital products or downloadable software',
          'Items without original packaging or tags',
          'Products damaged due to misuse'
        ],
        refundMethods: [
          'Credit/Debit Card',
          'PayPal',
          'Wallet Credit',
          'Bank Transfer'
        ]
      });

      setReturns([
        {
          id: '1',
          orderNumber: 'ORD-2024-001',
          customerId: 'customer1',
          productDetails: [
            {
              productId: 'prod1',
              productName: 'Premium Wireless Headphones',
              sku: 'WH-001',
              quantity: 1,
              price: 149.99,
              image: 'https://example.com/headphones.jpg'
            }
          ],
          reason: 'Defective product',
          reasonCode: 'defective',
          description: 'Left speaker not working properly',
          status: 'approved',
          returnMethod: 'pickup',
          refundAmount: 149.99,
          refundMethod: 'original-payment',
          images: [],
          notes: 'Quality inspection required',
          timeline: [
            {
              status: 'pending',
              timestamp: new Date('2024-01-15'),
              note: 'Return request submitted',
              updatedBy: 'Customer'
            },
            {
              status: 'approved',
              timestamp: new Date('2024-01-16'),
              note: 'Return approved by support team',
              updatedBy: 'Support Team'
            }
          ],
          createdAt: new Date('2024-01-15'),
          updatedAt: new Date('2024-01-16')
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateReturn = async (values: any) => {
    try {
      setReturnsLoading(true);

      const formData = {
        orderNumber: values.orderNumber,
        productIds: values.productIds,
        reason: values.reason,
        reasonCode: values.reasonCode,
        description: values.description,
        returnMethod: values.returnMethod,
        images: fileList.map(file => file.originFileObj).filter(Boolean)
      };

      await returnsAPI.createReturn(formData);

      message.success('Return request submitted successfully!');
      setCreateModalVisible(false);
      form.resetFields();
      setFileList([]);

      // Reload returns
      loadInitialData();
    } catch (error) {
      console.error('Error creating return:', error);
      message.error('Failed to submit return request');
    } finally {
      setReturnsLoading(false);
    }
  };

  const handleTrackReturn = async (returnId: string) => {
    try {
      const response = await returnsAPI.trackReturn(returnId);
      setSelectedReturn(response.data);
      setTrackModalVisible(true);
    } catch (error) {
      console.error('Error tracking return:', error);
      message.error('Failed to load return details');
    }
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      'pending': 'orange',
      'approved': 'blue',
      'rejected': 'red',
      'in-transit': 'purple',
      'received': 'cyan',
      'refunded': 'green'
    };
    return colors[status] || 'default';
  };

  const getStatusIcon = (status: string) => {
    const icons: Record<string, React.ReactNode> = {
      'pending': <ClockCircleOutlined />,
      'approved': <CheckCircleOutlined />,
      'rejected': <ExclamationCircleOutlined />,
      'in-transit': <TruckOutlined />,
      'received': <CheckCircleOutlined />,
      'refunded': <DollarOutlined />
    };
    return icons[status] || <QuestionCircleOutlined />;
  };

  const returnColumns = [
    {
      title: 'Return ID',
      dataIndex: 'id',
      key: 'id',
      render: (id: string) => <Text strong>#{id}</Text>
    },
    {
      title: 'Order Number',
      dataIndex: 'orderNumber',
      key: 'orderNumber',
      render: (orderNumber: string) => (
        <Button type="link" onClick={() => navigate(`/customer/orders/${orderNumber}`)}>
          {orderNumber}
        </Button>
      )
    },
    {
      title: 'Product',
      dataIndex: 'productDetails',
      key: 'product',
      render: (products: any[]) => (
        <div>
          <Text strong>{products[0]?.productName}</Text>
          <br />
          <Text type="secondary">{products[0]?.sku}</Text>
        </div>
      )
    },
    {
      title: 'Reason',
      dataIndex: 'reason',
      key: 'reason',
      render: (reason: string, record: ReturnRequest) => (
        <div>
          <Text>{reason}</Text>
          <br />
          <Tag color="blue">{record.reasonCode}</Tag>
        </div>
      )
    },
    {
      title: 'Amount',
      dataIndex: 'refundAmount',
      key: 'amount',
      render: (amount: number) => (
        <Text strong>${amount.toFixed(2)}</Text>
      )
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Badge 
          color={getStatusColor(status)} 
          text={status.replace('-', ' ').toUpperCase()}
        />
      )
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: Date) => new Date(date).toLocaleDateString()
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (record: ReturnRequest) => (
        <Space>
          <Button 
            size="small" 
            icon={<EyeOutlined />}
            onClick={() => handleTrackReturn(record.id)}
          >
            Track
          </Button>
          {record.status === 'pending' && (
            <Button 
              size="small" 
              danger
              onClick={() => handleCancelReturn(record.id)}
            >
              Cancel
            </Button>
          )}
        </Space>
      )
    }
  ];

  const handleCancelReturn = async (returnId: string) => {
    try {
      await returnsAPI.cancelReturn(returnId, 'Customer requested cancellation');
      message.success('Return request cancelled');
      loadInitialData();
    } catch (error) {
      console.error('Error cancelling return:', error);
      message.error('Failed to cancel return');
    }
  };

  const beforeUpload = (file: any) => {
    const isImage = file.type.startsWith('image/');
    if (!isImage) {
      message.error('You can only upload image files!');
      return false;
    }
    const isLt5M = file.size / 1024 / 1024 < 5;
    if (!isLt5M) {
      message.error('Image must be smaller than 5MB!');
      return false;
    }
    return false; // Prevent auto upload
  };

  const handleFileChange = ({ fileList: newFileList }: any) => {
    setFileList(newFileList);
  };

  if (loading) {
    return (
      <div style={{ padding: 24, textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: 24 }}>
      <Card>
        <div style={{ textAlign: 'center', marginBottom: 32 }}>
          <SyncOutlined style={{ fontSize: 48, color: '#52c41a', marginBottom: 16 }} />
          <Title level={2} style={{ marginBottom: 8 }}>
            Return & Refund Policy
          </Title>
          <Text type="secondary">Our commitment to your satisfaction</Text>
        </div>

        {/* Stats Overview */}
        <Row gutter={16} style={{ marginBottom: 32 }}>
          <Col xs={24} sm={6}>
            <Statistic 
              title="Total Returns" 
              value={returnStats.totalReturns} 
              prefix={<FileTextOutlined />}
            />
          </Col>
          <Col xs={24} sm={6}>
            <Statistic 
              title="Pending" 
              value={returnStats.pendingReturns} 
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Col>
          <Col xs={24} sm={6}>
            <Statistic 
              title="Approved" 
              value={returnStats.approvedReturns} 
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Col>
          <Col xs={24} sm={6}>
            <Statistic 
              title="Completed" 
              value={returnStats.completedReturns} 
              prefix={<DollarOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Col>
        </Row>

        <Tabs defaultActiveKey="policy">
          <TabPane tab="Return Policy" key="policy">
            {/* Key Highlights */}
            <Alert
              message={`${returnPolicy?.timeLimit || 30}-Day Return Policy`}
              description="We offer hassle-free returns within 30 days of delivery for most products. Your satisfaction is our priority."
              type="success"
              showIcon
              icon={<CheckCircleOutlined />}
              style={{ marginBottom: 32 }}
            />

            {/* Return Eligibility */}
            <Title level={3} style={{ marginTop: 32 }}>
              Return Eligibility
            </Title>
            <Card size="small" style={{ marginBottom: 24 }}>
              <Row gutter={24}>
                <Col xs={24} lg={12}>
                  <div>
                    <Text strong style={{ color: '#52c41a' }}>
                       Eligible for Return:
                    </Text>
                    <ul style={{ marginTop: 8 }}>
                      {(returnPolicy?.conditions || []).map((condition: string, index: number) => (
                        <li key={index}>{condition}</li>
                      ))}
                    </ul>
                  </div>
                </Col>
                <Col xs={24} lg={12}>
                  <div>
                    <Text strong style={{ color: '#ff4d4f' }}>
                       Not Eligible for Return:
                    </Text>
                    <ul style={{ marginTop: 8 }}>
                      {(returnPolicy?.excludedItems || []).map((item: string, index: number) => (
                        <li key={index}>{item}</li>
                      ))}
                    </ul>
                  </div>
                </Col>
              </Row>
            </Card>

            {/* Return Process */}
            <Title level={3}>How to Return an Item</Title>
            <Steps
              direction="vertical"
              current={-1}
              style={{ marginBottom: 32 }}
            >
              <Step
                title="Request Return"
                description="Log into your account and go to 'My Orders'. Select the item you want to return and click 'Return Item'. Provide a reason for the return."
                icon={<ClockCircleOutlined />}
              />
              <Step
                title="Get Approval"
                description="Our team will review your request within 24-48 hours. You'll receive an email with return instructions and a prepaid shipping label (if applicable)."
                icon={<CheckCircleOutlined />}
              />
              <Step
                title="Pack & Ship"
                description="Pack the item securely in its original packaging. Attach the return label and drop it off at the designated shipping location."
                icon={<SyncOutlined />}
              />
              <Step
                title="Inspection"
                description="Once we receive your return, we'll inspect the item to ensure it meets our return criteria. This usually takes 3-5 business days."
                icon={<CheckCircleOutlined />}
              />
              <Step
                title="Refund Processed"
                description="After inspection, your refund will be processed to your original payment method within 5-7 business days."
                icon={<CheckCircleOutlined />}
              />
            </Steps>

            {/* Refund Timeline */}
            <Title level={3}>Refund Timeline</Title>
            <Timeline style={{ marginBottom: 32 }}>
              <Timeline.Item color="blue">
                <Text strong>Day 1-2:</Text> Return request submitted and approved
              </Timeline.Item>
              <Timeline.Item color="green">
                <Text strong>Day 3-7:</Text> Item received at our warehouse
              </Timeline.Item>
              <Timeline.Item color="orange">
                <Text strong>Day 8-10:</Text> Quality inspection completed
              </Timeline.Item>
              <Timeline.Item color="purple">
                <Text strong>Day 11-15:</Text> Refund initiated
              </Timeline.Item>
              <Timeline.Item>
                <Text strong>Day 16-20:</Text> Refund appears in your account (depending on your bank)
              </Timeline.Item>
            </Timeline>

            {/* Refund Methods */}
            <Title level={3}>Refund Methods</Title>
            <Card size="small" style={{ marginBottom: 24 }}>
              <Space direction="vertical" style={{ width: '100%' }}>
                <div>
                  <Tag color="blue">Credit/Debit Card</Tag>
                  <Text> - Refunded to original card within 5-7 business days</Text>
                </div>
                <div>
                  <Tag color="green">PayPal</Tag>
                  <Text> - Refunded to PayPal account within 2-3 business days</Text>
                </div>
                <div>
                  <Tag color="purple">Wallet Credit</Tag>
                  <Text> - Instant credit to your GROOW wallet (optional)</Text>
                </div>
                <div>
                  <Tag color="orange">Bank Transfer</Tag>
                  <Text> - Refunded within 7-10 business days</Text>
                </div>
              </Space>
            </Card>

            {/* Contact Support */}
            <Card style={{ marginTop: 32, background: '#f5f5f5' }}>
              <Title level={4}>
                <QuestionCircleOutlined /> Need Help?
              </Title>
              <Paragraph>
                If you have questions about returns or need assistance, our customer support team is
                here to help:
              </Paragraph>
              <Space direction="vertical">
                <Text><MailOutlined /> Email: returns@groow.com</Text>
                <Text><PhoneOutlined /> Phone: +1 (555) 123-4567</Text>
                <Text><CommentOutlined /> Live Chat: Available 24/7</Text>
              </Space>
            </Card>
          </TabPane>

          <TabPane tab="My Returns" key="returns">
            <div style={{ marginBottom: 16 }}>
              <Space>
                <Button 
                  type="primary" 
                  icon={<PlusOutlined />}
                  onClick={() => setCreateModalVisible(true)}
                >
                  Create Return Request
                </Button>
                <Button 
                  icon={<SyncOutlined />}
                  onClick={loadInitialData}
                >
                  Refresh
                </Button>
              </Space>
            </div>

            {returns.length === 0 ? (
              <Empty description="No return requests found" />
            ) : (
              <Table
                columns={returnColumns}
                dataSource={returns}
                rowKey="id"
                pagination={{
                  total: returnStats.totalReturns,
                  pageSize: 10,
                  showSizeChanger: true,
                  showQuickJumper: true,
                  showTotal: (total, range) => 
                    `${range[0]}-${range[1]} of ${total} returns`
                }}
              />
            )}
          </TabPane>
        </Tabs>

        {/* Create Return Modal */}
        <Modal
          title="Create Return Request"
          open={createModalVisible}
          onCancel={() => {
            setCreateModalVisible(false);
            form.resetFields();
            setFileList([]);
          }}
          footer={null}
          width={600}
        >
          <Form
            form={form}
            layout="vertical"
            onFinish={handleCreateReturn}
          >
            <Form.Item
              name="orderNumber"
              label="Order Number"
              rules={[{ required: true, message: 'Please enter order number' }]}
            >
              <Input placeholder="Enter order number (e.g., ORD-2024-001)" />
            </Form.Item>

            <Form.Item
              name="productIds"
              label="Products to Return"
              rules={[{ required: true, message: 'Please select products' }]}
            >
              <Select
                mode="multiple"
                placeholder="Select products from your order"
                options={[
                  { label: 'Premium Wireless Headphones', value: 'prod1' },
                  { label: 'Bluetooth Speaker', value: 'prod2' },
                  { label: 'Smart Watch', value: 'prod3' }
                ]}
              />
            </Form.Item>

            <Form.Item
              name="reasonCode"
              label="Reason for Return"
              rules={[{ required: true, message: 'Please select a reason' }]}
            >
              <Select placeholder="Select reason">
                <Option value="defective">Defective Product</Option>
                <Option value="wrong-item">Wrong Item Shipped</Option>
                <Option value="not-as-described">Not as Described</Option>
                <Option value="changed-mind">Changed Mind</Option>
                <Option value="damaged">Damaged in Transit</Option>
                <Option value="other">Other</Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="description"
              label="Description"
              rules={[{ required: true, message: 'Please provide details' }]}
            >
              <TextArea
                rows={4}
                placeholder="Please provide detailed description of the issue..."
                maxLength={500}
                showCount
              />
            </Form.Item>

            <Form.Item
              name="returnMethod"
              label="Return Method"
              rules={[{ required: true, message: 'Please select return method' }]}
            >
              <Select placeholder="How would you like to return the item?">
                <Option value="pickup">Pickup from Address</Option>
                <Option value="drop-off">Drop-off at Store</Option>
                <Option value="mail">Mail Return</Option>
              </Select>
            </Form.Item>

            <Form.Item label="Upload Images (Optional)">
              <Upload
                listType="picture-card"
                fileList={fileList}
                onChange={handleFileChange}
                beforeUpload={beforeUpload}
                maxCount={5}
              >
                {fileList.length < 5 && (
                  <div>
                    <CameraOutlined />
                    <div style={{ marginTop: 8 }}>Upload</div>
                  </div>
                )}
              </Upload>
              <Text type="secondary" style={{ fontSize: '12px' }}>
                Upload photos of the product (max 5 images, 5MB each)
              </Text>
            </Form.Item>

            <Form.Item>
              <Space>
                <Button 
                  type="primary" 
                  htmlType="submit" 
                  loading={returnsLoading}
                >
                  Submit Return Request
                </Button>
                <Button onClick={() => setCreateModalVisible(false)}>
                  Cancel
                </Button>
              </Space>
            </Form.Item>
          </Form>
        </Modal>

        {/* Track Return Modal */}
        <Modal
          title="Return Details"
          open={trackModalVisible}
          onCancel={() => setTrackModalVisible(false)}
          footer={[
            <Button key="close" onClick={() => setTrackModalVisible(false)}>
              Close
            </Button>
          ]}
          width={700}
        >
          {selectedReturn && (
            <div>
              <Descriptions bordered size="small" column={2}>
                <Descriptions.Item label="Return ID">#{selectedReturn.id}</Descriptions.Item>
                <Descriptions.Item label="Order Number">{selectedReturn.orderNumber}</Descriptions.Item>
                <Descriptions.Item label="Status">
                  <Badge 
                    color={getStatusColor(selectedReturn.status)} 
                    text={selectedReturn.status.replace('-', ' ').toUpperCase()}
                  />
                </Descriptions.Item>
                <Descriptions.Item label="Refund Amount">
                  ${selectedReturn.refundAmount.toFixed(2)}
                </Descriptions.Item>
                <Descriptions.Item label="Return Method">{selectedReturn.returnMethod}</Descriptions.Item>
                <Descriptions.Item label="Refund Method">{selectedReturn.refundMethod}</Descriptions.Item>
                <Descriptions.Item label="Tracking Number" span={2}>
                  {selectedReturn.trackingNumber || 'Not assigned yet'}
                </Descriptions.Item>
              </Descriptions>

              <Title level={5} style={{ marginTop: 24 }}>Timeline</Title>
              <Timeline>
                {selectedReturn.timeline.map((event, index) => (
                  <Timeline.Item 
                    key={index}
                    dot={getStatusIcon(event.status)}
                    color={getStatusColor(event.status)}
                  >
                    <div>
                      <Text strong>{event.status.replace('-', ' ').toUpperCase()}</Text>
                      <br />
                      <Text type="secondary">{event.note}</Text>
                      <br />
                      <Text type="secondary" style={{ fontSize: '12px' }}>
                        {event.timestamp.toLocaleString()} - {event.updatedBy}
                      </Text>
                    </div>
                  </Timeline.Item>
                ))}
              </Timeline>
            </div>
          )}
        </Modal>
      </Card>
    </div>
  );
};

export default ReturnPolicyPage;

admin/vendors.__content:
import { ProTable } from '@ant-design/pro-components';
import type { ProColumns } from '@ant-design/pro-components';
import { Button, Space, Tag, Modal, Descriptions, Avatar, message } from 'antd';
import { EyeOutlined, CheckCircleOutlined, CloseCircleOutlined, StopOutlined } from '@ant-design/icons';
import { useState } from 'react';

interface VendorType {
  id: string;
  businessName: string;
  contactPerson: string;
  email: string;
  phone: string;
  status: 'pending' | 'verified' | 'rejected' | 'suspended';
  subscription: string;
  productsCount: number;
  ordersCount: number;
  revenue: number;
  joinedAt: string;
}

const AdminVendors = () => {
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [selectedVendor, setSelectedVendor] = useState<VendorType | null>(null);

  const statusColors = {
    pending: 'warning',
    verified: 'success',
    rejected: 'error',
    suspended: 'default',
  };

  const columns: ProColumns<VendorType>[] = [
    {
      title: 'Business Name',
      dataIndex: 'businessName',
      key: 'businessName',
      width: 200,
    },
    {
      title: 'Contact Person',
      dataIndex: 'contactPerson',
      key: 'contactPerson',
      width: 150,
    },
    {
      title: 'Email',
      dataIndex: 'email',
      key: 'email',
      width: 200,
      copyable: true,
    },
    {
      title: 'Phone',
      dataIndex: 'phone',
      key: 'phone',
      width: 130,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 120,
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Verified', value: 'verified' },
        { text: 'Rejected', value: 'rejected' },
        { text: 'Suspended', value: 'suspended' },
      ],
      render: (status) => (
        <Tag color={statusColors[status as keyof typeof statusColors]}>
          {status.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Subscription',
      dataIndex: 'subscription',
      key: 'subscription',
      width: 120,
    },
    {
      title: 'Products',
      dataIndex: 'productsCount',
      key: 'productsCount',
      width: 100,
      align: 'center',
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      width: 120,
      render: (revenue) => `$${revenue.toFixed(2)}`,
      sorter: true,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 250,
      render: (_, record) => (
        <Space size="small">
          <Button type="link" icon={<EyeOutlined />} onClick={() => {
            setSelectedVendor(record);
            setDrawerVisible(true);
          }}>
            View
          </Button>
          {record.status === 'pending' && (
            <>
              <Button type="link" icon={<CheckCircleOutlined />} onClick={() => handleApprove(record.id)}>
                Approve
              </Button>
              <Button type="link" danger icon={<CloseCircleOutlined />} onClick={() => handleReject(record.id)}>
                Reject
              </Button>
            </>
          )}
          {record.status === 'verified' && (
            <Button type="link" danger icon={<StopOutlined />} onClick={() => handleSuspend(record.id)}>
              Suspend
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const mockData: VendorType[] = [
    {
      id: '1',
      businessName: 'Tech Solutions Ltd',
      contactPerson: 'John Smith',
      email: 'john@techsolutions.com',
      phone: '+1234567890',
      status: 'pending',
      subscription: 'Premium',
      productsCount: 45,
      ordersCount: 120,
      revenue: 45678.90,
      joinedAt: '2024-11-01',
    },
    {
      id: '2',
      businessName: 'Fashion Hub Inc',
      contactPerson: 'Jane Doe',
      email: 'jane@fashionhub.com',
      phone: '+1234567891',
      status: 'verified',
      subscription: 'Basic',
      productsCount: 89,
      ordersCount: 340,
      revenue: 89456.50,
      joinedAt: '2024-10-15',
    },
  ];

  const handleApprove = (id: string) => {
    Modal.confirm({
      title: 'Approve Vendor',
      content: 'Are you sure you want to approve this vendor?',
      onOk: () => message.success('Vendor approved successfully'),
    });
  };

  const handleReject = (id: string) => {
    Modal.confirm({
      title: 'Reject Vendor',
      content: 'Are you sure you want to reject this vendor?',
      onOk: () => message.success('Vendor rejected'),
    });
  };

  const handleSuspend = (id: string) => {
    Modal.confirm({
      title: 'Suspend Vendor',
      content: 'Are you sure you want to suspend this vendor?',
      onOk: () => message.success('Vendor suspended'),
    });
  };

  return (
    <div>
      <ProTable<VendorType>
        columns={columns}
        dataSource={mockData}
        rowKey="id"
        pagination={{
          defaultPageSize: 20,
          showSizeChanger: true,
        }}
        search={{
          labelWidth: 'auto',
        }}
        headerTitle="Vendor Management"
        scroll={{ x: 1400 }}
      />

      <Modal
        title="Vendor Details"
        open={drawerVisible}
        onCancel={() => setDrawerVisible(false)}
        width={800}
        footer={null}
      >
        {selectedVendor && (
          <Descriptions bordered column={2}>
            <Descriptions.Item label="Business Name">{selectedVendor.businessName}</Descriptions.Item>
            <Descriptions.Item label="Contact Person">{selectedVendor.contactPerson}</Descriptions.Item>
            <Descriptions.Item label="Email">{selectedVendor.email}</Descriptions.Item>
            <Descriptions.Item label="Phone">{selectedVendor.phone}</Descriptions.Item>
            <Descriptions.Item label="Status">
              <Tag color={statusColors[selectedVendor.status]}>
                {selectedVendor.status.toUpperCase()}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item label="Subscription">{selectedVendor.subscription}</Descriptions.Item>
            <Descriptions.Item label="Products">{selectedVendor.productsCount}</Descriptions.Item>
            <Descriptions.Item label="Orders">{selectedVendor.ordersCount}</Descriptions.Item>
            <Descriptions.Item label="Revenue">${selectedVendor.revenue.toFixed(2)}</Descriptions.Item>
            <Descriptions.Item label="Joined">{selectedVendor.joinedAt}</Descriptions.Item>
          </Descriptions>
        )}
      </Modal>
    </div>
  );
};

export default AdminVendors;

customer/chat.__content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, Button, Table, Tag, Space, Modal, Form, Input, Select, Upload, message, Badge, Row, Col, Timeline, Avatar, Rate, Divider, Spin, Empty, Tabs, Statistic, Progress, Typography, List, Tooltip, FloatButton } from 'antd';
import { PlusOutlined, MessageOutlined, SearchOutlined, FilterOutlined, FileTextOutlined, ClockCircleOutlined, CheckCircleOutlined, ExclamationCircleOutlined, SendOutlined, PaperClipOutlined, CustomerServiceOutlined, StarOutlined, ReloadOutlined, QuestionCircleOutlined } from '@ant-design/icons';
import { supportTicketsAPI, type SupportTicket, type TicketMessage, type CreateTicketRequest, type TicketStats } from '../../services/api/supportTicketsAPI';
import './support-tickets.less';

const { TextArea } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Title, Text } = Typography;

// Support Tickets System with Live Chat Integration
const SupportTicketsSystem: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [tickets, setTickets] = useState<SupportTicket[]>([]);
  const [stats, setStats] = useState<TicketStats | null>(null);
  const [selectedTicket, setSelectedTicket] = useState<SupportTicket | null>(null);
  const [messages, setMessages] = useState<TicketMessage[]>([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showChatModal, setShowChatModal] = useState(false);
  const [showTicketDetails, setShowTicketDetails] = useState(false);
  const [messagesLoading, setMessagesLoading] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);
  const [chatAvailable, setChatAvailable] = useState(false);
  const [newMessage, setNewMessage] = useState('');
  const [filters, setFilters] = useState({
    status: undefined,
    priority: undefined,
    category: undefined,
    search: ''
  });

  const [createForm] = Form.useForm();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<any>(null);

  // Load initial data
  useEffect(() => {
    loadTickets();
    loadStats();
    checkChatAvailability();
  }, []);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const loadTickets = async () => {
    setLoading(true);
    try {
      const response = await supportTicketsAPI.getTickets(filters);
      if (response.success) {
        setTickets(response.data.tickets);
      } else {
        message.error('Failed to load tickets');
      }
    } catch (error) {
      console.error('Error loading tickets:', error);
      message.error('Failed to load tickets');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const response = await supportTicketsAPI.getTicketStats();
      if (response.success) {
        setStats(response.data);
      }
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const checkChatAvailability = async () => {
    try {
      const response = await supportTicketsAPI.getChatAvailability();
      if (response.success) {
        setChatAvailable(response.data.available);
      }
    } catch (error) {
      console.error('Error checking chat availability:', error);
    }
  };

  const loadTicketMessages = async (ticketId: string) => {
    setMessagesLoading(true);
    try {
      const response = await supportTicketsAPI.getTicketMessages(ticketId);
      if (response.success) {
        setMessages(response.data);
      } else {
        message.error('Failed to load messages');
      }
    } catch (error) {
      console.error('Error loading messages:', error);
      message.error('Failed to load messages');
    } finally {
      setMessagesLoading(false);
    }
  };

  const handleCreateTicket = async (values: any) => {
    try {
      const ticketData: CreateTicketRequest = {
        subject: values.subject,
        description: values.description,
        category: values.category,
        priority: values.priority,
        orderId: values.orderId
      };

      const response = await supportTicketsAPI.createTicket(ticketData);
      if (response.success) {
        message.success('Support ticket created successfully');
        setShowCreateModal(false);
        createForm.resetFields();
        loadTickets();
        loadStats();
      } else {
        message.error(response.message);
      }
    } catch (error) {
      console.error('Error creating ticket:', error);
      message.error('Failed to create ticket');
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim() || !selectedTicket) return;

    setSendingMessage(true);
    try {
      const response = await supportTicketsAPI.sendMessage(selectedTicket.id, newMessage);
      if (response.success) {
        setMessages([...messages, response.data]);
        setNewMessage('');
        loadTickets(); // Refresh tickets to update last activity
      } else {
        message.error('Failed to send message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      message.error('Failed to send message');
    } finally {
      setSendingMessage(false);
    }
  };

  const handleTicketSelect = async (ticket: SupportTicket) => {
    setSelectedTicket(ticket);
    setShowTicketDetails(true);
    await loadTicketMessages(ticket.id);
  };

  const handleSubmitRating = async (ticketId: string, rating: number, feedback?: string) => {
    try {
      const response = await supportTicketsAPI.submitRating(ticketId, rating, feedback);
      if (response.success) {
        message.success('Thank you for your feedback!');
        loadTickets();
      } else {
        message.error('Failed to submit rating');
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      message.error('Failed to submit rating');
    }
  };

  const getStatusColor = (status: SupportTicket['status']) => {
    const colors = {
      open: 'blue',
      in_progress: 'orange',
      waiting_for_customer: 'purple',
      resolved: 'green',
      closed: 'default'
    };
    return colors[status];
  };

  const getPriorityColor = (priority: SupportTicket['priority']) => {
    const colors = {
      low: 'default',
      medium: 'blue',
      high: 'orange',
      urgent: 'red'
    };
    return colors[priority];
  };

  const ticketColumns = [
    {
      title: 'Ticket #',
      dataIndex: 'ticketNumber',
      key: 'ticketNumber',
      render: (text: string, record: SupportTicket) => (
        <Button type="link" onClick={() => handleTicketSelect(record)}>
          {text}
        </Button>
      )
    },
    {
      title: 'Subject',
      dataIndex: 'subject',
      key: 'subject',
      ellipsis: true
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: string) => (
        <Tag>{category.replace('_', ' ').toUpperCase()}</Tag>
      )
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: SupportTicket['status']) => (
        <Tag color={getStatusColor(status)}>
          {status.replace('_', ' ').toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Priority',
      dataIndex: 'priority',
      key: 'priority',
      render: (priority: SupportTicket['priority']) => (
        <Tag color={getPriorityColor(priority)}>
          {priority.toUpperCase()}
        </Tag>
      )
    },
    {
      title: 'Agent',
      dataIndex: 'assignedAgent',
      key: 'assignedAgent',
      render: (agent: SupportTicket['assignedAgent']) => (
        agent ? (
          <Space>
            <Avatar size="small" src={agent.avatar} icon={<CustomerServiceOutlined />} />
            <span>{agent.name}</span>
            <Badge status={agent.isOnline ? 'success' : 'default'} />
          </Space>
        ) : (
          <Text type="secondary">Unassigned</Text>
        )
      )
    },
    {
      title: 'Last Updated',
      dataIndex: 'updatedAt',
      key: 'updatedAt',
      render: (date: string) => (
        <Tooltip title={new Date(date).toLocaleString()}>
          {new Date(date).toLocaleDateString()}
        </Tooltip>
      )
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_: any, record: SupportTicket) => (
        <Space>
          <Button 
            size="small" 
            icon={<MessageOutlined />}
            onClick={() => handleTicketSelect(record)}
          >
            View
          </Button>
          {record.status === 'resolved' && !record.satisfactionRating && (
            <Button 
              size="small" 
              icon={<StarOutlined />}
              onClick={() => {
                Modal.confirm({
                  title: 'Rate this support experience',
                  content: (
                    <div>
                      <p>How satisfied are you with the resolution?</p>
                      <Rate onChange={(value) => handleSubmitRating(record.id, value)} />
                    </div>
                  ),
                  okText: 'Submit Rating'
                });
              }}
            >
              Rate
            </Button>
          )}
        </Space>
      )
    }
  ];

  const renderTicketStats = () => (
    <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Total Tickets"
            value={stats?.total || 0}
            prefix={<FileTextOutlined />}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Open/In Progress"
            value={(stats?.open || 0) + (stats?.inProgress || 0)}
            prefix={<ClockCircleOutlined />}
            valueStyle={{ color: '#faad14' }}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Avg Response Time"
            value={stats?.avgResponseTime || 0}
            suffix="hrs"
            prefix={<ClockCircleOutlined />}
            precision={1}
          />
        </Card>
      </Col>
      <Col xs={12} sm={6}>
        <Card size="small">
          <Statistic
            title="Satisfaction Score"
            value={stats?.satisfactionScore || 0}
            prefix={<StarOutlined />}
            precision={1}
            suffix="/5"
            valueStyle={{ color: '#52c41a' }}
          />
        </Card>
      </Col>
    </Row>
  );

  const renderMessageBubble = (msg: TicketMessage) => {
    const isCustomer = msg.sender.type === 'customer';
    return (
      <div
        key={msg.id}
        style={{
          display: 'flex',
          justifyContent: isCustomer ? 'flex-end' : 'flex-start',
          marginBottom: 16
        }}
      >
        <div style={{ maxWidth: '70%' }}>
          {!isCustomer && (
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
              <Avatar size="small" src={msg.sender.avatar} icon={<CustomerServiceOutlined />} />
              <Text strong style={{ marginLeft: 8 }}>{msg.sender.name}</Text>
              {msg.sender.type === 'agent' && (
                <Tag style={{ marginLeft: 4 }}>Agent</Tag>
              )}
            </div>
          )}
          <div
            style={{
              backgroundColor: isCustomer ? '#1890ff' : '#f0f0f0',
              color: isCustomer ? 'white' : 'black',
              padding: '8px 12px',
              borderRadius: '8px',
              wordBreak: 'break-word'
            }}
          >
            {msg.message}
          </div>
          <div style={{ fontSize: '11px', color: '#999', marginTop: 4, textAlign: isCustomer ? 'right' : 'left' }}>
            {new Date(msg.sentAt).toLocaleString()}
          </div>
        </div>
      </div>
    );
  };

  const renderTicketDetails = () => (
    <Modal
      title={
        <Space>
          <FileTextOutlined />
          {selectedTicket?.ticketNumber} - {selectedTicket?.subject}
        </Space>
      }
      open={showTicketDetails}
      onCancel={() => {
        setShowTicketDetails(false);
        setSelectedTicket(null);
        setMessages([]);
      }}
      width={800}
      footer={null}
    >
      {selectedTicket && (
        <div>
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={8}>
              <Text strong>Status: </Text>
              <Tag color={getStatusColor(selectedTicket.status)}>
                {selectedTicket.status.replace('_', ' ').toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Priority: </Text>
              <Tag color={getPriorityColor(selectedTicket.priority)}>
                {selectedTicket.priority.toUpperCase()}
              </Tag>
            </Col>
            <Col span={8}>
              <Text strong>Category: </Text>
              <Tag>{selectedTicket.category.toUpperCase()}</Tag>
            </Col>
          </Row>

          {selectedTicket.assignedAgent && (
            <Card size="small" style={{ marginBottom: 16 }}>
              <Space>
                <Avatar src={selectedTicket.assignedAgent.avatar} icon={<CustomerServiceOutlined />} />
                <div>
                  <Text strong>{selectedTicket.assignedAgent.name}</Text>
                  <br />
                  <Text type="secondary">{selectedTicket.assignedAgent.role}</Text>
                  <Badge 
                    status={selectedTicket.assignedAgent.isOnline ? 'success' : 'default'} 
                    text={selectedTicket.assignedAgent.isOnline ? 'Online' : 'Offline'}
                    style={{ marginLeft: 8 }}
                  />
                </div>
              </Space>
            </Card>
          )}

          <Divider>Conversation</Divider>

          <div style={{ maxHeight: 400, overflowY: 'auto', padding: '0 8px', marginBottom: 16 }}>
            {messagesLoading ? (
              <div style={{ textAlign: 'center', padding: 20 }}>
                <Spin />
              </div>
            ) : messages.length > 0 ? (
              <>
                {messages.map(renderMessageBubble)}
                <div ref={messagesEndRef} />
              </>
            ) : (
              <Empty description="No messages yet" />
            )}
          </div>

          {selectedTicket.status !== 'closed' && selectedTicket.status !== 'resolved' && (
            <div style={{ display: 'flex', gap: 8 }}>
              <Input.TextArea
                ref={messageInputRef}
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                autoSize={{ minRows: 2, maxRows: 4 }}
                onPressEnter={(e) => {
                  if (e.shiftKey) return;
                  e.preventDefault();
                  handleSendMessage();
                }}
              />
              <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
                <Button
                  type="primary"
                  icon={<SendOutlined />}
                  onClick={handleSendMessage}
                  loading={sendingMessage}
                  disabled={!newMessage.trim()}
                >
                  Send
                </Button>
                <Button
                  size="small"
                  icon={<PaperClipOutlined />}
                  title="Attach File"
                >
                  Attach
                </Button>
              </div>
            </div>
          )}

          {selectedTicket.satisfactionRating && (
            <Card size="small" style={{ marginTop: 16, backgroundColor: '#f6ffed' }}>
              <Text strong>Customer Rating: </Text>
              <Rate disabled value={selectedTicket.satisfactionRating.rating} />
              {selectedTicket.satisfactionRating.feedback && (
                <div style={{ marginTop: 8 }}>
                  <Text>"{selectedTicket.satisfactionRating.feedback}"</Text>
                </div>
              )}
            </Card>
          )}
        </div>
      )}
    </Modal>
  );

  const renderCreateTicketModal = () => (
    <Modal
      title="Create New Support Ticket"
      open={showCreateModal}
      onCancel={() => {
        setShowCreateModal(false);
        createForm.resetFields();
      }}
      footer={null}
      width={600}
    >
      <Form
        form={createForm}
        layout="vertical"
        onFinish={handleCreateTicket}
      >
        <Form.Item
          label="Subject"
          name="subject"
          rules={[{ required: true, message: 'Please enter a subject' }]}
        >
          <Input placeholder="Brief description of your issue" />
        </Form.Item>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Category"
              name="category"
              rules={[{ required: true, message: 'Please select a category' }]}
            >
              <Select placeholder="Select category">
                <Option value="general">General Inquiry</Option>
                <Option value="order">Order Issue</Option>
                <Option value="payment">Payment Problem</Option>
                <Option value="technical">Technical Support</Option>
                <Option value="product">Product Question</Option>
                <Option value="account">Account Issue</Option>
                <Option value="shipping">Shipping Problem</Option>
                <Option value="returns">Returns & Refunds</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Priority"
              name="priority"
              rules={[{ required: true, message: 'Please select priority' }]}
            >
              <Select placeholder="Select priority">
                <Option value="low">Low</Option>
                <Option value="medium">Medium</Option>
                <Option value="high">High</Option>
                <Option value="urgent">Urgent</Option>
              </Select>
            </Form.Item>
          </Col>
        </Row>

        <Form.Item
          label="Order ID (optional)"
          name="orderId"
        >
          <Input placeholder="Enter order number if related to an order" />
        </Form.Item>

        <Form.Item
          label="Description"
          name="description"
          rules={[{ required: true, message: 'Please describe your issue' }]}
        >
          <TextArea
            rows={4}
            placeholder="Please provide detailed information about your issue..."
          />
        </Form.Item>

        <Form.Item
          label="Attachments"
          name="attachments"
        >
          <Upload
            multiple
            beforeUpload={() => false}
            fileList={[]}
          >
            <Button icon={<PaperClipOutlined />}>
              Attach Files (Screenshots, Documents)
            </Button>
          </Upload>
        </Form.Item>

        <Form.Item style={{ marginBottom: 0, textAlign: 'right' }}>
          <Space>
            <Button onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button type="primary" htmlType="submit">
              Create Ticket
            </Button>
          </Space>
        </Form.Item>
      </Form>
    </Modal>
  );

  const renderQuickActions = () => (
    <Card size="small" style={{ marginBottom: 16 }}>
      <Space wrap>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={() => setShowCreateModal(true)}
        >
          New Ticket
        </Button>
        <Button
          icon={<MessageOutlined />}
          onClick={() => setShowChatModal(true)}
          disabled={!chatAvailable}
        >
          Live Chat {chatAvailable && <Badge status="success" />}
        </Button>
        <Button
          icon={<ReloadOutlined />}
          onClick={loadTickets}
          loading={loading}
        >
          Refresh
        </Button>
        <Button
          icon={<QuestionCircleOutlined />}
          onClick={() => window.open('/help-center', '_blank')}
        >
          Help Center
        </Button>
      </Space>
    </Card>
  );

  return (
    <div className="support-tickets" style={{ padding: '24px', maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <CustomerServiceOutlined /> Support Center
        </Title>
        <Text type="secondary">
          Get help with your orders, technical issues, and general inquiries
        </Text>
      </div>

      {/* Stats */}
      {renderTicketStats()}

      {/* Quick Actions */}
      {renderQuickActions()}

      {/* Tickets Table */}
      <Card title="Your Support Tickets">
        <div style={{ marginBottom: 16 }}>
          <Row gutter={16}>
            <Col flex={1}>
              <Input
                placeholder="Search tickets..."
                prefix={<SearchOutlined />}
                value={filters.search}
                onChange={(e) => setFilters({ ...filters, search: e.target.value })}
                onPressEnter={loadTickets}
              />
            </Col>
            <Col>
              <Space>
                <Select
                  placeholder="Status"
                  allowClear
                  style={{ width: 120 }}
                  value={filters.status}
                  onChange={(value) => setFilters({ ...filters, status: value })}
                >
                  <Option value="open">Open</Option>
                  <Option value="in_progress">In Progress</Option>
                  <Option value="waiting_for_customer">Waiting</Option>
                  <Option value="resolved">Resolved</Option>
                  <Option value="closed">Closed</Option>
                </Select>
                <Select
                  placeholder="Priority"
                  allowClear
                  style={{ width: 100 }}
                  value={filters.priority}
                  onChange={(value) => setFilters({ ...filters, priority: value })}
                >
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="urgent">Urgent</Option>
                </Select>
                <Button icon={<FilterOutlined />} onClick={loadTickets}>
                  Filter
                </Button>
              </Space>
            </Col>
          </Row>
        </div>

        <Table
          columns={ticketColumns}
          dataSource={tickets}
          rowKey="id"
          loading={loading}
          pagination={{
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total) => `Total ${total} tickets`
          }}
        />
      </Card>

      {/* Modals */}
      {renderCreateTicketModal()}
      {renderTicketDetails()}

      {/* Live Chat Float Button */}
      {chatAvailable && (
        <FloatButton
          icon={<MessageOutlined />}
          type="primary"
          onClick={() => setShowChatModal(true)}
          tooltip="Start Live Chat"
        />
      )}

      {/* Chat Modal */}
      <Modal
        title="Live Chat Support"
        open={showChatModal}
        onCancel={() => setShowChatModal(false)}
        footer={null}
        width={500}
      >
        <div style={{ textAlign: 'center', padding: '20px 0' }}>
          <CustomerServiceOutlined style={{ fontSize: 48, color: '#1890ff', marginBottom: 16 }} />
          <Title level={4}>Connect with Support</Title>
          <Text>
            Our support agents are ready to help you with any questions or issues.
          </Text>
          <div style={{ margin: '20px 0' }}>
            <Button type="primary" size="large">
              Start Chat Session
            </Button>
          </div>
          <Text type="secondary">
            Average response time: 2 minutes
          </Text>
        </div>
      </Modal>
    </div>
  );
};

export default SupportTicketsSystem;

products/[id].__content:
import React, { useState, useEffect } from 'react';
import {
  Row,
  Col,
  Image,
  Typography,
  Rate,
  Button,
  InputNumber,
  Space,
  Tag,
  Tabs,
  Card,
  Avatar,
  Progress,
  Divider,
  Breadcrumb,
  message,
  Badge,
  Spin,
  Alert,
} from 'antd';
import {
  ShoppingCartOutlined,
  HeartOutlined,
  HeartFilled,
  ShareAltOutlined,
  SafetyOutlined,
  ShopOutlined,
  CheckCircleOutlined,
  StarFilled,
  LoadingOutlined,
} from '@ant-design/icons';
import { history, useParams } from 'umi';
import { motion } from 'framer-motion';
import { productAPI, productReviewAPI, wishlistAPI, type Product, type ProductReview } from '@/services/api/productAPI';
import { useCartStore } from '@/store/cartStore';
import './[id].less';

const { Title, Text, Paragraph } = Typography;

const ProductDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [reviews, setReviews] = useState<ProductReview[]>([]);
  const [loading, setLoading] = useState(true);
  const [reviewsLoading, setReviewsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [selectedVariant, setSelectedVariant] = useState<any>(null);
  const [ratingBreakdown, setRatingBreakdown] = useState<any>(null);
  const [relatedProducts, setRelatedProducts] = useState<any[]>([]);

  const addToCart = useCartStore((state) => state.addItem);

  // Fetch product data
  useEffect(() => {
    const fetchProduct = async () => {
      if (!id) return;

      try {
        setLoading(true);
        setError(null);

        // Fetch product details
        const productData = await productAPI.getProduct(id);
        setProduct(productData);
        setSelectedVariant(productData.variants?.[0] || null);

        // Check if product is in wishlist
        try {
          const wishlistStatus = await wishlistAPI.isInWishlist(productData.id);
          setIsWishlisted(wishlistStatus.inWishlist);
        } catch (wishlistError) {
          console.log('Could not check wishlist status:', wishlistError);
        }

        // Fetch related products
        try {
          const related = await productAPI.getRelatedProducts(productData.id);
          setRelatedProducts(related);
        } catch (relatedError) {
          console.log('Could not fetch related products:', relatedError);
        }

      } catch (error: any) {
        console.error('Error fetching product:', error);
        setError(error.message || 'Failed to load product details');
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  // Fetch reviews
  useEffect(() => {
    const fetchReviews = async () => {
      if (!product) return;

      try {
        setReviewsLoading(true);
        const reviewData = await productReviewAPI.getProductReviews(product.id);
        setReviews(reviewData.reviews);
        setRatingBreakdown(reviewData.ratingBreakdown);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      } finally {
        setReviewsLoading(false);
      }
    };

    fetchReviews();
  }, [product]);

  const handleAddToCart = async () => {
    if (!product) return;

    try {
      // Add to cart using store
      await addToCart({
        productId: product.id.toString(),
        quantity,
      });

      message.success('Added to cart successfully!');
    } catch (error) {
      console.error('Error adding to cart:', error);
      message.error('Failed to add to cart');
    }
  };

  const handleBuyNow = () => {
    handleAddToCart();
    setTimeout(() => {
      history.push('/checkout');
    }, 500);
  };

  const handleWishlist = async () => {
    if (!product) return;

    try {
      if (isWishlisted) {
        await wishlistAPI.removeFromWishlist(product.id);
        setIsWishlisted(false);
        message.success('Removed from wishlist');
      } else {
        await wishlistAPI.addToWishlist(product.id);
        setIsWishlisted(true);
        message.success('Added to wishlist');
      }
    } catch (error) {
      console.error('Error updating wishlist:', error);
      message.error('Failed to update wishlist');
    }
  };

  const handleShare = () => {
    if (navigator.share && product) {
      navigator.share({
        title: product.name,
        text: `Check out this product: ${product.name}`,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      message.success('Product link copied to clipboard!');
    }
  };

  const calculateRatingPercentage = (star: number) => {
    if (!ratingBreakdown) return '0';
    const total = Object.values(ratingBreakdown).reduce((a: number, b: any) => a + (Number(b) || 0), 0);
    const count = ratingBreakdown[star] || 0;
    return total > 0 ? ((count / total) * 100).toFixed(0) : '0';
  };

  // Loading state
  if (loading) {
    return (
      <div style={{ textAlign: 'center', padding: '100px 0' }}>
        <Spin size="large" indicator={<LoadingOutlined style={{ fontSize: 48 }} spin />} />
        <div style={{ marginTop: 16 }}>Loading product details...</div>
      </div>
    );
  }

  // Error state
  if (error || !product) {
    return (
      <div style={{ padding: '50px 0' }}>
        <Alert
          message="Product Not Found"
          description={error || "The product you're looking for doesn't exist or has been removed."}
          type="error"
          showIcon
          action={
            <Button type="primary" onClick={() => history.push('/products')}>
              Browse Products
            </Button>
          }
        />
      </div>
    );
  }

  return (
    <div className="product-detail-page">
      {/* Breadcrumb */}
      <Breadcrumb style={{ marginBottom: 24 }}>
        <Breadcrumb.Item>
          <a onClick={() => history.push('/')}>Home</a>
        </Breadcrumb.Item>
        <Breadcrumb.Item>
          <a onClick={() => history.push('/products')}>{product.category}</a>
        </Breadcrumb.Item>
        {product.subcategory && (
          <Breadcrumb.Item>{product.subcategory}</Breadcrumb.Item>
        )}
        <Breadcrumb.Item>{product.name}</Breadcrumb.Item>
      </Breadcrumb>

      <Row gutter={[32, 32]}>
        {/* Left: Image Gallery */}
        <Col xs={24} lg={10}>
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Card className="product-gallery">
              {/* Main Image */}
              <div className="main-image-wrapper">
                {product.discount && product.discount > 0 && (
                  <Badge.Ribbon text={`-${product.discount}%`} color="red">
                    <Image
                      src={product.images[selectedImage]}
                      alt={product.name}
                      preview={{
                        mask: 'Click to zoom',
                      }}
                      style={{ width: '100%', borderRadius: 8 }}
                    />
                  </Badge.Ribbon>
                )}
                {(!product.discount || product.discount === 0) && (
                  <Image
                    src={product.images[selectedImage]}
                    alt={product.name}
                    preview={{
                      mask: 'Click to zoom',
                    }}
                    style={{ width: '100%', borderRadius: 8 }}
                  />
                )}
              </div>

              {/* Thumbnail Images */}
              <Row gutter={[8, 8]} style={{ marginTop: 16 }}>
                {product.images.map((img, index) => (
                  <Col span={4} key={index}>
                    <div
                      className={`thumbnail ${selectedImage === index ? 'active' : ''}`}
                      onClick={() => setSelectedImage(index)}
                      style={{
                        cursor: 'pointer',
                        border: selectedImage === index ? '2px solid #FF9900' : '1px solid #ddd',
                        borderRadius: 4,
                        padding: 4,
                      }}
                    >
                      <img
                        src={img}
                        alt={`Product ${index + 1}`}
                        style={{ width: '100%', borderRadius: 4 }}
                      />
                    </div>
                  </Col>
                ))}
              </Row>

              {/* Share & Wishlist */}
              <Row gutter={16} style={{ marginTop: 16 }}>
                <Col span={12}>
                  <Button
                    block
                    icon={isWishlisted ? <HeartFilled /> : <HeartOutlined />}
                    onClick={handleWishlist}
                    danger={isWishlisted}
                  >
                    {isWishlisted ? 'Wishlisted' : 'Add to Wishlist'}
                  </Button>
                </Col>
                <Col span={12}>
                  <Button block icon={<ShareAltOutlined />} onClick={handleShare}>
                    Share
                  </Button>
                </Col>
              </Row>
            </Card>
          </motion.div>
        </Col>

        {/* Right: Product Details */}
        <Col xs={24} lg={14}>
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            {/* Product Title */}
            <Title level={2} style={{ marginBottom: 8 }}>
              {product.name}
            </Title>

            {/* Rating & Reviews */}
            <Space size="middle" style={{ marginBottom: 16 }}>
              <Rate disabled defaultValue={product.rating} allowHalf />
              <Text strong>{product.rating}</Text>
              <Text type="secondary">({product.reviewCount.toLocaleString()} reviews)</Text>
              {product.vendor.verified && (
                <Tag icon={<CheckCircleOutlined />} color="success">
                  Verified Seller
                </Tag>
              )}
            </Space>

            <Divider />

            {/* Price */}
            <Space align="baseline" size="large" style={{ marginBottom: 24 }}>
              <Title level={3} style={{ color: '#B12704', margin: 0 }}>
                ${product.price.toFixed(2)}
              </Title>
              {product.originalPrice && product.originalPrice > product.price && (
                <>
                  <Text delete type="secondary" style={{ fontSize: 18 }}>
                    ${product.originalPrice.toFixed(2)}
                  </Text>
                  <Tag color="red" style={{ fontSize: 14 }}>
                    Save {product.discount}%
                  </Tag>
                </>
              )}
            </Space>

            {/* Stock Status */}
            {product.inStock ? (
              <Tag color="success" icon={<CheckCircleOutlined />} style={{ marginBottom: 16 }}>
                In Stock ({product.stockQuantity} available)
              </Tag>
            ) : (
              <Tag color="error" style={{ marginBottom: 16 }}>
                Out of Stock
              </Tag>
            )}

            {/* Variant Selection */}
            {product.variants && product.variants.length > 0 && (
              <div style={{ marginBottom: 24 }}>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Variants:
                </Text>
                <Space>
                  {product.variants.map((variant) => (
                    <Button
                      key={variant.id}
                      type={selectedVariant?.id === variant.id ? 'primary' : 'default'}
                      disabled={!variant.available}
                      onClick={() => setSelectedVariant(variant)}
                    >
                      {variant.name}
                      {!variant.available && ' (Out of stock)'}
                    </Button>
                  ))}
                </Space>
              </div>
            )}

            {/* Quantity Selector */}
            <div style={{ marginBottom: 24 }}>
              <Text strong style={{ display: 'block', marginBottom: 8 }}>
                Quantity:
              </Text>
              <InputNumber
                min={1}
                max={product.stockQuantity}
                value={quantity}
                onChange={(val) => setQuantity(val || 1)}
                size="large"
              />
            </div>

            {/* Action Buttons */}
            <Space direction="vertical" style={{ width: '100%', marginBottom: 24 }} size="middle">
              <Button
                type="primary"
                size="large"
                block
                icon={<ShoppingCartOutlined />}
                onClick={handleAddToCart}
                disabled={!product.inStock}
              >
                Add to Cart
              </Button>
              <Button
                size="large"
                block
                style={{ background: '#FFA41C', borderColor: '#FF9900', color: '#000' }}
                onClick={handleBuyNow}
                disabled={!product.inStock}
              >
                Buy Now
              </Button>
            </Space>

            {/* Vendor Info */}
            <Card size="small" style={{ marginBottom: 24 }}>
              <Row align="middle" gutter={16}>
                <Col>
                  <Avatar size={50} src={product.vendor.logo} icon={<ShopOutlined />} />
                </Col>
                <Col flex={1}>
                  <Text strong style={{ display: 'block' }}>
                    {product.vendor.name}
                  </Text>
                  <Space size="small">
                    <Rate disabled defaultValue={product.vendor.rating} style={{ fontSize: 12 }} />
                    <Text type="secondary" style={{ fontSize: 12 }}>
                      ({product.vendor.productCount} products)
                    </Text>
                  </Space>
                </Col>
                <Col>
                  <Button onClick={() => history.push(`/vendor/${product.vendor.id}`)}>
                    Visit Store
                  </Button>
                </Col>
              </Row>
            </Card>

            {/* Trust Badges */}
            <Row gutter={16}>
              <Col span={8}>
                <Card size="small" style={{ textAlign: 'center' }}>
                  <SafetyOutlined style={{ fontSize: 24, color: '#52c41a' }} />
                  <Text style={{ display: 'block', marginTop: 8, fontSize: 12 }}>
                    Secure Payment
                  </Text>
                </Card>
              </Col>
              <Col span={8}>
                <Card size="small" style={{ textAlign: 'center' }}>
                  <CheckCircleOutlined style={{ fontSize: 24, color: '#1890ff' }} />
                  <Text style={{ display: 'block', marginTop: 8, fontSize: 12 }}>
                    Quality Assured
                  </Text>
                </Card>
              </Col>
              <Col span={8}>
                <Card size="small" style={{ textAlign: 'center' }}>
                  <SafetyOutlined style={{ fontSize: 24, color: '#faad14' }} />
                  <Text style={{ display: 'block', marginTop: 8, fontSize: 12 }}>
                    7 Day Returns
                  </Text>
                </Card>
              </Col>
            </Row>
          </motion.div>
        </Col>
      </Row>

      {/* Product Details Tabs */}
      <Card style={{ marginTop: 32 }}>
        <Tabs defaultActiveKey="1" size="large">
          <Tabs.TabPane tab="Description" key="1">
            <div dangerouslySetInnerHTML={{ __html: product.description }} />
          </Tabs.TabPane>

          <Tabs.TabPane tab="Specifications" key="2">
            <Row gutter={[16, 16]}>
              {product.specifications.map((spec, index) => (
                <React.Fragment key={index}>
                  <Col span={8}>
                    <Text strong>{spec.label}</Text>
                  </Col>
                  <Col span={16}>
                    <Text>{spec.value}</Text>
                  </Col>
                </React.Fragment>
              ))}
            </Row>
          </Tabs.TabPane>

          <Tabs.TabPane tab={`Reviews (${product.reviewCount})`} key="3">
            {reviewsLoading ? (
              <div style={{ textAlign: 'center', padding: '50px 0' }}>
                <Spin />
                <div style={{ marginTop: 16 }}>Loading reviews...</div>
              </div>
            ) : (
              <>
                {/* Rating Summary */}
                <Row gutter={32} style={{ marginBottom: 32 }}>
                  <Col xs={24} md={8}>
                    <div style={{ textAlign: 'center' }}>
                      <Title level={1} style={{ margin: 0 }}>
                        {product.rating}
                      </Title>
                      <Rate disabled defaultValue={product.rating} allowHalf />
                      <Text type="secondary" style={{ display: 'block', marginTop: 8 }}>
                        Based on {product.reviewCount.toLocaleString()} reviews
                      </Text>
                    </div>
                  </Col>
                  <Col xs={24} md={16}>
                    {ratingBreakdown && [5, 4, 3, 2, 1].map((star) => (
                      <Row key={star} align="middle" gutter={8} style={{ marginBottom: 8 }}>
                        <Col span={3}>
                          <Text>{star} star</Text>
                        </Col>
                        <Col span={18}>
                          <Progress
                            percent={Number(calculateRatingPercentage(star))}
                            strokeColor="#FF9900"
                            showInfo={false}
                          />
                        </Col>
                        <Col span={3}>
                          <Text type="secondary">{calculateRatingPercentage(star)}%</Text>
                        </Col>
                      </Row>
                    ))}
                  </Col>
                </Row>

                <Divider />

                {/* Reviews List */}
                {reviews.map((review) => (
                  <Card key={review.id} style={{ marginBottom: 16 }} size="small">
                    <Row gutter={16}>
                      <Col span={3}>
                        <Avatar size={48} src={review.userAvatar} />
                      </Col>
                      <Col span={21}>
                        <Space direction="vertical" size={4} style={{ width: '100%' }}>
                          <Space>
                            <Text strong>{review.userName}</Text>
                            {review.verified && (
                              <Tag color="success" icon={<CheckCircleOutlined />}>
                                Verified Purchase
                              </Tag>
                            )}
                          </Space>
                          <Rate disabled defaultValue={review.rating} style={{ fontSize: 14 }} />
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            Reviewed on {new Date(review.createdAt).toLocaleDateString()}
                          </Text>
                          {review.title && (
                            <Text strong style={{ display: 'block', marginTop: 4 }}>
                              {review.title}
                            </Text>
                          )}
                          <Paragraph style={{ marginTop: 8 }}>{review.comment}</Paragraph>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            {review.helpful} people found this helpful
                          </Text>
                        </Space>
                      </Col>
                    </Row>
                  </Card>
                ))}

                {reviews.length === 0 && (
                  <div style={{ textAlign: 'center', padding: '50px 0' }}>
                    <Text type="secondary">No reviews yet. Be the first to review this product!</Text>
                  </div>
                )}

                {reviews.length > 0 && (
                  <Button block style={{ marginTop: 16 }}>
                    Load More Reviews
                  </Button>
                )}
              </>
            )}
          </Tabs.TabPane>
        </Tabs>
      </Card>

      {/* Related Products */}
      <Card title="Related Products" style={{ marginTop: 32 }}>
        {relatedProducts.length > 0 ? (
          <Row gutter={[16, 16]}>
            {relatedProducts.slice(0, 4).map((relatedProduct) => (
              <Col xs={12} md={6} key={relatedProduct.id}>
                <Card
                  hoverable
                  cover={
                    <img
                      alt={relatedProduct.name}
                      src={relatedProduct.image}
                      style={{ height: 200, objectFit: 'cover' }}
                    />
                  }
                  onClick={() => history.push(`/products/${relatedProduct.id}`)}
                >
                  <Card.Meta
                    title={
                      <Text ellipsis style={{ fontSize: 14 }}>
                        {relatedProduct.name}
                      </Text>
                    }
                    description={
                      <Space direction="vertical" size={4}>
                        <Text strong style={{ color: '#B12704' }}>
                          ${relatedProduct.price.toFixed(2)}
                        </Text>
                        <Space size={4}>
                          <Rate disabled defaultValue={relatedProduct.rating} style={{ fontSize: 12 }} />
                          <Text style={{ fontSize: 12 }}>({relatedProduct.reviewCount})</Text>
                        </Space>
                      </Space>
                    }
                  />
                </Card>
              </Col>
            ))}
          </Row>
        ) : (
          <Text type="secondary">No related products found.</Text>
        )}
      </Card>
    </div>
  );
};

export default ProductDetailPage;

vendor/orders.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Tag,
  Button,
  Space,
  Input,
  Select,
  Drawer,
  Descriptions,
  Steps,
  Timeline,
  message,
  Modal,
  Form,
  Row,
  Col,
  Typography,
  Alert,
  Spin,
} from 'antd';
import {
  SearchOutlined,
  EyeOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  PrinterOutlined,
  TruckOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { ordersAPI } from '@/services/api/orders';
import type { Order } from '@/services/api/orders';
import { useAuthStore } from '@/store/auth';
import dayjs from 'dayjs';

const { Title, Text } = Typography;
const { Option } = Select;

const VendorOrdersPage: React.FC = () => {
  const { user } = useAuthStore();
  const [orders, setOrders] = useState<Order[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [trackingModalVisible, setTrackingModalVisible] = useState(false);
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [searchText, setSearchText] = useState('');
  const [form] = Form.useForm();

  // Fetch orders
  const fetchOrders = async () => {
    if (!user?.id) return;

    setLoading(true);
    try {
      const filters: any = {};
      if (statusFilter !== 'all') {
        filters.status = statusFilter;
      }

      const response = await ordersAPI.getVendorOrders(user.id, filters);

      // Filter by search text if provided
      let filteredData = response.data;
      if (searchText) {
        filteredData = filteredData.filter(
          (order) =>
            order.orderNumber.toLowerCase().includes(searchText.toLowerCase()) ||
            order.customerId.toLowerCase().includes(searchText.toLowerCase())
        );
      }

      setOrders(filteredData);
    } catch (error) {
      message.error('Failed to fetch orders');
      console.error('Error fetching orders:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, [statusFilter, user?.id]);

  const getStatusColor = (status: Order['status']) => {
    const colors: Record<Order['status'], string> = {
      pending: 'blue',
      confirmed: 'cyan',
      processing: 'orange',
      shipped: 'purple',
      delivered: 'success',
      cancelled: 'error',
      refunded: 'warning',
    };
    return colors[status] || 'default';
  };

  const handleViewOrder = (order: Order) => {
    setSelectedOrder(order);
    setDrawerVisible(true);
  };

  const handleConfirmOrder = async (order: Order) => {
    Modal.confirm({
      title: 'Confirm Order',
      content: `Confirm order ${order.orderNumber}?`,
      onOk: async () => {
        try {
          await ordersAPI.updateStatus(order.id, 'confirmed');
          message.success('Order confirmed successfully');
          fetchOrders();
        } catch (error) {
          message.error('Failed to confirm order');
          console.error('Error confirming order:', error);
        }
      },
    });
  };

  const handleShipOrder = (order: Order) => {
    setSelectedOrder(order);
    setTrackingModalVisible(true);
  };

  const handleCancelOrder = async (order: Order) => {
    Modal.confirm({
      title: 'Cancel Order',
      content: `Are you sure you want to cancel order ${order.orderNumber}?`,
      okText: 'Yes, Cancel Order',
      okType: 'danger',
      onOk: async () => {
        try {
          await ordersAPI.cancel(order.id, 'Cancelled by vendor');
          message.success('Order cancelled');
          fetchOrders();
        } catch (error) {
          message.error('Failed to cancel order');
          console.error('Error cancelling order:', error);
        }
      },
    });
  };

  const handleSubmitTracking = async (values: any) => {
    if (!selectedOrder) return;

    try {
      await ordersAPI.updateTracking(selectedOrder.id, values.trackingNumber);
      await ordersAPI.updateStatus(selectedOrder.id, 'shipped');
      message.success('Order marked as shipped with tracking info');
      setTrackingModalVisible(false);
      form.resetFields();
      fetchOrders();
    } catch (error) {
      message.error('Failed to update tracking');
      console.error('Error updating tracking:', error);
    }
  };

  const columns: ColumnsType<Order> = [
    {
      title: 'Order Number',
      dataIndex: 'orderNumber',
      key: 'orderNumber',
      render: (text: string) => <Text strong>{text}</Text>,
    },
    {
      title: 'Customer ID',
      dataIndex: 'customerId',
      key: 'customerId',
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => dayjs(date).format('MMM DD, YYYY'),
      sorter: (a, b) => dayjs(a.createdAt).unix() - dayjs(b.createdAt).unix(),
    },
    {
      title: 'Items',
      dataIndex: 'items',
      key: 'items',
      render: (items: any[]) => items?.length || 0,
    },
    {
      title: 'Total',
      dataIndex: 'total',
      key: 'total',
      render: (total: number) => (
        <Text strong style={{ color: '#52c41a' }}>
          ${total.toFixed(2)}
        </Text>
      ),
      sorter: (a, b) => a.total - b.total,
    },
    {
      title: 'Payment',
      dataIndex: 'paymentStatus',
      key: 'paymentStatus',
      render: (paymentStatus: string) => (
        <Tag color={paymentStatus === 'paid' ? 'success' : 'warning'}>
          {paymentStatus?.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Order['status']) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Confirmed', value: 'confirmed' },
        { text: 'Processing', value: 'processing' },
        { text: 'Shipped', value: 'shipped' },
        { text: 'Delivered', value: 'delivered' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="link"
            icon={<EyeOutlined />}
            onClick={() => handleViewOrder(record)}
          >
            View
          </Button>
          {record.status === 'pending' && (
            <Button
              type="link"
              icon={<CheckCircleOutlined />}
              onClick={() => handleConfirmOrder(record)}
            >
              Confirm
            </Button>
          )}
          {(record.status === 'confirmed' || record.status === 'processing') && (
            <Button
              type="link"
              icon={<TruckOutlined />}
              onClick={() => handleShipOrder(record)}
            >
              Ship
            </Button>
          )}
          {(record.status === 'pending' || record.status === 'confirmed') && (
            <Button
              type="link"
              danger
              icon={<CloseCircleOutlined />}
              onClick={() => handleCancelOrder(record)}
            >
              Cancel
            </Button>
          )}
        </Space>
      ),
    },
  ];

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24 }}>
        <Card title={<Title level={3}>Order Management</Title>}>
          {/* Filters */}
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col xs={24} sm={12} md={8}>
              <Input
                placeholder="Search orders..."
                prefix={<SearchOutlined />}
                size="large"
                value={searchText}
                onChange={(e) => setSearchText(e.target.value)}
                onPressEnter={fetchOrders}
              />
            </Col>
            <Col xs={24} sm={12} md={6}>
              <Select 
                placeholder="Status" 
                size="large" 
                style={{ width: '100%' }}
                value={statusFilter}
                onChange={setStatusFilter}
              >
                <Option value="all">All Orders</Option>
                <Option value="pending">Pending</Option>
                <Option value="confirmed">Confirmed</Option>
                <Option value="processing">Processing</Option>
                <Option value="shipped">Shipped</Option>
                <Option value="delivered">Delivered</Option>
                <Option value="cancelled">Cancelled</Option>
              </Select>
            </Col>
          </Row>

          {/* Stats */}
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Total Orders</Text>
                <Title level={3} style={{ margin: 0 }}>
                  {orders.length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Pending</Text>
                <Title level={3} style={{ margin: 0, color: '#1890ff' }}>
                  {orders.filter((o) => o.status === 'pending').length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">In Progress</Text>
                <Title level={3} style={{ margin: 0, color: '#faad14' }}>
                  {orders.filter((o) => ['confirmed', 'processing', 'shipped'].includes(o.status)).length}
                </Title>
              </Card>
            </Col>
          <Col span={6}>
            <Card size="small">
              <Text type="secondary">Total Revenue</Text>
              <Title level={3} style={{ margin: 0, color: '#52c41a' }}>
                ${orders.reduce((sum, o) => sum + o.total, 0).toFixed(2)}
              </Title>
            </Card>
          </Col>
        </Row>

        {/* Orders Table */}
        <Table
          columns={columns}
          dataSource={orders}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} orders`,
          }}
        />
      </Card>

      {/* Order Detail Drawer */}
      <Drawer
        title={`Order Details - ${selectedOrder?.orderNumber}`}
        placement="right"
        width={600}
        open={drawerVisible}
        onClose={() => setDrawerVisible(false)}
      >
        {selectedOrder && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Steps
              current={
                selectedOrder.status === 'pending' ? 0 :
                selectedOrder.status === 'confirmed' ? 1 :
                selectedOrder.status === 'processing' ? 2 :
                selectedOrder.status === 'shipped' ? 3 :
                selectedOrder.status === 'delivered' ? 4 : 0
              }
              items={[
                { title: 'Pending' },
                { title: 'Confirmed' },
                { title: 'Processing' },
                { title: 'Shipped' },
                { title: 'Delivered' },
              ]}
            />

            <Descriptions title="Order Information" bordered column={1} size="small">
              <Descriptions.Item label="Order Number">{selectedOrder.orderNumber}</Descriptions.Item>
              <Descriptions.Item label="Customer ID">{selectedOrder.customerId}</Descriptions.Item>
              <Descriptions.Item label="Order Date">
                {dayjs(selectedOrder.createdAt).format('MMM DD, YYYY hh:mm A')}
              </Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={getStatusColor(selectedOrder.status)}>
                  {selectedOrder.status.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Payment Status">
                <Tag color={selectedOrder.paymentStatus === 'paid' ? 'success' : 'warning'}>
                  {selectedOrder.paymentStatus?.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Payment Method">{selectedOrder.paymentMethod}</Descriptions.Item>
              {selectedOrder.trackingNumber && (
                <Descriptions.Item label="Tracking Number">{selectedOrder.trackingNumber}</Descriptions.Item>
              )}
              <Descriptions.Item label="Subtotal">${selectedOrder.subtotal.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Tax">${selectedOrder.tax.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Shipping">${selectedOrder.shipping.toFixed(2)}</Descriptions.Item>
              {selectedOrder.discount > 0 && (
                <Descriptions.Item label="Discount">-${selectedOrder.discount.toFixed(2)}</Descriptions.Item>
              )}
              <Descriptions.Item label="Total Amount">
                <Text strong style={{ fontSize: 18, color: '#52c41a' }}>
                  ${selectedOrder.total.toFixed(2)}
                </Text>
              </Descriptions.Item>
            </Descriptions>

            <Card title="Shipping Address" size="small">
              <Text>
                {selectedOrder.shippingAddress.firstName} {selectedOrder.shippingAddress.lastName}<br />
                {selectedOrder.shippingAddress.address1}<br />
                {selectedOrder.shippingAddress.address2 && <>{selectedOrder.shippingAddress.address2}<br /></>}
                {selectedOrder.shippingAddress.city}, {selectedOrder.shippingAddress.state} {selectedOrder.shippingAddress.zipCode}<br />
                {selectedOrder.shippingAddress.country}<br />
                Phone: {selectedOrder.shippingAddress.phone}
              </Text>
            </Card>

            <Card title="Order Items" size="small">
              <div style={{ marginBottom: 8 }}>
                <Text strong>{selectedOrder.items.length} item(s)</Text>
              </div>
              {selectedOrder.items.map((item) => (
                <div key={item.id} style={{ marginBottom: 12, padding: 12, background: '#f5f5f5', borderRadius: 4 }}>
                  <Space direction="vertical" size="small" style={{ width: '100%' }}>
                    <Text strong>{item.productName}</Text>
                    <Text type="secondary">Quantity: {item.quantity}</Text>
                    <Text>Price: ${item.price.toFixed(2)} each</Text>
                    <Text strong>Total: ${item.total.toFixed(2)}</Text>
                  </Space>
                </div>
              ))}
            </Card>

            <Space>
              <Button icon={<PrinterOutlined />}>Print Invoice</Button>
              {selectedOrder.status === 'pending' && (
                <Button
                  type="primary"
                  icon={<CheckCircleOutlined />}
                  onClick={() => handleConfirmOrder(selectedOrder)}
                >
                  Confirm Order
                </Button>
              )}
              {(selectedOrder.status === 'confirmed' || selectedOrder.status === 'processing') && (
                <Button
                  type="primary"
                  icon={<TruckOutlined />}
                  onClick={() => handleShipOrder(selectedOrder)}
                >
                  Mark as Shipped
                </Button>
              )}
            </Space>
          </Space>
        )}
      </Drawer>

      {/* Add Tracking Modal */}
      <Modal
        title="Add Shipping Details"
        open={trackingModalVisible}
        onCancel={() => setTrackingModalVisible(false)}
        footer={null}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmitTracking}>
          <Form.Item
            name="carrier"
            label="Shipping Carrier"
            rules={[{ required: true, message: 'Please select carrier' }]}
          >
            <Select placeholder="Select carrier">
              <Option value="fedex">FedEx</Option>
              <Option value="ups">UPS</Option>
              <Option value="usps">USPS</Option>
              <Option value="dhl">DHL</Option>
            </Select>
          </Form.Item>

          <Form.Item
            name="trackingNumber"
            label="Tracking Number"
            rules={[{ required: true, message: 'Please enter tracking number' }]}
          >
            <Input placeholder="Enter tracking number" />
          </Form.Item>

          <Form.Item name="estimatedDelivery" label="Estimated Delivery Date">
            <Input type="date" />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                Mark as Shipped
              </Button>
              <Button onClick={() => setTrackingModalVisible(false)}>Cancel</Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
      </div>
    </Spin>
  );
};

export default VendorOrdersPage;

vendor/wallet.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Statistic,
  Table,
  Button,
  Tag,
  Space,
  Modal,
  Form,
  Input,
  Select,
  message,
  Typography,
  Alert,
  Tabs,
  Spin,
  InputNumber,
} from 'antd';
import {
  WalletOutlined,
  DollarOutlined,
  ArrowUpOutlined,
  ArrowDownOutlined,
  BankOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { walletAPI } from '@/services/api/wallet';
import type { Wallet, WalletTransaction, PayoutRequest } from '@/services/api/wallet';
import { useAuthStore } from '@/store/auth';
import dayjs from 'dayjs';

const { Title, Text } = Typography;
const { Option } = Select;

const VendorWalletPage: React.FC = () => {
  const { user } = useAuthStore();
  const [wallet, setWallet] = useState<Wallet | null>(null);
  const [transactions, setTransactions] = useState<WalletTransaction[]>([]);
  const [payouts, setPayouts] = useState<PayoutRequest[]>([]);
  const [loading, setLoading] = useState(false);
  const [payoutModalVisible, setPayoutModalVisible] = useState(false);
  const [typeFilter, setTypeFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [form] = Form.useForm();

  // Fetch wallet balance
  const fetchWallet = async () => {
    try {
      const data = await walletAPI.getBalance();
      setWallet(data);
    } catch (error) {
      message.error('Failed to fetch wallet balance');
      console.error('Error fetching wallet:', error);
    }
  };

  // Fetch transactions
  const fetchTransactions = async () => {
    setLoading(true);
    try {
      const filters: any = {};
      if (typeFilter !== 'all') {
        filters.type = typeFilter;
      }
      if (statusFilter !== 'all') {
        filters.status = statusFilter;
      }

      const response = await walletAPI.getTransactions(filters);
      setTransactions(response.data);
    } catch (error) {
      message.error('Failed to fetch transactions');
      console.error('Error fetching transactions:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch payout requests
  const fetchPayouts = async () => {
    try {
      const data = await walletAPI.getPayoutRequests();
      setPayouts(data);
    } catch (error) {
      message.error('Failed to fetch payout requests');
      console.error('Error fetching payouts:', error);
    }
  };

  useEffect(() => {
    fetchWallet();
    fetchPayouts();
  }, []);

  useEffect(() => {
    fetchTransactions();
  }, [typeFilter, statusFilter]);

  const handleRequestPayout = async (values: any) => {
    try {
      await walletAPI.requestPayout(values.amount, values.method);
      message.success('Payout request submitted successfully');
      setPayoutModalVisible(false);
      form.resetFields();
      fetchPayouts(); // Refresh payout list
      fetchWallet(); // Update balance
    } catch (error) {
      message.error('Failed to request payout');
      console.error('Error requesting payout:', error);
    }
  };

  const transactionColumns: ColumnsType<WalletTransaction> = [
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (date: string) => dayjs(date).format('MMM DD, YYYY hh:mm A'),
      sorter: (a, b) => dayjs(a.createdAt).unix() - dayjs(b.createdAt).unix(),
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: WalletTransaction['type']) => (
        <Tag color={type === 'credit' ? 'success' : 'error'} icon={type === 'credit' ? <ArrowDownOutlined /> : <ArrowUpOutlined />}>
          {type === 'credit' ? 'CREDIT' : 'DEBIT'}
        </Tag>
      ),
      filters: [
        { text: 'Credit', value: 'credit' },
        { text: 'Debit', value: 'debit' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Description',
      dataIndex: 'description',
      key: 'description',
      render: (desc: string, record) => (
        <div>
          <Text>{desc}</Text>
          {record.reference && (
            <div>
              <Text type="secondary" style={{ fontSize: 12 }}>
                Ref: {record.reference}
              </Text>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Amount',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount: number, record) => (
        <Text strong style={{ color: record.type === 'credit' ? '#52c41a' : '#ff4d4f' }}>
          {record.type === 'credit' ? '+' : '-'}${amount.toFixed(2)}
        </Text>
      ),
      sorter: (a, b) => a.amount - b.amount,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: WalletTransaction['status']) => (
        <Tag 
          color={status === 'completed' ? 'success' : status === 'pending' ? 'processing' : 'error'} 
          icon={status === 'completed' ? <CheckCircleOutlined /> : <ClockCircleOutlined />}
        >
          {status.toUpperCase()}
        </Tag>
      ),
    },
  ];

  const payoutColumns: ColumnsType<PayoutRequest> = [
    {
      title: 'Requested',
      dataIndex: 'requestedAt',
      key: 'requestedAt',
      render: (date: string) => dayjs(date).format('MMM DD, YYYY'),
    },
    {
      title: 'Amount',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount: number) => (
        <Text strong style={{ color: '#1890ff', fontSize: 16 }}>
          ${amount.toFixed(2)}
        </Text>
      ),
    },
    {
      title: 'Method',
      dataIndex: 'method',
      key: 'method',
      render: (method: string) => (
        <Tag icon={<BankOutlined />}>{method}</Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: PayoutRequest['status']) => {
        const colors: Record<PayoutRequest['status'], string> = {
          pending: 'processing',
          approved: 'success',
          rejected: 'error',
          paid: 'success',
        };
        return (
          <Tag color={colors[status]}>{status.toUpperCase()}</Tag>
        );
      },
    },
  ];

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24 }}>
        <Title level={3}>Wallet & Payments</Title>

      {/* Balance Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Current Balance"
              value={wallet?.balance || 0}
              precision={2}
              prefix={<WalletOutlined style={{ color: '#1890ff' }} />}
              suffix="USD"
              valueStyle={{ color: '#1890ff' }}
            />
            <Button
              type="primary"
              style={{ marginTop: 16, width: '100%' }}
              onClick={() => setPayoutModalVisible(true)}
              disabled={(wallet?.balance || 0) < 100}
            >
              Request Payout
            </Button>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Pending Amount"
              value={wallet?.pendingBalance || 0}
              precision={2}
              prefix={<ClockCircleOutlined style={{ color: '#faad14' }} />}
              suffix="USD"
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Earnings"
              value={wallet?.totalEarned || 0}
              precision={2}
              prefix={<ArrowDownOutlined style={{ color: '#52c41a' }} />}
              suffix="USD"
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Payouts"
              value={wallet?.totalSpent || 0}
              precision={2}
              prefix={<ArrowUpOutlined style={{ color: '#ff4d4f' }} />}
              suffix="USD"
              valueStyle={{ color: '#ff4d4f' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Alert */}
      {(wallet?.balance || 0) < 100 && (
        <Alert
          message="Minimum payout amount is $100.00"
          description="You can request a payout once your balance reaches $100.00"
          type="info"
          showIcon
          style={{ marginBottom: 16 }}
        />
      )}

      {/* Tabs */}
      <Card>
        <Tabs
          defaultActiveKey="transactions"
          items={[
            {
              key: 'transactions',
              label: 'All Transactions',
              children: (
                <Table
                  columns={transactionColumns}
                  dataSource={transactions}
                  rowKey="id"
                  pagination={{
                    pageSize: 10,
                    showSizeChanger: true,
                    showTotal: (total) => `Total ${total} transactions`,
                  }}
                />
              ),
            },
            {
              key: 'payouts',
              label: 'Payout History',
              children: (
                <div>
                  <div style={{ marginBottom: 16, textAlign: 'right' }}>
                    <Button
                      type="primary"
                      icon={<DollarOutlined />}
                      onClick={() => setPayoutModalVisible(true)}
                      disabled={(wallet?.balance || 0) < 100}
                    >
                      Request New Payout
                    </Button>
                  </div>
                  <Table
                    columns={payoutColumns}
                    dataSource={payouts}
                    rowKey="id"
                    pagination={{
                      pageSize: 10,
                      showSizeChanger: true,
                      showTotal: (total) => `Total ${total} payouts`,
                    }}
                  />
                </div>
              ),
            },
          ]}
        />
      </Card>

      {/* Payout Request Modal */}
      <Modal
        title="Request Payout"
        open={payoutModalVisible}
        onCancel={() => setPayoutModalVisible(false)}
        footer={null}
      >
        <Alert
          message={`Available Balance: $${(wallet?.balance || 0).toFixed(2)}`}
          type="info"
          showIcon
          style={{ marginBottom: 16 }}
        />

        <Form form={form} layout="vertical" onFinish={handleRequestPayout}>
          <Form.Item
            name="amount"
            label="Payout Amount"
            rules={[
              { required: true, message: 'Please enter amount' },
              {
                validator: (_, value) => {
                  const balance = wallet?.balance || 0;
                  if (value && parseFloat(value) > balance) {
                    return Promise.reject('Amount exceeds available balance');
                  }
                  if (value && parseFloat(value) < 100) {
                    return Promise.reject('Minimum payout amount is $100.00');
                  }
                  return Promise.resolve();
                },
              },
            ]}
          >
            <Input
              type="number"
              prefix="$"
              placeholder="Enter amount"
              min={100}
              max={wallet?.balance || 0}
              step={0.01}
            />
          </Form.Item>

          <Form.Item
            name="method"
            label="Payout Method"
            rules={[{ required: true, message: 'Please select payout method' }]}
          >
            <Select placeholder="Select payout method">
              <Option value="Bank Transfer">Bank Transfer</Option>
              <Option value="PayPal">PayPal</Option>
              <Option value="Wire Transfer">Wire Transfer</Option>
            </Select>
          </Form.Item>

          <Form.Item
            name="accountNumber"
            label="Account Number"
            rules={[{ required: true, message: 'Please enter account number' }]}
          >
            <Input placeholder="Enter account number" />
          </Form.Item>

          <Form.Item name="notes" label="Notes (Optional)">
            <Input.TextArea rows={3} placeholder="Add any notes..." />
          </Form.Item>

          <Alert
            message="Payout Processing Time"
            description="Payout requests are typically processed within 3-5 business days. You will receive an email confirmation once approved."
            type="warning"
            showIcon
            style={{ marginBottom: 16 }}
          />

          <Form.Item>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => setPayoutModalVisible(false)}>Cancel</Button>
              <Button type="primary" htmlType="submit" icon={<DollarOutlined />}>
                Submit Request
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
      </div>
    </Spin>
  );
};

export default VendorWalletPage;

admin/brands.__content:
import { ProTable } from '@ant-design/pro-components';
import type { ProColumns, ActionType } from '@ant-design/pro-components';
import { Button, Space, Modal, Form, Input, Upload, message, Image, Switch } from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined, UploadOutlined } from '@ant-design/icons';
import { useState, useRef, useEffect } from 'react';
import { brandsAPI, type Brand } from '@/services/api/catalog';
import type { UploadFile } from 'antd/es/upload/interface';

const AdminBrands = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [editingBrand, setEditingBrand] = useState<Brand | null>(null);
  const [brands, setBrands] = useState<Brand[]>([]);
  const [loading, setLoading] = useState(false);
  const [fileList, setFileList] = useState<UploadFile[]>([]);
  const [form] = Form.useForm();
  const actionRef = useRef<ActionType>();

  useEffect(() => {
    fetchBrands();
  }, []);

  const fetchBrands = async () => {
    try {
      setLoading(true);
      const response = await brandsAPI.getAll();
      setBrands(response || []);
    } catch (error: any) {
      console.error('Failed to fetch brands:', error);
      message.error(error?.response?.data?.message || 'Failed to load brands');
    } finally {
      setLoading(false);
    }
  };

  const handleToggleActive = async (id: string, currentStatus: boolean) => {
    try {
      await brandsAPI.toggleActive(id);
      message.success('Brand status updated');
      fetchBrands();
    } catch (error: any) {
      message.error(error?.response?.data?.message || 'Failed to update brand status');
    }
  };

  const columns: ProColumns<Brand>[] = [
    {
      title: 'Logo',
      dataIndex: 'logo',
      key: 'logo',
      width: 100,
      render: (_: any, record: Brand) => record.logo ? (
        <Image src={record.logo} width={50} height={50} style={{ objectFit: 'contain' }} />
      ) : (
        <div style={{ width: 50, height: 50, background: '#f0f0f0', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          No Logo
        </div>
      ),
    },
    {
      title: 'Brand Name',
      dataIndex: 'name',
      key: 'name',
      width: 200,
    },
    {
      title: 'Slug',
      dataIndex: 'slug',
      key: 'slug',
      copyable: true,
    },
    {
      title: 'Products',
      dataIndex: 'productCount',
      key: 'productCount',
      width: 100,
      align: 'center' as const,
      render: (_: any, record: Brand) => record.productCount || 0,
    },
    {
      title: 'Status',
      dataIndex: 'isActive',
      key: 'isActive',
      width: 100,
      render: (_: any, record: Brand) => (
        <Switch checked={record.isActive} onChange={() => handleToggleActive(record.id, record.isActive)} />
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 150,
      render: (_: any, record: Brand) => (
        <Space>
          <Button type="link" icon={<EditOutlined />} onClick={() => handleEdit(record)} />
          <Button type="link" danger icon={<DeleteOutlined />} onClick={() => handleDelete(record.id)} />
        </Space>
      ),
    },
  ];

  const handleAdd = () => {
    setEditingBrand(null);
    form.resetFields();
    setFileList([]);
    setModalVisible(true);
  };

  const handleEdit = (record: Brand) => {
    setEditingBrand(record);
    form.setFieldsValue(record);
    if (record.logo) {
      setFileList([{
        uid: '-1',
        name: 'logo.png',
        status: 'done',
        url: record.logo,
      }]);
    }
    setModalVisible(true);
  };

  const handleDelete = (id: string) => {
    Modal.confirm({
      title: 'Delete Brand',
      content: 'Are you sure you want to delete this brand? This action cannot be undone.',
      onOk: async () => {
        try {
          await brandsAPI.delete(id);
          message.success('Brand deleted successfully');
          fetchBrands();
        } catch (error: any) {
          message.error(error?.response?.data?.message || 'Failed to delete brand');
        }
      },
    });
  };

  const handleSubmit = async (values: any) => {
    try {
      const brandData = {
        ...values,
        logo: fileList[0]?.url || fileList[0]?.response?.url || undefined,
      };

      if (editingBrand) {
        await brandsAPI.update(editingBrand.id, brandData);
        message.success('Brand updated successfully');
      } else {
        await brandsAPI.create(brandData);
        message.success('Brand created successfully');
      }
      setModalVisible(false);
      form.resetFields();
      setFileList([]);
      setEditingBrand(null);
      fetchBrands();
    } catch (error: any) {
      message.error(error?.response?.data?.message || 'Failed to save brand');
    }
  };

  return (
    <div>
      <ProTable<Brand>
        columns={columns}
        dataSource={brands}
        rowKey="id"
        loading={loading}
        search={false}
        headerTitle="Brands Management"
        toolBarRender={() => [
          <Button key="add" type="primary" icon={<PlusOutlined />} onClick={handleAdd}>
            Add Brand
          </Button>,
        ]}
      />

      <Modal
        title={editingBrand ? 'Edit Brand' : 'Add Brand'}
        open={modalVisible}
        onCancel={() => {
          setModalVisible(false);
          setEditingBrand(null);
          setFileList([]);
        }}
        footer={null}
        width={600}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item name="name" label="Brand Name" rules={[{ required: true, message: 'Please enter brand name' }]}>
            <Input />
          </Form.Item>
          <Form.Item name="slug" label="Slug" rules={[{ required: true, message: 'Please enter slug' }]}>
            <Input placeholder="auto-generated-from-name" />
          </Form.Item>
          <Form.Item name="description" label="Description">
            <Input.TextArea rows={3} />
          </Form.Item>
          <Form.Item name="website" label="Website URL">
            <Input placeholder="https://example.com" />
          </Form.Item>
          <Form.Item label="Brand Logo">
            <Upload
              listType="picture-card"
              fileList={fileList}
              onChange={({ fileList: newFileList }) => setFileList(newFileList)}
              beforeUpload={() => false}
              maxCount={1}
            >
              {fileList.length >= 1 ? null : (
                <div>
                  <UploadOutlined />
                  <div style={{ marginTop: 8 }}>Upload Logo</div>
                </div>
              )}
            </Upload>
          </Form.Item>
          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                {editingBrand ? 'Update' : 'Create'}
              </Button>
              <Button onClick={() => {
                setModalVisible(false);
                setEditingBrand(null);
                setFileList([]);
              }}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default AdminBrands;

admin/orders.__content:
import { ProTable } from '@ant-design/pro-components';
import type { ProColumns } from '@ant-design/pro-components';
import { Tag, Button, Space, Badge, Drawer, Descriptions, Timeline, message, Select } from 'antd';
import { EyeOutlined, PrinterOutlined, CheckCircleOutlined } from '@ant-design/icons';
import { useState, useRef } from 'react';
import type { ActionType } from '@ant-design/pro-components';
import { ORDER_STATUS_COLORS } from '@/config/constants';
import { ordersAPI, type Order } from '@/services/api/orders';

const AdminOrders = () => {
  const actionRef = useRef<ActionType>();
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [updatingStatus, setUpdatingStatus] = useState(false);

  const handleStatusChange = async (orderId: string, newStatus: string) => {
    try {
      setUpdatingStatus(true);
      await ordersAPI.updateStatus(orderId, newStatus as any);
      message.success('Order status updated successfully');
      actionRef.current?.reload();
      if (selectedOrder?.id === orderId) {
        const updatedOrder = await ordersAPI.getById(orderId);
        setSelectedOrder(updatedOrder);
      }
    } catch (error: any) {
      message.error(error.message || 'Failed to update order status');
    } finally {
      setUpdatingStatus(false);
    }
  };

  const columns: ProColumns<Order>[] = [
    {
      title: 'Order #',
      dataIndex: 'orderNumber',
      key: 'orderNumber',
      fixed: 'left',
      width: 140,
      render: (text) => <a>{text}</a>,
    },
    {
      title: 'Customer ID',
      dataIndex: 'customerId',
      key: 'customerId',
      width: 180,
      copyable: true,
    },
    {
      title: 'Vendor ID',
      dataIndex: 'vendorId',
      key: 'vendorId',
      width: 180,
    },
    {
      title: 'Items',
      dataIndex: 'items',
      key: 'items',
      width: 80,
      align: 'center',
      render: (_: any, record: Order) => <Badge count={record.items?.length || 0} showZero color="#1890ff" />,
    },
    {
      title: 'Total',
      dataIndex: 'total',
      key: 'total',
      width: 120,
      render: (_: any, record: Order) => `$${record.total?.toFixed(2) || '0.00'}`,
      sorter: true,
    },
    {
      title: 'Payment',
      dataIndex: 'paymentMethod',
      key: 'paymentMethod',
      width: 120,
      filters: [
        { text: 'Card', value: 'card' },
        { text: 'Wallet', value: 'wallet' },
        { text: 'COD', value: 'cod' },
      ],
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 200,
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Confirmed', value: 'confirmed' },
        { text: 'Processing', value: 'processing' },
        { text: 'Shipped', value: 'shipped' },
        { text: 'Delivered', value: 'delivered' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      render: (_: any, record: Order) => (
        <Select
          value={record.status}
          onChange={(value) => handleStatusChange(record.id, value)}
          loading={updatingStatus}
          style={{ width: 140 }}
        >
          <Select.Option value="pending">
            <Tag color="blue">PENDING</Tag>
          </Select.Option>
          <Select.Option value="confirmed">
            <Tag color="cyan">CONFIRMED</Tag>
          </Select.Option>
          <Select.Option value="processing">
            <Tag color="orange">PROCESSING</Tag>
          </Select.Option>
          <Select.Option value="shipped">
            <Tag color="purple">SHIPPED</Tag>
          </Select.Option>
          <Select.Option value="delivered">
            <Tag color="green">DELIVERED</Tag>
          </Select.Option>
          <Select.Option value="cancelled">
            <Tag color="red">CANCELLED</Tag>
          </Select.Option>
        </Select>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 180,
      valueType: 'dateTime',
      sorter: true,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 180,
      render: (_, record) => (
        <Space size="small">
          <Button 
            type="link" 
            icon={<EyeOutlined />}
            onClick={() => {
              setSelectedOrder(record);
              setDrawerVisible(true);
            }}
          >
            View
          </Button>
          <Button type="link" icon={<PrinterOutlined />}>
            Print
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <div>
      <ProTable<Order>
        columns={columns}
        actionRef={actionRef}
        request={async (params, sort, filter) => {
          try {
            const { current, pageSize, status, paymentMethod } = params;

            const response = await ordersAPI.getAll({
              page: current,
              limit: pageSize,
              status: filter?.status?.[0] as string,
            });

            return {
              data: response.data,
              success: true,
              total: response.total,
            };
          } catch (error) {
            message.error('Failed to load orders');
            return {
              data: [],
              success: false,
              total: 0,
            };
          }
        }}
        rowKey="id"
        pagination={{
          defaultPageSize: 20,
          showSizeChanger: true,
          showQuickJumper: true,
          showTotal: (total) => `Total ${total} orders`,
        }}
        search={{
          labelWidth: 'auto',
        }}
        dateFormatter="string"
        headerTitle="Order Management"
        toolBarRender={() => [
          <Button key="export">Export</Button>,
        ]}
        scroll={{ x: 1400 }}
      />

      <Drawer
        title="Order Details"
        placement="right"
        width={720}
        onClose={() => setDrawerVisible(false)}
        open={drawerVisible}
      >
        {selectedOrder && (
          <div>
            <Descriptions title="Order Information" bordered column={2}>
              <Descriptions.Item label="Order Number">{selectedOrder.orderNumber}</Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={ORDER_STATUS_COLORS[selectedOrder.status as keyof typeof ORDER_STATUS_COLORS] || 'blue'}>
                  {selectedOrder.status?.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Customer ID">{selectedOrder.customerId}</Descriptions.Item>
              <Descriptions.Item label="Vendor ID">{selectedOrder.vendorId}</Descriptions.Item>
              <Descriptions.Item label="Items Count">{selectedOrder.items?.length || 0}</Descriptions.Item>
              <Descriptions.Item label="Total">${selectedOrder.total?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Subtotal">${selectedOrder.subtotal?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Tax">${selectedOrder.tax?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Shipping">${selectedOrder.shipping?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Discount">${selectedOrder.discount?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Payment Method">{selectedOrder.paymentMethod}</Descriptions.Item>
              <Descriptions.Item label="Payment Status">
                <Tag color={selectedOrder.paymentStatus === 'paid' ? 'green' : 'orange'}>
                  {selectedOrder.paymentStatus?.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Date">{new Date(selectedOrder.createdAt).toLocaleString()}</Descriptions.Item>
              {selectedOrder.trackingNumber && (
                <Descriptions.Item label="Tracking Number">{selectedOrder.trackingNumber}</Descriptions.Item>
              )}
            </Descriptions>

            {/* Order Items */}
            <div style={{ marginTop: 24 }}>
              <h3>Order Items</h3>
              {selectedOrder.items?.map((item, index) => (
                <div key={item.id} style={{ 
                  padding: '12px', 
                  background: '#f5f5f5', 
                  marginBottom: 8,
                  borderRadius: 4 
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <div>
                      <strong>{item.productName}</strong>
                      <div style={{ color: '#666', fontSize: 12 }}>
                        Qty: {item.quantity}  ${item.price.toFixed(2)}
                      </div>
                    </div>
                    <div style={{ fontWeight: 600 }}>
                      ${item.total.toFixed(2)}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Shipping Address */}
            <div style={{ marginTop: 24 }}>
              <h3>Shipping Address</h3>
              <p>
                {selectedOrder.shippingAddress?.firstName} {selectedOrder.shippingAddress?.lastName}<br />
                {selectedOrder.shippingAddress?.address1}<br />
                {selectedOrder.shippingAddress?.address2 && <>{selectedOrder.shippingAddress.address2}<br /></>}
                {selectedOrder.shippingAddress?.city}, {selectedOrder.shippingAddress?.state} {selectedOrder.shippingAddress?.zipCode}<br />
                {selectedOrder.shippingAddress?.country}<br />
                Phone: {selectedOrder.shippingAddress?.phone}
              </p>
            </div>

            <div style={{ marginTop: 24 }}>
              <h3>Order Timeline</h3>
              <Timeline
                items={[
                  {
                    color: 'green',
                    children: `Order placed - ${new Date(selectedOrder.createdAt).toLocaleString()}`,
                  },
                  selectedOrder.status !== 'pending' && {
                    color: 'blue',
                    children: `Order ${selectedOrder.status} - ${new Date(selectedOrder.updatedAt).toLocaleString()}`,
                  },
                  selectedOrder.trackingNumber && {
                    color: 'purple',
                    children: `Tracking: ${selectedOrder.trackingNumber}`,
                  },
                ].filter(Boolean) as any}
              />
            </div>

            <div style={{ marginTop: 24 }}>
              <Space>
                <Select
                  value={selectedOrder.status}
                  onChange={(value) => handleStatusChange(selectedOrder.id, value)}
                  loading={updatingStatus}
                  style={{ width: 200 }}
                >
                  <Select.Option value="pending">Pending</Select.Option>
                  <Select.Option value="confirmed">Confirmed</Select.Option>
                  <Select.Option value="processing">Processing</Select.Option>
                  <Select.Option value="shipped">Shipped</Select.Option>
                  <Select.Option value="delivered">Delivered</Select.Option>
                  <Select.Option value="cancelled">Cancelled</Select.Option>
                </Select>
                <Button icon={<PrinterOutlined />}>Print Invoice</Button>
              </Space>
            </div>
          </div>
        )}
      </Drawer>
    </div>
  );
};

export default AdminOrders;

help-center.__content:
import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Typography, 
  Row, 
  Col, 
  Input, 
  Button, 
  Space, 
  Collapse, 
  Tag, 
  Spin, 
  message,
  Empty,
  Badge,
  Rate,
  AutoComplete,
  List,
  Avatar,
  Divider,
  Alert
} from 'antd';
import {
  SearchOutlined,
  QuestionCircleOutlined,
  ShoppingOutlined,
  UserOutlined,
  SafetyOutlined,
  RocketOutlined,
  LikeOutlined,
  EyeOutlined,
  ClockCircleOutlined,
  CustomerServiceOutlined,
  PhoneOutlined,
  MailOutlined,
  BookOutlined,
  ArrowRightOutlined,
  CheckCircleOutlined,
  BulbOutlined
} from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import { helpCenterAPI } from '../services/api/helpCenterAPI';
import type { FAQ, HelpCategory, HelpArticle, HelpBanner } from '../services/api/helpCenterAPI';

const { Title, Text, Paragraph } = Typography;
const { Panel } = Collapse;

const HelpCenterPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchSuggestions, setSearchSuggestions] = useState<string[]>([]);

  // Data states
  const [categories, setCategories] = useState<HelpCategory[]>([]);
  const [faqs, setFaqs] = useState<FAQ[]>([]);
  const [popularArticles, setPopularArticles] = useState<HelpArticle[]>([]);
  const [banners, setBanners] = useState<HelpBanner[]>([]);
  const [searchResults, setSearchResults] = useState<{
    faqs: FAQ[];
    articles: HelpArticle[];
    total: number;
  } | null>(null);

  // Load initial data
  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);

      const [
        categoriesRes,
        faqsRes,
        articlesRes,
        bannersRes
      ] = await Promise.all([
        helpCenterAPI.getHelpCategories(),
        helpCenterAPI.getFAQs({ active: true, limit: 8 }),
        helpCenterAPI.getHelpArticles({ limit: 6 }),
        helpCenterAPI.getHelpBanners()
      ]);

      if (categoriesRes.success) setCategories(categoriesRes.data);
      if (faqsRes.success) setFaqs(faqsRes.data);
      if (articlesRes.success) setPopularArticles(articlesRes.data);
      if (bannersRes.success) setBanners(bannersRes.data);

    } catch (error) {
      console.error('Error loading help center data:', error);
      message.error('Failed to load help center data');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (value: string) => {
    if (!value.trim()) {
      setSearchResults(null);
      return;
    }

    try {
      setSearchLoading(true);
      const response = await helpCenterAPI.searchHelp(value);

      if (response.success) {
        setSearchResults(response.data);
      } else {
        message.error('Search failed');
      }
    } catch (error) {
      console.error('Search error:', error);
      message.error('Search failed');
    } finally {
      setSearchLoading(false);
    }
  };

  const generateSearchSuggestions = (value: string) => {
    const commonQuestions = [
      'How to place an order',
      'Track my order',
      'Return policy',
      'Payment methods',
      'Shipping information',
      'Account settings',
      'Contact support'
    ];

    if (!value) return [];

    return commonQuestions.filter(q => 
      q.toLowerCase().includes(value.toLowerCase())
    );
  };

  const getCategoryIcon = (iconName: string) => {
    const icons: Record<string, React.ReactNode> = {
      'RocketOutlined': <RocketOutlined />,
      'ShoppingOutlined': <ShoppingOutlined />,
      'UserOutlined': <UserOutlined />,
      'SafetyOutlined': <SafetyOutlined />
    };
    return icons[iconName] || <QuestionCircleOutlined />;
  };

  const renderBanners = () => {
    if (!banners.length) return null;

    return (
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        {banners.map(banner => (
          <Col xs={24} key={banner.id}>
            <Alert
              message={banner.title}
              description={banner.content}
              type={banner.type}
              showIcon
              style={{ borderRadius: 8 }}
            />
          </Col>
        ))}
      </Row>
    );
  };

  const renderSearchResults = () => {
    if (!searchResults) return null;

    const { faqs, articles, total } = searchResults;

    if (total === 0) {
      return (
        <Card>
          <Empty
            image={Empty.PRESENTED_IMAGE_SIMPLE}
            description={
              <div>
                <Text>No results found for "{searchQuery}"</Text>
                <br />
                <Text type="secondary">Try different keywords or contact support</Text>
              </div>
            }
          >
            <Button type="primary" icon={<CustomerServiceOutlined />}>
              Contact Support
            </Button>
          </Empty>
        </Card>
      );
    }

    return (
      <div>
        <Title level={3}>Search Results ({total})</Title>

        {faqs.length > 0 && (
          <Card title="FAQs" style={{ marginBottom: 16 }}>
            <Collapse ghost>
              {faqs.map(faq => (
                <Panel
                  header={faq.question}
                  key={faq.id}
                  extra={<Tag color="blue">FAQ</Tag>}
                >
                  <div dangerouslySetInnerHTML={{ __html: faq.answer }} />
                </Panel>
              ))}
            </Collapse>
          </Card>
        )}

        {articles.length > 0 && (
          <Card title="Help Articles">
            <List
              dataSource={articles}
              renderItem={article => (
                <List.Item
                  actions={[
                    <Button 
                      type="link" 
                      icon={<ArrowRightOutlined />}
                      onClick={() => navigate(`/help/${article.slug}`)}
                    >
                      Read More
                    </Button>
                  ]}
                >
                  <List.Item.Meta
                    avatar={<Avatar icon={<BookOutlined />} />}
                    title={article.title}
                    description={
                      <div>
                        <Text ellipsis>{article.content?.substring(0, 150)}...</Text>
                        <br />
                        <Text type="secondary">
                          <ClockCircleOutlined /> {new Date(article.createdAt).toLocaleDateString()}
                        </Text>
                      </div>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div style={{ padding: 24 }}>
        <Card>
          <Spin size="large" style={{ display: 'block', textAlign: 'center', padding: 50 }} />
        </Card>
      </div>
    );
  }

  return (
    <div style={{ padding: 24, background: '#f5f5f5', minHeight: '100vh' }}>
      <div style={{ maxWidth: 1200, margin: '0 auto' }}>

        {/* Header */}
        <Card style={{ marginBottom: 24, textAlign: 'center' }}>
          <Title level={1}>
            <CustomerServiceOutlined style={{ color: '#1890ff', marginRight: 8 }} />
            Help Center
          </Title>
          <Paragraph style={{ fontSize: 16 }}>
            Find answers to your questions and get the help you need
          </Paragraph>

          {/* Search */}
          <div style={{ maxWidth: 600, margin: '0 auto' }}>
            <AutoComplete
              style={{ width: '100%' }}
              options={searchSuggestions.map(suggestion => ({ value: suggestion }))}
              onSearch={(value) => setSearchSuggestions(generateSearchSuggestions(value))}
              onSelect={handleSearch}
              value={searchQuery}
              onChange={setSearchQuery}
            >
              <Input.Search
                placeholder="Search for help..."
                size="large"
                loading={searchLoading}
                onSearch={handleSearch}
                enterButton={
                  <Button type="primary" icon={<SearchOutlined />}>
                    Search
                  </Button>
                }
              />
            </AutoComplete>
          </div>
        </Card>

        {/* Banners */}
        {renderBanners()}

        {/* Search Results */}
        {searchResults ? renderSearchResults() : (
          <>
            {/* Categories */}
            <Card 
              title={
                <Space>
                  <BulbOutlined />
                  Browse by Category
                </Space>
              }
              style={{ marginBottom: 24 }}
            >
              <Row gutter={[16, 16]}>
                {categories.map(category => (
                  <Col xs={24} sm={12} md={6} key={category.id}>
                    <Card
                      hoverable
                      style={{ textAlign: 'center', height: '100%' }}
                      onClick={() => navigate(`/help/category/${category.slug}`)}
                    >
                      <div style={{ fontSize: 32, color: category.color, marginBottom: 8 }}>
                        {getCategoryIcon(category.icon)}
                      </div>
                      <Title level={4}>{category.name}</Title>
                      <Text type="secondary">{category.description}</Text>
                      <br />
                      <Badge 
                        count={category.articleCount} 
                        style={{ backgroundColor: category.color }} 
                      />
                    </Card>
                  </Col>
                ))}
              </Row>
            </Card>

            {/* Popular Articles */}
            {popularArticles.length > 0 && (
              <Card 
                title={
                  <Space>
                    <BookOutlined />
                    Popular Articles
                  </Space>
                }
                style={{ marginBottom: 24 }}
              >
                <Row gutter={[16, 16]}>
                  {popularArticles.map(article => (
                    <Col xs={24} md={12} key={article.id}>
                      <Card
                        size="small"
                        hoverable
                        onClick={() => navigate(`/help/${article.slug}`)}
                      >
                        <Space align="start">
                          <Avatar icon={<BookOutlined />} />
                          <div style={{ flex: 1 }}>
                            <Title level={5} style={{ margin: 0 }}>
                              {article.title}
                            </Title>
                            <Text type="secondary">
                              <ClockCircleOutlined /> {new Date(article.createdAt).toLocaleDateString()}
                              {article.views && (
                                <>
                                  <Divider type="vertical" />
                                  <EyeOutlined /> {article.views} views
                                </>
                              )}
                            </Text>
                          </div>
                        </Space>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </Card>
            )}

            {/* Frequently Asked Questions */}
            <Card 
              title={
                <Space>
                  <QuestionCircleOutlined />
                  Frequently Asked Questions
                </Space>
              }
              style={{ marginBottom: 24 }}
            >
              {faqs.length > 0 ? (
                <Collapse ghost>
                  {faqs.map(faq => (
                    <Panel
                      header={faq.question}
                      key={faq.id}
                      extra={
                        <Space>
                          <LikeOutlined />
                          <Text type="secondary">Helpful</Text>
                        </Space>
                      }
                    >
                      <div dangerouslySetInnerHTML={{ __html: faq.answer }} />
                    </Panel>
                  ))}
                </Collapse>
              ) : (
                <Empty 
                  description="No FAQs available yet"
                  image={Empty.PRESENTED_IMAGE_SIMPLE}
                />
              )}
            </Card>

            {/* Contact Support */}
            <Card title="Still Need Help?">
              <Row gutter={[16, 16]}>
                <Col xs={24} md={8}>
                  <Card size="small" style={{ textAlign: 'center' }}>
                    <PhoneOutlined style={{ fontSize: 24, color: '#1890ff', marginBottom: 8 }} />
                    <Title level={4}>Call Us</Title>
                    <Text>+1 (555) 123-4567</Text>
                    <br />
                    <Text type="secondary">Mon-Fri 9AM-6PM</Text>
                  </Card>
                </Col>
                <Col xs={24} md={8}>
                  <Card size="small" style={{ textAlign: 'center' }}>
                    <MailOutlined style={{ fontSize: 24, color: '#52c41a', marginBottom: 8 }} />
                    <Title level={4}>Email Support</Title>
                    <Text>support@groow.com</Text>
                    <br />
                    <Text type="secondary">24/7 Response</Text>
                  </Card>
                </Col>
                <Col xs={24} md={8}>
                  <Card size="small" style={{ textAlign: 'center' }}>
                    <CustomerServiceOutlined style={{ fontSize: 24, color: '#faad14', marginBottom: 8 }} />
                    <Title level={4}>Live Chat</Title>
                    <Button type="primary">Start Chat</Button>
                    <br />
                    <Text type="secondary">Available Now</Text>
                  </Card>
                </Col>
              </Row>
            </Card>
          </>
        )}
      </div>
    </div>
  );
};

export default HelpCenterPage;

