vendor/kyc.__content:
import React, { useState } from 'react';
import {
  Card,
  Steps,
  Form,
  Input,
  Button,
  Upload,
  message,
  Row,
  Col,
  Select,
  Alert,
  Typography,
  Space,
  Tag,
  Descriptions,
} from 'antd';
import {
  UploadOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  CloseCircleOutlined,
  FileTextOutlined,
  IdcardOutlined,
  BankOutlined,
} from '@ant-design/icons';
import type { UploadFile } from 'antd/es/upload/interface';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TextArea } = Input;

const VendorKYCPage: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [form] = Form.useForm();
  const [businessDocsFiles, setBusinessDocsFiles] = useState<UploadFile[]>([]);
  const [identityFiles, setIdentityFiles] = useState<UploadFile[]>([]);
  const [taxFiles, setTaxFiles] = useState<UploadFile[]>([]);
  const [bankFiles, setBankFiles] = useState<UploadFile[]>([]);

  // Mock verification status
  const verificationStatus = {
    status: 'pending', // 'pending' | 'approved' | 'rejected' | 'resubmit'
    submittedDate: '2024-11-01',
    reviewedDate: null,
    rejectionReason: null,
    documents: {
      business: { status: 'approved', comment: 'Documents verified' },
      identity: { status: 'pending', comment: 'Under review' },
      tax: { status: 'pending', comment: 'Under review' },
      bank: { status: 'rejected', comment: 'Bank statement is not clear. Please reupload.' },
    },
  };

  const handleNext = () => {
    form.validateFields().then(() => {
      setCurrentStep(currentStep + 1);
    });
  };

  const handlePrev = () => {
    setCurrentStep(currentStep - 1);
  };

  const handleSubmit = (values: any) => {
    console.log('KYC Data:', values);
    console.log('Files:', {
      businessDocs: businessDocsFiles,
      identity: identityFiles,
      tax: taxFiles,
      bank: bankFiles,
    });
    message.success('KYC documents submitted successfully! We will review within 2-3 business days.');
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved':
        return <CheckCircleOutlined style={{ color: '#52c41a', fontSize: 24 }} />;
      case 'pending':
        return <ClockCircleOutlined style={{ color: '#faad14', fontSize: 24 }} />;
      case 'rejected':
        return <CloseCircleOutlined style={{ color: '#ff4d4f', fontSize: 24 }} />;
      default:
        return null;
    }
  };

  const getStatusTag = (status: string) => {
    const colors = {
      approved: 'success',
      pending: 'warning',
      rejected: 'error',
      resubmit: 'orange',
    };
    return <Tag color={colors[status as keyof typeof colors]}>{status.toUpperCase()}</Tag>;
  };

  return (
    <div style={{ padding: 24, maxWidth: 1200, margin: '0 auto' }}>
      <Title level={2}>KYC Verification</Title>
      <Paragraph type="secondary">
        Complete your KYC verification to start selling on our platform. All information is secure and confidential.
      </Paragraph>

      {/* Verification Status */}
      {verificationStatus.submittedDate && (
        <Alert
          message={
            <Space>
              <span>KYC Status:</span>
              {getStatusTag(verificationStatus.status)}
            </Space>
          }
          description={
            <div>
              <p>Submitted on: {new Date(verificationStatus.submittedDate).toLocaleDateString()}</p>
              {verificationStatus.status === 'pending' && (
                <p>Your documents are under review. We'll notify you once the review is complete.</p>
              )}
              {verificationStatus.status === 'approved' && (
                <p>Your KYC verification is approved! You can now start selling.</p>
              )}
              {verificationStatus.status === 'rejected' && (
                <p>Some documents were rejected. Please review and resubmit.</p>
              )}
            </div>
          }
          type={verificationStatus.status === 'approved' ? 'success' : verificationStatus.status === 'rejected' ? 'error' : 'warning'}
          showIcon
          style={{ marginBottom: 24 }}
        />
      )}

      {/* Document Status Cards */}
      {verificationStatus.submittedDate && (
        <Row gutter={16} style={{ marginBottom: 24 }}>
          <Col span={6}>
            <Card size="small">
              <Space direction="vertical" align="center" style={{ width: '100%' }}>
                {getStatusIcon(verificationStatus.documents.business.status)}
                <Text strong>Business Documents</Text>
                {getStatusTag(verificationStatus.documents.business.status)}
                <Text type="secondary" style={{ fontSize: 12, textAlign: 'center' }}>
                  {verificationStatus.documents.business.comment}
                </Text>
              </Space>
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Space direction="vertical" align="center" style={{ width: '100%' }}>
                {getStatusIcon(verificationStatus.documents.identity.status)}
                <Text strong>Identity Proof</Text>
                {getStatusTag(verificationStatus.documents.identity.status)}
                <Text type="secondary" style={{ fontSize: 12, textAlign: 'center' }}>
                  {verificationStatus.documents.identity.comment}
                </Text>
              </Space>
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Space direction="vertical" align="center" style={{ width: '100%' }}>
                {getStatusIcon(verificationStatus.documents.tax.status)}
                <Text strong>Tax Documents</Text>
                {getStatusTag(verificationStatus.documents.tax.status)}
                <Text type="secondary" style={{ fontSize: 12, textAlign: 'center' }}>
                  {verificationStatus.documents.tax.comment}
                </Text>
              </Space>
            </Card>
          </Col>
          <Col span={6}>
            <Card size="small">
              <Space direction="vertical" align="center" style={{ width: '100%' }}>
                {getStatusIcon(verificationStatus.documents.bank.status)}
                <Text strong>Bank Details</Text>
                {getStatusTag(verificationStatus.documents.bank.status)}
                <Text type="secondary" style={{ fontSize: 12, textAlign: 'center' }}>
                  {verificationStatus.documents.bank.comment}
                </Text>
              </Space>
            </Card>
          </Col>
        </Row>
      )}

      {/* KYC Form */}
      <Card>
        <Steps current={currentStep} style={{ marginBottom: 32 }}>
          <Steps.Step title="Business Info" icon={<FileTextOutlined />} />
          <Steps.Step title="Identity Proof" icon={<IdcardOutlined />} />
          <Steps.Step title="Tax Documents" icon={<FileTextOutlined />} />
          <Steps.Step title="Bank Details" icon={<BankOutlined />} />
        </Steps>

        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          {/* Step 1: Business Information */}
          {currentStep === 0 && (
            <div>
              <Title level={4}>Business Information</Title>
              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="businessName"
                    label="Business Name"
                    rules={[{ required: true, message: 'Please enter business name' }]}
                  >
                    <Input placeholder="ABC Corporation" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="businessType"
                    label="Business Type"
                    rules={[{ required: true }]}
                  >
                    <Select placeholder="Select business type">
                      <Option value="sole">Sole Proprietorship</Option>
                      <Option value="partnership">Partnership</Option>
                      <Option value="llc">LLC</Option>
                      <Option value="corporation">Corporation</Option>
                    </Select>
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="registrationNumber"
                    label="Business Registration Number"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="REG-123456789" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="incorporationDate"
                    label="Date of Incorporation"
                    rules={[{ required: true }]}
                  >
                    <Input type="date" />
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item
                name="businessAddress"
                label="Business Address"
                rules={[{ required: true }]}
              >
                <TextArea rows={3} placeholder="123 Business Street, City, State, ZIP" />
              </Form.Item>

              <Form.Item
                label="Business Registration Documents"
                extra="Upload business license, incorporation certificate, or registration proof"
              >
                <Upload
                  fileList={businessDocsFiles}
                  onChange={({ fileList }) => setBusinessDocsFiles(fileList)}
                  beforeUpload={() => false}
                  maxCount={5}
                >
                  <Button icon={<UploadOutlined />}>Upload Documents (PDF, JPG, PNG)</Button>
                </Upload>
              </Form.Item>
            </div>
          )}

          {/* Step 2: Identity Proof */}
          {currentStep === 1 && (
            <div>
              <Title level={4}>Identity Verification</Title>
              <Alert
                message="Upload any government-issued ID proof"
                description="Passport, Driver's License, National ID Card, or Voter ID"
                type="info"
                showIcon
                style={{ marginBottom: 16 }}
              />

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="idType"
                    label="ID Type"
                    rules={[{ required: true }]}
                  >
                    <Select placeholder="Select ID type">
                      <Option value="passport">Passport</Option>
                      <Option value="drivers_license">Driver's License</Option>
                      <Option value="national_id">National ID Card</Option>
                      <Option value="voter_id">Voter ID</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="idNumber"
                    label="ID Number"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="Enter ID number" />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="fullName"
                    label="Full Name (as per ID)"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="John Doe" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="dateOfBirth"
                    label="Date of Birth"
                    rules={[{ required: true }]}
                  >
                    <Input type="date" />
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item
                label="Identity Document"
                extra="Upload clear scans/photos of both front and back"
              >
                <Upload
                  fileList={identityFiles}
                  onChange={({ fileList }) => setIdentityFiles(fileList)}
                  beforeUpload={() => false}
                  maxCount={3}
                  listType="picture-card"
                >
                  {identityFiles.length < 3 && (
                    <div>
                      <UploadOutlined />
                      <div style={{ marginTop: 8 }}>Upload</div>
                    </div>
                  )}
                </Upload>
              </Form.Item>
            </div>
          )}

          {/* Step 3: Tax Documents */}
          {currentStep === 2 && (
            <div>
              <Title level={4}>Tax Information</Title>
              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="taxId"
                    label="Tax ID / EIN"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="XX-XXXXXXX" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="gstNumber"
                    label="GST/VAT Number (if applicable)"
                  >
                    <Input placeholder="GST123456789" />
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item
                name="taxStatus"
                label="Tax Status"
                rules={[{ required: true }]}
              >
                <Select placeholder="Select tax status">
                  <Option value="registered">Registered for Tax</Option>
                  <Option value="exempt">Tax Exempt</Option>
                  <Option value="pending">Registration Pending</Option>
                </Select>
              </Form.Item>

              <Form.Item
                label="Tax Documents"
                extra="Upload tax registration certificate, GST certificate, or tax returns"
              >
                <Upload
                  fileList={taxFiles}
                  onChange={({ fileList }) => setTaxFiles(fileList)}
                  beforeUpload={() => false}
                  maxCount={5}
                >
                  <Button icon={<UploadOutlined />}>Upload Tax Documents</Button>
                </Upload>
              </Form.Item>
            </div>
          )}

          {/* Step 4: Bank Details */}
          {currentStep === 3 && (
            <div>
              <Title level={4}>Bank Account Information</Title>
              <Alert
                message="Payment Settlement Account"
                description="This account will be used to receive payments for your sales"
                type="info"
                showIcon
                style={{ marginBottom: 16 }}
              />

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="accountHolderName"
                    label="Account Holder Name"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="ABC Corporation" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="accountNumber"
                    label="Account Number"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="1234567890" />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="bankName"
                    label="Bank Name"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="Bank of America" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="routingNumber"
                    label="Routing Number / IFSC Code"
                    rules={[{ required: true }]}
                  >
                    <Input placeholder="XXXXXXXXX" />
                  </Form.Item>
                </Col>
              </Row>

              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    name="accountType"
                    label="Account Type"
                    rules={[{ required: true }]}
                  >
                    <Select placeholder="Select account type">
                      <Option value="checking">Checking</Option>
                      <Option value="savings">Savings</Option>
                      <Option value="business">Business</Option>
                    </Select>
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="swift"
                    label="SWIFT / BIC Code (for international)"
                  >
                    <Input placeholder="BOFAUS3N" />
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item
                label="Bank Documents"
                extra="Upload cancelled cheque, bank statement (last 3 months), or bank letter"
              >
                <Upload
                  fileList={bankFiles}
                  onChange={({ fileList }) => setBankFiles(fileList)}
                  beforeUpload={() => false}
                  maxCount={3}
                >
                  <Button icon={<UploadOutlined />}>Upload Bank Documents</Button>
                </Upload>
              </Form.Item>
            </div>
          )}

          {/* Navigation Buttons */}
          <Form.Item style={{ marginTop: 32 }}>
            <Space>
              {currentStep > 0 && (
                <Button onClick={handlePrev}>Previous</Button>
              )}
              {currentStep < 3 && (
                <Button type="primary" onClick={handleNext}>
                  Next
                </Button>
              )}
              {currentStep === 3 && (
                <Button type="primary" htmlType="submit">
                  Submit for Verification
                </Button>
              )}
            </Space>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};

export default VendorKYCPage;

vendor/rfq.__content:
import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Space,
  Tag,
  Input,
  Select,
  Drawer,
  Descriptions,
  message,
  Typography,
  Row,
  Col,
  Form,
  Modal,
  InputNumber,
  Spin,
  DatePicker,
} from 'antd';
import {
  SearchOutlined,
  EyeOutlined,
  MessageOutlined,
  SendOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { rfqAPI } from '@/services/api/rfq';
import type { RFQ, CreateQuotationData, Quotation } from '@/services/api/rfq';
import { useAuthStore } from '@/store/auth';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TextArea } = Input;

const VendorRFQPage: React.FC = () => {
  const { user } = useAuthStore();
  const [rfqs, setRfqs] = useState<RFQ[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedRFQ, setSelectedRFQ] = useState<RFQ | null>(null);
  const [quotations, setQuotations] = useState<Quotation[]>([]);
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [quoteModalVisible, setQuoteModalVisible] = useState(false);
  const [messageModalVisible, setMessageModalVisible] = useState(false);
  const [statusFilter, setStatusFilter] = useState<string>('open');
  const [searchText, setSearchText] = useState('');
  const [form] = Form.useForm();
  const [messageForm] = Form.useForm();

  // Fetch RFQs
  const fetchRFQs = async () => {
    setLoading(true);
    try {
      const filters: any = {};
      if (statusFilter !== 'all') {
        filters.status = statusFilter;
      }

      const response = await rfqAPI.getAll(filters);

      // Filter by search text if provided
      let filteredData = response.data;
      if (searchText) {
        filteredData = filteredData.filter(
          (rfq) =>
            rfq.rfqNumber.toLowerCase().includes(searchText.toLowerCase()) ||
            rfq.title.toLowerCase().includes(searchText.toLowerCase())
        );
      }

      setRfqs(filteredData);
    } catch (error) {
      message.error('Failed to fetch RFQs');
      console.error('Error fetching RFQs:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch quotations for selected RFQ
  const fetchQuotations = async (rfqId: string) => {
    try {
      const data = await rfqAPI.getQuotations(rfqId);
      // Filter to show only vendor's own quotations
      const vendorQuotations = data.filter((q) => q.vendorId === user?.id);
      setQuotations(vendorQuotations);
    } catch (error) {
      console.error('Error fetching quotations:', error);
    }
  };

  useEffect(() => {
    fetchRFQs();
  }, [statusFilter]);

  const getStatusColor = (status: RFQ['status']) => {
    const colors: Record<RFQ['status'], string> = {
      open: 'blue',
      quoted: 'orange',
      closed: 'success',
      cancelled: 'error',
    };
    return colors[status] || 'default';
  };

  const handleViewRFQ = async (rfq: RFQ) => {
    setSelectedRFQ(rfq);
    setDrawerVisible(true);
    // Fetch vendor's quotations for this RFQ
    await fetchQuotations(rfq.id);
  };

  const handleSendQuote = (rfq: RFQ) => {
    setSelectedRFQ(rfq);
    setQuoteModalVisible(true);
    form.setFieldsValue({
      quantity: rfq.quantity,
      budget: rfq.budget,
    });
  };

  const handleSubmitQuote = async (values: any) => {
    if (!selectedRFQ || !user?.id) return;

    try {
      const quotationData: CreateQuotationData = {
        rfqId: selectedRFQ.id,
        price: values.quotedPrice,
        quantity: values.quantity,
        moq: values.moq,
        deliveryTime: values.deliveryTime,
        validUntil: values.validUntil.toISOString(),
        notes: values.notes,
      };

      await rfqAPI.createQuotation(quotationData);
      message.success('Quote sent successfully');
      setQuoteModalVisible(false);
      form.resetFields();
      fetchRFQs(); // Refresh list
    } catch (error) {
      message.error('Failed to send quote');
      console.error('Error submitting quote:', error);
    }
  };

  const handleSendMessage = (rfq: RFQ) => {
    setSelectedRFQ(rfq);
    setMessageModalVisible(true);
  };

  const handleSubmitMessage = async (values: any) => {
    // This would integrate with a messaging API when available
    message.success('Message sent to customer');
    setMessageModalVisible(false);
    messageForm.resetFields();
  };

  const columns: ColumnsType<RFQ> = [
    {
      title: 'RFQ Number',
      dataIndex: 'rfqNumber',
      key: 'rfqNumber',
      render: (text: string) => <Text strong>{text}</Text>,
    },
    {
      title: 'Customer ID',
      dataIndex: 'customerId',
      key: 'customerId',
    },
    {
      title: 'Title',
      dataIndex: 'title',
      key: 'title',
      render: (text: string) => <Text>{text}</Text>,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
    },
    {
      title: 'Quantity',
      dataIndex: 'quantity',
      key: 'quantity',
      render: (qty: number) => <Text strong>{qty} units</Text>,
      sorter: (a, b) => a.quantity - b.quantity,
    },
    {
      title: 'Budget',
      dataIndex: 'budget',
      key: 'budget',
      render: (budget: number) => (
        <Text type="secondary">${budget.toFixed(2)}</Text>
      ),
    },
    {
      title: 'Quotations',
      dataIndex: 'quotationCount',
      key: 'quotationCount',
      render: (count: number) => (
        <Tag color={count > 0 ? 'blue' : 'default'}>{count} quotes</Tag>
      ),
    },
    {
      title: 'Deadline',
      dataIndex: 'deadline',
      key: 'deadline',
      render: (date: string) => dayjs(date).format('MMM DD, YYYY'),
      sorter: (a, b) => dayjs(a.deadline).unix() - dayjs(b.deadline).unix(),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: RFQ['status']) => (
        <Tag color={getStatusColor(status)}>{status.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Open', value: 'open' },
        { text: 'Quoted', value: 'quoted' },
        { text: 'Closed', value: 'closed' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="link"
            icon={<EyeOutlined />}
            onClick={() => handleViewRFQ(record)}
          >
            View
          </Button>
          {record.status === 'open' && (
            <Button
              type="link"
              icon={<SendOutlined />}
              onClick={() => handleSendQuote(record)}
            >
              Send Quote
            </Button>
          )}
          <Button
            type="link"
            icon={<MessageOutlined />}
            onClick={() => handleSendMessage(record)}
          >
            Message
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <Spin spinning={loading}>
      <div style={{ padding: 24 }}>
        <Card title={<Title level={3}>RFQ Management</Title>}>
          {/* Filters */}
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col xs={24} sm={12} md={8}>
              <Input
                placeholder="Search RFQs..."
                prefix={<SearchOutlined />}
                size="large"
                value={searchText}
                onChange={(e) => setSearchText(e.target.value)}
                onPressEnter={fetchRFQs}
              />
            </Col>
            <Col xs={24} sm={12} md={6}>
              <Select 
                placeholder="Status" 
                size="large" 
                style={{ width: '100%' }}
                value={statusFilter}
                onChange={setStatusFilter}
              >
                <Option value="all">All RFQs</Option>
                <Option value="open">Open</Option>
                <Option value="quoted">Quoted</Option>
                <Option value="closed">Closed</Option>
                <Option value="cancelled">Cancelled</Option>
              </Select>
            </Col>
          </Row>

          {/* Stats */}
          <Row gutter={16} style={{ marginBottom: 16 }}>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Total RFQs</Text>
                <Title level={3} style={{ margin: 0 }}>
                  {rfqs.length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Open Requests</Text>
                <Title level={3} style={{ margin: 0, color: '#1890ff' }}>
                  {rfqs.filter((r) => r.status === 'open').length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Quoted</Text>
                <Title level={3} style={{ margin: 0, color: '#faad14' }}>
                  {rfqs.filter((r) => r.status === 'quoted').length}
                </Title>
              </Card>
            </Col>
            <Col span={6}>
              <Card size="small">
                <Text type="secondary">Closed</Text>
                <Title level={3} style={{ margin: 0, color: '#52c41a' }}>
                  {rfqs.filter((r) => r.status === 'closed').length}
                </Title>
              </Card>
            </Col>
          </Row>

        {/* RFQ Table */}
        <Table
          columns={columns}
          dataSource={rfqs}
          rowKey="id"
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `Total ${total} RFQs`,
          }}
        />
      </Card>

      {/* RFQ Detail Drawer */}
      <Drawer
        title={`RFQ Details - ${selectedRFQ?.rfqNumber}`}
        placement="right"
        width={600}
        open={drawerVisible}
        onClose={() => setDrawerVisible(false)}
      >
        {selectedRFQ && (
          <Space direction="vertical" style={{ width: '100%' }} size="large">
            <Descriptions title="RFQ Information" bordered column={1} size="small">
              <Descriptions.Item label="RFQ Number">
                {selectedRFQ.rfqNumber}
              </Descriptions.Item>
              <Descriptions.Item label="Customer ID">
                {selectedRFQ.customerId}
              </Descriptions.Item>
              <Descriptions.Item label="Title">
                {selectedRFQ.title}
              </Descriptions.Item>
              <Descriptions.Item label="Category">
                {selectedRFQ.category}
              </Descriptions.Item>
              <Descriptions.Item label="Quantity">
                <Text strong>{selectedRFQ.quantity} units</Text>
              </Descriptions.Item>
              <Descriptions.Item label="Budget">
                {selectedRFQ.budget ? `$${selectedRFQ.budget.toFixed(2)}` : 'Not specified'}
              </Descriptions.Item>
              <Descriptions.Item label="Deadline">
                {dayjs(selectedRFQ.deadline).format('MMM DD, YYYY')}
              </Descriptions.Item>
              <Descriptions.Item label="Quotations Received">
                <Tag color="blue">{selectedRFQ.quotationCount} quotes</Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={getStatusColor(selectedRFQ.status)}>
                  {selectedRFQ.status.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Created">
                {dayjs(selectedRFQ.createdAt).format('MMM DD, YYYY hh:mm A')}
              </Descriptions.Item>
            </Descriptions>

            <Card title="Description" size="small">
              <Paragraph>{selectedRFQ.description}</Paragraph>
            </Card>

            {quotations.length > 0 && (
              <Card title="Your Quotations" size="small">
                {quotations.map((quote, index) => (
                  <div key={quote.id} style={{ marginBottom: 12, padding: 12, background: '#f5f5f5', borderRadius: 4 }}>
                    <Space direction="vertical" size="small" style={{ width: '100%' }}>
                      <Text strong>Quote #{index + 1}</Text>
                      <Text>Price: ${quote.price.toFixed(2)} (Qty: {quote.quantity})</Text>
                      <Text>MOQ: {quote.moq} units</Text>
                      <Text>Delivery: {quote.deliveryTime}</Text>
                      <Text>Valid Until: {dayjs(quote.validUntil).format('MMM DD, YYYY')}</Text>
                      <Tag color={quote.status === 'pending' ? 'orange' : quote.status === 'accepted' ? 'success' : 'error'}>
                        {quote.status?.toUpperCase()}
                      </Tag>
                      {quote.notes && <Text type="secondary">Notes: {quote.notes}</Text>}
                    </Space>
                  </div>
                ))}
              </Card>
            )}

            <Space>
              {selectedRFQ.status === 'open' && (
                <Button
                  type="primary"
                  icon={<SendOutlined />}
                  onClick={() => handleSendQuote(selectedRFQ)}
                >
                  Send Quote
                </Button>
              )}
              <Button
                icon={<MessageOutlined />}
                onClick={() => handleSendMessage(selectedRFQ)}
              >
                Send Message
              </Button>
            </Space>
          </Space>
        )}
      </Drawer>

      {/* Quote Modal */}
      <Modal
        title="Send Quote"
        open={quoteModalVisible}
        onCancel={() => setQuoteModalVisible(false)}
        footer={null}
        width={600}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmitQuote}>
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Customer Budget" name="budget">
                <InputNumber
                  prefix="$"
                  disabled
                  style={{ width: '100%' }}
                />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Requested Quantity" name="quantity">
                <InputNumber disabled style={{ width: '100%' }} suffix="units" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            name="quotedPrice"
            label="Your Quoted Price (Total)"
            rules={[{ required: true, message: 'Please enter quoted price' }]}
          >
            <InputNumber
              prefix="$"
              min={0}
              step={0.01}
              style={{ width: '100%' }}
              placeholder="Enter total price for the quantity"
            />
          </Form.Item>

          <Form.Item
            name="moq"
            label="Minimum Order Quantity (MOQ)"
            rules={[{ required: true, message: 'Please enter MOQ' }]}
          >
            <InputNumber
              min={1}
              style={{ width: '100%' }}
              placeholder="Enter MOQ"
              suffix="units"
            />
          </Form.Item>

          <Form.Item
            name="deliveryTime"
            label="Estimated Delivery Time"
            rules={[{ required: true, message: 'Please enter delivery time' }]}
          >
            <Input placeholder="e.g., 7-10 business days" />
          </Form.Item>

          <Form.Item
            name="validUntil"
            label="Quote Valid Until"
            rules={[{ required: true, message: 'Please select valid until date' }]}
          >
            <DatePicker 
              style={{ width: '100%' }}
              disabledDate={(current) => current && current < dayjs().endOf('day')}
            />
          </Form.Item>

          <Form.Item name="notes" label="Additional Notes">
            <TextArea rows={4} placeholder="Add any additional information..." />
          </Form.Item>

          <Form.Item>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => setQuoteModalVisible(false)}>Cancel</Button>
              <Button type="primary" htmlType="submit" icon={<SendOutlined />}>
                Send Quote
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Message Modal */}
      <Modal
        title="Send Message to Customer"
        open={messageModalVisible}
        onCancel={() => setMessageModalVisible(false)}
        footer={null}
      >
        <Form form={messageForm} layout="vertical" onFinish={handleSubmitMessage}>
          <Form.Item
            name="subject"
            label="Subject"
            rules={[{ required: true, message: 'Please enter subject' }]}
          >
            <Input placeholder="Enter subject" />
          </Form.Item>

          <Form.Item
            name="message"
            label="Message"
            rules={[{ required: true, message: 'Please enter message' }]}
          >
            <TextArea rows={6} placeholder="Type your message..." />
          </Form.Item>

          <Form.Item>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => setMessageModalVisible(false)}>Cancel</Button>
              <Button type="primary" htmlType="submit" icon={<MessageOutlined />}>
                Send Message
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
      </div>
    </Spin>
  );
};

export default VendorRFQPage;

admin/rfq.__content:
import { ProTable } from '@ant-design/pro-components';
import type { ProColumns } from '@ant-design/pro-components';
import { Button, Space, Tag, Drawer, Descriptions, message, Input } from 'antd';
import { EyeOutlined, MessageOutlined } from '@ant-design/icons';
import { useState } from 'react';

interface RFQType {
  id: string;
  rfqNumber: string;
  customer: string;
  title: string;
  category: string;
  quantity: number;
  budget: string;
  status: 'pending' | 'quoted' | 'approved' | 'rejected';
  quotesCount: number;
  createdAt: string;
}

const AdminRFQ = () => {
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [selectedRFQ, setSelectedRFQ] = useState<RFQType | null>(null);

  const statusColors = {
    pending: 'warning',
    quoted: 'processing',
    approved: 'success',
    rejected: 'error',
  };

  const columns: ProColumns<RFQType>[] = [
    {
      title: 'RFQ #',
      dataIndex: 'rfqNumber',
      key: 'rfqNumber',
      width: 130,
      fixed: 'left',
    },
    {
      title: 'Title',
      dataIndex: 'title',
      key: 'title',
      width: 250,
      ellipsis: true,
    },
    {
      title: 'Customer',
      dataIndex: 'customer',
      key: 'customer',
      width: 150,
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      width: 120,
    },
    {
      title: 'Quantity',
      dataIndex: 'quantity',
      key: 'quantity',
      width: 100,
      align: 'center',
    },
    {
      title: 'Budget',
      dataIndex: 'budget',
      key: 'budget',
      width: 120,
    },
    {
      title: 'Quotes',
      dataIndex: 'quotesCount',
      key: 'quotesCount',
      width: 100,
      align: 'center',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 120,
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Quoted', value: 'quoted' },
        { text: 'Approved', value: 'approved' },
        { text: 'Rejected', value: 'rejected' },
      ],
      render: (status: string) => (
        <Tag color={statusColors[status as keyof typeof statusColors]}>
          {status?.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 120,
      valueType: 'date',
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 150,
      render: (_, record) => (
        <Space>
          <Button type="link" icon={<EyeOutlined />} onClick={() => {
            setSelectedRFQ(record);
            setDrawerVisible(true);
          }}>
            View
          </Button>
          <Button type="link" icon={<MessageOutlined />}>Messages</Button>
        </Space>
      ),
    },
  ];

  const mockData: RFQType[] = [
    {
      id: '1',
      rfqNumber: 'RFQ-2024-001',
      customer: 'John Doe',
      title: 'Bulk order for office furniture',
      category: 'Furniture',
      quantity: 100,
      budget: '$10,000 - $15,000',
      status: 'pending',
      quotesCount: 3,
      createdAt: '2024-11-04',
    },
    {
      id: '2',
      rfqNumber: 'RFQ-2024-002',
      customer: 'Jane Smith',
      title: 'Electronic components wholesale',
      category: 'Electronics',
      quantity: 500,
      budget: '$25,000+',
      status: 'quoted',
      quotesCount: 7,
      createdAt: '2024-11-03',
    },
  ];

  return (
    <div>
      <ProTable<RFQType>
        columns={columns}
        dataSource={mockData}
        rowKey="id"
        search={{
          labelWidth: 'auto',
        }}
        headerTitle="RFQ Management"
        pagination={{
          defaultPageSize: 20,
        }}
        scroll={{ x: 1200 }}
      />

      <Drawer
        title="RFQ Details"
        placement="right"
        width={720}
        onClose={() => setDrawerVisible(false)}
        open={drawerVisible}
      >
        {selectedRFQ && (
          <div>
            <Descriptions title="RFQ Information" bordered column={2}>
              <Descriptions.Item label="RFQ Number">{selectedRFQ.rfqNumber}</Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={statusColors[selectedRFQ.status]}>
                  {selectedRFQ.status.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Customer" span={2}>{selectedRFQ.customer}</Descriptions.Item>
              <Descriptions.Item label="Title" span={2}>{selectedRFQ.title}</Descriptions.Item>
              <Descriptions.Item label="Category">{selectedRFQ.category}</Descriptions.Item>
              <Descriptions.Item label="Quantity">{selectedRFQ.quantity}</Descriptions.Item>
              <Descriptions.Item label="Budget" span={2}>{selectedRFQ.budget}</Descriptions.Item>
              <Descriptions.Item label="Quotes Received">{selectedRFQ.quotesCount}</Descriptions.Item>
              <Descriptions.Item label="Created">{selectedRFQ.createdAt}</Descriptions.Item>
            </Descriptions>

            <div style={{ marginTop: 24 }}>
              <h4>Requirements Description</h4>
              <p>Looking for high-quality office furniture including desks, chairs, and storage units for a new office setup. Need delivery within 2 weeks.</p>
            </div>

            <div style={{ marginTop: 24 }}>
              <h4>Vendor Quotes ({selectedRFQ.quotesCount})</h4>
              <Space direction="vertical" style={{ width: '100%' }}>
                <div style={{ padding: 16, border: '1px solid #d9d9d9', borderRadius: 4 }}>
                  <div><strong>Tech Furniture Ltd</strong> - $12,500</div>
                  <div style={{ fontSize: 12, color: '#666', marginTop: 4 }}>Quoted on 2024-11-04</div>
                </div>
              </Space>
            </div>
          </div>
        )}
      </Drawer>
    </div>
  );
};

export default AdminRFQ;

checkout.__content:
import { Row, Col, Card, Form, Input, Select, Radio, Button, Steps, Divider, Space, message, Spin } from 'antd';
import { CreditCardOutlined, HomeOutlined, ShoppingCartOutlined } from '@ant-design/icons';
import { useState, useEffect } from 'react';
import { useNavigate } from 'umi';
import { useCartStore } from '@/store/cartStore';
import { ordersAPI } from '@/services/api/orders';

const CheckoutPage = () => {
  const navigate = useNavigate();
  const { cart, loading: cartLoading, fetchCart } = useCartStore();
  const [currentStep, setCurrentStep] = useState(0);
  const [shippingForm] = Form.useForm();
  const [paymentForm] = Form.useForm();
  const [submitting, setSubmitting] = useState(false);
  const [shippingData, setShippingData] = useState<any>(null);

  useEffect(() => {
    fetchCart();
  }, []);

  const cartItems = cart?.items || [];
  const subtotal = cart?.subtotal || 0;
  const shipping = cart?.shipping || 0;
  const tax = cart?.tax || 0;
  const total = cart?.total || 0;

  const handleShippingSubmit = (values: any) => {
    setShippingData(values);
    setCurrentStep(1);
  };

  const handlePaymentSubmit = async (values: any) => {
    if (!cart || cartItems.length === 0) {
      message.error('Your cart is empty');
      return;
    }

    try {
      setSubmitting(true);

      // Prepare address
      const address = {
        firstName: shippingData.firstName,
        lastName: shippingData.lastName,
        address1: shippingData.address,
        city: shippingData.city,
        state: shippingData.city, // Using city as state for now
        zipCode: shippingData.zipCode,
        country: shippingData.country,
        phone: shippingData.phone,
      };

      // Prepare order data
      const orderData = {
        shippingAddress: address,
        billingAddress: address, // Same as shipping for now
        paymentMethod: values.paymentMethod,
        items: cartItems.map(item => ({
          productId: item.productId,
          quantity: item.quantity,
        })),
      };

      // Create order
      const order = await ordersAPI.create(orderData);

      message.success('Order placed successfully!');

      // Redirect to order details or orders list
      setTimeout(() => {
        navigate(`/customer/orders/${order.id}`);
      }, 1500);

    } catch (error: any) {
      message.error(error.message || 'Failed to place order');
    } finally {
      setSubmitting(false);
    }
  };

  if (cartLoading && !cart) {
    return (
      <div style={{ padding: '80px 0', textAlign: 'center' }}>
        <Spin size="large" />
      </div>
    );
  }

  if (!cartLoading && cartItems.length === 0) {
    return (
      <div style={{ padding: '80px 0', textAlign: 'center' }}>
        <h2>Your cart is empty</h2>
        <Button type="primary" onClick={() => navigate('/products')} style={{ marginTop: 16 }}>
          Continue Shopping
        </Button>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: 1200, margin: '0 auto', padding: '40px 24px' }}>
      <h1 style={{ marginBottom: 24 }}>Checkout</h1>

      <Steps
        current={currentStep}
        items={[
          { title: 'Shipping', icon: <HomeOutlined /> },
          { title: 'Payment', icon: <CreditCardOutlined /> },
          { title: 'Review', icon: <ShoppingCartOutlined /> },
        ]}
        style={{ marginBottom: 40 }}
      />

      <Row gutter={24}>
        <Col xs={24} lg={16}>
          {currentStep === 0 && (
            <Card title="Shipping Information">
              <Form form={shippingForm} layout="vertical" onFinish={handleShippingSubmit}>
                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item name="firstName" label="First Name" rules={[{ required: true }]}>
                      <Input size="large" />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item name="lastName" label="Last Name" rules={[{ required: true }]}>
                      <Input size="large" />
                    </Form.Item>
                  </Col>
                </Row>

                <Form.Item name="email" label="Email" rules={[{ required: true, type: 'email' }]}>
                  <Input size="large" />
                </Form.Item>

                <Form.Item name="phone" label="Phone" rules={[{ required: true }]}>
                  <Input size="large" />
                </Form.Item>

                <Form.Item name="address" label="Address" rules={[{ required: true }]}>
                  <Input.TextArea rows={3} />
                </Form.Item>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item name="city" label="City" rules={[{ required: true }]}>
                      <Input size="large" />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item name="zipCode" label="ZIP Code" rules={[{ required: true }]}>
                      <Input size="large" />
                    </Form.Item>
                  </Col>
                </Row>

                <Form.Item name="country" label="Country" rules={[{ required: true }]}>
                  <Select size="large">
                    <Select.Option value="us">United States</Select.Option>
                    <Select.Option value="ca">Canada</Select.Option>
                    <Select.Option value="uk">United Kingdom</Select.Option>
                  </Select>
                </Form.Item>

                <Form.Item>
                  <Button type="primary" htmlType="submit" size="large" block>
                    Continue to Payment
                  </Button>
                </Form.Item>
              </Form>
            </Card>
          )}

          {currentStep === 1 && (
            <Card title="Payment Method">
              <Form form={paymentForm} layout="vertical" onFinish={handlePaymentSubmit}>
                <Form.Item name="paymentMethod" label="Select Payment Method" rules={[{ required: true }]}>
                  <Radio.Group size="large">
                    <Space direction="vertical">
                      <Radio value="card">Credit/Debit Card</Radio>
                      <Radio value="wallet">Wallet</Radio>
                      <Radio value="cod">Cash on Delivery</Radio>
                    </Space>
                  </Radio.Group>
                </Form.Item>

                <Divider />

                <h4>Card Information</h4>
                <Form.Item name="cardNumber" label="Card Number" rules={[{ required: true }]}>
                  <Input size="large" placeholder="1234 5678 9012 3456" />
                </Form.Item>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item name="expiry" label="Expiry Date" rules={[{ required: true }]}>
                      <Input size="large" placeholder="MM/YY" />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item name="cvv" label="CVV" rules={[{ required: true }]}>
                      <Input size="large" placeholder="123" />
                    </Form.Item>
                  </Col>
                </Row>

                <Form.Item name="cardName" label="Cardholder Name" rules={[{ required: true }]}>
                  <Input size="large" />
                </Form.Item>

                <Space>
                  <Button size="large" onClick={() => setCurrentStep(0)} disabled={submitting}>
                    Back
                  </Button>
                  <Button type="primary" htmlType="submit" size="large" loading={submitting}>
                    Place Order
                  </Button>
                </Space>
              </Form>
            </Card>
          )}
        </Col>

        <Col xs={24} lg={8}>
          <Card title="Order Summary" style={{ position: 'sticky', top: 24 }}>
            {cartItems.map(item => (
              <div key={item.id} style={{ marginBottom: 16 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <span>{item.productName} (x{item.quantity})</span>
                  <span>${item.total.toFixed(2)}</span>
                </div>
              </div>
            ))}

            <Divider />

            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
              <span>Subtotal</span>
              <span>${subtotal.toFixed(2)}</span>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
              <span>Shipping</span>
              <span>${shipping.toFixed(2)}</span>
            </div>

            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
              <span>Tax</span>
              <span>${tax.toFixed(2)}</span>
            </div>

            <Divider />

            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: 18, fontWeight: 600 }}>
              <span>Total</span>
              <span style={{ color: '#B12704' }}>${total.toFixed(2)}</span>
            </div>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default CheckoutPage;

register.__content:
import { Form, Input, Button, Card, Select, Checkbox, message, Tabs } from 'antd';
import { UserOutlined, LockOutlined, MailOutlined, PhoneOutlined, ShopOutlined } from '@ant-design/icons';
import { useNavigate } from 'umi';
import { authAPI } from '@/services/api';

const { Option } = Select;

const RegisterPage = () => {
  const navigate = useNavigate();
  const [customerForm] = Form.useForm();
  const [vendorForm] = Form.useForm();

  const onCustomerRegister = async (values: any) => {
    try {
      await authAPI.register({ ...values, role: 'customer' });
      message.success('Registration successful! Please login.');
      navigate('/login');
    } catch (error: any) {
      message.error(error.response?.data?.message || 'Registration failed');
    }
  };

  const onVendorRegister = async (values: any) => {
    try {
      await authAPI.register({ ...values, role: 'vendor' });
      message.success('Vendor registration submitted! We will verify your account soon.');
      navigate('/login');
    } catch (error: any) {
      message.error(error.response?.data?.message || 'Registration failed');
    }
  };

  const customerTab = (
    <Form
      form={customerForm}
      name="customer-register"
      onFinish={onCustomerRegister}
      layout="vertical"
      autoComplete="off"
    >
      <Form.Item
        name="name"
        label="Full Name"
        rules={[{ required: true, message: 'Please enter your full name' }]}
      >
        <Input prefix={<UserOutlined />} placeholder="John Doe" size="large" />
      </Form.Item>

      <Form.Item
        name="email"
        label="Email"
        rules={[
          { required: true, message: 'Please enter your email' },
          { type: 'email', message: 'Please enter a valid email' }
        ]}
      >
        <Input prefix={<MailOutlined />} placeholder="john@example.com" size="large" />
      </Form.Item>

      <Form.Item
        name="phone"
        label="Phone Number"
        rules={[{ required: true, message: 'Please enter your phone number' }]}
      >
        <Input prefix={<PhoneOutlined />} placeholder="+1 234 567 8900" size="large" />
      </Form.Item>

      <Form.Item
        name="password"
        label="Password"
        rules={[
          { required: true, message: 'Please enter your password' },
          { min: 8, message: 'Password must be at least 8 characters' }
        ]}
      >
        <Input.Password prefix={<LockOutlined />} placeholder="" size="large" />
      </Form.Item>

      <Form.Item
        name="confirmPassword"
        label="Confirm Password"
        dependencies={['password']}
        rules={[
          { required: true, message: 'Please confirm your password' },
          ({ getFieldValue }) => ({
            validator(_, value) {
              if (!value || getFieldValue('password') === value) {
                return Promise.resolve();
              }
              return Promise.reject(new Error('Passwords do not match'));
            },
          }),
        ]}
      >
        <Input.Password prefix={<LockOutlined />} placeholder="" size="large" />
      </Form.Item>

      <Form.Item
        name="agreement"
        valuePropName="checked"
        rules={[
          { 
            validator: (_, value) => value 
              ? Promise.resolve() 
              : Promise.reject(new Error('Please accept the terms and conditions')) 
          }
        ]}
      >
        <Checkbox>
          I agree to the <a href="/terms">Terms and Conditions</a> and <a href="/privacy">Privacy Policy</a>
        </Checkbox>
      </Form.Item>

      <Form.Item>
        <Button type="primary" htmlType="submit" block size="large">
          Create Customer Account
        </Button>
      </Form.Item>
    </Form>
  );

  const vendorTab = (
    <Form
      form={vendorForm}
      name="vendor-register"
      onFinish={onVendorRegister}
      layout="vertical"
      autoComplete="off"
    >
      <Form.Item
        name="businessName"
        label="Business Name"
        rules={[{ required: true, message: 'Please enter your business name' }]}
      >
        <Input prefix={<ShopOutlined />} placeholder="ABC Company Ltd." size="large" />
      </Form.Item>

      <Form.Item
        name="name"
        label="Contact Person Name"
        rules={[{ required: true, message: 'Please enter contact person name' }]}
      >
        <Input prefix={<UserOutlined />} placeholder="John Doe" size="large" />
      </Form.Item>

      <Form.Item
        name="email"
        label="Business Email"
        rules={[
          { required: true, message: 'Please enter business email' },
          { type: 'email', message: 'Please enter a valid email' }
        ]}
      >
        <Input prefix={<MailOutlined />} placeholder="business@example.com" size="large" />
      </Form.Item>

      <Form.Item
        name="phone"
        label="Business Phone"
        rules={[{ required: true, message: 'Please enter business phone' }]}
      >
        <Input prefix={<PhoneOutlined />} placeholder="+1 234 567 8900" size="large" />
      </Form.Item>

      <Form.Item
        name="businessType"
        label="Business Type"
        rules={[{ required: true, message: 'Please select business type' }]}
      >
        <Select placeholder="Select business type" size="large">
          <Option value="manufacturer">Manufacturer</Option>
          <Option value="wholesaler">Wholesaler</Option>
          <Option value="distributor">Distributor</Option>
          <Option value="retailer">Retailer</Option>
        </Select>
      </Form.Item>

      <Form.Item
        name="password"
        label="Password"
        rules={[
          { required: true, message: 'Please enter password' },
          { min: 8, message: 'Password must be at least 8 characters' }
        ]}
      >
        <Input.Password prefix={<LockOutlined />} placeholder="" size="large" />
      </Form.Item>

      <Form.Item
        name="confirmPassword"
        label="Confirm Password"
        dependencies={['password']}
        rules={[
          { required: true, message: 'Please confirm password' },
          ({ getFieldValue }) => ({
            validator(_, value) {
              if (!value || getFieldValue('password') === value) {
                return Promise.resolve();
              }
              return Promise.reject(new Error('Passwords do not match'));
            },
          }),
        ]}
      >
        <Input.Password prefix={<LockOutlined />} placeholder="" size="large" />
      </Form.Item>

      <Form.Item
        name="agreement"
        valuePropName="checked"
        rules={[
          { 
            validator: (_, value) => value 
              ? Promise.resolve() 
              : Promise.reject(new Error('Please accept the terms')) 
          }
        ]}
      >
        <Checkbox>
          I agree to the <a href="/vendor-terms">Vendor Terms</a> and <a href="/privacy">Privacy Policy</a>
        </Checkbox>
      </Form.Item>

      <Form.Item>
        <Button type="primary" htmlType="submit" block size="large">
          Register as Vendor
        </Button>
      </Form.Item>
    </Form>
  );

  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '40px 20px',
    }}>
      <Card 
        title={
          <div style={{ textAlign: 'center' }}>
            <h2 style={{ margin: 0 }}>Create Account</h2>
            <p style={{ color: '#666', margin: '8px 0 0 0' }}>Join Groow - India's Leading B2B & B2C Marketplace</p>
          </div>
        }
        style={{ width: '100%', maxWidth: 500 }}
      >
        <Tabs
          defaultActiveKey="customer"
          centered
          items={[
            {
              key: 'customer',
              label: (
                <span>
                  <UserOutlined />
                  Customer
                </span>
              ),
              children: customerTab,
            },
            {
              key: 'vendor',
              label: (
                <span>
                  <ShopOutlined />
                  Vendor/Business
                </span>
              ),
              children: vendorTab,
            },
          ]}
        />

        <div style={{ textAlign: 'center', marginTop: 16 }}>
          Already have an account? <a onClick={() => navigate('/login')}>Sign in</a>
        </div>
      </Card>
    </div>
  );
};

export default RegisterPage;

