            <Tag color="green">DELIVERED</Tag>
          </Select.Option>
          <Select.Option value="cancelled">
            <Tag color="red">CANCELLED</Tag>
          </Select.Option>
        </Select>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 180,
      valueType: 'dateTime',
      sorter: true,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
