          <Row gutter={16}>
            <Col span={12}>
              <Form.Item 
                name="category" 
                label="Category" 
                rules={[{ required: true, message: 'Please select category' }]}
              >
                <Select placeholder="Select category">
                  <Select.Option value="shipping">Shipping</Select.Option>
                  <Select.Option value="returns">Returns</Select.Option>
