import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Form,
  Input,
  Button,
  Table,
  Tag,
  Space,
  Select,
  InputNumber,
  Upload,
  Modal,
  message,
  Divider,
  Statistic,
  Switch,
  Alert,
} from 'antd';
import {
  InboxOutlined,
  UploadOutlined,
  FileExcelOutlined,
  CloudUploadOutlined,
  CheckCircleOutlined,
  WarningOutlined,
  DeleteOutlined,
  EditOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import type { UploadProps } from 'antd';
import dayjs from 'dayjs';

const { Title, Text, Paragraph } = Typography;
const { Dragger } = Upload;
const { Option } = Select;

interface ImportedProduct {
  id: number;
  sku: string;
  name: string;
  category: string;
  price: number;
  stock: number;
  status: 'success' | 'warning' | 'error';
  issues: string[];
}

interface ImportHistory {
  id: number;
  filename: string;
  totalRows: number;
  successCount: number;
  errorCount: number;
  timestamp: string;
  status: 'completed' | 'processing' | 'failed';
}

const mockImportedProducts: ImportedProduct[] = [
  {
    id: 1,
    sku: 'ELEC-001',
    name: 'Wireless Headphones',
    category: 'Electronics',
    price: 79.99,
    stock: 150,
    status: 'success',
    issues: [],
  },
  {
    id: 2,
    sku: 'FASH-002',
    name: 'Leather Jacket',
    category: 'Fashion',
    price: 199.99,
    stock: 0,
    status: 'warning',
    issues: ['Stock is 0'],
  },
  {
    id: 3,
    sku: 'HOME-003',
    name: 'Office Chair',
    category: 'Home & Garden',
    price: -50.00,
    stock: 45,
    status: 'error',
    issues: ['Invalid price (negative value)', 'Missing product description'],
  },
  {
    id: 4,
    sku: 'ELEC-004',
