              <div style={{ marginBottom: 24 }}>
                <h4>Brand</h4>
                <Checkbox.Group
                  options={[
                    { label: 'Apple', value: 'apple' },
                    { label: 'Samsung', value: 'samsung' },
                    { label: 'Sony', value: 'sony' },
                  ]}
                  onChange={() => {}}
                />
              </div>

              <div>
                <h4>Rating</h4>
                <Checkbox.Group
                  options={[
                    { label: ' 5 Stars', value: '5' },
                    { label: ' 4 Stars & Up', value: '4' },
                    { label: ' 3 Stars & Up', value: '3' },
