      dataIndex: 'joinedAt',
      key: 'joinedAt',
      width: 120,
      valueType: 'date',
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 200,
      render: (_, record) => (
        <Space>
          <Button type="link" icon={<EyeOutlined />}>View</Button>
          {record.status === 'active' ? (
            <Button type="link" danger icon={<StopOutlined />} onClick={() => handleSuspend(record.id)}>
              Suspend
            </Button>
          ) : (
            <Button type="link" icon={<CheckCircleOutlined />} onClick={() => handleActivate(record.id)}>
              Activate
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const mockData: CustomerType[] = [
    {
      id: '1',
      name: 'Alice Johnson',
      email: 'alice@example.com',
      phone: '+1234567890',
      status: 'active',
      ordersCount: 15,
      totalSpent: 3456.78,
      lastOrder: '2024-11-03',
      joinedAt: '2024-06-15',
    },
    {
      id: '2',
      name: 'Bob Williams',
      email: 'bob@example.com',
      phone: '+1234567891',
      status: 'active',
      ordersCount: 8,
      totalSpent: 1234.56,
      lastOrder: '2024-10-28',
      joinedAt: '2024-08-20',
    },
  ];

  const handleSuspend = (id: string) => {
    message.success('Customer suspended');
  };

  const handleActivate = (id: string) => {
    message.success('Customer activated');
  };

  return (
    <ProTable<CustomerType>
      columns={columns}
      dataSource={mockData}
      rowKey="id"
      search={{
        labelWidth: 'auto',
      }}
      headerTitle="Customer Management"
      pagination={{
        defaultPageSize: 20,
        showSizeChanger: true,
      }}
    />
  );
};

export default AdminCustomers;

