import { ProTable } from '@ant-design/pro-components';
import type { ProColumns } from '@ant-design/pro-components';
import { Tag, Button, Space, Badge, Drawer, Descriptions, Timeline, message, Select } from 'antd';
import { EyeOutlined, PrinterOutlined, CheckCircleOutlined } from '@ant-design/icons';
import { useState, useRef } from 'react';
import type { ActionType } from '@ant-design/pro-components';
import { ORDER_STATUS_COLORS } from '@/config/constants';
import { ordersAPI, type Order } from '@/services/api/orders';

const AdminOrders = () => {
  const actionRef = useRef<ActionType>();
  const [drawerVisible, setDrawerVisible] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [updatingStatus, setUpdatingStatus] = useState(false);

  const handleStatusChange = async (orderId: string, newStatus: string) => {
    try {
      setUpdatingStatus(true);
      await ordersAPI.updateStatus(orderId, newStatus as any);
      message.success('Order status updated successfully');
      actionRef.current?.reload();
      if (selectedOrder?.id === orderId) {
        const updatedOrder = await ordersAPI.getById(orderId);
        setSelectedOrder(updatedOrder);
      }
    } catch (error: any) {
      message.error(error.message || 'Failed to update order status');
    } finally {
      setUpdatingStatus(false);
    }
  };

  const columns: ProColumns<Order>[] = [
    {
      title: 'Order #',
      dataIndex: 'orderNumber',
      key: 'orderNumber',
      fixed: 'left',
      width: 140,
      render: (text) => <a>{text}</a>,
    },
    {
      title: 'Customer ID',
      dataIndex: 'customerId',
      key: 'customerId',
      width: 180,
      copyable: true,
    },
    {
      title: 'Vendor ID',
      dataIndex: 'vendorId',
      key: 'vendorId',
      width: 180,
    },
    {
      title: 'Items',
      dataIndex: 'items',
      key: 'items',
      width: 80,
      align: 'center',
      render: (_: any, record: Order) => <Badge count={record.items?.length || 0} showZero color="#1890ff" />,
    },
    {
      title: 'Total',
      dataIndex: 'total',
      key: 'total',
      width: 120,
      render: (_: any, record: Order) => `$${record.total?.toFixed(2) || '0.00'}`,
      sorter: true,
    },
    {
      title: 'Payment',
      dataIndex: 'paymentMethod',
      key: 'paymentMethod',
      width: 120,
      filters: [
        { text: 'Card', value: 'card' },
        { text: 'Wallet', value: 'wallet' },
        { text: 'COD', value: 'cod' },
      ],
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 200,
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Confirmed', value: 'confirmed' },
        { text: 'Processing', value: 'processing' },
        { text: 'Shipped', value: 'shipped' },
        { text: 'Delivered', value: 'delivered' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      render: (_: any, record: Order) => (
        <Select
          value={record.status}
          onChange={(value) => handleStatusChange(record.id, value)}
          loading={updatingStatus}
          style={{ width: 140 }}
        >
          <Select.Option value="pending">
            <Tag color="blue">PENDING</Tag>
          </Select.Option>
          <Select.Option value="confirmed">
            <Tag color="cyan">CONFIRMED</Tag>
          </Select.Option>
          <Select.Option value="processing">
            <Tag color="orange">PROCESSING</Tag>
          </Select.Option>
          <Select.Option value="shipped">
            <Tag color="purple">SHIPPED</Tag>
          </Select.Option>
          <Select.Option value="delivered">
            <Tag color="green">DELIVERED</Tag>
          </Select.Option>
          <Select.Option value="cancelled">
            <Tag color="red">CANCELLED</Tag>
          </Select.Option>
        </Select>
      ),
    },
    {
      title: 'Date',
      dataIndex: 'createdAt',
      key: 'createdAt',
      width: 180,
      valueType: 'dateTime',
      sorter: true,
    },
    {
      title: 'Actions',
      key: 'actions',
      fixed: 'right',
      width: 180,
      render: (_, record) => (
        <Space size="small">
          <Button 
            type="link" 
            icon={<EyeOutlined />}
            onClick={() => {
              setSelectedOrder(record);
              setDrawerVisible(true);
            }}
          >
            View
          </Button>
          <Button type="link" icon={<PrinterOutlined />}>
            Print
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <div>
      <ProTable<Order>
        columns={columns}
        actionRef={actionRef}
        request={async (params, sort, filter) => {
          try {
            const { current, pageSize, status, paymentMethod } = params;

            const response = await ordersAPI.getAll({
              page: current,
              limit: pageSize,
              status: filter?.status?.[0] as string,
            });

            return {
              data: response.data,
              success: true,
              total: response.total,
            };
          } catch (error) {
            message.error('Failed to load orders');
            return {
              data: [],
              success: false,
              total: 0,
            };
          }
        }}
        rowKey="id"
        pagination={{
          defaultPageSize: 20,
          showSizeChanger: true,
          showQuickJumper: true,
          showTotal: (total) => `Total ${total} orders`,
        }}
        search={{
          labelWidth: 'auto',
        }}
        dateFormatter="string"
        headerTitle="Order Management"
        toolBarRender={() => [
          <Button key="export">Export</Button>,
        ]}
        scroll={{ x: 1400 }}
      />

      <Drawer
        title="Order Details"
        placement="right"
        width={720}
        onClose={() => setDrawerVisible(false)}
        open={drawerVisible}
      >
        {selectedOrder && (
          <div>
            <Descriptions title="Order Information" bordered column={2}>
              <Descriptions.Item label="Order Number">{selectedOrder.orderNumber}</Descriptions.Item>
              <Descriptions.Item label="Status">
                <Tag color={ORDER_STATUS_COLORS[selectedOrder.status as keyof typeof ORDER_STATUS_COLORS] || 'blue'}>
                  {selectedOrder.status?.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Customer ID">{selectedOrder.customerId}</Descriptions.Item>
              <Descriptions.Item label="Vendor ID">{selectedOrder.vendorId}</Descriptions.Item>
              <Descriptions.Item label="Items Count">{selectedOrder.items?.length || 0}</Descriptions.Item>
              <Descriptions.Item label="Total">${selectedOrder.total?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Subtotal">${selectedOrder.subtotal?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Tax">${selectedOrder.tax?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Shipping">${selectedOrder.shipping?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Discount">${selectedOrder.discount?.toFixed(2)}</Descriptions.Item>
              <Descriptions.Item label="Payment Method">{selectedOrder.paymentMethod}</Descriptions.Item>
              <Descriptions.Item label="Payment Status">
                <Tag color={selectedOrder.paymentStatus === 'paid' ? 'green' : 'orange'}>
                  {selectedOrder.paymentStatus?.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Date">{new Date(selectedOrder.createdAt).toLocaleString()}</Descriptions.Item>
              {selectedOrder.trackingNumber && (
                <Descriptions.Item label="Tracking Number">{selectedOrder.trackingNumber}</Descriptions.Item>
              )}
            </Descriptions>

            {/* Order Items */}
            <div style={{ marginTop: 24 }}>
              <h3>Order Items</h3>
              {selectedOrder.items?.map((item, index) => (
                <div key={item.id} style={{ 
                  padding: '12px', 
                  background: '#f5f5f5', 
                  marginBottom: 8,
                  borderRadius: 4 
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <div>
                      <strong>{item.productName}</strong>
                      <div style={{ color: '#666', fontSize: 12 }}>
                        Qty: {item.quantity}  ${item.price.toFixed(2)}
                      </div>
                    </div>
                    <div style={{ fontWeight: 600 }}>
                      ${item.total.toFixed(2)}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Shipping Address */}
            <div style={{ marginTop: 24 }}>
              <h3>Shipping Address</h3>
              <p>
                {selectedOrder.shippingAddress?.firstName} {selectedOrder.shippingAddress?.lastName}<br />
                {selectedOrder.shippingAddress?.address1}<br />
                {selectedOrder.shippingAddress?.address2 && <>{selectedOrder.shippingAddress.address2}<br /></>}
                {selectedOrder.shippingAddress?.city}, {selectedOrder.shippingAddress?.state} {selectedOrder.shippingAddress?.zipCode}<br />
                {selectedOrder.shippingAddress?.country}<br />
                Phone: {selectedOrder.shippingAddress?.phone}
              </p>
            </div>

            <div style={{ marginTop: 24 }}>
              <h3>Order Timeline</h3>
              <Timeline
                items={[
                  {
                    color: 'green',
                    children: `Order placed - ${new Date(selectedOrder.createdAt).toLocaleString()}`,
                  },
                  selectedOrder.status !== 'pending' && {
                    color: 'blue',
                    children: `Order ${selectedOrder.status} - ${new Date(selectedOrder.updatedAt).toLocaleString()}`,
                  },
                  selectedOrder.trackingNumber && {
                    color: 'purple',
                    children: `Tracking: ${selectedOrder.trackingNumber}`,
                  },
                ].filter(Boolean) as any}
              />
            </div>

            <div style={{ marginTop: 24 }}>
              <Space>
                <Select
                  value={selectedOrder.status}
                  onChange={(value) => handleStatusChange(selectedOrder.id, value)}
                  loading={updatingStatus}
                  style={{ width: 200 }}
                >
                  <Select.Option value="pending">Pending</Select.Option>
                  <Select.Option value="confirmed">Confirmed</Select.Option>
                  <Select.Option value="processing">Processing</Select.Option>
                  <Select.Option value="shipped">Shipped</Select.Option>
                  <Select.Option value="delivered">Delivered</Select.Option>
                  <Select.Option value="cancelled">Cancelled</Select.Option>
                </Select>
                <Button icon={<PrinterOutlined />}>Print Invoice</Button>
              </Space>
            </div>
          </div>
        )}
      </Drawer>
    </div>
  );
};

export default AdminOrders;

_temp/checkout/payment-integration.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Radio,
  Space,
  Button,
  Form,
  Input,
  Select,
  Divider,
  Alert,
  Switch,
  Tag,
  Statistic,
  message,
  Modal,
  List,
  Avatar,
} from 'antd';
import {
  CreditCardOutlined,
  BankOutlined,
  DollarOutlined,
  SafetyOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  WarningOutlined,
  PayCircleOutlined,
  WalletOutlined,
} from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;

interface PaymentMethod {
  id: string;
  type: 'card' | 'paypal' | 'bank' | 'wallet' | 'cod';
  name: string;
  description: string;
  icon: React.ReactNode;
  enabled: boolean;
  processingFee: number;
  processingTime: string;
}

interface SavedCard {
  id: number;
  type: 'visa' | 'mastercard' | 'amex';
  last4: string;
  expiryMonth: number;
  expiryYear: number;
  isDefault: boolean;
  holderName: string;
}

const paymentMethods: PaymentMethod[] = [
  {
    id: 'stripe',
    type: 'card',
    name: 'Credit/Debit Card',
    description: 'Pay securely with Stripe. We accept Visa, Mastercard, and American Express.',
    icon: <CreditCardOutlined />,
    enabled: true,
    processingFee: 2.9,
    processingTime: 'Instant',
  },
  {
    id: 'paypal',
    type: 'paypal',
    name: 'PayPal',
    description: 'Pay with your PayPal account or PayPal Credit.',
    icon: <PayCircleOutlined />,
    enabled: true,
    processingFee: 3.49,
    processingTime: 'Instant',
  },
  {
    id: 'bank_transfer',
    type: 'bank',
    name: 'Bank Transfer',
    description: 'Direct bank transfer. Order will be processed after payment verification.',
    icon: <BankOutlined />,
    enabled: true,
    processingFee: 0,
    processingTime: '1-3 business days',
  },
  {
    id: 'wallet',
    type: 'wallet',
    name: 'Digital Wallet',
    description: 'Use your account balance for instant payment.',
    icon: <WalletOutlined />,
    enabled: true,
    processingFee: 0,
    processingTime: 'Instant',
  },
  {
    id: 'cod',
    type: 'cod',
    name: 'Cash on Delivery',
    description: 'Pay when your order is delivered. Additional fees may apply.',
    icon: <DollarOutlined />,
    enabled: true,
    processingFee: 5,
    processingTime: 'On delivery',
  },
];

const mockSavedCards: SavedCard[] = [
  {
    id: 1,
    type: 'visa',
    last4: '4242',
    expiryMonth: 12,
    expiryYear: 2025,
    isDefault: true,
    holderName: 'John Doe',
  },
  {
    id: 2,
    type: 'mastercard',
    last4: '5555',
    expiryMonth: 8,
    expiryYear: 2026,
    isDefault: false,
    holderName: 'John Doe',
  },
];

const PaymentIntegrationPage: React.FC = () => {
  const [selectedMethod, setSelectedMethod] = useState<string>('stripe');
  const [savedCards, setSavedCards] = useState<SavedCard[]>(mockSavedCards);
  const [selectedCard, setSelectedCard] = useState<number | null>(1);
  const [saveCard, setSaveCard] = useState(false);
  const [amount] = useState(299.99);
  const [form] = Form.useForm();

  const currentMethod = paymentMethods.find((m) => m.id === selectedMethod);
  const processingFee = currentMethod ? (amount * currentMethod.processingFee) / 100 : 0;
  const totalAmount = amount + processingFee;

  const handlePayment = () => {
    if (selectedMethod === 'stripe') {
      if (selectedCard) {
        // Use saved card
        message.success('Payment processed successfully with saved card!');
      } else {
        // New card
        form.validateFields().then((values) => {
          message.success('Payment processed successfully!');
          if (saveCard) {
            const newCard: SavedCard = {
              id: Date.now(),
              type: 'visa',
              last4: values.cardNumber.slice(-4),
              expiryMonth: parseInt(values.expiry.split('/')[0]),
              expiryYear: parseInt('20' + values.expiry.split('/')[1]),
              isDefault: savedCards.length === 0,
              holderName: values.cardHolder,
            };
            setSavedCards([...savedCards, newCard]);
          }
        });
      }
    } else if (selectedMethod === 'paypal') {
      // Simulate PayPal redirect
      message.info('Redirecting to PayPal...');
      setTimeout(() => {
        message.success('PayPal payment completed!');
      }, 2000);
    } else {
      message.success(`Payment initiated via ${currentMethod?.name}`);
    }
  };

  const handleSetDefaultCard = (id: number) => {
    setSavedCards((prev) =>
      prev.map((card) => ({ ...card, isDefault: card.id === id }))
    );
    message.success('Default payment method updated');
  };

  const handleDeleteCard = (id: number) => {
    Modal.confirm({
      title: 'Delete Card',
      content: 'Are you sure you want to remove this card?',
      okText: 'Delete',
      okType: 'danger',
      onOk: () => {
        setSavedCards((prev) => prev.filter((card) => card.id !== id));
        if (selectedCard === id) setSelectedCard(null);
        message.success('Card removed successfully');
      },
    });
  };

  const getCardIcon = (type: string) => {
    switch (type) {
      case 'visa':
        return ' Visa';
      case 'mastercard':
        return ' Mastercard';
      case 'amex':
        return ' Amex';
      default:
        return '';
    }
  };

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <CreditCardOutlined style={{ color: '#1890ff' }} /> Payment Integration
        </Title>
        <Paragraph type="secondary">
          Secure payment processing with multiple payment gateways
        </Paragraph>
      </div>

      <Alert
        message="Secure Payments"
        description="All transactions are encrypted and secure. We never store your full card details."
        type="info"
        showIcon
        icon={<SafetyOutlined />}
        style={{ marginBottom: 24 }}
      />

      <Row gutter={16}>
        {/* Payment Method Selection */}
        <Col xs={24} lg={16}>
          <Card title="Select Payment Method" style={{ marginBottom: 16 }}>
            <Radio.Group
              value={selectedMethod}
              onChange={(e) => setSelectedMethod(e.target.value)}
              style={{ width: '100%' }}
            >
              <Space direction="vertical" style={{ width: '100%' }} size="middle">
                {paymentMethods.map((method) => (
                  <Card
                    key={method.id}
                    size="small"
                    hoverable
                    style={{
                      border: selectedMethod === method.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                      background: selectedMethod === method.id ? '#e6f7ff' : 'white',
                    }}
                  >
                    <Radio value={method.id} style={{ width: '100%' }}>
                      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Space>
                          <div style={{ fontSize: 24, color: '#1890ff' }}>{method.icon}</div>
                          <div>
                            <Text strong>{method.name}</Text>
                            <div>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {method.description}
                              </Text>
                            </div>
                          </div>
                        </Space>
                        <Space direction="vertical" align="end" size={0}>
                          <Tag color={method.enabled ? 'green' : 'red'}>
                            {method.enabled ? 'Available' : 'Unavailable'}
                          </Tag>
                          <Text type="secondary" style={{ fontSize: 11 }}>
                            Fee: {method.processingFee}%
                          </Text>
                        </Space>
                      </div>
                    </Radio>
                  </Card>
                ))}
              </Space>
            </Radio.Group>
          </Card>

          {/* Payment Details */}
          {selectedMethod === 'stripe' && (
            <Card title="Payment Details">
              {savedCards.length > 0 && (
                <>
                  <Text strong style={{ display: 'block', marginBottom: 12 }}>
                    Saved Cards
                  </Text>
                  <Radio.Group
                    value={selectedCard}
                    onChange={(e) => setSelectedCard(e.target.value)}
                    style={{ width: '100%', marginBottom: 16 }}
                  >
                    <Space direction="vertical" style={{ width: '100%' }}>
                      {savedCards.map((card) => (
                        <Card
                          key={card.id}
                          size="small"
                          style={{
                            border: selectedCard === card.id ? '2px solid #1890ff' : '1px solid #d9d9d9',
                          }}
                        >
                          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Radio value={card.id}>
                              <Space>
                                <Text>{getCardIcon(card.type)}</Text>
                                <Text> {card.last4}</Text>
                                <Text type="secondary">
                                  {card.expiryMonth}/{card.expiryYear}
                                </Text>
                                {card.isDefault && <Tag color="blue">Default</Tag>}
                              </Space>
                            </Radio>
                            <Space>
                              {!card.isDefault && (
                                <Button size="small" onClick={() => handleSetDefaultCard(card.id)}>
                                  Set Default
                                </Button>
                              )}
                              <Button size="small" danger onClick={() => handleDeleteCard(card.id)}>
                                Remove
                              </Button>
                            </Space>
                          </div>
                        </Card>
                      ))}
                      <Radio value={null}>
                        <Text type="secondary">Use a different card</Text>
                      </Radio>
                    </Space>
                  </Radio.Group>
                  <Divider />
                </>
              )}

              {selectedCard === null && (
                <Form form={form} layout="vertical">
                  <Form.Item
                    name="cardNumber"
                    label="Card Number"
                    rules={[
                      { required: true, message: 'Please enter card number' },
                      { pattern: /^\d{16}$/, message: 'Invalid card number' },
                    ]}
                  >
                    <Input
                      prefix={<CreditCardOutlined />}
                      placeholder="1234 5678 9012 3456"
                      maxLength={16}
                    />
                  </Form.Item>
                  <Form.Item
                    name="cardHolder"
                    label="Card Holder Name"
                    rules={[{ required: true, message: 'Please enter card holder name' }]}
                  >
                    <Input placeholder="John Doe" />
                  </Form.Item>
                  <Row gutter={16}>
                    <Col span={12}>
                      <Form.Item
                        name="expiry"
                        label="Expiry Date"
                        rules={[
                          { required: true, message: 'Required' },
                          { pattern: /^\d{2}\/\d{2}$/, message: 'MM/YY' },
                        ]}
                      >
                        <Input placeholder="MM/YY" maxLength={5} />
                      </Form.Item>
                    </Col>
                    <Col span={12}>
                      <Form.Item
                        name="cvv"
                        label="CVV"
                        rules={[
                          { required: true, message: 'Required' },
                          { pattern: /^\d{3,4}$/, message: 'Invalid CVV' },
                        ]}
                      >
                        <Input.Password placeholder="123" maxLength={4} />
                      </Form.Item>
                    </Col>
                  </Row>
                  <Form.Item>
                    <Switch checked={saveCard} onChange={setSaveCard} />
                    <Text style={{ marginLeft: 8 }}>Save card for future payments</Text>
                  </Form.Item>
                </Form>
              )}
            </Card>
          )}

          {selectedMethod === 'paypal' && (
            <Card>
              <Space direction="vertical" align="center" style={{ width: '100%', padding: '40px 0' }}>
                <PayCircleOutlined style={{ fontSize: 64, color: '#0070ba' }} />
                <Title level={4}>PayPal Checkout</Title>
                <Paragraph type="secondary" style={{ textAlign: 'center' }}>
                  You'll be redirected to PayPal to complete your payment securely
                </Paragraph>
              </Space>
            </Card>
          )}

          {selectedMethod === 'bank_transfer' && (
            <Card>
              <Alert
                message="Bank Transfer Instructions"
                description={
                  <div style={{ marginTop: 12 }}>
                    <Text strong>Bank Name:</Text> Example Bank
                    <br />
                    <Text strong>Account Number:</Text> 1234567890
                    <br />
                    <Text strong>Routing Number:</Text> 987654321
                    <br />
                    <Text strong>Reference:</Text> Your Order ID
                    <br />
                    <br />
                    <Text type="secondary">
                      Please include your order ID in the transfer reference. Your order will be
                      processed once payment is verified.
                    </Text>
                  </div>
                }
                type="warning"
                showIcon
              />
            </Card>
          )}

          {selectedMethod === 'wallet' && (
            <Card>
              <Space direction="vertical" style={{ width: '100%' }}>
                <Statistic
                  title="Available Balance"
                  value={1250.50}
                  prefix={<WalletOutlined />}
                  precision={2}
                  valueStyle={{ color: '#52c41a' }}
                />
                {amount > 1250.50 ? (
                  <Alert
                    message="Insufficient Balance"
                    description="You don't have enough balance. Please add funds to your wallet."
                    type="error"
                    showIcon
                  />
                ) : (
                  <Alert
                    message="Sufficient Balance"
                    description="You can complete this payment with your wallet balance."
                    type="success"
                    showIcon
                  />
                )}
              </Space>
            </Card>
          )}
        </Col>

        {/* Order Summary */}
        <Col xs={24} lg={8}>
          <Card title="Order Summary" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="middle">
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text>Subtotal</Text>
                <Text strong>${amount.toFixed(2)}</Text>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text>Processing Fee ({currentMethod?.processingFee}%)</Text>
                <Text>${processingFee.toFixed(2)}</Text>
              </div>
              <Divider style={{ margin: '8px 0' }} />
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <Text strong style={{ fontSize: 18 }}>Total</Text>
                <Text strong style={{ fontSize: 18, color: '#1890ff' }}>
                  ${totalAmount.toFixed(2)}
                </Text>
              </div>
              <Button
                type="primary"
                size="large"
                block
                icon={<SafetyOutlined />}
                onClick={handlePayment}
              >
                Pay ${totalAmount.toFixed(2)}
              </Button>
              <Text type="secondary" style={{ fontSize: 11, textAlign: 'center', display: 'block' }}>
                <SafetyOutlined /> Secured by 256-bit SSL encryption
              </Text>
            </Space>
          </Card>

          <Card title="Processing Time">
            <Space direction="vertical" style={{ width: '100%' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                <ClockCircleOutlined style={{ color: '#1890ff' }} />
                <Text>{currentMethod?.processingTime}</Text>
              </div>
              <Divider style={{ margin: '12px 0' }} />
              <Text type="secondary" style={{ fontSize: 12 }}>
                Your order will be processed immediately after payment confirmation.
              </Text>
            </Space>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default PaymentIntegrationPage;

_temp/demo/personalized-dashboard.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Switch,
  Divider,
  message,
  Select,
  Slider,
  ColorPicker,
  Input,
  Tag,
  List,
  Avatar,
  Checkbox,
} from 'antd';
import {
  DashboardOutlined,
  PlusOutlined,
  DeleteOutlined,
  EditOutlined,
  DragOutlined,
  BarChartOutlined,
  ShoppingCartOutlined,
  UserOutlined,
  DollarOutlined,
  HeartOutlined,
  SaveOutlined,
  ReloadOutlined,
} from '@ant-design/icons';
import type { Color } from 'antd/es/color-picker';

const { Title, Text, Paragraph } = Typography;

interface Widget {
  id: string;
  type: 'statistic' | 'chart' | 'list' | 'activity';
  title: string;
  icon: React.ReactNode;
  color: string;
  enabled: boolean;
  size: 'small' | 'medium' | 'large';
  position: number;
}

const availableWidgets: Widget[] = [
  {
    id: 'total_sales',
    type: 'statistic',
    title: 'Total Sales',
    icon: <DollarOutlined />,
    color: '#52c41a',
    enabled: true,
    size: 'small',
    position: 0,
  },
  {
    id: 'total_orders',
    type: 'statistic',
    title: 'Total Orders',
    icon: <ShoppingCartOutlined />,
    color: '#1890ff',
    enabled: true,
    size: 'small',
    position: 1,
  },
  {
    id: 'total_customers',
    type: 'statistic',
    title: 'Total Customers',
    icon: <UserOutlined />,
    color: '#722ed1',
    enabled: true,
    size: 'small',
    position: 2,
  },
  {
    id: 'wishlist_items',
    type: 'statistic',
    title: 'Wishlist Items',
    icon: <HeartOutlined />,
    color: '#ff4d4f',
    enabled: true,
    size: 'small',
    position: 3,
  },
  {
    id: 'sales_chart',
    type: 'chart',
    title: 'Sales Overview',
    icon: <BarChartOutlined />,
    color: '#1890ff',
    enabled: true,
    size: 'large',
    position: 4,
  },
  {
    id: 'recent_orders',
    type: 'list',
    title: 'Recent Orders',
    icon: <ShoppingCartOutlined />,
    color: '#ff9900',
    enabled: true,
    size: 'medium',
    position: 5,
  },
  {
    id: 'activity_feed',
    type: 'activity',
    title: 'Activity Feed',
    icon: <DashboardOutlined />,
    color: '#13c2c2',
    enabled: false,
    size: 'medium',
    position: 6,
  },
];

const PersonalizedDashboardPage: React.FC = () => {
  const [widgets, setWidgets] = useState<Widget[]>(availableWidgets);
  const [theme, setTheme] = useState<'light' | 'dark'>('light');
  const [compactMode, setCompactMode] = useState(false);
  const [primaryColor, setPrimaryColor] = useState<Color | string>('#1890ff');
  const [refreshInterval, setRefreshInterval] = useState(30);
  const [showAnimations, setShowAnimations] = useState(true);

  const handleToggleWidget = (id: string) => {
    setWidgets((prev) =>
      prev.map((w) => (w.id === id ? { ...w, enabled: !w.enabled } : w))
    );
    message.success('Widget visibility updated');
  };

  const handleChangeWidgetSize = (id: string, size: 'small' | 'medium' | 'large') => {
    setWidgets((prev) =>
      prev.map((w) => (w.id === id ? { ...w, size } : w))
    );
    message.success('Widget size updated');
  };

  const handleSaveSettings = () => {
    const settings = {
      widgets,
      theme,
      compactMode,
      primaryColor,
      refreshInterval,
      showAnimations,
    };
    localStorage.setItem('dashboard_settings', JSON.stringify(settings));
    message.success('Dashboard settings saved successfully!');
  };

  const handleResetSettings = () => {
    setWidgets(availableWidgets);
    setTheme('light');
    setCompactMode(false);
    setPrimaryColor('#1890ff');
    setRefreshInterval(30);
    setShowAnimations(true);
    localStorage.removeItem('dashboard_settings');
    message.info('Dashboard settings reset to default');
  };

  const enabledWidgets = widgets.filter((w) => w.enabled).sort((a, b) => a.position - b.position);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <DashboardOutlined style={{ color: '#1890ff' }} /> Personalized Dashboard
        </Title>
        <Paragraph type="secondary">
          Customize your dashboard layout, widgets, and appearance
        </Paragraph>
      </div>

      <Row gutter={16}>
        {/* Settings Panel */}
        <Col xs={24} lg={8}>
          <Card title="Dashboard Settings" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {/* Theme */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Theme
                </Text>
                <Select
                  style={{ width: '100%' }}
                  value={theme}
                  onChange={setTheme}
                  options={[
                    { label: ' Light Theme', value: 'light' },
                    { label: ' Dark Theme', value: 'dark' },
                  ]}
                />
              </div>

              {/* Primary Color */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Primary Color
                </Text>
                <ColorPicker
                  value={primaryColor}
                  onChange={setPrimaryColor}
                  showText
                  style={{ width: '100%' }}
                />
              </div>

              {/* Refresh Interval */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Auto Refresh Interval: {refreshInterval}s
                </Text>
                <Slider
                  min={10}
                  max={300}
                  step={10}
                  value={refreshInterval}
                  onChange={setRefreshInterval}
                  marks={{
                    10: '10s',
                    60: '1m',
                    120: '2m',
                    300: '5m',
                  }}
                />
              </div>

              <Divider />

              {/* Display Options */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 12 }}>
                  Display Options
                </Text>
                <Space direction="vertical" style={{ width: '100%' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text>Compact Mode</Text>
                    <Switch checked={compactMode} onChange={setCompactMode} />
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text>Show Animations</Text>
                    <Switch checked={showAnimations} onChange={setShowAnimations} />
                  </div>
                </Space>
              </div>

              <Divider />

              {/* Action Buttons */}
              <Space style={{ width: '100%' }} direction="vertical">
                <Button
                  type="primary"
                  icon={<SaveOutlined />}
                  block
                  onClick={handleSaveSettings}
                >
                  Save Settings
                </Button>
                <Button
                  danger
                  icon={<ReloadOutlined />}
                  block
                  onClick={handleResetSettings}
                >
                  Reset to Default
                </Button>
              </Space>
            </Space>
          </Card>

          {/* Widget Manager */}
          <Card title="Widget Manager">
            <List
              dataSource={widgets}
              renderItem={(widget) => (
                <List.Item
                  actions={[
                    <Switch
                      key="toggle"
                      checked={widget.enabled}
                      onChange={() => handleToggleWidget(widget.id)}
                    />,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        icon={widget.icon}
                        style={{ backgroundColor: widget.color }}
                      />
                    }
                    title={widget.title}
                    description={
                      <Space>
                        <Select
                          size="small"
                          value={widget.size}
                          onChange={(size) => handleChangeWidgetSize(widget.id, size)}
                          disabled={!widget.enabled}
                          style={{ width: 100 }}
                          options={[
                            { label: 'Small', value: 'small' },
                            { label: 'Medium', value: 'medium' },
                            { label: 'Large', value: 'large' },
                          ]}
                        />
                        <Tag color={widget.enabled ? 'green' : 'default'}>
                          {widget.type}
                        </Tag>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        {/* Dashboard Preview */}
        <Col xs={24} lg={16}>
          <Card
            title={
              <Space>
                <DashboardOutlined />
                <span>Dashboard Preview</span>
                <Tag color="blue">{enabledWidgets.length} widgets</Tag>
              </Space>
            }
          >
            <Row gutter={[16, 16]}>
              {enabledWidgets.map((widget) => {
                const colSpan =
                  widget.size === 'small'
                    ? { xs: 24, sm: 12, md: 6 }
                    : widget.size === 'medium'
                    ? { xs: 24, sm: 12 }
                    : { xs: 24 };

                return (
                  <Col key={widget.id} {...colSpan}>
                    <Card
                      size={compactMode ? 'small' : 'default'}
                      style={{
                        borderLeft: `4px solid ${widget.color}`,
                        transition: showAnimations ? 'all 0.3s' : 'none',
                      }}
                      hoverable={showAnimations}
                    >
                      <Space direction="vertical" style={{ width: '100%' }}>
                        <Space>
                          <div style={{ fontSize: 24, color: widget.color }}>
                            {widget.icon}
                          </div>
                          <div>
                            <Text strong style={{ fontSize: 16 }}>
                              {widget.title}
                            </Text>
                            <div>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {widget.type.toUpperCase()}
                              </Text>
                            </div>
                          </div>
                        </Space>

                        {/* Widget Content Preview */}
                        {widget.type === 'statistic' && (
                          <div style={{ padding: '16px 0' }}>
                            <div style={{ fontSize: 32, fontWeight: 'bold', color: widget.color }}>
                              {Math.floor(Math.random() * 10000)}
                            </div>
                            <Text type="secondary">
                              +{Math.floor(Math.random() * 20)}% from last month
                            </Text>
                          </div>
                        )}

                        {widget.type === 'chart' && (
                          <div
                            style={{
                              height: 200,
                              background: '#f0f0f0',
                              borderRadius: 4,
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                            }}
                          >
                            <BarChartOutlined style={{ fontSize: 48, color: '#bfbfbf' }} />
                          </div>
                        )}

                        {widget.type === 'list' && (
                          <List
                            size="small"
                            dataSource={[1, 2, 3]}
                            renderItem={(item) => (
                              <List.Item>
                                <Text>Order #1234{item}</Text>
                                <Tag color="blue">${(Math.random() * 100).toFixed(2)}</Tag>
                              </List.Item>
                            )}
                          />
                        )}

                        {widget.type === 'activity' && (
                          <Space direction="vertical" style={{ width: '100%' }} size="small">
                            {[1, 2, 3].map((i) => (
                              <div key={i} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                                <Avatar size="small" icon={<UserOutlined />} />
                                <Text type="secondary" style={{ fontSize: 12 }}>
                                  User action #{i}
                                </Text>
                              </div>
                            ))}
                          </Space>
                        )}
                      </Space>
                    </Card>
                  </Col>
                );
              })}

              {enabledWidgets.length === 0 && (
                <Col span={24}>
                  <div
                    style={{
                      textAlign: 'center',
                      padding: '60px 20px',
                      background: '#fafafa',
                      borderRadius: 8,
                    }}
                  >
                    <DashboardOutlined style={{ fontSize: 64, color: '#bfbfbf', marginBottom: 16 }} />
                    <Title level={4} type="secondary">
                      No Widgets Enabled
                    </Title>
                    <Paragraph type="secondary">
                      Enable widgets from the Widget Manager to customize your dashboard
                    </Paragraph>
                  </div>
                </Col>
              )}
            </Row>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default PersonalizedDashboardPage;

admin/advanced-order-management.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Space,
  Button,
  Statistic,
  Progress,
  Select,
  DatePicker,
  Tabs,
  Input,
  Modal,
  Form,
  Steps,
  Timeline,
  Badge,
  Divider,
  List,
  Avatar,
  message,
  Spin,
  Drawer,
  TreeSelect,
  Cascader,
  Collapse,
  notification,
  Radio,
  Checkbox,
  Tooltip,
  Popconfirm,
  Switch,
  InputNumber,
  Upload,
  Alert,
  Empty,
  Dropdown,
} from 'antd';
import {
  ShoppingCartOutlined,
  UserOutlined,
  DollarOutlined,
  TruckOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  ExclamationCircleOutlined,
  EditOutlined,
  EyeOutlined,
  DeleteOutlined,
  PlusOutlined,
  DownloadOutlined,
  PrinterOutlined,
  MailOutlined,
  PhoneOutlined,
  MessageOutlined,
  SplitCellsOutlined,
  MergeOutlined,
  SettingOutlined,
  RobotOutlined,
  ThunderboltOutlined,
  BarChartOutlined,
  LineChartOutlined,
  PieChartOutlined,
  FilterOutlined,
  SearchOutlined,
  ReloadOutlined,
  BulbOutlined,
  FileExcelOutlined,
  FilePdfOutlined,
  FileTextOutlined,
  TagOutlined,
  StarOutlined,
  WarningOutlined,
  InfoCircleOutlined,
  HomeOutlined,
  GlobalOutlined,
  BankOutlined,
  CreditCardOutlined,
  SafetyCertificateOutlined,
  RocketOutlined,
  SyncOutlined,
  AimOutlined,
  AlertOutlined,
  BellOutlined,
  CarOutlined,
  CalendarOutlined,
  TeamOutlined,
  BranchesOutlined,
  NodeIndexOutlined,
  DatabaseOutlined,
  CloudServerOutlined,
  ApiOutlined,
  LinkOutlined,
} from '@ant-design/icons';
import { Line, Column, Pie, Area } from '@ant-design/charts';
import { formatPieLabelContent } from '@/utils/chartHelpers';
import type { ColumnsType } from 'antd/es/table';
import dayjs, { Dayjs } from 'dayjs';
import { 
  advancedOrderAPI, 
  AdvancedOrder, 
  AdvancedOrderItem,
  OrderEvent,
  OrderNote,
  OrderSplit,
  OrderStats,
  WorkflowTemplate,
  AutomationRule,
  OrderBatch,
  AdvancedOrderFilters
} from '../../services/api/advancedOrders';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { TabPane } = Tabs;
const { Panel } = Collapse;
const { Step } = Steps;
const { Search } = Input;

const AdvancedOrderManagementPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [orders, setOrders] = useState<AdvancedOrder[]>([]);
  const [stats, setStats] = useState<OrderStats | null>(null);
  const [selectedOrders, setSelectedOrders] = useState<string[]>([]);
  const [selectedOrder, setSelectedOrder] = useState<AdvancedOrder | null>(null);
  const [orderDetailVisible, setOrderDetailVisible] = useState(false);
  const [splitModalVisible, setSplitModalVisible] = useState(false);
  const [workflowDrawerVisible, setWorkflowDrawerVisible] = useState(false);
  const [automationDrawerVisible, setAutomationDrawerVisible] = useState(false);
  const [batchModalVisible, setBatchModalVisible] = useState(false);
  const [activeTab, setActiveTab] = useState('orders');
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState<AdvancedOrderFilters>({
    page: 1,
    limit: 20,
    sortBy: 'createdAt',
    sortOrder: 'desc',
  });
  const [dateRange, setDateRange] = useState<[Dayjs, Dayjs]>([
    dayjs().subtract(30, 'days'),
    dayjs(),
  ]);
  const [workflows, setWorkflows] = useState<WorkflowTemplate[]>([]);
  const [automationRules, setAutomationRules] = useState<AutomationRule[]>([]);
  const [batches, setBatches] = useState<OrderBatch[]>([]);
  const [orderTimeline, setOrderTimeline] = useState<OrderEvent[]>([]);
  const [orderNotes, setOrderNotes] = useState<OrderNote[]>([]);
  const [orderSplits, setOrderSplits] = useState<OrderSplit[]>([]);
  const [form] = Form.useForm();

  // Load data
  useEffect(() => {
    loadOrders();
    loadStats();
    loadWorkflows();
    loadAutomationRules();
    loadBatches();
  }, [filters, dateRange]);

  const loadOrders = async () => {
    setLoading(true);
    try {
      const response = await advancedOrderAPI.getOrders({
        ...filters,
        dateRange: {
          start: dateRange[0].format('YYYY-MM-DD'),
          end: dateRange[1].format('YYYY-MM-DD'),
        },
      });
      setOrders(response.orders);
    } catch (error) {
      console.error('Error loading orders:', error);
      message.error('Failed to load orders');
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      const statsData = await advancedOrderAPI.getOrderStats({
        startDate: dateRange[0].format('YYYY-MM-DD'),
        endDate: dateRange[1].format('YYYY-MM-DD'),
      });
      setStats(statsData);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const loadWorkflows = async () => {
    try {
      const workflowData = await advancedOrderAPI.getWorkflowTemplates();
      setWorkflows(workflowData);
    } catch (error) {
      console.error('Error loading workflows:', error);
    }
  };

  const loadAutomationRules = async () => {
    try {
      const rulesData = await advancedOrderAPI.getAutomationRules();
      setAutomationRules(rulesData);
    } catch (error) {
      console.error('Error loading automation rules:', error);
    }
  };

  const loadBatches = async () => {
    try {
      const batchData = await advancedOrderAPI.getBatches();
      setBatches(batchData);
    } catch (error) {
      console.error('Error loading batches:', error);
    }
  };

  const loadOrderDetails = async (orderId: string) => {
    try {
      const [timeline, notes, splits] = await Promise.all([
        advancedOrderAPI.getOrderTimeline(orderId),
        advancedOrderAPI.getOrderNotes(orderId),
        advancedOrderAPI.getSplits(orderId),
      ]);
      setOrderTimeline(timeline);
      setOrderNotes(notes);
      setOrderSplits(splits);
    } catch (error) {
      console.error('Error loading order details:', error);
    }
  };

  const handleOrderClick = async (order: AdvancedOrder) => {
    setSelectedOrder(order);
    await loadOrderDetails(order.id);
    setOrderDetailVisible(true);
  };

  const handleStatusUpdate = async (orderId: string, newStatus: string, reason?: string) => {
    try {
      await advancedOrderAPI.updateOrderStatus(orderId, newStatus, reason);
      message.success('Order status updated successfully');
      loadOrders();
    } catch (error) {
      console.error('Error updating order status:', error);
      message.error('Failed to update order status');
    }
  };

  const handleAssignOrder = async (orderId: string, assignee: string, department?: string) => {
    try {
      await advancedOrderAPI.assignOrder(orderId, assignee, department);
      message.success('Order assigned successfully');
      loadOrders();
    } catch (error) {
      console.error('Error assigning order:', error);
      message.error('Failed to assign order');
    }
  };

  const handleEscalateOrder = async (orderId: string, reason: string, assignTo?: string) => {
    try {
      await advancedOrderAPI.escalateOrder(orderId, reason, assignTo);
      message.success('Order escalated successfully');
      loadOrders();
    } catch (error) {
      console.error('Error escalating order:', error);
      message.error('Failed to escalate order');
    }
  };

  const handleSplitOrder = async (orderId: string, splitData: any) => {
    try {
      await advancedOrderAPI.splitOrder(orderId, splitData);
      message.success('Order split successfully');
      setSplitModalVisible(false);
      loadOrders();
    } catch (error) {
      console.error('Error splitting order:', error);
      message.error('Failed to split order');
    }
  };

  const handleBulkAction = async (action: string, orderIds: string[], data?: any) => {
    try {
      setLoading(true);
      switch (action) {
        case 'status':
          await advancedOrderAPI.batchUpdateStatus(orderIds, data.status, data.reason);
          break;
        case 'assign':
          await advancedOrderAPI.batchAssign(orderIds, data.assignee);
          break;
        case 'tags':
          await advancedOrderAPI.batchAddTags(orderIds, data.tags);
          break;
        default:
          throw new Error('Unknown bulk action');
      }
      message.success(`Bulk ${action} completed successfully`);
      setSelectedOrders([]);
      loadOrders();
    } catch (error) {
      console.error(`Error in bulk ${action}:`, error);
      message.error(`Failed to perform bulk ${action}`);
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async (format: 'csv' | 'xlsx' | 'pdf') => {
    try {
      setLoading(true);
      const orderIds = selectedOrders.length > 0 ? selectedOrders : orders.map(o => o.id);
      const blob = await advancedOrderAPI.batchExport(orderIds, format);

      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `orders-${dayjs().format('YYYY-MM-DD')}.${format}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      message.success(`Orders exported as ${format.toUpperCase()}`);
    } catch (error) {
      console.error('Export error:', error);
      message.error('Failed to export orders');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: AdvancedOrder['status']) => {
    const colors = {
      pending: 'orange',
      confirmed: 'blue',
      processing: 'purple',
      split: 'cyan',
      partially_shipped: 'geekblue',
      shipped: 'green',
      delivered: 'success',
      cancelled: 'red',
      returned: 'magenta',
      refunded: 'volcano',
    };
    return colors[status] || 'default';
  };

  const getPriorityColor = (priority: AdvancedOrder['priority']) => {
    const colors = {
      low: 'green',
      normal: 'blue',
      high: 'orange',
      urgent: 'red',
    };
    return colors[priority];
  };

  const ordersColumns: ColumnsType<AdvancedOrder> = [
    {
      title: 'Order',
      key: 'order',
      render: (_, record) => (
        <div>
          <Button
            type="link"
            onClick={() => handleOrderClick(record)}
            style={{ padding: 0, fontSize: '14px', fontWeight: 'bold' }}
          >
            #{record.orderNumber}
          </Button>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {dayjs(record.createdAt).format('MMM DD, YYYY HH:mm')}
          </div>
          <div style={{ marginTop: 4 }}>
            <Tag color={getPriorityColor(record.priority)}>
              {record.priority.toUpperCase()}
            </Tag>
          </div>
        </div>
      ),
      width: 150,
    },
    {
      title: 'Customer',
      key: 'customer',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar icon={<UserOutlined />} size="small" style={{ marginRight: 8 }} />
          <div>
            <Text strong style={{ display: 'block' }}>{record.customerName}</Text>
            <Text type="secondary" style={{ fontSize: '12px' }}>
              {record.customerEmail}
            </Text>
          </div>
        </div>
      ),
      width: 200,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: AdvancedOrder['status']) => (
        <Tag color={getStatusColor(status)}>
          {status.replace('_', ' ').toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Pending', value: 'pending' },
        { text: 'Confirmed', value: 'confirmed' },
        { text: 'Processing', value: 'processing' },
        { text: 'Shipped', value: 'shipped' },
        { text: 'Delivered', value: 'delivered' },
        { text: 'Cancelled', value: 'cancelled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Items',
      key: 'items',
      render: (_, record) => (
        <div>
          <Text strong>{record.items.length} items</Text>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {record.items.reduce((sum, item) => sum + item.quantity, 0)} units
          </div>
        </div>
      ),
    },
    {
      title: 'Total',
      key: 'total',
      render: (_, record) => (
        <div>
          <Text strong style={{ fontSize: '16px' }}>
            {record.totals.currency} {record.totals.total.toFixed(2)}
          </Text>
          <div style={{ fontSize: '12px', color: '#666' }}>
            Payment: {record.payment.status}
          </div>
        </div>
      ),
      sorter: (a, b) => a.totals.total - b.totals.total,
    },
    {
      title: 'Workflow',
      key: 'workflow',
      render: (_, record) => (
        <div>
          <Tag color="blue">{record.workflow.currentStage}</Tag>
          {record.workflow.assignedTo && (
            <div style={{ fontSize: '12px', color: '#666', marginTop: 2 }}>
              Assigned: {record.workflow.assignedTo}
            </div>
          )}
          {record.workflow.escalationLevel > 0 && (
            <div style={{ marginTop: 2 }}>
              <Tag color="red">
                Escalated L{record.workflow.escalationLevel}
              </Tag>
            </div>
          )}
        </div>
      ),
    },
    {
      title: 'Shipping',
      key: 'shipping',
      render: (_, record) => (
        <div>
          <div style={{ fontSize: '12px' }}>
            <TruckOutlined style={{ marginRight: 4 }} />
            {record.shipping.carrier} - {record.shipping.service}
          </div>
          <div style={{ fontSize: '12px', color: '#666' }}>
            Est: {record.estimatedDelivery ? dayjs(record.estimatedDelivery).format('MMM DD') : 'TBD'}
          </div>
          {record.tracking.status && (
            <Tag color="green" style={{ marginTop: 2 }}>
              {record.tracking.status}
            </Tag>
          )}
        </div>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="View Details">
            <Button
              type="text"
              size="small"
              icon={<EyeOutlined />}
              onClick={() => handleOrderClick(record)}
            />
          </Tooltip>
          <Tooltip title="Edit Order">
            <Button
              type="text"
              size="small"
              icon={<EditOutlined />}
            />
          </Tooltip>
          <Dropdown
            menu={{
              items: [
                {
                  key: 'status',
                  label: 'Update Status',
                  icon: <CheckCircleOutlined />,
                },
                {
                  key: 'assign',
                  label: 'Assign',
                  icon: <TeamOutlined />,
                },
                {
                  key: 'escalate',
                  label: 'Escalate',
                  icon: <WarningOutlined />,
                },
                {
                  key: 'split',
                  label: 'Split Order',
                  icon: <SplitCellsOutlined />,
                },
                {
                  key: 'print',
                  label: 'Print',
                  icon: <PrinterOutlined />,
                },
              ],
            }}
            trigger={['click']}
          >
            <Button type="text" size="small" icon={<SettingOutlined />} />
          </Dropdown>
        </Space>
      ),
      width: 120,
    },
  ];

  const exportMenuItems = [
    {
      key: 'csv',
      label: (
        <Space>
          <FileTextOutlined />
          Export as CSV
        </Space>
      ),
      onClick: () => handleExport('csv'),
    },
    {
      key: 'xlsx',
      label: (
        <Space>
          <FileExcelOutlined />
          Export as Excel
        </Space>
      ),
      onClick: () => handleExport('xlsx'),
    },
    {
      key: 'pdf',
      label: (
        <Space>
          <FilePdfOutlined />
          Export as PDF
        </Space>
      ),
      onClick: () => handleExport('pdf'),
    },
  ];

  const bulkActionMenuItems = [
    {
      key: 'status',
      label: 'Update Status',
      icon: <CheckCircleOutlined />,
    },
    {
      key: 'assign',
      label: 'Assign Orders',
      icon: <TeamOutlined />,
    },
    {
      key: 'tags',
      label: 'Add Tags',
      icon: <TagOutlined />,
    },
    {
      key: 'batch',
      label: 'Create Batch',
      icon: <BulbOutlined />,
    },
  ];

  // Chart data
  const orderTrendData = stats?.revenueByMonth?.map(item => ({
    month: item.month,
    orders: item.orders,
    revenue: item.revenue,
  })) || [];

  const statusDistribution = stats?.ordersByStatus?.map(item => ({
    type: item.status,
    value: item.count,
  })) || [];

  const priorityDistribution = stats?.ordersByPriority?.map(item => ({
    type: item.priority,
    value: item.count,
  })) || [];

  return (
    <Spin spinning={loading} tip="Loading order management...">
      <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
        {/* Header */}
        <div style={{ marginBottom: 24 }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <Title level={3}>
                <ShoppingCartOutlined style={{ color: '#1890ff' }} /> Advanced Order Management
              </Title>
              <Paragraph type="secondary">
                Comprehensive order processing with workflow automation and advanced tracking
              </Paragraph>
            </div>
            <Space>
              <Search
                placeholder="Search orders..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onSearch={(value) => setFilters({ ...filters, orderNumber: value })}
                style={{ width: 250 }}
              />
              <Select
                value={filters.status}
                onChange={(status) => setFilters({ ...filters, status })}
                style={{ width: 120 }}
                placeholder="Status"
              >
                <Select.Option value="">All Status</Select.Option>
                <Select.Option value="pending">Pending</Select.Option>
                <Select.Option value="confirmed">Confirmed</Select.Option>
                <Select.Option value="processing">Processing</Select.Option>
                <Select.Option value="shipped">Shipped</Select.Option>
                <Select.Option value="delivered">Delivered</Select.Option>
              </Select>
              <Select
                value={filters.priority}
                onChange={(priority) => setFilters({ ...filters, priority })}
                style={{ width: 120 }}
                placeholder="Priority"
              >
                <Select.Option value="">All Priority</Select.Option>
                <Select.Option value="low">Low</Select.Option>
                <Select.Option value="normal">Normal</Select.Option>
                <Select.Option value="high">High</Select.Option>
                <Select.Option value="urgent">Urgent</Select.Option>
              </Select>
              <RangePicker
                value={dateRange}
                onChange={(dates) => dates && setDateRange(dates as [Dayjs, Dayjs])}
              />
              <Button
                icon={<ReloadOutlined />}
                onClick={loadOrders}
              >
                Refresh
              </Button>
              <Dropdown menu={{ items: exportMenuItems }} placement="bottomRight">
                <Button icon={<DownloadOutlined />}>
                  Export
                </Button>
              </Dropdown>
            </Space>
          </div>
        </div>

        {/* Statistics */}
        <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Total Orders"
                value={stats?.totalOrders || 0}
                prefix={<ShoppingCartOutlined />}
                valueStyle={{ color: '#1890ff' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Total Revenue"
                value={stats?.totalRevenue || 0}
                prefix={<DollarOutlined />}
                precision={2}
                valueStyle={{ color: '#52c41a' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Avg Order Value"
                value={stats?.averageOrderValue || 0}
                prefix={<DollarOutlined />}
                precision={2}
                valueStyle={{ color: '#722ed1' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card>
              <Statistic
                title="Fulfillment Rate"
                value={stats?.fulfillmentRate || 0}
                prefix={<TruckOutlined />}
                precision={1}
                suffix="%"
                valueStyle={{ color: '#fa8c16' }}
              />
            </Card>
          </Col>
        </Row>

        {/* Main Content */}
        <Tabs activeKey={activeTab} onChange={setActiveTab}>
          <TabPane
            tab={
              <span>
                <ShoppingCartOutlined />
                Orders ({orders.length})
              </span>
            }
            key="orders"
          >
            <Card>
              {selectedOrders.length > 0 && (
                <div style={{ marginBottom: 16 }}>
                  <Space>
                    <Text strong>{selectedOrders.length} orders selected</Text>
                    <Dropdown
                      menu={{
                        items: bulkActionMenuItems,
                        onClick: ({ key }) => {
                          // Handle bulk actions
                          if (key === 'batch') {
                            setBatchModalVisible(true);
                          }
                        },
                      }}
                    >
                      <Button>
                        Bulk Actions <DownloadOutlined />
                      </Button>
                    </Dropdown>
                    <Button
                      onClick={() => setSelectedOrders([])}
                    >
                      Clear Selection
                    </Button>
                  </Space>
                </div>
              )}

              <Table
                columns={ordersColumns}
                dataSource={orders}
                rowKey="id"
                rowSelection={{
                  selectedRowKeys: selectedOrders,
                  onChange: (keys) => setSelectedOrders(keys as string[]),
                }}
                pagination={{
                  pageSize: 20,
                  showSizeChanger: true,
                  showQuickJumper: true,
                  showTotal: (total, range) =>
                    `${range[0]}-${range[1]} of ${total} orders`,
                }}
                scroll={{ x: 1400 }}
              />
            </Card>
          </TabPane>

          <TabPane
            tab={
              <span>
                <BarChartOutlined />
                Analytics
              </span>
            }
            key="analytics"
          >
            <Row gutter={[16, 16]}>
              <Col xs={24} lg={16}>
                <Card title="Order Trends">
                  <Area
                    data={orderTrendData}
                    xField="month"
                    yField="orders"
                    height={300}
                  />
                </Card>
              </Col>
              <Col xs={24} lg={8}>
                <Card title="Status Distribution">
                  <Pie
                    data={statusDistribution}
                    angleField="value"
                    colorField="type"
                    radius={0.8}
                    label={{
                      type: 'outer',
                      content: formatPieLabelContent,
                    }}
                    height={300}
                  />
                </Card>
              </Col>
              <Col xs={24} lg={12}>
                <Card title="Revenue Trends">
                  <Column
                    data={orderTrendData}
                    xField="month"
                    yField="revenue"
                    height={250}
                  />
                </Card>
              </Col>
              <Col xs={24} lg={12}>
                <Card title="Priority Distribution">
                  <Pie
                    data={priorityDistribution}
                    angleField="value"
                    colorField="type"
                    radius={0.8}
                    height={250}
                  />
                </Card>
              </Col>
            </Row>
          </TabPane>

          <TabPane
            tab={
              <span>
                <BranchesOutlined />
                Workflows ({workflows.length})
              </span>
            }
            key="workflows"
          >
            <Card
              extra={
                <Button
                  type="primary"
                  icon={<PlusOutlined />}
                  onClick={() => setWorkflowDrawerVisible(true)}
                >
                  Create Workflow
                </Button>
              }
            >
              <List
                dataSource={workflows}
                renderItem={(workflow) => (
                  <List.Item
                    actions={[
                      <Button type="link" size="small">Edit</Button>,
                      <Button type="link" size="small">Test</Button>,
                      <Button type="link" size="small" danger>Delete</Button>
                    ]}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar style={{ backgroundColor: workflow.isActive ? '#52c41a' : '#d9d9d9' }}>
                          <BranchesOutlined />
                        </Avatar>
                      }
                      title={
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                          <span>{workflow.name}</span>
                          <Tag color={workflow.isActive ? 'green' : 'default'}>
                            {workflow.isActive ? 'Active' : 'Inactive'}
                          </Tag>
                        </div>
                      }
                      description={
                        <div>
                          <div>{workflow.description}</div>
                          <div style={{ marginTop: 4 }}>
                            <Text type="secondary" style={{ fontSize: '12px' }}>
                              {workflow.stages.length} stages  {workflow.automationRules.length} automation rules
                            </Text>
                          </div>
                        </div>
                      }
                    />
                  </List.Item>
                )}
              />
            </Card>
          </TabPane>

          <TabPane
            tab={
              <span>
                <RobotOutlined />
                Automation ({automationRules.length})
              </span>
            }
            key="automation"
          >
            <Card
              extra={
                <Button
                  type="primary"
                  icon={<PlusOutlined />}
                  onClick={() => setAutomationDrawerVisible(true)}
                >
                  Create Rule
                </Button>
              }
            >
              <List
                dataSource={automationRules}
                renderItem={(rule) => (
                  <List.Item
                    actions={[
                      <Button type="link" size="small">Edit</Button>,
                      <Button type="link" size="small">Test</Button>,
                      <Button type="link" size="small" danger>Delete</Button>
                    ]}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar style={{ backgroundColor: rule.isActive ? '#1890ff' : '#d9d9d9' }}>
                          <RobotOutlined />
                        </Avatar>
                      }
                      title={
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                          <span>{rule.name}</span>
                          <div>
                            <Tag color="blue">Priority {rule.priority}</Tag>
                            <Tag color={rule.isActive ? 'green' : 'default'}>
                              {rule.isActive ? 'Active' : 'Inactive'}
                            </Tag>
                          </div>
                        </div>
                      }
                      description={
                        <div>
                          <div><strong>Trigger:</strong> {rule.trigger}</div>
                          <div><strong>Conditions:</strong> {rule.conditions.length} conditions</div>
                          <div><strong>Actions:</strong> {rule.actions.length} actions</div>
                        </div>
                      }
                    />
                  </List.Item>
                )}
              />
            </Card>
          </TabPane>

          <TabPane
            tab={
              <span>
                <BulbOutlined />
                Batches ({batches.length})
              </span>
            }
            key="batches"
          >
            <Card
              extra={
                <Button
                  type="primary"
                  icon={<PlusOutlined />}
                  onClick={() => setBatchModalVisible(true)}
                >
                  Create Batch
                </Button>
              }
            >
              <List
                dataSource={batches}
                renderItem={(batch) => (
                  <List.Item
                    actions={[
                      <Button type="link" size="small">View</Button>,
                      <Button type="link" size="small">Process</Button>,
                      <Button type="link" size="small" danger>Delete</Button>
                    ]}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar style={{ backgroundColor: '#722ed1' }}>
                          <BulbOutlined />
                        </Avatar>
                      }
                      title={
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                          <span>{batch.name}</span>
                          <Tag color={batch.status === 'completed' ? 'green' : 'processing'}>
                            {batch.status.toUpperCase()}
                          </Tag>
                        </div>
                      }
                      description={
                        <div>
                          <div>{batch.orderIds.length} orders  Type: {batch.batchType}</div>
                          <div style={{ fontSize: '12px', color: '#666' }}>
                            Created: {dayjs(batch.createdAt).format('MMM DD, YYYY HH:mm')}
                          </div>
                          {batch.results && (
                            <div style={{ marginTop: 4 }}>
                              <Tag color="green">{batch.results.successful} successful</Tag>
                              {batch.results.failed > 0 && (
                                <Tag color="red">{batch.results.failed} failed</Tag>
                              )}
                            </div>
                          )}
                        </div>
                      }
                    />
                  </List.Item>
                )}
              />
            </Card>
          </TabPane>
        </Tabs>

        {/* Order Detail Modal */}
        <Modal
          title={`Order #${selectedOrder?.orderNumber}`}
          open={orderDetailVisible}
          onCancel={() => setOrderDetailVisible(false)}
          width={1200}
          footer={[
            <Button key="close" onClick={() => setOrderDetailVisible(false)}>
              Close
            </Button>,
            <Button key="print" icon={<PrinterOutlined />}>
              Print
            </Button>,
            <Button key="edit" type="primary" icon={<EditOutlined />}>
              Edit Order
            </Button>,
          ]}
        >
          {selectedOrder && (
            <div>
              <Tabs defaultActiveKey="details">
                <TabPane tab="Order Details" key="details">
                  <Row gutter={16}>
                    <Col span={12}>
                      <Card title="Customer Information" size="small">
                        <div><strong>Name:</strong> {selectedOrder.customerName}</div>
                        <div><strong>Email:</strong> {selectedOrder.customerEmail}</div>
                        <div><strong>Customer ID:</strong> {selectedOrder.customerId}</div>
                      </Card>
                    </Col>
                    <Col span={12}>
                      <Card title="Order Information" size="small">
                        <div><strong>Status:</strong> <Tag color={getStatusColor(selectedOrder.status)}>{selectedOrder.status}</Tag></div>
                        <div><strong>Priority:</strong> <Tag color={getPriorityColor(selectedOrder.priority)}>{selectedOrder.priority}</Tag></div>
                        <div><strong>Type:</strong> {selectedOrder.orderType}</div>
                        <div><strong>Created:</strong> {dayjs(selectedOrder.createdAt).format('YYYY-MM-DD HH:mm')}</div>
                      </Card>
                    </Col>
                    <Col span={24} style={{ marginTop: 16 }}>
                      <Card title="Order Items" size="small">
                        <Table
                          dataSource={selectedOrder.items}
                          rowKey="id"
                          size="small"
                          pagination={false}
                          columns={[
                            {
                              title: 'Product',
                              key: 'product',
                              render: (_, item) => (
                                <div style={{ display: 'flex', alignItems: 'center' }}>
                                  <Avatar src={item.image} style={{ marginRight: 8 }}>
                                    {item.name.charAt(0)}
                                  </Avatar>
                                  <div>
                                    <div>{item.name}</div>
                                    <div style={{ fontSize: '12px', color: '#666' }}>SKU: {item.sku}</div>
                                  </div>
                                </div>
                              ),
                            },
                            {
                              title: 'Quantity',
                              dataIndex: 'quantity',
                              key: 'quantity',
                            },
                            {
                              title: 'Unit Price',
                              dataIndex: 'unitPrice',
                              key: 'unitPrice',
                              render: (price) => `$${price.toFixed(2)}`,
                            },
                            {
                              title: 'Total',
                              dataIndex: 'totalPrice',
                              key: 'totalPrice',
                              render: (price) => `$${price.toFixed(2)}`,
                            },
                            {
                              title: 'Status',
                              dataIndex: 'fulfillmentStatus',
                              key: 'fulfillmentStatus',
                              render: (status) => <Tag>{status}</Tag>,
                            },
                          ]}
                        />
                      </Card>
                    </Col>
                  </Row>
                </TabPane>
                <TabPane tab="Timeline" key="timeline">
                  <Timeline>
                    {orderTimeline.map((event) => (
                      <Timeline.Item
                        key={event.id}
                        color={event.type === 'status_change' ? 'blue' : 'green'}
                      >
                        <div>
                          <strong>{event.description}</strong>
                          <div style={{ fontSize: '12px', color: '#666' }}>
                            {dayjs(event.timestamp).format('YYYY-MM-DD HH:mm')} {event.user && `by ${event.user}`}
                          </div>
                        </div>
                      </Timeline.Item>
                    ))}
                  </Timeline>
                </TabPane>
                <TabPane tab="Notes" key="notes">
                  <List
                    dataSource={orderNotes}
                    renderItem={(note) => (
                      <List.Item>
                        <List.Item.Meta
                          title={
                            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                              <span>{note.author}</span>
                              <div>
                                <Tag color={note.type === 'internal' ? 'blue' : 'green'}>{note.type}</Tag>
                                {note.isImportant && <Tag color="red">Important</Tag>}
                              </div>
                            </div>
                          }
                          description={
                            <div>
                              <div>{note.content}</div>
                              <div style={{ fontSize: '12px', color: '#666', marginTop: 4 }}>
                                {dayjs(note.createdAt).format('YYYY-MM-DD HH:mm')}
                              </div>
                            </div>
                          }
                        />
                      </List.Item>
                    )}
                  />
                </TabPane>
                <TabPane tab="Splits" key="splits">
                  {orderSplits.length > 0 ? (
                    <List
                      dataSource={orderSplits}
                      renderItem={(split) => (
                        <List.Item>
                          <List.Item.Meta
                            title={`Split #${split.splitNumber}`}
                            description={
                              <div>
                                <div><strong>Items:</strong> {split.items.length}</div>
                                <div><strong>Total:</strong> {split.totals.currency} {split.totals.total.toFixed(2)}</div>
                                <div><strong>Status:</strong> <Tag>{split.status}</Tag></div>
                                <div><strong>Reason:</strong> {split.reason}</div>
                              </div>
                            }
                          />
                        </List.Item>
                      )}
                    />
                  ) : (
                    <Empty description="No splits found" />
                  )}
                </TabPane>
              </Tabs>
            </div>
          )}
        </Modal>

        {/* Split Order Modal */}
        <Modal
          title="Split Order"
          open={splitModalVisible}
          onCancel={() => setSplitModalVisible(false)}
          width={800}
          footer={null}
        >
          <Form
            form={form}
            layout="vertical"
            onFinish={(values) => {
              if (selectedOrder) {
                handleSplitOrder(selectedOrder.id, values);
              }
            }}
          >
            <Form.Item
              name="reason"
              label="Split Reason"
              rules={[{ required: true, message: 'Please enter split reason' }]}
            >
              <TextArea rows={3} placeholder="Enter reason for splitting the order" />
            </Form.Item>
            {/* Add split configuration form here */}
            <div style={{ textAlign: 'right' }}>
              <Space>
                <Button onClick={() => setSplitModalVisible(false)}>
                  Cancel
                </Button>
                <Button type="primary" htmlType="submit">
                  Split Order
                </Button>
              </Space>
            </div>
          </Form>
        </Modal>

        {/* Workflow Drawer */}
        <Drawer
          title="Workflow Management"
          placement="right"
          onClose={() => setWorkflowDrawerVisible(false)}
          open={workflowDrawerVisible}
          width={600}
        >
          <div>Workflow management interface coming soon...</div>
        </Drawer>

        {/* Automation Drawer */}
        <Drawer
          title="Automation Rules"
          placement="right"
          onClose={() => setAutomationDrawerVisible(false)}
          open={automationDrawerVisible}
          width={600}
        >
          <div>Automation rules interface coming soon...</div>
        </Drawer>

        {/* Batch Modal */}
        <Modal
          title="Create Batch Operation"
          open={batchModalVisible}
          onCancel={() => setBatchModalVisible(false)}
          footer={null}
        >
          <div>Batch operation interface coming soon...</div>
        </Modal>
      </div>
    </Spin>
  );
};

export default AdvancedOrderManagementPage;

admin/performance-optimization.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Statistic,
  Space,
  Button,
  Progress,
  Alert,
  Tabs,
  List,
  Switch,
  Select,
  Divider,
  message,
} from 'antd';
import {
  ThunderboltOutlined,
  RocketOutlined,
  CloudServerOutlined,
  DatabaseOutlined,
  CompressOutlined,
  FileImageOutlined,
  ApiOutlined,
  CheckCircleOutlined,
  WarningOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text, Paragraph } = Typography;

interface PerformanceMetric {
  name: string;
  current: number;
  target: number;
  unit: string;
  status: 'good' | 'warning' | 'critical';
}

interface CacheEntry {
  key: string;
  type: 'redis' | 'memory' | 'cdn';
  size: string;
  hits: number;
  hitRate: number;
  ttl: string;
}

interface AssetOptimization {
  type: string;
  originalSize: number;
  optimizedSize: number;
  savings: number;
  count: number;
}

const performanceMetrics: PerformanceMetric[] = [
  { name: 'First Contentful Paint (FCP)', current: 1.2, target: 1.8, unit: 's', status: 'good' },
  { name: 'Largest Contentful Paint (LCP)', current: 2.1, target: 2.5, unit: 's', status: 'good' },
  { name: 'Time to Interactive (TTI)', current: 3.5, target: 3.8, unit: 's', status: 'good' },
  { name: 'Total Blocking Time (TBT)', current: 180, target: 300, unit: 'ms', status: 'good' },
  { name: 'Cumulative Layout Shift (CLS)', current: 0.08, target: 0.1, unit: '', status: 'good' },
  { name: 'Speed Index', current: 2.8, target: 3.4, unit: 's', status: 'good' },
];

const cacheData: CacheEntry[] = [
  { key: 'product_list', type: 'redis', size: '2.4 MB', hits: 15420, hitRate: 94.2, ttl: '1h' },
  { key: 'user_sessions', type: 'redis', size: '1.8 MB', hits: 8920, hitRate: 89.5, ttl: '30m' },
  { key: 'api_responses', type: 'memory', size: '850 KB', hits: 12340, hitRate: 91.8, ttl: '15m' },
  { key: 'static_assets', type: 'cdn', size: '45 MB', hits: 45680, hitRate: 98.5, ttl: '24h' },
];

const assetOptimizations: AssetOptimization[] = [
  { type: 'Images', originalSize: 125.5, optimizedSize: 42.8, savings: 65.9, count: 342 },
  { type: 'JavaScript', originalSize: 3.8, optimizedSize: 1.2, savings: 68.4, count: 45 },
  { type: 'CSS', originalSize: 1.2, optimizedSize: 0.45, savings: 62.5, count: 18 },
  { type: 'Fonts', originalSize: 2.4, optimizedSize: 1.1, savings: 54.2, count: 8 },
];

const PerformanceOptimizationPage: React.FC = () => {
  const [cacheEnabled, setCacheEnabled] = useState<boolean>(true);
  const [cdnEnabled, setCdnEnabled] = useState<boolean>(true);
  const [compressionEnabled, setCompressionEnabled] = useState<boolean>(true);
  const [lazyLoadingEnabled, setLazyLoadingEnabled] = useState<boolean>(true);

  const handleClearCache = (type: string) => {
    message.success(`${type} cache cleared successfully`);
  };

  const handleOptimizeAssets = () => {
    message.success('Asset optimization started');
  };

  const metricColumns: ColumnsType<PerformanceMetric> = [
    {
      title: 'Metric',
      dataIndex: 'name',
      key: 'name',
      render: (name) => <Text strong>{name}</Text>,
    },
    {
      title: 'Current',
      key: 'current',
      render: (_, record) => (
        <Text code>
          {record.current}
          {record.unit}
        </Text>
      ),
    },
    {
      title: 'Target',
      key: 'target',
      render: (_, record) => (
        <Text type="secondary">
          {record.target}
          {record.unit}
        </Text>
      ),
    },
    {
      title: 'Progress',
      key: 'progress',
      render: (_, record) => {
        const progress = Math.min((record.target / record.current) * 100, 100);
        return <Progress percent={Math.round(progress)} size="small" />;
      },
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: PerformanceMetric['status']) => {
        const config: Record<PerformanceMetric['status'], { color: string; icon: React.ReactNode; text: string }> = {
          good: { color: 'green', icon: <CheckCircleOutlined />, text: 'Good' },
          warning: { color: 'orange', icon: <WarningOutlined />, text: 'Needs Work' },
          critical: { color: 'red', icon: <WarningOutlined />, text: 'Critical' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
    },
  ];

  const cacheColumns: ColumnsType<CacheEntry> = [
    {
      title: 'Cache Key',
      dataIndex: 'key',
      key: 'key',
      render: (key) => <Text code>{key}</Text>,
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: CacheEntry['type']) => {
        const colors: Record<CacheEntry['type'], string> = { redis: 'red', memory: 'blue', cdn: 'green' };
        return <Tag color={colors[type]}>{type.toUpperCase()}</Tag>;
      },
    },
    {
      title: 'Size',
      dataIndex: 'size',
      key: 'size',
    },
    {
      title: 'Hits',
      dataIndex: 'hits',
      key: 'hits',
      render: (hits) => <Text>{hits.toLocaleString()}</Text>,
      sorter: (a, b) => a.hits - b.hits,
    },
    {
      title: 'Hit Rate',
      dataIndex: 'hitRate',
      key: 'hitRate',
      render: (rate) => (
        <div>
          <Progress percent={rate} size="small" />
        </div>
      ),
      sorter: (a, b) => a.hitRate - b.hitRate,
    },
    {
      title: 'TTL',
      dataIndex: 'ttl',
      key: 'ttl',
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Button size="small" danger onClick={() => handleClearCache(record.type)}>
          Clear
        </Button>
      ),
    },
  ];

  const assetColumns: ColumnsType<AssetOptimization> = [
    {
      title: 'Asset Type',
      dataIndex: 'type',
      key: 'type',
      render: (type) => <Text strong>{type}</Text>,
    },
    {
      title: 'Original Size',
      dataIndex: 'originalSize',
      key: 'originalSize',
      render: (size) => <Text>{size} MB</Text>,
    },
    {
      title: 'Optimized Size',
      dataIndex: 'optimizedSize',
      key: 'optimizedSize',
      render: (size) => <Text>{size} MB</Text>,
    },
    {
      title: 'Savings',
      dataIndex: 'savings',
      key: 'savings',
      render: (savings) => <Tag color="green">{savings.toFixed(1)}%</Tag>,
      sorter: (a, b) => a.savings - b.savings,
    },
    {
      title: 'Files',
      dataIndex: 'count',
      key: 'count',
      render: (count) => <Text type="secondary">{count} files</Text>,
    },
  ];

  const performanceScore = 94;
  const avgLoadTime = 1.8;
  const cacheHitRate = 93.5;
  const totalSavings = 65.2;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <RocketOutlined style={{ color: '#1890ff' }} /> Performance Optimization
        </Title>
        <Paragraph type="secondary">
          Monitor and optimize website performance, caching, and asset delivery
        </Paragraph>
      </div>

      <Alert
        message="Performance Status: Excellent"
        description="All performance metrics are within target ranges. Page load time is 40% faster than industry average."
        type="success"
        showIcon
        icon={<ThunderboltOutlined />}
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Performance Score"
              value={performanceScore}
              suffix="/100"
              prefix={<RocketOutlined />}
              valueStyle={{ color: performanceScore > 90 ? '#52c41a' : '#faad14' }}
            />
            <Progress percent={performanceScore} showInfo={false} strokeColor="#52c41a" />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Load Time"
              value={avgLoadTime}
              suffix="s"
              prefix={<ThunderboltOutlined />}
              valueStyle={{ color: '#1890ff' }}
              precision={1}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Cache Hit Rate"
              value={cacheHitRate}
              suffix="%"
              prefix={<DatabaseOutlined />}
              valueStyle={{ color: '#52c41a' }}
              precision={1}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Size Reduction"
              value={totalSavings}
              suffix="%"
              prefix={<CompressOutlined />}
              valueStyle={{ color: '#722ed1' }}
              precision={1}
            />
          </Card>
        </Col>
      </Row>

      <Tabs
        defaultActiveKey="metrics"
        items={[
          {
            key: 'metrics',
            label: 'Performance Metrics',
            children: (
              <Card title="Core Web Vitals & Metrics">
                <Table
                  columns={metricColumns}
                  dataSource={performanceMetrics}
                  rowKey="name"
                  pagination={false}
                />
              </Card>
            ),
          },
          {
            key: 'caching',
            label: 'Caching',
            children: (
              <Card
                title="Cache Management"
                extra={
                  <Space>
                    <Button onClick={() => handleClearCache('all')}>Clear All Caches</Button>
                  </Space>
                }
              >
                <Row gutter={16} style={{ marginBottom: 16 }}>
                  <Col span={8}>
                    <Card size="small">
                      <Statistic
                        title="Redis Cache"
                        value="4.2 MB"
                        prefix={<DatabaseOutlined />}
                        valueStyle={{ fontSize: 18 }}
                      />
                    </Card>
                  </Col>
                  <Col span={8}>
                    <Card size="small">
                      <Statistic
                        title="Memory Cache"
                        value="850 KB"
                        prefix={<CloudServerOutlined />}
                        valueStyle={{ fontSize: 18 }}
                      />
                    </Card>
                  </Col>
                  <Col span={8}>
                    <Card size="small">
                      <Statistic
                        title="CDN Cache"
                        value="45 MB"
                        prefix={<ApiOutlined />}
                        valueStyle={{ fontSize: 18 }}
                      />
                    </Card>
                  </Col>
                </Row>

                <Table columns={cacheColumns} dataSource={cacheData} rowKey="key" pagination={false} />
              </Card>
            ),
          },
          {
            key: 'assets',
            label: 'Asset Optimization',
            children: (
              <Card
                title="Asset Optimization Summary"
                extra={
                  <Button type="primary" icon={<CompressOutlined />} onClick={handleOptimizeAssets}>
                    Optimize All Assets
                  </Button>
                }
              >
                <Alert
                  message="Total Size Reduction"
                  description={`Optimized assets are ${totalSavings.toFixed(1)}% smaller than originals, saving ${(
                    assetOptimizations.reduce((sum, a) => sum + (a.originalSize - a.optimizedSize), 0)
                  ).toFixed(1)} MB`}
                  type="info"
                  showIcon
                  style={{ marginBottom: 16 }}
                />

                <Table
                  columns={assetColumns}
                  dataSource={assetOptimizations}
                  rowKey="type"
                  pagination={false}
                />
              </Card>
            ),
          },
          {
            key: 'settings',
            label: 'Optimization Settings',
            children: (
              <Card title="Performance Settings">
                <List
                  dataSource={[
                    {
                      title: 'Redis Caching',
                      description: 'Cache database queries and API responses',
                      enabled: cacheEnabled,
                      toggle: setCacheEnabled,
                      icon: <DatabaseOutlined />,
                    },
                    {
                      title: 'CDN Delivery',
                      description: 'Serve static assets from global CDN',
                      enabled: cdnEnabled,
                      toggle: setCdnEnabled,
                      icon: <CloudServerOutlined />,
                    },
                    {
                      title: 'GZIP Compression',
                      description: 'Compress responses to reduce transfer size',
                      enabled: compressionEnabled,
                      toggle: setCompressionEnabled,
                      icon: <CompressOutlined />,
                    },
                    {
                      title: 'Lazy Loading',
                      description: 'Load images and components on demand',
                      enabled: lazyLoadingEnabled,
                      toggle: setLazyLoadingEnabled,
                      icon: <FileImageOutlined />,
                    },
                  ]}
                  renderItem={(item) => (
                    <List.Item
                      actions={[
                        <Switch
                          key="switch"
                          checked={item.enabled}
                          onChange={item.toggle}
                          checkedChildren="ON"
                          unCheckedChildren="OFF"
                        />,
                      ]}
                    >
                      <List.Item.Meta
                        avatar={
                          <div
                            style={{
                              fontSize: 24,
                              color: item.enabled ? '#52c41a' : '#d9d9d9',
                            }}
                          >
                            {item.icon}
                          </div>
                        }
                        title={<Text strong>{item.title}</Text>}
                        description={item.description}
                      />
                    </List.Item>
                  )}
                />

                <Divider />

                <Title level={5}>Additional Settings</Title>
                <Space direction="vertical" style={{ width: '100%' }} size="middle">
                  <div>
                    <Text strong>Image Optimization Quality:</Text>
                    <Select defaultValue="85" style={{ width: 200, marginLeft: 16 }}>
                      <Select.Option value="70">70% (Higher compression)</Select.Option>
                      <Select.Option value="85">85% (Balanced)</Select.Option>
                      <Select.Option value="95">95% (Higher quality)</Select.Option>
                    </Select>
                  </div>
                  <div>
                    <Text strong>Cache TTL (Time to Live):</Text>
                    <Select defaultValue="3600" style={{ width: 200, marginLeft: 16 }}>
                      <Select.Option value="1800">30 minutes</Select.Option>
                      <Select.Option value="3600">1 hour</Select.Option>
                      <Select.Option value="86400">24 hours</Select.Option>
                    </Select>
                  </div>
                  <div>
                    <Text strong>Code Splitting Strategy:</Text>
                    <Select defaultValue="route" style={{ width: 200, marginLeft: 16 }}>
                      <Select.Option value="route">Route-based</Select.Option>
                      <Select.Option value="component">Component-based</Select.Option>
                      <Select.Option value="vendor">Vendor splitting</Select.Option>
                    </Select>
                  </div>
                </Space>

                <Divider />

                <Button type="primary" size="large">
                  Save Settings
                </Button>
              </Card>
            ),
          },
        ]}
      />
    </div>
  );
};

export default PerformanceOptimizationPage;

vendor/subscription-management.__content:
import React, { useState } from 'react';
import { Card, Row, Col, Statistic, Typography, Button, Space, Alert, Badge, Progress, Table, Modal, Form, Select, Input, Switch, Divider, Timeline, Tag } from 'antd';
import { CreditCardOutlined, StarOutlined, CheckCircleOutlined, ClockCircleOutlined, TrophyOutlined, UpgradeOutlined, GiftOutlined, SettingOutlined } from '@ant-design/icons';

const { Title, Text, Paragraph } = Typography;
const { Option } = Select;
const { TextArea } = Input;

const VendorSubscriptionManagement: React.FC = () => {
  const [currentPlan, setCurrentPlan] = useState('professional');
  const [upgradeModalVisible, setUpgradeModalVisible] = useState(false);
  const [selectedUpgrade, setSelectedUpgrade] = useState('');
  const [form] = Form.useForm();

  const subscriptionData = {
    planName: 'Professional',
    status: 'active',
    billingCycle: 'monthly',
    nextBilling: '2025-12-06',
    monthlyFee: 299.99,
    commission: 8.5,
    maxProducts: 1000,
    currentProducts: 342,
    features: ['Advanced Analytics', 'Priority Support', 'Bulk Operations', 'API Access'],
  };

  const plans = [
    {
      id: 'basic',
      name: 'Basic',
      price: 99.99,
      commission: 12.0,
      maxProducts: 100,
      features: ['Basic Analytics', 'Email Support', 'Standard Listings'],
      badge: null,
    },
    {
      id: 'professional',
      name: 'Professional',
      price: 299.99,
      commission: 8.5,
      maxProducts: 1000,
      features: ['Advanced Analytics', 'Priority Support', 'Bulk Operations', 'API Access'],
      badge: 'Current',
    },
    {
      id: 'enterprise',
      name: 'Enterprise',
      price: 599.99,
      commission: 6.0,
      maxProducts: 10000,
      features: ['Custom Analytics', '24/7 Phone Support', 'White-label Options', 'Dedicated Manager'],
      badge: 'Popular',
    },
  ];

  const usageHistory = [
    { date: '2025-11-06', products: 342, orders: 87, revenue: 12450, commission: 8.5 },
    { date: '2025-10-06', products: 298, orders: 74, revenue: 9850, commission: 8.5 },
    { date: '2025-09-06', products: 245, orders: 62, revenue: 8200, commission: 8.5 },
  ];

  const billingHistory = [
    { date: '2025-11-06', amount: 299.99, status: 'paid', method: 'Credit Card ****1234' },
    { date: '2025-10-06', amount: 299.99, status: 'paid', method: 'Credit Card ****1234' },
    { date: '2025-09-06', amount: 299.99, status: 'paid', method: 'Credit Card ****1234' },
    { date: '2025-08-06', amount: 299.99, status: 'failed', method: 'Credit Card ****1234' },
  ];

  const addOns = [
    { id: 'analytics-plus', name: 'Advanced Analytics Plus', price: 49.99, description: 'Enhanced reporting with custom dashboards' },
    { id: 'premium-support', name: 'Premium Support', price: 99.99, description: '24/7 priority support with dedicated rep' },
    { id: 'marketing-tools', name: 'Marketing Automation', price: 79.99, description: 'Email campaigns and promotional tools' },
  ];

  const handleUpgrade = async () => {
    try {
      const values = await form.validateFields();
      // Handle upgrade logic here
      setUpgradeModalVisible(false);
      setCurrentPlan(selectedUpgrade);
    } catch (error) {
      console.error('Upgrade failed:', error);
    }
  };

  const usageColumns = [
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
    },
    {
      title: 'Products',
      dataIndex: 'products',
      key: 'products',
    },
    {
      title: 'Orders',
      dataIndex: 'orders',
      key: 'orders',
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      render: (revenue: number) => `$${revenue.toLocaleString()}`,
    },
    {
      title: 'Commission Rate',
      dataIndex: 'commission',
      key: 'commission',
      render: (rate: number) => `${rate}%`,
    },
  ];

  const billingColumns = [
    {
      title: 'Date',
      dataIndex: 'date',
      key: 'date',
    },
    {
      title: 'Amount',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount: number) => `$${amount}`,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Badge 
          status={status === 'paid' ? 'success' : 'error'} 
          text={status.toUpperCase()} 
        />
      ),
    },
    {
      title: 'Payment Method',
      dataIndex: 'method',
      key: 'method',
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <CreditCardOutlined style={{ marginRight: 8 }} />
          Subscription Management
        </Title>
        <Text type="secondary">Phase 5 - Vendor subscription and billing management</Text>
      </div>

      {/* Current Plan Overview */}
      <Card style={{ marginBottom: 24 }}>
        <Row gutter={16}>
          <Col span={6}>
            <div style={{ textAlign: 'center' }}>
              <Badge.Ribbon text="Active" color="green">
                <Card size="small">
                  <StarOutlined style={{ fontSize: 32, color: '#1890ff', marginBottom: 8 }} />
                  <Title level={4}>{subscriptionData.planName} Plan</Title>
                  <Text type="secondary">Monthly Billing</Text>
                </Card>
              </Badge.Ribbon>
            </div>
          </Col>
          <Col span={18}>
            <Row gutter={16}>
              <Col span={4}>
                <Statistic
                  title="Monthly Fee"
                  value={subscriptionData.monthlyFee}
                  prefix="$"
                  precision={2}
                />
              </Col>
              <Col span={4}>
                <Statistic
                  title="Commission Rate"
                  value={subscriptionData.commission}
                  suffix="%"
                  valueStyle={{ color: '#52c41a' }}
                />
              </Col>
              <Col span={4}>
                <Statistic
                  title="Products Used"
                  value={subscriptionData.currentProducts}
                  suffix={`/ ${subscriptionData.maxProducts}`}
                />
              </Col>
              <Col span={4}>
                <Statistic
                  title="Next Billing"
                  value={subscriptionData.nextBilling}
                  valueStyle={{ fontSize: '14px' }}
                />
              </Col>
              <Col span={8}>
                <div style={{ marginTop: 8 }}>
                  <Text strong>Product Usage:</Text>
                  <Progress 
                    percent={(subscriptionData.currentProducts / subscriptionData.maxProducts) * 100} 
                    status="active" 
                    style={{ marginTop: 4 }}
                  />
                  <Space wrap style={{ marginTop: 8 }}>
                    {subscriptionData.features.map((feature, index) => (
                      <Tag key={index} color="blue" icon={<CheckCircleOutlined />}>
                        {feature}
                      </Tag>
                    ))}
                  </Space>
                </div>
              </Col>
            </Row>
          </Col>
        </Row>
      </Card>

      <Alert
        message="Subscription Status"
        description="Your Professional plan is active and in good standing. Next billing date: December 6, 2025."
        type="success"
        showIcon
        style={{ marginBottom: 24 }}
        action={
          <Button size="small" type="primary" onClick={() => setUpgradeModalVisible(true)}>
            Upgrade Plan
          </Button>
        }
      />

      <Row gutter={16}>
        <Col span={16}>
          {/* Available Plans */}
          <Card title="Available Plans" style={{ marginBottom: 24 }}>
            <Row gutter={16}>
              {plans.map((plan) => (
                <Col span={8} key={plan.id}>
                  <Card
                    size="small"
                    style={{
                      border: plan.id === currentPlan ? '2px solid #1890ff' : '1px solid #d9d9d9',
                      position: 'relative',
                    }}
                  >
                    {plan.badge && (
                      <div style={{
                        position: 'absolute',
                        top: -8,
                        right: 8,
                        background: plan.badge === 'Current' ? '#1890ff' : '#52c41a',
                        color: 'white',
                        padding: '2px 8px',
                        borderRadius: 4,
                        fontSize: '12px',
                      }}>
                        {plan.badge}
                      </div>
                    )}
                    <div style={{ textAlign: 'center', marginBottom: 16 }}>
                      <Title level={4}>{plan.name}</Title>
                      <Title level={2} style={{ color: '#1890ff', margin: 0 }}>
                        ${plan.price}
                      </Title>
                      <Text type="secondary">/month</Text>
                    </div>
                    <div style={{ marginBottom: 16 }}>
                      <Text strong>Commission: {plan.commission}%</Text>
                      <br />
                      <Text>Max Products: {plan.maxProducts.toLocaleString()}</Text>
                    </div>
                    <div style={{ marginBottom: 16 }}>
                      {plan.features.map((feature, index) => (
                        <div key={index} style={{ marginBottom: 4 }}>
                          <CheckCircleOutlined style={{ color: '#52c41a', marginRight: 8 }} />
                          <Text>{feature}</Text>
                        </div>
                      ))}
                    </div>
                    {plan.id !== currentPlan && (
                      <Button 
                        type="primary" 
                        block 
                        onClick={() => {
                          setSelectedUpgrade(plan.id);
                          setUpgradeModalVisible(true);
                        }}
                      >
                        {plan.price > subscriptionData.monthlyFee ? 'Upgrade' : 'Downgrade'}
                      </Button>
                    )}
                  </Card>
                </Col>
              ))}
            </Row>
          </Card>

          {/* Usage History */}
          <Card title="Usage History">
            <Table
              columns={usageColumns}
              dataSource={usageHistory}
              rowKey="date"
              pagination={false}
              size="small"
            />
          </Card>
        </Col>

        <Col span={8}>
          {/* Add-ons */}
          <Card title="Available Add-ons" style={{ marginBottom: 24 }}>
            {addOns.map((addon) => (
              <Card key={addon.id} size="small" style={{ marginBottom: 12 }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                  <div style={{ flex: 1 }}>
                    <Text strong>{addon.name}</Text>
                    <br />
                    <Text type="secondary" style={{ fontSize: '12px' }}>
                      {addon.description}
                    </Text>
                    <br />
                    <Text strong style={{ color: '#1890ff' }}>${addon.price}/month</Text>
                  </div>
                  <Button size="small" type="link">
                    Add
                  </Button>
                </div>
              </Card>
            ))}
          </Card>

          {/* Billing History */}
          <Card title="Recent Billing">
            <Timeline size="small">
              {billingHistory.slice(0, 4).map((bill, index) => (
                <Timeline.Item
                  key={index}
                  color={bill.status === 'paid' ? 'green' : 'red'}
                  dot={bill.status === 'paid' ? <CheckCircleOutlined /> : <ClockCircleOutlined />}
                >
                  <div>
                    <Text strong>${bill.amount}</Text>
                    <br />
                    <Text type="secondary">{bill.date}</Text>
                    <br />
                    <Badge status={bill.status === 'paid' ? 'success' : 'error'} text={bill.status.toUpperCase()} />
                  </div>
                </Timeline.Item>
              ))}
            </Timeline>
            <Button type="link" size="small">View All Billing History</Button>
          </Card>
        </Col>
      </Row>

      {/* Upgrade Modal */}
      <Modal
        title="Upgrade Subscription Plan"
        visible={upgradeModalVisible}
        onOk={handleUpgrade}
        onCancel={() => setUpgradeModalVisible(false)}
        width={600}
      >
        {selectedUpgrade && (
          <div>
            <Alert
              message="Plan Upgrade Confirmation"
              description={`You are upgrading to the ${plans.find(p => p.id === selectedUpgrade)?.name} plan.`}
              type="info"
              style={{ marginBottom: 16 }}
            />

            <Form form={form} layout="vertical">
              <Form.Item label="Billing Cycle" name="billingCycle" initialValue="monthly">
                <Select>
                  <Option value="monthly">Monthly - Pay each month</Option>
                  <Option value="yearly">Yearly - Save 20%</Option>
                </Select>
              </Form.Item>

              <Form.Item label="Payment Method" name="paymentMethod" initialValue="existing">
                <Select>
                  <Option value="existing">Use existing card ****1234</Option>
                  <Option value="new">Add new payment method</Option>
                </Select>
              </Form.Item>

              <Form.Item label="Promo Code (Optional)" name="promoCode">
                <Input placeholder="Enter promo code" />
              </Form.Item>

              <Form.Item name="autoRenew" valuePropName="checked" initialValue={true}>
                <Switch /> <Text>Enable auto-renewal</Text>
              </Form.Item>

              <Divider />

              <div style={{ background: '#f5f5f5', padding: 16, borderRadius: 4 }}>
                <Text strong>Billing Summary:</Text>
                <div style={{ marginTop: 8 }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
