    icon: <DesktopOutlined />,
    color: '#13c2c2',
  },
];

const responsiveFeatures: ResponsiveFeature[] = [
  {
    feature: 'Navigation Menu',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Product Grid Layout',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Shopping Cart',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Product Details',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'Checkout Flow',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
  {
    feature: 'User Profile',
    mobile: true,
    tablet: true,
    desktop: true,
    status: 'complete',
  },
