} from 'antd';
import {
  BellOutlined,
  ShoppingCartOutlined,
  GiftOutlined,
  TruckOutlined,
  CheckCircleOutlined,
  WarningOutlined,
  StarOutlined,
  DollarOutlined,
  HeartOutlined,
  MessageOutlined,
  MoreOutlined,
  DeleteOutlined,
  EyeOutlined,
  SettingOutlined,
  ClockCircleOutlined,
} from '@ant-design/icons';
import dayjs from 'dayjs';
