    actionUrl: '/orders/12343',
    actionText: 'View Order',
    image: 'https://via.placeholder.com/40?text=Confirm',
  },
];

const NotificationCenterPage: React.FC = () => {
  const [notifications, setNotifications] = useState<Notification[]>(mockNotifications);
  const [filterType, setFilterType] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [activeTab, setActiveTab] = useState<string>('all');

  const typeConfig = {
    order: { icon: <ShoppingCartOutlined />, color: '#1890ff', label: 'Orders' },
    promotion: { icon: <GiftOutlined />, color: '#52c41a', label: 'Promotions' },
    shipping: { icon: <TruckOutlined />, color: '#fa8c16', label: 'Shipping' },
    payment: { icon: <DollarOutlined />, color: '#722ed1', label: 'Payments' },
    review: { icon: <StarOutlined />, color: '#faad14', label: 'Reviews' },
    wishlist: { icon: <HeartOutlined />, color: '#eb2f96', label: 'Wishlist' },
    message: { icon: <MessageOutlined />, color: '#13c2c2', label: 'Messages' },
    system: { icon: <WarningOutlined />, color: '#f5222d', label: 'System' },
  };

  const handleMarkAsRead = (id: number) => {
    setNotifications(
      notifications.map((n) => (n.id === id ? { ...n, isRead: true } : n))
    );
  };

  const handleMarkAllAsRead = () => {
    setNotifications(notifications.map((n) => ({ ...n, isRead: true })));
    message.success('All notifications marked as read');
  };

  const handleDelete = (id: number) => {
    setNotifications(notifications.filter((n) => n.id !== id));
    message.success('Notification deleted');
  };

  const handleClearAll = () => {
    setNotifications(notifications.filter((n) => !n.isRead));
    message.success('All read notifications cleared');
  };

  const filteredNotifications = notifications.filter((n) => {
    const typeMatch = filterType === 'all' || n.type === filterType;
    const searchMatch =
      searchQuery === '' ||
      n.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      n.message.toLowerCase().includes(searchQuery.toLowerCase());
    const tabMatch =
      activeTab === 'all' ||
      (activeTab === 'unread' && !n.isRead) ||
      (activeTab === 'read' && n.isRead);
    return typeMatch && searchMatch && tabMatch;
  });

  const unreadCount = notifications.filter((n) => !n.isRead).length;
  const highPriorityCount = notifications.filter(
    (n) => !n.isRead && n.priority === 'high'
  ).length;

  const notificationMenu = (notification: Notification) => (
    <Menu>
      <Menu.Item
        key="view"
        icon={<EyeOutlined />}
        onClick={() => {
          handleMarkAsRead(notification.id);
          if (notification.actionUrl) {
            message.info(`Navigating to ${notification.actionUrl}`);
          }
        }}
      >
        View Details
      </Menu.Item>
      <Menu.Item
        key="mark"
        icon={<CheckCircleOutlined />}
        onClick={() => handleMarkAsRead(notification.id)}
        disabled={notification.isRead}
      >
        Mark as Read
      </Menu.Item>
      <Menu.Divider />
      <Menu.Item
        key="delete"
        icon={<DeleteOutlined />}
        danger
        onClick={() => handleDelete(notification.id)}
      >
        Delete
      </Menu.Item>
    </Menu>
  );

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3}>
            <Badge count={unreadCount} offset={[10, 0]}>
              <BellOutlined style={{ color: '#1890ff', marginRight: 8 }} />
            </Badge>
            Notification Center
          </Title>
          <Paragraph type="secondary">
            Stay updated with your orders, promotions, and more
          </Paragraph>
        </Col>
        <Col>
          <Space>
            <Button onClick={handleMarkAllAsRead} disabled={unreadCount === 0}>
              Mark All as Read
            </Button>
            <Button onClick={handleClearAll} disabled={notifications.every((n) => !n.isRead)}>
              Clear Read
            </Button>
            <Button icon={<SettingOutlined />}>Settings</Button>
          </Space>
        </Col>
      </Row>

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div
                style={{
                  width: 48,
                  height: 48,
                  background: '#e6f7ff',
                  borderRadius: 8,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <BellOutlined style={{ fontSize: 24, color: '#1890ff' }} />
              </div>
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Unread Notifications
                </Text>
                <div>
                  <Text strong style={{ fontSize: 24 }}>
                    {unreadCount}
                  </Text>
                </div>
              </div>
            </div>
          </Card>
        </Col>

        <Col xs={24} sm={8}>
          <Card>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div
                style={{
                  width: 48,
                  height: 48,
                  background: '#fff7e6',
                  borderRadius: 8,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <WarningOutlined style={{ fontSize: 24, color: '#fa8c16' }} />
              </div>
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  High Priority
                </Text>
                <div>
                  <Text strong style={{ fontSize: 24 }}>
                    {highPriorityCount}
                  </Text>
                </div>
              </div>
            </div>
          </Card>
        </Col>

        <Col xs={24} sm={8}>
          <Card>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <div
                style={{
                  width: 48,
                  height: 48,
                  background: '#f6ffed',
                  borderRadius: 8,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <CheckCircleOutlined style={{ fontSize: 24, color: '#52c41a' }} />
              </div>
              <div>
                <Text type="secondary" style={{ fontSize: 12 }}>
                  Total Notifications
                </Text>
                <div>
                  <Text strong style={{ fontSize: 24 }}>
                    {notifications.length}
                  </Text>
                </div>
              </div>
            </div>
          </Card>
        </Col>
      </Row>

      {highPriorityCount > 0 && (
        <Alert
          message={`You have ${highPriorityCount} high priority notification${highPriorityCount > 1 ? 's' : ''}`}
          description="Please review and take action on important notifications"
          type="warning"
          showIcon
          icon={<WarningOutlined />}
          closable
          style={{ marginBottom: 16 }}
        />
      )}

      <Card>
        <Space direction="vertical" style={{ width: '100%' }} size="middle">
          <Row gutter={16}>
            <Col xs={24} md={12}>
              <Search
                placeholder="Search notifications..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                allowClear
                size="large"
              />
            </Col>
            <Col xs={24} md={12}>
              <Select
                value={filterType}
                onChange={setFilterType}
                style={{ width: '100%' }}
                size="large"
              >
                <Option value="all">All Types</Option>
                {Object.entries(typeConfig).map(([key, config]) => (
                  <Option key={key} value={key}>
                    {config.icon} {config.label}
                  </Option>
                ))}
              </Select>
            </Col>
          </Row>

          <Tabs
            activeKey={activeTab}
            onChange={setActiveTab}
            items={[
              {
                key: 'all',
                label: (
                  <span>
                    All
                    <Badge
                      count={notifications.length}
                      style={{ marginLeft: 8, backgroundColor: '#d9d9d9' }}
                    />
                  </span>
                ),
              },
              {
                key: 'unread',
                label: (
                  <span>
                    Unread
                    <Badge count={unreadCount} style={{ marginLeft: 8 }} />
                  </span>
                ),
              },
              {
                key: 'read',
                label: (
                  <span>
                    Read
                    <Badge
                      count={notifications.length - unreadCount}
                      style={{ marginLeft: 8, backgroundColor: '#52c41a' }}
                    />
                  </span>
                ),
              },
            ]}
          />

          {filteredNotifications.length === 0 ? (
            <Empty
              description={
                searchQuery
                  ? 'No notifications found'
                  : activeTab === 'unread'
                  ? "You're all caught up!"
                  : 'No notifications'
              }
              image={Empty.PRESENTED_IMAGE_SIMPLE}
            />
          ) : (
            <List
              dataSource={filteredNotifications}
              renderItem={(notification) => {
                const config = typeConfig[notification.type];
                return (
                  <List.Item
                    key={notification.id}
                    style={{
                      background: notification.isRead ? '#fff' : '#e6f7ff',
                      padding: '16px',
                      marginBottom: 8,
                      borderRadius: 8,
                      border: notification.isRead ? '1px solid #f0f0f0' : '1px solid #91d5ff',
                    }}
                    actions={[
                      notification.actionText && (
                        <Button
                          type="link"
                          onClick={() => {
                            handleMarkAsRead(notification.id);
                            message.info(`Navigating to ${notification.actionUrl}`);
                          }}
                        >
                          {notification.actionText}
                        </Button>
                      ),
                      <Dropdown overlay={notificationMenu(notification)} trigger={['click']}>
                        <Button type="text" icon={<MoreOutlined />} />
                      </Dropdown>,
                    ]}
                  >
                    <List.Item.Meta
                      avatar={
                        <Badge dot={!notification.isRead} offset={[-5, 5]}>
                          <Avatar
                            src={notification.image}
                            icon={config.icon}
                            style={{ backgroundColor: config.color }}
                            size={48}
                          />
                        </Badge>
                      }
                      title={
                        <Space>
                          <Text strong={!notification.isRead}>{notification.title}</Text>
                          <Tag color={config.color}>{config.label}</Tag>
                          {notification.priority === 'high' && (
                            <Tag color="red" icon={<WarningOutlined />}>
                              High Priority
                            </Tag>
                          )}
                        </Space>
                      }
                      description={
                        <div>
                          <Paragraph
                            style={{ margin: 0, marginBottom: 8 }}
                            ellipsis={{ rows: 2 }}
                          >
                            {notification.message}
                          </Paragraph>
                          <Text type="secondary" style={{ fontSize: 12 }}>
                            <ClockCircleOutlined /> {dayjs(notification.timestamp).fromNow()}
                          </Text>
                        </div>
                      }
                    />
                  </List.Item>
                );
              }}
            />
          )}
        </Space>
      </Card>
    </div>
  );
};

export default NotificationCenterPage;

admin/reports/subscriptions.__content:
/**
 * SAFE API RESPONSE HANDLING - APPLY THIS PATTERN:
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * Always check: Array.isArray(data) before .map()/.filter()
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Statistic,
  Select,
  DatePicker,
  Button,
  Space,
  Typography,
  Table,
  Tag,
  Progress,
} from 'antd';
import {
  CrownOutlined,
  DollarOutlined,
  RiseOutlined,
  DownloadOutlined,
  TeamOutlined,
  TrophyOutlined,
} from '@ant-design/icons';
import { Column, Line } from '@ant-design/charts';
import type { ColumnsType } from 'antd/es/table';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;

interface SubscriptionData {
  id: number;
  planName: string;
  planType: string;
  subscribers: number;
  revenue: number;
  avgDuration: number;
  renewalRate: number;
  churnRate: number;
}

const mockSubscriptions: SubscriptionData[] = [
  {
    id: 1,
    planName: 'Basic Plan',
    planType: 'monthly',
    subscribers: 145,
    revenue: 7250,
    avgDuration: 8.5,
    renewalRate: 75,
    churnRate: 25,
  },
  {
    id: 2,
    planName: 'Pro Plan',
    planType: 'monthly',
    subscribers: 89,
    revenue: 8900,
    avgDuration: 12.3,
    renewalRate: 82,
    churnRate: 18,
  },
  {
    id: 3,
    planName: 'Enterprise Plan',
    planType: 'yearly',
    subscribers: 34,
    revenue: 20400,
    avgDuration: 18.7,
    renewalRate: 91,
    churnRate: 9,
  },
  {
    id: 4,
    planName: 'Starter Plan',
    planType: 'monthly',
    subscribers: 267,
    revenue: 8010,
    avgDuration: 5.2,
    renewalRate: 65,
    churnRate: 35,
  },
];

// Monthly subscription trend
const trendData = [
  { month: 'Jun', subscriptions: 412 },
  { month: 'Jul', subscriptions: 445 },
  { month: 'Aug', subscriptions: 478 },
  { month: 'Sep', subscriptions: 501 },
  { month: 'Oct', subscriptions: 520 },
  { month: 'Nov', subscriptions: 535 },
];

const AdminSubscriptionReportsPage: React.FC = () => {
  const [period, setPeriod] = useState<string>('month');

  const totalSubscribers = mockSubscriptions.reduce((sum, s) => sum + s.subscribers, 0);
  const totalRevenue = mockSubscriptions.reduce((sum, s) => sum + s.revenue, 0);
  const avgRenewalRate = mockSubscriptions.reduce((sum, s) => sum + s.renewalRate, 0) / mockSubscriptions.length;
  const avgChurnRate = mockSubscriptions.reduce((sum, s) => sum + s.churnRate, 0) / mockSubscriptions.length;

  // Growth calculation
  const lastMonth = trendData[trendData.length - 2]?.subscriptions || 0;
  const currentMonth = trendData[trendData.length - 1]?.subscriptions || 0;
  const growthRate = lastMonth > 0 ? ((currentMonth - lastMonth) / lastMonth) * 100 : 0;

  // Subscription trend chart
  const trendChartConfig = {
    data: trendData,
    xField: 'month',
    yField: 'subscriptions',
    smooth: true,
    color: '#1890ff',
    point: {
      size: 5,
      shape: 'circle',
    },
    label: {
      style: {
        fill: '#666',
      },
    },
  };

  // Revenue by plan chart
  const revenueChartData = mockSubscriptions.map(s => ({
    plan: s.planName,
    revenue: s.revenue,
  }));

  const revenueChartConfig = {
    data: revenueChartData,
    xField: 'plan',
    yField: 'revenue',
    color: '#52c41a',
    columnWidthRatio: 0.6,
    label: {
      position: 'top' as const,
      formatter: (datum: { revenue: number }) => `$${datum.revenue.toLocaleString()}`,
    },
    yAxis: {
      label: {
        formatter: (v: string) => `$${Number(v).toLocaleString()}`,
      },
    },
  };

  const getPlanTypeColor = (type: string) => {
    return type === 'yearly' ? 'gold' : 'blue';
  };

  const columns: ColumnsType<SubscriptionData> = [
    {
      title: 'Plan Name',
      dataIndex: 'planName',
      key: 'planName',
      fixed: 'left',
      width: 180,
      render: (text: string) => <Text strong>{text}</Text>,
    },
    {
      title: 'Type',
      dataIndex: 'planType',
      key: 'planType',
      width: 120,
      render: (type: string) => (
        <Tag color={getPlanTypeColor(type)}>
          {type.toUpperCase()}
        </Tag>
      ),
      filters: [
        { text: 'Monthly', value: 'monthly' },
        { text: 'Yearly', value: 'yearly' },
      ],
      onFilter: (value, record) => record.planType === value,
    },
    {
      title: 'Subscribers',
      dataIndex: 'subscribers',
      key: 'subscribers',
      width: 120,
      sorter: (a, b) => a.subscribers - b.subscribers,
      render: (value: number) => (
        <Space>
          <TeamOutlined />
          {value.toLocaleString()}
        </Space>
      ),
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      width: 140,
      sorter: (a, b) => a.revenue - b.revenue,
      render: (value: number) => (
        <Text strong style={{ color: '#52c41a' }}>
          ${value.toLocaleString()}
        </Text>
      ),
    },
    {
      title: 'Avg Duration (months)',
      dataIndex: 'avgDuration',
      key: 'avgDuration',
      width: 180,
      sorter: (a, b) => a.avgDuration - b.avgDuration,
      render: (value: number) => `${value.toFixed(1)} months`,
    },
    {
      title: 'Renewal Rate',
      dataIndex: 'renewalRate',
      key: 'renewalRate',
      width: 150,
      sorter: (a, b) => a.renewalRate - b.renewalRate,
      render: (value: number) => (
        <div>
          <Progress
            percent={value}
            size="small"
            status={value >= 80 ? 'success' : value >= 60 ? 'normal' : 'exception'}
          />
        </div>
      ),
    },
    {
      title: 'Churn Rate',
      dataIndex: 'churnRate',
      key: 'churnRate',
      width: 150,
      sorter: (a, b) => a.churnRate - b.churnRate,
      render: (value: number) => (
        <div>
          <Progress
            percent={value}
            size="small"
            strokeColor={value < 20 ? '#52c41a' : value < 30 ? '#faad14' : '#ff4d4f'}
          />
        </div>
      ),
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <Title level={3}>Subscription Reports</Title>
        <Space>
          <Select value={period} onChange={setPeriod} style={{ width: 150 }}>
            <Option value="week">Last 7 Days</Option>
            <Option value="month">This Month</Option>
            <Option value="quarter">This Quarter</Option>
            <Option value="year">This Year</Option>
            <Option value="custom">Custom Range</Option>
          </Select>
          {period === 'custom' && <RangePicker />}
          <Button type="primary" icon={<DownloadOutlined />}>
            Export Report
          </Button>
        </Space>
      </div>

      {/* Statistics Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Subscribers"
              value={totalSubscribers}
              prefix={<CrownOutlined style={{ color: '#1890ff' }} />}
              valueStyle={{ color: '#1890ff' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              <RiseOutlined style={{ color: '#52c41a' }} /> {growthRate.toFixed(1)}% growth
            </Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Total Revenue"
              value={totalRevenue}
              precision={2}
              prefix={<DollarOutlined style={{ color: '#52c41a' }} />}
              valueStyle={{ color: '#52c41a' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              Monthly recurring revenue
            </Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Avg Renewal Rate"
              value={avgRenewalRate}
              precision={1}
              suffix="%"
              prefix={<TrophyOutlined style={{ color: '#722ed1' }} />}
              valueStyle={{ color: '#722ed1' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              Customer retention
            </Text>
          </Card>
        </Col>
        <Col xs={24} sm={12} lg={6}>
          <Card>
            <Statistic
              title="Avg Churn Rate"
              value={avgChurnRate}
              precision={1}
              suffix="%"
              valueStyle={{ color: avgChurnRate < 20 ? '#52c41a' : '#faad14' }}
            />
            <Text type="secondary" style={{ fontSize: 12 }}>
              Customer drop-off
            </Text>
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={12}>
          <Card title="Subscription Growth Trend" variant="borderless">
            <Line {...trendChartConfig} />
          </Card>
        </Col>
        <Col xs={24} lg={12}>
          <Card title="Revenue by Plan" variant="borderless">
            <Column {...revenueChartConfig} />
          </Card>
        </Col>
      </Row>

      {/* Subscription Table */}
      <Card title="Subscription Plan Performance" variant="borderless">
        <Table
          columns={columns}
          dataSource={mockSubscriptions}
          rowKey="id"
          scroll={{ x: 1200 }}
          pagination={false}
        />
      </Card>
    </div>
  );
};

export default AdminSubscriptionReportsPage;

admin/security-enhancements.__content:
/**
 * SAFE API RESPONSE HANDLING
 * Use pattern: const data = response?.data?.data || response?.data || [];
 * Use pattern: const total = response?.data?.meta?.total || response?.meta?.total || 0;
 */
import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Table,
  Tag,
  Statistic,
  Space,
  Switch,
  Button,
  Alert,
  Tabs,
  List,
  Form,
  Input,
  Select,
  Divider,
  Progress,
  message,
  DatePicker,
  Spin,
  Modal,
} from 'antd';
import {
  SafetyOutlined,
  LockOutlined,
  WarningOutlined,
  CheckCircleOutlined,
  FireOutlined,
  SecurityScanOutlined,
  KeyOutlined,
  AuditOutlined,
  DownloadOutlined,
  ReloadOutlined,
  DeleteOutlined,
  EyeOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { logsAPI } from '@/services/api/logs';
import { securityAPI } from '@/services/api/security';
import type {
  ActivityLog,
  ErrorLog,
  SecurityLog,
  SystemLog,
  LogStats,
  LogFilters,
} from '@/services/api/logs';
import type {
  SecurityThreat,
  SecurityEvent as SecurityEventAPI,
  BlockedIP,
  SecurityMetrics,
  SecurityConfiguration,
  SecurityFilters,
} from '@/services/api/security';

const { Title, Text, Paragraph } = Typography;
const { RangePicker } = DatePicker;

interface SecurityEvent {
  id: number;
  type: 'login_attempt' | 'password_change' | 'suspicious_activity' | 'blocked_ip' | 'api_abuse';
  severity: 'low' | 'medium' | 'high' | 'critical';
  description: string;
  ip: string;
  timestamp: string;
  status: 'resolved' | 'pending' | 'investigating';
}

interface IPBlocklist {
  id: number;
  ip: string;
  reason: string;
  blockedAt: string;
  expiresAt: string | null;
  permanent: boolean;
}

const mockSecurityEvents: SecurityEvent[] = [
  {
    id: 1,
    type: 'login_attempt',
    severity: 'high',
    description: 'Multiple failed login attempts detected',
    ip: '192.168.1.100',
    timestamp: dayjs().subtract(2, 'hours').format('YYYY-MM-DD HH:mm:ss'),
    status: 'resolved',
  },
  {
    id: 2,
    type: 'suspicious_activity',
    severity: 'critical',
    description: 'SQL injection attempt detected',
    ip: '45.123.67.89',
    timestamp: dayjs().subtract(5, 'hours').format('YYYY-MM-DD HH:mm:ss'),
    status: 'resolved',
  },
  {
    id: 3,
    type: 'api_abuse',
    severity: 'medium',
    description: 'Rate limit exceeded (500 req/min)',
    ip: '10.0.0.55',
    timestamp: dayjs().subtract(1, 'day').format('YYYY-MM-DD HH:mm:ss'),
    status: 'investigating',
  },
  {
    id: 4,
    type: 'blocked_ip',
    severity: 'high',
    description: 'Malicious bot activity detected',
    ip: '203.45.12.78',
    timestamp: dayjs().subtract(3, 'days').format('YYYY-MM-DD HH:mm:ss'),
    status: 'resolved',
  },
];

const mockBlocklist: IPBlocklist[] = [
  {
    id: 1,
    ip: '45.123.67.89',
    reason: 'SQL injection attempts',
    blockedAt: dayjs().subtract(5, 'hours').format('YYYY-MM-DD HH:mm:ss'),
    expiresAt: null,
    permanent: true,
  },
  {
    id: 2,
    ip: '203.45.12.78',
    reason: 'Bot activity',
    blockedAt: dayjs().subtract(3, 'days').format('YYYY-MM-DD HH:mm:ss'),
    expiresAt: dayjs().add(7, 'days').format('YYYY-MM-DD HH:mm:ss'),
    permanent: false,
  },
];

const SecurityEnhancementsPage: React.FC = () => {
  // State for security features
  const [twoFactorEnabled, setTwoFactorEnabled] = useState<boolean>(true);
  const [sslEnabled, setSSLEnabled] = useState<boolean>(true);
  const [firewallEnabled, setFirewallEnabled] = useState<boolean>(true);
  const [bruteForceProtection, setBruteForceProtection] = useState<boolean>(true);
  const [form] = Form.useForm();

  // State for logs data
  const [loading, setLoading] = useState<boolean>(false);
  const [activityLogs, setActivityLogs] = useState<ActivityLog[]>([]);
  const [errorLogs, setErrorLogs] = useState<ErrorLog[]>([]);
  const [securityLogs, setSecurityLogs] = useState<SecurityLog[]>([]);
  const [systemLogs, setSystemLogs] = useState<SystemLog[]>([]);
  const [logStats, setLogStats] = useState<LogStats | null>(null);
  const [filters, setFilters] = useState<LogFilters>({});
  const [selectedLog, setSelectedLog] = useState<ActivityLog | ErrorLog | SecurityLog | SystemLog | null>(null);
  const [isDetailModalVisible, setIsDetailModalVisible] = useState<boolean>(false);

  // Security-specific state
  const [securityMetrics, setSecurityMetrics] = useState<SecurityMetrics | null>(null);
  const [securityThreats, setSecurityThreats] = useState<SecurityThreat[]>([]);
  const [securityEvents, setSecurityEvents] = useState<SecurityEventAPI[]>([]);
  const [blockedIPsList, setBlockedIPsList] = useState<BlockedIP[]>([]);
  const [securityConfig, setSecurityConfig] = useState<SecurityConfiguration | null>(null);
  const [securityLoading, setSecurityLoading] = useState<boolean>(false);

  // Fetch logs on mount
  useEffect(() => {
    fetchAllLogs();
    fetchLogStats();
    fetchSecurityData();
  }, []);

  const fetchSecurityData = async () => {
    setSecurityLoading(true);
    try {
      const [metricsRes, threatsRes, eventsRes, blockedIPsRes, configRes] = await Promise.all([
        securityAPI.getSecurityMetrics(),
        securityAPI.getThreats({ limit: 50 }),
        securityAPI.getSecurityEvents({ limit: 50 }),
        securityAPI.getBlockedIPs({ limit: 50 }),
        securityAPI.getSecurityConfiguration(),
      ]);

      setSecurityMetrics(metricsRes);
      setSecurityThreats(threatsRes.data);
      setSecurityEvents(eventsRes.data);
      setBlockedIPsList(blockedIPsRes.data);
      setSecurityConfig(configRes);
    } catch (error) {
      // If APIs aren't available yet, continue with mock data
      console.warn('Security APIs not available, using mock data:', error);
    } finally {
      setSecurityLoading(false);
    }
  };

  const fetchAllLogs = async () => {
    setLoading(true);
    try {
      const [activityRes, errorRes, securityRes, systemRes] = await Promise.all([
        logsAPI.getActivityLogs({ limit: 50 }),
        logsAPI.getErrorLogs({ limit: 50 }),
        logsAPI.getSecurityLogs({ limit: 50 }),
        logsAPI.getSystemLogs({ limit: 50 }),
      ]);
      setActivityLogs(activityRes.data.logs);
      setErrorLogs(errorRes.data.logs);
      setSecurityLogs(securityRes.data.logs);
      setSystemLogs(systemRes.data.logs);
    } catch (error) {
      message.error('Failed to fetch logs');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const fetchLogStats = async () => {
    try {
      const response = await logsAPI.getLogStats();
      setLogStats(Array.isArray(response?.data?.data) ? response.data.data : (Array.isArray(response?.data) ? response.data : []));
    } catch (error) {
      console.error('Failed to fetch log stats:', error);
    }
  };

  const handleRefresh = () => {
    fetchAllLogs();
    fetchLogStats();
    message.success('Logs refreshed');
  };

  const handleExport = async (type: 'activity' | 'error' | 'security' | 'system') => {
    try {
      setLoading(true);
      const response = await logsAPI.exportLogs({
        type,
        format: 'csv',
        filters,
      });

      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${type}-logs-${dayjs().format('YYYY-MM-DD')}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      message.success('Logs exported successfully');
    } catch (error) {
      message.error('Failed to export logs');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteLogs = async (type: 'activity' | 'error' | 'security' | 'system', ids: string[]) => {
    try {
      await logsAPI.bulkDeleteLogs(type, ids);
      message.success('Logs deleted successfully');
      fetchAllLogs();
    } catch (error) {
      message.error('Failed to delete logs');
      console.error(error);
    }
  };

  const handleResolveError = async (id: string) => {
    try {
      await logsAPI.resolveErrorLog(id, 'Admin');
      message.success('Error log resolved');
      fetchAllLogs();
    } catch (error) {
      message.error('Failed to resolve error log');
      console.error(error);
    }
  };

  const handleUpdateSecurityStatus = async (id: string, status: SecurityLog['status']) => {
    try {
      await logsAPI.updateSecurityLogStatus(id, status, 'Admin');
      message.success('Security log status updated');
      fetchAllLogs();
    } catch (error) {
      message.error('Failed to update status');
      console.error(error);
    }
  };

  // Security-specific handlers
  const handleBlockIP = async (values: { ip: string; reason: string; permanent?: boolean }) => {
    try {
      await securityAPI.blockIP({
        ipAddress: values.ip,
        reason: values.reason,
        permanent: values.permanent || false,
      });
      message.success('IP address blocked successfully');
      form.resetFields();
      fetchSecurityData();
    } catch (error) {
      message.error('Failed to block IP address');
      console.error(error);
    }
  };

  const handleUnblockIP = async (id: string) => {
    try {
      await securityAPI.unblockIP(id);
      message.success('IP address unblocked successfully');
      fetchSecurityData();
    } catch (error) {
      message.error('Failed to unblock IP address');
      console.error(error);
    }
  };

  const handleResolveThreat = async (id: string, actionTaken: string) => {
    try {
      await securityAPI.resolveThreat(id, actionTaken);
      message.success('Threat resolved successfully');
      fetchSecurityData();
    } catch (error) {
      message.error('Failed to resolve threat');
      console.error(error);
    }
  };

  const handleSecurityRefresh = () => {
    fetchAllLogs();
    fetchSecurityData();
  };

  const handleViewDetails = (log: ActivityLog | ErrorLog | SecurityLog | SystemLog) => {
    setSelectedLog(log);
    setIsDetailModalVisible(true);
  };

  const eventColumns: ColumnsType<SecurityLog> = [
    {
      title: 'Severity',
      dataIndex: 'severity',
      key: 'severity',
      render: (severity: SecurityLog['severity']) => {
        const config: Record<SecurityLog['severity'], { color: string; icon: React.ReactNode }> = {
          low: { color: 'blue', icon: <CheckCircleOutlined /> },
          medium: { color: 'orange', icon: <WarningOutlined /> },
          high: { color: 'red', icon: <FireOutlined /> },
          critical: { color: 'purple', icon: <FireOutlined /> },
        };
        return (
          <Tag color={config[severity].color} icon={config[severity].icon}>
            {severity.toUpperCase()}
          </Tag>
        );
      },
      filters: [
        { text: 'Low', value: 'low' },
        { text: 'Medium', value: 'medium' },
        { text: 'High', value: 'high' },
        { text: 'Critical', value: 'critical' },
      ],
      onFilter: (value, record) => record.severity === value,
    },
    {
      title: 'Event Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: string) => <Tag>{type.replace('_', ' ').toUpperCase()}</Tag>,
    },
    {
      title: 'Description',
      dataIndex: 'description',
      key: 'description',
    },
    {
      title: 'IP Address',
      dataIndex: 'ipAddress',
      key: 'ipAddress',
      render: (ip) => <Text code>{ip}</Text>,
    },
    {
      title: 'Timestamp',
      dataIndex: 'createdAt',
      key: 'createdAt',
      render: (timestamp) => <Text type="secondary">{dayjs(timestamp).format('YYYY-MM-DD HH:mm:ss')}</Text>,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: SecurityLog['status']) => {
        const colors: Record<SecurityLog['status'], string> = {
          resolved: 'green',
          pending: 'orange',
          investigating: 'blue',
        };
        return <Tag color={colors[status]}>{status.toUpperCase()}</Tag>;
      },
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="link"
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handleViewDetails(record)}
          >
            View
          </Button>
          {record.status !== 'resolved' && (
            <Button
              type="link"
              size="small"
              onClick={() => handleUpdateSecurityStatus(record.id, 'resolved')}
            >
              Resolve
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const blocklistColumns: ColumnsType<BlockedIP | IPBlocklist> = [
    {
      title: 'IP Address',
      dataIndex: 'ipAddress',
      key: 'ipAddress',
      render: (ipAddress, record: any) => (
        <Text code strong>{ipAddress || record.ip}</Text>
      ),
    },
    {
      title: 'Reason',
      dataIndex: 'reason',
      key: 'reason',
    },
    {
      title: 'Blocked At',
      dataIndex: 'blockedAt',
      key: 'blockedAt',
      render: (time) => <Text type="secondary">{dayjs(time).format('YYYY-MM-DD HH:mm:ss')}</Text>,
    },
    {
      title: 'Expires',
      dataIndex: 'expiresAt',
      key: 'expiresAt',
      render: (expires, record: any) =>
        record.permanent ? <Tag color="red">Permanent</Tag> : 
        expires ? <Text>{dayjs(expires).format('YYYY-MM-DD HH:mm:ss')}</Text> : 
        <Text type="secondary">-</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record: any) => (
        <Button 
          size="small" 
          danger 
          onClick={() => handleUnblockIP(record.id)}
        >
          Unblock
        </Button>
      ),
    },
  ];

  const securityFeatures = [
    {
      title: 'SSL/TLS Encryption',
      description: '256-bit SSL encryption for all data transmission',
      enabled: sslEnabled,
      toggle: setSSLEnabled,
      icon: <LockOutlined />,
    },
    {
      title: 'Two-Factor Authentication',
      description: 'Require 2FA for admin and vendor accounts',
      enabled: twoFactorEnabled,
      toggle: setTwoFactorEnabled,
      icon: <KeyOutlined />,
    },
    {
      title: 'Web Application Firewall',
      description: 'Block malicious requests and common attacks',
      enabled: firewallEnabled,
      toggle: setFirewallEnabled,
      icon: <SecurityScanOutlined />,
    },
    {
      title: 'Brute Force Protection',
      description: 'Rate limiting and automatic IP blocking',
      enabled: bruteForceProtection,
      toggle: setBruteForceProtection,
      icon: <SafetyOutlined />,
    },
  ];

  // Calculate security statistics using real data when available
  const securityScore = securityMetrics?.securityScore || 
    (logStats?.totalLogs ? Math.min(100, 90 + Math.floor(logStats.totalLogs / 1000)) : 92);
  const threatsBlocked = securityMetrics?.totalThreats || logStats?.securityLogs || 142;
  const activeAlerts = securityEvents.filter((e) => e.severity === 'critical' || e.severity === 'high').length ||
    securityLogs.filter((e) => e.status !== 'resolved').length;
  const blockedIPsCount = blockedIPsList.length || mockBlocklist.length;

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <SecurityScanOutlined style={{ color: '#1890ff' }} /> Security Enhancements
        </Title>
        <Paragraph type="secondary">
          Manage security settings, monitor threats, and protect your platform
        </Paragraph>
      </div>

      <Alert
        message="Security Status: Good"
        description="All critical security features are enabled. No active threats detected."
        type="success"
        showIcon
        icon={<SafetyOutlined />}
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Security Score"
              value={securityScore}
              suffix="/100"
              prefix={<SecurityScanOutlined />}
              valueStyle={{ color: securityScore > 80 ? '#52c41a' : '#faad14' }}
            />
            <Progress percent={securityScore} showInfo={false} strokeColor="#52c41a" />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Threats Blocked"
              value={threatsBlocked}
              prefix={<FireOutlined />}
              valueStyle={{ color: '#ff4d4f' }}
              suffix={
                <Tag color="green" style={{ marginLeft: 8 }}>
                  Today
                </Tag>
              }
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Active Alerts"
              value={activeAlerts}
              prefix={<WarningOutlined />}
              valueStyle={{ color: activeAlerts > 0 ? '#faad14' : '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Blocked IPs"
              value={blockedIPsCount}
              prefix={<AuditOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      <Tabs
        defaultActiveKey="features"
        items={[
          {
            key: 'features',
            label: 'Security Features',
            children: (
              <Card title="Security Features Configuration">
                <List
                  dataSource={securityFeatures}
                  renderItem={(item) => (
                    <List.Item
                      actions={[
                        <Switch
                          key="switch"
                          checked={item.enabled}
                          onChange={item.toggle}
                          checkedChildren="ON"
                          unCheckedChildren="OFF"
                        />,
                      ]}
                    >
                      <List.Item.Meta
                        avatar={
                          <div
                            style={{
                              fontSize: 24,
                              color: item.enabled ? '#52c41a' : '#d9d9d9',
                            }}
                          >
                            {item.icon}
                          </div>
                        }
                        title={<Text strong>{item.title}</Text>}
                        description={item.description}
                      />
                    </List.Item>
                  )}
                />

                <Divider />

                <Title level={5}>Additional Security Settings</Title>
                <Form layout="vertical">
                  <Row gutter={16}>
                    <Col span={12}>
                      <Form.Item label="Session Timeout (minutes)">
                        <Select defaultValue="30" style={{ width: '100%' }}>
                          <Select.Option value="15">15 minutes</Select.Option>
                          <Select.Option value="30">30 minutes</Select.Option>
                          <Select.Option value="60">1 hour</Select.Option>
                          <Select.Option value="120">2 hours</Select.Option>
                        </Select>
                      </Form.Item>
                    </Col>
                    <Col span={12}>
                      <Form.Item label="Max Login Attempts">
                        <Select defaultValue="5" style={{ width: '100%' }}>
                          <Select.Option value="3">3 attempts</Select.Option>
                          <Select.Option value="5">5 attempts</Select.Option>
                          <Select.Option value="10">10 attempts</Select.Option>
                        </Select>
                      </Form.Item>
                    </Col>
                    <Col span={12}>
                      <Form.Item label="Password Minimum Length">
                        <Select defaultValue="8" style={{ width: '100%' }}>
                          <Select.Option value="6">6 characters</Select.Option>
                          <Select.Option value="8">8 characters</Select.Option>
                          <Select.Option value="12">12 characters</Select.Option>
                        </Select>
                      </Form.Item>
                    </Col>
                    <Col span={12}>
                      <Form.Item label="API Rate Limit (req/min)">
                        <Select defaultValue="100" style={{ width: '100%' }}>
                          <Select.Option value="50">50 requests</Select.Option>
                          <Select.Option value="100">100 requests</Select.Option>
                          <Select.Option value="200">200 requests</Select.Option>
                        </Select>
                      </Form.Item>
                    </Col>
                  </Row>
                  <Button type="primary">Save Settings</Button>
                </Form>
              </Card>
            ),
          },
          {
            key: 'events',
            label: 'Security Events',
            children: (
              <Card
                title="Recent Security Events"
                extra={
                  <Space>
                    <Button icon={<ReloadOutlined />} onClick={handleSecurityRefresh}>
                      Refresh
                    </Button>
                    <Button icon={<DownloadOutlined />} onClick={() => handleExport('security')}>
                      Export
                    </Button>
                  </Space>
                }
              >
                <Spin spinning={loading}>
                  <Table
                    columns={eventColumns}
                    dataSource={securityLogs}
                    rowKey="id"
                    pagination={{ pageSize: 10 }}
                  />
                </Spin>
              </Card>
            ),
          },
          {
            key: 'blocklist',
            label: 'IP Blocklist',
            children: (
              <Card
                title="Blocked IP Addresses"
                extra={
                  <Button type="primary" onClick={() => form.resetFields()}>
                    Add IP to Blocklist
                  </Button>
                }
              >
                <Form form={form} layout="inline" onFinish={handleBlockIP} style={{ marginBottom: 16 }}>
                  <Form.Item name="ip" rules={[{ required: true, message: 'Enter IP address' }]}>
                    <Input placeholder="Enter IP address" style={{ width: 200 }} />
                  </Form.Item>
                  <Form.Item name="reason" rules={[{ required: true, message: 'Enter reason' }]}>
                    <Input placeholder="Reason for blocking" style={{ width: 250 }} />
                  </Form.Item>
                  <Form.Item name="permanent" valuePropName="checked">
                    <Space>
                      <Text>Permanent:</Text>
                      <Switch />
                    </Space>
                  </Form.Item>
                  <Form.Item>
                    <Button type="primary" htmlType="submit">
                      Block IP
                    </Button>
                  </Form.Item>
                </Form>

                <Table
                  columns={blocklistColumns}
                  dataSource={blockedIPsList.length > 0 ? blockedIPsList : mockBlocklist as any}
                  rowKey="id"
                  pagination={{ pageSize: 10 }}
                  loading={securityLoading}
                />
              </Card>
            ),
          },
          {
            key: 'audit',
            label: 'Activity Logs',
            children: (
              <Card
                title="Activity Audit Log"
                extra={
                  <Space>
                    <Button icon={<ReloadOutlined />} onClick={handleRefresh}>
                      Refresh
                    </Button>
                    <Button icon={<DownloadOutlined />} onClick={() => handleExport('activity')}>
                      Export
                    </Button>
                  </Space>
                }
              >
                <Spin spinning={loading}>
                  <Table
                    columns={[
                      {
                        title: 'User',
                        dataIndex: 'userName',
                        key: 'userName',
                        render: (name, record: ActivityLog) => (
                          <Space direction="vertical" size={0}>
                            <Text strong>{name}</Text>
                            <Tag color="blue">{record.userRole}</Tag>
                          </Space>
                        ),
                      },
                      {
                        title: 'Action',
                        dataIndex: 'action',
                        key: 'action',
                        render: (action) => <Tag color="green">{action}</Tag>,
                      },
                      {
                        title: 'Resource',
                        dataIndex: 'resource',
                        key: 'resource',
                        render: (resource, record: ActivityLog) => (
                          <Space direction="vertical" size={0}>
                            <Text>{resource}</Text>
                            {record.resourceId && <Text type="secondary" code>{record.resourceId}</Text>}
                          </Space>
                        ),
                      },
                      {
                        title: 'Method',
                        dataIndex: 'method',
                        key: 'method',
                        render: (method) => {
                          const colors: Record<string, string> = {
                            GET: 'blue',
                            POST: 'green',
                            PUT: 'orange',
                            PATCH: 'purple',
                            DELETE: 'red',
                          };
                          return <Tag color={colors[method]}>{method}</Tag>;
                        },
                      },
                      {
                        title: 'Status',
                        dataIndex: 'status',
                        key: 'status',
                        render: (status) => (
                          <Tag color={status === 'success' ? 'green' : 'red'}>
                            {status.toUpperCase()}
                          </Tag>
                        ),
                      },
                      {
                        title: 'IP Address',
                        dataIndex: 'ipAddress',
                        key: 'ipAddress',
                        render: (ip) => <Text code>{ip}</Text>,
                      },
                      {
                        title: 'Duration',
                        dataIndex: 'duration',
                        key: 'duration',
                        render: (duration) => <Text type="secondary">{duration}ms</Text>,
                      },
                      {
                        title: 'Timestamp',
                        dataIndex: 'createdAt',
                        key: 'createdAt',
                        render: (timestamp) => (
                          <Text type="secondary">{dayjs(timestamp).format('YYYY-MM-DD HH:mm:ss')}</Text>
                        ),
                      },
                      {
                        title: 'Actions',
                        key: 'actions',
                        render: (_, record) => (
                          <Button
                            type="link"
                            size="small"
                            icon={<EyeOutlined />}
                            onClick={() => handleViewDetails(record)}
                          >
                            View
                          </Button>
                        ),
                      },
                    ]}
                    dataSource={activityLogs}
                    rowKey="id"
                    pagination={{ pageSize: 10 }}
                  />
                </Spin>
              </Card>
            ),
          },
          {
            key: 'errors',
            label: 'Error Logs',
            children: (
              <Card
                title="Error Logs"
                extra={
                  <Space>
                    <Button icon={<ReloadOutlined />} onClick={handleRefresh}>
                      Refresh
                    </Button>
                    <Button icon={<DownloadOutlined />} onClick={() => handleExport('error')}>
                      Export
                    </Button>
                  </Space>
                }
              >
                <Spin spinning={loading}>
                  <Table
                    columns={[
                      {
                        title: 'Level',
                        dataIndex: 'level',
                        key: 'level',
                        render: (level: ErrorLog['level']) => {
                          const colors: Record<ErrorLog['level'], string> = {
                            error: 'red',
                            warn: 'orange',
                            info: 'blue',
                            debug: 'gray',
                          };
                          return <Tag color={colors[level]}>{level.toUpperCase()}</Tag>;
                        },
                        filters: [
                          { text: 'Error', value: 'error' },
                          { text: 'Warn', value: 'warn' },
                          { text: 'Info', value: 'info' },
                          { text: 'Debug', value: 'debug' },
                        ],
                        onFilter: (value, record) => record.level === value,
                      },
                      {
                        title: 'Message',
                        dataIndex: 'message',
                        key: 'message',
                        ellipsis: true,
                      },
                      {
                        title: 'Code',
                        dataIndex: 'code',
                        key: 'code',
                        render: (code) => code && <Tag>{code}</Tag>,
                      },
                      {
                        title: 'User',
                        dataIndex: 'userName',
                        key: 'userName',
                      },
                      {
                        title: 'URL',
                        dataIndex: 'url',
                        key: 'url',
                        ellipsis: true,
                        render: (url) => <Text code>{url}</Text>,
                      },
                      {
                        title: 'Status',
                        dataIndex: 'resolved',
                        key: 'resolved',
                        render: (resolved) => (
                          <Tag color={resolved ? 'green' : 'red'}>
                            {resolved ? 'Resolved' : 'Unresolved'}
                          </Tag>
                        ),
                      },
                      {
                        title: 'Timestamp',
                        dataIndex: 'createdAt',
                        key: 'createdAt',
                        render: (timestamp) => (
                          <Text type="secondary">{dayjs(timestamp).format('YYYY-MM-DD HH:mm:ss')}</Text>
                        ),
                      },
                      {
                        title: 'Actions',
                        key: 'actions',
                        render: (_, record) => (
                          <Space>
                            <Button
                              type="link"
                              size="small"
                              icon={<EyeOutlined />}
                              onClick={() => handleViewDetails(record)}
                            >
                              View
                            </Button>
                            {!record.resolved && (
                              <Button
                                type="link"
                                size="small"
                                onClick={() => handleResolveError(record.id)}
                              >
                                Resolve
                              </Button>
                            )}
                          </Space>
                        ),
                      },
                    ]}
                    dataSource={errorLogs}
                    rowKey="id"
                    pagination={{ pageSize: 10 }}
                  />
                </Spin>
              </Card>
            ),
          },
          {
            key: 'system',
            label: 'System Logs',
            children: (
              <Card
                title="System Logs"
                extra={
                  <Space>
                    <Button icon={<ReloadOutlined />} onClick={handleRefresh}>
                      Refresh
                    </Button>
                    <Button icon={<DownloadOutlined />} onClick={() => handleExport('system')}>
                      Export
                    </Button>
                  </Space>
                }
              >
                <Spin spinning={loading}>
                  <Table
                    columns={[
                      {
                        title: 'Type',
                        dataIndex: 'type',
                        key: 'type',
                        render: (type) => <Tag color="blue">{type.toUpperCase()}</Tag>,
                        filters: [
                          { text: 'System', value: 'system' },
                          { text: 'Database', value: 'database' },
                          { text: 'Cache', value: 'cache' },
                          { text: 'Storage', value: 'storage' },
                          { text: 'Queue', value: 'queue' },
                          { text: 'Email', value: 'email' },
                        ],
                        onFilter: (value, record) => record.type === value,
                      },
                      {
                        title: 'Level',
                        dataIndex: 'level',
                        key: 'level',
                        render: (level: SystemLog['level']) => {
                          const colors: Record<SystemLog['level'], string> = {
                            info: 'blue',
                            warn: 'orange',
                            error: 'red',
                            critical: 'purple',
                          };
                          return <Tag color={colors[level]}>{level.toUpperCase()}</Tag>;
                        },
                      },
                      {
                        title: 'Service',
                        dataIndex: 'service',
                        key: 'service',
                      },
                      {
                        title: 'Message',
                        dataIndex: 'message',
                        key: 'message',
                        ellipsis: true,
                      },
                      {
                        title: 'Timestamp',
                        dataIndex: 'createdAt',
                        key: 'createdAt',
                        render: (timestamp) => (
                          <Text type="secondary">{dayjs(timestamp).format('YYYY-MM-DD HH:mm:ss')}</Text>
                        ),
                      },
                      {
                        title: 'Actions',
                        key: 'actions',
                        render: (_, record) => (
                          <Button
                            type="link"
                            size="small"
                            icon={<EyeOutlined />}
                            onClick={() => handleViewDetails(record)}
                          >
                            View
                          </Button>
                        ),
                      },
                    ]}
                    dataSource={systemLogs}
                    rowKey="id"
                    pagination={{ pageSize: 10 }}
                  />
                </Spin>
              </Card>
            ),
          },
        ]}
      />

      {/* Log Detail Modal */}
      <Modal
        title="Log Details"
        open={isDetailModalVisible}
        onCancel={() => setIsDetailModalVisible(false)}
        footer={[
          <Button key="close" onClick={() => setIsDetailModalVisible(false)}>
            Close
          </Button>,
        ]}
        width={800}
      >
        {selectedLog && (
          <div>
            <Divider orientation="left">Basic Information</Divider>
            <Row gutter={[16, 16]}>
              <Col span={12}>
                <Text strong>ID:</Text> <Text code>{selectedLog.id}</Text>
              </Col>
              <Col span={12}>
                <Text strong>Timestamp:</Text>{' '}
                <Text>{dayjs(selectedLog.createdAt).format('YYYY-MM-DD HH:mm:ss')}</Text>
              </Col>
              {'userName' in selectedLog && selectedLog.userName && (
                <Col span={12}>
                  <Text strong>User:</Text> <Text>{selectedLog.userName}</Text>
                </Col>
              )}
              {'ipAddress' in selectedLog && (
                <Col span={12}>
                  <Text strong>IP Address:</Text> <Text code>{selectedLog.ipAddress}</Text>
                </Col>
              )}
            </Row>

            {/* Activity Log Details */}
            {'action' in selectedLog && (
              <>
                <Divider orientation="left">Activity Details</Divider>
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Action:</Text> <Tag color="green">{selectedLog.action}</Tag>
                  </Col>
                  <Col span={12}>
                    <Text strong>Resource:</Text> <Text>{selectedLog.resource}</Text>
                  </Col>
                  <Col span={12}>
                    <Text strong>Method:</Text> <Tag>{selectedLog.method}</Tag>
                  </Col>
                  <Col span={12}>
                    <Text strong>Status:</Text>{' '}
                    <Tag color={selectedLog.status === 'success' ? 'green' : 'red'}>
                      {selectedLog.status}
                    </Tag>
                  </Col>
                  <Col span={12}>
                    <Text strong>Duration:</Text> <Text>{selectedLog.duration}ms</Text>
                  </Col>
                </Row>
              </>
            )}

            {/* Error Log Details */}
            {'level' in selectedLog && 'message' in selectedLog && (
              <>
                <Divider orientation="left">Error Details</Divider>
                <Row gutter={[16, 16]}>
                  <Col span={24}>
                    <Text strong>Message:</Text>
                    <br />
                    <Text>{selectedLog.message}</Text>
                  </Col>
                  {'stack' in selectedLog && selectedLog.stack && (
                    <Col span={24}>
                      <Text strong>Stack Trace:</Text>
                      <pre
                        style={{
                          background: '#f5f5f5',
                          padding: 12,
                          borderRadius: 4,
                          overflow: 'auto',
                          maxHeight: 300,
                        }}
                      >
                        {selectedLog.stack}
                      </pre>
                    </Col>
                  )}
                </Row>
              </>
            )}

            {/* Security Log Details */}
            {'severity' in selectedLog && (
              <>
                <Divider orientation="left">Security Details</Divider>
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Type:</Text> <Tag>{selectedLog.type}</Tag>
                  </Col>
                  <Col span={12}>
                    <Text strong>Severity:</Text> <Tag color="red">{selectedLog.severity}</Tag>
                  </Col>
                  <Col span={24}>
                    <Text strong>Description:</Text>
                    <br />
                    <Text>{selectedLog.description}</Text>
                  </Col>
                </Row>
              </>
            )}

            {/* System Log Details */}
            {'service' in selectedLog && (
              <>
                <Divider orientation="left">System Details</Divider>
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Type:</Text> <Tag>{selectedLog.type}</Tag>
                  </Col>
                  <Col span={12}>
                    <Text strong>Service:</Text> <Text>{selectedLog.service}</Text>
                  </Col>
                </Row>
              </>
            )}

            {/* Metadata */}
            {selectedLog.metadata && Object.keys(selectedLog.metadata).length > 0 && (
              <>
                <Divider orientation="left">Metadata</Divider>
                <pre
                  style={{
                    background: '#f5f5f5',
                    padding: 12,
                    borderRadius: 4,
                    overflow: 'auto',
                  }}
                >
                  {JSON.stringify(selectedLog.metadata, null, 2)}
                </pre>
              </>
            )}
          </div>
        )}
      </Modal>
    </div>
  );
};

export default SecurityEnhancementsPage;

demo/personalized-dashboard.__content:
import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Switch,
  Divider,
  message,
  Select,
  Slider,
  ColorPicker,
  Input,
  Tag,
  List,
  Avatar,
  Checkbox,
} from 'antd';
import {
  DashboardOutlined,
  PlusOutlined,
  DeleteOutlined,
  EditOutlined,
  DragOutlined,
  BarChartOutlined,
  ShoppingCartOutlined,
  UserOutlined,
  DollarOutlined,
  HeartOutlined,
  SaveOutlined,
  ReloadOutlined,
} from '@ant-design/icons';
import type { Color } from 'antd/es/color-picker';

const { Title, Text, Paragraph } = Typography;

interface Widget {
  id: string;
  type: 'statistic' | 'chart' | 'list' | 'activity';
  title: string;
  icon: React.ReactNode;
  color: string;
  enabled: boolean;
  size: 'small' | 'medium' | 'large';
  position: number;
}

const availableWidgets: Widget[] = [
  {
    id: 'total_sales',
    type: 'statistic',
    title: 'Total Sales',
    icon: <DollarOutlined />,
    color: '#52c41a',
    enabled: true,
    size: 'small',
    position: 0,
  },
  {
    id: 'total_orders',
    type: 'statistic',
    title: 'Total Orders',
    icon: <ShoppingCartOutlined />,
    color: '#1890ff',
    enabled: true,
    size: 'small',
    position: 1,
  },
  {
    id: 'total_customers',
    type: 'statistic',
    title: 'Total Customers',
    icon: <UserOutlined />,
    color: '#722ed1',
    enabled: true,
    size: 'small',
    position: 2,
  },
  {
    id: 'wishlist_items',
    type: 'statistic',
    title: 'Wishlist Items',
    icon: <HeartOutlined />,
    color: '#ff4d4f',
    enabled: true,
    size: 'small',
    position: 3,
  },
  {
    id: 'sales_chart',
    type: 'chart',
    title: 'Sales Overview',
    icon: <BarChartOutlined />,
    color: '#1890ff',
    enabled: true,
    size: 'large',
    position: 4,
  },
  {
    id: 'recent_orders',
    type: 'list',
    title: 'Recent Orders',
    icon: <ShoppingCartOutlined />,
    color: '#ff9900',
    enabled: true,
    size: 'medium',
    position: 5,
  },
  {
    id: 'activity_feed',
    type: 'activity',
    title: 'Activity Feed',
    icon: <DashboardOutlined />,
    color: '#13c2c2',
    enabled: false,
    size: 'medium',
    position: 6,
  },
];

const PersonalizedDashboardPage: React.FC = () => {
  const [widgets, setWidgets] = useState<Widget[]>(availableWidgets);
  const [theme, setTheme] = useState<'light' | 'dark'>('light');
  const [compactMode, setCompactMode] = useState(false);
  const [primaryColor, setPrimaryColor] = useState<Color | string>('#1890ff');
  const [refreshInterval, setRefreshInterval] = useState(30);
  const [showAnimations, setShowAnimations] = useState(true);

  const handleToggleWidget = (id: string) => {
    setWidgets((prev) =>
      prev.map((w) => (w.id === id ? { ...w, enabled: !w.enabled } : w))
    );
    message.success('Widget visibility updated');
  };

  const handleChangeWidgetSize = (id: string, size: 'small' | 'medium' | 'large') => {
    setWidgets((prev) =>
      prev.map((w) => (w.id === id ? { ...w, size } : w))
    );
    message.success('Widget size updated');
  };

  const handleSaveSettings = () => {
    const settings = {
      widgets,
      theme,
      compactMode,
      primaryColor,
      refreshInterval,
      showAnimations,
    };
    localStorage.setItem('dashboard_settings', JSON.stringify(settings));
    message.success('Dashboard settings saved successfully!');
  };

  const handleResetSettings = () => {
    setWidgets(availableWidgets);
    setTheme('light');
    setCompactMode(false);
    setPrimaryColor('#1890ff');
    setRefreshInterval(30);
    setShowAnimations(true);
    localStorage.removeItem('dashboard_settings');
    message.info('Dashboard settings reset to default');
  };

  const enabledWidgets = widgets.filter((w) => w.enabled).sort((a, b) => a.position - b.position);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={3}>
          <DashboardOutlined style={{ color: '#1890ff' }} /> Personalized Dashboard
        </Title>
        <Paragraph type="secondary">
          Customize your dashboard layout, widgets, and appearance
        </Paragraph>
      </div>

      <Row gutter={16}>
        {/* Settings Panel */}
        <Col xs={24} lg={8}>
          <Card title="Dashboard Settings" style={{ marginBottom: 16 }}>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              {/* Theme */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Theme
                </Text>
                <Select
                  style={{ width: '100%' }}
                  value={theme}
                  onChange={setTheme}
                  options={[
                    { label: ' Light Theme', value: 'light' },
                    { label: ' Dark Theme', value: 'dark' },
                  ]}
                />
              </div>

              {/* Primary Color */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Primary Color
                </Text>
                <ColorPicker
                  value={primaryColor}
                  onChange={setPrimaryColor}
                  showText
                  style={{ width: '100%' }}
                />
              </div>

              {/* Refresh Interval */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 8 }}>
                  Auto Refresh Interval: {refreshInterval}s
                </Text>
                <Slider
                  min={10}
                  max={300}
                  step={10}
                  value={refreshInterval}
                  onChange={setRefreshInterval}
                  marks={{
                    10: '10s',
                    60: '1m',
                    120: '2m',
                    300: '5m',
                  }}
                />
              </div>

              <Divider />

              {/* Display Options */}
              <div>
                <Text strong style={{ display: 'block', marginBottom: 12 }}>
                  Display Options
                </Text>
                <Space direction="vertical" style={{ width: '100%' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text>Compact Mode</Text>
                    <Switch checked={compactMode} onChange={setCompactMode} />
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Text>Show Animations</Text>
                    <Switch checked={showAnimations} onChange={setShowAnimations} />
                  </div>
                </Space>
              </div>

              <Divider />

              {/* Action Buttons */}
              <Space style={{ width: '100%' }} direction="vertical">
                <Button
                  type="primary"
                  icon={<SaveOutlined />}
                  block
                  onClick={handleSaveSettings}
                >
                  Save Settings
                </Button>
                <Button
                  danger
                  icon={<ReloadOutlined />}
                  block
                  onClick={handleResetSettings}
                >
                  Reset to Default
                </Button>
              </Space>
            </Space>
          </Card>

          {/* Widget Manager */}
          <Card title="Widget Manager">
            <List
              dataSource={widgets}
              renderItem={(widget) => (
                <List.Item
                  actions={[
                    <Switch
                      key="toggle"
                      checked={widget.enabled}
                      onChange={() => handleToggleWidget(widget.id)}
                    />,
                  ]}
                >
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        icon={widget.icon}
                        style={{ backgroundColor: widget.color }}
                      />
                    }
                    title={widget.title}
                    description={
                      <Space>
                        <Select
                          size="small"
                          value={widget.size}
                          onChange={(size) => handleChangeWidgetSize(widget.id, size)}
                          disabled={!widget.enabled}
                          style={{ width: 100 }}
                          options={[
                            { label: 'Small', value: 'small' },
                            { label: 'Medium', value: 'medium' },
                            { label: 'Large', value: 'large' },
                          ]}
                        />
                        <Tag color={widget.enabled ? 'green' : 'default'}>
                          {widget.type}
                        </Tag>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        {/* Dashboard Preview */}
        <Col xs={24} lg={16}>
          <Card
            title={
              <Space>
                <DashboardOutlined />
                <span>Dashboard Preview</span>
                <Tag color="blue">{enabledWidgets.length} widgets</Tag>
              </Space>
            }
          >
            <Row gutter={[16, 16]}>
              {enabledWidgets.map((widget) => {
                const colSpan =
                  widget.size === 'small'
                    ? { xs: 24, sm: 12, md: 6 }
                    : widget.size === 'medium'
                    ? { xs: 24, sm: 12 }
                    : { xs: 24 };

                return (
                  <Col key={widget.id} {...colSpan}>
                    <Card
                      size={compactMode ? 'small' : 'default'}
                      style={{
                        borderLeft: `4px solid ${widget.color}`,
                        transition: showAnimations ? 'all 0.3s' : 'none',
                      }}
                      hoverable={showAnimations}
                    >
                      <Space direction="vertical" style={{ width: '100%' }}>
                        <Space>
                          <div style={{ fontSize: 24, color: widget.color }}>
                            {widget.icon}
                          </div>
                          <div>
                            <Text strong style={{ fontSize: 16 }}>
                              {widget.title}
                            </Text>
                            <div>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {widget.type.toUpperCase()}
                              </Text>
                            </div>
                          </div>
                        </Space>

                        {/* Widget Content Preview */}
                        {widget.type === 'statistic' && (
                          <div style={{ padding: '16px 0' }}>
                            <div style={{ fontSize: 32, fontWeight: 'bold', color: widget.color }}>
                              {Math.floor(Math.random() * 10000)}
                            </div>
                            <Text type="secondary">
                              +{Math.floor(Math.random() * 20)}% from last month
                            </Text>
                          </div>
                        )}

                        {widget.type === 'chart' && (
                          <div
                            style={{
                              height: 200,
                              background: '#f0f0f0',
                              borderRadius: 4,
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                            }}
                          >
                            <BarChartOutlined style={{ fontSize: 48, color: '#bfbfbf' }} />
                          </div>
                        )}

                        {widget.type === 'list' && (
                          <List
                            size="small"
                            dataSource={[1, 2, 3]}
                            renderItem={(item) => (
                              <List.Item>
                                <Text>Order #1234{item}</Text>
                                <Tag color="blue">${(Math.random() * 100).toFixed(2)}</Tag>
                              </List.Item>
                            )}
                          />
                        )}

                        {widget.type === 'activity' && (
                          <Space direction="vertical" style={{ width: '100%' }} size="small">
                            {[1, 2, 3].map((i) => (
                              <div key={i} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                                <Avatar size="small" icon={<UserOutlined />} />
                                <Text type="secondary" style={{ fontSize: 12 }}>
                                  User action #{i}
                                </Text>
                              </div>
                            ))}
                          </Space>
                        )}
                      </Space>
                    </Card>
                  </Col>
                );
              })}

              {enabledWidgets.length === 0 && (
                <Col span={24}>
                  <div
