const { Title, Text } = Typography;
const { Option } = Select;
const { RangePicker } = DatePicker;

interface Transaction {
  id: number;
  transactionId: string;
  date: string;
  type: string;
  category: string;
  amount: number;
  status: string;
  from: string;
  to: string;
