admin/inventory-management.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import { Card, Table, Button, Space, Tag, Row, Col, Statistic, Input, Select, message, Alert, Tabs, Typography, Modal, Form, InputNumber } from 'antd';
import { DownloadOutlined, EditOutlined, ExclamationCircleOutlined, WarningOutlined, ReloadOutlined, InboxOutlined, SearchOutlined } from '@ant-design/icons';
import { productAPI, inventoryAlertsAPI } from '@/services/api';
import type { Product } from '@/services/api';

const { Search } = Input;
const { Option } = Select;
const { Title, Text } = Typography;
const { TabPane } = Tabs;

const InventoryManagementPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [inventory, setInventory] = useState<Product[]>([]);
  const [filteredInventory, setFilteredInventory] = useState<Product[]>([]);
  const [alerts, setAlerts] = useState<any[]>([]);
  const [stats, setStats] = useState({
    totalProducts: 0,
    totalValue: 0,
    lowStockCount: 0,
    outOfStockCount: 0,
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('');
  const [editModalVisible, setEditModalVisible] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [form] = Form.useForm();

  useEffect(() => {
    fetchInventory();
    fetchAlerts();
  }, []);

  useEffect(() => {
    filterInventory();
  }, [inventory, searchTerm, statusFilter]);

  const fetchInventory = async () => {
    setLoading(true);
    try {
      const response = await productAPI.getProducts({
        page: 1,
        limit: 1000, // Get all products for inventory view
      });

      const products = response?.data?.data || response?.data || []?.items || [];
      setInventory(products);

      // Calculate stats
      const totalProducts = products.length;
      const totalValue = products.reduce((sum: number, product: Product) => sum + (product.basePrice * product.stockQuantity), 0);
      const lowStockCount = products.filter((p: Product) => p.stockQuantity > 0 && p.stockQuantity <= 10).length;
      const outOfStockCount = products.filter((p: Product) => p.stockQuantity === 0).length;

      setStats({
        totalProducts,
        totalValue,
        lowStockCount,
        outOfStockCount,
      });
    } catch (error) {
      console.error('Failed to fetch inventory:', error);
      message.error('Failed to load inventory data');
    } finally {
      setLoading(false);
    }
  };

  const fetchAlerts = async () => {
    try {
      const response = await inventoryAlertsAPI.getAlerts({
        limit: 100,
        offset: 0,
      });
      setAlerts(response.alerts || []);
    } catch (error) {
      console.error('Failed to fetch alerts:', error);
    }
  };

  const filterInventory = () => {
    let filtered = inventory;

    if (searchTerm) {
      filtered = filtered.filter(product => 
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.sku.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (statusFilter) {
      filtered = filtered.filter(product => {
        const status = getStockStatus(product);
        return status === statusFilter;
      });
    }

    setFilteredInventory(filtered);
  };

  const getStockStatus = (product: Product) => {
    if (product.stockQuantity === 0) return 'out_of_stock';
    if (product.stockQuantity <= 10) return 'low_stock';
    return 'in_stock';
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'in_stock': return 'green';
      case 'low_stock': return 'orange';
      case 'out_of_stock': return 'red';
      default: return 'blue';
    }
  };

  const handleStockUpdate = (product: Product) => {
    setEditingProduct(product);
    form.setFieldsValue({
      currentStock: product.stockQuantity,
    });
    setEditModalVisible(true);
  };

  const handleStockUpdateSubmit = async () => {
    try {
      const values = await form.validateFields();
      if (!editingProduct) return;

      await productAPI.updateProductStock(editingProduct.id, {
        stockQuantity: values.currentStock,
        operation: 'set',
        reason: 'Admin adjustment',
      });

      message.success('Stock updated successfully');
      setEditModalVisible(false);
      setEditingProduct(null);
      form.resetFields();
      fetchInventory(); // Refresh inventory
    } catch (error) {
      console.error('Stock update failed:', error);
      message.error('Failed to update stock');
    }
  };

  const columns = [
    {
      title: 'Product',
      key: 'product',
      render: (record: Product) => (
        <div>
          <Text strong>{record.name}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: '12px' }}>SKU: {record.sku}</Text>
        </div>
      ),
      width: 200,
    },
    {
      title: 'Category',
      key: 'category',
      render: (record: Product) => record.category?.name || 'No Category',
      width: 150,
    },
    {
      title: 'Current Stock',
      key: 'stock',
      render: (record: Product) => (
        <div>
          <Text strong style={{ color: record.stockQuantity <= 10 ? '#fa8c16' : '#52c41a' }}>
            {record.stockQuantity}
          </Text>
          <Text type="secondary"> units</Text>
        </div>
      ),
      width: 120,
    },
    {
      title: 'Status',
      key: 'status',
      render: (record: Product) => {
        const status = getStockStatus(record);
        return <Tag color={getStatusColor(status)}>{status.replace('_', ' ').toUpperCase()}</Tag>;
      },
      width: 120,
    },
    {
      title: 'Unit Price',
      key: 'price',
      render: (record: Product) => <Text strong>${record.basePrice.toFixed(2)}</Text>,
      width: 100,
    },
    {
      title: 'Total Value',
      key: 'value',
      render: (record: Product) => <Text strong>${(record.basePrice * record.stockQuantity).toLocaleString()}</Text>,
      width: 120,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (record: Product) => (
        <Button 
          type="text" 
          icon={<EditOutlined />} 
          onClick={() => handleStockUpdate(record)}
          title="Update Stock"
        />
      ),
      width: 80,
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ marginBottom: 24 }}>
        <Title level={2}>
          <InboxOutlined style={{ marginRight: 8 }} />
          Inventory Management
        </Title>
        <Text type="secondary">Phase 4 - Advanced inventory tracking system</Text>
      </div>

      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic title="Total Products" value={stats.totalProducts} valueStyle={{ color: '#1890ff' }} prefix={<InboxOutlined />} />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic title="Total Value" value={stats.totalValue} precision={2} prefix="$" valueStyle={{ color: '#52c41a' }} />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic title="Low Stock Items" value={stats.lowStockCount} valueStyle={{ color: '#fa8c16' }} prefix={<WarningOutlined />} />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic title="Out of Stock" value={stats.outOfStockCount} valueStyle={{ color: '#f5222d' }} prefix={<ExclamationCircleOutlined />} />
          </Card>
        </Col>
      </Row>

      <Alert
        message="Inventory Status Alert"
        description={`${stats.outOfStockCount} products out of stock, ${stats.lowStockCount} products running low.`}
        type="warning"
        showIcon
        style={{ marginBottom: 16 }}
        closable
      />

      <Card>
        <Tabs defaultActiveKey="inventory">
          <TabPane tab="Inventory Overview" key="inventory">
            <Row gutter={16} style={{ marginBottom: 16 }}>
              <Col span={6}>
                <Search 
                  placeholder="Search products..." 
                  prefix={<SearchOutlined />} 
                  allowClear 
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </Col>
              <Col span={4}>
                <Select 
                  placeholder="Status" 
                  style={{ width: '100%' }}
                  value={statusFilter}
                  onChange={setStatusFilter}
                  allowClear
                >
                  <Option value="in_stock">In Stock</Option>
                  <Option value="low_stock">Low Stock</Option>
                  <Option value="out_of_stock">Out of Stock</Option>
                </Select>
              </Col>
              <Col span={10}>
                <Space>
                  <Button icon={<ReloadOutlined />} onClick={fetchInventory} loading={loading}>
                    Refresh
                  </Button>
                  <Button icon={<DownloadOutlined />} onClick={() => message.success('Export completed')}>
                    Export
                  </Button>
                </Space>
              </Col>
            </Row>

            <Table
              columns={columns}
              dataSource={filteredInventory}
              rowKey="id"
              loading={loading}
              pagination={{ pageSize: 10, showTotal: (total) => `Total ${total} items` }}
            />
          </TabPane>

          <TabPane tab="Stock Movements" key="movements">
            <Alert message="Stock Movement Tracking" description="Track all inventory movements and transfers." type="info" />
          </TabPane>

          <TabPane tab="Analytics" key="analytics">
            <Alert message="Inventory Analytics" description="Advanced analytics and demand forecasting." type="info" />
          </TabPane>
        </Tabs>
      </Card>

      {/* Stock Update Modal */}
      <Modal
        title="Update Stock Quantity"
        open={editModalVisible}
        onOk={handleStockUpdateSubmit}
        onCancel={() => {
          setEditModalVisible(false);
          setEditingProduct(null);
          form.resetFields();
        }}
        okText="Update Stock"
      >
        {editingProduct && (
          <Form
            form={form}
            layout="vertical"
          >
            <div style={{ marginBottom: 16 }}>
              <Text strong>Product: </Text>
              <Text>{editingProduct.name}</Text>
              <br />
              <Text strong>SKU: </Text>
              <Text>{editingProduct.sku}</Text>
              <br />
              <Text strong>Current Stock: </Text>
              <Text>{editingProduct.stockQuantity} units</Text>
            </div>

            <Form.Item
              name="currentStock"
              label="New Stock Quantity"
              rules={[
                { required: true, message: 'Please enter stock quantity' },
                { type: 'number', min: 0, message: 'Stock cannot be negative' }
              ]}
            >
              <InputNumber
                style={{ width: '100%' }}
                placeholder="Enter new stock quantity"
                min={0}
                precision={0}
              />
            </Form.Item>
          </Form>
        )}
      </Modal>
    </div>
  );
};

export default InventoryManagementPage;

admin/procurement-insights.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Statistic,
  Table,
  Space,
  Button,
  DatePicker,
  Select,
  Tag,
  Progress,
  Alert,
  Tooltip,
  Tabs,
  Divider,
  Badge,
  message,
  Spin,
} from 'antd';
import {
  DollarOutlined,
  ShoppingCartOutlined,
  RiseOutlined,
  FallOutlined,
  TeamOutlined,
  BarChartOutlined,
  FileTextOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  DownloadOutlined,
  ReloadOutlined,
} from '@ant-design/icons';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as ChartTooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import { ColumnsType } from 'antd/es/table';
import dayjs from 'dayjs';
import { analyticsAPI } from '../../services/api/analytics';

const { Title, Text } = Typography;
const { RangePicker } = DatePicker;
const { Option } = Select;
const { TabPane } = Tabs;

interface ProcurementData {
  totalSpend: number;
  totalOrders: number;
  uniqueVendors: number;
  avgOrderValue: number;
  savingsRealized: number;
  contractCompliance: number;
  spendByCategory: Array<{
    category: string;
    spend: number;
    orders: number;
    avgValue: number;
    growth: number;
  }>;
  vendorPerformance: Array<{
    vendorName: string;
    totalSpend: number;
    orderCount: number;
    performanceScore: number;
    onTimeDelivery: number;
    qualityRating: number;
    costEfficiency: number;
  }>;
  spendTrends: Array<{
    month: string;
    totalSpend: number;
    orders: number;
    avgValue: number;
  }>;
  savingsOpportunities: Array<{
    category: string;
    currentSpend: number;
    potentialSavings: number;
    savingsPercentage: number;
    effort: 'Low' | 'Medium' | 'High';
    timeline: string;
    recommendation: string;
  }>;
  riskIndicators: Array<{
    vendor: string;
    riskType: 'Financial' | 'Operational' | 'Compliance' | 'Performance';
    level: 'Low' | 'Medium' | 'High';
    description: string;
    lastAssessed: string;
  }>;
  contractMetrics: {
    totalContracts: number;
    expiringContracts: number;
    renewalOpportunities: number;
    complianceRate: number;
  };
}

const ProcurementInsights: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState<ProcurementData | null>(null);
  const [dateRange, setDateRange] = useState<[dayjs.Dayjs, dayjs.Dayjs]>([
    dayjs().subtract(3, 'months'),
    dayjs(),
  ]);
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchProcurementData();
  }, [dateRange, selectedCategory]);

  const fetchProcurementData = async () => {
    try {
      setLoading(true);

      // Fetch analytics data and generate procurement insights
      const [overviewData, salesData] = await Promise.all([
        analyticsAPI.getOverview({
          startDate: dateRange[0].format('YYYY-MM-DD'),
          endDate: dateRange[1].format('YYYY-MM-DD'),
          category: selectedCategory !== 'all' ? selectedCategory : undefined,
        }),
        analyticsAPI.getSalesMetrics({
          startDate: dateRange[0].format('YYYY-MM-DD'),
          endDate: dateRange[1].format('YYYY-MM-DD'),
        }),
      ]);

      // Generate mock procurement data based on analytics
      const mockSpendAnalysis = [
        { category: 'Electronics', total_spend: 125000, vendor_count: 8, avg_order_value: 2500 },
        { category: 'Office Supplies', total_spend: 85000, vendor_count: 12, avg_order_value: 850 },
        { category: 'Materials', total_spend: 95000, vendor_count: 6, avg_order_value: 3200 },
        { category: 'Services', total_spend: 75000, vendor_count: 15, avg_order_value: 1200 },
        { category: 'Equipment', total_spend: 65000, vendor_count: 5, avg_order_value: 4300 },
      ];

      const mockSourcingOpportunities = [
        { category: 'Electronics', potential_savings: 18750, effort_required: 'medium', timeline: '3-6 months', recommendations: ['Consolidate suppliers', 'Negotiate volume discounts'] },
        { category: 'Office Supplies', potential_savings: 8500, effort_required: 'low', timeline: '1-2 months', recommendations: ['Switch to preferred vendor', 'Implement automatic reordering'] },
        { category: 'Materials', potential_savings: 14250, effort_required: 'high', timeline: '6-12 months', recommendations: ['Source alternative materials', 'Long-term contracts'] },
      ];

      // Transform the data to our interface
      const transformedData: ProcurementData = {
        totalSpend: mockSpendAnalysis.reduce((sum: number, item: any) => sum + item.total_spend, 0),
        totalOrders: mockSpendAnalysis.reduce((sum: number, item: any) => sum + (item.avg_order_value > 0 ? Math.round(item.total_spend / item.avg_order_value) : 0), 0),
        uniqueVendors: mockSpendAnalysis.reduce((sum: number, item: any) => sum + item.vendor_count, 0),
        avgOrderValue: mockSpendAnalysis.reduce((sum: number, item: any) => sum + item.avg_order_value, 0) / mockSpendAnalysis.length || 0,
        savingsRealized: mockSourcingOpportunities.reduce((sum: number, item: any) => sum + item.potential_savings, 0) * 0.3,
        contractCompliance: 92.5,
        spendByCategory: mockSpendAnalysis.map((item: any) => ({
          category: item.category,
          spend: item.total_spend,
          orders: Math.round(item.total_spend / item.avg_order_value) || 0,
          avgValue: item.avg_order_value,
          growth: Math.random() * 20 - 10,
        })),
        vendorPerformance: generateVendorPerformance(mockSpendAnalysis),
        spendTrends: generateSpendTrends(),
        savingsOpportunities: mockSourcingOpportunities.map((item: any) => ({
          category: item.category,
          currentSpend: item.potential_savings / 0.15,
          potentialSavings: item.potential_savings,
          savingsPercentage: 15,
          effort: item.effort_required === 'low' ? 'Low' : item.effort_required === 'medium' ? 'Medium' : 'High',
          timeline: item.timeline,
          recommendation: item.recommendations.join('; '),
        })),
        riskIndicators: generateRiskIndicators(),
        contractMetrics: {
          totalContracts: 45,
          expiringContracts: 8,
          renewalOpportunities: 12,
          complianceRate: 92.5,
        },
      };

      setData(transformedData);
    } catch (error) {
      console.error('Error fetching procurement data:', error);
      message.error('Failed to load procurement insights');
    } finally {
      setLoading(false);
    }
  };

  const generateVendorPerformance = (spendAnalysis: any[]) => {
    const vendors = ['TechCorp Solutions', 'Global Supplies Inc', 'Premium Materials Ltd', 'Efficient Systems', 'Quality Providers'];
    return vendors.map((vendor, index) => ({
      vendorName: vendor,
      totalSpend: spendAnalysis[index % spendAnalysis.length]?.total_spend || Math.random() * 100000,
      orderCount: Math.floor(Math.random() * 50) + 10,
      performanceScore: Math.floor(Math.random() * 30) + 70,
      onTimeDelivery: Math.floor(Math.random() * 20) + 80,
      qualityRating: Math.floor(Math.random() * 15) + 85,
      costEfficiency: Math.floor(Math.random() * 25) + 75,
    }));
  };

  const generateSpendTrends = () => {
    const months = [];
    for (let i = 11; i >= 0; i--) {
      const date = dayjs().subtract(i, 'month');
      months.push({
        month: date.format('MMM YYYY'),
        totalSpend: Math.floor(Math.random() * 50000) + 100000,
        orders: Math.floor(Math.random() * 100) + 50,
        avgValue: Math.floor(Math.random() * 1000) + 500,
      });
    }
    return months;
  };

  const generateRiskIndicators = () => [
    {
      vendor: 'TechCorp Solutions',
      riskType: 'Financial' as const,
      level: 'Medium' as const,
      description: 'Recent credit rating downgrade',
      lastAssessed: '2024-11-01',
    },
    {
      vendor: 'Global Supplies Inc',
      riskType: 'Operational' as const,
      level: 'Low' as const,
      description: 'Stable operations with minor delays',
      lastAssessed: '2024-10-28',
    },
    {
      vendor: 'Premium Materials Ltd',
      riskType: 'Performance' as const,
      level: 'High' as const,
      description: 'Quality issues reported in last quarter',
      lastAssessed: '2024-10-25',
    },
  ];

  const exportData = async () => {
    try {
      const blob = await analyticsAPI.exportData(
        'all',
        'xlsx',
        {
          startDate: dateRange[0].format('YYYY-MM-DD'),
          endDate: dateRange[1].format('YYYY-MM-DD'),
          category: selectedCategory !== 'all' ? selectedCategory : undefined,
        }
      );

      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `procurement-insights-${dateRange[0].format('YYYY-MM-DD')}-to-${dateRange[1].format('YYYY-MM-DD')}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
      message.success('Data exported successfully');
    } catch (error) {
      console.error('Error exporting data:', error);
      message.error('Failed to export data');
    }
  };

  const categoryColumns: ColumnsType<any> = [
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (category: string) => <Text strong>{category}</Text>,
    },
    {
      title: 'Total Spend',
      dataIndex: 'spend',
      key: 'spend',
      render: (spend: number) => (
        <Text style={{ color: '#1890ff', fontWeight: 'bold' }}>
          ${spend.toLocaleString()}
        </Text>
      ),
      sorter: (a, b) => a.spend - b.spend,
    },
    {
      title: 'Orders',
      dataIndex: 'orders',
      key: 'orders',
      render: (orders: number) => <Badge count={orders} showZero color="#52c41a" />,
    },
    {
      title: 'Avg Order Value',
      dataIndex: 'avgValue',
      key: 'avgValue',
      render: (value: number) => `$${value.toLocaleString()}`,
    },
    {
      title: 'Growth',
      dataIndex: 'growth',
      key: 'growth',
      render: (growth: number) => (
        <Space>
          {growth > 0 ? (
            <RiseOutlined style={{ color: '#52c41a' }} />
          ) : (
            <FallOutlined style={{ color: '#ff4d4f' }} />
          )}
          <Text style={{ color: growth > 0 ? '#52c41a' : '#ff4d4f' }}>
            {Math.abs(growth).toFixed(1)}%
          </Text>
        </Space>
      ),
    },
  ];

  const vendorColumns: ColumnsType<any> = [
    {
      title: 'Vendor',
      dataIndex: 'vendorName',
      key: 'vendorName',
      render: (name: string) => <Text strong>{name}</Text>,
    },
    {
      title: 'Total Spend',
      dataIndex: 'totalSpend',
      key: 'totalSpend',
      render: (spend: number) => `$${spend.toLocaleString()}`,
      sorter: (a, b) => a.totalSpend - b.totalSpend,
    },
    {
      title: 'Orders',
      dataIndex: 'orderCount',
      key: 'orderCount',
    },
    {
      title: 'Performance',
      dataIndex: 'performanceScore',
