    message.success('Coupon created successfully!');
    setIsCreating(false);
    form.resetFields();
  };

  const handleDeactivateCoupon = (code: string) => {
    message.success(`Coupon ${code} deactivated`);
  };

  const couponColumns: ColumnsType<Coupon> = [
    {
      title: 'Coupon Code',
      dataIndex: 'code',
      key: 'code',
      render: (code) => <Text code strong style={{ fontSize: 14 }}>{code}</Text>,
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      render: (type: Coupon['type']) => {
        const config: Record<Coupon['type'], { color: string; icon: React.ReactNode; text: string }> = {
          percentage: { color: 'blue', icon: <PercentageOutlined />, text: 'Percentage' },
          fixed: { color: 'green', icon: <DollarOutlined />, text: 'Fixed Amount' },
          free_shipping: { color: 'purple', icon: <GiftOutlined />, text: 'Free Shipping' },
          bogo: { color: 'orange', icon: <TagsOutlined />, text: 'BOGO' },
        };
        return (
          <Tag color={config[type].color} icon={config[type].icon}>
            {config[type].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Percentage', value: 'percentage' },
        { text: 'Fixed Amount', value: 'fixed' },
        { text: 'Free Shipping', value: 'free_shipping' },
        { text: 'BOGO', value: 'bogo' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Discount',
      key: 'discount',
      render: (_, record) => {
        if (record.type === 'percentage') {
          return <Text strong>{record.value}% OFF</Text>;
        } else if (record.type === 'fixed') {
          return <Text strong>${record.value} OFF</Text>;
        } else if (record.type === 'free_shipping') {
          return <Text strong>FREE SHIPPING</Text>;
        }
        return <Text>-</Text>;
      },
    },
    {
      title: 'Min Purchase',
      dataIndex: 'minPurchase',
      key: 'minPurchase',
      render: (amount) => (amount > 0 ? `$${amount}` : 'No minimum'),
    },
    {
      title: 'Usage',
      key: 'usage',
      render: (_, record) => (
        <div>
          <div style={{ marginBottom: 4 }}>
            <Text>{record.usedCount} / {record.usageLimit}</Text>
          </div>
          <Progress
            percent={(record.usedCount / record.usageLimit) * 100}
            size="small"
            showInfo={false}
          />
        </div>
      ),
    },
    {
      title: 'Valid Period',
      key: 'period',
      render: (_, record) => (
        <div>
          <div><Text type="secondary" style={{ fontSize: 12 }}>From: {record.startDate}</Text></div>
          <div><Text type="secondary" style={{ fontSize: 12 }}>To: {record.endDate}</Text></div>
        </div>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: Coupon['status']) => {
        const config: Record<Coupon['status'], { color: string; icon: React.ReactNode; text: string }> = {
          active: { color: 'green', icon: <CheckCircleOutlined />, text: 'Active' },
          expired: { color: 'red', icon: <ClockCircleOutlined />, text: 'Expired' },
          scheduled: { color: 'blue', icon: <ClockCircleOutlined />, text: 'Scheduled' },
        };
        return (
          <Tag color={config[status].color} icon={config[status].icon}>
            {config[status].text}
          </Tag>
        );
      },
      filters: [
        { text: 'Active', value: 'active' },
        { text: 'Expired', value: 'expired' },
        { text: 'Scheduled', value: 'scheduled' },
      ],
      onFilter: (value, record) => record.status === value,
    },
    {
      title: 'Actions',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button size="small">Edit</Button>
          {record.status === 'active' && (
            <Button size="small" danger onClick={() => handleDeactivateCoupon(record.code)}>
              Deactivate
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const activeCoupons = mockCoupons.filter((c) => c.status === 'active').length;
  const totalRedemptions = mockCoupons.reduce((sum, c) => sum + c.usedCount, 0);
  const avgUsageRate = (mockCoupons.reduce((sum, c) => sum + (c.usedCount / c.usageLimit) * 100, 0) / mockCoupons.length).toFixed(1);

  return (
    <div style={{ padding: 24, background: '#f0f2f5', minHeight: '100vh' }}>
      <div style={{ marginBottom: 24 }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div>
            <Title level={3}>
              <TagsOutlined style={{ color: '#1890ff' }} /> Promotions & Discounts
            </Title>
            <Paragraph type="secondary">
              Create and manage coupon codes, promotional campaigns, and discount rules
            </Paragraph>
          </div>
          <Button
            type="primary"
            icon={<PlusOutlined />}
            size="large"
            onClick={() => setIsCreating(!isCreating)}
          >
            Create Coupon
          </Button>
        </div>
      </div>

      <Alert
        message="Promotional Campaigns Active"
        description="3 active coupon codes are currently available to customers. Track performance in real-time."
        type="info"
        showIcon
        closable
        style={{ marginBottom: 24 }}
      />

      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Active Coupons"
              value={activeCoupons}
              prefix={<TagsOutlined />}
              valueStyle={{ color: '#1890ff' }}
              suffix={`/ ${mockCoupons.length}`}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Redemptions"
              value={totalRedemptions}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Usage Rate"
              value={avgUsageRate}
              suffix="%"
              prefix={<PercentageOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Discount Given"
              value="$12,450"
              prefix={<DollarOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      {isCreating && (
        <Card title="Create New Coupon" style={{ marginBottom: 16 }}>
          <Form form={form} layout="vertical" onFinish={handleCreateCoupon}>
            <Row gutter={16}>
              <Col xs={24} md={12}>
                <Form.Item
                  label="Coupon Code"
                  name="code"
                  rules={[{ required: true, message: 'Please enter coupon code' }]}
                  help="Use uppercase letters and numbers (e.g., SAVE20, SUMMER2024)"
                >
                  <Input placeholder="e.g., SAVE20" maxLength={20} />
                </Form.Item>
              </Col>
              <Col xs={24} md={12}>
                <Form.Item
                  label="Coupon Type"
                  name="type"
                  rules={[{ required: true }]}
                  initialValue="percentage"
                >
                  <Select onChange={setCouponType}>
                    <Option value="percentage">Percentage Discount</Option>
                    <Option value="fixed">Fixed Amount</Option>
                    <Option value="free_shipping">Free Shipping</Option>
                    <Option value="bogo">Buy One Get One</Option>
                  </Select>
                </Form.Item>
              </Col>

              {(couponType === 'percentage' || couponType === 'fixed') && (
                <Col xs={24} md={12}>
                  <Form.Item
                    label={couponType === 'percentage' ? 'Discount Percentage' : 'Discount Amount'}
                    name="value"
                    rules={[{ required: true }]}
                  >
                    <InputNumber
                      style={{ width: '100%' }}
                      min={0}
                      max={couponType === 'percentage' ? 100 : undefined}
                      prefix={couponType === 'fixed' ? '$' : undefined}
                      suffix={couponType === 'percentage' ? '%' : undefined}
                    />
                  </Form.Item>
                </Col>
              )}

              <Col xs={24} md={12}>
                <Form.Item label="Minimum Purchase" name="minPurchase" initialValue={0}>
                  <InputNumber style={{ width: '100%' }} min={0} prefix="$" />
                </Form.Item>
              </Col>

              {couponType === 'percentage' && (
                <Col xs={24} md={12}>
                  <Form.Item label="Maximum Discount" name="maxDiscount">
                    <InputNumber style={{ width: '100%' }} min={0} prefix="$" placeholder="No limit" />
                  </Form.Item>
                </Col>
              )}

              <Col xs={24} md={12}>
                <Form.Item
                  label="Usage Limit"
                  name="usageLimit"
                  rules={[{ required: true }]}
                  initialValue={1000}
                >
                  <InputNumber style={{ width: '100%' }} min={1} />
                </Form.Item>
              </Col>

              <Col xs={24} md={12}>
                <Form.Item
                  label="Valid Period"
                  name="dateRange"
                  rules={[{ required: true, message: 'Please select valid period' }]}
                >
                  <RangePicker style={{ width: '100%' }} />
                </Form.Item>
              </Col>

              <Col xs={24} md={12}>
                <Form.Item label="Applicable To" name="applicableTo" initialValue="all">
                  <Select>
                    <Option value="all">All Products</Option>
                    <Option value="electronics">Electronics</Option>
                    <Option value="fashion">Fashion</Option>
                    <Option value="home">Home & Garden</Option>
                    <Option value="new_customers">New Customers Only</Option>
                  </Select>
                </Form.Item>
              </Col>

              <Col xs={24}>
                <Form.Item label="Auto-activate" name="autoActivate" valuePropName="checked">
                  <Switch /> <Text type="secondary" style={{ marginLeft: 8 }}>Activate coupon immediately after creation</Text>
                </Form.Item>
              </Col>
            </Row>

            <Divider />

            <Form.Item>
              <Space>
                <Button type="primary" htmlType="submit" size="large">
                  Create Coupon
                </Button>
                <Button size="large" onClick={() => setIsCreating(false)}>
                  Cancel
                </Button>
              </Space>
            </Form.Item>
          </Form>
        </Card>
      )}

      <Card title="All Coupons">
        <Table
          columns={couponColumns}
          dataSource={mockCoupons}
          rowKey="id"
          pagination={{ pageSize: 10 }}
        />
      </Card>
    </div>
  );
};

export default PromotionsDiscountsPage;

admin/analytics-dashboard.__content:
/**
 *  SAFE API RESPONSE HANDLING - ALWAYS USE THIS PATTERN:
 * 
 * const dataArray = response?.data?.data || response?.data || [];
 * const total = response?.data?.meta?.total || response?.meta?.total || response?.total || 0;
 * 
 * Before using .map()/.filter()/.forEach():
 * setItems(Array.isArray(dataArray) ? dataArray : []);
 */

import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  Statistic, 
  Typography, 
  Tabs, 
  Table, 
  Space, 
  Button, 
  DatePicker, 
  Select, 
  Alert, 
  Progress,
  Spin,
  Badge,
  Tag,
  message,
  Tooltip,
  Avatar,
  List,
  Divider
} from 'antd';
import { 
  LineChart, 
  Line, 
  AreaChart, 
  Area, 
  BarChart, 
  Bar, 
  PieChart, 
  Pie, 
  Cell, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip as RechartsTooltip, 
  Legend, 
  ResponsiveContainer 
} from 'recharts';
import {
  ArrowUpOutlined,
  ArrowDownOutlined,
  DollarOutlined,
  ShoppingCartOutlined,
  TeamOutlined,
  BarChartOutlined,
  DownloadOutlined,
  ReloadOutlined,
  EyeOutlined,
  CheckCircleOutlined,
  WarningOutlined,
  ClockCircleOutlined,
  CloseCircleOutlined,
  FireOutlined,
  ThunderboltOutlined,
  UserOutlined,
  ShopOutlined,
  RiseOutlined,
  FallOutlined
} from '@ant-design/icons';
import { analyticsAPI } from '../../services/api/analytics';
import type { AnalyticsFilters } from '../../services/api/analytics';

const { Title, Text } = Typography;
const { TabPane } = Tabs;
const { RangePicker } = DatePicker;
const { Option } = Select;

const AnalyticsDashboard: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [realTimeLoading, setRealTimeLoading] = useState(false);
  const [timeRange, setTimeRange] = useState('7d');
  const [activeTab, setActiveTab] = useState('overview');

  // Enhanced state for real API integration
  const [dashboardData, setDashboardData] = useState<any>(null);
  const [executiveData, setExecutiveData] = useState<any>(null);
  const [realTimeData, setRealTimeData] = useState<any>(null);
  const [salesMetrics, setSalesMetrics] = useState<any>(null);
  const [vendorPerformance, setVendorPerformance] = useState<any[]>([]);
  const [businessIntelligence, setBusinessIntelligence] = useState<any>(null);

  // Load dashboard data on component mount
  useEffect(() => {
    loadDashboardData();
    loadRealTimeData();

    // Set up real-time updates every 30 seconds
    const interval = setInterval(loadRealTimeData, 30000);
    return () => clearInterval(interval);
  }, [timeRange]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);

      const filters: AnalyticsFilters = {
        granularity: timeRange === '7d' ? 'day' : timeRange === '30d' ? 'week' : 'month',
        compareWith: 'previous-period'
      };

      const [
        dashboardResponse,
        executiveResponse,
        salesResponse,
        vendorResponse,
        biResponse
      ] = await Promise.all([
        analyticsAPI.getDashboardData(filters),
        analyticsAPI.getExecutiveDashboard(filters),
        analyticsAPI.getSalesMetrics(filters),
        analyticsAPI.getVendorPerformance(filters),
        analyticsAPI.getBusinessIntelligence(filters)
      ]);

      setDashboardData(dashboardResponse);
      setExecutiveData(executiveResponse);
      setSalesMetrics(salesResponse);
      setVendorPerformance(vendorResponse.vendors || []);
      setBusinessIntelligence(biResponse);

    } catch (error) {
      console.error('Error loading dashboard data:', error);
      message.error('Failed to load analytics data');
    } finally {
      setLoading(false);
    }
  };

  const loadRealTimeData = async () => {
    try {
      setRealTimeLoading(true);
      const response = await analyticsAPI.getRealTimeMetrics();
      setRealTimeData(response);
    } catch (error) {
      console.error('Error loading real-time data:', error);
    } finally {
      setRealTimeLoading(false);
    }
  };

  // Helper functions
  const formatCurrency = (value: number) => `$${value?.toLocaleString('en-US', { minimumFractionDigits: 2 }) || '0.00'}`;

  const formatGrowth = (growth: number) => {
    const color = growth >= 0 ? '#52c41a' : '#ff4d4f';
    const icon = growth >= 0 ? <RiseOutlined /> : <FallOutlined />;
    return (
      <span style={{ color, fontSize: 12 }}>
        {icon} {Math.abs(growth).toFixed(1)}%
      </span>
    );
  };

  const getStatusIcon = (status: 'healthy' | 'warning' | 'critical') => {
    const icons = {
      healthy: <CheckCircleOutlined style={{ color: '#52c41a' }} />,
      warning: <WarningOutlined style={{ color: '#faad14' }} />,
      critical: <CloseCircleOutlined style={{ color: '#ff4d4f' }} />
    };
    return icons[status] || icons.healthy;
  };

  const refreshData = () => {
    loadDashboardData();
    loadRealTimeData();
  };

  // Data processing with fallback values
  const kpiData = {
    totalRevenue: executiveData?.revenue_breakdown ? 
      (executiveData.revenue_breakdown.b2b + executiveData.revenue_breakdown.b2c + executiveData.revenue_breakdown.enterprise) : 
      dashboardData?.salesMetrics?.totalRevenue || 0,
    totalOrders: salesMetrics?.totalOrders || dashboardData?.salesMetrics?.totalOrders || 0,
    avgOrderValue: salesMetrics?.averageOrderValue || dashboardData?.salesMetrics?.averageOrderValue || 0,
    conversionRate: salesMetrics?.conversionRate || dashboardData?.salesMetrics?.conversionRate || 0,
    customerCount: dashboardData?.customerInsights?.totalCustomers || 0,
    vendorCount: vendorPerformance.length || 0,
  };

  const salesData = dashboardData?.revenueChart || [];
  const categoryData = dashboardData?.topProducts?.slice(0, 5).map((product: any, index: number) => ({
    name: product.name,
    value: product.sales,
    color: ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#0088fe'][index] || '#8884d8'
  })) || [];

  const inventoryTrends = dashboardData?.trafficOverview || [];

  if (loading) {
    return (
      <div style={{ padding: '50px', textAlign: 'center' }}>
        <Spin size="large" />
        <div style={{ marginTop: 16 }}>
          <Typography.Text>Loading analytics dashboard...</Typography.Text>
        </div>
      </div>
    );
  }

  const vendorColumns = [
    {
      title: 'Vendor',
      dataIndex: 'name',
      key: 'name',
      render: (name: string) => <Text strong>{name}</Text>,
    },
    {
      title: 'Orders',
      dataIndex: 'orders',
      key: 'orders',
      sorter: (a: any, b: any) => a.orders - b.orders,
    },
    {
      title: 'Revenue',
      dataIndex: 'revenue',
      key: 'revenue',
      render: (revenue: number) => <Text strong>${revenue.toLocaleString()}</Text>,
      sorter: (a: any, b: any) => a.revenue - b.revenue,
    },
    {
      title: 'Rating',
      dataIndex: 'rating',
      key: 'rating',
      render: (rating: number) => (
        <div>
          <Text>{rating}</Text>
          <Progress 
            percent={(rating / 5) * 100} 
            size="small" 
            strokeColor="#52c41a" 
            showInfo={false}
            style={{ width: 50, marginLeft: 8 }}
          />
        </div>
      ),
      sorter: (a: any, b: any) => a.rating - b.rating,
    },
  ];

  return (
    <div style={{ padding: 24 }}>
      <div style={{ marginBottom: 24, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <Title level={2}>
            <BarChartOutlined style={{ marginRight: 8 }} />
            Analytics Dashboard
          </Title>
          <Text type="secondary">Phase 4 - Advanced business intelligence</Text>
        </div>
        <Space>
          <Select value={timeRange} onChange={setTimeRange} style={{ width: 120 }}>
            <Option value="7d">Last 7 days</Option>
            <Option value="30d">Last 30 days</Option>
            <Option value="90d">Last 90 days</Option>
          </Select>
          <Button icon={<ReloadOutlined />} onClick={refreshData} loading={loading}>
            Refresh
          </Button>
          <Button icon={<DownloadOutlined />} type="primary">
            Export Report
          </Button>
        </Space>
      </div>

      {/* KPI Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={4}>
          <Card>
            <Statistic
              title="Total Revenue"
              value={kpiData.totalRevenue}
              precision={2}
              prefix="$"
              valueStyle={{ color: '#52c41a' }}
              suffix={<ArrowUpOutlined style={{ color: '#52c41a' }} />}
            />
          </Card>
        </Col>
        <Col span={4}>
          <Card>
            <Statistic
              title="Total Orders"
              value={kpiData.totalOrders}
              valueStyle={{ color: '#1890ff' }}
              prefix={<ShoppingCartOutlined />}
            />
          </Card>
        </Col>
        <Col span={4}>
          <Card>
            <Statistic
              title="Avg Order Value"
              value={kpiData.avgOrderValue}
              precision={2}
              prefix="$"
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
        <Col span={4}>
          <Card>
            <Statistic
              title="Conversion Rate"
              value={kpiData.conversionRate}
              suffix="%"
              valueStyle={{ color: '#fa8c16' }}
            />
          </Card>
        </Col>
        <Col span={4}>
          <Card>
            <Statistic
              title="Customers"
              value={kpiData.customerCount}
              valueStyle={{ color: '#13c2c2' }}
              prefix={<TeamOutlined />}
            />
          </Card>
        </Col>
        <Col span={4}>
          <Card>
            <Statistic
              title="Vendors"
              value={kpiData.vendorCount}
              valueStyle={{ color: '#eb2f96' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Real-time System Health & Status */}
      {realTimeData && (
        <Row gutter={16} style={{ marginBottom: 24 }}>
          <Col span={24}>
            <Card 
              title={
                <Space>
                  <ThunderboltOutlined />
                  Real-time System Monitoring
                  <Badge 
                    status={realTimeData.system_health?.status === 'healthy' ? 'success' : 
                           realTimeData.system_health?.status === 'warning' ? 'warning' : 'error'} 
                    text={realTimeData.system_health?.status?.toUpperCase()} 
                  />
                </Space>
              }
              loading={realTimeLoading}
              extra={
                <Space>
                  <Tag color="green">Live</Tag>
                  <Button size="small" icon={<ReloadOutlined />} onClick={loadRealTimeData}>
                    Refresh
                  </Button>
                </Space>
              }
            >
              <Row gutter={16}>
                <Col xs={12} sm={6}>
                  <Statistic
                    title="Active Visitors"
                    value={realTimeData.currentVisitors}
                    prefix={<EyeOutlined />}
                    valueStyle={{ color: '#1890ff' }}
                  />
                </Col>
                <Col xs={12} sm={6}>
                  <Statistic
                    title="Active Orders"
                    value={realTimeData.activeOrders}
                    prefix={<ShoppingCartOutlined />}
                    valueStyle={{ color: '#52c41a' }}
                  />
                </Col>
                <Col xs={12} sm={6}>
                  <Statistic
                    title="Today's Revenue"
                    value={realTimeData.todayRevenue}
                    prefix="$"
                    precision={2}
                    valueStyle={{ color: '#722ed1' }}
                  />
                </Col>
                <Col xs={12} sm={6}>
                  <Statistic
                    title="System Uptime"
                    value={realTimeData.system_health?.uptime || 99.9}
                    suffix="%"
                    valueStyle={{ color: realTimeData.system_health?.uptime > 99 ? '#52c41a' : '#faad14' }}
                  />
                </Col>
              </Row>

              {/* Live transactions */}
              {realTimeData.live_transactions && realTimeData.live_transactions.length > 0 && (
                <div style={{ marginTop: 16 }}>
                  <Title level={5}>Live Transactions</Title>
                  <List
                    size="small"
                    dataSource={realTimeData.live_transactions.slice(0, 5)}
                    renderItem={(transaction: any) => (
                      <List.Item>
                        <Space>
                          <Avatar icon={<DollarOutlined />} size="small" />
                          <Text>{formatCurrency(transaction.amount)}</Text>
                          <Text type="secondary">from {transaction.customer}</Text>
                          <Text type="secondary"></Text>
                          <Text>{transaction.product}</Text>
                          <Tag color="green">{transaction.timestamp}</Tag>
                        </Space>
                      </List.Item>
                    )}
                  />
                </div>
              )}
            </Card>
          </Col>
        </Row>
      )}

      {/* Executive Dashboard Insights */}
      {executiveData && (
        <Row gutter={16} style={{ marginBottom: 24 }}>
          <Col span={16}>
            <Card title="Executive Key Performance Indicators">
              <Row gutter={16}>
                {executiveData.kpis && Object.entries(executiveData.kpis).map(([key, kpi]: [string, any]) => (
                  <Col xs={12} sm={8} key={key}>
                    <Card size="small" style={{ textAlign: 'center' }}>
                      <Statistic
                        title={key.replace(/_/g, ' ').toUpperCase()}
                        value={kpi.value}
                        valueStyle={{
                          color: kpi.trend === 'up' ? '#52c41a' : kpi.trend === 'down' ? '#ff4d4f' : '#1890ff'
                        }}
                        suffix={formatGrowth(kpi.change)}
                      />
                    </Card>
                  </Col>
                ))}
              </Row>
            </Card>
          </Col>
          <Col span={8}>
            <Card title="Strategic Insights">
              {executiveData.strategic_insights && (
                <List
                  size="small"
                  dataSource={executiveData.strategic_insights.slice(0, 4)}
                  renderItem={(insight: any) => (
                    <List.Item>
                      <Space direction="vertical" size="small">
                        <Space>
                          <Tag color={
                            insight.priority === 'high' ? 'red' :
