                  <Divider />
                  <Statistic
                    title="Avg Vendor Rating"
                    value={vendorPerformance.length > 0 ? 
                      (vendorPerformance.reduce((acc: number, v: any) => acc + (v.averageRating || 0), 0) / vendorPerformance.length).toFixed(1) : 0
