      >
        <Form
          form={form}
          onFinish={handleSubmitRequirement}
          layout="vertical"
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="name"
                label="Requirement Name"
                rules={[{ required: true, message: 'Please enter requirement name' }]}
              >
                <Input placeholder="e.g., ISO 9001:2015 Certification" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="category"
                label="Category"
                rules={[{ required: true, message: 'Please select category' }]}
              >
                <Select placeholder="Select category">
                  <Option value="safety">Safety</Option>
                  <Option value="quality">Quality</Option>
                  <Option value="environmental">Environmental</Option>
                  <Option value="financial">Financial</Option>
                  <Option value="data">Data Protection</Option>
                  <Option value="labor">Labor</Option>
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            name="description"
            label="Description"
            rules={[{ required: true, message: 'Please enter description' }]}
          >
            <Input.TextArea rows={3} placeholder="Describe the compliance requirement..." />
          </Form.Item>

          <Row gutter={16}>
            <Col span={8}>
              <Form.Item
                name="priority"
                label="Priority"
                rules={[{ required: true, message: 'Please select priority' }]}
              >
                <Select placeholder="Select priority">
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="critical">Critical</Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item
                name="riskLevel"
                label="Risk Level"
                rules={[{ required: true, message: 'Please select risk level' }]}
              >
                <Select placeholder="Select risk level">
                  <Option value="low">Low</Option>
                  <Option value="medium">Medium</Option>
                  <Option value="high">High</Option>
                  <Option value="critical">Critical</Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item
                name="status"
                label="Status"
                rules={[{ required: true, message: 'Please select status' }]}
              >
                <Select placeholder="Select status">
                  <Option value="pending">Pending</Option>
                  <Option value="compliant">Compliant</Option>
                  <Option value="non-compliant">Non-compliant</Option>
                  <Option value="expired">Expired</Option>
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="dueDate"
                label="Due Date"
                rules={[{ required: true, message: 'Please select due date' }]}
              >
                <DatePicker style={{ width: '100%' }} />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="renewalDate"
                label="Renewal Date (Optional)"
              >
                <DatePicker style={{ width: '100%' }} />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="assignedTo"
                label="Assigned To"
                rules={[{ required: true, message: 'Please enter assignee' }]}
              >
                <Input placeholder="team@company.com" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="cost"
                label="Cost ($)"
                rules={[{ required: true, message: 'Please enter cost' }]}
              >
                <Input type="number" placeholder="0" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="jurisdiction"
                label="Jurisdiction"
                rules={[{ required: true, message: 'Please enter jurisdiction' }]}
              >
                <Input placeholder="e.g., US, EU, Global" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="regulator"
                label="Regulator"
                rules={[{ required: true, message: 'Please enter regulator' }]}
              >
                <Input placeholder="e.g., FDA, ISO, OSHA" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            name="automatedChecking"
            label="Enable Automated Monitoring"
            valuePropName="checked"
          >
            <Switch />
          </Form.Item>

          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit" loading={loading}>
                {editingRequirement ? 'Update Requirement' : 'Create Requirement'}
              </Button>
              <Button onClick={() => setModalVisible(false)}>
                Cancel
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {renderRequirementDetail()}
    </div>
  );
};

export default ComplianceTracking;

