    {
      title: 'Success',
      dataIndex: 'successCount',
      key: 'successCount',
      render: (count) => <Tag color="green">{count.toLocaleString()}</Tag>,
    },
    {
      title: 'Errors',
      dataIndex: 'errorCount',
      key: 'errorCount',
      render: (count) => <Tag color={count > 0 ? 'red' : 'default'}>{count}</Tag>,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
